"use strict";(self.webpackChunkmy_blog=self.webpackChunkmy_blog||[]).push([[5418],{8549:n=>{n.exports=JSON.parse('{"archive":{"blogPosts":[{"id":"/Oct-Recap","metadata":{"permalink":"/en/blog/Oct-Recap","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2024-11-10-Oct-Recap/2024-11-10-Oct-Recap.md","source":"@site/blog/2024-11-10-Oct-Recap/2024-11-10-Oct-Recap.md","title":"\u272810\uc6d4 \ud68c\uace0","description":"\uc878\uc5c5 \uc791\ud488\uacfc \ucde8\uc5c5 \uc900\ube44\ub85c \ubc14\uc05c 10\uc6d4\uc744 \ub418\ub3cc\uc544\ubcf4\uba70","date":"2024-11-09T00:00:00.000Z","tags":[{"inline":true,"label":"Recap","permalink":"/en/blog/tags/recap"}],"hasTruncateMarker":false,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\u272810\uc6d4 \ud68c\uace0","date":"2024-11-09","authors":"teddygood","tags":["Recap"],"draft":false,"slug":"/Oct-Recap","description":"\uc878\uc5c5 \uc791\ud488\uacfc \ucde8\uc5c5 \uc900\ube44\ub85c \ubc14\uc05c 10\uc6d4\uc744 \ub418\ub3cc\uc544\ubcf4\uba70","keywords":["Recap","October"]},"unlisted":false,"nextItem":{"title":"\ud83d\udcd6 \uc624\ud508","permalink":"/en/blog/open"}},"content":"\uc774\uc804 \uae00\uc5d0\uc11c [10\uc6d4 \ubaa9\ud45c](https://teddygood.github.io/blog/starting-geulddo#10%EC%9B%94-%EB%AA%A9%ED%91%9C)\ub97c \uc801\uc5c8\uc5c8\ub2e4. \ub0b4 \ubaa9\ud45c\ub294 \uc774\ub8e8\uc5c8\ub294\uc9c0 \uc810\uac80\ud574\ubcf4\uace0\uc790 \uc774 \uae00\uc744 \uc791\uc131\ud55c\ub2e4. 10\uc6d4\uc744 \uc798 \uc0b4\uc9c0 \ubabb \ud574\uc11c \uc774 \uae00\uc744 \uc801\ub294 \uac83\uc774 \uc870\uae08 \ubb34\uc12d\uc9c0\ub9cc \uadf8\ub798\ub3c4 \uc801\uc5b4\ubcf8\ub2e4.\\n\\n## 6\uc2dc \uae30\uc0c1\\n\\n\uc77c\uc8fc\uc77c\uc5d0 4\ubc88 \uc815\ub3c4 6\uc2dc \uae30\uc0c1\uc744 \ud588\ub2e4. \uadf8\ub7fc \ub098\uba38\uc9c0 \uc808\ubc18\uc740 \uba87 \uc2dc\uc5d0 \uae30\uc0c1\ud588\uc744\uae4c \uae30\ub85d\ud55c \ub178\ud2b8\ub97c \uc0b4\ud3b4\ubcf4\ub2c8 7~11\uc2dc \uc0ac\uc774\uc600\ub2e4. \uc774\uc720\ub294 \uc544\ub798\uc640 \uac19\uc558\ub2e4.\\n\\n- \ub9ac\uadf8 \uc624\ube0c \ub808\uc804\ub4dc\\n- \uce5c\uad6c \ub610\ub294 \ud63c\uc790 \uc220 \ub9c8\uc154\uc11c \ub2a6\uac8c \uc794 \uac83\\n- \uc0dd\ud65c \ud328\ud134 \ub9dd\uac00\uc838\uc11c \uc878\ub9ac\uc9c0 \uc54a\uc544 \ubabb \uc794 \uac83\\n- \uce74\ud398\uc778 \ub108\ubb34 \ub9ce\uc774 \uc12d\ucde8\\n\\n6\uc2dc\uc5d0 \ubabb \uc77c\uc5b4\ub098\ub294 \ub370\ub294 \uc5ed\uc2dc \uc774\uc720\uac00 \ub2e4 \uc788\ub2e4. \uc774\uc720\ub97c \uc54c\uace0 \uc788\ub294\ub370\ub3c4 \uace0\uce58\uc9c0 \ubabb \ud560 \ubfd0\uc774\ub2e4. \\n\\n- \ub864\uc744 \ud560 \uac70\uba74 \ubb34\uc870\uac74 11\uc2dc 30\ubd84 \uc804\uc5d0 \ub05d\ub0b4\uae30\\n- \ud63c\uc220 \uadf8\ub9cc\ud558\uae30\\n- \uce5c\uad6c\ub4e4\uacfc \uc220\uc744 \uba39\uac8c \ub418\uba74 \uc77c\uc8fc\uc77c\uc5d0 \ud55c \ubc88\ub9cc \ub9c8\uc2dc\uae30\\n- \uce74\ud398\uc778\uc740 \uc624\uc804\uc5d0\ub9cc \uc12d\ucde8\ud558\uace0 \uc624\ud6c4\uc5d0\ub294 \ub514\uce74\ud398\uc778 \ucee4\ud53c \ub9c8\uc2dc\uae30\\n- \uce74\ud398\uc778 200mg \ub118\uae30\uc9c0 \uc54a\uae30\\n\\n## \uc6b4\ub3d9\uacfc \uc2dd\ub2e8\\n\\n\uba3c\uc800 \uc0c8\ubcbd \uacf5\ubcf5 \uc720\uc0b0\uc18c\ub294 6\uc2dc\uc5d0 \uc77c\uc5b4\ub0ac\uc744 \ub54c\ub294 \ubb34\uc870\uac74 \uac14\ub2e4. \uc0c8\ubcbd \uacf5\uae30\uac00 \uc88b\uc558\ub294\ub370 \uc774\uc81c\ub294 \uc810\uc810 \ucd94\uc6cc\uc838\uc11c 11\uc6d4\uc5d0\ub294 \uc911\ub2e8\ud560 \uc608\uc815\uc774\ub2e4. \uc6e8\uc774\ud2b8 \ud2b8\ub808\uc774\ub2dd\uc740 \uc77c\uc8fc\uc77c\uc5d0 5\ubc88 \ud588\ub2e4. \ub2a6\uac8c \uc77c\uc5b4\ub098\uac70\ub098 \uc219\ucde8\uac00 \uc788\ub354\ub77c\ub3c4 \uc800\ub141 \uba39\uae30 \uc804\uc5d0 \ud5ec\uc2a4\uc7a5\uc5d0 \uac00\uc11c \ud558\uace0 \uc654\ub2e4. \\n\\n\ubcf4\ud1b5 \uc77c\uc5b4\ub098\uc11c \uc544\uce68\uc744 \uba39\uace0 3\uc2dc\uac04 \uc774\ud6c4\uc5d0 \uac00\ub294 \ud3b8\uc778\ub370 \uadf8\ub54c \uac00\ubc84\ub9ac\uba74 \uc624\uc804\uc774 \ub05d\ub098\uae30 \ub54c\ubb38\uc5d0 \uc624\ud6c4 4\uc2dc~5\uc2dc \uc0ac\uc774\uc5d0 \uac00\ub294 \uac83\uc73c\ub85c \ubc14\uafe8\ub2e4. \uc624\uc804\uc5d0 \uc9d1\uc911\uc774 \uc798 \ub418\ub294\ub370 \uadf8\ub54c \uc6b4\ub3d9\uc744 \ud574\ubc84\ub9ac\ub2c8 \uc6b4\ub3d9\uc774 \ub0b4 \uc0b6\uc758 \uc8fc\uac00 \ub418\uc5b4\uac00\ub294 \ub290\ub08c\uc774\ub77c \uc624\ud6c4\ub85c \uc2dc\uac04\uc744 \ubc14\uafe8\ub2e4. \\n\\n\uc2dd\ub2e8\uc740 \uc608\uc804\ubcf4\ub2e4 \ub098\uc544\uc84c\ub2e4. \ubc30\ub2ec \uc74c\uc2dd\uc740 \uc77c\uc8fc\uc77c\uc5d0 \uc138 \ubc88\uc5d0\uc11c \ud55c \ubc88 \uba39\ub294 \uac78\ub85c \ubc14\ub00c\uc5c8\uace0, \ud558\ub8e8\uc5d0 \ud55c \ubc88 \uc77c\ubc18\uc2dd\uc744 \uba39\uace0 \ub098\uba38\uc9c0\ub294 \ub2ed\uac00\uc2b4\uc0b4\uacfc \ubc25, \uae40\uce58, \uacc4\ub780\uc744 \uba39\uace0 \uc788\ub2e4. \uadfc\uc721\ub7c9\uc740 \ub298\uace0 \uc788\ub294\ub370 \uccb4\uc9c0\ubc29\uc740 \uc904\uc5b4\ub4e4\uc9c0 \uc54a\ub294\ub2e4. \ucde8\uc5c5\uc774 \ub05d\ub098\uae30 \uc804\uae4c\uc9c0\ub294 \uc774\ub807\uac8c \ucb49 \uba39\uc9c0 \uc54a\uc744\uae4c \uc2f6\ub2e4. \uc644\uc804\ud788 \ub2e4\uc774\uc5b4\ud2b8\ub294 \ud798\ub4e4 \uac83 \uac19\ub2e4.\\n\\n## \ub3c5\uc11c\\n\\n- \ubab0\uc785\uc758 \uc990\uac70\uc6c0\\n- \uc6d0\uc53d\\n- \uc624\ud508\\n\\n\uc774\ub807\uac8c 3\uad8c\uc758 \ucc45\uc744 \uc77d\uc5c8\ub2e4. \ucc45 \uc77d\uc73c\uba74\uc11c \ubc18\uc131\uc774 \ub418\uae30\ub3c4 \ud558\uace0, \ub0b4\uac00 \uc9c0\uae08 \ub108\ubb34 \ub9ce\uc740 \uac83\uc744 \ud558\ub824\uace0 \uc788\ub294\uac00\uc5d0 \ub300\ud574 \ub610 \ub2e4\uc2dc \uc0dd\uac01\ud558\uac8c \ub418\uba74\uc11c \uc9c0\uae08 \ub098\uc5d0\uac8c \ub2f9\uc7a5 \ud544\uc694 \uc5c6\ub2e4\uace0 \uc0dd\uac01\ud558\ub294 \ubd80\ubd84\ub4e4\uc740 \uc5c6\uc560\uace0 \uc788\ub2e4.\\n\\n\uc790\uae30 1\uc2dc\uac04 \uc804\uc5d0 \ub3c5\uc11c\ud558\ub294 \uac83\ub3c4 \ubaa9\ud45c\uc600\ub294\ub370 \uc774\uac83\ub3c4 \uccab \uc8fc\ub294 \uc798 \ub410\ub2e4. \uadfc\ub370 \uc790\uae30 \uc804\uc5d0 \uc878\ub824 \uc8fd\uaca0\ub294\ub370 \uc778\uc99d\uae4c\uc9c0 \ud558\ub294 \uac83\uc774 \ub108\ubb34 \uadc0\ucc2e\uc544\uc11c \uad00\ub450\uac8c \ub410\ub2e4. \uc9c0\uae08\uc740 \ucc45\uc744 \uc77d\uace0 \uc2f6\uc744 \ub54c\ub098 \uc5ec\uc720\ub86d\ub2e4\uace0 \ud310\ub2e8\ub418\ub294 \uc8fc\ub9d0 \uc544\uce68\uc5d0 \uc870\uae08\uc529 \uc77d\uace0 \uc788\ub2e4.\\n\\n\ud604\uc7ac \uc77d\uace0 \uc788\ub294 \ucc45\uc740 [\uc18c\ud504\ud2b8\uc6e8\uc5b4 \uc5d4\uc9c0\ub2c8\uc5b4 \uac00\uc774\ub4dc\ubd81](https://product.kyobobook.co.kr/detail/S000214576874)\uc774\ub2e4. \uc774 \ucc45\uc744 \ub2e4 \uc77d\uace0 \uc5ec\uc720\uac00 \ub41c\ub2e4\uba74 \ub2e4\uc74c \ucc45\uc73c\ub85c\ub294 \ud55c\uac15 \uc791\uac00\ub2d8\uc758 [\uc791\ubcc4\ud558\uc9c0 \uc54a\ub294\ub2e4](https://product.kyobobook.co.kr/detail/S000000781116)\ub97c \uc77d\uc5b4 \ubcf4\uace0 \uc2f6\ub2e4.\\n\\n## \uc2a4\ud130\ub514\\n\\n- ETL \uc2a4\ud130\ub514([\ub7ec\ub2dd \uc2a4\ud30c\ud06c](https://product.kyobobook.co.kr/detail/S000061353994)): \ub370\uc774\ud130 \uc5d4\uc9c0\ub2c8\uc5b4, \uc18c\ud504\ud2b8\uc6e8\uc5b4 \uc5d4\uc9c0\ub2c8\uc5b4 \ubd84\ub4e4\uacfc \ud568\uaed8 \uacf5\ubd80\ud588\ub358 \uc2a4\ud130\ub514\ub77c \uc2e4\uc81c \ud604\uc5c5\uc5d0\uc11c\uc758 \ub0b4\uc6a9\ub3c4 \ub4e4\uc744 \uc218 \uc788\uc5c8\uace0, \ubc30\uc6b8 \uc810\uc774 \uc815\ub9d0 \ub9ce\uc558\ub358 \uc2a4\ud130\ub514\uc600\ub2e4. \ud55c \uac00\uc9c0 \uc544\uc26c\uc6b4 \uc810\uc740 \ucc45\uc744 \ub2e4 \uc77d\uace0 \ud504\ub85c\uc81d\ud2b8\ub97c \ud558\ub294 \uac83\uae4c\uc9c0 \ubaa9\ud45c\uc600\ub294\ub370, \uc878\uc5c5 \uc791\ud488\uc774 \ub9c8\ubb34\ub9ac\uac00 \ub418\uc5b4 \uac00\ub2e4 \ubcf4\ub2c8 \ubc14\ube60\uc838\uc11c \uc9d1\uc911\ud560 \uc2dc\uac04\uc774 \uc5c6\ub2e4\uace0 \ud310\ub2e8\ud558\uc5ec \uadf8\ub9cc\ub450\uc5c8\ub2e4.  \\n- \uc54c\uace0\ub9ac\uc998 \uc2a4\ud130\ub514([Coursera Stanford Algorithms](https://www.coursera.org/specializations/algorithms)): \uc774 \uac15\uc758\ub294 \uc54c\uace0\ub9ac\uc998\uc744 \uc218\ud559\uc801\uc73c\ub85c \uc811\uadfc\ud558\ub294 \ubd80\ubd84\ub4e4\uc774 \ub9ce\uc544 \uc880 \ub9ce\uc774 \uc5b4\ub824\uc6e0\ub2e4. \uadf8\ub807\uc9c0\ub9cc \ub610 \ub2e4\uc2dc \uc54c\uace0\ub9ac\uc998\uc744 \uac1c\ub150\uc801\uc73c\ub85c \uc811\uadfc\ud558\uc5ec \uacf5\ubd80\ud558\ub2c8 \uc2dc\uac04 \ubcf5\uc7a1\ub3c4 \uacc4\uc0b0\uc774 \ud6e8\uc52c \uc218\uc6d4\ud574\uc84c\uace0, \uc54c\uace0\ub9ac\uc998 \ubb38\uc81c\ub97c \ud478\ub294\ub370\ub3c4 \ub3c4\uc6c0\uc774 \ub410\ub2e4. \\n- DDIA \uc2a4\ud130\ub514([\ub370\uc774\ud130 \uc911\uc2ec \uc560\ud50c\ub9ac\ucf00\uc774\uc158 \uc124\uacc4](https://product.kyobobook.co.kr/detail/S000001766328)): \uc774 \uc2a4\ud130\ub514\uc5d0\ub3c4 DE, SWE \ubd84\ub4e4\uc774 \ub300\ubd80\ubd84\uc774\uc5c8\uc5b4\uc11c \ub9ce\uc740 \uac78 \ubc30\uc6b8 \uc218 \uc788\ub358 \uc2a4\ud130\ub514\uc600\uc73c\ub098 \uc9c0\uae08\uc758 \ub098\uc5d0\uac8c\ub294 \ub108\ubb34 \uc5b4\ub824\uc6b4 \ub0b4\uc6a9\uc774\uae30\ub3c4 \ud558\uace0, \ub9c8\ucc2c\uac00\uc9c0\ub85c \uc878\uc5c5 \uc791\ud488 \ub54c\ubb38\uc5d0 \ub108\ubb34 \ubc14\ube60\uc838 \uadf8\ub9cc\ub450\uc5c8\ub2e4.\\n\\n\uc801\ub2e4 \ubcf4\ub2c8 \ub2e4 \uadf8\ub9cc\ub454 \ub0b4\uc6a9 \ubc16\uc5d0 \uc5c6\ub294 \uac83 \uac19\ub2e4. \uc544\ubb34\ub798\ub3c4 \uc878\uc5c5 \uc791\ud488 \ub9c8\ubb34\ub9ac\uac00 \uace7 \ub2e4\uac00\uc624\ub2e4 \ubcf4\ub2c8 \uadf8\ucabd\uc5d0 \ub354 \uc2e0\uacbd\uc744 \uc4f8 \uc218 \ubc16\uc5d0 \uc5c6\uc5c8\ub2e4. \ub370\uc774\ud130 \uc911\uc2ec \uc560\ud50c\ub9ac\ucf00\uc774\uc158 \uc124\uacc4 \ucc45 \uac19\uc740 \uacbd\uc6b0\uc5d0\ub294 \ub098\uc911\uc5d0 \ubc14\uc058\uc9c0 \uc54a\uc744 \ub54c \uc2a4\ud130\ub514\ub97c \ub2e4\uc2dc \ud574\uc57c \ud560 \uac83 \uac19\ub2e4. \ucc45\uc774 \uc5b4\ub835\uae30\ub3c4 \ud558\uace0, \ub2e4\ub978 \ubd84\ub4e4\uacfc \ud568\uaed8 \uacbd\ud5d8\uc744 \uc704\uc8fc\ub85c \uc598\uae30\ud558\uba74\uc11c \uacf5\ubd80\ud558\uba74 \ub354 \ub3c4\uc6c0\uc774 \ub420 \ucc45\uc774\ub77c \uc0dd\uac01\ud55c\ub2e4.\\n\\n## \uac1c\uc778 \uacf5\ubd80\\n\\n\uc778\uacfc\ucd94\ub860: \uccab \uc8fc\uc5d0 \uc5f4\uc2ec\ud788 \ud558\ub2e4\uac00 \uadf8\ub9cc\ub450\uc5c8\ub2e4. \uc815\ub9d0 \uacf5\ubd80\ud574\ubcf4\uace0 \uc2f6\uc5c8\ub358 \ubd84\uc57c\uc9c0\ub9cc \ud604\uc7ac \ub0b4\uac00 \ud574\uc57c \ud560 \uacf5\ubd80\ub294 \uc544\ub2c8\ub77c \ud310\ub2e8\ud588\ub2e4. \uc9c0\uae08\uc740 \ucde8\uc5c5 \uc900\ube44\ub97c \uc704\ud55c \uacf5\ubd80\uac00 \ub354 \uc6b0\uc120\uc774\ub77c \uc0dd\uac01\ud588\ub2e4.\\n\\n\uc790\ubc14\uc2a4\ud06c\ub9bd\ud2b8: [\ucf54\uc5b4 \uc790\ubc14\uc2a4\ud06c\ub9bd\ud2b8](https://product.kyobobook.co.kr/detail/S000001766397) \ucc45\uc744 \ub2e4 \uc77d\uc5c8\ub2e4. \uc815\ub9d0 \uc720\uc775\ud55c \ucc45\uc774\ub77c \uc0dd\uac01\ud55c\ub2e4. \ud575\uc2ec\ub9cc \ub4e4\uc5b4\uac00 \uc788\uace0, \ucc45\uc758 \ub450\uaed8\uac00 \ub450\uaecd\uc9c0 \uc54a\uc544 \ud3b8\ud558\uac8c \uc77d\uc744 \uc218 \uc788\uc5c8\ub2e4. \uc774\uac83\ub3c4 \uc0ac\uc2e4 \ub0b4\uac00 \ud504\ub860\ud2b8\uc5d4\ub4dc\ub85c \ucde8\uc9c1\ud560 \uac83\ub3c4 \uc544\ub2c8\uace0 \uaf2d \ud544\uc694\ud55c \uac83\uc740 \uc544\ub2c8\uc9c0\ub9cc \uadf8\ub798\ub3c4 \ucc45\uc774 \uc587\uc544 \uc77d\uc5b4\ubcfc \uc218 \uc788\uc5c8\ub2e4.\\n\\n\ud559\uad50 \uc218\ud559 \uac15\uc758: \uc120\ud615\ub300\uc218\ud559, \ud655\ub960 \ubc0f \ud1b5\uacc4 \uadf8\ub9ac\uace0 \uace0\ub4f1\ud559\uad50\uc5d0\uc11c \uacf5\ubd80\ud588\ub358 \uc218\ud559\uc5d0 \uc57d\uac04\uc758 \uc815\ubcf4 \uc774\ub860\uc774 \ucd94\uac00\ub41c \uad50\uc591 \uac15\uc758\ub2e4. \ub9ce\uc740 \ubd84\uc57c\ub97c \ud55c \ubc88\uc5d0 \ub2e4\ub8e8\uace0 \uc608\uc804\uc5d0 \uacf5\ubd80\ud588\ub358 \uac83\ub4e4\uc774\ub77c \ud070 \uc5b4\ub824\uc6c0\uc740 \uc5c6\uc5c8\ub2e4.\\n\\n## \uc878\uc5c5 \uc791\ud488\\n\\n\ud504\ub85c\uc81d\ud2b8 \uc790\uccb4\ub294 \uac70\uc758 \ub9c8\ubb34\ub9ac\uac00 \ub418\uc5b4 \uc804\uccb4 \uacfc\uc815\uc744 \uacc4\uc18d \ubc18\ubcf5\ud574\ubcf4\uba74\uc11c \uc6b0\ub9ac\uac00 \ub9cc\ub4e0 \uc54c\uace0\ub9ac\uc998\uc774 \uc798 \uc791\ub3d9\ub418\ub294\uc9c0 \ud655\uc778\ud588\ub2e4. \uadf8\ub7ec\uace0\ub294 \ud3ec\uc2a4\ud130\ub97c \ub9cc\ub4dc\ub294 \uac83\uc5d0 \uc9d1\uc911\ud588\ub2e4. \ud300 \ubbf8\ud305\uc73c\ub85c \uc8fc 3\ubc88 \uc815\ub3c4\ub294 \ud544\uc218\ub85c \ud588\uace0, \uad50\uc218\ub2d8\uacfc\uc758 \ubbf8\ud305\uc740 \ub9e4\ubc88 \uc788\ub358 \uac83\ucc98\ub7fc 2\uc8fc\uc5d0 \ud55c \ubc88\uc529 \uc9c4\ud589\ud588\ub2e4. \ud300 \ubbf8\ud305\uc5d0\ub294 \uc5b8\uc81c\ub098 \uc870\uad50\ub2d8\uaed8\uc11c \uc870\uc5b8\uc744 \ud574\uc8fc\uc2dc\uace0, \ubc29\ud5a5\uc131\uc744 \uc7a1\uc544\uc8fc\uc154\uc11c \ud3ec\uc2a4\ud130\ub3c4 \uae54\ub054\ud558\uace0 \uc608\uc058\uac8c \ub9cc\ub4e4 \uc218 \uc788\uc5c8\ub2e4.\\n\\n\uc878\uc5c5 \uc791\ud488\uc740 \ub098\uc911\uc5d0 \ub530\ub85c \ud68c\uace0\ub97c \uc62c\ub9b4 \uc608\uc815\uc774\ub2e4.\\n\\n## \ucde8\uc5c5 \uc900\ube44\\n\\n### \uc774\ub825\uc11c \uc791\uc131\\n\\n\uc601\ubb38 \uc774\ub825\uc11c \uc791\uc131\uc744 \ub9c8\ubb34\ub9ac \ud588\uace0, \uac00\uc7a5 \ub4e4\uc5b4\uac00\uace0 \uc2f6\uc5c8\ub358 \uae30\uc5c5\uc5d0 \uc9c0\uc6d0\ud55c \uc0c1\ud0dc\uc774\ub2e4. \uc11c\ub958 \ud569\uaca9\uc744 \ud588\uace0, OA\uae4c\uc9c0 \ubcf8 \uc0c1\ud0dc\uc774\uba70 \uc9c0\uae08\uc740 \uba74\uc811\uc744 \uc900\ube44\ud558\uace0 \uc788\ub2e4. \uc774 \ucc44\uc6a9 \ud504\ub85c\uc138\uc2a4\uac00 \ub05d\ub098\uba74 \ud55c\uad6d\uc5b4 \uc774\ub825\uc11c\ub3c4 \uc791\uc131\ud574\ub458 \uc608\uc815\uc774\ub2e4. \\n\\n### \uc774\uc804 \ud504\ub85c\uc81d\ud2b8 \ucf54\ub4dc \uc815\ub9ac\\n\\n\uc9c0\uae08\uae4c\uc9c0 \ud559\uad50\uc5d0\uc11c \uc9c4\ud589\ud588\ub358 \ud504\ub85c\uc81d\ud2b8\ub97c \uc544\uc9c1 \uc815\ub9ac\ud558\uace0 \uc788\ub294 \uc0c1\ud0dc\uc774\ub2e4. 2000\uc904\uc774 \ub118\ub294 \ud504\ub85c\uc81d\ud2b8\ub3c4 \uc788\uace0, \ud588\ub358 \uc77c\ub4e4\uc774 \ub108\ubb34 \ub9ce\uc544 \uc815\ub9ac\ud558\uae30\uac00 \uc27d\uc9c0 \uc54a\uc558\ub2e4. \uc8fc\uc11d\ub3c4 \uc601\ubb38\uc73c\ub85c \ubc14\uafb8\uace0, \uc804\uccb4\uc801\uc73c\ub85c \uc5b4\ub5bb\uac8c \uc791\ub3d9\ub418\ub294\uc9c0 \uad6c\uc870\ub97c \ud30c\uc545\ud558\uba74\uc11c \uacf5\ubd80 \uc911\uc774\ub2e4. \ud2b9\ud788 \uc774\ub825\uc11c\uc5d0 \uc801\uc5c8\ub358 \ud504\ub85c\uc81d\ud2b8\ub97c \uc911\uc2ec\uc73c\ub85c \uc815\ub9ac\ud558\uace0 \uc788\ub2e4.\\n\\n### \uc54c\uace0\ub9ac\uc998\\n\\n\uc6d0\ub798 \ubaa9\ud45c\ub294 \ub9e4\uc77c \uc138 \ubb38\uc81c\uc529 \ud478\ub294 \uac83\uc600\uc9c0\ub9cc \ub9e4\uc77c \uc544\uce68\uc5d0 \uc77c\uc815 \uc2dc\uac04 \ub0b4\uc5d0 \ud480\uace0 \uc788\uace0, \ud574\uc124\uc744 \ubcf4\uba74\uc11c \ub2e4\uc2dc \uc0dd\uac01\ud574\ubcf4\uba74 \uc2dc\uac04\uc774 \ud655 \uac00\ubc84\ub824\uc11c \ud55c \ubb38\uc81c\ub9cc \ud480\uace0 \uc788\ub2e4. \ub9ac\ud2b8\ucf54\ub4dc \uae30\uc900\uc73c\ub85c easy, medium \ubb38\uc81c\ub97c \ud480\uba74\uc11c \ubb38\uc81c\uc5d0 \uc5b4\ub5bb\uac8c \uc811\uadfc\ud560\uc9c0\ub97c \uc911\uc2ec\uc73c\ub85c \uc0dd\uac01\ud558\uba74\uc11c \ud480\uace0 \uc788\ub2e4.\\n\\n## \ub124\ud2b8\uc6cc\ud0b9\\n\\n### \uac15\ubd81\uc5d0\ub3c4\uc0ac\ub78c\uc788\ub610\\n\\n\uae00\ub610\uc5d0 **\uac15\ubd81\uc5d0\ub3c4\uc0ac\ub78c\uc788\ub610** \ubaa8\uc784\uc5d0 \ud55c \ubc88 \ucc38\uc5ec\ud588\ub2e4. \ub178\uc6d0\uc5d0\uc11c \ub9cc\ub0a0 \uc904 \uc54c\uc558\ub294\ub370 \uc758\uc678\ub85c \ucda9\ubb34\ub85c\uc5d0\uc11c \ubaa8\uc784\uc744 \uc9c4\ud589\ud588\uace0, \uc624\ub79c\ub9cc\uc5d0 \uaf64 \uba40\ub9ac \ub180\ub7ec \uac00\uc11c \uc88b\uc558\ub2e4. \ub610, \uac1c\ubc1c\uc774\ub098 \ub300\ud559\uc6d0 \uc598\uae30 \uadf8\ub9ac\uace0 \uc774\uac83\uc800\uac83 \ub2e4\uc591\ud55c \uc8fc\uc81c\uc758 \uc598\uae30\ub4e4\ub3c4 \ud560 \uc218 \uc788\uace0, \ub4e4\uc744 \uc218 \uc788\uc5b4\uc11c \uc88b\uc740 \uc2dc\uac04\uc774\uc5c8\ub2e4. \uc774 \ubaa8\uc784\uc744 \ud1b5\ud574 \ub0b4 \uace0\ubbfc\ub3c4 \uc5b4\ub290 \uc815\ub3c4 \ud574\uacb0\uc774 \ub410\ub2e4. \uadf8\ub54c \uac00\uc7a5 \ud070 \uace0\ubbfc\uc740 \'\uc5b4\ub5a4 \ubd84\uc57c\ub85c \uc55e\uc73c\ub85c \ub098\uc544\uac00\uc57c \ud560\uc9c0 \uc798 \ubaa8\ub974\uaca0\ub2e4\' \uc600\ub294\ub370 \ubc94\uc6a9\uc131\uc744 \uc0dd\uac01\ud558\uac70\ub098 \uc644\uc804\ud788 \ud558\ub098\uc758 \ubd84\uc57c\ub9cc \ud30c\uac70\ub098 \ub458 \uc911 \ud558\ub098\ub97c \uc120\ud0dd\ud558\ub77c\uace0 \ud558\uc2e0 \uc870\uc5b8\uc774 \ub098\uc5d0\uac8c \uc5c4\uccad \uc640\ub2ff\uc558\ub2e4. \\n\\n\uc5b4\uc0c9\ud568 \ubc18, \uc7ac\ubbf8 \ubc18\uc774\uc5c8\uc9c0\ub9cc \uc0c8\ub85c\uc6b4 \uc0ac\ub78c\ub4e4\uacfc \ub9cc\ub098\uc11c \uc598\uae30\ud558\ub294 \uac74 \uc5b8\uc81c\ub098 \uc7ac\ubc0c\ub2e4. \ud2b9\ud788 \ub0b4 \uace0\ubbfc\uc744 \uc27d\uac8c \uc598\uae30\ud560 \uc218 \uc788\ub2e4\ub294 \uac83\ub3c4 \ub108\ubb34 \uc88b\uace0, \ub2e4\ub978 \ubd84\ub4e4\uc758 \uace0\ubbfc\uc774\ub098 \uacbd\ud5d8\uc744 \ub4e4\uc5b4\ubcfc \uc218 \uc788\ub2e4\ub294 \uac8c \ub108\ubb34 \ud070 \uc7ac\ubbf8\ub77c\uace0 \uc0dd\uac01\ud55c\ub2e4. \ub3d9\uae30\ubd80\uc5ec\ub3c4 \ub418\uace0, \ud3c9\uc18c \ubabb \ub098\ub204\ub358 \uc598\uae30\ub4e4\ub3c4 \ud560 \uc218 \uc788\uae30\ub3c4 \ud588\ub2e4. \uc5ed\uc2dc \ucee4\ubba4\ub2c8\ud2f0\uac00 \uc8fc\ub294 \ud798\uc740 \ud06c\ub2e4\uace0 \uc0dd\uac01\ud55c\ub2e4.\\n\\n### \ub2f9\uadfc \ud14c\ud06c \ubc0b\uc5c5\\n\\n\ub2f9\uadfc \ud14c\ud06c \ubc0b\uc5c5\uc5d0 \uc6b4 \uc88b\uac8c \ucc38\uc5ec\ud560 \uc218 \uc788\uc5c8\ub2e4. \ub09c Data/ML \ucabd\uc5d0\uc11c \uac15\uc5f0\uc744 \ub4e4\uc5c8\uace0, \ub610 \uc6b4 \uc88b\uac8c \ub2e4\uc591\ud55c \ub2f9\uadfc \ud300\ub4e4\uacfc \ub124\ud2b8\uc6cc\ud0b9\uc744 \ud560 \uc218 \uc788\uc5c8\ub2e4. \ub2f9\uadfc \ud300\ub4e4\uacfc \ub124\ud2b8\uc6cc\ud0b9\uc744 \ud558\ub294 \uac83\uc740 \uc120\ucc29\uc21c\uc774\uc5c8\ub2e4. \uc815\ub9d0 \ube60\ub978 \uc18d\ub3c4\ub85c \ub124\ud2b8\uc6cc\ud0b9\uc774 \ub9c8\uac10\ub418\uc5c8\ub294\ub370 \ucc44\uc6a9 \ud300\uacfc\uc758 \ub124\ud2b8\uc6cc\ud0b9\uc740 \ubabb \ud55c \uac83\uc774 \ub9e4\uc6b0 \uc544\uc26c\uc6e0\ub2e4.\\n\\n\ub2e4\ub140\uc640\uc11c \ub0b4\ub9b0 \uacb0\ub860\uc774 \ud558\ub098 \uc788\uc5c8\ub2e4. \uc9c0\uae08\uc740 MLOps \uc9c1\ubb34\ub294 \uc544\uc608 \uace0\ub824\ud558\uc9c0 \uc54a\uae30\ub85c, \uadf8\ub9ac\uace0 \ub370\uc774\ud130 \uc5d4\uc9c0\ub2c8\uc5b4\ub9c1\ub3c4 \uc5ed\uc2dc \uc0dd\uac01\ud558\uc9c0 \uc54a\uae30\ub85c \ud588\ub2e4. \uc774\ub7f0 \uc9c1\uad70\uc740 \ubcf4\ud1b5 \ub2e4 \uc774\uc9c1\uc744 \ud1b5\ud574 \ub418\uac70\ub098 \uc9c1\uc7a5\uc5d0\uc11c \uc2dc\ucf1c\uc11c \uc77c\uc744 \ud558\uac8c \ub418\ub294 \uc9c1\ubb34\uc778 \uac78 \uc54c\uace0 \uc788\uc74c\uc5d0\ub3c4 \'\ud639\uc2dc\ub098 \uc2e0\uc785\uc5d0\uac8c \uae30\ud68c\uac00 \uc788\uc9c0 \uc54a\uc744\uae4c\'\ub77c\ub294 \uc0dd\uac01\uc5d0 \uace0\ub824\ud558\uace0 \uc788\uc5c8\ub294\ub370 \uae54\ub054\ud558\uac8c \ub193\uc544\uc8fc\uae30\ub85c \ud588\ub2e4. \uc774\uac74 \ub370\uc774\ud130 \uc5d4\uc9c0\ub2c8\uc5b4\ub9c1 \uc2a4\ud130\ub514\ub97c \uc9c4\ud589\ud558\uba74\uc11c\ub3c4 \uc0dd\uac01\ud558\uac8c \ub41c \uacb0\uacfc\uc774\ub2e4. \\n\\n\uadf8\ub9ac\uace0 \ub9cc\uc57d\uc5d0 MLE\uac00 \ub418\uace0 \uc2f6\ub2e4\uba74 ML \uae30\ubcf8\uae30\uc640 \ucf54\ub529 \ud14c\uc2a4\ud2b8\ub294 \uc798 \uc900\ube44\ud574\uc57c \ud55c\ub2e4. \uc720\uc800 \uc11c\ube44\uc2a4 \uacbd\ud5d8\ub3c4 \uc788\uc73c\uba74 \uc88b\uc9c0\ub9cc \uc2e0\uc785\uc5d0\uac8c \uadf8\uac83\uae4c\uc9c0 \ubc14\ub77c\uc9c0\ub294 \uc54a\ub294\ub2e4\uace0 \ud558\uc168\ub2e4. \uadf8\ub9ac\uace0 \ud504\ub85c\uc81d\ud2b8 \uacbd\ud5d8\uc740 \uc5ed\uc2dc\ub098 \uce90\uae00\uc744 \ucd94\ucc9c\ud558\uc168\ub2e4.\\n\\n## \uc601\uc5b4 \uacf5\ubd80\\n\\nChatGPT\uac00 Advanced Voice Mode\ub77c\ub294 \uae30\ub2a5\uc744 \ubc1c\ud45c\ud588\uae38\ub798 \uc601\uc5b4 \ud68c\ud654 \uacf5\ubd80\ub97c \ud574\ubcf4\ub824 \ud588\ub294\ub370 \uc0c1\ub2f9\ud788 \uc27d\uc9c0 \uc54a\uc558\ub2e4. \uc2a4\ud53c\ud0b9\uc740 \ub09c\uc0dd \ucc98\uc74c \ud574\ubcf4\ub294 \uac70\ub77c \ub108\ubb34 \uc5b4\ub824\uc6e0\uace0, \uadf8\ub7ec\ub2e4 \ubcf4\ub2c8 \uc2b5\uad00\uc73c\ub85c \uc774\uc5b4\uc9c0\uc9c0 \uc54a\uc558\ub358 \uac83 \uac19\ub2e4. \\n\\n## 11\uc6d4 \ubaa9\ud45c\\n\\n- \uc2dd\ub2e8, \uc6b4\ub3d9 \uc720\uc9c0\\n- 6\uc2dc \uae30\uc0c1\\n- \ub3c5\uc11c, \ub124\ud2b8\uc6cc\ud0b9\uc740 \ucde8\uc5c5\uc5d0 \ubc29\ud574\ub418\uc9c0 \uc54a\ub294 \uc120\uc5d0\uc11c \uc870\uc808\\n- \ub9ac\ud2b8\ucf54\ub4dc \ub9e4\uc77c 1\ubb38\uc81c \ud480\uae30\\n- \uba74\uc811 \uc900\ube44\\n- \uc878\uc5c5 \uc791\ud488 \uc804\uc2dc\ud68c \ub9c8\ubb34\ub9ac\\n- \uc2a4\ud130\ub514X\\n\\n10\uc6d4 \ubaa9\ud45c\uc5d0 \ube44\ud574 \ub9e4\uc6b0 \ub2e8\uc21c\ud574\uc84c\ub2e4. \uba74\uc811\uc744 \uc55e\ub450\uace0 \uc788\ub2e4 \ubcf4\ub2c8 \ub9ce\uc740 \uac83\uc744 \ud560 \uc218 \uc5c6\ub294 \uc0c1\ud0dc\uc774\ub2e4.\\n\\n\ub098\ub984 \uad6c\uccb4\uc801\uc73c\ub85c \uc801\uc5c8\ub294\ub370 \ub610 \ub098 \uc790\uc2e0\uacfc\uc758 \uc57d\uc18d\uc744 \ubabb \uc9c0\ud0ac\uae4c \ub450\ub835\uae30\ub3c4 \ud558\ub2e4. \uadf8\ub798\ub3c4 \ub0b4 \ube14\ub85c\uadf8\uc5d0 \ubc15\uc81c\ud588\uc73c\ub2c8 \ub098 \uc790\uc2e0\uc5d0\uac8c \ubd80\ub044\ub7fd\uc9c0 \uc54a\uc73c\ub824\uba74 \uace0\uccd0\uc57c \ud558\uc9c0 \uc54a\uc744\uae4c. \uacb0\uacfc\uac00 \uc5b4\ub5bb\ub4e0 \ub178\ub825\uc740 \ud574\ubcf4\uc790."},{"id":"/open","metadata":{"permalink":"/en/blog/open","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2024-10-27-open/2024-10-27-open.md","source":"@site/blog/2024-10-27-open/2024-10-27-open.md","title":"\ud83d\udcd6 \uc624\ud508","description":"\ube44\uc988\ub2c8\uc2a4 \ud328\uad8c\uc758 \uc5f4\uc1e0","date":"2024-10-27T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \uc624\ud508","date":"2024-10-27","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/open","description":"\ube44\uc988\ub2c8\uc2a4 \ud328\uad8c\uc758 \uc5f4\uc1e0"},"unlisted":false,"prevItem":{"title":"\u272810\uc6d4 \ud68c\uace0","permalink":"/en/blog/Oct-Recap"},"nextItem":{"title":"\ud83c\udfac \ucc98\uc74c\uc774\uc790 \ub9c8\uc9c0\ub9c9 \uae00\ub610 \uc2dc\uc791","permalink":"/en/blog/starting-geulddo"}},"content":":::info\\n\ud55c\ube5b\ubbf8\ub514\uc5b4 \\\\<\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4\\\\> \ud65c\ub3d9\uc744 \uc704\ud574\uc11c \ucc45\uc744 \uc81c\uacf5\ubc1b\uc544 \uc791\uc131\ub41c \uc11c\ud3c9\uc785\ub2c8\ub2e4.\\n:::\\n\\n![\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4 2024](../assets/I-am-reviewer-2024.jpg)\\n\\n## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/open.png)](https://product.kyobobook.co.kr/detail/S000214413027)\\n\\n- \uc81c\ubaa9: \uc624\ud508\\n- \uc800\uc790: \ubc15\uc218\ud64d\\n- \ucd9c\ud310\uc0ac: \ud55c\ube5b\ubbf8\ub514\uc5b4\\n- \ucd9c\uac04: 2024-09-30\\n\\n{/* truncate */}\\n\\n## Intro\\n\\n\uc624\ud508 \uc18c\uc2a4\ub97c \ucc98\uc74c \uc54c\uac8c \ub41c \ub0a0\uc774 \uc5b8\uc81c\uc600\uc744\uae4c \uc0dd\uac01\uc744 \ud574\ubcf4\uba74 \uadf8\ub54c\uc600\ub2e4. \uc911\ud559\uc0dd \ub54c \ub2e8\uc21c\ud788 \ud504\ub85c\uadf8\ub798\uba38\uac00 \ub418\uaca0\ub2e4\ub294 \ub9c8\uc74c\uc744 \uba39\uace0 \ud55c\ube5b\ubbf8\ub514\uc5b4\uc5d0\uc11c \ucd9c\uac04\ud55c **[\ud504\ub85c\uadf8\ub798\uba38\ub85c \uc0ac\ub294 \ubc95](https://product.kyobobook.co.kr/detail/S000001223874)**\uc744 \uc77d\uc5c8\uc744 \ub54c. \uadf8 \ucc45\uc5d0\ub294 \ub9ac\ub204\uc2a4 \ud1a0\ub974\ubc1c\uc2a4, \ub9ac\ucc28\ub4dc \uc2a4\ud1a8\ub9cc \ub4f1 \uc815\ub9d0 \ub300\ub2e8\ud558\uc2e0 \uc120\ubc30 \ud504\ub85c\uadf8\ub798\uba38 \ubd84\ub4e4\uc758 \uc598\uae30\uac00 \ub098\uc624\ub294\ub370 \uadf8\ub54c **\uc624\ud508 \uc18c\uc2a4**\ub77c\ub294 \uc6a9\uc5b4\ub97c \ucc98\uc74c \uc54c\uac8c \ub410\ub358 \uac83 \uac19\ub2e4. \ubcc4\ub3c4\uc758 \uc598\uae30\uc9c0\ub9cc \uae30\ud68c\uac00 \ub41c\ub2e4\uba74 \uc77d\uc5b4\ubcf4\ub294 \uac78 \ucd94\ucc9c\ud55c\ub2e4. \uc790\ubc14, C++\uc744 \ub9cc\ub4e0 \uc0ac\ub78c\ubd80\ud130 \uc774\uba54\uc77c \ub9cc\ub4e0 \uc0ac\ub78c, \uc560\ud50c \ucef4\ud4e8\ud130 \ub9cc\ub4e0 \uc0ac\ub78c \ub4f1 \uc774 \uc5c5\uacc4\uc5d0 \ubab8 \ub2f4\uace0 \uc788\uc73c\uba74 \uc54c \uc218 \uc788\ub294 \uc804\uc124\ub4e4\uc758 \uc778\ud130\ubdf0\uac00 \ub2f4\uaca8 \uc788\ub2e4. \ucc45 \uc77d\uc744 \ub2f9\uc2dc\uc5d0\ub294 \uc544\ubb34\uac83\ub3c4 \ubaa8\ub974\ub294 \ud559\uc0dd\uc774\uc5c8\uae30\uc5d0 \ubcc4 \uad00\uc2ec \uc5c6\uc5c8\uc9c0\ub9cc, \ucef4\ud4e8\ud130\uacf5\ud559\uacfc\ub97c \uc9c4\ud559\ud558\uba74\uc11c \ubaa8\ub97c \uc218\uac00 \uc5c6\ub294 \ub2e8\uc5b4\uac00 \ub410\ub2e4. \uc624\ud508 \uc18c\uc2a4\uc5d0 \uae30\uc5ec\ub97c \ud574\ubcf4\ub294 \uac83\uc744 \ubaa9\ud45c\ub85c \ud558\ub294 \uc804\uacf5 \uacfc\ubaa9\ub3c4 \ub4e4\uc740 \uc801\ub3c4 \uc788\uc5c8\ub2e4. \uadf8\ub9ac\uace0 \uc9c0\uae08\ub3c4 \uc5ec\uc804\ud788 \ub2e4\uc591\ud55c \ucee4\ubba4\ub2c8\ud2f0, \uc18c\uc15c \ubbf8\ub514\uc5b4\ub97c \ud1b5\ud574 \uc624\ud508 \uc18c\uc2a4\uc758 \uc88b\uace0 \uc548 \uc88b\uc740 \uc18c\uc2dd\ub4e4\uc744 \ub4e3\uae30\ub3c4 \ud55c\ub2e4. \uadf8\ub798\uc11c \uadf8\ub7f0\uc9c0 \uc774 \ucc45\uc5d0\uc11c \uacfc\uc5f0 \uc5b4\ub5a4 \ub0b4\uc6a9\uc744 \ub2e4\ub8e8\ub294\uc9c0 \uad81\uae08\ud588\uace0, \ub9ac\ubdf0\uc5b4\ub85c \uc2e0\uccad\ud558\uac8c \ub410\ub2e4.\\n\\n## Book Review\\n\\n### \ud504\ub864\ub85c\uadf8\\n\\n\uc774 \ucc45\uc740 \uc804\ubc18\ubd80\ud130 \uc7ac\ubc0c\uac8c \ud504\ub864\ub85c\uadf8\ub85c \uc2dc\uc791\ub41c\ub2e4. \\n\\n> \uc624\ud508\uc740 \uc18d\uc148\uc774 \uc788\ub2e4.\\n\\n\uc815\ub9d0 \uc0dd\uac01\uc774 \ub9ce\uc774 \ub4e4\uac8c \ud558\ub294 \ubb38\uc7a5\uc774\ub2e4. \ucd5c\uadfc \uba87 \ub144 \ub3d9\uc548 IT \ud2b8\ub80c\ub4dc\ub97c \uc54c\uae30 \uc704\ud574 \uac01\uc885 \uc18c\uc15c \ubbf8\ub514\uc5b4\uc640 \ucee4\ubba4\ub2c8\ud2f0\ub97c \ubcf4\uba74\uc11c \ub2e4\uc591\ud55c \ub0b4\uc6a9\ub4e4\uc744 \uc811\ud588\ub2e4. \uadf8\uc911\uc5d0\ub294 \ub2f9\uc5f0\ud788 \uc624\ud508 \uc18c\uc2a4\uc640 \uad00\ub828\ub41c \ub0b4\uc6a9\ub3c4 \ub9ce\uc558\ub2e4. \ub0b4\uac00 \ucc98\uc74c \uc624\ud508 \uc18c\uc2a4\uc5d0 \ub300\ud574 \uc54c\uac8c \ub420 \ub54c\ub294 \uc790\uc2e0\uc758 \uc7ac\ub2a5\uacfc \uc9c0\uc2dd\uc744 \uacf5\uc720\ud558\uace0, \ub204\uad6c\ub098 \uc811\uadfc \uac00\ub2a5\ud558\uba70, \ud22c\uba85\ud558\ub2e4\ub294 \uc7a5\uc810\ub9cc \uc54c\uace0 \uc788\uc5c8\ub2e4. \uadf8\ub7ec\ub098 \uc624\ud508 \uc18c\uc2a4\uc5d0 \ub300\ud574 \uacf5\ubd80\ud560\uc218\ub85d \uc774\uba74\uc774 \uc874\uc7ac\ud55c\ub2e4\ub294 \uac78 \uae68\ub2ec\uc558\ub2e4. \uc774 \ucc45\uc744 \uc77d\uc73c\uba74\uc11c \uadf8\ub7f0 \ub0b4\uc6a9\ub4e4\uc744 \ub2e4\uc2dc \ud55c \ubc88 \uc0c1\uae30\uc2dc\ud0ac \uc218 \uc788\uc5c8\ub2e4. \\n\\n### \uc804\ubc18\uc801\uc778 \ucc45 \ub9ac\ubdf0\\n\\n\uc774 \ucc45\uc740 \ucd1d 5\uc7a5\uc73c\ub85c \uc774\ub8e8\uc5b4\uc838 \uc788\uc73c\uba70 \uc9e7\uc740 \uc5d0\uc138\uc774\uac00 \uc5ec\ub7ec \uac1c \ubaa8\uc5ec\uc788\ub294 \ud615\ud0dc\ub85c \uad6c\uc131\ub418\uc5b4 \uc788\ub2e4. \ud070 \uc8fc\uc81c\ub294 \uc788\uc73c\ub098 \uc5f0\uc18d\uc801\uc73c\ub85c \uc774\uc5b4\uc9c4 \uc774\uc57c\uae30\uac00 \uc544\ub2c8\ub77c\uc11c \ub9e4\uc77c\ub9c8\ub2e4 \uba87 \uc7a5\uc758 \uc5d0\uc138\uc774\ub97c \uc870\uae08\uc529 \uc77d\uc73c\uba74\uc11c \uc990\uae38 \uc218 \uc788\ub294 \ucc45\uc774\ub2e4. \uae30\uc220\uc801\uc73c\ub85c \uae4a\uc774 \uc788\ub294 \uc124\uba85\ubcf4\ub2e4\ub294, \uc624\ud508 \uc18c\uc2a4\uc5d0 \ub300\ud55c \uc774\ud574\ub97c \ub192\uc774\uae30\uc5d0 \uc88b\uc740 \ucc45\uc774\ub77c\uace0 \uc0dd\uac01\ud55c\ub2e4. \\n\\n1\uc7a5\uc5d0\uc11c\ub294 \'\uc624\ud508\'\uc774\ub77c\ub294 \uac1c\ub150\uc758 \ubaa8\uc2b5\ub4e4\uc744 \ub2e4\uc591\ud558\uac8c \uc124\uba85\ud55c\ub2e4. \uc624\ud508\uc740 \ud3c9\ub4f1\ud558\uc9c0\ub9cc \uacf5\ud3c9\ud558\uc9c0 \uc54a\ub2e4\ub294 \uac83\uc744 \uc0c1\uae30\uc2dc\ud0a4\uace0 \ucc45\uc744 \uc2dc\uc791\ud558\ub294 \ub290\ub08c\uc774\uc5c8\ub2e4. 1\uc7a5\uc5d0\uc11c\ub294 \uc624\ud508\uc758 \ubaa8\uc2b5\uc5d0 \ub300\ud574 \uc774\uc57c\uae30\ub97c \ud55c\ub2e4\uba74, 2\uc7a5\uc5d0\uc11c\ub294 \uc624\ud508\uc758 \ucf00\uc774\uc2a4\ub4e4\uc744 \uc124\uba85\ud558\uba74\uc11c \uc624\ud508\uc758 \uc774\uc720\uc5d0 \ub300\ud574 \uc54c\ub824\uc900\ub2e4.\\n\\n3\uc7a5\uc5d0\uc11c\ub294 \uc624\ud508\uc774 \uc65c \uac15\ud55c\uac00\uc5d0 \ub300\ud574 \uc598\uae30\ub97c \ud558\uba70, 4\uc7a5\uc5d0\uc11c\ub294 \uc624\ud508 \uc18c\uc2a4\uac00 \ub2e8\uc21c\ud788 \uc21c\uc218\ud558\uc9c0 \uc54a\ub2e4\ub294 \uac83\uc744 \ub9d0\ud55c\ub2e4. \uac1c\uc778\uc801\uc73c\ub85c 4\uc7a5\uc774 \uac00\uc7a5 \uc778\uc0c1 \uae4a\uc5c8\ub2e4. \ub9c8\uc774\ud06c\ub85c\uc18c\ud504\ud2b8\uc640 \ub9ac\ub205\uc2a4, \uc0bc\uc131\uacfc \uc778\ud154\uc758 IoT \uc624\ud508 \uc18c\uc2a4, \ub0b4\uac00 \uc9c0\uc6d0\ud574\ubcf4\uace0 \uc2f6\uc5c8\ub358 GSoC(Google Summer of Code) \ub4f1 \ub2e4\uc591\ud55c \uc0ac\ub840\ub4e4\uc774 \ub098\uc640\uc11c \uc7ac\ubbf8\uc788\uac8c \uc77d\uc5c8\ub2e4. \ud14c\ud06c \uc9c1\uad70\uc5d0 \uc788\uc73c\uc2e0 \ubd84\ub4e4\uc774\ub77c\uba74 \uc815\ub9d0 \uc7ac\ubc0c\uac8c \uc77d\uc744 \uc218 \uc788\ub294 \ud30c\ud2b8\ub77c\uace0 \uc0dd\uac01\ud55c\ub2e4.\\n\\n\ub9c8\uc9c0\ub9c9 5\uc7a5\uc5d0\uc11c\ub294 \uc624\ud508 \uc18c\uc2a4\uc758 \uc2dc\ub300\uc5d0\uc11c \uc5b4\ub5bb\uac8c \ub300\ucc98\ud574\uc57c \ud560\uc9c0 \uc0dd\uac01\ud558\uac8c \ub9cc\ub4e0\ub2e4. \ud2b9\ud788 \uc624\ud508 \uc18c\uc2a4 \uac1c\ubc1c\uc790 \ucc44\uc6a9\uc5d0 \ub300\ud55c \ub0b4\uc6a9\uc774 \uc778\uc0c1 \uae4a\uc5c8\ub2e4. \uc815\ub9d0 \uc624\ud508 \uc18c\uc2a4\uc5d0 \uc5f4\uc815\uc801\uc774\uace0 \uc7ac\ub2a5\uc774 \uc788\ub2e4\uba74 \uba87 \ubc88\uc758 \uba54\uc2dc\uc9c0\ub9cc\uc73c\ub85c \ucc44\uc6a9\uc774 \ub420 \uc218\ub3c4 \uc788\ub2e4\ub294 \ub0b4\uc6a9\uc774 \uc815\ub9d0 \uc2e0\uae30\ud588\ub2e4. \ub610\ud55c, \ub0b4\ubd80 \ucc44\uc6a9 \ud504\ub85c\uc138\uc2a4\ub294 \uae30\uc5c5\ub9c8\ub2e4 \ub2e4\uc591\ud558\uaca0\uc9c0\ub9cc, \ud589\uc0ac\uac00 \ub05d\ub09c \ud6c4 \ucc44\uc6a9 \uac8c\uc2dc\ud310\uc5d0 JD\uac00 \uc801\ud600 \uc788\ub294 \uac83\uc774 \uc544\ub2cc \'\uc624\ud508 \uc18c\uc2a4 \uac1c\ubc1c\uc790\ub97c \ucc44\uc6a9\ud569\ub2c8\ub2e4\' \ubb38\uad6c \ud558\ub098\ub9cc \uc801\ud78c \ud3ec\uc2a4\ud2b8\uc787\uc73c\ub85c \ucc44\uc6a9 \ud504\ub85c\uc138\uc2a4\ub97c \uc2dc\uc791\ud55c\ub2e4\ub294 \uc810\ub3c4 \uc7ac\ubc0c\uc5c8\ub2e4. \\n\\n### \uc624\ud508 \uc18c\uc2a4\uc758 \uc774\uba74\uc5d0 \ub300\ud55c \ud1b5\ucc30\\n\\n\uc774 \ucc45\uc740 \uc624\ud508 \uc18c\uc2a4\uc758 \uae0d\uc815\uc801\uc778 \uba74\ubfd0 \uc544\ub2c8\ub77c \uadf8 \uc774\uba74 \ub610\ud55c \ub2e4\ub8e8\uba70, \ub300\uae30\uc5c5\uc774 \uc624\ud508 \uc18c\uc2a4\ub97c \uc5b4\ub5bb\uac8c \uc774\uc6a9\ud558\uace0 \uc788\ub294\uc9c0 \uc804\ub7b5\uc801 \uad00\uc810\uc5d0\uc11c\ub3c4 \ubcf4\uc5ec\uc900\ub2e4. \uc774\ub294 \uc624\ud508 \uc18c\uc2a4\ub97c \ub2e8\uc21c\ud55c \ucf54\ub4dc \uacf5\uc720\uc640 \uc811\uadfc\uc131\uc73c\ub85c\ub9cc \uc774\ud574\ud588\ub358 \uc0ac\ub78c\ub4e4\uc774 \u2018\uacf5\uc720\u2019\uc640 \u2018\uc790\uc720\u2019\uc640 \uac19\uc740 \ub0ad\ub9cc\uc801\uc778 \uac1c\ub150\uc744 \ub118\uc5b4, \uc624\ud508 \uc18c\uc2a4\uac00 \uc2e4\uc81c \ube44\uc988\ub2c8\uc2a4 \uc804\ub7b5\uc73c\ub85c \uc5b4\ub5bb\uac8c \ud65c\uc6a9\ud558\uace0 \uc788\ub294\uc9c0\uc5d0 \ub300\ud55c \uc778\uc0ac\uc774\ud2b8\ub97c \uc81c\uacf5\ud55c\ub2e4. \ud2b9\ud788 \uc624\ud508 \uc18c\uc2a4 \uc0dd\ud0dc\uacc4\uac00 \ub2e8\uc21c\ud788 \uae30\uc220\uc801\uc778 \uc2e4\ud5d8\uc7a5\uc774 \uc544\ub2cc, \uae00\ub85c\ubc8c \uae30\uc5c5\ub4e4\uc774 \uae30\uc220\uc801 \uc6b0\uc704\ub97c \uc810\ud558\uae30 \uc704\ud55c \uc804\ub7b5\uc801 \ub3c4\uad6c\ub85c \ubcc0\ud558\uace0 \uc788\uc74c\uc744 \ubcf4\uc5ec\uc8fc\ub294 \ubd80\ubd84\uc740 \uc774 \ucc45\uc758 \ud070 \uc7a5\uc810\uc774\ub77c\uace0 \uc0dd\uac01\ud55c\ub2e4.\\n\\n### \uc0bc\uc131\uc804\uc790\\n\\n\ub098\ub294 \uc194\uc9c1\ud788 \uc774 \ucc45\uc744 \uc77d\uc73c\uba74\uc11c \uc815\ub9d0 \ub180\ub77c\uc6e0\ub358 \uac83\uc740 \uc624\ud508 \uc18c\uc2a4\uc758 \uc774\uba74\ubcf4\ub2e4\ub294 \uc0bc\uc131\uc804\uc790\uc5c8\ub2e4. \uc704\uc5d0\uc11c\ub3c4 \uc5b8\uae09\ud588\ub4ef\uc774 \uc624\ud508 \uc18c\uc2a4\uc758 \uc774\uba74\uc5d0 \ub300\ud574\uc11c\ub294 \uc18c\uc15c \ubbf8\ub514\uc5b4, \ucee4\ubba4\ub2c8\ud2f0\ub97c \ud1b5\ud574 \uc774\ubbf8 \uc54c\uace0 \uc788\uc5c8\ub2e4. \ub098\ub294 \ucde8\uc5c5 \uc900\ube44\uc0dd\uc758 \uc785\uc7a5\uc5d0\uc11c \uc0bc\uc131\uc804\uc790\ub294 \uc5c4\uccad\ub09c \uae30\uc5c5\uc774\ub77c\uace0 \uc0dd\uac01\ud558\uc9c0\ub9cc \ub2e4\ub978 \ud14c\ud06c \uae30\uc5c5\uc5d0 \ube44\ud574\uc11c\ub294 \uc544\uc9c1 \ubcf4\uc218\uc801\uc778 \uae30\uc5c5\uc774\ub77c\uace0 \uc0dd\uac01\ud588\ub2e4. \uadf8\ub7f0\ub370 \uc774 \ucc45\uc744 \uc77d\uc73c\uba74\uc11c \uc0bc\uc131\uc804\uc790\ub77c\ub294 \uae30\uc5c5\uc774 \uc624\ud508 \uc18c\uc2a4\uc5d0 \ub300\ud574 \uc5b4\ub5bb\uac8c \uc0dd\uac01\ud558\uace0 \uc788\ub294\uc9c0 \uc54c \uc218 \uc788\uc5c8\uace0, \uc870\uae08\uc774\ub098\ub9c8 \ubcf4\uc218\uc801\uc774\ub77c\ub294 \uc0dd\uac01\uc744 \ubc97\uc744 \uc218 \uc788\uc5c8\ub2e4.\\n\\n\uc0bc\uc131\uc774 IoT\uc5d0\uc11c \uc624\ud508 \uc18c\uc2a4\ub97c \uc5b4\ub5bb\uac8c \ud65c\uc6a9\ud558\uace0 \uc788\ub294\uc9c0, \uadf8\ub9ac\uace0 \uc5b4\ub5a4 \ud504\ub85c\uc81d\ud2b8\ub97c \uc9c4\ud589\ud558\uace0 \uc788\ub294\uc9c0 \ub300\ub7b5\uc801\uc73c\ub85c \uc54c \uc218 \uc788\uc5c8\uc73c\uba70, \uc800\uc790\uc640 \uac19\uc740 \ub300\ub2e8\ud55c \uc0ac\ub78c\uc774 \uc0bc\uc131\uc5d0 \uc788\ub2e4\ub294 \uac83\ub9cc\uc73c\ub85c\ub3c4 \uadf8 \uadf8\ub8f9\uc5d0 \uc815\ub9d0 \ub4e4\uc5b4\uac00\uc11c \ud568\uaed8 \uc77c\uc744 \uacbd\ud5d8\ud574\ubcf4\uace0 \uc2f6\uc740 \uae30\uc5c5\uc774\ub77c\ub294 \uc0dd\uac01\uc774 \ub4e4 \uc815\ub3c4\uc600\ub2e4.\\n\\n## \ub300\uc0c1 \ub3c5\uc790\\n\\n\ub2e8\uc21c\ud788 \uc624\ud508 \uc18c\uc2a4\uc5d0 \uad00\uc2ec\uc774 \uc788\ub294 \uc0ac\ub78c\ubfd0\ub9cc \uc544\ub2c8\ub77c \uadf8 \uc0dd\ud0dc\uacc4\ub97c \uc774\ud574\ud574\ubcf4\uace0 \uc2f6\uc740 \uc0ac\ub78c\uc5d0\uac8c \ucd94\ucc9c\ud558\ub294 \ucc45\uc774\ub2e4. \uc704\uc5d0\uc11c\ub3c4 \uc124\uba85\ud588\ub4ef\uc774 \uc800\uc790\uc758 \uacbd\ud5d8\uc744 \ud480\uc5b4 \uc124\uba85\ud558\ub294 \ubd80\ubd84\uc740 \uc815\ub9d0 \uc7ac\ubc0c\uc5c8\uace0 \uc778\uc0c1\uc801\uc774\uc5c8\ub2e4. \uc624\ud508 \uc18c\uc2a4\uc5d0 \ub300\ud55c \uc0ac\uc804 \uc9c0\uc2dd\uc774 \uc788\uc73c\uba74 \uc77d\ub294\ub370 \ub354\uc6b1 \ud3b8\ud558\uaca0\uc9c0\ub9cc, \ud070 \ubd80\ub2f4 \uc5c6\uc774 \uc811\uadfc\ud560 \uc218 \uc788\uc73c\uba70, \uc624\ud508 \uc18c\uc2a4\uac00 \ub2e8\uc21c\ud788 \uae30\uc220\uc801\uc778 \ub3c4\uad6c\uac00 \uc544\ub2c8\ub77c \uac15\ub825\ud55c \uc804\ub7b5\uc774\ub77c\ub294 \uac83\uc744 \uae68\ub2ec\uc744 \uc218 \uc788\uc744 \uac83\uc774\ub2e4."},{"id":"/starting-geulddo","metadata":{"permalink":"/en/blog/starting-geulddo","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2024-10-05-starting-geulddo/2024-10-05-starting-geulddo.md","source":"@site/blog/2024-10-05-starting-geulddo/2024-10-05-starting-geulddo.md","title":"\ud83c\udfac \ucc98\uc74c\uc774\uc790 \ub9c8\uc9c0\ub9c9 \uae00\ub610 \uc2dc\uc791","description":"\uc815\ub9d0 \uba4b\uc9c0\uace0 \ub530\ub73b\ud55c \ucee4\ubba4\ub2c8\ud2f0","date":"2024-10-05T00:00:00.000Z","tags":[{"inline":true,"label":"Recap","permalink":"/en/blog/tags/recap"}],"hasTruncateMarker":false,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83c\udfac \ucc98\uc74c\uc774\uc790 \ub9c8\uc9c0\ub9c9 \uae00\ub610 \uc2dc\uc791","date":"2024-10-05","authors":"teddygood","tags":["Recap"],"draft":false,"slug":"/starting-geulddo","description":"\uc815\ub9d0 \uba4b\uc9c0\uace0 \ub530\ub73b\ud55c \ucee4\ubba4\ub2c8\ud2f0","keywords":["\uae00\ub610","\ucee4\ubba4\ub2c8\ud2f0","\uc131\uc7a5"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \uc624\ud508","permalink":"/en/blog/open"},"nextItem":{"title":"\ud83d\udcd6 AI \ud2b8\ub8e8\uc2a4","permalink":"/en/blog/AI-Truth"}},"content":"## \uae00\ub610 10\uae30\\n\\n### \uc9c0\uc6d0\\n\\n\uc774\ubc88\uc5d0 \uae00\ub610 \ucee4\ubba4\ub2c8\ud2f0\uc5d0 \ub4e4\uc5b4\uac08 \uc218 \uc788\ub294 \uae30\ud68c\uac00 \uc0dd\uacbc\ub2e4. \ub9e4 \uae30\uc218\ub9c8\ub2e4 \ucc38\uc5ec\ud558\uace0 \uc2f6\uc740 \ub9c8\uc74c\uc774 \uc788\uc5c8\uc9c0\ub9cc, \ubc14\uc058\uac70\ub098 \uccb4\ud06c\ub97c \uc548 \ud574\ub46c\uc11c \ub193\uce58\ub294 \uacbd\uc6b0\ub3c4 \uc788\uc5c8\uace0, \uc9c0\uc6d0\uc11c\ub97c \uc791\uc131\ud558\uba74\uc11c \'\ub098\' \uc790\uc2e0\uc744 \ub418\ub3cc\uc544 \ubcf4\ub294 \uac83\uc774 \ubd80\ub2f4\uc2a4\ub7ec\uc6e0\uc744 \ub54c\ub3c4 \uc788\uc5c8\ub2e4. \uc0ac\uc2e4 \uc774\ubc88\uc5d0\ub3c4 \uc9c0\uc6d0\uc11c\ub97c \uc791\uc131\ud558\ub294 \uac8c \uc27d\uc9c0 \uc54a\uc558\uc9c0\ub9cc, \uc774\ub807\uac8c\ub77c\ub3c4 \uc9c0\uae08\uae4c\uc9c0\uc758 \ub0b4\uac00 \ubb58 \ud588\uace0, \uc5b4\ub5bb\uac8c \uc0b4\uc558\ub294\uc9c0 \ub2e4\uc2dc \ud55c \ubc88 \uc54c\uc544\ubcfc \uc218 \uc788\uc5b4\uc11c \uc815\ub9d0 \uc88b\uc740 \uacbd\ud5d8\uc774\uc5c8\ub2e4. \uadf8\ub9ac\uace0 \ubb34\uc5c7\ubcf4\ub2e4 \uc774\ubc88\uc774 \uae00\ub610\uc758 \ub9c8\uc9c0\ub9c9\uc774\uae30\uc5d0 \uaf2d \ud65c\ub3d9\ud574\ubcf4\uace0 \uc2f6\uc5c8\ub2e4.\\n\\n### \uae00\ub610\uc5d0\uc11c \uc774\ub8e8\uace0 \uc2f6\uc740 \ubaa9\ud45c\\n\\n\ucde8\uc5c5 \uc900\ube44 \uc911\uc774\ub77c \ubc14\uc058\uc9c0\ub9cc, \ucc98\uc74c\uc774\uc790 \ub9c8\uc9c0\ub9c9 \uae00\ub610 \ud65c\ub3d9\uc774\uae30\uc5d0 \ucd5c\ub300\ud55c \ub9ce\uc740 \ud65c\ub3d9\uc744 \ud574\ubcf4\ub824\uace0 \ud55c\ub2e4.\\n\\n- \ub2e4\uc591\ud55c \uc18c\ubaa8\uc784 \ucc44\ub110\uc5d0 \ucc38\uc5ec\\n- \ub2e4\uc591\ud55c \ubd84\ub4e4\uacfc \ucee4\ud53c\ucc57 \ub610\ub294 \ubaa8\uac01\ucf54, \ubaa8\uac01\uae00\\n- \uac74\uac15\ud55c \uc0b6\uc744 \uc704\ud55c \uc2b5\uad00 \ud615\uc131\\n\\n\\n9\uc6d4\uc740 \uc815\ub9d0 \ud798\ub4e0 \ub2ec\uc774\uc5c8\ub2e4. \ucde8\uc5c5 \uc900\ube44\ub85c \uc778\ud55c \uc628\uac16 \uc2a4\ud2b8\ub808\uc2a4\uc5d0 \uc2dc\ub2ec\ub9ac\uba70, \ud3c9\uc18c \uc9c0\ud0a4\ub358 \ub8e8\ud2f4\uc740 \ubb34\ub108\uc838 \ubc84\ub838\ub2e4. \uc77c\uc8fc\uc77c\uc5d0 \uc138 \ubc88\uc529 \ubc30\ub2ec \uc74c\uc2dd\uc744 \uc2dc\ucf1c \uba39\uc5c8\uace0, \ud63c\uc790 \uc220\uc744 \ub9c8\uc2dc\ub294 \ub0a0\ub3c4 \uc7a6\uc558\ub2e4. \uc6b0\uc5f0\ud788 \uae00\ub610\uc5d0 \ub4e4\uc5b4\uc62c \uc218 \uc788\uac8c \ub410\uace0, \uc774\ub300\ub85c\ub294 \uc548 \ub418\uaca0\ub2e4\ub294 \uc0dd\uac01\uc5d0 10\uc6d4\uc5d0\ub294 \uae00\ub610\uc758 \ub2e4\uc591\ud55c \uc18c\ubaa8\uc784 \ucc44\ub110\uc5d0 \ucc38\uc5ec\ud558\uc5ec \ub2e4\uc2dc \uac74\uac15\ud55c \uc2b5\uad00\uc744 \ub9cc\ub4e4\uae30\ub85c \ub2e4\uc9d0\ud588\ub2e4.\\n\\n\uadf8 \uc911 \ud558\ub098\ub85c \uc608\uc804\uc758 \ubfcc\ub4ef\ud568\uacfc \uc131\ucde8\uac10\uc744 \ub418\ucc3e\uace0\uc790 \ub2e4\uc2dc 6\uc2dc \uae30\uc0c1\uc5d0 \ub3c4\uc804\ud558\uace0 \uc788\ub294\ub370, \uc608\uc804\uc5d0\ub294 \uadf8\ub807\uac8c \uc26c\uc6e0\ub358 \uc77c\uc774 \uc694\uc998\uc5d0\ub294 \uc65c \uadf8\ub807\uac8c \uc5b4\ub824\uc6b4\uc9c0 \ubaa8\ub974\uaca0\ub2e4. \uc608\uc0c1 \uc678\ub85c \uc774\ubc88\uc8fc\uc5d0 \uac00\uc7a5 \uc21c\uc870\ub86d\uac8c \uc798 \ud574\ub0bc \uc218 \uc788\uc744 \uac83 \uac19\uc558\ub5a4 \uae30\uc0c1 \uc2b5\uad00\uc774 \ubc8c\uc368 \uc2e4\ud328\ud588. \uadf8\ub807\uc9c0\ub9cc \ud3ec\uae30\ud558\uc9c0 \uc54a\uace0, \uacc4\uc18d \ub3c4\uc804\ud560 \uc608\uc815\uc774\ub2e4. \ube44\ub85d \uac11\uc790\uae30 \ub108\ubb34 \ub9ce\uc740 \uac83\uc744 \ud558\ub824 \ud574\uc11c \uc2a4\ud2b8\ub808\uc2a4\ub97c \ubc1b\uace0 \uc788\uc9c0\ub9cc, \ud558\ub8e8\ud558\ub8e8\uac00 \ud589\ubcf5\ud558\uace0 \uac10\uc0ac\ud558\ub2e4.\\n\\n\uc2a4\uc2a4\ub85c \uc544\uc9c1 \uc804\ubb38\uc131\uc774 \ubd80\uc871\ud558\ub2e4\uace0 \uc0dd\uac01\ud558\uc5ec \ucee4\ud53c\ucc57\uc740 \uc544\uc9c1 \uc870\uae08 \ub450\ub835\uae30\ub3c4 \ud558\ub2e4. \uacfc\uc5f0 \ub0b4\uac00 1:1\ub85c \ub300\ud654\ub97c \ud558\uac70\ub098 \uc5ec\ub7ec \uc0ac\ub78c\uc744 \ub9cc\ub0ac\uc744 \ub54c, \uacfc\uc5f0 \uc5b4\ub5a4 \uc774\uc57c\uae30\ub97c \ub098\ub20c \uc218 \uc788\uc744\uc9c0 \uace0\ubbfc\uc774 \ub41c\ub2e4. \ub2e4\ub978 \ubd84\ub4e4\uc740 \ub300\ubd80\ubd84 \uc9c1\uc7a5\uc778\uc774\uc2dc\uae30 \ub54c\ubb38\uc5d0 \ub098\uc758 \uc9c0\uc2dd\uc774\ub098 \uacbd\ud5d8\uc774 \ubd80\uc871\ud558\uc5ec \ub300\ud654\uac00 \uc6d0\ud65c\ud558\uac8c \uc548 \uc774\ub904\uc9c8 \uc218\ub3c4 \uc788\uc9c0 \uc54a\uc744\uae4c \uac71\uc815\ub3c4 \ub41c\ub2e4.\\n\\n\ud558\uc9c0\ub9cc \uc774\ub7f0 \uace0\ubbfc\uc740 \ud56d\uc0c1 \uc0c8\ub85c\uc6b4 \uc0ac\ub78c\ub4e4\uc744 \ub9cc\ub0a0 \ub54c\ub9c8\ub2e4 \ubc18\ubcf5\ub41c\ub2e4\uace0 \ub290\ub080\ub2e4. \uadf8\ub807\uae30\uc5d0 \uc774\ubc88 \uae00\ub610\ub97c \uc798 \ud65c\uc6a9\ud558\uc5ec \uc774\ub97c \uadf9\ubcf5\ud558\uace0, \uc870\uae08 \ub354 \uc801\uadf9\uc801\uc73c\ub85c \uc18c\ud1b5\ud574\ubcf4\ub824\uace0 \ud55c\ub2e4. \uc804\ubb38\uc131\uc5d0 \ub300\ud55c \ubd80\ub2f4 \ub0b4\ub824\ub193\uace0, \uc644\ubcbd\ud558\uc9c0 \uc54a\uc544\ub3c4 \uad1c\ucc2e\ub2e4\ub294 \ub9c8\uc74c\uc73c\ub85c \ud65c\ub3d9\ud574\ubcf4\ub824\uace0 \ud55c\ub2e4. \uc5b4\ucc28\ud53c \ub09c \uc544\uc9c1 \ub300\ud559\uc0dd\uc774\uace0, \uc9c1\uc7a5\uc778\ub3c4 \uc544\ub2c8\uae30\uc5d0 \ub098\uc5d0\uac8c \ud070 \uae30\ub300\uac00 \uc5c6\uc744 \uac83\uc774\ub2e4. \uadf8\ub7ec\ub2c8 \ub108\ubb34 \uac71\uc815\ud558\uc9c0 \ub9d0\uace0, \uc774 \uc2dc\uac04\uc744 \ucda9\ubd84\ud788 \uc990\uae30\uc790.\\n\\n\uadf8\ub9ac\uace0 \uc5b4\uca4c\uba74 \ub0b4\uac00 \uac00\uc9c4 \uc791\uc740 \uc9c0\uc2dd\uc774\ub098 \uacbd\ud5d8\ub3c4 \ub204\uad70\uac00\uc5d0\uac8c \ud070 \ub3c4\uc6c0\uc774 \ub420 \uc218\ub3c4 \uc788\uc9c0 \uc54a\uc744\uae4c? \ub098\ub3c4 \uc0c1\ub300\ubc29\uc758 \uc774\uc57c\uae30\ub97c \ub4e3\uace0 \ubc30\uc6b0\uace0, \ub098 \ub610\ud55c \ub0b4 \uc774\uc57c\uae30\ub3c4 \ud574\ubcf4\uba74\uc11c \uc11c\ub85c\uac00 \ub354 \uc131\uc7a5\ud560 \uc218 \uc788\ub294 \uadf8\ub7f0 \uc778\uc5f0\uc744 \ub9cc\ub4e4\uc5b4 \ubcf4\uace0 \uc2f6\ub2e4.\\n\\n### \uc5b4\ub5a4 \uae00\uc744 \uc4f8 \uac83\uc778\uac00\\n\\n\ub09c \ube14\ub85c\uadf8\uc640 \uc704\ud0a4\ub97c \ub530\ub85c \uc6b4\uc601\ud558\uace0 \uc788\ub2e4. \uc608\uc804\ubd80\ud130 \uc704\ud0a4\uc5d0\ub294 CS \uc9c0\uc2dd\uc774\ub098 \uac04\ub2e8\ud55c \uc778\uc0ac\uc774\ud2b8\ub97c \ub2f4\uace0 \uc2f6\uc5c8\uace0, \ube14\ub85c\uadf8\uc5d0\ub294 \ub2e8\uc21c\ud788 \uc9c0\uc2dd\uc744 \uc804\ub2ec\ud558\ub294 \uae00\uc774 \uc544\ub2c8\ub77c \ub098\uc758 \uc0dd\uac01\uacfc \uacbd\ud5d8\uc774 \ub2f4\uae34 \uae00\uc744 \uc801\uc5b4\ubcf4\uace0 \uc2f6\uc5c8\ub2e4. \uadf8\ub9ac\uace0 \uc77d\uae30 \uc26c\uc6b0\uba74\uc11c\ub3c4 \uc9e7\uc740 \uae00\uc774 \uc544\ub2cc \uc870\uae08 \uae34 \uae00\uc744 \uc4f0\uace0 \uc2f6\uc5c8\ub2e4.\\n\\n![post-planning.png](../assets/recap/post-planning.png)\\n\\n\uc9c0\uae08\uae4c\uc9c0 \uc0dd\uac01\ud55c \uc8fc\uc81c\ub294 \uc704\uc640 \uac19\ub2e4. \ub300\ub7b5\uc801\uc73c\ub85c \uc774\ub7f0 \ub0b4\uc6a9\ub4e4\uc758 \uae00\uc744 \uc4f0\uc9c0 \uc54a\uc744\uae4c \uc2f6\ub2e4. ML\uacfc \uad00\ub828\ub41c \uba4b\uc9c4 \uae00\uc744 \uc368\ubcf4\uace0 \uc2f6\uae30\ub3c4 \ud55c\ub370 \uc544\uc9c1\uae4c\uc9c0\ub294 \uc5b4\ub5a4 \uc8fc\uc81c\ub85c \uae00\uc744 \uc4f0\uac8c \ub420\uc9c0 \ubaa8\ub974\uaca0\ub2e4. \ud65c\ub3d9\ud558\ub2e4 \ubcf4\uba74 \uc88b\uc740 \uae00\uc774 \ub5a0\uc624\ub974\uc9c0 \uc54a\uc744\uae4c \uc2f6\ub2e4. \\n\\n## 10\uc6d4 \ubaa9\ud45c\\n\\n\ub9c8\uc9c0\ub9c9\uc73c\ub85c 10\uc6d4 \ubaa9\ud45c\ub97c \uac04\ub2e8\ud788 \uacf5\uc720\ud574\ubcf4\ub824\uace0 \ud55c\ub2e4! \ud56d\uc0c1 \uacc4\ud68d\ud558\uace0 \ubaa9\ud45c\ub97c \uc124\uc815\ud558\uace0 \ud589\ub3d9\ud558\ub824 \ud558\uc9c0\ub9cc, \ub0b4 \uc0dd\uac01\uacfc \ud604\uc2e4\uc740 \ud56d\uc0c1 \ub2e4\ub978 \uac83 \uac19\ub2e4. \uadf8\ub798\uc11c \uacc4\ud68d\uc744 \uc124\uc815\ud558\ub294 \uac83\uc774 \uc5b4\ub835\uc9c0\ub9cc \uc7ac\ubc0c\uae30\ub3c4 \ud558\ub2e4.\\n\\n- \ub9e4\uc77c \ud560 \uc218 \uc788\ub294 \uc2b5\uad00 \ub9cc\ub4e4\uae30\\n  - 6\uc2dc \uae30\uc0c1\\n  - \uc0c8\ubcbd 30\ubd84 \uacf5\ubcf5 \uc720\uc0b0\uc18c(\uac77\uae30, \ub6f0\uae30)\\n  - \uc8fc 5\uc77c \uc6e8\uc774\ud2b8(\uac00\uc2b4, \ub4f1, \uc5b4\uae68, \ud558\uccb4, \ud314)\\n  - ChatGPT\uc640 \ud568\uaed8 \ud558\ub294 \uc601\uc5b4 \uacf5\ubd80\\n  - \ub9ac\ud2b8\ucf54\ub4dc \ucd5c\uc18c 3\ubb38\uc81c \ud480\uae30\\n  - \uc790\uae30 1\uc2dc\uac04 \uc804\uc5d0 \ucd5c\uc18c 20p \ub3c5\uc11c\\n- \uc2a4\ud130\ub514 \ucc38\uc5ec\\n  - \uc54c\uace0\ub9ac\uc998 \uc2a4\ud130\ub514([Coursera Stanford Algorithms](https://www.coursera.org/specializations/algorithms))\\n  - ETL \uc2a4\ud130\ub514([\ub7ec\ub2dd \uc2a4\ud30c\ud06c](https://product.kyobobook.co.kr/detail/S000061353994))\\n  - DDIA \uc2a4\ud130\ub514([\ub370\uc774\ud130 \uc911\uc2ec \uc560\ud50c\ub9ac\ucf00\uc774\uc158 \uc124\uacc4](https://product.kyobobook.co.kr/detail/S000001766328))\\n- \uac1c\uc778 \uacf5\ubd80\\n  - \uc778\uacfc\ucd94\ub860\\n    - [\uc2e4\ubb34\ub85c \ud1b5\ud558\ub294 \uc778\uacfc\ucd94\ub860 with \ud30c\uc774\uc36c](https://product.kyobobook.co.kr/detail/S000212577153)\\n    - [\uc778\uacfc\uad00\uacc4 \ucd94\ub860\uacfc \ubc1c\uacac with Python](https://product.kyobobook.co.kr/detail/S000213021532)\\n  - \uc790\ubc14\uc2a4\ud06c\ub9bd\ud2b8\\n    - [\ucf54\uc5b4 \uc790\ubc14\uc2a4\ud06c\ub9bd\ud2b8](https://product.kyobobook.co.kr/detail/S000001766397)\\n  - [\uad6c\uae00 \ud074\ub77c\uc6b0\ub4dc AI \uc2a4\ud130\ub514\uc7bc](https://sites.google.com/view/2024-study-jams/ai?fbclid=IwY2xjawFGYPlleHRuA2FlbQIxMAABHfcnts6c4jK3Hu0Z54OCOf1N3TLFGWM7Kcs5r43nvVpXteynWljH_6o4oA_aem_wSwPx49oIGWNDlWfJcizow)\\n- \ud559\uad50 \uac15\uc758\\n  - \uad50\uc591 \uc911\uac04\uace0\uc0ac \ub300\uccb4 \ub808\ud3ec\ud2b8 \uc791\uc131\\n  - \uc218\ud559 \uacfc\ubaa9 \uacfc\uc81c\\n  - \uc218\ud559 \uacfc\ubaa9 \uc911\uac04\uace0\uc0ac \uacf5\ubd80(\ub9c8\uc9c0\ub9c9 \ud559\uae30\uc778\ub370\ub3c4 \uc218\ud559 \uacfc\ubaa9\uc744 \ub4e3\ub294 \ubbf8\uccd0\ubc84\ub9b0 \ub098)\\n- \uc878\uc5c5 \uc791\ud488\\n  - \ud608\uad00 \ub370\uc774\ud130 \uc218\uc9d1\\n  - \ub370\uc774\ud130 \uc804\ucc98\ub9ac(CLAHE, LDC(Lightweight Dense CNN for Edge Detection))\\n  - DVS(Dynamic Vision Sensor) \uce74\uba54\ub77c\ub85c \ucd2c\uc601\\n  - YOLOv8\uc744 \ud65c\uc6a9\ud558\uc5ec \ubc31\ud608\uad6c \uce74\uc6b4\ud305\\n  - \uacc4\uc18d \uc2e4\ud5d8\\n- \ub3c5\uc11c\\n  - \ubab0\uc785\uc758 \uc990\uac70\uc6c0(\ubc8c\uc368 3\ubc88\uc9f8 \uc77d\ub294 \ucc45)\\n  - \ud568\uaed8 \uc790\ub77c\uae30(\ub2e4\uc74c \uc77d\uc744 \ucc45)\\n- \ucde8\uc5c5 \uc900\ube44\\n  - \uc774\ub825\uc11c \uc791\uc131\\n  - \uc9c0\uae08\uae4c\uc9c0 \ud588\ub358 \ud559\uad50 \ud504\ub85c\uc81d\ud2b8 \ubcf5\uc2b5 \ud6c4 GitHub\uc5d0 \uae54\ub054\ud558\uac8c \uc62c\ub9ac\uae30\\n- \ud589\uc0ac \ucc38\uc5ec\\n  - \ub2f9\uadfc \ubc0b\uc5c5\\n  - \ud574\ucee4\ud1a4(\ud559\uad50 \ub0b4\ubd80\uc5d0\uc11c \uace7 \uc9c4\ud589 \uc608\uc815)\\n\\n\uc774\ub807\uac8c \uc801\uc5b4\ubcf4\ub2c8 \uc815\ub9d0 \ub9ce\uc740 \uac83 \uac19\ub2e4. \uc77c\ub2e8 \uc9c0\uae08 \ud558\uace0 \uc788\ub294 \uac83\ub4e4\uacfc \uc608\uc815\ub418\uc5b4 \uc788\ub294 \uac78 \uc801\uc5b4\ubd24\ub294\ub370 \ubcc0\uc218\uac00 \ub354 \uc788\uc744 \uc218\ub3c4 \uc788\ub2e4. \uc5b4\ub5bb\uac8c \ub420\uc9c0\ub294 \uc544\ubb34\ub3c4 \ubaa8\ub978\ub2e4. \uac1c\uc778\uc801\uc73c\ub85c\ub294 \ucc45\uc744 \ub354 \uc77d\uace0 \uc2f6\uc740\ub370 \uadf8\ub7f4 \uc218 \uc5c6\ub294 \uac8c \uc548\ud0c0\uae5d\ub2e4.\\n\\n\uadfc\ub370 \ub098\ub3c4 \uc774\uac78 \ub2e4 \uc5b4\ub5bb\uac8c \ud558\uace0 \uc788\ub294\uc9c0 \uc798 \ubaa8\ub974\uaca0\ub2e4. \uadf8\ub0e5 \uc5f4\uc2ec\ud788 \uc0b4\ub824\uace0 \ub178\ub825\ud558\ub2e4 \ubcf4\ub2c8 \uc774\ub807\uac8c \ub41c \uac83 \uac19\ub2e4. \uc2a4\ud130\ub514 \uc911\uc5d0\uc11c \ub7ec\ub2dd \uc2a4\ud30c\ud06c\ub791 \ub370\uc774\ud130 \uc911\uc2ec \uc560\ud50c\ub9ac\ucf00\uc774\uc158 \uc124\uacc4\ub294 \uc77d\uc5b4\ub3c4 \uc77d\uc5b4\ub3c4 \uc815\ub9d0 \uc5b4\ub835\ub2e4\ub294 \ub290\ub08c\uc744 \ubc1b\uace0 \uc788\ub2e4. \ub098\uc911\uc5d0 \ub610 \uc77d\uc5b4 \ubd10\uc57c \ud560 \uac70 \uac19\uc740 \ub290\ub08c. \uc778\uacfc\ucd94\ub860\uc740 \uc5bc\ub9c8\ub098 \ub354 \uc5b4\ub824\uc6b8\uae4c \uae30\ub300 \ubc18, \uac71\uc815 \ubc18\uc774\ub2e4.\\n\\n![Oct-planning.png](../assets/recap/Oct-planning.png)\\n\uc131\uc724\ub2d8\uaed8\uc11c \uc54c\ub824\uc8fc\uc2e0 [napkin.ai](https://www.napkin.ai/)\ub97c \ud65c\uc6a9\ud574\uc11c \uc2dc\uac01\ud654 \ud574\ubd24\ub2e4. \uc608\uc05c UI\ub97c \ub9cc\ub4e4\uc5b4 \uc900\ub2e4\ub294 \uc810\uc5d0\uc11c \uc55e\uc73c\ub85c \uc790\uc8fc \uc4f8 \uac83 \uac19\ub2e4. \uc88b\uc740 \ud234 \uc54c\ub824\uc8fc\uc154\uc11c \uac10\uc0ac\ud569\ub2c8\ub2e4! \\n\\n## \uc9c4\uc9dc \ub9c8\ubb34\ub9ac\\n\\n\uc774\ub807\uac8c \uae34 \uae00\uc744 \uc4f0\ub294 \uac74 \uc5ed\uc2dc \uc5b4\ub835\ub2e4\uace0 \ub290\ub080\ub2e4. \uadf8\ub807\uc9c0\ub9cc \uacc4\uc18d \uc4f0\ub2e4\ubcf4\uba74 \uc2e4\ub825\ub3c4 \ub298\uc9c0 \uc54a\uc744\uae4c? 6\uac1c\uc6d4 \ud6c4\uc758 \ub098\ub294 \uc5b4\ub5a4 \ubcc0\ud654\ub97c \uacaa\uc5c8\uc744\uae4c. \uae00\ub610\ub97c \ud1b5\ud574 \ubcc0\ud654\ub420 \ub0b4\uac00 \uae30\ub300\ub41c\ub2e4. \uadf8\ub54c\ub294 \uc5bc\ub9c8\ub098 \ub9ce\uc740 \uc778\uc5f0\uc744 \ub9cc\ub4e4\uc5c8\uc744\uae4c? \uc5b4\ub5a4 \uae00\uc744 \uc4f0\uace0 \uc788\uc744\uae4c? \uc5b4\ub5bb\uac8c \uc0b4\uace0 \uc788\uc744\uae4c? \uadf8\ub54c\ub97c \uae30\ub2e4\ub9ac\uba74\uc11c \ub354 \uc5f4\uc2ec\ud788 \uc0b4\uace0 \ub178\ub825\ud574\ubcf4\uc790. \ucda9\ubd84\ud788 \ud560 \uc218 \uc788\ub2e4.\\n\\n\ub9c8\uc9c0\ub9c9\uc73c\ub85c \uae00\ub610\uc57c \uc798 \ubd80\ud0c1\ud574!"},{"id":"/AI-Truth","metadata":{"permalink":"/en/blog/AI-Truth","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2024-09-30-AI-Truth/2024-09-30-AI-Truth.md","source":"@site/blog/2024-09-30-AI-Truth/2024-09-30-AI-Truth.md","title":"\ud83d\udcd6 AI \ud2b8\ub8e8\uc2a4","description":"\ub450\ub824\uc6c0\uc758 \uc2dc\ub300, \ub0c9\ucca0\ud558\uac8c \ub9c8\uc8fc\ud574\uc57c \ud560 \uac00\uc7a5 \uba85\ud655\ud55c \uc9c4\uc2e4","date":"2024-09-30T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 AI \ud2b8\ub8e8\uc2a4","date":"2024-09-30","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/AI-Truth","description":"\ub450\ub824\uc6c0\uc758 \uc2dc\ub300, \ub0c9\ucca0\ud558\uac8c \ub9c8\uc8fc\ud574\uc57c \ud560 \uac00\uc7a5 \uba85\ud655\ud55c \uc9c4\uc2e4"},"unlisted":false,"prevItem":{"title":"\ud83c\udfac \ucc98\uc74c\uc774\uc790 \ub9c8\uc9c0\ub9c9 \uae00\ub610 \uc2dc\uc791","permalink":"/en/blog/starting-geulddo"},"nextItem":{"title":"\ud83d\uddfa\ufe0f \uc0b6\uc758 \uc9c0\ub3c4","permalink":"/en/blog/Life-Map"}},"content":":::info\\n\ud55c\ube5b\ubbf8\ub514\uc5b4 \\\\<\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4\\\\> \ud65c\ub3d9\uc744 \uc704\ud574\uc11c \ucc45\uc744 \uc81c\uacf5\ubc1b\uc544 \uc791\uc131\ub41c \uc11c\ud3c9\uc785\ub2c8\ub2e4.\\n:::\\n\\n![\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4 2024](../assets/I-am-reviewer-2024.jpg)\\n\\n## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/AI-Truth.png)](https://product.kyobobook.co.kr/detail/S000214055422)\\n\\n- \uc81c\ubaa9: AI \ud2b8\ub8e8\uc2a4\\n- \uc800\uc790: \uc784\ubc31\uc900\\n- \ucd9c\ud310\uc0ac: \ud55c\ube5b\ubbf8\ub514\uc5b4\\n- \ucd9c\uac04: 2024-08-20\\n\\n{/* truncate */}\\n\\n## Intro\\n\\n2024\ub144 \ub124 \ubc88\uc9f8 \uae30\uc220\ucc45\uc774\ub2e4. \ucd5c\uadfc \ucde8\uc5c5 \uc900\ube44\ub3c4 \ud558\uace0, \uc778\uac15\ub3c4 \ub4e3\uace0, \uac01\uc885 \ud504\ub85c\uc81d\ud2b8\ub3c4 \ud558\uace0 \uc788\uae30\uc5d0 \uc2dc\uac04\uc774 \ubd80\uc871\ud558\uc5ec \uc77d\ub358 \uac1c\ubc1c \uc11c\uc801\uc758 \uc644\ub3c5\uc774 \uc5b4\ub824\uc6cc\uc9c0\uace0 \uc788\ub2e4. \uadf8\ub7fc\uc5d0\ub3c4 \ubd88\uad6c\ud558\uace0 \uc774\ubc88\uc5d0 \uc77d\uc5b4 \ubcf4\uace0 \uc2f6\uc740 \ucc45\uc774 \uc0dd\uaca8 \ub098\ub294 \ub9ac\ubdf0\uc5b4\uc5d0 \uc9c0\uc6d0\ud574\uc11c \uc77d\uac8c \ub410\ub2e4. \ub0b4\uac00 \uc800\uc790\uc758 \ud32c\uc774\uae30\ub3c4 \ud558\uace0, \uc778\uacf5\uc9c0\ub2a5\uc5d0 \ub300\ud55c \uad00\uc2ec\uc774 \ub9ce\uc544\uc11c \uc120\ud0dd\ud588\ub2e4.\\n\\n## Book Review\\n\\n### \uc804\ubc18\uc801\uc778 \ucc45 \ub9ac\ubdf0\\n\\n\ucc98\uc74c\uc5d0\ub294 \uc800\uc790\uac00 10\ub144 \ud6c4 \uc778\uacf5\uc9c0\ub2a5\uacfc \ud568\uaed8 \uc77c\ud558\ub294 \ubaa8\uc2b5\uc744 \uc0c1\uc0c1\ud55c \uc18c\uc124\ub85c \uc2dc\uc791\ud55c\ub2e4. \uc7ac\ubbf8\uac00 \uc5c6\uc744 \uc218\ub3c4 \uc788\ub294 \uc8fc\uc81c\uc778\ub370 \uc18c\uc124\ub85c \uc2dc\uc791\ud558\ub2c8 \uc5d0\ud53c\ud0c0\uc774\uc800\ub97c \uba39\uc73c\uba70 \uc785\ub9db\uc744 \ub3cb\uad6c\ub294 \ub290\ub08c\uc774\uc5c8\ub2e4. \uadf8\ub9ac\uace0 \uc2e4\uc81c\ub85c \uadf8\ub807\uac8c \ub420 \uac00\ub2a5\uc131\ub3c4 \ucda9\ubd84\ud788 \uc788\ub2e4\uace0 \uc0dd\uac01\ud574\uc11c \uc7ac\ubc0c\uac8c \uc77d\uc740 \ubd80\ubd84\uc774\ub2e4. 2\uc7a5\uc5d0\uc11c\ub294 \uc778\uacf5\uc9c0\ub2a5\uc758 \uc5ed\uc0ac\ub97c \ub2e4\ub8e8\ub294\ub370, \ub108\ubb34 \uae4a\uc9c0\ub3c4 \uc595\uc9c0\ub3c4 \uc54a\uac8c \uc801\uc808\ud55c \uc218\uc900\uc73c\ub85c \uc798 \ub2e4\ub904\uc8fc\uae30\uc5d0 \uc774\ud574\ud558\uae30 \ud3b8\ud588\ub2e4. \uc778\uacf5\uc9c0\ub2a5\uc774 \uc5b4\ub5bb\uac8c \ubc1c\uc804\ud574 \uc654\ub294\uc9c0 \ud655\uc778\ud560 \uc218 \uc788\ub294 \ubd80\ubd84\uc774\uc5c8\ub2e4.\\n\\n3\uc7a5\uc5d0\uc11c\ub294 \uc778\uacf5\uc9c0\ub2a5\uc774 \uc0ac\ub78c\uc744 \ub180\ub77c\uac8c \ud588\ub358 \uc0ac\ub840\ub4e4\uc774 \ub098\uc628\ub2e4. \ucd5c\uadfc \ubb38\uc81c\ub85c \ub5a0\uc624\ub974\uace0 \uc788\ub294 \ub525\ud398\uc774\ud06c\ubd80\ud130 \uc2dc\uc791\ud574, \uc608\uc804\uc5d0 \ub274\uc2a4\uc5d0\ub3c4 \ub9ce\uc774 \ub098\uc654\ub358 \ub525\ub9c8\uc778\ub4dc \ud300\uc758 \uc54c\ud30c\uace0, \ub2e8\ubc31\uc9c8 \uad6c\uc870\ub97c \uc608\uce21\ud558\ub294 \uc54c\ud30c\ud3f4\ub4dc\uae4c\uc9c0 \ub2e4\uc591\ud55c \uc0ac\ub840\ub97c \uc54c\ub824\uc900\ub2e4. 4\uc7a5\uc740 \uc774 \ucc45\uc758 \ud575\uc2ec\uc774\ub77c\uace0 \ub9d0\ud560 \uc218 \uc788\ub294\ub370, \ud2b9\ud788 \uac1c\ubc1c\uc790\uac00 \uc778\uc0ac\uc774\ud2b8\ub97c \uc5bb\uc744 \uc218 \uc788\ub294 \ub0b4\uc6a9\uc774 \ub9ce\uc774 \ub2f4\uaca8 \uc788\ub2e4\uace0 \uc0dd\uac01\ud55c\ub2e4. \uc5ec\uae30\uae4c\uc9c0 \uc77d\ub2e4 \ubcf4\uba74 \uc774 \ucc45\uc774 \uc815\ub9d0 \ucd5c\uadfc \uc774\uc288\uae4c\uc9c0 \uc798 \ubc18\uc601\ub418\uc5b4 \uc788\ub294 \ucc45\uc774\ub77c\ub294 \uc0ac\uc2e4\uc744 \uc54c \uc218 \uc788\uc744 \uac83\uc774\ub2e4.\\n\\n5\uc7a5\uc5d0\uc11c\ub294 \uc778\uacf5\uc9c0\ub2a5\uc774 \uc2e4\uc81c\ub85c \uc5b4\ub514\uc5d0, \uadf8\ub9ac\uace0 \uc5b4\ub5bb\uac8c \uc4f0\uc774\uace0 \uc788\ub294\uc9c0 \uc124\uba85\ud574\uc900\ub2e4. \ub9c8\uc9c0\ub9c9 6\uc7a5\uc740 \uc800\uc790\uc758 \uc0c1\uc0c1\uacfc \ud568\uaed8 \uc6b0\ub9ac\uac00 \ub450\ub824\uc6cc\ud574\uc57c \ud558\ub294 \uac83\uc5d0 \ub300\ud574 \ub2e4\ub8ec\ub2e4. 6\uc7a5\uc740 \uc0dd\uac01\ud558\uba74 \ud560\uc218\ub85d \ubb34\uc12d\uace0 \ub450\ub835\ub2e4. \ubb3c\ub860 \ud574\uacb0\ucc45\ub3c4 \ub2e4\ub8e8\uc9c0\ub9cc, \uc5ec\uae30\uc5d0 \uc801\uc5b4\ub450\uc9c0\ub294 \uc54a\uaca0\ub2e4. \ub9cc\uc57d \uad81\uae08\ud558\ub2e4\uba74 \uc9c1\uc811 \ucc45\uc744 \uc77d\uc5b4\ubcf4\uae38 \ubc14\ub780\ub2e4.\\n\\n### \uac1c\ubc1c\uc790\ub294 \ub300\uccb4\ub418\ub294\uac00\\n\\n\ud604\uc7ac \uac1c\ubc1c\uc790\ub85c \uc77c\uc744 \ud558\uace0 \uc788\uac70\ub098, \uac1c\ubc1c\uc790\ub97c \uafc8\uafb8\ub294 \ud559\uc0dd\uc774\ub77c\uba74, AI\uc758 \ube60\ub978 \ubc1c\uc804\uc73c\ub85c \uc778\ud574 \uac1c\ubc1c\uc790\ub77c\ub294 \uc9c1\uad70\uc774 AI\ub85c \ub300\uccb4\ub418\uc9c0 \uc54a\uc744\uae4c\ub77c\ub294 \uc0dd\uac01\uc744 \ud55c \ubc88\ucbe4\uc740 \ud574\ubcf4\uc9c0 \uc54a\uc558\uc744\uae4c \uc0dd\uac01\ud574\ubcf8\ub2e4. \ub098\ub294 \uc194\uc9c1\ud788 \ub9d0\ud558\uba74 \ub450\ub824\uc6c0\uc744 \ub290\uaf08\uc5c8\ub2e4. \ucd5c\uadfc Anthropic \uc0ac\uc758 Claude \uae30\ub2a5 \uc911 Artifacts \ub77c\ub294 \uae30\ub2a5\uc774 Feature Preview\ub85c \uc120\uacf5\uac1c \ub41c \uc801\uc774 \uc788\uc5c8\ub2e4. \ub09c \ud2b8\uc704\ud130\uc5d0\uc11c \uc54c\uac8c \ub418\uc5b4 \uadf8 \uae30\ub2a5\uc744 \ubc14\ub85c \uc368\ubd24\uc5c8\ub294\ub370 \uc815\ub9d0 \ub180\ub77c\uc6e0\ub2e4. \uc6d0\ud558\ub294 UI\uac00 \uc788\uc73c\uba74 \ucea1\ucc98\ub97c \ud55c \ud6c4, input\uc73c\ub85c \ub123\uace0 \'\uc774 UI\ucc98\ub7fc \ub9cc\ub4e4\uc5b4\uc918\'\ub77c\ub294 \ud55c \ubb38\uc7a5\ub9cc \uc785\ub825\ud558\uba74 \uc644\ubcbd\ud558\uc9c0\ub294 \uc54a\uc9c0\ub9cc \ube44\uc2b7\ud55c UI\ub97c \ub9cc\ub4e4\uc5b4\uc900\ub2e4. \ud504\ub808\uc784\uc6cc\ud06c\ub3c4 React\ub85c \ub9cc\ub4e4\uc5b4\ub2ec\ub77c\uace0 \ud558\uba74 \ub9cc\ub4e4\uc5b4 \uc8fc\uace0, \uc5b8\uc5b4\ub3c4 TypeScript\ub85c \ub9cc\ub4e4\uc5b4 \ub2ec\ub77c\uace0 \uc694\uccad\ub9cc \ud558\uba74 \ub9cc\ub4e4\uc5b4\uc900\ub2e4. \ucc98\uc74c\uc5d0\ub294 \ub0b4\uac00 \ud504\ub860\ud2b8\uc5d4\ub4dc\ub97c \uc8fc\ub85c \ud558\uc9c0 \uc54a\uc73c\ub2c8 \'\uc640, \ud504\ub860\ud2b8\uc5d4\ub4dc \uac1c\ubc1c\uc790\ub4e4 \uc870\uae08\ub9cc \ub354 \uc788\uc73c\uba74 \ub300\uccb4 \ub418\ub294 \uac70 \uc544\ub0d0?\'\ub77c\ub294 \ubc14\ubcf4 \uac19\uc740 \uc0dd\uac01\uc744 \uc870\uae08 \ud588\uc5c8\ub2e4. \uac70\uae30\ub2e4 \ud50c\ub7ec\uc2a4\ub85c \ud504\ub860\ud2b8\uc5d4\ub4dc\ub3c4 \uc774 \uc815\ub3c4\uc778\ub370 \ub2e4\ub978 \ubd84\uc57c\ub3c4 \uc774 \uc815\ub3c4\uba74 \uace7 \ub300\uccb4\uac00 \ub418\uc9c0 \uc54a\uc744\uae4c \ud558\ub294 \uc0dd\uac01\ub3c4 \ub4e4\uc5c8\ub2e4.\\n\\n\uadfc\ub370 \uc774 \ucc45\uc744 \uc77d\uc73c\uba74\uc11c \uadf8\ub7f0 \uc0dd\uac01\uc740 \uc0ac\ub77c\uc9c0\uac8c \ub410\ub2e4. \ub2f9\uc5f0\ud55c \uc598\uae30\uc9c0\ub9cc \uc778\uacf5\uc9c0\ub2a5\uc740 \uc544\uc9c1 \uc644\ubcbd\ud558\uc9c0 \uc54a\ub2e4. \ubb34\uc5b8\uac00 \ub9cc\ub4e4\uc5b4 \ub2ec\ub77c\uace0 \ud558\ub294 input\uc744 \ub123\uc73c\uba74 \uc644\ubcbd\ud558\uac8c output\uc774 \ub098\uc624\uc9c0 \uc54a\ub294\ub2e4. \uac00\ub054 \ud658\uac01(hallucination)\uc774 \uc788\uc744 \uc218\ub3c4 \uc788\uace0, \uc544\uc9c1\uc740 \uc778\uac04\uc774 \uc6d0\ud558\ub294\ub300\ub85c \uc644\ubcbd\ud558\uac8c \ub098\uc624\uc9c0\ub294 \uc54a\ub294\ub2e4. \ubb3c\ub860 \ucf54\ub4dc\uc758 \ud488\uc9c8 \uc790\uccb4\ub294 \uc774\ubbf8 \uc0ac\ub78c\uc774 \uc791\uc131\ud55c \ucf54\ub4dc\ubcf4\ub2e4 \ub354 \uc815\ud655\ud558\uace0 \uae54\ub054\ud55c \ucf54\ub4dc\ub97c \uc798 \ub9cc\ub4e4\uc5b4 \ub0b4\uace0 \uc788\ub2e4\uace0 \ub290\ub080\ub2e4. \uadf8\ub7ec\ub098 \ucf54\ub4dc\uc758 \ubc94\uc704\uac00 \ub113\uc5b4\uc9c0\uace0, \ucd94\uc0c1 \uc218\uc900\uc774 \uc62c\ub77c\uac00\uba74 \uc544\uc9c1\uc740 \uc0ac\ub78c\uc758 \uc2e4\ub825\uc5d0 \ubbf8\uce58\uc9c0 \ubabb\ud55c\ub2e4.  \\n\\n\uadf8\ub9ac\uace0 \uc55e\uc73c\ub85c \uac1c\ubc1c\uc790\uac00 \ud544\uc694\uac00 \uc5c6\uc5b4\uc9c4\ub2e4\ub294 \uac74 \uc560\ucd08\uc5d0 \ub9d0\uc774 \uc548 \ub418\ub294 \uc598\uae30\ub2e4. \uc5b4\ub5a4 \ud55c \uc0ac\ub78c\uc774 \uc778\uacf5\uc9c0\ub2a5\uc774 \uc791\uc131\ud55c \ucf54\ub4dc\ub97c \ud65c\uc6a9\ud558\uc5ec \ub3c4\ubc15, \uc0ac\uc774\ubc84 \ubc94\uc8c4 \ub4f1 \ube44\uc724\ub9ac\uc801\uc778 \uc18c\ud504\ud2b8\uc6e8\uc5b4\ub97c \ub9cc\ub4e4\uba74 \ub204\uac00 \ub9c9\uc744 \uac83\uc778\uc9c0 \uc0dd\uac01\uc744 \ud574\ubd10\uc57c \ud55c\ub2e4. \uc9c0\uae08\uc740 \ud604\uc5c5\uc5d0 \uacc4\uc2dc\ub294 \ubd84\ub4e4\uc774 \uc788\uc9c0\ub9cc, \uc55e\uc73c\ub85c \uc2e0\uc785 \uac1c\ubc1c\uc790\ub97c \ubf51\uc9c0 \uc54a\ub294\ub2e4\uba74 \ub9c9\uc744 \ubc29\ubc95\uc774 \uc5c6\ub2e4. \uadf8\uac83\ub3c4 \'\uc778\uacf5\uc9c0\ub2a5\uc774 \uc54c\uc544\uc11c \ud574\uc904 \uac70\uc57c\'\ub77c\uace0 \uc0dd\uac01\ud558\uba74\uc11c \ubbf8\ub798\ub97c \ub300\ube44\ud558\uc9c0 \uc54a\ub294 \uac83\uc740 \uc704\ud5d8\ud55c \uc0dd\uac01\uc774\ub2e4.\\n\\n\uacb0\uad6d \uc2e0\uc785 \uac1c\ubc1c\uc790\ub294 \uc801\uc740 \uc218\uac00 \ub410\ub4e0 \ub9ce\uc740 \uc218\uac00 \ub410\ub4e0 \uc9c0\uc18d\uc801\uc73c\ub85c \ubf51\uc544\uc57c \ud558\ub294 \uac83\uc774 \ub9de\ub2e4. \uadf8\ub7ec\ub2c8 \ud604\uc7ac \uc2dc\uc810\uc5d0\uc11c \uac1c\ubc1c\uc790\uac00 \ub300\uccb4\ub418\ub294 \uac83\uc740 \uc544\ub2c8\ub2e4. \uc774 \uc774\uc720\uac00 \uc544\ub2c8\ub354\ub77c\ub3c4 \ub300\uccb4 \ub418\uc9c0 \uc54a\ub294 \uc774\uc720\uc5d0 \ub300\ud574\uc11c\ub294 \ucc45\uc5d0\uc11c \ub354 \uc124\uba85\ud558\ub2c8 \ucc45\uc744 \uc77d\uc5b4\ubcf4\uae38 \ubc14\ub780\ub2e4.\\n\\n### \ub450\ub824\uc6c0\\n\\n\uadf8\ub798\ub3c4 \uc5b4\ub290\uc815\ub3c4\uc758 \ub450\ub824\uc6c0\uc740 \uac00\uc9c8 \ud544\uc694\uac00 \uc788\ub2e4. \uae30\uc220\uc758 \ubc1c\uc804\uc740 \uc810\uc810 \ub354 \ube68\ub77c\uc9c0\uace0 \uc788\uae30 \ub54c\ubb38\uc774\ub2e4. \uc774\ubc88\ub144\ub3c4 \ucd08\uc5d0\ub294 GPT-3.5 Turbo\ub97c \uc4f0\uace0 \uc788\uc5c8\ub294\ub370 \uc5b4\ub290\uc0c8 ChatGPT \uc11c\ube44\uc2a4\uc5d0\uc11c GPT-3.5 Turbo\ub294 \uc5c6\uc5b4\uc84c\ub2e4. \uadf8\ub9ac\uace0 \uc774\uc81c\ub294 GPT-4\ub97c \ub118\uc5b4\uc11c\uc11c GPT-4o, \uc9c0\uae08\uc740 o1-preview\uc640 o1-mini\uae4c\uc9c0 \ub098\uc628 \uc0c1\ud0dc\uc774\ub2e4. \uc815\ub9d0 \uae30\uc220\uc758 \ubc1c\uc804\uc774 \ube60\ub974\ub2e4\ub294 \uac83\uc744 \ub290\ub080\ub2e4. \ub610 \uc5b8\uc81c \ub2e4\ub978 \ubaa8\ub378\uc774 \uac11\uc790\uae30 \ub098\uc62c\uc9c0\ub3c4 \ubaa8\ub978\ub2e4. \uc774\uac8c \uc9c0\uae08 \uc6b0\ub9ac\uac00 \ubc14\ub77c\ubd10\uc57c \ud558\ub294 \ud604\uc2e4\uc774\ub2e4. \uc6b0\ub9ac\ub294 \ub450\ub824\uc6c0\uc744 \uac00\uc9c0\uba74\uc11c\ub3c4 \uc774 \uc2dc\ub300\ub97c \ub300\ube44\ud574\uc57c \ud55c\ub2e4.\\n\\n\uc774 \ucc45\uc744 \ub9c8\uc9c0\ub9c9\uc73c\ub85c \uc4f4 \uc2dc\uc810\uc774 \uc5b8\uc81c\uc778\uc9c0\ub294 \uc798 \ubaa8\ub974\uaca0\uc73c\ub098 \uc815\ub9d0 \ub180\ub790\ub358 \ubd80\ubd84\uc774 \uc788\uc5c8\ub2e4. \ud604\uc7ac\uc758 \uc778\uacf5\uc9c0\ub2a5 \ubaa8\ub378\uc740 \ub300\ubd80\ubd84 \ud655\ub960\uacfc \ud1b5\uacc4\ub97c \uae30\ubc18\uc73c\ub85c \ud558\ub294 \ubaa8\ub378\uc774\ub2e4. \uadf8\ub7f0\ub370 \uc774 \ucc45\uc5d0\uc11c\ub294 \ub2e8\uc21c\ud788 \ud655\ub960\uacfc \ud1b5\uacc4 \uae30\ubc18\uc73c\ub85c \'\ub9d0\ud558\uae30\'\ub97c \ub118\uc5b4 \uadf8 \ub2e4\uc74c \ub2e8\uacc4\uc778 \'\uc0dd\uac01\ud558\uae30\'\ub85c \ub098\uc544\uac08 \uc218 \uc788\ub2e4\uace0 \ub9d0\ud55c\ub2e4. \uc9c0\uae08 OpenAI\uc758 o1 \ubaa8\ub378\uc774 \uadf8 \ub2e8\uacc4\uc774\ub2e4. \uc694\uc998 \ucde8\uc5c5 \uc900\ube44 \ub54c\ubb38\uc5d0 \uc778\uacf5\uc9c0\ub2a5 \uacf5\ubd80\ub97c \uc7a0\uc2dc \ub193\uace0 \uc788\uc5c8\ub294\ub370, \uc774 \ucc45\uc744 \uc77d\uc73c\uba74\uc11c \ucd5c\uadfc \uc774\uc288\ub4e4\uc744 \ub9ce\uc774 \ub193\uce58\uace0 \uc788\ub2e4\ub294 \uac78 \uae68\ub2eb\uace0 \ubc18\uc131\ud558\uac8c \ub410\ub2e4. \\n\\n### \uadf8\ub7fc \ubb34\uc5c7\uc744 \uc900\ube44\ud574\uc57c \ud558\ub294\uac00\\n\\n\uc778\uacf5\uc9c0\ub2a5\uc740 \uacc4\uc18d \ubc1c\uc804\ud560 \uac70\uace0 \uc6b0\ub9ac\ub294 \uc774 \uc2dc\uc810\uc5d0\uc11c \ubb34\uc5c7\uc744 \ud560 \uc218 \uc788\uc744\uae4c? \uadf8\ub9ac\uace0 \ubb34\uc5c7\uc744 \ud574\uc57c \ud560\uae4c? \uba3c\uc800 \ud504\ub85c\uadf8\ub7a8 \uc804\ubd80\ub97c \ucf54\ub529\ud558\ub294 \uc0ac\ub78c\uc774 \ub420 \ud544\uc694\ub294 \uc5c6\uc5b4 \ubcf4\uc778\ub2e4. \ubc18\ubcf5\uc801\uc778 \uc77c\uc740 \ube60\ub974\uac8c \uc778\uacf5\uc9c0\ub2a5\uc5d0\uac8c \ub9e1\uae30\uace0, \uc870\uae08 \uc5b4\ub824\uc6b4 \uc5c5\ubb34\ub098 \uc778\uac04\uc774 \ud560 \uc218 \uc788\ub294 \ub2e4\ub978 \uc9c1\uad70\uacfc\uc758 \ucee4\ubba4\ub2c8\ucf00\uc774\uc158\uc744 \ud558\uac70\ub098, AI\uac00 \uc791\uc131\ud55c \ucf54\ub4dc\ub97c \ud655\uc778\ud558\uace0 \ud14c\uc2a4\ud2b8\ud558\ub294 \ub4f1\uc758 \uc77c\uc744 \ub354 \ud574\uc57c \ud55c\ub2e4\uace0 \uc0dd\uac01\ud55c\ub2e4. \uc989, \uc778\uacf5\uc9c0\ub2a5\uc744 \ub3c4\uad6c\ucc98\ub7fc \uc0ac\uc6a9\ud558\uba74\uc11c \ud480\uc5b4\uc57c \ud558\ub294 \ubb38\uc81c\ub97c \ud574\uacb0\ud558\ub294 \uac83\uc5d0 \ub354 \uc9d1\uc911\ud574\uc57c \ud55c\ub2e4\uace0 \ub290\ub080\ub2e4.\\n\\n\uadf8\ub7f0 \uc0ac\ub78c\uc740 \uc5b4\ub5bb\uac8c \ub420 \uc218 \uc788\uc744\uae4c? \uacfc\uac70\uc758 \uc218\uc900 \ub192\uc740 \uac1c\ubc1c\uc790\uc640 \ud06c\uac8c \ub2e4\ub978 \uac83\uc740 \uc5c6\uc5b4 \ubcf4\uc778\ub2e4. \ub9ce\uc740 \uae30\uc220\uc744 \uc54c\uc544\uc57c \ud558\uace0, \ubb38\uc81c\ub97c \ud574\uacb0\ud558\ub294 \uc218\ud559\uc801\uc778 \uc7ac\ub2a5, \ucf54\ub4dc\ub97c \uc77d\uace0 \uc774\ud574\ud558\ub294 \ub2a5\ub825, \ubab0\uc785\ud560 \uc218 \uc788\ub294 \uc9d1\uc911\ub825 \ub4f1\uc774 \ud544\uc694\ud560 \uac83\uc774\ub2e4. \uacb0\uad6d \uc778\uacf5\uc9c0\ub2a5\uc744 \ub354 \ube60\ub974\uac8c \uc774\uc6a9\ud560 \uc218 \uc788\uc5b4\uc57c \ud55c\ub2e4\ub294 \uac83\uc744 \uc81c\uc678\ud558\uba74 \ud06c\uac8c \ub2ec\ub77c\uc9c4 \uac83\uc740 \uc5c6\ub2e4.\\n\\n### \uc9c4\uc9dc \uac00\uce58\\n\\n\ucc45\uc5d0\uc11c \uac8c\uc784 \uac1c\ubc1c\uc790\ub85c \uc720\uba85\ud55c \uc874 \uce74\ub9e5\uc774 \ud55c \ub9d0\uc774 \uc778\uc6a9\ub418\uc5b4 \uc788\ub2e4. \uc774 \ubd80\ubd84\uc740 \ub108\ubb34 \uc778\uc0c1 \uae4a\uc5b4 \uac00\uc838\uc654\ub2e4.\\n\\n> \uac00\uce58\uc758 \uc6d0\ucc9c\uc774 \'\ucf54\ub529\'\uc778 \uc801\uc740 \ud55c \ubc88\ub3c4 \uc5c6\uc5c8\ub2e4. \uadf8\ub7ec\ub2c8 \uc0ac\ub78c\ub4e4\uc744 \ucf54\ub529\uc5d0 \ub108\ubb34 \uc9d1\ucc29\ud558\uc9c0 \ub9d0\uc544\uc57c \ud55c\ub2e4. \ubb38\uc81c\ub97c \ud574\uacb0\ud558\ub294 \uac83\uc774\uc57c\ub9d0\ub85c \ud575\uc2ec \uae30\uc220\uc774\ub2e4. \uc804\ud1b5\uc801\uc778 \ud504\ub85c\uadf8\ub798\ubc0d\uc774 \uc694\uad6c\ud558\ub294 \uaddc\uc728\uacfc \uc815\ud655\uc131\uc740 \uacc4\uc18d \uc18c\uc911\ud55c \uac00\uce58\ub85c \ub0a8\uc544 \uc788\uaca0\uc9c0\ub9cc, \uadf8\ub7f0 \uac83\ub4e4\uc740 \ub354 \uc774\uc0c1 \uc9c4\uc785\uc7a5\ubcbd \uc5ed\ud560\uc744 \ud558\uc9c4 \uc54a\uc744 \uac83\uc774\ub2e4. \ub098\ub294 \uc778\uacf5\uc9c0\ub2a5\uc774 \ub098\ubcf4\ub2e4 \ub354 \ub6f0\uc5b4\ub09c \ud504\ub85c\uadf8\ub798\uba38\ub77c\uba74 \uc778\uacf5\uc9c0\ub2a5\uc744 \uad00\ub9ac\ud558\ub294 \uc77c\uc744 \uc990\uac81\uac8c \ud560 \uc218 \uc788\uc744 \uac83 \uac19\ub2e4.\\n\\n\uac1c\ubc1c\uc790\ub294 \uacb0\uad6d \ubb38\uc81c\ub97c \ud574\uacb0\ud558\ub294 \uc9c1\uad70\uc774\ub2e4. \uc694\uc998 \uac01 \uae30\uc5c5\uc758 JD(Job Description)\uc744 \uc77d\uc73c\uba74\uc11c \ub098\ub3c4 \uccb4\uac10\ud558\uace0 \uc788\ub2e4. \uae30\uc5c5\ub4e4\ub3c4 \uacb0\uad6d \ubb38\uc81c\ub97c \uc798 \ud574\uacb0\ud560 \uc904 \uc54c\uace0 \uadf8\ub7f0 \uacbd\ud5d8\uc744 \ud574\ubcf8 \uc0ac\ub78c\uc744 \uc6d0\ud55c\ub2e4.\\n\\n\uc5b4\uca0c\ub4e0 \uc774\uc81c \ucf54\ub529\uc740 \uc544\ubb34\ub098 \ud560 \uc218 \uc788\ub294 \uc2dc\ub300\uac00 \ub410\ub2e4. \uc778\ud130\ub137\uc5d0\ub294 \uc88b\uc740 \ubb38\uc11c, \uac15\uc758 \ub4f1\uc774 \uc815\ub9d0 \ub9ce\uace0, \uadf8\ub9cc\ud07c \ucf54\ub529\uc744 \ud560 \uc218 \uc788\ub294 \uc0ac\ub78c\ub3c4 \ub9ce\uc544\uc9c0\uace0 \uc788\ub2e4. \uc608\uc804\uc5d0\ub294 \ucf54\ub529\uc774\ub77c\ub294 \uac83\uc744 \uc544\ubb34\ub098 \ud560 \uc218 \uc788\ub294 \uac8c \uc544\ub2c8\uc5c8\uaca0\uc9c0\ub9cc \ud604\uc7ac\ub294 \ub204\uad6c\ub098 \uacf5\ubd80\ub9cc \ud558\uba74 \ud560 \uc218 \uc788\ub294 \uc2dc\ub300\uac00 \ub410\ub2e4. \uc6d0\ub798 \ucc45 \ub9ac\ubdf0\ub97c \uc801\uc73c\uba74\uc11c \ucc45 \ub0b4\uc6a9\uc744 \uc778\uc6a9\ud558\uc9c0\ub294 \uc54a\uc9c0\ub9cc \uc9c4\uc9dc \ub9c8\uc9c0\ub9c9\uc73c\ub85c \ucc45\uc758 \ubb38\uc7a5\uc744 \ub610 \uc778\uc6a9\ud574\ubcf4\uaca0\ub2e4. \uc815\ub9d0 \uc778\uc0c1 \uae4a\uc5c8\ub358 \ubb38\uc7a5\uc774\ub2e4.\\n\\n> \ucf54\ub529\uc5d0 \uc9d1\ucc29\ud558\uc9c0 \ub9d0\ub77c. \uc790\uae30\uac00 \uc0ac\uc6a9\ud558\ub294 \uae30\uc220\uc5d0 \ub9e4\ub2ec\ub9ac\uc9c0 \ub9d0\ub77c. \uc778\uacf5\uc9c0\ub2a5\uc758 \ub3c4\uc6c0\uc744 \ubc1b\ub294 \uac83\uc744 \uc990\uac81\uac8c \uc0dd\uac01\ud574\uc57c \ud55c\ub2e4. \uc778\uacf5\uc9c0\ub2a5\uc758 \ub3c4\uc6c0\uc744 \ubc1b\uc544 \ubb38\uc81c\ub97c \ud574\uacb0\ud558\ub294 \uac83\uc774\uc57c\ub9d0\ub85c \uc9c4\uc815\ud55c \uac00\uce58\uc5d0 \ud574\ub2f9\ud558\uae30 \ub54c\ubb38\uc774\ub2e4. **\uc9c4\uc9dc \uac00\uce58\ub294 \ubb38\uc81c\ub97c \ud574\uacb0\ud558\ub294 \uac83\uc774\ub2e4.** \ucf54\ub529\uc774 \uc544\ub2c8\ub2e4.\\n\\n## \ub300\uc0c1 \ub3c5\uc790\\n\\n\uc774 \ucc45\uc740 \uac1c\ubc1c\uc790\uc5d0\uac8c \ucd94\ucc9c\ud558\ub294 \ucc45\uc774\ub2e4. \ud2b9\ud788 \uc778\uacf5\uc9c0\ub2a5 \uc9c1\uad70\uc5d0 \uc788\uc9c0 \uc54a\uc740 \uac1c\ubc1c\uc790\uc5d0\uac8c \ub354 \ucd94\ucc9c\ud558\ub294 \ucc45\uc774\ub2e4. \uc774 \ucc45\uc744 \uc77d\uc73c\uba74\uc11c \uc778\uacf5\uc9c0\ub2a5\uc774 \uc5b4\ub5bb\uac8c \ubc1c\uc804\ud574\uc654\ub294\uc9c0, \uc5b4\ub5bb\uac8c \uc0ac\uc6a9\ub418\uace0, \uc5b4\ub5bb\uac8c \uc0ac\uc6a9\ud574\uc57c \ud558\ub294\uc9c0\uc5d0 \ub300\ud574 \uc54c \uc218 \uc788\uc744 \uac83\uc774\ub2e4. \uadf8\ub9ac\uace0 \uc774 \ucc45\uc744 \uc77d\uc73c\uba74\uc11c \uc778\uacf5\uc9c0\ub2a5\uc774 \ubc1c\uc804\ud558\ub294 \uc2dc\ub300 \uc18d\uc5d0\uc11c \uac1c\ubc1c\uc790\ub85c\uc11c\uc758 \uac00\uce58\ub97c \ub192\uc774\ub294 \ubc29\ubc95\uc5d0 \ub300\ud574 \uc0dd\uac01\ud574\ubcfc \uc218 \uc788\uc744 \uac83\uc774\ub2e4."},{"id":"/Life-Map","metadata":{"permalink":"/en/blog/Life-Map","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2024-09-22-Life-Map/2024-09-22-Life-Map.md","source":"@site/blog/2024-09-22-Life-Map/2024-09-22-Life-Map.md","title":"\ud83d\uddfa\ufe0f \uc0b6\uc758 \uc9c0\ub3c4","description":"\uae00\ub610 10\uae30 \uc9c0\uc6d0\uc11c \uc791\uc131\uc744 \uc704\ud55c \uc0b6\uc758 \uc9c0\ub3c4","date":"2024-09-22T00:00:00.000Z","tags":[{"inline":true,"label":"Recap","permalink":"/en/blog/tags/recap"}],"hasTruncateMarker":false,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\uddfa\ufe0f \uc0b6\uc758 \uc9c0\ub3c4","date":"2024-09-22","authors":"teddygood","tags":["Recap"],"draft":false,"slug":"/Life-Map","description":"\uae00\ub610 10\uae30 \uc9c0\uc6d0\uc11c \uc791\uc131\uc744 \uc704\ud55c \uc0b6\uc758 \uc9c0\ub3c4","keywords":["\uc0b6\uc758 \uc9c0\ub3c4","\uae00\ub610","\uc131\uc7a5"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 AI \ud2b8\ub8e8\uc2a4","permalink":"/en/blog/AI-Truth"},"nextItem":{"title":"\ud83d\udcd6 \ub7ad\uccb4\uc778\uc73c\ub85c LLM \uae30\ubc18\uc758 AI \uc11c\ube44\uc2a4 \uac1c\ubc1c\ud558\uae30","permalink":"/en/blog/Developing-AI-services-with-LangChain"}},"content":"## \uc0b6\uc758 \uc9c0\ub3c4\\n\\n\uc774\ubc88\uc5d0 \uae00\ub610\ub97c \uc9c0\uc6d0\ud558\uba74\uc11c \uc9c0\ub098\uc628 \uc2dc\uac04\ub4e4\uc744 \ub418\ub3cc\uc544 \ubcf4\uac8c \ub418\ub294 \uae30\ud68c\ub97c \uc5bb\uac8c \ub410\ub2e4. \uadf8\ub3d9\uc548 \ub9ce\uc740 \uc77c\ub4e4\uc774 \uc788\uc5c8\uace0, \uadf8 \uacfc\uc815\uc5d0\uc11c \uc800 \uc790\uc2e0\uc774 \uc5b4\ub5bb\uac8c \ubcc0\ud574\uc654\ub294\uc9c0 \ub3cc\uc544\ubcfc \uc218 \uc788\uc5c8\ub2e4. \ub9e4\ubc88 \uc9e7\uc740 \uae30\uac04\uc758 \ud68c\uace0\ub9cc \ud588\uc9c0, \uc774\ub807\uac8c \uc0b6 \uc804\uccb4\ub97c \ud68c\uace0\ud574\ubcf4\ub294 \uac74 \uba87 \ubc88 \uc548 \ub418\ub294 \uadc0\uc911\ud55c \uacbd\ud5d8\uc778 \uac83 \uac19\ub2e4. \uc774 \uae00\uc5d0\uc11c\ub294 \uc9c0\uae08\uae4c\uc9c0 \uc5b4\ub5bb\uac8c \uc131\uc7a5\ud588\ub294\uc9c0 \uc804\uccb4\uc801\uc73c\ub85c \uc815\ub9ac\ud574\ubcf4\ub824 \ud55c\ub2e4. \uc774 \uae00\uc744 \uc4f8 \uc218 \uc788\uac8c \ub418\uc5b4 \uae00\ub610\uc5d0\uac8c \uac10\uc0ac\ud558\uace0, \ubcc4 \uac70 \uc544\ub2cc \ub098\uc758 \uc0b6\uc744 \uad81\uae08\ud558\uc5ec \uc774 \uae00\uc744 \uc77d\uc73c\uc2dc\ub294 \ubd84\ub4e4\uc5d0\uac8c\ub3c4 \uac10\uc0ac\ud558\ub2e4.\\n\\n### \uadf8\uc800 \ucef4\ud4e8\ud130\ub97c \uc88b\uc544\ud558\ub358 \ub098\\n\\n\uc5b4\ub838\uc744 \ub54c\ubd80\ud130 \ub098\ub294 \ucef4\ud4e8\ud130\ub97c \uc88b\uc544\ud558\ub358 \uc544\uc774\uc600\ub2e4. \ubc29\uacfc \ud6c4 \uc218\uc5c5\uc744 \uc2e0\uccad\ud558\uc5ec \ucef4\ud4e8\ud130\uc2e4\uc5d0\uc11c \ud30c\uc6cc\ud3ec\uc778\ud2b8, \uc5d1\uc140, \uc778\ud130\ub137, \uc6cc\ub4dc\ud504\ub85c\uc138\uc11c \uc790\uaca9\uc99d\uc744 \uc900\ube44\ud588\uace0, \uc790\uc5f0\uc2a4\ub7fd\uac8c \ucef4\ud4e8\ud130\uc640 \uce5c\ud574\uc9c8 \uc218 \uc788\uc5c8\ub2e4. \uc810\ucc28 \uac8c\uc784\uc5d0\ub3c4 \uad00\uc2ec\uc774 \uc0dd\uacbc\uc9c0\ub9cc, \uac8c\uc784\uc744 \uc798 \ud558\uc9c0\ub294 \ubabb \ud574\uc11c \uac8c\uc784\uc744 \ud50c\ub808\uc774 \ud558\ub294 \ud50c\ub808\uc774\uc5b4\uac00 \uc544\ub2cc \uac8c\uc784\uc744 \ub9cc\ub4dc\ub294 \ud504\ub85c\uadf8\ub798\uba38\uac00 \ub418\uace0 \uc2f6\uc5c8\ub2e4. \uc544\ub2c8\uba74 \ud574\ud0b9\uc744 \ub9c9\ub294 \ud654\uc774\ud2b8 \ud574\ucee4\uac00 \ub418\uace0 \uc2f6\uc5c8\ub2e4. \ub2e8\uc21c\ud788 \uc7ac\ubc0c\uc744 \uac83 \uac19\uace0 \uba4b\uc788\ub2e4\ub294 \uc774\uc720\uc600\ub2e4. \\n\\n\uc911\ud559\uc0dd\uc774 \ub418\uc790 \ubcf8\uaca9\uc801\uc73c\ub85c \ucef4\ud4e8\ud130 \uae30\ucd08 \uc9c0\uc2dd\uc5d0 \ub300\ud574 \uacf5\ubd80\ud558\uae30 \uc2dc\uc791\ud588\ub2e4. \ub2f9\uc2dc [\uc5f4\ud608 C \ud504\ub85c\uadf8\ub798\ubc0d](https://product.kyobobook.co.kr/detail/S000001589148)\uacfc [\uc2dc\uc2a4\ucf54 \ub124\ud2b8\uc6cc\ud0b9](https://product.kyobobook.co.kr/detail/S000000562247)\uc744 \uc595\uac8c \uc77d\uae30 \uc2dc\uc791\ud588\uc73c\uba70, [pwnable.kr](https://pwnable.kr/play.php) \ub610\ub294 [Lena\'s Reversing for Newbies](https://forum.tuts4you.com/files/file/1307-lenas-reversing-for-newbies/)\uc758 \ubb38\uc81c\ub97c \ud574\uc124 \ubcf4\uba74\uc11c \ud480\uae30\ub3c4 \ud558\uace0 [\ud574\ucee4\uc2a4\ucfe8](https://www.hackerschool.org/Sub_Html/HS_Community/index.html) \ucee4\ubba4\ub2c8\ud2f0\ub97c \ud1b5\ud574 \ub2e4\uc591\ud55c \uc815\ubcf4\ub97c \uc5bb\uc5c8\uc5c8\ub2e4. \uadf8\ub7ec\ub2e4 \ub354 \uc804\ubb38\uc801\uc73c\ub85c \uacf5\ubd80\ud574\ubcf4\uace0 \uc2f6\uc5b4 \ub514\uc9c0\ud138 \ubbf8\ub514\uc5b4 \uace0\ub4f1\ud559\uad50\uc5d0 \uc9c4\ud559\uc744 \uc2dc\ub3c4\ud588\uc73c\ub098 \uc544\uc27d\uac8c \uc2e4\ud328\ud588\uc5c8\ub2e4. \uc131\uc801\uc740 \ub410\ub294\ub370 \uba74\uc811\uc5d0\uc11c \ub5a8\uc5b4\uc84c\ub358 \uac83\uc73c\ub85c \uae30\uc5b5\ud55c\ub2e4. \ube44\ub85d \ud2b9\uc131\ud654 \uace0\ub4f1\ud559\uad50\uac00 \uc544\ub2cc \uc77c\ubc18 \uace0\ub4f1\ud559\uad50\ub97c \uac14\uc9c0\ub9cc, \uafc8\uc744 \ud3ec\uae30\ud558\uc9c0 \uc54a\uace0 \uc544\uc8fc \uc870\uae08\uc529 \ud559\uad50 \uacf5\ubd80\uac00 \ud558\uae30 \uc2eb\uc744 \ub54c\ub9c8\ub2e4 \ud504\ub85c\uadf8\ub798\ubc0d\uc744 \uacf5\ubd80\ud588\ub2e4. \uace0\ub4f1\ud559\uad50 \ubb3c\ub9ac \uc120\uc0dd\ub2d8\uaed8\uc11c \ud504\ub85c\uadf8\ub798\ubc0d\uc5d0(\ub2f9\uc2dc\uc5d0\ub294 \ucf54\ub529\uc774\ub77c\ub294 \ub9d0\uc774 \uc77c\ubc18\uc801\uc73c\ub85c \uc4f0\uc774\uc9c0\ub294 \uc54a\uc558\ub2e4.) \uad00\uc2ec\uc774 \ub9ce\uc73c\uc168\uc5b4\uc11c \uce5c\uad6c\uc640 \ud568\uaed8 \ub3d9\uc544\ub9ac\ub97c \ub9cc\ub4e4\uc5b4 \uad50\ub0b4 \ub300\ud68c\uc5d0 \ub098\uac00 \uc785\uc0c1\uc744 \ud558\ub294 \uacbd\ud5d8\ub3c4 \uc5bb\uc744 \uc218 \uc788\uc5c8\ub2e4. \ub2f9\uc2dc \uc0dd\uac01\ud574\ubcf4\uba74 \uc774\ub54c \ub09c \ud55c \uac70 \uc5c6\uc5c8\uae34 \ud588\ub2e4. \uc544\uc8fc \uc9e7\uc9c0\ub9cc IoT \uac1c\ub150\uc5d0 \ub300\ud574 \uacf5\ubd80\ud574\ubcf4\uba74\uc11c \uc18c\ub17c\ubb38\uc744 \uc791\uc131\ud574\ubcf4\uae30\ub3c4 \ud588\ub2e4. \ub2f9\uc2dc \uc18c\ub17c\ubb38\uc774 \ud30c\uc77c\ub85c \ub0a8\uc544 \uc77d\uc5b4 \ubd24\ub294\ub370 \uc18c\ub17c\ubb38\uc774\ub77c\uace0\ub3c4 \ub9d0\ud558\uae30\ub3c4 \ud798\ub4e0 \uadf8\ub7f0 \uc815\ubcf4 \uae00\uc774\uc5c8\ub2e4. \uadf8\ub798\ub3c4 \ubb34\uc5b8\uac00 \ud574\ubcf4\ub824\uace0 \ud588\ub2e4\ub294 \uac83\uc5d0 \uc758\ubbf8\ub97c \ub450\uace0 \uc788\ub2e4.\\n\\n### \ud130\ub2dd \ud3ec\uc778\ud2b8\\n\\n\uace0\ub4f1\ud559\uad50 \ub3d9\uc544\ub9ac \ud65c\ub3d9 \uc911 \uac00\uc7a5 \uae30\uc5b5\uc5d0 \ub0a8\ub294 \uc21c\uac04\uc740 \ub2f9\uc2dc \ubaa8 \uac8c\uc784 \ud68c\uc0ac\uc5d0 \uadfc\ubb34\ud558\uc2dc\ub358 \ud504\ub85c\uadf8\ub798\uba38 \ubd84\uc758 \uac15\uc5f0\uc744 \ub4e4\uc5c8\uc744 \ub54c\ub2e4. \uadf8\ubd84\uc744 \ud1b5\ud574 \ud504\ub85c\uadf8\ub798\uba38\ub85c\uc11c\uc758 \uc0b6\uc744 \uc54c \uc218 \uc788\uc5c8\uace0, \ucef4\ud4e8\ud130\uacf5\ud559\uacfc\uc5d0 \uc9c4\ud559\ud558\uae30 \uc704\ud574 \uc5b4\ub5a4 \ub178\ub825\uc744 \ud574\uc57c \ud558\ub294\uc9c0 \uc54c \uc218 \uc788\uc5c8\ub2e4. \uc0ac\uc2e4 \uc18c\ub17c\ubb38\uc744 \ucd94\ucc9c\ud558\uc2e0 \uac83\ub3c4 \uadf8\ubd84\uc774\uc5c8\ub2e4. \uadf8\ubd84\uc740 \ub9ce\uc740 \ub3c4\uc6c0\uc744 \uc8fc\uc168\uc9c0\ub9cc, \ub2f9\uc2dc \ub0b4\uac00 \ucca0\uc774 \uc5c6\uc5c8\uae30\ub3c4 \ud558\uace0 \ud504\ub85c\uadf8\ub798\ubc0d \ub9d0\uace0 \ud559\uc5c5\uc758 \uc2a4\ud2b8\ub808\uc2a4\uac00 \ub108\ubb34 \ub9ce\uc558\uc5b4\uc11c \uc790\uc5f0\uc2a4\ub7fd\uac8c \uadf8\ubd84\uacfc\uc758 \uad50\ub958\ub294 \ub04a\uae30\uac8c \ub410\ub2e4. \uc5b4\uca0c\ub4e0 \uadf8\ubd84\uc758 \uac15\uc5f0 \uc774\ud6c4, \ub09c \ud504\ub85c\uadf8\ub798\uba38\ub77c\ub294 \uc9c1\uc5c5\uc740 \uadf8\uc800 \uba4b\uc9c4 \uc9c1\uc5c5\uc774 \uc544\ub2c8\ub77c \uc9c4\uc815\uc73c\ub85c \ub0b4\uac00 \ud574\ubcf4\uace0 \uc2f6\uc740 \uc77c\uc774\ub77c\ub294 \ud655\uc2e0\uc744 \uac16\uac8c \ub410\uc5c8\ub2e4. \uadf8\ub7ec\uae30 \uc704\ud574\uc11c\ub294 \ucef4\ud4e8\ud130\uacf5\ud559\uacfc\uc5d0 \ub4e4\uc5b4\uac00\uc57c \ud588\uc5c8\uace0, \uadf8\ub798\uc11c \uadf8\ub54c \uc774\ud6c4\ub85c \ud559\uc5c5\uc5d0 \ub354 \uc9d1\uc911\ud558\ub824 \ub178\ub825\ud588\ub358 \uac83 \uac19\ub2e4. \\n\\n\uadf8\ubd84\uaed8\uc11c \uad50\uc721\uc790\ub85c\uc11c \ud559\uc0dd\ub4e4\uc5d0\uac8c \ub3c4\uc6c0\uc744 \uc8fc\ub294 \ud65c\ub3d9\uc744 \ud558\uc2dc\ub294 \ubaa8\uc2b5 \ub610\ud55c \uc778\uc0c1 \uae4a\uc5c8\ub2e4. \uadf8\ubd84\uc740 \ub2f9\uc2dc \ud2b9\uc131\ud654 \uace0\ub4f1\ud559\uad50\uc5d0 \uba58\ud1a0\ub9c1\uc744 \ud558\uc168\ub358 \uac83\uc73c\ub85c \uae30\uc5b5\ud558\ub294\ub370 \uadf8\ub7f0 \ubaa8\uc2b5\ub4e4\uc774 \ub108\ubb34 \uba4b\uc788\uc5b4 \ubcf4\uc600\ub2e4. \ub2f9\uc2dc \ud398\uc774\uc2a4\ubd81 \ud398\uc774\uc9c0\uc778 \'\uc0dd\ud65c\ucf54\ub529\'\uc774 \uc720\uba85\ud588\uc5c8\ub294\ub370 \uadf8\uacf3\uc744 \ub9cc\ub4dc\uc2e0 \'\uc774\uace0\uc789\'\ub2d8\uc740 \uc6f9 \uac15\uc758\ub97c \ub2e4\ub978 \uc0ac\ub78c\ub4e4\ub3c4 \uacf5\ubd80\ud558\uae30 \uc27d\uac8c \uac15\uc758\ub85c \uc81c\uacf5\ud558\uc168\uc5c8\ub2e4. \uc774\ub7f0 \ubd84\ub4e4\uc744 \ubcf4\uba74\uc11c \ub098\ub3c4 \ub2e8\uc21c\ud788 \uae30\uc220\uc744 \uacf5\ubd80\ud558\ub294 \uac83\uc744 \ub118\uc5b4\uc11c\uc11c \uc790\uc2e0\uc774 \uc54c\uace0 \uc788\ub294 \uae30\uc220\uc744 \ud1b5\ud574 \uc0ac\ub78c\ub4e4\uc5d0\uac8c \uae0d\uc815\uc801\uc778 \uc601\ud5a5\uc744 \ubbf8\uce58\ub294 \uc0ac\ub78c\uc774 \ub418\uace0 \uc2f6\ub2e4\ub294 \uc0dd\uac01\uc744 \ud558\uac8c \ub410\ub2e4.\\n\\n### \ub04a\uc784\uc5c6\ub294 \ub178\ub825\\n\\n\ub300\ud559\uc5d0 \uc9c4\ud559 \ud6c4\uc5d0\ub294 \ubcf8\uaca9\uc801\uc73c\ub85c \ucef4\ud4e8\ud130 \uacf5\ud559\uc744 \uacf5\ubd80\ud558\uba74\uc11c \uac1c\ubc1c\uc790\ub77c\ub294 \uc9c1\uc5c5\uc744 \ud3ec\uae30\ud558\uace0 \uc2f6\uc5c8\ub358 \uc21c\uac04\ub4e4\uc774 \uc815\ub9d0 \ub9ce\uc558\ub2e4. \uccab \ubc88\uc9f8\ub85c\ub294 C\uc5b8\uc5b4\uc758 \uc774\uc911\ud3ec\uc778\ud130\uc640 \ubc30\uc5f4\uacfc\uc758 \uad00\uacc4\uc600\ub2e4. \uc608\uc804\uc5d0 \uacf5\ubd80\ub3c4 \ud588\uc5c8\uc9c0\ub9cc \ub2e4\uc2dc \ud558\ub824\ub2c8 \uadf8\ub807\uac8c \uc5b4\ub824\uc6b8 \uc218\uac00 \uc5c6\uc5c8\ub2e4. \ub2e4\ub978 \uce5c\uad6c\ub4e4\uc740 C\uc5b8\uc5b4\uac00 \uc190\uc5d0 \ube68\ub9ac \ubd99\uc5b4 \uacfc\uc81c\ub3c4 \uc798 \ud558\ub294\ub370 \ub098\ub9cc \ubabb \ud558\ub2c8 \'\ucef4\ud4e8\ud130 \uacf5\ud559\uc740 \ub098\uc640 \uc548 \ub9de\ub294 \uac74\uac00?\' \uace0\ubbfc\uc744 \ud558\uba70 \uc88c\uc808\ud588\uc5c8\ub2e4. \uadf8\ub807\uc9c0\ub9cc \ud3ec\uae30\ud558\uc9c0\ub294 \uc54a\uc558\ub2e4. \'\uc5b4\ub5bb\uac8c\ub4e0 \ud574\ub0b4\uc790\'\ub77c\ub294 \uc0dd\uac01\uc5d0 \uc120\ubc30\uac00 \uadf8\ud574 \uc5ec\ub984 \ubc29\ud559\uc5d0 Microsoft MVP \ubd84\uc774 \uc9c4\ud589\ud558\uc2dc\ub294 \uad50\uc721\uc5d0 \uc9c0\uc6d0\ud574\ubcf4\ub77c\uace0 \ud558\uc154\uc11c \uc9c0\uc6d0\ud588\uace0, \ub9c8\uc774\ud06c\ub85c\uc18c\ud504\ud2b8 \ucf54\ub9ac\uc544 \uac74\ubb3c\uc5d0\uc11c C/C++, Windows API \uadf8\ub9ac\uace0 MFC\ub97c \uacf5\ubd80\ud560 \uc218 \uc788\uc5c8\ub2e4. \ub2f9\uc2dc IT \ud68c\uc0ac\uc5d0 \uac00\ubcf8 \uac83\uc740 \ucc98\uc74c\uc774\ub77c \uc815\ub9d0 \uc2e0\uae30\ud588\uace0, \uad11\ud654\ubb38 \ubdf0\uac00 \uc815\ub9d0 \uba4b\uc788\uc5c8\ub2e4. \\n\\n\ub450 \ubc88\uc9f8\ub85c\ub294 2\ud559\ub144 \ub54c \uc804\uc790\ud68c\ub85c\uc640 \ubca0\ub9b4\ub85c\uadf8 \uadf8\ub9ac\uace0 ARM \uc5b4\uc148\ube14\ub9ac \uc5b8\uc5b4\ub97c \ubc30\uc6b0\ub358 \uc2dc\uae30\uc600\ub2e4. \ub300\ud559\uc5d0 \uc624\uba74 \uc11c\ubc84\ub97c \ub2e4\ub8e8\ub294 \ubc31\uc5d4\ub4dc\ub098 UI\ub97c \ub2f4\ub2f9\ud558\ub294 \ud504\ub860\ud2b8 \uac19\uc774 \uac1c\ubc1c\uc790\uac00 \uc2e4\ubb34\uc5d0\uc11c \ud558\ub294 \uc77c\ub4e4\uc744 \uacf5\ubd80\ud560 \uc904 \uc54c\uc558\ub294\ub370 \uc6b0\ub9ac \uacfc\ub294 \uacf5\ud559\uc744 \uacf5\ubd80\ud558\ub294 \ud559\uacfc\uc600\uae30\uc5d0 \ud558\ub4dc\uc6e8\uc5b4\uc5d0 \ucd08\uc810\uc774 \uc880 \ub354 \ub9de\ucdb0\uc838 \uc788\uc5c8\ub2e4. \uadf8\ub7ec\ub2e4 \ubcf4\ub2c8 \uc608\uc0c1\uacfc \ub2e4\ub974\uac8c low level\uc758 \uac1c\ub150\uc744 \uacf5\ubd80\ud574\uc57c\ub9cc \ud588\ub2e4. \ub2f9\uc2dc \ud558\uace0 \uc2f6\uc9c0 \uc54a\uc740 \uacf5\ubd80\ub97c \uc5b5\uc9c0\ub85c \ud574\uc57c \ud588\uc5b4\uc11c \uc2a4\ud2b8\ub808\uc2a4\ub3c4 \uc815\ub9d0 \ub9ce\uc774 \ubc1b\uc558\uace0, \ub2f9\uc5f0\ud788 \uc131\uc801\ub3c4 \uc88b\uc9c0 \uc54a\uc558\ub2e4.\\n\\n\uc138 \ubc88\uc9f8\ub85c\ub294 3\ud559\ub144\uc774 \ub05d\ub098\uace0 \uc9c4\ub85c \uace0\ubbfc\uc744 \ud560 \ub54c\uc600\ub2e4. \ud559\uad50\uc5d0\uc11c \ubc30\uc6b4 \uac74 \uc55e\uc11c \uc5b8\uae09\ud55c \uacfc\ubaa9\ub4e4\uacfc \ucef4\ud4e8\ud130 \uad6c\uc870, \uc6b4\uc601\uccb4\uc81c, \uc2dc\uc2a4\ud15c \ud504\ub85c\uadf8\ub798\ubc0d \ub4f1 \ud754\ud788 CS(Computer Science)\ub77c\uace0 \ubd88\ub9ac\ub294 \uacfc\ubaa9 \ubc16\uc5d0 \uc5c6\uc5c8\ub2e4. \ud504\ub860\ud2b8\uc5d4\ub4dc, \ubc31\uc5d4\ub4dc\ub294 \uac70\uc758 \ud560 \uc904\ub3c4 \ubab0\ub790\uc73c\uba70 \uc55e\uc73c\ub85c \ubb34\uc5c7\uc744 \ud574\uc57c \ud560\uc9c0 \ud655\uc2e0\uc774 \uc11c\uc9c0 \uc54a\uc558\uc5c8\ub2e4. \ud560 \uc904 \uc544\ub294 \uac70\ub77c\uace0\ub294 C\uc5b8\uc5b4\uc640 C++ \uadf8\ub9ac\uace0 \ubca0\ub9b4\ub85c\uadf8, \uc5b4\uc148\ube14\ub9ac, \uc544\uc8fc \uac04\ub2e8\ud55c \uc6f9 \uc815\ub3c4 \ubc16\uc5d0 \uc5c6\uc5c8\ub2e4. \uadf8\ub7ec\ub2e4 \ub9c8\uce68 \uad70 \ubcf5\ubb34\ub3c4 \ud574\uc57c \ud588\uace0, \ud734\ud559\uc744 \ud558\uac8c \ub410\ub2e4.\\n\\n### \uc9c4\ub85c \uace0\ubbfc\\n\\n\ubcf8\uac00\uc5d0 \ub0b4\ub824\uac00 \uc9c4\ub85c\uc5d0 \ub300\ud574 \uace0\ubbfc\uc744 \ud558\uae30 \uc2dc\uc791\ud588\uc9c0\ub9cc, \uc544\ubb34\ub9ac \uc0dd\uac01\ud574\ub3c4 \uba85\ud655\ud55c \uacb0\ub860\uc774 \ub098\uc624\uc9c0 \uc54a\uc558\ub2e4. \uadf8\ub798\uc11c \'\uadf8\ub0e5 \ub2e4 \uacbd\ud5d8\ud574\ubcf4\uc790. \ubb50\ub4e0 \ud574\ubcf4\uba74 \uacbd\ud5d8\uce58\uac00 \uc313\uc774\uace0 \ubaa9\ud45c\uac00 \uc0dd\uae30\uaca0\uc9c0\'\ub77c\ub294 \ub2e8\uc21c\ud55c \uc0dd\uac01\uc73c\ub85c \uc544\ubb34 \ubd84\uc57c\ub098 \ubb34\uc791\uc815 \uacf5\ubd80\ub97c \ud558\uae30 \uc2dc\uc791\ud588\ub2e4. \ub2e4\ud589\ud788 \ub0b4\uac00 \uc0b4\uc558\ub358 \uc9c0\uc5ed\uc5d0 \uc774\uc6a9\ud560 \uc218 \uc788\ub294 \ub3c4\uc11c\uad00\uc774 \ub450 \uacf3\uc774 \uc788\uc5b4 \uadf8\uacf3\uc5d0\uc11c \ucc45\uc744 \ube4c\ub824 \uc77d\uae30 \uc2dc\uc791\ud588\ub2e4. \uadfc\ub370 \ub108\ubb34 \uc61b\ub0a0 \ucc45\ub4e4\uc774 \ub9ce\uc558\uc5b4\uc11c \ucc45\uc744 \uad6c\ub9e4\ud558\uc5ec \uc77d\uae30 \uc2dc\uc791\ud588\ub294\ub370 \ubc31\uc218\uc5d0\uac8c\ub294 \ub108\ubb34 \ud070 \ub3c8\uc774\uc5c8\ub2e4. \uadf8\ub798\uc11c \ub9ac\ubdf0\uc5b4\ub97c \uc2e0\uccad\ud558\uac8c \ub410\uace0, 2021\ub144\ubd80\ud130 \ud55c\ube5b\ubbf8\ub514\uc5b4\uc5d0\uc11c \uc9c4\ud589\ud558\ub294 \'\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4\'\uc5d0 \ucc38\uc5ec\ud560 \uc218 \uc788\uc5c8\ub2e4. \uc9c0\uae08\uae4c\uc9c0 \ub9ce\uc740 \ucc45\uc744 \uc81c\uacf5 \ubc1b\uc544 \uc77d\uace0 \uc788\ub294\ub370 \uc815\ub9d0 \uac10\uc0ac\ud558\ub2e4.\\n\\n\uc774\ub54c \'\uac00\uc9dc\uc5f0\uad6c\uc18c\'\uc5d0\uc11c \uc9c4\ud589\ud588\ub358 DataCamp Data Science Fellowship\uc5d0\ub3c4 \ucc38\uc5ec\ud558\uac8c \ub410\uc5c8\ub2e4. \ub370\uc774\ud130 \uc5d4\uc9c0\ub2c8\uc5b4\ub9c1\uc774\ub77c\ub294 \ubd84\uc57c\ub97c \ucc98\uc74c\uc73c\ub85c \uacf5\ubd80\ud558\uac8c \ub410\uc5c8\ub294\ub370 \uc815\ub9d0 \uc5b4\ub824\uc6e0\ub358 \uae30\uc5b5\uc774 \ub09c\ub2e4. \uc774\ubc16\uc5d0\ub3c4 \ud63c\uc790 Django, \uc790\ubc14\uc2a4\ud06c\ub9bd\ud2b8, Node.js, \uc778\uacf5\uc9c0\ub2a5 \ub4f1\uc744 \uacf5\ubd80\ud558\uae30 \uc2dc\uc791\ud588\uc73c\ub098 \uacc4\ud68d \uc5c6\uc774 \uc774\uac83\uc800\uac83 \uacf5\ubd80\ud558\ub2e4 \ubcf4\ub2c8 \uc9c0\uc2dd\uc740 \uc870\uae08\uc529 \uc313\uc600\uc9c0\ub9cc, \uc810\uc810 \ubc88\uc544\uc6c3\uc774 \uc624\uae30 \uc2dc\uc791\ud588\ub2e4.\\n\\n\uc774\ub54c \uac1c\ubc1c\uc744 \uc7a0\uc2dc \uba48\ucd94\uace0 \uc26c\ub358 \uc911 2023\ub144, GDG Daegu\ub97c \uc54c\uac8c \ub410\ub2e4. \uc774 \ucee4\ubba4\ub2c8\ud2f0\ub97c \ud1b5\ud574 \ub610 \ub2e4\uc2dc \uc758\uc695\uc744 \uc5bb\uac8c \ub418\uc5c8\uace0, \ub6f0\uc5b4\ub09c \uac1c\ubc1c\uc790\ub85c \uc131\uc7a5\ud558\uae30 \uc704\ud574\uc11c\ub294 \uae30\uc220\ubfd0\ub9cc \uc544\ub2c8\ub77c \ucee4\ubba4\ub2c8\ucf00\uc774\uc158 \ub2a5\ub825 \ub610\ud55c \uc911\uc694\ud558\ub2e4\ub294 \uac83\ub3c4 \uae68\ub2ec\uc558\ub2e4. \uc774 \ucee4\ubba4\ub2c8\ud2f0\ub294 \uc774\ud6c4\uc5d0\ub3c4 \ub0b4\uac00 \ub2e4\uc591\ud55c \ucee4\ubba4\ub2c8\ud2f0, \ucee8\ud37c\ub7f0\uc2a4, \ubc0b\uc5c5 \ub4f1 \uc0ac\ub78c\ub4e4\uc774 \ubaa8\uc774\ub294 \uacf3\uc5d0 \uc801\uadf9\uc801\uc73c\ub85c \ucc38\uc5ec\ud558\uac8c \ub418\ub294 \uacc4\uae30\uac00 \ub418\uc5c8\ub2e4. \\n\\n\uadf8\ub9ac\uace0 \uac70\uae30\uc11c \uc544\uc8fc \uc870\uae08\uc774\ub098\ub9c8 \ud65c\ub3d9\uc744 \ud558\uba70 \ub0b4 \ub610\ub798 \uadf8\ub9ac\uace0 \uc120\ubc30 \uac1c\ubc1c\uc790\ub4e4\uc758 \ub2e4\uc591\ud55c \uc774\uc57c\uae30\ub97c \ub4e4\uc744 \uc218 \uc788\uc5c8\ub294\ub370, \'\uc77c\ub85c \ud588\uc744 \ub54c \uac00\uc7a5 \uc7ac\ubc0c\ub294 \uac83\uc774 \ubb50\uc77c\uc9c0 \uc0dd\uac01\ud574\ubd10\ub77c\'\ub77c\ub294 \uc870\uc5b8\uc744 \ubc1b\uc558\ub358 \uae30\uc5b5\uc774 \ub09c\ub2e4. \uccab \ubc88\uc9f8\ub85c \ub5a0\uc624\ub978 \uac74 \uc5f4\uc2ec\ud788 \uacf5\ubd80\ud588\ub358 \uc778\uacf5\uc9c0\ub2a5\uacfc \ub370\uc774\ud130\uc600\ub2e4. \ub2f9\uc2dc \uac00\uc9dc\uc5f0\uad6c\uc18c\uc5d0\uc11c \ub525\ub7ec\ub2dd \ud504\ub808\uc784\uc6cc\ud06c \uad6c\ud604 \uc2a4\ud130\ub514\uc5d0\ub3c4 \ucc38\uc5ec\ud588\uc5c8\ub2e4. \uadf8\ub7ec\ub098 \uc774 \ub450 \uc8fc\uc81c\ub9cc\uc73c\ub85c\ub3c4 \ub2e4\uc591\ud55c \uc9c1\uad70\uc774 \uc874\uc7ac\ud558\ub2e4 \ubcf4\ub2c8 \uace0\ubbfc\uc744 \ub354 \ud558\uac8c \ub410\uc2b5\ub2c8\ub2e4.\\n\\n2023\ub144 \ud558\ubc18\uae30, \uacb0\uad6d \ub2e4\uc2dc \uc774 \uc8fc\uc81c\ub4e4\uc744 \uc5f4\uc2ec\ud788 \uacf5\ubd80\ud574\ubcf4\uae30\ub85c \uacb0\uc2ec\ud588\uace0, \uad6c\uae00\uc5d0\uc11c \uc9c4\ud589\ud558\ub294 \uba38\uc2e0\ub7ec\ub2dd \ubd80\ud2b8\ucea0\ud504\uc5d0 \ucc38\uc5ec\ud558\uac8c \ub410\uc5c8\ub2e4. \uba38\uc2e0\ub7ec\ub2dd \uae30\ucd08 \uac1c\ub150\ub3c4 \ub2e4\uc2dc \ubcf5\uc2b5\ud558\uace0 \uc88b\uc740 \uc0ac\ub78c\ub4e4\ub3c4 \ub9cc\ub098\uace0 \uc7ac\ubc0c\uc5c8\ub358 \uacbd\ud5d8\uc774\ub2e4. \ubcf5\ud559 \ud6c4\uc5d0\ub294 \ud559\uad50 GDSC ML Member\ub85c \ucc38\uc5ec\ud558\uba70 \ud604\uc7ac\ub294 \uba38\uc2e0\ub7ec\ub2dd \uc5d4\uc9c0\ub2c8\uc5b4\ub85c \uac70\uc758 \ud655\uc815\uc9c0\uc5c8\uc9c0\ub9cc, \uc5ec\uc804\ud788 \uc9c4\ub85c\uc5d0 \ub300\ud574 \ub04a\uc784\uc5c6\uc774 \uace0\ubbfc\uc744 \ud558\uace0 \uc788\ub2e4.\\n\\n### \uadf8\ub9ac\uace0 \ud604\uc7ac\uc758 \ub098\\n\\n\uc9c0\uae08\uc740 \uc774\uc81c \uace7 \uc878\uc5c5\uc744 \uc55e\ub450\uace0 \ucde8\uc5c5 \uc900\ube44\ub97c \ud558\uace0 \uc788\ub2e4. \uc774\ub807\uac8c \uae00\uc744 \uc368\ubcf4\ub2c8 \ub0b4\uac00 \uc9c0\uae08\uae4c\uc9c0 \uac00\uc7a5 \uc5f4\uc2ec\ud788 \uacf5\ubd80\ud55c \ubd84\uc57c\ub294 \uc778\uacf5\uc9c0\ub2a5\uc774\ub2e4. \uadf8\ub7ec\ub098 \ud604\uc7ac \ucde8\uc5c5 \uc2dc\uc7a5\uacfc \uac01\uc885 \ucc44\uc6a9 \uacf5\uace0\ub4e4\uc744 \uc77d\uc5b4\ubcf4\uba74 \uc774 \uae38\uc744 \uc5bc\ub978 \ud3ec\uae30\ud558\uace0 \ube68\ub9ac \ub2e4\ub978 \uae38\ub85c \uc804\ud658\ud574\uc57c \ub420\uc9c0\ub3c4 \ubaa8\ub974\uaca0\ub2e4\ub294 \uc0dd\uac01\uc774 \ub04a\uc784\uc5c6\uc774 \ub4e0\ub2e4. \ub300\ubd80\ubd84\uc758 \uae30\uc5c5\uc740 \uc11d\uc0ac \uc774\uc0c1\uc758 \ud559\ub825\uc744 \uc694\uad6c\ud558\uace0, \uadf8\ub807\uc9c0 \uc54a\uc740 \uae30\uc5c5\uc740 \uc11d\uc0ac \uc774\uc0c1\uc758 \uc2e4\ub825\uc744 \uc694\uad6c\ud55c\ub2e4. \ub098\ub294 \uc5b4\ub290 \uacf3\uc5d0\ub3c4 \uc18d\ud558\uc9c0 \uc54a\ub2e4. \\n\\n\uadf8\ub7fc\uc5d0\ub3c4 \ubd88\uad6c\ud558\uace0, \ub098\ub294 \uc5b4\ub5bb\uac8c\ub4e0 \uc774 \uc0c1\ud669\uc744 \uadf9\ubcf5\ud560 \uc218 \uc788\ub2e4\uace0 \ubbff\ub294\ub2e4. \uc9c0\uae08\uae4c\uc9c0 \uadf8\ub807\uac8c \ud574\uc654\uace0, \uc0b4\uc558\uc73c\ub2c8\uae4c. \ud798\ub4e0 \uc21c\uac04\ub9c8\ub2e4 \ud3ec\uae30\ud558\uc9c0 \uc54a\uc558\ub4ef\uc774, \uc774\ubc88\uc5d0\ub3c4 \ubc18\ub4dc\uc2dc \uc774\uaca8\ub0bc \uac70\ub77c \ud655\uc2e0\ud55c\ub2e4."},{"id":"/Developing-AI-services-with-LangChain","metadata":{"permalink":"/en/blog/Developing-AI-services-with-LangChain","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2024-06-03-Developing-AI-services-with-LangChain/2024-06-03-Developing-AI-services-with-LangChain.md","source":"@site/blog/2024-06-03-Developing-AI-services-with-LangChain/2024-06-03-Developing-AI-services-with-LangChain.md","title":"\ud83d\udcd6 \ub7ad\uccb4\uc778\uc73c\ub85c LLM \uae30\ubc18\uc758 AI \uc11c\ube44\uc2a4 \uac1c\ubc1c\ud558\uae30","description":"\ud604\uc9c1 AI Specialist\uc5d0\uac8c \ubc30\uc6b0\ub294 RAG! | \ub7ad\uccb4\uc778, \uc624\ud508AI API, \uc2a4\ud2b8\ub9bc\ub9bf\uc73c\ub85c 8\uac00\uc9c0 \uc11c\ube44\uc2a4 \uad6c\ud604\uae4c\uc9c0","date":"2024-06-03T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \ub7ad\uccb4\uc778\uc73c\ub85c LLM \uae30\ubc18\uc758 AI \uc11c\ube44\uc2a4 \uac1c\ubc1c\ud558\uae30","date":"2024-06-03","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/Developing-AI-services-with-LangChain","description":"\ud604\uc9c1 AI Specialist\uc5d0\uac8c \ubc30\uc6b0\ub294 RAG! | \ub7ad\uccb4\uc778, \uc624\ud508AI API, \uc2a4\ud2b8\ub9bc\ub9bf\uc73c\ub85c 8\uac00\uc9c0 \uc11c\ube44\uc2a4 \uad6c\ud604\uae4c\uc9c0","keywords":["\ub7ad\uccb4\uc778","LLM","OpenAI"]},"unlisted":false,"prevItem":{"title":"\ud83d\uddfa\ufe0f \uc0b6\uc758 \uc9c0\ub3c4","permalink":"/en/blog/Life-Map"},"nextItem":{"title":"\ud83d\udcd6 \ub354 \ub098\uc740 \ud504\ub85c\uadf8\ub798\uba38 \ub418\ub294 \ubc95","permalink":"/en/blog/Becoming-a-Better-Programmer"}},"content":"## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/Developing-AI-services-with-LangChain.png)](https://product.kyobobook.co.kr/detail/S000212568407)\\n\\n- \uc81c\ubaa9: \ub7ad\uccb4\uc778\uc73c\ub85c LLM \uae30\ubc18\uc758 AI \uc11c\ube44\uc2a4 \uac1c\ubc1c\ud558\uae30\\n- \uc800\uc790: \uc11c\uc9c0\uc601\\n- \ucd9c\ud310\uc0ac: \uae38\ubc97\\n- \ucd9c\uac04: 2024-02-27\\n\\n{/* truncate */}\\n\\n## Intro\\n\\n\uc694\uc998 LLM(Large Language Model)\uc774 \ub108\ubb34 \uc778\uae30\uac00 \ub9ce\uc544 \ub098\ub3c4 \uc2dc\ud5d8\uae30\uac04\uc774\uc9c0\ub9cc \uc7a0\uae50 \uc2dc\uac04\uc744 \ub0b4\uc5b4\uc11c \uacf5\ubd80\ud574 \ubd24\ub2e4. \uc0ac\uc2e4 \uc778\uacf5\uc9c0\ub2a5\uc744 \uacf5\ubd80\ud558\ub294 \uc0ac\ub78c\uc774\ub2c8 LLM\uc774 \ubb34\uc5c7\uc778\uc9c0\ub3c4 \uc54c\uace0, \uc5b4\ub5bb\uac8c \uc0ac\uc6a9\ud560\uc9c0\ub3c4 \ub300\ucda9\uc740 \uc54c\uace0 \uc788\ub2e4. \uadf8\ub7ec\ub098 \ucc45\uc744 \uc77d\ub294 \uc774\uc720\ub294 \ub2e4\uc2dc \ud55c\ubc88 \ub354 \ubcf5\uc2b5\uc744 \uc704\ud574\uc11c\uc774\uae30\ub3c4 \ud558\uace0, \ubb34\uc5c7\ubcf4\ub2e4\ub3c4 \uc774 \ucc45\uc774 \uc5c4\uccad \uc587\uc558\uae30 \ub54c\ubb38\uc774\ub2e4. \uac70\uae30\ub2e4 \ub9c8\uc774\ud06c\ub85c\uc18c\ud504\ud2b8 \uc9c1\uc6d0\uc774 \uc4f0\uc168\uc73c\ub2c8 \ubb34\uc5b8\uac00 \ub354 \ud2b9\ubcc4\ud55c \ud301\uc774 \uc5c6\uc9c0 \uc54a\uc744\uae4c \uc0dd\uac01\ud55c \uc810\ub3c4 \uc788\uc5c8\ub2e4.\\n\\n## Book Review\\n\\n### \uba38\uc2e0\ub7ec\ub2dd \uc9c1\uad70\uc774 \uc544\ub2cc \uac1c\ubc1c\uc790\uc5d0\uac8c \uc88b\uc740 \ucc45\\n\\n\uba38\uc2e0\ub7ec\ub2dd\uc744 \uacf5\ubd80\ud558\uc9c0 \uc54a\uc740 \uac1c\ubc1c\uc790\uc5d0\uac8c \ub3c4\uc6c0\uc774 \ub418\ub294 \ucc45\uc774\ub77c \uc0dd\uac01\ud55c\ub2e4. \uc804\ubc18\uc801\uc778 \uc778\uacf5\uc9c0\ub2a5 \uac1c\ub150\uc774 \ub2f4\uaca8 \uc788\uc73c\uba70, \ucf54\ub4dc\uac00 \uadf8\ub807\uac8c \ub9ce\uc9c0 \uc54a\uc740 \ud3b8\uc774\ub2e4. \uc989, \uac00\ubccd\uac8c \uc77d\uae30 \uc88b\uc558\ub358 \ucc45\uc774\ub2e4. \ucf58\ub2e4 \uc124\uce58\ubd80\ud130 OpenAI API key\ub97c \ubc1b\ub294 \uac83, \uadf8\ub9ac\uace0 \ud5c8\uae45\ud398\uc774\uc2a4 \ud1a0\ud070\uae4c\uc9c0 \uc5bb\ub294 \uacfc\uc815\uc774 \uc0c1\uc138\ud788 \ub098\uc640 \uc788\uc73c\ub2c8 \uae30\ucd08 \uac1c\ub150\uc11c\ub85c \ucda9\ubd84\ud788 \uc88b\uc740 \ucc45\uc774\ub77c \uc0dd\uac01\ud55c\ub2e4.\\n\\n\ub098\ub294 RAG\uc5d0 \ub300\ud55c \ubd80\ubd84\uc744 \ubcf5\uc2b5\ud560 \uc218 \uc788\uc5b4\uc11c \ub3c4\uc6c0\uc774 \ub410\uace0, \ub7ad\uccb4\uc778\ub3c4 \ucc98\uc74c \uc368\ubd24\ub294\ub370 \uc740\uadfc \ub098\uc058\uc9c0 \uc54a\uc558\ub2e4. \ub05d\ubd80\ubd84\uc5d0\ub294 \ub7ad\uccb4\uc778\uc73c\ub85c \ud560 \uc218 \uc788\ub294 \ud504\ub85c\uc81d\ud2b8\ub4e4\uc774 \uc18c\uac1c\ub418\uc5b4 \uc788\uc73c\ub2c8 \ucc38\uace0\ud558\uba74 \uc88b\uc744 \uac83 \uac19\ub2e4. \ub098\ub3c4 \ub0b4 \ud504\ub85c\uc81d\ud2b8\uc5d0 \ub7ad\uccb4\uc778\uacfc LLM\uc744 \uc5b4\ub5bb\uac8c \uc368\ubcfc\uc9c0 \uace0\ubbfc\ud588\ub358 \uc88b\uc740 \uacc4\uae30\uac00 \ub41c \ucc45\uc774\ub2e4. \uace7 \ub7ad\uccb4\uc778\uc73c\ub85c \ud504\ub85c\uc81d\ud2b8\ub97c \ud558\ub098 \ud574\ubd10\uc57c\uaca0\ub2e4.\\n\\n## \ub300\uc0c1 \ub3c5\uc790\\n\\nLLM\uc774 \uc778\uae30\uac00 \ub9ce\uc544 \ud55c \ubc88\ucbe4 \uacf5\ubd80\ud574 \ubcf4\uace0 \uc2f6\uc740 \ubd84\ub4e4\uaed8 \uc774 \ucc45\uc744 \ucd94\ucc9c\ud55c\ub2e4."},{"id":"/Becoming-a-Better-Programmer","metadata":{"permalink":"/en/blog/Becoming-a-Better-Programmer","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2024-05-23-Becoming-a-Better-Programmer/2024-05-23-Becoming-a-Better-Programmer.md","source":"@site/blog/2024-05-23-Becoming-a-Better-Programmer/2024-05-23-Becoming-a-Better-Programmer.md","title":"\ud83d\udcd6 \ub354 \ub098\uc740 \ud504\ub85c\uadf8\ub798\uba38 \ub418\ub294 \ubc95","description":"\uc9c0\uae08 \ubc14\ub85c \uc2e4\ucc9c\ud560 \uc218 \uc788\ub294 \uc120\ubc30 \uac1c\ubc1c\uc790\uc758 39\uac00\uc9c0 \ub178\ud558\uc6b0","date":"2024-05-23T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \ub354 \ub098\uc740 \ud504\ub85c\uadf8\ub798\uba38 \ub418\ub294 \ubc95","date":"2024-05-23","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/Becoming-a-Better-Programmer","description":"\uc9c0\uae08 \ubc14\ub85c \uc2e4\ucc9c\ud560 \uc218 \uc788\ub294 \uc120\ubc30 \uac1c\ubc1c\uc790\uc758 39\uac00\uc9c0 \ub178\ud558\uc6b0","keywords":["\ud504\ub85c\uadf8\ub798\uba38","TDD","\uacf5\ubd80","\uac1c\ubc1c\uc790","\uc0ac\ub78c"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \ub7ad\uccb4\uc778\uc73c\ub85c LLM \uae30\ubc18\uc758 AI \uc11c\ube44\uc2a4 \uac1c\ubc1c\ud558\uae30","permalink":"/en/blog/Developing-AI-services-with-LangChain"},"nextItem":{"title":"\ud83d\udcd6 \ub525\ub7ec\ub2dd\uc758 \uc815\uc11d","permalink":"/en/blog/Fundamentals-of-DL"}},"content":":::info\\n\ud55c\ube5b\ubbf8\ub514\uc5b4 \\\\<\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4\\\\> \ud65c\ub3d9\uc744 \uc704\ud574\uc11c \ucc45\uc744 \uc81c\uacf5\ubc1b\uc544 \uc791\uc131\ub41c \uc11c\ud3c9\uc785\ub2c8\ub2e4.\\n:::\\n\\n![\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4 2024](../assets/I-am-reviewer-2024.jpg)\\n\\n## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/Becoming-a-Better-Programmer.jpg)](https://product.kyobobook.co.kr/detail/S000213044936)\\n\\n- \uc81c\ubaa9: \ub354 \ub098\uc740 \ud504\ub85c\uadf8\ub798\uba38 \ub418\ub294 \ubc95\\n- \uc800\uc790: \ud53c\ud2b8 \uad6c\ub4e4\ub9ac\ud504\\n- \uc5ed\uc790: \ucd5c\uc6d0\uc7ac, \uac15\uc804\ud76c, \uc548\uc7ac\ub355, \ub0a8\uc724\ud654 \\n- \ucd9c\ud310\uc0ac: \ud55c\ube5b\ubbf8\ub514\uc5b4\\n- \ucd9c\uac04: 2024-04-30\\n\\n{/* truncate */}\\n\\n## Intro\\n\\n2024\ub144 \ub450 \ubc88\uc9f8 \uae30\uc220\ucc45\uc774\ub2e4. \ucd5c\uadfc \ub2e4\uc591\ud55c \ucc45\ub4e4\uc744 \uc811\ud558\uace0 \ud504\ub85c\uc81d\ud2b8\ub3c4 \ud558\uace0 \uc788\uae30\uc5d0 \uc2dc\uac04\uc774 \ubd80\uc871\ud558\uc5ec \uc644\ub3c5\uc774 \uc5b4\ub824\uc6cc\uc9c0\uace0 \uc788\ub2e4. \uadf8\ub7fc\uc5d0\ub3c4 \ubd88\uad6c\ud558\uace0 \uc774 \ucc45\uc740 \uc77d\uae30 \uc26c\uc6b8 \uac83 \uac19\uae30\ub3c4 \ud558\uace0 \'\ub354 \ub098\uc740 \ud504\ub85c\uadf8\ub798\uba38\uac00 \ub418\uae30 \uc704\ud574\uc11c\ub294 \uc5b4\ub5a4 \uac78 \ub354 \ud574\uc57c \ud560\uae4c?\'\ub77c\ub294 \uad81\uae08\uc99d\uc774 \uc0dd\uaca8 \uc77d\uac8c \ub410\ub2e4.\\n\\n## Book Review\\n\\n### \ubc18\uc131\ud558\uac8c \ub418\ub294 \ucc45\\n\\n\uc774 \ucc45\uc740 \ud504\ub85c\uadf8\ub798\uba38\ub85c\uc11c \uc5b4\ub5bb\uac8c \uc131\uc7a5\ud574\uc57c \ud558\ub294\uc9c0\uc5d0 \ub300\ud574 \ub2e4\ub8e8\uace0 \uc788\ub2e4. \ucc45\uc744 \uc77d\uc73c\uba74\uc11c \ub098\ub294 \uc774\ub7f0 \ubd80\ubd84\uc744 \ub193\ucce4\uad6c\ub098, \uc774\ub7f0 \ubd80\ubd84\uc744 \ub354 \uc2e0\uacbd \uc368\uc57c\uaca0\ub2e4\ub294 \uc0dd\uac01\uc774 \ub4e4\uc5c8\ub2e4. \ucc45\uc744 \uc77d\uc73c\uba74\uc11c \ubc18\uc131\ud558\uac8c \ub418\ub294 \ubd80\ubd84\uc774 \ub9ce\uc558\ub2e4. \'\ub098\ub294 \ucf54\ub4dc\uc5d0 \uc2e0\uacbd\uc744 \uc4f0\uace0 \uc788\ub294\uac00?\', \'\ub0b4\uac00 \uc4f4 \ucf54\ub4dc\ub97c \ub2e4\uc2dc \ubcf4\ub294\uac00?\', \'\ud14c\uc2a4\ud2b8 \ucf54\ub4dc\ub97c \uc791\uc131\ud558\ub294 \uac83\uc774 \uc911\uc694\ud55c\uac00?\' \ub4f1. \uc774\ub7f0 \ubd80\ubd84\ub4e4\uc744 \ub2e4\uc2dc \ud55c \ubc88 \uc0dd\uac01\ud574\ubcf4\uac8c \ub418\uc5c8\ub2e4.\\n\\n### \uc120\ubc30\uac00 \uc54c\ub824\uc8fc\ub294 \ucc45\\n\\n\ucc45\uc744 \uc77d\uc73c\uba74\uc11c \uc120\ubc30 \ud504\ub85c\uadf8\ub798\uba38\uac00 \ud558\ub098\ud558\ub098 \uc54c\ub824\uc8fc\ub294 \ub290\ub08c\uc774 \ub4e4\uc5c8\ub2e4. \ud504\ub85c\uadf8\ub798\uba38\ub85c\uc11c \uc5b4\ub5bb\uac8c \uc0dd\uac01\ud558\uace0 \ud589\ub3d9\ud574\uc57c \ud558\ub294\uc9c0 \ub4f1\uc758 \ub178\ud558\uc6b0. \uadf8\ub7f0 \ub178\ud558\uc6b0\ub97c \ubc30\uc6b4 \ub290\ub08c\uc774 \ub4e4\uc5c8\ub2e4. \ub098\ub294 \ubd84\uba85\ud788 \uc9c0\uae08 \ud68c\uc0ac\uc5d0 \ub2e4\ub2c8\uace0 \uc788\uc9c0\ub3c4 \uc54a\uace0 \uc120\ubc30\ub3c4 \uc5c6\ub294\ub370 \uc120\ubc30\uac00 \uc0dd\uae34 \ub4ef\ud55c \ub290\ub08c. \ucc45\uc5d0\uc11c\ub3c4 \'\ub9c8\uce58\uba70\' \ubd80\ubd84\uc5d0 \'\uc0dd\uac01\ud574\ubcf4\uae30\'\uac00 \uc788\ub294\ub370 \uac70\uae30\uc5d0 \uc801\ud78c \uc9c8\ubb38 \uc678\uc5d0\ub3c4 \uc0dd\uac01\ud574\ubcfc \ub9cc\ud55c \uac83\ub4e4\uc774 \ub9ce\uc558\ub2e4. \ub098\uc911\uc5d0 \uae30\ud68c\uac00 \ub41c\ub2e4\uba74 \ub2e4\ub978 \uc0ac\ub78c\ub4e4\uacfc \uc774 \ucc45\uc73c\ub85c \uac04\ub2e8\ud558\uac8c \uc2a4\ud130\ub514\ub97c \ud574\ubcf4\uace0 \uc2f6\uc744 \uc815\ub3c4\uc774\ub2e4.\\n\\n## \ub300\uc0c1 \ub3c5\uc790\\n\\n\ud504\ub85c\uadf8\ub798\uba38\ub85c\uc11c \uc131\uc7a5\ud558\uace0 \uc2f6\uc740 \uc0ac\ub78c\uc774\ub77c\uba74 \uc77d\uc5b4\ubcf4\uba74 \uc88b\uc744 \uac83 \uac19\ub2e4. \ud2b9\ud788 \uc2e0\uc785, \uc8fc\ub2c8\uc5b4\uac00 \uc77d\uc73c\uba74 \uc88b\uc740 \ucc45\uc774\uc9c0 \uc54a\uc744\uae4c \uc2f6\ub2e4. \ud559\uc0dd\ub3c4 \uc88b\ub2e4. \ub098\ub3c4 \ud559\uc0dd\uc774\ub2c8\uae4c. \uc774 \ucc45\uc744 \uc77d\uc73c\uba74\uc11c \ubc18\uc131\ud558\uace0 \ub354 \ub098\uc740 \ud504\ub85c\uadf8\ub798\uba38\ub85c \uc131\uc7a5\ud558\uace0 \uc2f6\uc740 \uc0ac\ub78c\uc774\ub77c\uba74 \ucd94\ucc9c\ud55c\ub2e4."},{"id":"/Fundamentals-of-DL","metadata":{"permalink":"/en/blog/Fundamentals-of-DL","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2024-02-25-Fundamentals-of-DL/2024-02-25-Fundamentals-of-DL.md","source":"@site/blog/2024-02-25-Fundamentals-of-DL/2024-02-25-Fundamentals-of-DL.md","title":"\ud83d\udcd6 \ub525\ub7ec\ub2dd\uc758 \uc815\uc11d","description":"\uc120\ud615\ub300\uc218\ud559\uacfc \ud655\ub960\ub85c \uc2dc\uc791\ud558\uc5ec \ud30c\uc774\ud1a0\uce58\ub85c \uc644\uc131\ud558\ub294 \ub525\ub7ec\ub2dd\uc758 \uc774\ub860\uacfc \uc2e4\uc804","date":"2024-02-25T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \ub525\ub7ec\ub2dd\uc758 \uc815\uc11d","date":"2024-02-25","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/Fundamentals-of-DL","description":"\uc120\ud615\ub300\uc218\ud559\uacfc \ud655\ub960\ub85c \uc2dc\uc791\ud558\uc5ec \ud30c\uc774\ud1a0\uce58\ub85c \uc644\uc131\ud558\ub294 \ub525\ub7ec\ub2dd\uc758 \uc774\ub860\uacfc \uc2e4\uc804","keywords":["Deep Learning","\ub525\ub7ec\ub2dd"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \ub354 \ub098\uc740 \ud504\ub85c\uadf8\ub798\uba38 \ub418\ub294 \ubc95","permalink":"/en/blog/Becoming-a-Better-Programmer"},"nextItem":{"title":"\ud83d\udcd6 \ub9cc\ub4e4\uba74\uc11c \ubc30\uc6b0\ub294 \uc0dd\uc131 AI","permalink":"/en/blog/Generative-Deep-Learning"}},"content":":::info\\n\ud55c\ube5b\ubbf8\ub514\uc5b4 \\\\<\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4\\\\> \ud65c\ub3d9\uc744 \uc704\ud574\uc11c \ucc45\uc744 \uc81c\uacf5\ubc1b\uc544 \uc791\uc131\ub41c \uc11c\ud3c9\uc785\ub2c8\ub2e4.\\n:::\\n\\n![\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4 2024](../assets/I-am-reviewer-2024.jpg)\\n\\n## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/Fundamentals-of-DL.jpg)](https://product.kyobobook.co.kr/detail/S000212175486)\\n\\n- \uc81c\ubaa9: \ub525\ub7ec\ub2dd\uc758 \uc815\uc11d\\n- \uc800\uc790: \ub2c8\ud2f4 \ubd80\ub450\ub9c8, \ub2c8\ud0ac \ubd80\ub450\ub9c8, \uc870 \ud30c\ud30c\\n- \uc5ed\uc790: \ucd5c\uc7ac\ud6c8, \ucc28\uc131\uc7ac \\n- \uac10\uc218\uc790: \uc131\ud0dc\uc751, \ub9f9\uc724\ud638\\n- \ucd9c\ud310\uc0ac: \ud55c\ube5b\ubbf8\ub514\uc5b4\\n- \ucd9c\uac04: 2024-02-02\\n\\n{/* truncate */}\\n\\n## Intro\\n\\n\ubc8c\uc368 2024\ub144\uc774\ub2e4. \uc9c0\uae08\uae4c\uc9c0 \ub525\ub7ec\ub2dd\uc744 \uacf5\ubd80\ud558\uba74\uc11c \uc5b4\ub824\uc6c0\uc774 \ub9ce\uc558\ub2e4. \ubb3c\ub860 \uc544\uc9c1\ub3c4 \uc5b4\ub835\ub2e4. \uc774\ubc88 \uc5f0\ub3c4\uc5d0\ub3c4 \ub09c \uc5f4\uc2ec\ud788 \uba38\uc2e0\ub7ec\ub2dd\uc744 \uacf5\ubd80\ud558\uaca0\uc9c0. \uc77d\uace0 \uc2f6\uc740 \ucc45\ub3c4 \ub9e4\uc6b0 \ub9ce\ub2e4. \uc774\ubc88 2024\ub144\uc5d0\ub3c4 \uac10\uc0ac\ud558\uac8c\ub3c4 \ud55c\ube5b\ubbf8\ub514\uc5b4\uc758 \'\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4\'\uc5d0 \ucc38\uc5ec\ud558\uac8c \ub418\uc5b4 2024\ub144 \uccab IT \ucc45\uc740 \ub525\ub7ec\ub2dd\uc758 \uc815\uc11d\uc744 \uc77d\uac8c \ub410\ub2e4.\\n\\n## Book Review\\n\\n### \uae30\ucd08 \uc218\ud559\\n\\n\ucc98\uc74c\uc5d0\ub294 \uc120\ud615\ub300\uc218, \ud655\ub960\uc5d0\uc11c \uae30\ucd08\uc801\uc774\uace0, \ud544\uc218\uc801\uc778 \ubd80\ubd84\ub4e4\uc744 \uc555\ucd95\ud558\uc5ec \uc798 \uc124\uba85\ud574 \uc900\ub2e4. \uadf8\ub7ec\ub098 \uc774 \uc218\ud559\ub4e4\uc744 \ud55c \ubc88\uc774\ub77c\ub3c4 \uacf5\ubd80\ud558\uc9c0 \uc54a\uc558\ub2e4\uba74, \uc5b4\ub835\uac8c \ub290\uaef4\uc9c8 \uc218 \uc788\uc744 \uac70\ub77c \uc0dd\uac01\ud55c\ub2e4. \uc815\ub9d0 \uc124\uba85\uc774 \uc798 \ub418\uc5b4 \uc788\uc9c0\ub9cc, \ub098\ub3c4 \ub2e4\uc2dc \ubd10\ub3c4 \uc870\uae08 \uc5b4\ub835\ub2e4. \uc0ac\uc2e4 \uc5b4\ub824\uc6b4 \uac8c \ub2f9\uc5f0\ud558\ub2e4. \uc5ec\uae30\uc5d0 \ub098\uc628 1\uc7a5 \uc120\ud615\ub300\uc218\ud559 \uae30\ucd08, 2\uc7a5 \ud655\ub960 \uae30\ucd08\ub85c\ub9cc \ud574\ub3c4 \ub300\ud559 \uc218\uc5c5 \ud558\ub098\ub294 \ub420 \uac83\uc774\ub2e4. \uadf8\ub7ec\ub2c8 \ubbf8\ub9ac \uc120\ud615\ub300\uc218\ud559, \ud655\ub960\ub860 \ub4f1\uc744 \uacf5\ubd80\ud558\uace0 \uc774 \ucc45\uc744 \uc811\ud558\uba74 \ub354\uc6b1 \uc720\uc775\ud558\uac8c \uc77d\uc744 \uc218 \uc788\uc744 \uac83\uc774\ub2e4. \uc5b4\uca0c\ub4e0 \uc774\ub807\uac8c \ub2e4\uc2dc \uac1c\ub150\uc744 \uc7a1\uace0 \uc2dc\uc791\ud558\ub2c8\uae4c \uc88b\uc558\ub2e4. \ud2b9\ud788 \uc65c \uc774 \uac1c\ub150\ub4e4\uc744 \uacf5\ubd80\ud574\uc57c \ud558\ub294\uc9c0 \uc54c\ub824\uc8fc\uae30\uc5d0 \ub3c5\uc790\ub4e4\uc5d0\uac8c \ub3d9\uae30\ubd80\uc5ec\uac00 \ub418\uc9c0 \uc54a\uc744\uae4c \uc2f6\ub2e4.\\n\\n### \uc800\uc790\uc640 \uc5ed\uc790\uc758 \ub178\ub825\uc774 \ubcf4\uc774\ub294 \ucc45\\n\\n\ub0b4\uac00 \uc5ed\uc790\uc600\ub2e4\uba74 \uc774 \ucc45\uc744 \ubc88\uc5ed\ud558\uae30 \uc2eb\uc5c8\uc744 \uac83 \uac19\ub2e4. \ubb38\uc7a5\ub4e4 \ud558\ub098\ud558\ub098\uac00 \uc740\uadfc\ud788 \uc5b4\ub835\ub2e4. \uc800\uc790\uac00 \uc218\ud559\uc801\uc778 \ub0b4\uc6a9\ub4e4\ub3c4 \ub9d0\ub85c \uc798 \ud480\uc5b4 \uc4f0\ub824\uace0 \ud588\ub358 \uac83\ub4e4\uc774 \ubcf4\uc778\ub2e4. \uc5ed\uc790\ub3c4 \uc774\uac78 \ud55c\uad6d\uc5b4\ub85c \uc62e\uae30\ub824\uace0 \uc5bc\ub9c8\ub098 \ub178\ub825\ud588\uc744\uae4c. \uadf8\ub7ec\ub2c8 \uac10\uc218\uc790\ub3c4 \ub450 \ubd84\uc774\ub098 \uc788\uc73c\uc2dc\uaca0\uc9c0. \\n\\n### \ud55c\uae00 \uc6a9\uc5b4\\n\\n\ub0b4\uac00 \uba38\uc2e0\ub7ec\ub2dd, \ub525\ub7ec\ub2dd\uc744 \uc601\uc5b4\ub85c \uacf5\ubd80\ud574\uc11c \uadf8\ub7f0\uc9c0 \uc77d\ub294\ub370 \uc5b4\uc0c9\ud55c \ub290\ub08c\uc744 \ubc1b\uc558\ub2e4. \uc774 \ucc45\uc758 \uc8fc \uc5b8\uc5b4\uac00 \ud55c\uae00\ub85c \ud45c\uae30\ub418\uc5c8\uae30 \ub54c\ubb38\uc774\ub2e4. \ubb3c\ub860 \uc601\uc5b4\ub85c\ub3c4 \ub2e4 \ud55c \ubc88\uc529\uc740 \ud45c\uae30\ub418\uc5b4 \uc788\uc5c8\ub2e4(e.g., \uace0\uc720\ubca1\ud130$^{eigenvectors}$, \uc0ac\ud6c4\ud655\ub960$^{posterior \\\\ probability}$, L2 \uaddc\uc81c\ud654$^{regularization}$). \ud55c\uad6d\uc5b4\ub85c \ubc88\uc5ed\ud560 \uc218 \uc788\ub294 \uc6a9\uc5b4\ub294 \uc804\ubd80 \ub2e4 \ud55c\uad6d\uc5b4\ub85c \ubc88\uc5ed\ud55c \uac83 \uac19\ub2e4. \ub4dc\ub86d\uc544\uc6c3 \uac19\uc774 \ubc88\uc5ed\ud558\uae30 \uc5b4\ub824\uc6b4 \uc6a9\uc5b4\ub294 \uadf8\ub0e5 \uadf8\ub300\ub85c \ud45c\uae30\ub418\uc5b4 \uc788\ub2e4. \uc774 \ubd80\ubd84\uc740 \ubc88\uc5ed\uc11c\ub77c \uc5b4\uca54 \uc218 \uc5c6\ub2e4\uace0 \uc0dd\uac01\ud558\uc9c0\ub9cc \ub9ce\uc774 \uc544\uc26c\uc6b4 \ubd80\ubd84\uc774\ub2e4.  \\n\\n### \ub2e4\ub978 \ub525\ub7ec\ub2dd \ucc45\uacfc \ub2e4\ub978 \uc810\\n\\n\uc774 \ucc45\uc744 \uc77d\ub2e4 \ubcf4\uba74 \ud398\uc774\uc9c0\uc5d0 \uac01\uc8fc\uac00 \ub9ce\uc774 \ubcf4\uc77c \uac83\uc774\ub2e4. \uc774\uac8c \ub2e4\ub978 \ucc45\ub4e4\uacfc \ube44\uad50\ub418\ub294 \ubd80\ubd84\uc774\ub77c \uc0dd\uac01\ub41c\ub2e4. \uc911\uc694\ud55c \uba38\uc2e0\ub7ec\ub2dd \uac1c\ub150\uc774 \ub098\uc62c \ub54c\ub9c8\ub2e4 \uc5b4\ub5a4 \ub17c\ubb38\uc5d0\uc11c \uadf8 \uac1c\ub150\uc774 \ub098\uc640 \uc788\ub294\uc9c0 \uac01\uc8fc\ub85c \ud45c\uc2dc\ub418\uc5b4 \uc788\ub2e4. \uc560\ucd08\uc5d0 \ucc45 \uc790\uccb4\uac00 \ub17c\ubb38\uc758 \ub0b4\uc6a9\uc744 \ubc14\ud0d5\uc73c\ub85c \uc800\uc790\uac00 \uc774\ud574\ud55c \ub0b4\uc6a9\uc744 \ucc45\uc73c\ub85c \uc62e\uae34 \ub290\ub08c\uc774\ub2e4. \uadf8\ub798\uc11c TMI \uac19\uc740 \ub0b4\uc6a9\ub3c4 \uc788\uc5c8\uace0, \uc5b4\ub824\uc6b4 \uc124\uba85\ub4e4\ub3c4 \ub9ce\uc558\uc9c0\ub9cc \ub17c\ubb38 \uc77d\ub294 \uae30\ubd84\uc774\ub77c \ub098\uc058\uc9c0 \uc54a\uc558\ub2e4. \uc65c \uc81c\ubaa9\uc774 \'\ub525\ub7ec\ub2dd\uc758 \uc815\uc11d\'\uc778\uc9c0 \uc54c \uac83 \uac19\uc558\ub2e4.\\n\\n### \ud30c\uc774\ud1a0\uce58\\n\\n\ud30c\uc774\ud1a0\uce58\ub294 5\uc7a5\ubd80\ud130 \uc2dc\uc791\ud55c\ub2e4. \uc774\uc804\uc5d0\ub294 \ub525\ub7ec\ub2dd\uc758 \uae30\ubcf8 \uc9c0\uc2dd\uc744 \uc54c\ub824\uc900\ub2e4. \ud30c\uc774\ud1a0\uce58\uc5d0 \ub300\ud574\uc11c \uc5c4\uccad \uc790\uc138\ud788 \uc54c\ub824\uc8fc\ub294 \ub290\ub08c\uc740 \uc544\ub2c8\ub2e4. \ud544\uc694\ud55c \ubd80\ubd84\uc774 \uc788\uc73c\uba74 \uac01\uc790 \uc54c\uc544\uc11c \ub0b4\uc6a9\uc744 \ub354 \ucc3e\uc544\ubd10\uc57c \ud55c\ub2e4. \uadf8\ub807\uae30\ub3c4 \ud55c \uac8c \uc774 \ucc45\uc740 \ud30c\uc774\ud1a0\uce58\ub97c \uc54c\ub824\uc8fc\ub294 \ucc45\uc774 \uc544\ub2c8\uae30 \ub54c\ubb38\uc774\ub2e4. \uadf8\ub807\uae30\uc5d0 \ud30c\uc774\ud1a0\uce58\ub3c4 \ubbf8\ub9ac \uacf5\ubd80\ud558\uace0 \uc77d\uae30\ub97c \ubc14\ub780\ub2e4. [\ud30c\uc774\ud1a0\uce58 \ud55c\uad6d\uc5b4 \ud29c\ud1a0\ub9ac\uc5bc](https://tutorials.pytorch.kr/)\uc774\ub77c\ub3c4 \uac04\ub2e8\ud558\uac8c \ud55c \ubc88 \uc77d\uace0 \uc804\uccb4\uc801\uc778 \uad6c\uc870\ub97c \uc54c\uac8c \ub418\uba74 \uc77d\uae30 \uc218\uc6d4\ud560 \uac83\uc774\ub2e4.\\n\\n### \ub2e4\uc591\ud55c \ubc94\uc704\\n\\nCNN, RNN, \uac15\ud654\ud559\uc2b5, \uc0dd\uc131 \ubaa8\ub378, XAI \uadf8\ub9ac\uace0 NTM\uc774\ub77c\uace0 \ubd88\ub9ac\ub294 Neural Turing Machines\uae4c\uc9c0 \ub2e4 \uc5b8\uae09\ud558\uace0 \uac04\ub2e8\ud558\uac8c\ub77c\ub3c4 \ub2e4\ub904\ubcf8\ub2e4. CNN, RNN, \uac15\ud654\ud559\uc2b5\uc5d0 \ube44\ud558\uba74 \uc9c0\uc5fd\uc801\uc774\uae30\uc5d0 XAI, NTM \ubd80\ubd84\uc740 \uac1c\uc778\uc801\uc73c\ub85c \ub108\ubb34 \uc5b4\ub824\uc6e0\ub2e4. \uadf8\ub7ec\ub098 \uc774\ub7f0 \ubd84\uc57c\uac00 \uc788\ub2e4\ub294 \uac78 \uc54c\uac8c \ub418\uc5b4\uc11c \ud765\ubbf8\ub85c\uc6e0\ub358 \ubd80\ubd84\uc774\uae30\ub3c4 \ud588\ub2e4.\\n\\n## \ub300\uc0c1 \ub3c5\uc790\\n\\n\ucd08\uc2ec\uc790\uc5d0\uac8c\ub294 \uc5b4\ub824\uc6b4 \ucc45\uc774\ub77c \uc0dd\uac01\ub418\uae30\uc5d0 \ucd94\ucc9c\ud558\uace0 \uc2f6\uc9c0 \uc54a\ub2e4. \uadf8\ub7ec\ub098 \uc5b4\ub290 \uc815\ub3c4 \ub525\ub7ec\ub2dd\uc758 \ud750\ub984\uc744 \uc54c\uace0 \uc788\ub2e4\uba74 \uc774 \ucc45\uc744 \uad1c\ucc2e\uac8c \uc77d\uc744 \uc218 \uc788\uc744 \uac83\uc774\ub2e4. \ub525\ub7ec\ub2dd\uc744 \ub9d0\ub85c \uc124\uba85\uc774 \uc798 \ub418\uc5b4 \uc788\ub294 \ucc45\uc73c\ub85c \ubcf5\uc2b5\ud558\uace0 \uc2f6\ub2e4\uba74 \uc774 \ucc45\uc744 \ucd94\ucc9c\ud55c\ub2e4."},{"id":"/Generative-Deep-Learning","metadata":{"permalink":"/en/blog/Generative-Deep-Learning","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2023-10-29-Generative-Deep-Learning/2023-10-29-Generative-Deep-Learning.md","source":"@site/blog/2023-10-29-Generative-Deep-Learning/2023-10-29-Generative-Deep-Learning.md","title":"\ud83d\udcd6 \ub9cc\ub4e4\uba74\uc11c \ubc30\uc6b0\ub294 \uc0dd\uc131 AI","description":"\ud2b8\ub79c\uc2a4\ud3ec\uba38\ubd80\ud130 GPT, DALL\xb7E 2, \uc2a4\ud14c\uc774\ube14 \ub514\ud4e8\uc804, \ud50c\ub77c\ubc0d\uace0\uae4c\uc9c0","date":"2023-10-29T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \ub9cc\ub4e4\uba74\uc11c \ubc30\uc6b0\ub294 \uc0dd\uc131 AI","date":"2023-10-29","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/Generative-Deep-Learning","description":"\ud2b8\ub79c\uc2a4\ud3ec\uba38\ubd80\ud130 GPT, DALL\xb7E 2, \uc2a4\ud14c\uc774\ube14 \ub514\ud4e8\uc804, \ud50c\ub77c\ubc0d\uace0\uae4c\uc9c0","keywords":["DALL\xb7E 2","Stable Diffusion","\ub525\ub7ec\ub2dd"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \ub525\ub7ec\ub2dd\uc758 \uc815\uc11d","permalink":"/en/blog/Fundamentals-of-DL"},"nextItem":{"title":"\ud83d\udcd6 MLOps \uc2e4\uc804 \uac00\uc774\ub4dc","permalink":"/en/blog/practical-MLOps"}},"content":":::info\\n\ud55c\ube5b\ubbf8\ub514\uc5b4 \\\\<\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4\\\\> \ud65c\ub3d9\uc744 \uc704\ud574\uc11c \ucc45\uc744 \uc81c\uacf5\ubc1b\uc544 \uc791\uc131\ub41c \uc11c\ud3c9\uc785\ub2c8\ub2e4.\\n:::\\n\\n![\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4 2023](../assets/I-am-reviewer-2023.jpg)\\n\\n## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/Generative-deep-learning.jpg)](https://product.kyobobook.co.kr/detail/S000208953342)\\n\\n- \uc81c\ubaa9: \ub9cc\ub4e4\uba74\uc11c \ubc30\uc6b0\ub294 \uc0dd\uc131 AI\\n- \uc800\uc790: \ub370\uc774\ube44\ub4dc \ud3ec\uc2a4\ud130\\n- \uc5ed\uc790: \ubc15\ud574\uc120\\n- \ucd9c\ud310\uc0ac: \ud55c\ube5b\ubbf8\ub514\uc5b4\\n- \ucd9c\uac04: 2023-09-15\\n\\n{/* truncate */}\\n\\n## Intro\\n\\n\ucd5c\uadfc \uc0dd\uc131 AI\ub294 AI \ubd84\uc57c\uc5d0\uc11c \uac00\uc7a5 \ub728\uace0 \uc788\ub294 \ubd84\uc57c\ub77c\uace0 \uc0dd\uac01\ud55c\ub2e4. OpenAI\uc758 GPT4\ubd80\ud130 \ucd5c\uadfc\uc5d0\ub294 DALL\xb7E 3\uae4c\uc9c0 \ub9ce\uc740 \ubc1c\uc804\uc744 \uc774\ub8e8\uace0 \uc788\ub294 \ubd84\uc57c\uc774\ub2e4. OpenAI\ubfd0\ub9cc \uc544\ub2c8\ub77c \ub2e4\ub978 \uae30\uc5c5\ub4e4\uc5d0\uc11c\ub3c4 \uc5c4\uccad\ub09c \uc81c\ud488\ub4e4\uc744 \ub9cc\ub4e4\uc5b4\ub0b4\uace0 \uc788\ub2e4. AI\uac00 \uc810\uc810 \ub354 \uc6b0\ub9ac\uc758 \uc0dd\ud65c\uc744 \ud3b8\ud558\uac8c \ud574\uc8fc\uba70 \uc880 \ub354 \uac00\uae4c\uc6cc\uc9c0\uace0 \uc788\ub294 \ubaa8\uc2b5\uc774\ub2e4. \uc774\ub7f0 \uc138\uc0c1 \uc18d\uc5d0\uc11c \uc0dd\uc131 AI\uc5d0 \ub300\ud574 \ub354 \uad81\uae08\ud574\uc84c\uace0, \uc774 \ucc45\uc744 \uc77d\uac8c \ub410\ub2e4. \uc815\ub9d0 GAN\uc744 \uacf5\ubd80\ud588\uc744 \ub54c\uac00 \uc5ca\uadf8\uc81c \uac19\uc740\ub370, \uc9c4\uc9dc \ube60\ub974\uac8c \ubc1c\uc804\ud588\ub2e4. \uc55e\uc73c\ub85c AI\uac00 \uc5bc\ub9c8\ub098 \ub354 \ubc1c\uc804\ud560\uc9c0 \ud55c\ud3b8\uc73c\ub85c\ub294 \ubb34\uc12d\uae30\ub3c4 \ud558\uc9c0\ub9cc \uc778\uacf5\uc9c0\ub2a5\uc744 \uacf5\ubd80\ud558\uaca0\ub2e4\uace0 \ub9c8\uc74c \uba39\uc740 \uc774\uc0c1 \ud3ec\uae30\ud558\uc9c0 \uc54a\uace0 \uacc4\uc18d \ub418\ub294 \ub370\uae4c\uc9c0\ub294 \uacf5\ubd80\ud574\ubcf4\ub824\uace0 \ud55c\ub2e4.\\n\\n## Book Review\\n\\n### \ub9cc\ub4e4\uba74\uc11c \ubc30\uc6b0\ub294\\n\\n\uc815\ub9d0 \uc81c\ubaa9\uc744 \uc798 \uc9c0\uc5c8\ub2e4\uace0 \uc0dd\uac01\ud55c\ub2e4. \uc9c1\uc811 \ucf00\ub77c\uc2a4\ub85c \ubaa8\ub378\uc744 \ub9cc\ub4e4\uc5b4 \ubcf4\uba74\uc11c \ud558\ub098\ud558\ub098 \uacf5\ubd80\ud558\ub294 \uad6c\uc870\uc774\ub2e4. \uc774\ub7f0 \ucc45\uc774 \uc815\ub9d0 \uc88b\ub2e4. \uc774\ub860\uc801\uc778 \ubd80\ubd84\ub9cc \uacf5\ubd80\ud558\ub2e4\uac00\ub294 \uc774\ud574\uac00 \uc798\ub418\uc9c0 \uc54a\ub294 \uacbd\ud5a5\uc774 \uc788\ub2e4. \uadf8\ub798\uc11c \uc774\ub860\uc801\uc778 \ubd80\ubd84\uc744 \uacf5\ubd80\ud558\uba74\uc11c \uc2e4\uc2b5\ub3c4 \ubcd1\ud589\ud558\ub294 \uac83\uc774 \uc815\ub9d0 \uc88b\ub2e4. \uc774 \ucc45\ub3c4 \uadf8\ub7f0 \ucc45\uc774\ub2e4. \\n\\n### \ub418\ub3c4\ub85d \uc26c\uc6b4 \uc124\uba85\\n\\n\uc800\uc790\uac00 \ub9ce\uc740 \uc2e0\uacbd\uc744 \uc4f4 \ucc45\uc774\ub77c \uc0dd\uac01\ud55c\ub2e4. AI \ubd84\uc57c \uc911\uc5d0\uc11c\ub3c4 \uc0dd\uc131 AI\ub294 \uc815\ub9d0 \uc5b4\ub824\uc6b4 \ubd84\uc57c\ub77c\uace0 \ub290\ub080\ub2e4. \uadf8\ub798\uc11c \uc774 \ucc45\uc744 \uc77d\uae30 \uc804\uc5d0\ub294 \ubb50\ub784\uae4c \uc608\uc804\uc5d0 GAN\uc744 \uacf5\ubd80\ud588\ub358 \uae30\uc5b5\uc774 \ub5a0\uc624\ub974\uba74\uc11c \uc5c4\uccad \uc5b4\ub824\uc6b8 \uac83 \uac19\uc558\ub2e4. \uadf8\ub7ec\ub098 \uc800\uc790\uac00 \ucda9\ubd84\ud788 \uc774\ud574\ud560 \uc218 \uc788\ub3c4\ub85d \uc608\uc2dc\ub97c \ub4e4\uae30\ub3c4 \ud558\uace0 \uadf8\ub9bc \uc0bd\ud654\uac00 \ub9e4\uc6b0 \uc801\uc808\ud558\uac8c \uc788\uc5b4 \ucc45\uc744 \ud3b8\ud558\uac8c \uc77d\uc744 \uc218 \uc788\uc5c8\ub2e4. \uadf8\ub9bc\ub4e4\ub3c4 \uac70\uc758 \uc9c1\uc811 \ub17c\ubb38\uc5d0\uc11c \ubc1c\ucdcc\ud55c \uac83\uc774\uc5c8\ub2e4. \uc218\ub9ce\uc740 \ub17c\ubb38\ub4e4\uc744 \uc77d\ub294 \ub300\uc2e0 \uc774 \ucc45\uc744 \ud3b8\ud558\uac8c \uc77d\uc744 \uc218 \uc788\uc5c8\ub2e4. \uc601\uc5b4\uac00 \uc5b4\ub835\uace0, \ub17c\ubb38\uc744 \uc77d\uae30 \ud798\ub4dc\uc2e0 \ubd84\ub4e4\uaed8\ub294 \ub3c4\uc6c0\uc774 \ub418\uc9c0 \uc54a\uc744\uae4c \uc2f6\ub2e4. \ub610\ud55c, \ubbff\uc744 \uc218 \uc788\ub294 \uc5ed\uc790\ub2d8\uaed8\uc11c \ubc88\uc5ed\ud558\uc168\uae30\uc5d0 \ubc88\uc5ed\uc5d0 \ud070 \uc758\uc2ec\uc740 \ud558\uc9c0 \uc54a\uace0 \uc77d\uc5b4\ub3c4 \ub410\ub2e4. \\n\\n### \ud2b9\ud788 \uc88b\uc558\ub358 \ubd80\ubd84\\n\\n\uc544\ubb34\ub798\ub3c4 \uac00\uc7a5 \uc88b\uc558\ub358 \ubd80\ubd84\uc740 \uc774 \ucc45\uc758 \ub9c8\uc9c0\ub9c9 \ubd80\ubd84\uc774\uc5c8\ub2e4. \uaf64 \ucd5c\uc2e0 \uae30\uc220\uc778 DALL\xb7E 2\uc640 \uc2a4\ud14c\uc774\ube14 \ub514\ud4e8\uc804 \uad6c\uc870\ub97c \uc870\uae08\uc774\ub098\ub9c8 \uacf5\ubd80\ud574 \ubcfc \uc218 \uc788\ub2e4\ub294 \uac83 \ub610\ud55c \uc774 \ucc45\uc758 \uc7a5\uc810\uc774\ub77c \uc0dd\uac01\ud55c\ub2e4. \ub610 \uc88b\uc558\ub358 \ubd80\ubd84\uc740 \ub9c8\uc9c0\ub9c9\uc5d0 \uc0dd\uc131 AI\uc758 \ud0c0\uc784\ub77c\uc778\uc774 \ub098\uc624\ub294\ub370 \uc9c0\uae08\uae4c\uc9c0 \uc5b4\ub5a4 \ubaa8\ub378\ub4e4\uc774 \uc788\uc5c8\uace0 \uc5b4\ub5bb\uac8c \ubc1c\uc804\ud558\uace0 \uc788\ub294\uc9c0 \uc54c\uc544\ubcfc \uc218 \uc788\uc5b4\uc11c \uc88b\uc558\ub2e4. \\n\\n## \ub300\uc0c1 \ub3c5\uc790\\n\\n\uc0dd\uc131 AI\ub97c \uc804\ud600 \ubab0\ub77c\ub3c4 \uc77d\uc744 \uc218 \uc788\ub294 \ucc45\uc774\uc9c0\ub9cc \uadf8\ub798\ub3c4 \uae30\ubcf8\uc801\uc778 \ub525\ub7ec\ub2dd \uac1c\ub150\uc744 \uc54c\uace0 \ucd5c\uc2e0 \ub525\ub7ec\ub2dd \uae30\uc220\ub4e4\uc5d0 \ub300\ud574\uc11c\ub3c4 \uad00\uc2ec \uc788\uace0 \uacf5\ubd80\ud558\uc2e0 \ubd84\uc774 \uc77d\uc73c\uba74 \ub3c4\uc6c0\uc774 \ub420 \uac83\uc774\ub77c \uc0dd\uac01\ud55c\ub2e4. \ub610\ud55c, \uc0dd\uc131 AI\uc5d0 \ub300\ud574 \ub354 \uae4a\uac8c \uacf5\ubd80\ud558\uace0 \uc2f6\uc740 \ubd84\ub4e4\uaed8\ub3c4 \ucd94\ucc9c\ud55c\ub2e4."},{"id":"/practical-MLOps","metadata":{"permalink":"/en/blog/practical-MLOps","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2023-07-23-practical-MLOps/2023-07-23-practical-MLOps.md","source":"@site/blog/2023-07-23-practical-MLOps/2023-07-23-practical-MLOps.md","title":"\ud83d\udcd6 MLOps \uc2e4\uc804 \uac00\uc774\ub4dc","description":"DevOps\uc640 MLOps\uc758 \uc774\ub860\uacfc \uc2e4\uc2b5\ubd80\ud130 \ud074\ub77c\uc6b0\ub4dc \ucef4\ud4e8\ud305, AutoML, \uc5e3\uc9c0 \ucef4\ud4e8\ud305\uae4c\uc9c0 | AWS, GCP, Azure \ud074\ub77c\uc6b0\ub4dc \uae30\ubc18 \ud658\uacbd \uc2e4\uc2b5","date":"2023-07-23T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 MLOps \uc2e4\uc804 \uac00\uc774\ub4dc","date":"2023-07-23","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/practical-MLOps","description":"DevOps\uc640 MLOps\uc758 \uc774\ub860\uacfc \uc2e4\uc2b5\ubd80\ud130 \ud074\ub77c\uc6b0\ub4dc \ucef4\ud4e8\ud305, AutoML, \uc5e3\uc9c0 \ucef4\ud4e8\ud305\uae4c\uc9c0 | AWS, GCP, Azure \ud074\ub77c\uc6b0\ub4dc \uae30\ubc18 \ud658\uacbd \uc2e4\uc2b5","keywords":["MLOps","DevOps","\uba38\uc2e0\ub7ec\ub2dd"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \ub9cc\ub4e4\uba74\uc11c \ubc30\uc6b0\ub294 \uc0dd\uc131 AI","permalink":"/en/blog/Generative-Deep-Learning"},"nextItem":{"title":"\ud83d\udcd6 \ud14c\ub77c\ud3fc\uc73c\ub85c \uc2dc\uc791\ud558\ub294 IaC","permalink":"/en/blog/terraform101"}},"content":":::info\\n\ud55c\ube5b\ubbf8\ub514\uc5b4 \\\\<\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4\\\\> \ud65c\ub3d9\uc744 \uc704\ud574\uc11c \ucc45\uc744 \uc81c\uacf5\ubc1b\uc544 \uc791\uc131\ub41c \uc11c\ud3c9\uc785\ub2c8\ub2e4.\\n:::\\n\\n![\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4 2023](../assets/I-am-reviewer-2023.jpg)\\n\\n## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/practical-MLOps.jpg)](https://product.kyobobook.co.kr/detail/S000202854952)\\n\\n- \uc81c\ubaa9: MLOps \uc2e4\uc804 \uac00\uc774\ub4dc\\n- \uc800\uc790: \ub178\uc544 \uae30\ud504\ud2b8, \uc54c\ud504\ub808\ub3c4 \ub370\uc790\\n- \uc5ed\uc790: \uc774\uc7a5\ud6c4, \uc774\uc77c\uc12d, \uc11c\uae30\uc6d0\\n- \ucd9c\ud310\uc0ac: \ud55c\ube5b\ubbf8\ub514\uc5b4\\n- \ucd9c\uac04: 2023-05-24\\n\\n{/* truncate */}\\n\\n## Intro\\n\\nMLOps\ub294 \uba38\uc2e0\ub7ec\ub2dd\uc774 \ub728\uae30 \uc2dc\uc791\ud558\uae30\ubd80\ud130 \uc5c4\uccad \ub728\uace0 \uc788\ub294 \ubd84\uc57c\uc774\ub2e4. \uc694\uc998\uc740 \uba38\uc2e0\ub7ec\ub2dd \ubaa8\ub378\ub9cc \uc798 \ub9cc\ub4e4\uc5b4\uc57c \ub418\ub294 \uac83\uc774 \uc544\ub2cc \ud30c\uc774\ud504\ub77c\uc778\uae4c\uc9c0 \uc798 \ub9cc\ub4e4\uc5b4\uc57c \ud55c\ub2e4. MLOps\ub294 \uba38\uc2e0\ub7ec\ub2dd(ML)\uacfc DevOps\uc758 \ud569\uc131\uc5b4\ub85c \uc27d\uac8c \uc124\uba85\ud558\uba74 \uba38\uc2e0\ub7ec\ub2dd \ubaa8\ub378\uc744 \uac1c\ubc1c\ud558\uace0 \ubc30\ud3ec\ud558\ub294 \uacfc\uc815\uc744 \uc790\ub3d9\ud654\ud558\ub294 \uac83\uc774\ub2e4. \uc774\uac8c \uc5b4\ub5bb\uac8c \uac00\ub2a5\ud55c\uac00 \uad81\uae08\ud55c \uc0ac\ub78c\ub4e4\uc774 \ub9ce\uc744 \uac83\uc774\ub2e4. \ub098 \ub610\ud55c \uadf8\ub807\uac8c \uacf5\ubd80\ub97c \uc2dc\uc791\ud588\uc73c\uba70, \ud604\uc7ac\ub294 \uba38\uc2e0\ub7ec\ub2dd\uc744 \uacf5\ubd80\ud558\uace0 \ub354 \ub098\uc740 \ud30c\uc774\ud504\ub77c\uc778\uc744 \ub9cc\ub4e4\uae30 \uc704\ud574 MLOps, \ubc31\uc5d4\ub4dc \ubd80\ubd84\uc744 \ub354 \uacf5\ubd80\ud574\ubcf4\uace0 \uc788\ub2e4. \uc774 \ucc45 \ub610\ud55c \ub098\uc5d0\uac8c \ub3c4\uc6c0\uc774 \ub420 \uac83 \uac19\uc544 \uc77d\uac8c \ub410\ub2e4.\\n\\n## Book Review\\n\\n### \ucd08\uc2ec\uc790\ub3c4 \uc77d\uc744\ub9cc\ud55c\uac00\\n\\n\uc5c4\uccad \uc27d\ub2e4\uace0\ub294 \ub9d0\ud558\uc9c0 \ubabb \ud560 \uac83 \uac19\ub2e4. \uadf8\ub7ec\ub098 MLOps\uc5d0 \ub300\ud574 \uad00\uc2ec\uc774 \uc788\uace0 \ud55c \ubc88\uc774\ub77c\ub3c4 \uacf5\ubd80\ud574\ubcf8 \ubd84\ub4e4\uaed8\ub294 \uc774 \ucc45\uc744 \ucd94\ucc9c\ud560 \uac83 \uac19\ub2e4. \uc81c\ubaa9 \uadf8\ub300\ub85c \uc2e4\uc804 \uac00\uc774\ub4dc\uc774\uae30\uc5d0 \uc774\ub860\uc801\uc778 \ubd80\ubd84\ubcf4\ub2e4\ub294 \uc2e4\uc2b5 \uc704\uc8fc\ub85c \uc124\uba85\uc774 \ub418\uc5b4\uc788\ub2e4. \uadf8\ub7ec\ub098 \uc774\ub860\uc801\uc778 \ubd80\ubd84\ub3c4 \ucda9\ubd84\ud788 \uc124\uba85\uc774 \ub418\uc5b4\uc788\uae30 \ub54c\ubb38\uc5d0 \ucee4\ub9e8\ub4dc\ub77c\uc778 \ud658\uacbd, \ud30c\uc774\uc36c, \ucee8\ud14c\uc774\ub108 \ub4f1 \uae30\ubcf8 \uac1c\ub150\ub9cc \uc788\ub2e4\uba74 \uc774 \ucc45\uc744 \uc77d\uae30\uc5d0 \ub354\uc6b1 \uc218\uc6d4\ud560 \uac83\uc774\ub2e4. MLOps\uc5d0 \uad00\uc2ec\uc774 \uc788\ub2e4\uba74 \ud55c \ubc88\ucbe4 \uc77d\uc5b4\ubd10\uc57c \ud560 \ucc45\uc774\ub77c\uace0 \uc0dd\uac01\ud55c\ub2e4.\\n\\n### \uc2e4\uc2b5 \uac00\uc774\ub4dc\\n\\n\uc774 \ucc45\uc740 \uc704\uc5d0\uc11c\ub3c4 \uc5b8\uae09 \ud588\ub4ef\uc774 \uc2e4\uc2b5 \uac00\uc774\ub4dc \uadf8 \uc790\uccb4\ub77c\uace0 \ub9d0\ud560 \uc218 \uc788\ub2e4. MLOps\ub97c \uacf5\ubd80\ud558\uace0 \uc2f6\ub2e4\uba74 \uc774\ub7f0 \uac83\ub4e4\uc740 \uc54c\uc544\uc57c \ud55c\ub2e4\uace0 \uac04\uc811\uc801\uc73c\ub85c \uc54c\ub824\uc8fc\ub294\ub4ef\uc774 \ub9d0\ud55c\ub2e4. MLOps\ub77c\ub294 \ubd84\uc57c \uc790\uccb4\uac00 \uc5c4\uccad \ub9ce\uc740 \uac83\ub4e4\uc744 \uc54c\uc544\uc57c \ud560 \uc218 \uc788\ub294 \ubd84\uc57c\uc774\uae30\uc5d0 \uc774\ub807\uac8c \uc124\uba85\ud588\ub2e4\uace0 \uc0dd\uac01\ud55c\ub2e4. \uae4a\uc774\ubcf4\ub2e4\ub294 \ub2e4\uc591\ud55c \uac83\ub4e4\uc744 \uc54c\ub824\uc8fc\uae30 \uc704\ud55c \ucc45\uc774\ub77c \ub9d0\ud560 \uc218 \uc788\ub2e4. \uadf8\ub798\uc11c \uc774 \ucc45\uc744 \uc77d\uace0 \ub098\uba74 MLOps\uc5d0 \ub300\ud574 \uc5b4\ub5a4 \uac83\ub4e4\uc744 \uacf5\ubd80\ud574\uc57c \ud558\ub294\uc9c0 \uc54c \uc218 \uc788\uc744 \uac83\uc774\ub2e4. \uc7ac\ubc0c\uc5c8\ub358 \uc810\uc740 \uac01 \ucc55\ud130\uc758 \ub9c8\uc9c0\ub9c9\uc5d0 \'\uc5f0\uc2b5\ud574\ubcf4\uae30\'\uc640 \'\uc0dd\uac01\ud574\ubcf4\uae30\'\uac00 \uc788\ub294\ub370 \ubb38\uc81c\ub97c \ud574\uacb0\ud574\uc57c \ud55c\ub2e4\ub294 \uac83\uc774 \uc7ac\ubc0c\uc5c8\ub2e4. \uc5f0\uc2b5\ud574\ubcf4\uae30\ub294 \uc2e4\uc2b5 \uc704\uc8fc\ub85c \ubb38\uc81c\ub97c \ud480\uc5b4\uc57c \ud558\uace0 \uc0dd\uac01\ud574\ubcf4\uae30\ub294 \uc774\ub860\uc801\uc778 \ubd80\ubd84\uc744 \uc0dd\uac01\ud574\uc11c \ud480\uc5b4\ubd10\uc57c \ud55c\ub2e4. \uc774\ub7f0 \ubd80\ubd84\uae4c\uc9c0 \ub2e4 \ud574\uacb0\ud574\ubcf4\uba74 MLOps\uc5d0 \ub300\ud574 \ub354 \uc798 \uc774\ud574\ud560 \uc218 \uc788\uc9c0 \uc54a\uc744\uae4c. \uac1c\uc778\uc801\uc73c\ub85c \uc124\uba85\ub3c4 \uaf64 \uc798 \ub418\uc5b4 \uc788\uace0, \ubc88\uc5ed\ub3c4 \uc798 \ub418\uc5b4 \uc788\ub2e4\uace0 \ub290\uaf08\ub2e4. \ub610\ud55c, \uc9c0\uae08 \ud558\uace0 \uc788\ub294 \uacf5\ubd80\uc5d0 \uc5ec\uc720\uac00 \uc0dd\uae34\ub2e4\uba74 MLOps\uc5d0 \ub300\ud574 \ub354 \uae4a\uac8c \uacf5\ubd80\ud574\ubcf4\uace0 \uc2f6\ub2e4\ub294 \uc0dd\uac01\uc774 \ub4e4\uc5c8\ub2e4.\\n\\n### 21\ub144\ub3c4\uc5d0 \ubc88\uc5ed\ub418\uc5c8\ub2e4\uba74\\n\\n\uc870\uae08 \uc544\uc26c\uc6e0\ub358 \uc810\uc740 \uc774 \ucc45\uc774 flask\ub97c \uc0ac\uc6a9\ud55c\ub2e4\ub294 \uc810. \uc694\uc998\ub3c4 \ubb3c\ub860 flask\ub97c \ub9ce\uc774 \uc0ac\uc6a9\ud558\uace0 \uc788\uaca0\uc9c0\ub9cc, fastapi\ub97c \uc0ac\uc6a9\ud558\ub294 \ucd94\uc138\uc774\uc9c0 \uc54a\ub098 \uc2f6\ub2e4. \uae30\ud68c\uac00 \ub41c\ub2e4\uba74 \uc774 \ucc45\uc758 flask \ucf54\ub4dc\ub97c fastapi\ub85c \ubc14\uafd4\ubcf4\ub294 \uac83\ub3c4 \uc7ac\ubc0c\ub294 \uacbd\ud5d8\uc774 \ub420 \uac83 \uac19\ub2e4.\\n\\n\uc774\ucabd \uae30\uc220\uc740 \ud56d\uc0c1 \ubcc0\ud654\uac00 \ube60\ub974\uae30\uc5d0 \uc774\ub7f0 \ubd80\ubd84\uc5d0\uc11c\ub294 \uc544\uc26c\uc6c0\uc774 \ub0a8\ub294\ub2e4. \uc544\ubb34\ub798\ub3c4 \uc774 \ucc45\uc758 \uc6d0\uc11c\uac00 2021\ub144\ub3c4\uc5d0 \ucd9c\uac04\ub41c \uac83\uc774\ub77c \uadf8\ub7f0 \uac83 \uac19\ub2e4. 2021\ub144\ub3c4\uc5d0 \uc774 \ucc45\uc774 \ubc88\uc5ed\ub418\uc5b4 \ub098\uc654\ub2e4\uba74 \uc5c4\uccad \uc778\uae30\uac00 \uc788\uc5c8\uc9c0 \uc54a\uc558\uc744\uae4c.\\n\\n21\ub144\ub3c4\uc5d0\ub294 \ucf54\ub4dc\uac00 \ub3cc\uc544\uac14\uc9c0\ub9cc, \ud604\uc7ac 23\ub144\ub3c4\uc5d0\ub294 \uc548 \ub3cc\uc544\uac00\ub294 \ucf54\ub4dc\ub4e4\ub3c4 \uc788\uc744 \uc218 \uc788\uc744 \uc218 \uc788\uc73c\ub2c8\uae4c \uc5ed\uc790\ubd84\ub4e4\uaed8\uc11c \uadf8\ub7f0 \ubd80\ubd84\uc744 \ucc3e\uc544\ub0b4\uace0 \uc218\uc815 \ubc0f \ubcf4\uc644\ud558\ub294 \uacfc\uc815\uc5d0\uc11c \uc5c4\uccad \ud798\ub4dc\uc168\uc9c0 \uc54a\uc73c\uc168\uc744\uae4c \uc2f6\ub2e4. \\n\\n## \ub300\uc0c1 \ub3c5\uc790\\n\\nMLOps \ucabd\uc73c\ub85c \uc5c5\ubb34\ub97c \ud574\uc57c \ud558\ub294 \uc0ac\ub78c\uc5d0\uac8c \ucd94\ucc9c\ud55c\ub2e4. \uad00\uc2ec\uc774 \uc788\uac70\ub098 \uc774\ucabd\uc744 \uacbd\ud5d8\ud574\ubcf4\uace0 \uc2f6\uc740 \ubd84\ub4e4\uaed8\ub3c4 \ucd94\ucc9c\ud560 \uc218 \uc788\uc744 \uac83 \uac19\ub2e4. \uc0ac\uc804\uc5d0 \ucee8\ud14c\uc774\ub108, \ucee4\ub9e8\ub4dc\ub77c\uc778, \ubc31\uc5d4\ub4dc, \ud074\ub77c\uc6b0\ub4dc \ub4f1\uc744 \uc54c\uace0 \uc788\uc73c\uba74 \uc774 \ucc45\uc744 \uc77d\uae30 \ub354\uc6b1 \uc26c\uc6b8 \uac83\uc774\ub2e4."},{"id":"/terraform101","metadata":{"permalink":"/en/blog/terraform101","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2023-06-24-terraform101/terraform101.md","source":"@site/blog/2023-06-24-terraform101/terraform101.md","title":"\ud83d\udcd6 \ud14c\ub77c\ud3fc\uc73c\ub85c \uc2dc\uc791\ud558\ub294 IaC","description":"\ud14c\ub77c\ud3fc \uc785\ubb38\ubd80\ud130 \uc2e4\ubb34 \uc801\uc6a9\uae4c\uc9c0 \uc62c\uc778\uc6d0 \uac00\uc774\ub4dc","date":"2023-06-24T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \ud14c\ub77c\ud3fc\uc73c\ub85c \uc2dc\uc791\ud558\ub294 IaC","date":"2023-06-24","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/terraform101","description":"\ud14c\ub77c\ud3fc \uc785\ubb38\ubd80\ud130 \uc2e4\ubb34 \uc801\uc6a9\uae4c\uc9c0 \uc62c\uc778\uc6d0 \uac00\uc774\ub4dc","keywords":["\ud14c\ub77c\ud3fc","IaC","DevOps"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 MLOps \uc2e4\uc804 \uac00\uc774\ub4dc","permalink":"/en/blog/practical-MLOps"},"nextItem":{"title":"\ud83d\udcd6 Docs for Developers \uae30\uc220 \ubb38\uc11c \uc791\uc131 \uc644\ubcbd \uac00\uc774\ub4dc","permalink":"/en/blog/docs-for-developers"}},"content":":::info\\n\ud55c\ube5b\ubbf8\ub514\uc5b4 \\\\<\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4\\\\> \ud65c\ub3d9\uc744 \uc704\ud574\uc11c \ucc45\uc744 \uc81c\uacf5\ubc1b\uc544 \uc791\uc131\ub41c \uc11c\ud3c9\uc785\ub2c8\ub2e4.\\n:::\\n\\n![\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4 2023](../assets/I-am-reviewer-2023.jpg)\\n\\n## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/terraform101.jpg)](https://product.kyobobook.co.kr/detail/S000202478097)\\n\\n- \uc81c\ubaa9: \ud14c\ub77c\ud3fc\uc73c\ub85c \uc2dc\uc791\ud558\ub294 IaC\\n- \uc800\uc790: \uae40\ubbfc\uc218, \uae40\uc7ac\uc900, \uc774\uaddc\uc11d, \uc774\uc720\uc885\\n- \ucd9c\ud310\uc0ac: \ud55c\ube5b\ubbf8\ub514\uc5b4\\n- \ucd9c\uac04: 2023-05-24\\n\\n{/* truncate */}\\n\\n## Intro\\n\\n\uc0ac\uc2e4 \ub09c DevOps\ub294 \uc544\ub294 \uac83\uc774 \ubcc4\ub85c \uc5c6\ub294 \ud559\uc0dd\uc774\ub2e4. \ucfe0\ubc84\ub124\ud2f0\uc2a4\uc640 \ub3c4\ucee4\ub97c \uc7a0\uae50 \uacf5\ubd80\ud574\ubcf4\uae34 \ud588\uc9c0\ub9cc \uae4a\uac8c \uacf5\ubd80\ud558\uc9c0\ub294 \uc54a\uc558\ub2e4. \uadf8\ub807\uae30\uc5d0 \uc774 \ucc45\uc744 \uc120\ud0dd\ud588\uc744 \ub54c \ub450\ub824\uc6c0\ub3c4 \uc788\uc5c8\uc73c\ub098, \uc5b8\uc820\uac00\ub294 DevOps \ucabd\ub3c4 \uacf5\ubd80\ud574\ubcf4\uace0 \uc2f6\uc5c8\uae30\uc5d0 \uc774 \ucc45\uc744 \uc120\ud0dd\ud588\ub2e4. \ud14c\ub77c\ud3fc\uc774\ub77c\ub294 \uc6a9\uc5b4\ub3c4 \uac00\ub054 \uae30\uc5c5\ub4e4\uc758 CI/CD \ud30c\uc774\ud504\ub77c\uc778 \uc774\ubbf8\uc9c0\uc5d0\uc11c\ub9cc \ubcf4\uac70\ub098, \uc5bc\ud54f \ub4e4\uc5b4\ubcf4\uae30\ub9cc \ud588\uc9c0 \uc815\ud655\ud788 \ubb34\uc5c7\uc778\uc9c0\ub294 \ubab0\ub790\ub2e4. \uadf8\ub798\uc11c \uc774 \ucc45\uc744 \uc77d\uc73c\uba74\uc11c \ud14c\ub77c\ud3fc\uc774 \ubb34\uc5c7\uc778\uc9c0, \uc5b4\ub5bb\uac8c \uc0ac\uc6a9\ud558\ub294\uc9c0 \uc54c\uace0 \uc2f6\uc5c8\ub2e4.\\n\\n## Book Review\\n\\n### \ucd08\uc2ec\uc790\ub3c4 \uc77d\uc744\ub9cc\ud55c \ucc45 \\n\\n\uc704\uc5d0\uc11c\ub3c4 \uc5b8\uae09\ud588\ub4ef\uc774 \uc774 \ucc45\uc744 \uc77d\uae30 \uc804 \ub450\ub824\uc6c0\uc774 \ub9ce\uc558\uc5c8\ub2e4. \uc608\uc804\uc5d0 \ucfe0\ubc84\ub124\ud2f0\uc2a4, \ub3c4\ucee4\ub97c \uacf5\ubd80\ud574\ubcfc \ub54c \'\uadf8\ub0e5 \ud558\uba74 \ub418\uaca0\uc9c0\'\ub77c\ub294 \uc2ec\uc815\uc73c\ub85c \uacf5\ubd80\ub97c \ud588\ub294\ub370 \uc0dd\uac01\ubcf4\ub2e4 \uc5b4\ub824\uc6e0\uc5b4\uc11c \ud14c\ub77c\ud3fc\ub3c4 \uc5b4\ub835\uc9c0 \uc54a\uc744\uae4c \uc0dd\uac01\ud588\uc5c8\ub2e4. \uac8c\ub2e4\uac00 DevOps \ucd08\uc2ec\uc790\ub77c \uacf5\ubd80\ud558\uba74\uc11c \ubaa8\ub974\ub294 \uc6a9\uc5b4\ub3c4 \ubd84\uba85\ud788 \ub9ce\uc744 \uac70\ub77c \uc0dd\uac01\ud574\uc11c \ucc45\uc744 \uc77d\uae30\ub3c4 \uc804\uc5d0 \uac81\uc744 \uba39\uc5c8\uc5c8\ub2e4. \uadf8\ub7ec\ub098 \ub450\ub824\uc6c0\uc740 \ucc45\uc744 \uc77d\uc73c\uba70 \uc810\ucc28 \uc0ac\ub77c\uc84c\uace0, \uc774 \ucc45\uc740 \ucd08\uc2ec\uc790\ub3c4 \uc77d\uc73c\ub824\uace0 \ub178\ub825\ub9cc \ud55c\ub2e4\uba74 \uc77d\uc744\ub9cc\ud55c \ucc45\uc774\ub77c\ub294 \uac83\uc744 \ub290\uaf08\ub2e4.\\n\\n\ud14c\ub77c\ud3fc\uc758 \uae30\ubcf8 \uc0ac\uc6a9\ubc95\ubd80\ud130, \ub3d9\uc791 \ubc29\uc2dd, \uc5b4\ub5bb\uac8c \uc801\uc6a9\ud558\uace0 \uc0ac\uc6a9\ud558\ub294\uc9c0 \ub4f1 \ud14c\ub77c\ud3fc\uc744 \uc5b4\ub5bb\uac8c \ud65c\uc6a9\ud560 \uc218 \uc788\ub294\uc9c0 \uc124\uba85\ud55c\ub2e4. \ud06c\uac8c 1\ubd80\uc640 2\ubd80\ub85c \ub098\ub258\uc5b4\uc9c0\ub294\ub370 1\ubd80\uc5d0\uc11c\ub294 \uae30\ubcf8\uc801\uc73c\ub85c \ud14c\ub77c\ud3fc\uc744 \uc5b4\ub5bb\uac8c \uc0ac\uc6a9\ud558\ub294\uc9c0 \uadf8\ub9ac\uace0 \ud504\ub85c\ubc14\uc774\ub354, \ub3d9\uc791 \ubc29\uc2dd \ub4f1\uc744 \uc124\uba85\ud55c\ub2e4. 2\ubd80\uc5d0\uc11c\ub294 \ud14c\ub77c\ud3fc\uc73c\ub85c \ud504\ub85c\ube44\uc800\ub2dd\uc744 \ud558\uba74\uc11c \uc0ac\uc6a9 \uaddc\ubaa8\uac00 \ud655\uc7a5\ub418\ub294 \ubaa8\ubc94 \uc0ac\ub840\ub97c \uc54c\ub824\uc900\ub2e4. \uc774 \ubd80\ubd84\uc774 \ub098\ub294 \uc7a5\uc810\uc774\ub77c \uc0dd\uac01\ud588\ub2e4. \ub2e4\ub978 \ud300 \ub610\ub294 \uc870\uc9c1\uacfc \ud611\uc5c5\ud558\ub294 \ubc29\ubc95\uc744 \uc54c\ub824\uc8fc\uae30 \ub54c\ubb38\uc774\ub2e4. \ub098\uc911\uc5d0 \ud14c\ub77c\ud3fc\uc744 \ub3c4\uc785\ud560 \uc77c\uc774 \uc0dd\uae34\ub2e4\uba74 \uc774 \ucc45\uc744 \ub2e4\uc2dc \ubcfc \uc608\uc815\uc774\ub2e4.\\n\\n## \ub300\uc0c1 \ub3c5\uc790\\n\\n\ub300\uc0c1 \ub3c5\uc790\ub294 DevOps\uc5d0 \uad00\uc2ec\uc774 \uc788\uae30 \ubcf4\ub2e4\ub294 \ud14c\ub77c\ud3fc\uc774\ub77c\ub294 \uae30\uc220\uc5d0 \ub300\ud574 \uba3c\uc800 \uc54c\uace0 \uacf5\ubd80\ud558\uace0 \uc2f6\uc740 \uc0ac\ub78c\ub4e4\uc5d0\uac8c \ucd94\ucc9c\ud55c\ub2e4. \ud2b9\ud788 \uc778\ud504\ub77c \uc5d4\uc9c0\ub2c8\uc5b4, \ub370\ube0c\uc635\uc2a4 \uc5d4\uc9c0\ub2c8\uc5b4 \ub4f1 \uc11c\ube44\uc2a4\ub97c \uc124\uacc4\ud558\uace0 \uad00\ub9ac\ud558\ub294 \uc9c1\uad70\uc5d0\uac8c \uc5b4\uc6b8\ub9ac\ub294 \ucc45\uc774 \uc544\ub2cc\uac00 \uc2f6\ub2e4."},{"id":"/docs-for-developers","metadata":{"permalink":"/en/blog/docs-for-developers","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2023-05-28-docs-for-developers/docs-for-developers.md","source":"@site/blog/2023-05-28-docs-for-developers/docs-for-developers.md","title":"\ud83d\udcd6 Docs for Developers \uae30\uc220 \ubb38\uc11c \uc791\uc131 \uc644\ubcbd \uac00\uc774\ub4dc","description":"\uad6d\ub0b4 \ud14c\ud06c\ub2c8\uceec \ub77c\uc774\ud130 11\uc778 \uc778\ud130\ubdf0 \uc218\ub85d","date":"2023-05-28T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 Docs for Developers \uae30\uc220 \ubb38\uc11c \uc791\uc131 \uc644\ubcbd \uac00\uc774\ub4dc","date":"2023-05-28","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/docs-for-developers","description":"\uad6d\ub0b4 \ud14c\ud06c\ub2c8\uceec \ub77c\uc774\ud130 11\uc778 \uc778\ud130\ubdf0 \uc218\ub85d","keywords":["\ubb38\uc11c\ud654","\uac1c\ubc1c\uc790","\ud14c\ud06c\ub2c8\uceec \ub77c\uc774\ud130","\uae30\uc220 \ubb38\uc11c"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \ud14c\ub77c\ud3fc\uc73c\ub85c \uc2dc\uc791\ud558\ub294 IaC","permalink":"/en/blog/terraform101"},"nextItem":{"title":"\ud83d\udcd6 Node.js \ubc31\uc5d4\ub4dc \uac1c\ubc1c\uc790 \ub418\uae30","permalink":"/en/blog/become-a-node-js-engineer"}},"content":":::info\\n\ud55c\ube5b\ubbf8\ub514\uc5b4 \\\\<\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4\\\\> \ud65c\ub3d9\uc744 \uc704\ud574\uc11c \ucc45\uc744 \uc81c\uacf5\ubc1b\uc544 \uc791\uc131\ub41c \uc11c\ud3c9\uc785\ub2c8\ub2e4.\\n:::\\n\\n![\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4 2023](../assets/I-am-reviewer-2023.jpg)\\n\\n## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/docs-for-developers.jpg)](https://product.kyobobook.co.kr/detail/S000201419245)\\n\\n- \uc81c\ubaa9: Docs for Developers \uae30\uc220 \ubb38\uc11c \uc791\uc131 \uc644\ubcbd \uac00\uc774\ub4dc\\n- \uc800\uc790: \uc790\ub808\ub4dc \ubc14\ud2f0, \uc7ac\ucee4\ub9ac \uc0ac\ub77c \ucf5c\ub77c\uc774\uc13c, \uc820 \ub7a8\ubcf8, \ub370\uc774\ube44\ub4dc \ub204\ub124\uc988, \ud558\uc774\ub4dc \uc6cc\ud130\ud558\uc6b0\uc2a4\\n- \uc5ed\uc790: \ud558\uc131\ucc3d\\n- \ucd9c\ud310\uc0ac: \ud55c\ube5b\ubbf8\ub514\uc5b4\\n- \ucd9c\uac04: 2023-04-10\\n\\n{/* truncate */}\\n\\n## Intro\\n\\n\ub098\ub294 \uc608\uc804\uc5d0 \uac1c\ubc1c\uc790\ub77c\uba74 \uadf8\uc800 \uac1c\ubc1c\ub9cc \uc798\ud558\uba74 \ub418\ub294 \uac74 \uc904 \uc54c\uc558\ub2e4. \uadf8\ub7ec\ub098 \uc2e4\uc0c1\uc740 \uadf8\ub807\uc9c0 \uc54a\uc558\ub2e4. \uc120\ubc30 \uac1c\ubc1c\uc790\ubd84\ub4e4\uc774 \uc4f0\uc2e0 \ucc45\ub4e4\uc744 \ud1b5\ud574 \ud611\uc5c5\uc774\ub77c\ub294 \uac83\uc744 \ud574\uc57c \ub41c\ub2e4\ub294 \uac83\uc744 \uc54c\uac8c \ub410\uace0, \ub610\ud55c \uc11c\ub85c \uc758 \uc18c\ud1b5\uc744 \ud574\uc57c \ud55c\ub2e4\ub294 \uac83\uc744 \uc54c\uac8c \ub410\ub2e4. \uc774 \uc18c\ud1b5\uc774 \uadf8\uc800 \ub9d0\ub85c \ud558\ub294 \ucee4\ubba4\ub2c8\ucf00\uc774\uc158\uc774\ub77c\uace0 \uc0dd\uac01\ud560 \uc218\ub3c4 \uc788\uaca0\uc9c0\ub9cc, \ubb38\uc11c\ub97c \ud1b5\ud574 \uc18c\ud1b5\ud558\ub294 \uacbd\uc6b0\ub3c4 \ub9ce\ub2e4\uace0 \ud55c\ub2e4. \uadf8\ub807\uae30\uc5d0 \ub098\ub294 \ubb38\uc11c\ub97c \uc798 \uc4f0\ub294 \uac83 \ub610\ud55c \uac1c\ubc1c\uc790\uc5d0\uac8c \uac1c\ubc1c\ud558\ub294 \uac83\ub9cc\ud07c \uc911\uc694\ud55c \ub2a5\ub825\uc774\ub77c \uc0dd\uac01\ud588\uace0 \uc774 \ucc45\uc744 \uc120\ud0dd\ud558\uac8c \ub418\uc5c8\ub2e4.\\n\\n## Book Review\\n\\n### \ub204\uad6c\ub97c \uc704\ud55c \ubb38\uc11c\uc778\uac00\\n\\n\uc774 \ucc45\uc740 **\'\uc6b0\ub9ac\uac00 \uc791\uc131\ud558\uace0 \uc788\ub294 \ubb38\uc11c\ub97c \uc77d\ub294 \uc0ac\ub78c\uc740 \ub204\uad6c\uc778\uac00?\'**\ub97c \uacc4\uc18d \uc0c1\uae30\uc2dc\ucf1c \uc8fc\ub294 \ucc45\uc774\ub2e4. \uc774 \ucc45\uc740 \uc9c0\uc2dd\uc758 \uc800\uc8fc\ub97c \uac15\uc870\ud55c\ub2e4. \ub9c8\ube14\uc758 \uc601\ud654\ub97c \ubd24\ub2e4\uba74 \uc54c \uac83\uc774\ub2e4. \uac04\ub2e8\ud558\uac8c \uc124\uba85\ud558\uba74 \ub0b4\uac00 \uc544\ub294 \uac83\uc744 \uc0c1\ub300\ubc29\ub3c4 \uc54c \uac83\uc774\ub77c\uace0 \uc0dd\uac01\ud558\ub294 \uac83\uc774\ub2e4. \ubb38\uc11c\ub97c \uc77d\ub294 \uc0ac\ub78c\uc774 \ub0b4\uac00 \uc791\uc131\ud55c \ucf54\ub4dc\uc640 \uc124\uba85\uc5d0 \ub300\ud574 \uc774\ud574\ud558\uace0 \uc788\uc744 \uac70\ub77c\uace0 \uc0dd\uac01\ud558\uc9c0 \ub9d0\uace0, \ub3c5\uc790\uc758 \ub208\ub192\uc774\uc5d0 \ub9de\uac8c \uae30\uc220 \uc218\uc900\uc744 \ud480\uc5b4\uc11c \uc124\uba85\ud558\ub77c\uace0 \ub9d0\ud55c\ub2e4. \uacb0\uad6d \ub3c5\uc790\uac00 \uc54c\uace0 \uc2f6\uc5b4 \ud558\ub294 \uac83\uc744 \uba85\ud655\ud558\uace0 \uac04\uacb0\ud558\uac8c \uc804\ub2ec\ud558\ub294 \uac83\uc774 \uc911\uc694\ud558\ub2e4\ub294 \uac83\uc774\ub2e4.\\n\\n### \uac04\ub2e8\ud55c \uad6c\uc131\uacfc \ubc18\uc131\\n\\n\uc774 \ucc45\uc740 \'Corg.ly\'\ub77c\ub294 \uac00\uc0c1\uc758 \ud504\ub85c\uc81d\ud2b8\ub97c \uc124\uc815\ud558\uace0 \ucd9c\uc2dc \uc804\ubd80\ud130 \ucd9c\uc2dc \ud6c4\uae4c\uc9c0\uc758 \ub0b4\uc6a9\uc744 \ub2e4\ub8ec\ub2e4. \uadf8 \uacfc\uc815\uc5d0\uc11c \uc5b4\ub5bb\uac8c \uac1c\ubc1c \ubb38\uc11c\ub97c \uc791\uc131\ud558\uace0 \uac1c\uc120\ud558\ub294\uc9c0 \uc54c\ub824\uc900\ub2e4.\\n\\n\uc774 \ucc45\uc744 \uc77d\uc73c\uba74\uc11c \uc9c0\uae08\uae4c\uc9c0 \ub0b4\uac00 \uc37c\ub358 \ubb38\uc11c\ub4e4\uc5d0 \ub300\ud574\uc11c \ubc18\uc131\uc744 \ud558\uac8c \ub410\ub2e4. \ub300\uac1c \'\ub0b4\uac00 \uc4f4 \ubb38\uc11c\ub4e4\uc740 \uadf8\ub798\ub3c4 \uac1c\ubc1c\uc790\uac00 \uc77d\uc744 \ud14c\ub2c8\uae4c \ub300\ucda9\uc774\ub77c\ub3c4 \uc774 \uae30\uc220\uc5d0 \ub4e4\uc5b4\ubd24\uace0 \uad00\uc2ec\uc774 \uc788\uc744 \uac83\uc774\ub2e4.\'\ub77c\uace0 \uc0dd\uac01\ud558\uace0 \uc4f4 \uae00\ub4e4\uc774 \ub9ce\ub2e4. \ucc45 \ub9ac\ubdf0\ub3c4 \ub9c8\ucc2c\uac00\uc9c0\ub77c\ub294 \uac83\uc744 \uc54c\uac8c \ub410\uace0, \uc55e\uc73c\ub85c \uae00\uc744 \uc5b4\ub5bb\uac8c \uc791\uc131\ud574\uc57c \ud560\uc9c0 \uace0\ubbfc\ud558\uac8c \ub410\ub2e4. \\n\\n### \ubd80\ub85d\\n\\n\ubd80\ub85d\uc740 \uad6d\ub0b4 \ud14c\ud06c\ub2c8\uceec \ub77c\uc774\ud305\uc5d0 \uad00\ud55c \ub0b4\uc6a9, \uc804\ubb38\uac00\ub97c \uace0\uc6a9\ud574\uc57c \ud558\ub294 \uacbd\uc6b0, \ucc38\uace0 \uc790\ub8cc. \uc989, \ucd1d 3\uac00\uc9c0\uac00 \uc81c\uacf5\ub41c\ub2e4. \ub098\ub294 \ud2b9\ud788 \uad6d\ub0b4 \ud14c\ud06c\ub2c8\uceec \ub77c\uc774\ud305\uc5d0 \uad00\ud55c \ub0b4\uc6a9\uc774 \ub9c8\uc74c\uc5d0 \ub4e4\uc5c8\ub2e4. \ub09c \ud14c\ud06c\ub2c8\uceec \ub77c\uc774\ud130\ub77c\ub294 \uc9c1\uc5c5\uc5d0 \ub300\ud574\uc11c \ub2e8\ud3b8\uc801\uc73c\ub85c \uc54c\uace0 \uc788\uc5c8\uc73c\uba70, \uc815\ud655\ud788 \uc5b4\ub5a4 \uc77c\uc744 \ub2f4\ub2f9\ud558\uace0 \uc788\ub294\uc9c0 \ubab0\ub790\ub2e4. \uadf8\ub7ec\ub098 \ubd80\ub85d\uc778 \uad6d\ub0b4\uc758 \ud14c\ud06c\ub2c8\uceec \ub77c\uc774\ud130 11\ubd84\uc758 \uc778\ud130\ubdf0\ub97c \ud1b5\ud574 \uc774 \uc9c1\uc5c5\uc5d0 \ub300\ud574 \ub354\uc6b1 \uc790\uc138\ud558\uac8c \uc54c\uac8c \ub410\uace0, \uc774 \uc9c1\uc5c5\uc5d0 \ub300\ud574 \ud765\ubbf8\ub3c4 \uc0dd\uae30\uac8c \ub410\ub2e4. \uc194\uc9c1\ud788 \ud14c\ud06c\ub2c8\uceec \ub77c\uc774\ud130\uac00 \ub418\uae30 \uc704\ud55c \ubd84\ub4e4\uc740 \ubd80\ub85d\ub9cc \ud574\ub3c4 \uc774 \ucc45\uc758 \uac00\uce58\ub294 \ucda9\ubd84\ud558\ub2e4\uace0 \uc0dd\uac01\ud55c\ub2e4.\\n\\n## \ub300\uc0c1 \ub3c5\uc790\\n\\n\uc790\uc2e0\uc774 \uac1c\ubc1c\ud55c \uac83\ub4e4\uc744 \uc790\uc8fc \ubb38\uc11c\ud654\ud574\uc57c \ud558\ub294 \uc0ac\ub78c\ub4e4\uc5d0\uac8c \ucd94\ucc9c\ud55c\ub2e4. \uadf8 \uc774\uc678\uc5d0\ub3c4 \ud14c\ud06c\ub2c8\uceec \ub77c\uc774\ud130\uac00 \ub418\uace0 \uc2f6\uc5b4\ud558\ub294 \ubd84\ub4e4\uaed8\ub3c4 \uc774 \ucc45\uc744 \ucd94\ucc9c\ud55c\ub2e4. \uc704\uc5d0\uc11c\ub3c4 \uc5b8\uae09\ud588\ub4ef\uc774 \ubd80\ub85d\uc5d0 \ud604\uc5c5\uc5d0\uc11c \uc77c\ud558\uc2dc\uace0 \uc788\uc73c\uc2e0 \uad6d\ub0b4 \ud14c\ud06c\ub2c8\uceec \ub77c\uc774\ud130 11\ubd84\uc758 \uc778\ud130\ubdf0\uac00 \ub2f4\uaca8 \uc788\uae30\uc5d0 \ud14c\ud06c\ub2c8\uceec \ub77c\uc774\ud130\uac00 \ub418\uace0 \uc2f6\uc5b4 \ud558\ub294 \ubd84\ub4e4\uaed8\ub3c4 \uc774 \ucc45\uc744 \ucd94\ucc9c\ud558\uace0 \uc2f6\ub2e4."},{"id":"/become-a-node-js-engineer","metadata":{"permalink":"/en/blog/become-a-node-js-engineer","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2023-05-21-become-a-node-js-engineer/become-a-node-js-engineer.md","source":"@site/blog/2023-05-21-become-a-node-js-engineer/become-a-node-js-engineer.md","title":"\ud83d\udcd6 Node.js \ubc31\uc5d4\ub4dc \uac1c\ubc1c\uc790 \ub418\uae30","description":"TypeScript + Node.js + Express + NestJS\ub85c \ubc30\uc6b0\ub294 \uc790\ubc14\uc2a4\ud06c\ub9bd\ud2b8 \ubc31\uc5d4\ub4dc \uc785\ubb38\uc790\ub97c \uc704\ud55c \ud480 \ud328\ud0a4\uc9c0","date":"2023-05-21T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 Node.js \ubc31\uc5d4\ub4dc \uac1c\ubc1c\uc790 \ub418\uae30","date":"2023-05-21","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/become-a-node-js-engineer","description":"TypeScript + Node.js + Express + NestJS\ub85c \ubc30\uc6b0\ub294 \uc790\ubc14\uc2a4\ud06c\ub9bd\ud2b8 \ubc31\uc5d4\ub4dc \uc785\ubb38\uc790\ub97c \uc704\ud55c \ud480 \ud328\ud0a4\uc9c0","keywords":["TypeScript","Node.js","Express","NestJS"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 Docs for Developers \uae30\uc220 \ubb38\uc11c \uc791\uc131 \uc644\ubcbd \uac00\uc774\ub4dc","permalink":"/en/blog/docs-for-developers"},"nextItem":{"title":"\ud83d\udcd6 \uac1c\ubc1c\uc790\ub97c \uc704\ud55c \uba38\uc2e0\ub7ec\ub2dd&\ub525\ub7ec\ub2dd","permalink":"/en/blog/AI-ML-4-Coders"}},"content":":::info\\n\uace8\ub4e0\ub798\ube57 \ucd9c\ud310\uc0ac\ub85c\ubd80\ud130 \ucc45\uc744 \uc81c\uacf5\ubc1b\uc544 \uc791\uc131\ud588\uc2b5\ub2c8\ub2e4.\\n:::\\n\\n## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/become-a-node-js-engineer.jpg)](https://product.kyobobook.co.kr/detail/S000201457949)\\n\\n- \uc81c\ubaa9: Node.js \ubc31\uc5d4\ub4dc \uac1c\ubc1c\uc790 \ub418\uae30\\n- \uc800\uc790: \ubc15\uc2b9\uaddc\\n- \ucd9c\ud310\uc0ac: \uace8\ub4e0\ub798\ube57\\n- \ucd9c\uac04: 2023-04-15\\n\\n{/* truncate */}\\n\\n## Intro\\n\\n\ucd5c\uadfc Django, FastAPI\ub85c \ubb34\uc5b8\uac00 \ub9cc\ub4e4\uc5b4 \ubcf4\ub824\uace0 \ub178\ub825 \uc911\uc774\uc5c8\ub294\ub370 \uc218\ub9ce\uc740 \ucc44\uc6a9 \uacf5\uace0\ub4e4\uc744 \uc77d\uc5b4\ubcf4\ub2c8 \ud30c\uc774\uc36c \ubc31\uc5d4\ub4dc \uac1c\ubc1c\ub85c\ub294 \ucde8\uc5c5\uc5d0 \ud55c\uacc4\uac00 \uc788\uc744 \uac70\ub77c \uc0dd\uac01\ud558\uac8c \ub410\ub2e4. \uad73\uc774 \ud55c \uae30\uc220\ub9cc \ud314 \ud544\uc694\ub3c4 \uc5c6\uace0 \ub2e4\ub978 \uae30\uc220\ub4e4\ub3c4 \uacf5\ubd80\ud574 \ubcf4\uace0 \uc2f6\ub2e4\uace0 \uc0dd\uac01\ud558\uc5ec \uc608\uc804\uc5d0 \uacbd\ud5d8\ud574\ubd24\ub358 Node.js\ub97c \uc120\ud0dd\ud558\uac8c \ub410\ub2e4. \\"Node.js \uac1c\ubc1c\uc790\uac00 \ub418\uae30 \uc704\ud574\uc11c\ub294 \uc5b4\ub5a4 \uac83\ub4e4\uc744 \uacf5\ubd80\ud574\uc57c \ud560\uae4c?\\" \ub77c\ub294 \uc0dd\uac01\uc5d0 \ucc44\uc6a9 \uacf5\uace0\ub4e4\uc744 \uc77d\uc5b4\ubd24\ub294\ub370 \uac00\ub054 NestJS\uac00 \uc801\ud600\uc788\ub294 \uac78 \ubcfc \uc218 \uc788\uc5c8\ub2e4. NestJS\ub294 \uc2a4\ud504\ub9c1\uacfc \ube44\uc2b7\ud55c \ub290\ub08c\uc758 \ud504\ub808\uc784\uc6cc\ud06c\ub77c\uace0 \ub9ce\uc774 \ub9d0\ud558\ub358\ub370 \uad81\uae08\ud574\uc11c \uc774 \uae30\uc220\ub3c4 \uacf5\ubd80\ud574 \ubcf4\uace0 \uc2f6\uc5c8\ub2e4. \uadf8\ub7ec\ub358 \uc911 \ucd5c\uadfc \ud56b\ud55c IT \ucd9c\ud310\uc0ac\uc778 \uace8\ub4e0\ub798\ube57\uc5d0\uc11c \uc774 \ucc45\uc744 \ucd9c\uac04\ud558\uac8c \ub418\uc5b4 \ubd81\uc2a4\ud3ec\uc77c\ub7ec\ub97c \ubaa8\uc9d1\ud558\uace0 \uc788\uc5c8\ub2e4. Node.js \ubc31\uc5d4\ub4dc \uac1c\ubc1c\uc790\uac00 \ub418\uae30 \uc704\ud574 \ud544\uc694\ud55c \uae30\uc220\ub4e4\uc744 \ud55c \uad8c\uc5d0 \ub2f4\uace0 \uc788\ub2e4\uace0 \ud574\uc11c \uc9c0\uc6d0\ud588\uace0 \ub2f9\ucca8\ub418\uc5b4 \ucc45\uc744 \ubc1b\uac8c \ub418\uc5c8\ub2e4. \\n\\n## Book Review\\n\\n### \uc62c\uc778\uc6d0 \ucc45\\n\\n\uc774 \ucc45\uc740 Node.js \uac1c\ubc1c\uc790\uac00 \ub418\uae30 \uc704\ud55c \uae30\uc220\ub4e4\uc744 \uacf5\ubd80\ud560 \uc218 \uc788\ub294 \uc62c\uc778\uc6d0 \ucc45\uc774\ub2e4. \uc9c0\uae08\uae4c\uc9c0 \ub098\ub3c4 \ud55c \ubc88\uc5d0 \ub9ce\uc740 \uae30\uc220\ub4e4\uc744 \uacf5\ubd80\ud558\uace0 \uccb4\ud5d8\ud574 \ubcfc \uc218 \uc788\ub294 \uc62c\uc778\uc6d0 \ucc45\ub4e4\uc744 \uc77d\uc5b4\uc654\uc9c0\ub9cc \ub2e8\uc5f0\ucf54 \uc774 \ucc45\uc774 \uc9c0\uae08\uae4c\uc9c0 \ucd5c\uace0\ub77c\uace0 \ub9d0\ud574\uc8fc\uace0 \uc2f6\ub2e4.\\n\\nDB, \ub124\ud2b8\uc6cc\ud06c, Node.js, Express, JavaScript, TypeScript, NestJS \ub4f1 \uacf5\ubd80\ud574\uc57c \ud560 \uac83\ub4e4\uc774 \uc5b4\ub9c8\ubb34\uc2dc\ud558\uac8c \ub9ce\uc740\ub370 \uc774 \ucc45\uc740 \uc774 \ubaa8\ub4e0 \uac83\ub4e4\uc744 \ud55c \uad8c\uc5d0 \ub2f4\uc73c\ub824\uace0 \ub178\ub825\ud588\ub2e4. \uc138\ubd80\uc801\uc778 \uc124\uba85\uc774 \ub9ce\uc558\ub2e4. \ub2e4\uc2dc \ubd10\ub3c4 \\"\uc774\uac78 \uc5b4\ub5bb\uac8c \ub2e4 \ub2f4\uc558\uc9c0?\\"\ub77c\ub294 \uc0dd\uac01\uc774 \ub4e4 \uc815\ub3c4\uc774\ub2e4. \\n\\nDB, \ub124\ud2b8\uc6cc\ud06c \uadf8\ub9ac\uace0 \uc5b8\uc5b4\uc801\uc778 \ubd80\ubd84\uae4c\uc9c0\ub3c4 \uaf64 \uc790\uc138\ud558\uac8c \uc124\uba85\ud574 \uc900\ub2e4. \uc194\uc9c1\ud788 \uc9c0\uae08\uae4c\uc9c0 \ubd10\uc654\ub358 \uc62c\uc778\uc6d0 \ucc45\ub4e4\uc740 \ub300\ucda9 \uc124\uba85\ud558\uc5ec \uad6c\uae00\ub9c1\uc744 \ud558\uba74\uc11c \uc77d\uc5b4\uc57c \ud558\ub294 \ucc45\ub4e4\ub3c4 \uaf64 \uc788\uc5c8\ub294\ub370 \uc774 \ucc45\uc740 \ub2ec\ub790\ub2e4. \uc124\uba85\uc774 \ub108\ubb34 \uc798 \ub418\uc5b4 \uc788\uc5b4\uc11c \ub180\ub77c\uc6b8 \uc815\ub3c4\uc600\ub2e4. \\n\\n\ud504\ub808\uc784\uc6cc\ud06c\uc5d0\ub9cc \uc9d1\uc911\ud558\ub2e4 \ubcf4\uba74 \uc5b8\uc5b4\uc801\uc778 \ubd80\ubd84\uc740 \uc124\uba85\uc774 \uc5c6\uae30 \ub9c8\ub828\uc778\ub370 \uc774 \ucc45\uc740 \ud0c0\uc785\uc2a4\ud06c\ub9bd\ud2b8\ub294 \ubd80\ub85d\uc73c\ub85c \ub530\ub85c \uc81c\uacf5\ud558\uae30\ub3c4 \ud558\uace0 \uc790\ubc14\uc2a4\ud06c\ub9bd\ud2b8\ub3c4 \ud544\uc218\uc801\uc73c\ub85c \uc54c\uc544\uc57c\ud558\ub294 \ubd80\ubd84\uc740 \uc124\uba85\ud574 \uc900\ub2e4. \ud0c0\uc785\uc2a4\ud06c\ub9bd\ud2b8\uc5d0 \uc775\uc219\ud558\uc9c0 \uc54a\uc740 \ub098\ub3c4 \uc774 \ucc45\uc744 \ud3b8\ud558\uac8c \uc77d\uc744 \uc218 \uc788\uc5c8\ub2e4.\\n\\n\ub610\ud55c, \ub9ac\ub205\uc2a4 \uba85\ub839\uc5b4\uc5d0 \uc775\uc219\ud558\uc9c0 \uc54a\uc740 \ub3c5\uc790\ub97c \uc704\ud574\uc11c \ubd80\ub85d\uc73c\ub85c \uba85\ub839\uc5b4\ub3c4 \uae30\uc220\ub418\uc5b4 \uc788\ub2e4. \uc815\ub9d0 \uc62c\uc778\uc6d0 \ucc45 \uc911 \uc774\ub807\uac8c \uc790\uc138\ud558\uac8c \uc124\uba85\ud574 \uc8fc\ub294 \ucc45\uc740 \ucc98\uc74c\uc774\uc5c8\ub2e4.\\n\\n### \ud0a4\uc6cc\ub4dc \uc81c\uc2dc\\n\\n\uc774 \ucc45 \ub610\ud55c \ud6c4\uc5d0 \uc5b4\ub5a4 \uac83\uc744 \ub354 \uacf5\ubd80\ud558\uba74 \uc88b\uc744\uc9c0 \ud0a4\uc6cc\ub4dc\ub97c \uc81c\uc2dc\ud574 \uc900\ub2e4. \uc800\uc790\ubd84\uaed8\uc11c \uc62c\ub9ac\uc2e0 [\uac15\uc758](https://www.inflearn.com/course/%EC%B4%88%EB%B3%B4-%EB%B0%B1%EC%97%94%EB%93%9C-%EA%B0%9C%EB%B0%9C%EC%9E%90-%EB%A1%9C%EB%93%9C%EB%A7%B5)\ub3c4 \ucc38\uace0\ud558\ub294 \uac83\uc744 \ucd94\ucc9c\ud55c\ub2e4. \uc774 \ucc45\uc744 \uc77d\uc73c\uba74\uc11c \ub354 \uacf5\ubd80\ud574\uc57c \ud560 \uac83\ub4e4\uc744 \ub354 \uba85\ud655\ud558\uac8c \uc54c \uc218 \uc788\uc5b4\uc11c \uc88b\uc558\ub2e4.\\n\\n### \ud2b8\ub80c\ub514\ud55c \ucc45\\n\\n\uc774 \ucc45\uc740 \ucd5c\uadfc \ud56b\ud55c \uae30\uc220\uc778 NestJS\ub97c \uc0ac\uc6a9\ud55c\ub2e4\ub294 \uc810 \ub54c\ubb38\uc5d0 \ud2b8\ub80c\ub514\ud55c \ucc45\uc774\ub2e4. \ud604\uc7ac \ud55c\uad6d\uc5b4\ub85c \ucd9c\uac04\ub41c NestJS \ucc45\uc740 \uc774 \ucc45\uc774 \uc544\ub9c8 \ub450 \ubc88\uc9f8\uc77c \uac83\uc774\ub2e4. \uacf5\uc2dd \ubb38\uc11c\ub97c \uc77d\ub294 \uac83\ubcf4\ub2e4 \ucc45\uc73c\ub85c \uacf5\ubd80\ud558\ub294 \uac83\uc774 \ub354 \uc775\uc219\ud55c \ub3c5\uc790\uc5d0\uac8c\ub294 \uc815\ub9d0 \ub3c4\uc6c0\uc774 \ub9ce\uc774 \ub418\ub294 \ucc45\uc774\ub77c \uc0dd\uac01\ud55c\ub2e4. \\n\\n## \ub300\uc0c1 \ub3c5\uc790\\n\\n\uc774 \ucc45\uc740 Node.js \ubc31\uc5d4\ub4dc \uac1c\ubc1c\uc790\uac00 \ub418\uae30 \uc704\ud55c \uae30\uc220\ub4e4\uc744 \uacf5\ubd80\ud558\uace0 \uc2f6\uc740 \uc0ac\ub78c\ub4e4\uc744 \uc704\ud55c \ucc45\uc774\ub2e4. \uc0ac\uc804 \uc9c0\uc2dd\uc73c\ub85c \uc790\ubc14\uc2a4\ud06c\ub9bd\ud2b8\ub97c \uc54c\uace0 \uc788\uc73c\uba74 \uc774 \ucc45\uc744 \uc77d\ub294 \ub370 \ub3c4\uc6c0\uc774 \ub9ce\uc774 \ub420 \uac83\uc774\ub2e4."},{"id":"/AI-ML-4-Coders","metadata":{"permalink":"/en/blog/AI-ML-4-Coders","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2023-04-23-AI-ML-4-Coders/AI-ML-4-Coders.md","source":"@site/blog/2023-04-23-AI-ML-4-Coders/AI-ML-4-Coders.md","title":"\ud83d\udcd6 \uac1c\ubc1c\uc790\ub97c \uc704\ud55c \uba38\uc2e0\ub7ec\ub2dd&\ub525\ub7ec\ub2dd","description":"\uc778\uacf5\uc9c0\ub2a5 \uac1c\ubc1c\uc790\ub85c \ub808\ubca8 \uc5c5\ud558\uae30! | \uc2e0\uacbd\ub9dd \uae30\ucd08\ubd80\ud130 \ucef4\ud4e8\ud130 \ube44\uc804, \uc790\uc5f0\uc5b4 \ucc98\ub9ac, \uc2dc\uacc4\uc5f4 \uc608\uce21\uae4c\uc9c0","date":"2023-04-23T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \uac1c\ubc1c\uc790\ub97c \uc704\ud55c \uba38\uc2e0\ub7ec\ub2dd&\ub525\ub7ec\ub2dd","date":"2023-04-23","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/AI-ML-4-Coders","description":"\uc778\uacf5\uc9c0\ub2a5 \uac1c\ubc1c\uc790\ub85c \ub808\ubca8 \uc5c5\ud558\uae30! | \uc2e0\uacbd\ub9dd \uae30\ucd08\ubd80\ud130 \ucef4\ud4e8\ud130 \ube44\uc804, \uc790\uc5f0\uc5b4 \ucc98\ub9ac, \uc2dc\uacc4\uc5f4 \uc608\uce21\uae4c\uc9c0","keywords":["\uba38\uc2e0\ub7ec\ub2dd","\ub525\ub7ec\ub2dd","\ucef4\ud4e8\ud130 \ube44\uc804","\uc790\uc5f0\uc5b4 \ucc98\ub9ac","\uc2dc\uacc4\uc5f4 \uc608\uce21"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 Node.js \ubc31\uc5d4\ub4dc \uac1c\ubc1c\uc790 \ub418\uae30","permalink":"/en/blog/become-a-node-js-engineer"},"nextItem":{"title":"\ud83d\udcd6 \ud30c\uc774\uc36c \uae30\ubc18 \uae08\uc735 \uc778\uacf5\uc9c0\ub2a5","permalink":"/en/blog/Artificial-Intelligence-in-Finance"}},"content":":::info\\n\ud55c\ube5b\ubbf8\ub514\uc5b4 \\\\<\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4\\\\> \ud65c\ub3d9\uc744 \uc704\ud574\uc11c \ucc45\uc744 \uc81c\uacf5\ubc1b\uc544 \uc791\uc131\ub41c \uc11c\ud3c9\uc785\ub2c8\ub2e4.\\n:::\\n\\n![\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4 2023](../assets/I-am-reviewer-2023.jpg)\\n\\n## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/AI-ML-4-Coders.jpg)](https://product.kyobobook.co.kr/detail/S000061585519)\\n\\n- \uc81c\ubaa9: \uac1c\ubc1c\uc790\ub97c \uc704\ud55c \uba38\uc2e0\ub7ec\ub2dd&\ub525\ub7ec\ub2dd\\n- \uc800\uc790: \ub85c\ub7f0\uc2a4 \ubaa8\ub85c\ub2c8\\n- \uc5ed\uc790: \ubc15\ud574\uc120\\n- \ucd9c\ud310\uc0ac: \ud55c\ube5b\ubbf8\ub514\uc5b4\\n- \ucd9c\uac04: 2022-08-24\\n\\n{/* truncate */}\\n\\n## Intro\\n\\n\ub2e4\uc2dc \uba38\uc2e0\ub7ec\ub2dd \ucc45 \ub9ac\ubdf0\ub97c \uc4f0\uac8c \ub410\ub2e4. AI\uac00 \uc778\uae30\uac00 \ub9ce\uc740 \ub9cc\ud07c \uba38\uc2e0\ub7ec\ub2dd \ucc45\ub3c4 \ub9ce\uc774 \ub098\uc624\uace0 \uc788\ub2e4. \uc2e4\ubb34\uc5d0\uc11c \uc790\uc8fc \uc4f0\uc774\ub294 \ubc31\uc5d4\ub4dc, \ud504\ub860\ud2b8\uc5d4\ub4dc \uae30\uc220\uacfc \uad00\ub828\ud574\uc11c\ub3c4 \ucc45\uc774 \ub9ce\uc774 \ub098\uc654\uc73c\uba74 \uc88b\uaca0\ub294\ub370 \uc694\uc998 \ud2b8\ub80c\ub4dc\uac00 \uc774\ub7ec\ud558\ub2c8 \uc5b4\uca54 \uc218 \uc5c6\ub294 \uac83 \uac19\ub2e4. \\n\\n\uc5b4\uca0c\ub4e0 \uc774\ub7f0 \ucc45\ub4e4\uc774 \ub9ce\uc774 \ub098\uc624\ub2c8 \uba38\uc2e0\ub7ec\ub2dd\uc740 \ub354 \uc774\uc0c1 \uc804\uacf5\uc790\ub9cc \ubc30\uc6b8 \uc218 \uc788\ub294 \uae30\uc220\uc774 \uc544\ub2c8\uac8c \ub418\uace0 \uc788\ub2e4. \ub204\uad6c\ub098 \ubc30\uc6b0\uace0 \uc2f6\uc73c\uba74 \ubc30\uc6b8 \uc218 \uc788\ub2e4. \uadf8\ub7ec\ub2c8 \uba38\uc2e0\ub7ec\ub2dd \ub610\ub294 \ub525\ub7ec\ub2dd\uc744 \uacf5\ubd80\ud574 \ubcf4\uace0 \uc2f6\ub2e4\uba74 \uc0ac\ub78c\ub4e4\uc774 \uc4f4 \ub9ac\ubdf0\ub97c \uc798 \ud310\ubcc4\ud574\uc11c \ucc45\uc744 \uace8\ub77c\ubcf4\uc790. \\n\\n## Book Review\\n\\n### \uac1c\ubc1c\uc790\uc5d0\uac8c \ucd94\ucc9c\ud558\uace0 \uc2f6\uc740 \ucc45\\n\\n\uac1c\uc778\uc801\uc73c\ub85c \ub098\ub294 \ubc11\ubc14\ub2e5\ubd80\ud130 \uacf5\ubd80\ud574\uc11c \uc62c\ub77c\uc624\ub294 Bottom-Up \ubc29\uc2dd\uc744 \uc88b\uc544\ud55c\ub2e4. \uadf8\ub798\uc11c \ub0b4 \uccab \ub525\ub7ec\ub2dd \ucc45\uc740 [\ubc11\ubc14\ub2e5\ubd80\ud130 \uc2dc\uc791\ud558\ub294 \ub525\ub7ec\ub2dd](https://teddygood.github.io/blog/deep-learning-from-scratch)\uc774\uc5c8\ub2e4. \uadf8\ub7ec\ub098 \uc9c0\uc778\ub4e4\uc5d0\uac8c \ucc45\uc744 \ucd94\ucc9c\ud560 \ub54c **Bottom-Up \ubc29\uc2dd\uc758 \uacf5\ubd80\ub97c \uc88b\uc544\ud558\uc9c0 \uc54a\uc73c\uba74 \uacfc\uc5f0 \uc5b4\ub5a4 \ucc45\uc744 \ucd94\ucc9c\ud560 \uc218 \uc788\uc744\uae4c?** \ub77c\ub294 \uc0dd\uac01\uc774 \ub4e4\uc5c8\ub2e4. \\n\\n\ub204\uad70\uac00\ub294 \uac1c\ub150\ub4e4\uc744 \uac04\ub2e8\ud558\uac8c \uacf5\ubd80\ud55c \ud6c4, \ube60\ub974\uac8c \uc801\uc6a9\ud558\uace0 \uc2f6\uc744 \uc218\ub3c4 \uc788\uc9c0 \uc54a\uc740\uac00. \uc774 \ucc45\uc740 \uadf8\ub7f0 \uacbd\uc6b0\uc5d0 \uc801\uc808\ud558\uac8c \uc0ac\uc6a9\ud560 \uc218 \uc788\ub294 \ucc45\uc774\ub77c \uc0dd\uac01\ud55c\ub2e4. \ubaa8\ub974\ub294 \uac1c\ub150\ub4e4\uacfc \uc6a9\uc5b4\ub294 \uac1c\uc778\uc801\uc73c\ub85c \uad6c\uae00\ub9c1\uc744 \ud1b5\ud574 \ucc3e\uc544\ubcf4\uba74 \ub41c\ub2e4. \ub610\ud55c, \uc800\uc790\ubfd0\ub9cc \uc544\ub2c8\ub77c \uc5ed\uc790\ubd84\uaed8\uc11c \uc5ed\uc8fc\ub97c \uc0ac\uc6a9\ud558\uc5ec \ubd80\uc871\ud55c \uac1c\ub150\ub4e4\uc744 \ubcf4\ucda9\ud574 \uc8fc\uc168\uae30\uc5d0 \uc77d\uae30 \uc26c\uc6b8 \uac83\uc774\ub2e4. \uc774\ubc88\uc5d0 \uc774 \ucc45\uc744 \uc77d\uc73c\uba74\uc11c \ub2e4\uc74c\ubd80\ud130 \uac1c\ubc1c\uc790\ub4e4\uc5d0\uac8c \uba38\uc2e0\ub7ec\ub2dd, \ub525\ub7ec\ub2dd \ucc45\uc744 \ucd94\ucc9c\ud560 \uc77c\uc774 \uc788\ub2e4\uba74 \uc774 \ucc45\uc744 \ucd94\ucc9c\ud560 \uc608\uc815\uc774\ub2e4. \\n\\n### \uac04\ub2e8\ud55c \uc21c\uc11c\\n\\n\uc774 \ucc45\uc740 PART 1\uacfc PART 2\ub85c \ub098\ub204\uc5b4\uc9c4\ub2e4. \ucc98\uc74c\uc5d0 PART 1\uc5d0\uc11c\ub294 \uc804\ubc18\uc801\uc73c\ub85c \ubaa8\ub378\ub4e4\uc5d0 \ub300\ud574 \uc124\uba85\ud55c\ub2e4. \ud150\uc11c\ud50c\ub85c\ubd80\ud130 \uac04\ub2e8\ud558\uac8c \uc18c\uac1c\ud558\uace0 \ubc14\ub85c \ucef4\ud4e8\ud130 \ube44\uc804\uacfc CNN\uc73c\ub85c \ub118\uc5b4\uac04\ub2e4. \uadf8\ub9ac\uace0 \ud150\uc11c\ud50c\ub85c \ub370\uc774\ud130\uc14b\uc744 \uc0ac\uc6a9\ud558\ub294 \ubc29\ubc95\uc744 \uc54c\ub824\uc8fc\ub294\ub370 \uc5ec\uae30\uc11c **4.6 \ud150\uc11c\ud50c\ub85c\uc5d0\uc11c \ub370\uc774\ud130 \uad00\ub9ac\ub97c \uc704\ud55c ETL \ud504\ub85c\uc138\uc2a4**\uac00 \uc778\uc0c1\uc801\uc774\uc5c8\ub2e4. \uac04\ub2e8\ud558\uac8c \uc124\uba85\ud558\uc9c0\ub9cc \uc774 \ubd80\ubd84\uc774 \uc2e4\uc81c\ub85c \uc911\uc694\ud55c \ubd80\ubd84\uc774\ub77c \uc0dd\uac01\ub41c\ub2e4. \uc774\ud6c4 \uc790\uc5f0\uc5b4\ucc98\ub9ac, RNN \uadf8\ub9ac\uace0 \uc2dc\uacc4\uc5f4 \ub370\uc774\ud130\uae4c\uc9c0 \ub2e4\ub8ec\ub2e4. \ub204\uad70\uac00\uc5d0\uac8c\ub294 \uc774 \ucc45\uc774 \ubd88\uce5c\uc808\ud558\ub2e4\uace0 \uc0dd\uac01\ud560 \uc218 \uc788\ub2e4. \ud558\uc9c0\ub9cc \ucc45\uc744 \uc77d\ub294 \ub300\uc0c1 \uc790\uccb4\uac00 \uac1c\ubc1c\uc790\ub85c \uc9c0\uc815\ud588\uae30\uc5d0 \ubd88\uce5c\uc808\ud558\ub354\ub77c\ub3c4 \ubaa8\ub974\ub294 \ubd80\ubd84\uc740 \uac80\uc0c9\ud558\uba74\uc11c \uc77d\uc5b4\uc57c \ud558\uc9c0 \uc54a\uc744\uae4c \uc0dd\uac01\uc774 \ub41c\ub2e4. \uac80\uc0c9\ud558\uba74\uc11c \ubaa8\ub974\ub294 \uac1c\ub150\ub4e4\uc744 \uacf5\ubd80\ud574 \ubcfc \uc218 \uc788\ub294 \uac83\uc774\uace0 \uc2dc\uac04\uc774 \uc870\uae08 \uac78\ub9ac\uaca0\uc9c0\ub9cc \uadf8\ub798\ub3c4 \uba38\uc2e0\ub7ec\ub2dd\uc744 \ube60\ub974\uac8c \ud65c\uc6a9\ud560 \uc218 \uc788\uac8c \ub3c4\uc640\uc900\ub2e4\ub294 \uc810\uc5d0\uc11c \ub098\ub294 \uc774 \ucc45\uc744 \ucd94\ucc9c\ud558\uace0 \uc2f6\ub2e4.\\n\\nPART 2\uc5d0\uc11c\ub294 \ubaa8\ub378\uc744 \uc5b4\ub5bb\uac8c \uc0ac\uc6a9\ud560 \uc218 \uc788\ub294\uc9c0\uc5d0 \ub300\ud574 \uc124\uba85\ud55c\ub2e4. \\n \ud150\uc11c\ud50c\ub85c \ub77c\uc774\ud2b8\ub97c \ud65c\uc6a9\ud558\uc5ec \uc548\ub4dc\ub85c\uc774\ub4dc, iOS\uc5d0\uc11c \uc0ac\uc6a9\ud560 \uc218 \uc788\ub3c4\ub85d \uc54c\ub824\uc8fc\uace0, TensorFlow.js\ub97c \uc0ac\uc6a9\ud558\uc5ec \uc6f9\uc5d0\uc11c \uc0ac\uc6a9\ud560 \uc218 \uc788\ub3c4\ub85d \uc54c\ub824\uc900\ub2e4. TensorFlow.js \uae30\uc220\uc5d0 \ub300\ud574\uc11c\ub294 \uc608\uc804\uc5d0 \ub4e4\uc5b4\ubcf8 \uc801\uc774 \uc788\uc5c8\uc9c0\ub9cc, \uc6f9\uc5d0\uc11c CNN\uae4c\uc9c0 \uc0ac\uc6a9\ud574 \ubcfc \uc218 \uc788\ub2e4\ub294 \uc810\uc740 \uc774 \ucc45\uc744 \uc77d\uc73c\uba70 \ucc98\uc74c \uc54c\uac8c \ub410\ub2e4. \uae30\uc220\uc758 \ubc1c\uc804\uc774 \uc815\ub9d0 \ube60\ub974\ub2e4\ub294 \uac83\uc744 \ub290\uaf08\ub2e4.\\n\\n\ub610\ud55c, TFX\ub97c \uac04\ub2e8\ud558\uac8c \uc18c\uac1c\ud558\uace0 TensorFlow Serving(TFS)\uc5d0 \ub300\ud574\uc11c \uc54c\ub824\uc900\ub2e4. TFS \uc774\uc678\uc758 TFX\ub294 [\uc0b4\uc544 \uc6c0\uc9c1\uc774\ub294 \uba38\uc2e0\ub7ec\ub2dd \ud30c\uc774\ud504\ub77c\uc778 \uc124\uacc4](https://teddygood.github.io/blog/building-machine-learning-pipelines)\ub97c \ucc38\uace0\ud558\ub294 \uac83\uc744 \ucd94\ucc9c\ud55c\ub2e4. \uac1c\uc778\uc801\uc73c\ub85c TFX\ub97c \ub354 \ub2e4\ub904\uc92c\uc73c\uba74 \uc88b\uaca0\ub2e4\uace0 \uc0dd\uac01\ud558\ub098 \uadf8\ub7ac\ub2e4\uba74 \ucc45\uc774 \uc5c4\uccad\ub098\uac8c \uad75\uc5b4\uc84c\uc744 \uac83\uc774\ub77c \uc608\uc0c1\ud55c\ub2e4. \ub9c8\uc9c0\ub9c9\uc73c\ub85c \uc778\uacf5\uc9c0\ub2a5 \uc724\ub9ac, \uac1c\uc778 \uc815\ubcf4 \ubcf4\ud638 \ub4f1\uc5d0 \ub300\ud574 \uac04\ub2e8\ud558\uac8c \uc124\uba85\ud558\uace0 \ub9c8\ubb34\ub9ac\ub41c\ub2e4. \uc774\ub7f0 \ubd80\ubd84\uc5d0\uc11c \ub9ce\uc740 \uac1c\ubc1c\uc790\ub4e4\uc774 \uc9c0\uc2dd\uc774 \ubd80\uc871\ud588\uc744 \ud150\ub370 \ub05d\uc73c\ub85c \uc0c1\uae30\uc2dc\ucf1c \uc8fc\uba74\uc11c \ub2e4\ub904\uc8fc\ub294 \uac83\uc774 \uc88b\uc558\ub2e4.\\n\\n## \ub300\uc0c1 \ub3c5\uc790\\n\\n\uc774 \ucc45\uc740 \uac1c\ubc1c\uc790\ub97c \ub300\uc0c1\uc73c\ub85c \uc4f0\uc600\ub2e4. \uadf8\ub798\uc11c \uba38\uc2e0\ub7ec\ub2dd, \ub525\ub7ec\ub2dd\uc5d0 \ub300\ud55c \uae30\ubcf8\uc801\uc778 \uc9c0\uc2dd\uc774 \uc5c6\ub2e4\uba74 \uc774 \ucc45\uc744 \uc77d\ub294 \uac83\uc774 \uc870\uae08 \ud798\ub4e4 \uc218\ub3c4 \uc788\ub2e4. \ud558\uc9c0\ub9cc \uac1c\ubc1c\uc790\ub77c\uba74 \ub204\uad6c\ub098 \uc77d\uc744 \uc218 \uc788\uac8c \ucd5c\ub300\ud55c \uac04\ub2e8\ud558\uace0 \uc27d\uac8c \uc124\uba85\ud558\uace0 \uc788\uc73c\ub2c8 \uba38\uc2e0\ub7ec\ub2dd, \ub525\ub7ec\ub2dd\uc5d0 \uad00\uc2ec\uc774 \uc788\ub2e4\uba74 \ub3c4\uc804\ud574\ubcf4\uae38 \ubc14\ub780\ub2e4. \uc218\uc2dd\uc774\ub098 \uc5b4\ub824\uc6b4 ML \uac1c\ub150\uc740 \ub300\ubd80\ubd84 \uc81c\uc678\ud558\uace0 \ucf54\ub4dc\uc640 \uacb0\uacfc \uc704\uc8fc\ub85c \uc4f0\uc5ec\uc84c\uae30 \ub54c\ubb38\uc5d0 \ub370\uc774\ud130 \uacfc\ud559\uc790\uac00 \uc544\ub2cc \uac1c\ubc1c\uc790\uc5d0\uac8c \ub354\uc6b1 \ud2b9\ud654\ub41c \ucc45\uc774\ub77c \uc0dd\uac01\ud55c\ub2e4. \ubb3c\ub860 \uba38\uc2e0\ub7ec\ub2dd\uc5d0 \ub300\ud574 \uc0ac\uc804 \uc9c0\uc2dd\uc774 \uc788\uc73c\uba74 \uc774 \ucc45\uc744 \uc77d\uae30\uc5d0\ub294 \ub354\uc6b1 \uc26c\uc6b8 \uac83\uc774\ub2e4."},{"id":"/Artificial-Intelligence-in-Finance","metadata":{"permalink":"/en/blog/Artificial-Intelligence-in-Finance","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2023-03-25-Artificial-Intelligence-in-Finance/Artificial-Intelligence-in-Finance.md","source":"@site/blog/2023-03-25-Artificial-Intelligence-in-Finance/Artificial-Intelligence-in-Finance.md","title":"\ud83d\udcd6 \ud30c\uc774\uc36c \uae30\ubc18 \uae08\uc735 \uc778\uacf5\uc9c0\ub2a5","description":"\ud30c\uc774\uc36c\uacfc \ucf00\ub77c\uc2a4\ub97c \ud65c\uc6a9\ud55c \uae08\uc735 \uc2dc\uacc4\uc5f4 \ub370\uc774\ud130 \uae30\ubc18 \uc54c\uace0\ub9ac\uc998 \ud2b8\ub808\uc774\ub529\uc804\ub7b5","date":"2023-03-25T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \ud30c\uc774\uc36c \uae30\ubc18 \uae08\uc735 \uc778\uacf5\uc9c0\ub2a5","date":"2023-03-25","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/Artificial-Intelligence-in-Finance","description":"\ud30c\uc774\uc36c\uacfc \ucf00\ub77c\uc2a4\ub97c \ud65c\uc6a9\ud55c \uae08\uc735 \uc2dc\uacc4\uc5f4 \ub370\uc774\ud130 \uae30\ubc18 \uc54c\uace0\ub9ac\uc998 \ud2b8\ub808\uc774\ub529\uc804\ub7b5","keywords":["Keras","\uae08\uc735","\ud2b8\ub808\uc774\ub529"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \uac1c\ubc1c\uc790\ub97c \uc704\ud55c \uba38\uc2e0\ub7ec\ub2dd&\ub525\ub7ec\ub2dd","permalink":"/en/blog/AI-ML-4-Coders"},"nextItem":{"title":"\ud83d\udcd6 \uc790\ubc14\uc2a4\ud06c\ub9bd\ud2b8\ub85c \ubc30\uc6b0\ub294 SICP","permalink":"/en/blog/SICP-JavaScript"}},"content":":::info\\n\ud55c\ube5b\ubbf8\ub514\uc5b4 \\\\<\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4\\\\> \ud65c\ub3d9\uc744 \uc704\ud574\uc11c \ucc45\uc744 \uc81c\uacf5\ubc1b\uc544 \uc791\uc131\ub41c \uc11c\ud3c9\uc785\ub2c8\ub2e4.\\n:::\\n\\n![\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4 2023](../assets/I-am-reviewer-2023.jpg)\\n\\n## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/Artificial-Intelligence-in-Finance.jpg)](https://product.kyobobook.co.kr/detail/S000061897998)\\n\\n- \uc81c\ubaa9: \ud30c\uc774\uc36c \uae30\ubc18 \uae08\uc735 \uc778\uacf5\uc9c0\ub2a5\\n- \uc800\uc790: \uc774\ube0c \ud790\ud53c\uc2dc\\n- \uc5ed\uc790: \uae40\ub3c4\ud615\\n- \ucd9c\ud310\uc0ac: \ud55c\ube5b\ubbf8\ub514\uc5b4\\n- \ucd9c\uac04: 2022-09-30\\n\\n{/* truncate */}\\n\\n## Intro\\n\\n\ucd5c\uadfc \uae08\uc735 \ub3c4\uba54\uc778\uc5d0 \uad00\uc2ec\uc774 \uc0dd\uaca8 \uc5b4\ub5bb\uac8c \uacf5\ubd80\ud560 \ubc29\ubc95\uc774 \uc5c6\ub098 \ucc3e\uc544\ubcf4\ub2e4\uac00 \uc774 \ucc45\uc744 \uc77d\uac8c \ub410\ub2e4. \uc0ac\uc2e4 \uae08\uc735 \ucabd\uc744 \uacf5\ubd80\ud558\uae30 \uc704\ud574\uc11c \uc774\ub7f0 \ucc45\ubcf4\ub2e4\ub294 \uc804\ubb38 \uc9c0\uc2dd\uacfc \uad00\ub828\ub41c \ucc45\uc744 \uc77d\ub294 \uac83\uc774 \ub354 \ub098\uc744 \uc218\ub3c4 \uc788\ub2e4. \uadf8\ub7ec\ub098 \uc774 \ucc45\uc744 \uc120\ud0dd\ud558\uac8c \ub41c \uac74 **\uc5b4\ub5bb\uac8c \uc778\uacf5\uc9c0\ub2a5\uc5d0 \uae08\uc735 \uc9c0\uc2dd\uc744 \uc811\ubaa9\ud560 \uc218 \uc788\uc744\uae4c?**\ub77c\ub294 \uc774\uc720 \ub54c\ubb38\uc774\ub2e4. \ub0b4\uac00 \ub9cc\ub4e0 \uc778\uacf5\uc9c0\ub2a5\uc774 \ud22c\uc790\uae4c\uc9c0 \ud574\uc11c \ub3c8\uc744 \ubc8c\uc5b4\ub2e4 \uc904 \uc218\ub9cc \uc788\ub2e4\uba74 \uc5bc\ub9c8\ub098 \uc88b\uc744\uae4c. \'\uc774\uc81c \uba87 \ub144\ub9cc \uc880 \ub354 \uc9c0\ub098\uba74 \uac00\ub2a5\ud558\uc9c0 \uc54a\uc744\uae4c?\'\ub77c\ub294 \ud76c\ub9dd\uc744 \uac16\uace0 \uc774 \ucc45\uc744 \ud1b5\ud574 \uae08\uc735 \uae30\ucd08 \uc9c0\uc2dd\uc744 \uacf5\ubd80\ud558\uace0 \uc2f6\uc5b4 \uc120\ud0dd\ud588\ub2e4. \\n\\n## Book Review\\n\\n### \uc0c1\ub2f9\ud788 \uc5b4\ub824\uc6b4 \ub09c\uc774\ub3c4\\n\\n\ucd08\ubc18\uc5d0\ub294 \uc778\uacf5\uc9c0\ub2a5\uacfc \ucd08\uc9c0\ub2a5\uc5d0 \ub300\ud574 \uc124\uba85\ud558\uba70 Chapter 3\ubd80\ud130 \ubcf8\uaca9\uc801\uc73c\ub85c \uae08\uc735 \uad00\ub828 \uc9c0\uc2dd\uc774 \ub098\uc624\uae30 \uc2dc\uc791\ud55c\ub2e4. \ud655\ub960 \ubc0f \ud1b5\uacc4, \uae08\uc735, \uacbd\uc81c \ub4f1\uc758 \uc9c0\uc2dd\uc774 \ub098\uc624\ub2c8 \uc0ac\uc804 \uc9c0\uc2dd\uc774 \uc788\uc73c\uba74 \ub354\uc6b1 \uc774 \ucc45\uc744 \uc77d\uae30\uc5d0 \uc218\uc6d4\ud560 \uac83\uc774\ub2e4.\\n\\n\ub098\ub294 \uae08\uc735 \uc9c0\uc2dd\uc774 \ubd80\uc871\ud558\uc5ec \uc774 \ucc45\uc744 \uc77d\ub294\ub370 \uc5b4\ub824\uc6c0\uc774 \uc788\uc5c8\ub2e4. \uadf8\ub7ec\ub098 \uac01 chapter\ub9c8\ub2e4 \ucc38\uace0 \ubb38\ud5cc\uc774 \uc788\uc5b4 \ud2b9\uc815 \ubd80\ubd84\uc744 \ub354 \uacf5\ubd80\ud558\uace0 \uc2f6\uc73c\uba74 \ucc38\uace0 \ubb38\ud5cc\uc744 \uc77d\uc5b4\ubd04\uc73c\ub85c\uc11c \ub354 \uacf5\ubd80\ud560 \uc218 \uc788\uc5c8\ub2e4.\\n\\n\uc774 \ucc45\uc744 \uc77d\uae30 \uc804 \uc774 \ucc45\uc758 \uc800\uc790\uac00 \uc4f4 [\ud30c\uc774\uc36c\uc744 \ud65c\uc6a9\ud55c \uae08\uc735 \ubd84\uc11d](https://product.kyobobook.co.kr/detail/S000001810474)\uc744 \uc77d\uc5b4\ubcf4\uba74 \uc774 \ucc45\uc744 \ub354 \ud3b8\ud558\uac8c \uc77d\uc744 \uc218 \uc788\uc9c0 \uc54a\uc744\uae4c \uc608\uc0c1\ud574\ubcf8\ub2e4. \\n\\n### \ucf54\ub4dc \uc81c\uacf5\uc758 \ubd88\ud3b8\ud568\\n\\n\uc77c\ubc18\uc801\uc778 \ucc45\ub4e4\uc740 \uc18c\uc2a4\ucf54\ub4dc\ub97c GitHub\uc5d0 \uc62c\ub824\ub193\uae30 \ub9c8\ub828\uc774\ub2e4. \uadf8\ub7ec\ub098 \uc774 \ucc45\uc740 \uc790\uc2e0\uc758 \uc0ac\uc774\ud2b8\uc5d0 \uc18c\uc2a4\ucf54\ub4dc\ub97c \uacf5\uac1c\ud574\ub480\ub2e4. \uc0ac\uc774\ud2b8\uc5d0\uc11c \uc8fc\ud53c\ud130 \ub7a9\uc73c\ub85c \uc5f0\uacb0\ub418\uc5b4 \ucf54\ub4dc\ub97c \ubc14\ub85c \uc2e4\ud589\uc2dc\ud0a4\ub294 \uac83\uc740 \uc7a5\uc810\uc774\ub098 \uc77c\ubd80 \ucf54\ub4dc\uac00 \uc798 \uc548 \ub3cc\uc544\uac00\ub294 \uacbd\uc6b0\ub3c4 \uc788\uc5c8\ub2e4. \ucf54\ub4dc\ub97c \ub2e4\uc6b4 \ubc1b\uc544 colab \ub610\ub294 \uc790\uc2e0\uc758 \ub85c\uceec \ud658\uacbd\uc5d0\uc11c \uc2e4\ud589\uc2dc\ud0a4\ub294 \uac83\uc744 \ucd94\ucc9c\ud55c\ub2e4.\\n\\n## \ub300\uc0c1 \ub3c5\uc790\\n\\n\uae08\uc735 \ub610\ub294 \uacbd\uc81c\uc5d0 \uad00\uc2ec\uc774 \ub9ce\uc73c\uba70, Keras\ub97c \ud55c\ubc88\ucbe4 \uc0ac\uc6a9\ud574\ubcf8 \ud504\ub85c\uadf8\ub798\uba38\uc5d0\uac8c \ucd94\ucc9c\ud55c\ub2e4. \uc774 \ucc45\uc740 \uc808\ub300 \uc785\ubb38\uc11c\uac00 \uc544\ub2c8\ub2e4. \uc26c\uc6b4\ub4ef\uc774 \uc124\uba85\ub418\uc5b4 \uc788\uc9c0\ub9cc, \uc0ac\uc2e4 \ud558\ub098\ud558\ub098 \uc5b4\ub824\uc6b4 \ub0b4\uc6a9\uc774\ub2c8 \uc0ac\uc804 \uc9c0\uc2dd\uc744 \uacf5\ubd80 \ud6c4\uc5d0 \uc774 \ucc45\uc744 \uc77d\uae30\ub97c \ubc14\ub780\ub2e4."},{"id":"/SICP-JavaScript","metadata":{"permalink":"/en/blog/SICP-JavaScript","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2023-02-26-SICP-JavaScript/SICP-JavaScript.md","source":"@site/blog/2023-02-26-SICP-JavaScript/SICP-JavaScript.md","title":"\ud83d\udcd6 \uc790\ubc14\uc2a4\ud06c\ub9bd\ud2b8\ub85c \ubc30\uc6b0\ub294 SICP","description":"\ucef4\ud4e8\ud130 \ud504\ub85c\uadf8\ub7a8\uc758 \uad6c\uc870\uc640 \ud574\uc11d","date":"2023-02-26T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \uc790\ubc14\uc2a4\ud06c\ub9bd\ud2b8\ub85c \ubc30\uc6b0\ub294 SICP","date":"2023-02-26","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/SICP-JavaScript","description":"\ucef4\ud4e8\ud130 \ud504\ub85c\uadf8\ub7a8\uc758 \uad6c\uc870\uc640 \ud574\uc11d","keywords":["SICP","MIT","JavaScript"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \ud30c\uc774\uc36c \uae30\ubc18 \uae08\uc735 \uc778\uacf5\uc9c0\ub2a5","permalink":"/en/blog/Artificial-Intelligence-in-Finance"},"nextItem":{"title":"\ud83d\udcd6 \uae40\uae30\ud604\uc758 \ub525\ub7ec\ub2dd \ubd80\ud2b8\ucea0\ud504 with \ud30c\uc774\ud1a0\uce58","permalink":"/en/blog/deep-learning-bootcamp-with-pytorch"}},"content":":::info\\n\ud55c\ube5b\ubbf8\ub514\uc5b4 \\\\<\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4\\\\> \ud65c\ub3d9\uc744 \uc704\ud574\uc11c \ucc45\uc744 \uc81c\uacf5\ubc1b\uc544 \uc791\uc131\ub41c \uc11c\ud3c9\uc785\ub2c8\ub2e4.\\n:::\\n\\n![\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4 2023](../assets/I-am-reviewer-2023.jpg)\\n\\n## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/SICP-JavaScript.jpg)](https://product.kyobobook.co.kr/detail/S000200526321)\\n\\n- \uc81c\ubaa9: \uc790\ubc14\uc2a4\ud06c\ub9bd\ud2b8\ub85c \ubc30\uc6b0\ub294 SICP\\n- \uc800\uc790: \ud574\ub7f4\ub4dc \uc5d0\uc774\ube14\uc2a8, \uc81c\ub7f4\ub4dc \uc81c\uc774 \uc11c\uc2a4\uba3c, \ub9c8\ud2f4 \ud5e8\uc988, \ud1a0\ube44\uc544\uc2a4 \ube0c\ub9ad\uc2a4\ud0c0\ub4dc, \uc904\ub9ac \uc11c\uc2a4\uba3c\\n- \uc5ed\uc790: \ub958\uad11\\n- \ucd9c\ud310\uc0ac: \ud55c\ube5b\ubbf8\ub514\uc5b4\\n- \ucd9c\uac04: 2022-12-30\\n\\n{/* truncate */}\\n\\n## Intro\\n\\nSICP(Structure & Interpretation of Computer Programs)\ub294 \ucef4\ud4e8\ud130 \uacfc\ud559 \ubd84\uc57c\uc5d0\uc11c\ub294 \ube60\uc9c8 \uc218 \uc5c6\ub294 \uc720\uba85\ud55c \uba85\uc11c\uc774\ub2e4. \uadf8\ub7ec\ub098 \ucd5c\uadfc\uc5d0\ub294 \ubaa8\ub974\ub294 \uc0ac\ub78c\uc774 \ub354 \ub9ce\uc744 \uac83\uc774\ub2e4. \ub098 \ub610\ud55c \uc774 \ucc45\uc744 \ucc98\uc74c \uc54c\uac8c \ub410\uc744 \ub54c\uac00 2020\ub144\ub3c4\uc600\ub2e4. \uc54c\ub354\ub77c\ub3c4 \uc774 \ucc45\uc5d0 \ub3c4\uc804\ud558\ub294 \uc0ac\ub78c\uc740 \uba87 \uc5c6\uc5c8\ub2e4. \uadf8 \uc774\uc720\ub294 Scheme\uc774\ub77c\ub294 \uc0dd\uc18c\ud55c \uc5b8\uc5b4\ub97c \uc0ac\uc6a9\ud558\uae30 \ub54c\ubb38\uc774\ub2e4. MIT\uc5d0\uc11c\ub3c4 \uc218\uc5c5 \uad50\uc7ac\ub85c \uc4f0\uc600\ub358 \ucc45\uc774\uc5c8\uace0, \uc774 \ubd84\uc57c\uc5d0\uc11c\ub294 \uaf64 \uc720\uba85\ud55c \ucc45\uc774\ub77c \uc77d\uc5b4\ubcf4\uace0 \uc2f6\uc5c8\uc9c0\ub9cc \uc778\uacf5\uc9c0\ub2a5\uc5d0 \ube60\uc838 \uc120\ud615\ub300\uc218\ud559\uc744 \uacf5\ubd80\ud558\uace0 \uc788\uc744 \ub54c\ub77c \uc544\uc27d\uac8c\ub3c4 \uc77d\uc744 \uc2dc\uac04\uc774 \uc5c6\uc5c8\ub2e4.\\n\\n\uc774\ubc88 \ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4 2023\uc5d0 \ucc38\uc5ec\ud558\uac8c \ub418\uc5b4 JavaScript\ub85c \uac01\uc0c9\ub41c \uc774 \ucc45\uc744 \uc77d\uc744 \uc218 \uc788\uac8c \ub410\ub2e4. \uc5c4\uccad\ub09c \ud589\uc6b4\uc774\ub77c \uc5ec\uae34\ub2e4. \uc774 \ucc45\uc774 \ub450\uaebc\uc6cc \uc644\ub3c5\uae4c\uc9c0 \uc5bc\ub9c8\ub098 \uac78\ub9b4\uc9c0\ub294 \ubaa8\ub974\uaca0\uc9c0\ub9cc, \uacc4\uc18d \uafb8\uc900\ud788 \uc77d\uc5b4\ubcfc \uc608\uc815\uc774\ub2e4. \\n\\n## Book Review\\n\\n### JavaScript\\n\\n\uc774 \ucc45\uc740 \uc6d0\ub798 Scheme\uc774\ub77c\ub294 \uc5b8\uc5b4\ub85c \uc4f0\uc600\ub358 \ucc45\uc774\ub2e4. [Scheme](https://ko.wikipedia.org/wiki/%EC%8A%A4%ED%82%B4_(%ED%94%84%EB%A1%9C%EA%B7%B8%EB%9E%98%EB%B0%8D_%EC%96%B8%EC%96%B4))\uc740 [LISP](https://ko.wikipedia.org/wiki/%EB%A6%AC%EC%8A%A4%ED%94%84)\ub77c\ub294 \uc5b8\uc5b4\uc758 \ud55c \ubc29\uc5b8\uc774\ub2e4. \ub458 \ub2e4 \ud568\uc218\ud615 \ud504\ub85c\uadf8\ub798\ubc0d\uc5d0 \uad00\uc2ec\uc774 \uc788\ub2e4\uba74 \ub4e4\uc5b4\ubcfc \ub9cc\ud55c \uc5b8\uc5b4\uc774\uc9c0\ub9cc \uaf64 \uc0dd\uc18c\ud55c \uc5b8\uc5b4\uc774\ub2e4.\\n\\n\uc774 \ucc45\uc744 \ub9ac\ubdf0\ud560 \ub54c \ub2f9\uc5f0\ud788 \ucc98\uc74c\ubd80\ud130 \ud558\uace0 \uc2f6\uc740 \ub9d0\uc740 \uc774 \ucc45\uc774 JavaScript\ub85c \uc4f0\uc600\ub2e4\ub294 \uc810\uc774\ub2e4. \uc811\uadfc\uc131\uc744 \ub9e4\uc6b0 \ub192\uc600\ub2e4\uace0 \ub9d0\ud560 \uc218 \uc788\ub2e4. \uc774\uc81c \ub098\ub97c \ud3ec\ud568\ud558\uc5ec \uc774 \ucc45\uc5d0 \uad00\uc2ec \uc788\ub358 \uc0ac\ub78c\ub4e4\uc774 \uc77d\uc744 \uc218 \uc788\uac8c \ub410\ub2e4. \uc774\uac8c \ub2e4 \uc790\ubc14\uc2a4\ud06c\ub9bd\ud2b8, \ud30c\uc774\uc36c \ub4f1 \uc790\uc8fc \uc4f0\uc774\ub294 \uc5b8\uc5b4\uc5d0\ub3c4 \ud568\uc218\ud615 \ud504\ub85c\uadf8\ub798\ubc0d\uc774 \ucd94\uac00\ub418\uc5b4 \uc774 \ucc45\uc744 \uc77d\uc744 \uc218 \uc788\uac8c \ub410\ub2e4. \uad73\uc774 LISP\uacfc Scheme\uc744 \ubab0\ub77c\ub3c4 \ud568\uc218\ud615 \ud504\ub85c\uadf8\ub798\ubc0d\uc744 \ubc30\uc6cc\ubcfc \uc218 \uc788\ub294 \uae30\ud68c\uac00 \uc0dd\uae34 \uac83\uc774\ub2e4. \\n\\n### \uc5c4\uccad\ub09c \ub450\uaed8\\n\\n\ub450\uaed8\uc5d0 \uc555\ub3c4\ub2f9\ud558\ub294 \ucc45\uc774\ub2e4. \uc804\uacf5\ucc45\uc774 \uc0dd\uac01\ub098\uba70 \uc774 \ucc45\uc744 \ubc1b\uc558\uc744 \ub54c \'\uc5b8\uc81c \ub2e4 \uc77d\uc9c0?\'\ub77c\ub294 \uc0dd\uac01\uc774 \ub4e4\uc5c8\uc744 \uc815\ub3c4\uc774\ub2e4. \ucc38\uace0\ubb38\ud5cc(Bibliography), \ucc3e\uc544\ubcf4\uae30(Index)\ub97c \uc81c\uc678\ud574\ub3c4 824\ucabd\uc774\ub098 \ub41c\ub2e4. \uadf8\ub7ec\ub098 \uc774\ub807\uac8c \ub450\uaebc\uc6b4 \ub370\ub294 \ub2e4 \uc774\uc720\uac00 \uc788\ub2e4.\\n\\n\uccab \ubc88\uc9f8 \uc774\uc720\ub85c \uc5f0\uc2b5\ubb38\uc81c\uac00 \uaf64 \ub9ce\ub2e4. \uc5b4\ub290 \uc815\ub3c4 \uc55e\ubd80\ubd84\uc744 \uc77d\uc5c8\uc744 \ub54c\ub294 \'\uc7ac\ubc0c\ub294 \ubb38\uc81c\uac00 \ub9ce\ub124.\' \uc815\ub3c4\uc600\ub294\ub370 \ub4a4\ub85c \uac08\uc218\ub85d \uc810\uc810 \ub354 \uc5b4\ub824\uc6b4 \ubb38\uc81c\ub4e4\uc774 \ub9ce\uc774 \ub098\uc628\ub2e4. \ud2b9\ud788 sin \ud568\uc218\ub97c \uacc4\uc0b0\ud574\ubcf4\uae30\ub3c4 \ud558\uace0, \uc801\ubd84\uc744 \ud558\ub294 \ub4f1\uc758 \uc218\ud559\uacfc \uad00\ub828\ub41c \ub0b4\uc6a9\uc774 \ub9ce\ub2e4. \ub610\ud55c, \uc54c\uace0\ub9ac\uc998 \ubb38\uc81c\uc5d0 \uc790\uc8fc \uc0ac\uc6a9\ub418\ub294 \uac1c\ub150\ub4e4\ub3c4 \ub9ce\uae30\uc5d0 \uc0ac\uc804\uc5d0 \uc54c\uace0\ub9ac\uc998 \uc218\uc5c5\uc744 \ub4e4\uc5b4\ubd24\uac70\ub098 \uacf5\ubd80\ud574 \ubcf8 \uc0ac\ub78c\uc774\ub77c\uba74 \uc77d\ub294 \ub370 \uc720\ub9ac\ud560 \uac83\uc774\ub2e4.\\n\\n\ub450 \ubc88\uc9f8 \uc774\uc720\ub85c \ucef4\ud4e8\ud130 \uacfc\ud559\uacfc \uad00\ub828\ub41c \ub2e4\uc591\ud55c \uc8fc\uc81c\ub97c \ub2e4\ub8e8\uba74\uc11c \uc124\uba85\ub3c4 \uaf64 \uc790\uc138\ud558\ub2e4. \ud504\ub85c\uadf8\ub798\ubc0d \uc5b8\uc5b4\ubfd0\ub9cc\uc774 \uc544\ub2c8\ub77c \uc790\ub8cc\uad6c\uc870, \ucef4\ud30c\uc77c\ub7ec, \ub3d9\uc2dc\uc131, \ub17c\ub9ac \ud68c\ub85c \ub4f1\uc774 \ucc45\uc5d0\uc11c \ub098\uc628\ub2e4. \uadf8\ub807\uae30\uc5d0 \uc804\ubc18\uc801\uc778 CS \uc9c0\uc2dd\uc774 \uc788\ub2e4\uba74 \uc77d\uae30 \ub354\uc6b1 \uc26c\uc6b8 \uac83\uc774\ub77c \uc0dd\uac01\ud55c\ub2e4. \\n\\n## \ub300\uc0c1 \ub3c5\uc790\\n\\n\ud568\uc218\ud615 \ud504\ub85c\uadf8\ub798\ubc0d\uacfc \uc804\ubc18\uc801\uc778 CS \uc9c0\uc2dd\uc744 \uacf5\ubd80\ud558\uace0 \uc2f6\uc740 \uc0ac\ub78c\uc5d0\uac8c \uc774 \ucc45\uc744 \ucd94\ucc9c\ud558\uace0 \uc2f6\ub2e4. \ub610\ud55c, \uafb8\uc900\ud788 \uacf5\ubd80 \uc2b5\uad00\uc744 \uae38\ub7ec\ubcf4\uace0 \uc2f6\uc740 \ubd84\ub4e4\uaed8\ub3c4 \ucd94\ucc9c\ud55c\ub2e4. \uc774 \ucc45\uc740 \ubb34\ub824 \uc57d 800\ucabd\uc758 \ub450\uaebc\uc6b4 \ucc45\uc774\ub2e4. \uadf8\ub807\uae30\uc5d0 \uc2e4\uc81c\ub85c \uc774 \ucc45\uc744 \uc77d\ub2e4\uac00 \ud3ec\uae30\ud558\uc2e0 \ubd84\ub4e4\ub3c4 \ub9ce\ub2e4\uace0 \uc54c\uace0 \uc788\ub2e4. \uc644\ub3c5\uc744 \ubaa9\ud45c\ub85c \ud55c\ub2e4\uba74 \ub2e4\ub978 \uc120\uc218 \uc9c0\uc2dd\uc740 \ub2e4 \ud544\uc694 \uc5c6\uace0 \uc778\ub0b4\uc2ec\uc744 \uac16\uace0 \ucc9c\ucc9c\ud788 \uc77d\ub294 \uac83\uc744 \ucd94\ucc9c\ud55c\ub2e4."},{"id":"/deep-learning-bootcamp-with-pytorch","metadata":{"permalink":"/en/blog/deep-learning-bootcamp-with-pytorch","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2022-10-02-deep-learning-bootcamp-with-pytorch/deep-learning-bootcamp-with-pytorch.md","source":"@site/blog/2022-10-02-deep-learning-bootcamp-with-pytorch/deep-learning-bootcamp-with-pytorch.md","title":"\ud83d\udcd6 \uae40\uae30\ud604\uc758 \ub525\ub7ec\ub2dd \ubd80\ud2b8\ucea0\ud504 with \ud30c\uc774\ud1a0\uce58","description":"\uc18c\ubb38\ub09c \uba85\uac15\uc758 | \uae30\ucd08\ubd80\ud130 \uc218\uc2dd, \uc2e4\uc2b5\uae4c\uc9c0 \ub2f4\uc740 \uc62c\uc778\uc6d0 \ub525\ub7ec\ub2dd \uc785\ubb38 \uad50\uacfc\uc11c","date":"2022-10-02T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \uae40\uae30\ud604\uc758 \ub525\ub7ec\ub2dd \ubd80\ud2b8\ucea0\ud504 with \ud30c\uc774\ud1a0\uce58","date":"2022-10-02","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/deep-learning-bootcamp-with-pytorch","description":"\uc18c\ubb38\ub09c \uba85\uac15\uc758 | \uae30\ucd08\ubd80\ud130 \uc218\uc2dd, \uc2e4\uc2b5\uae4c\uc9c0 \ub2f4\uc740 \uc62c\uc778\uc6d0 \ub525\ub7ec\ub2dd \uc785\ubb38 \uad50\uacfc\uc11c","keywords":["\uc18c\ubb38\ub09c \uba85\uac15\uc758","\ud30c\uc774\ud1a0\uce58","\ub525\ub7ec\ub2dd"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \uc790\ubc14\uc2a4\ud06c\ub9bd\ud2b8\ub85c \ubc30\uc6b0\ub294 SICP","permalink":"/en/blog/SICP-JavaScript"},"nextItem":{"title":"\ud83d\udcd6 \ub370\uc2f8\ub178\ud2b8\uc758 \uc2e4\uc804\uc5d0\uc11c \ud1b5\ud558\ub294 \uba38\uc2e0\ub7ec\ub2dd","permalink":"/en/blog/ML-in-practice"}},"content":":::info\\n\ud55c\ube5b\ubbf8\ub514\uc5b4 \\\\<\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4\\\\> \ud65c\ub3d9\uc744 \uc704\ud574\uc11c \ucc45\uc744 \uc81c\uacf5\ubc1b\uc544 \uc791\uc131\ub41c \uc11c\ud3c9\uc785\ub2c8\ub2e4.\\n:::\\n\\n![\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4 2022](../assets/I-am-reviewer-2022.jpg)\\n\\n## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/deep-learning-bootcamp-with-pytorch.jpg)](https://www.kyobobook.co.kr/product/detailViewKor.laf?ejkGb=KOR&mallGb=KOR&barcode=9791169210140&orderClick=LEa&Kc=)\\n\\n- \uc81c\ubaa9: \uae40\uae30\ud604\uc758 \ub525\ub7ec\ub2dd \ubd80\ud2b8\ucea0\ud504 with \ud30c\uc774\ud1a0\uce58\\n- \uc800\uc790: \uae40\uae30\ud604\\n- \ucd9c\ud310\uc0ac: \ud55c\ube5b\ubbf8\ub514\uc5b4\\n- \ucd9c\uac04: 2022-08-30\\n\\n{/* truncate */}\\n\\n## Intro\\n\\n\uc694\uc998\uc740 \ub525\ub7ec\ub2dd\uc744 \ub2e4\ub8ec \ucc45\uc774 \uc815\ub9d0 \ub9ce\uc774 \uc99d\uac00\ud588\uc2b5\ub2c8\ub2e4. \uadf8\ub7f0 \ucc45\ub4e4\uc740 \ub2e8\uc21c\ud788 \uc8fc\ud53c\ud130 \ub178\ud2b8\ubd81\uc73c\ub85c \uad6c\uc131\ub418\uace0, \uc2e4\ubb34\uc801\uc778 \ubd80\ubd84\uc5d0\uc11c \uc124\uba85\uc774 \ubd80\uc871\ud55c \uacbd\uc6b0\uac00 \ub9ce\ub2e4\uace0 \ub290\uaf08\uc2b5\ub2c8\ub2e4. \uadf8\ub7ec\ub098 \uc774 \ucc45\uc740 \uc2e4\ubb34\uc5d0 \ubc00\uc811\ud558\uac8c \ub525\ub7ec\ub2dd\uc744 \uacbd\ud5d8\ud574\ubcfc \uc218 \uc788\ub2e4\ub294 \uc810\uc774 \ub2e4\ub978 \ucc45\ub4e4\uacfc \ucc28\uc774\uc810\uc774\ub77c\uace0 \uc0dd\uac01\ud558\uc5ec \uc77d\uc5b4\ubcf4\uac8c \ub410\uc2b5\ub2c8\ub2e4.\\n\\n## Book Review\\n\\n### \uc800\uc790\uc758 \uc0dd\uac01\uacfc \uc2e4\ubb34 \uacbd\ud5d8\uc774 \ub2f4\uae34 \ucc45\\n\\n\ubcf4\ud1b5 \ub525\ub7ec\ub2dd \ub610\ub294 \uba38\uc2e0\ub7ec\ub2dd \ucc45\ub4e4\uc740 \uac1c\ub150\uc801\uc778 \ubd80\ubd84\ub9cc \uc124\uba85\ud558\uae30 \ubc14\uc069\ub2c8\ub2e4. \uadf8\ub7ec\ub098 \uc774 \ucc45\uc740 \uac1c\ub150 \uc124\uba85\ub3c4 \uc5b4\ub290\uc815\ub3c4 \uac00\uc838 \uac00\uba74\uc11c \uc800\uc790\uc758 \uc0dd\uac01\uc774 \ud55c\ubc29\uc6b8 \ucca8\uac00\ub410\uc2b5\ub2c8\ub2e4. \uc120\uc0dd\ub2d8 \ub610\ub294 \uac15\uc0ac\ub2d8\uc774 \uc124\uba85\ud574\uc8fc\uc2dc\ub294 \ub290\ub08c\uc73c\ub85c \uce5c\uc808\ud558\uac8c \uc124\uba85\uc774 \ub418\uc5b4 \uc788\uc2b5\ub2c8\ub2e4. \ub610\ud55c, \uc800\uc790\uc758 \uc2e4\ubb34 \uacbd\ud5d8\uc774 \ub2f4\uaca8 \uc788\uc2b5\ub2c8\ub2e4. \uc774 \ucc45\uc740 \uadf8\ub7f0 \ubd80\uac00\uc801\uc778 \ub0b4\uc6a9\uc744 \uc54c\uae30 \uc704\ud574\uc11c\ub77c\ub3c4 \uc77d\uc5b4\ubcf4\ub294 \uac00\uce58\uac00 \uc788\ub2e4\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4. \uae30\ubcf8 \uac1c\ub150\ub9cc \uacf5\ubd80\ud558\uba74\uc11c \uc2e4\ubb34\ub97c \ubbf8\ub9ac \uacbd\ud5d8\ud574\ubcf4\uace0 \uc2f6\uc740 \ubd84\ub4e4\uc740 \uac04\ub2e8\ud788 \ubcf5\uc2b5\ud558\ub294 \ub290\ub08c\uc73c\ub85c \uc77d\uc5b4\ubcf4\ub294 \uac83\ub3c4 \uc88b\uc2b5\ub2c8\ub2e4.\\n\\n### \ucf54\ub4dc\\n\\n\uc774\ub807\uac8c \ub530\ub85c \ud5e4\ub354\ub97c \ucd94\uac00\ud574\uc11c \uc801\ub294 \uc774\uc720\ub294 \uc81c\uacf5\ub41c \ucf54\ub4dc\uc5d0 \ub300\ud574 \uc544\uc26c\uc6c0\uc774 \uc870\uae08 \uc788\uc5c8\uae30 \ub54c\ubb38\uc785\ub2c8\ub2e4. Chpater 05\ubd80\ud130\ub294 \uc8fc\ud53c\ud130 \ub178\ud2b8\ubd81\uc758 chapter \ubc88\ud638\uac00 \ub2e4\ub974\uac8c \uc801\ud600 \uc788\uc2b5\ub2c8\ub2e4. \ud55c \uce78 \ubc00\ub824 \uc788\uc8e0. \ucc45 \uc77d\ub294 \ubd80\ubd84\uc5d0\uc11c \\n\uc774 \ucc45\uc758 \ucf54\ub4dc\ub294 \uc8fc\ud53c\ud130 \ub178\ud2b8\ubd81\uc73c\ub85c \uc81c\uacf5\ub429\ub2c8\ub2e4. \uc544\uc26c\uc6e0\ub358 \uc810\uc740 \ucf54\ub4dc\uc758 \uac04\ub2e8\ud55c \uc124\uba85\ub4e4\uc774 \uc601\uc5b4\ub85c \uc801\ud600 \uc788\uc5c8\ub358 \uc810\uacfc \ucc45\uc758 \ucf54\ub4dc\uc640\ub294 \uc0b4\uc9dd \ub2e4\ub978 \ubd80\ubd84\ub4e4\ub3c4 \uc788\uae30\uc5d0 \uae30\ubcf8\uc801\uc778 \ud30c\uc774\uc36c \ubb38\ubc95\ub4e4\uc744 \ubaa8\ub978\ub2e4\uba74 \uc5b4\ub824\uc6b8 \uc218 \uc788\uaca0\ub2e4\uace0 \ub290\uaf08\uc2b5\ub2c8\ub2e4. \\n\\n### \uadf8\ub2e4\uc74c \uacf5\ubd80\\n\\n\uac01 Chapter\ub9c8\ub2e4 *\ub9c8\uce58\uba70*\ub77c\ub294 \ubd80\ubd84\uc5d0\uc11c \uc800\uc790\uc758 \uc870\uc5b8\uacfc \uadf8\ub2e4\uc74c\uc5d0\ub294 \uc5b4\ub5a4 \uac78 \uacf5\ubd80\ud558\uba74 \uc88b\uc744\uc9c0, \uc694\uc57d \ub4f1\uc744 \uc124\uba85\ud569\ub2c8\ub2e4. \uac1c\uc778\uc801\uc73c\ub85c \uc774\ub807\uac8c \ub9e4 Chapter\ub9c8\ub2e4 \uae54\ub054\ud558\uac8c \uc815\ub9ac\ud558\ub294 \ubd80\ubd84\ub3c4 \uc88b\uc558\uc2b5\ub2c8\ub2e4.\\n\\n## \ub300\uc0c1 \ub3c5\uc790\\n\\n\uc800\ub294 \uc774 \ucc45\uc73c\ub85c \ub525\ub7ec\ub2dd\uc744 \ucc98\uc74c \uacf5\ubd80\ud558\uc154\ub3c4 \ubb34\ubc29\ud558\ub2e4\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4. \ub610\ud55c, \ub525\ub7ec\ub2dd\uc744 \uc54c\uace0 \uc788\uc9c0\ub9cc \uc8fc\ud53c\ud130 \ub178\ud2b8\ubd81\ub9cc \uacbd\ud5d8\ud574\ubcf4\uc2e0 \ubd84\ub4e4\uaed8\ub3c4 \ucd94\ucc9c\ud569\ub2c8\ub2e4. \uc694\uc998 \ub098\uc624\ub294 \uac70\uc758 \ubaa8\ub4e0 \uba38\uc2e0\ub7ec\ub2dd, \ub525\ub7ec\ub2dd \ucc45\ub4e4\uc774 \uc8fc\ud53c\ud130 \ub178\ud2b8\ubd81\uc5d0\uc11c \uc2e4\uc2b5\ud558\uc9c0\ub9cc, \uc774 \ucc45\uc740 \uac00\uc7a5 \uae30\ucd08 \ub2e8\uacc4\uc5d0\uc11c \uba38\uc2e0\ub7ec\ub2dd \ud504\ub85c\uc81d\ud2b8\ub97c \uac04\uc811\uc801\uc73c\ub85c \uacbd\ud5d8\uc2dc\ucf1c \uc904 \uc218 \uc788\ub294 \ucc45\uc774\uc5c8\uc2b5\ub2c8\ub2e4. \uadf8\ub807\uae30\uc5d0 \uae30\ucd08\uac00 \ubd80\uc871\ud55c \ub3c5\uc790\uc5d0\uac8c\ub3c4 \uc801\uc808\ud558\ub2e4\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4."},{"id":"/ML-in-practice","metadata":{"permalink":"/en/blog/ML-in-practice","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2022-09-12-ML-in-practice/ML-in-practice.md","source":"@site/blog/2022-09-12-ML-in-practice/ML-in-practice.md","title":"\ud83d\udcd6 \ub370\uc2f8\ub178\ud2b8\uc758 \uc2e4\uc804\uc5d0\uc11c \ud1b5\ud558\ub294 \uba38\uc2e0\ub7ec\ub2dd","description":"Must Have | \ud53c\ucc98 \uc5d4\uc9c0\ub2c8\uc5b4\ub9c1 + TOP 10 \uc54c\uace0\ub9ac\uc998 + \uc2e4\ubb34 \ub178\ud558\uc6b0\ub85c \uc775\ud788\ub294 \uc2e4\ubb34\ud615 \ubb38\uc81c\ud480\uc774 \ud574\ubc95\uc11c","date":"2022-09-12T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \ub370\uc2f8\ub178\ud2b8\uc758 \uc2e4\uc804\uc5d0\uc11c \ud1b5\ud558\ub294 \uba38\uc2e0\ub7ec\ub2dd","date":"2022-09-12","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/ML-in-practice","description":"Must Have | \ud53c\ucc98 \uc5d4\uc9c0\ub2c8\uc5b4\ub9c1 + TOP 10 \uc54c\uace0\ub9ac\uc998 + \uc2e4\ubb34 \ub178\ud558\uc6b0\ub85c \uc775\ud788\ub294 \uc2e4\ubb34\ud615 \ubb38\uc81c\ud480\uc774 \ud574\ubc95\uc11c","keywords":["Machine Learning","\uba38\uc2e0\ub7ec\ub2dd","\ud53c\ucc98 \uc5d4\uc9c0\ub2c8\uc5b4\ub9c1"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \uae40\uae30\ud604\uc758 \ub525\ub7ec\ub2dd \ubd80\ud2b8\ucea0\ud504 with \ud30c\uc774\ud1a0\uce58","permalink":"/en/blog/deep-learning-bootcamp-with-pytorch"},"nextItem":{"title":"\ud83d\udcd6 \ud63c\uc790 \uacf5\ubd80\ud558\ub294 \ucef4\ud4e8\ud130 \uad6c\uc870+\uc6b4\uc601\uccb4\uc81c","permalink":"/en/blog/computer-structure-and-OS-to-study-alone"}},"content":":::info\\n\uace8\ub4e0\ub798\ube57 \ucd9c\ud310\uc0ac\ub85c\ubd80\ud130 \ucc45\uc744 \uc81c\uacf5\ubc1b\uc544 \uc791\uc131\ud588\uc2b5\ub2c8\ub2e4.\\n:::\\n\\n## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/ML-in-practice.jpg)](http://www.kyobobook.co.kr/product/detailViewKor.laf?ejkGb=KOR&mallGb=KOR&barcode=9791191905014&orderClick=LEa&Kc=)\\n\\n- \uc81c\ubaa9: \ub370\uc2f8\ub178\ud2b8\uc758 \uc2e4\uc804\uc5d0\uc11c \ud1b5\ud558\ub294 \uba38\uc2e0\ub7ec\ub2dd\\n- \uc800\uc790: \uad8c\uc2dc\ud604\\n- \ucd9c\ud310\uc0ac: \uace8\ub4e0\ub798\ube57\\n- \ucd9c\uac04: 2022-07-08\\n\\n{/* truncate */}\\n\\n## Intro\\n\\n\uba54\uc77c\ud568\uc744 \uc0dd\uac01 \uc5c6\uc774 \uc77d\ub2e4\uac00 \uace8\ub4e0\ub798\ube57 \ucd9c\ud310\uc0ac\uc5d0\uc11c \ubd81\uc2a4\ud3ec\uc77c\ub7ec\ub97c \ubaa8\uc9d1\ud55c\ub2e4\ub294 \uba54\uc77c\uc744 \ubcf4\uac8c \ub410\uc2b5\ub2c8\ub2e4. \ucc98\uc74c \ucc45 \uc81c\ubaa9\uc744 \uc811\ud588\uc744 \ub54c\ub294 **\\"\uc544, \ub610 \uba38\uc2e0\ub7ec\ub2dd \ucc45 \ub098\uc624\ub294\uad6c\ub098\\"** \ub77c\ub294 \uc0dd\uac01\uc774\uc5c8\uc2b5\ub2c8\ub2e4. \uadf8\ub7ec\ub098 \ucc45\uc758 \uad6c\uc131\uc744 \ubcf4\uace0 \uc0dd\uac01\uc774 \uc870\uae08 \ubc14\ub00c\uc5c8\uc2b5\ub2c8\ub2e4. \uc9c0\uae08\uae4c\uc9c0 \uc81c\uac00 \uc77d\ub358 \uba38\uc2e0\ub7ec\ub2dd \ucc45\uacfc\ub294 \ub2e4\ub974\ub2e4\ub294 \ub290\ub08c\uc744 \ubc1b\uc558\uc73c\uba70 \uc77d\uc5b4\ubcf4\uace0 \uc2f6\ub2e4\uace0 \uc0dd\uac01\ud588\uae30\uc5d0 \ubd81\uc2a4\ud3ec\uc77c\ub7ec\uc5d0 \ub3c4\uc804\ud588\uc2b5\ub2c8\ub2e4.\\n\\n## Book Review\\n\\n### \uba38\uc2e0\ub7ec\ub2dd\uc5d0 \uc7ac\ubc0c\uac8c \uc811\uadfc\ud558\uae30 \uc88b\uc740 \ucc45\\n\\n\ubb38\uc81c\ub97c \ud574\uacb0\ud558\ub294 \uac78 \uc88b\uc544\ud558\ub294 \uc0ac\ub78c\uc774\ub77c\uba74 \uc88b\uc544\ud560 \uc218\ubc16\uc5d0 \uc5c6\ub294 \ucc45 \uad6c\uc131\uc785\ub2c8\ub2e4. \uac01 Chapter\ub9c8\ub2e4 \ub300\ub7b5 \uba38\uc2e0\ub7ec\ub2dd \uc54c\uace0\ub9ac\uc998\uc744 \uc18c\uac1c\ud558\uace0, \uba38\uc2e0\ub7ec\ub2dd \ubb38\uc81c\ub97c \uc815\uc758\ud558\uc5ec \uadf8 \ubb38\uc81c\ub97c \ud480\uc5b4\uac00\ub294 \uacfc\uc815\uc744 \ubcf4\uc5ec\uc90d\ub2c8\ub2e4. \uc774\ub7f0 \uacfc\uc815\uc5d0\uc11c \uba38\uc2e0\ub7ec\ub2dd \uc54c\uace0\ub9ac\uc998\uc758 \ubd80\uac00\uc801\uc778 \ub0b4\uc6a9\uc744 \ub354 \uc54c\ub824\uc8fc\ub294 \uad6c\uc131\uc785\ub2c8\ub2e4. \uba38\uc2e0\ub7ec\ub2dd\uc758 \uc804\uccb4\uc801\uc778 \uacfc\uc815\uc744 \uccb4\ud5d8\ud574\ubcfc \uc218 \uc788\uace0, \uc2e4\uc804 \ubb38\uc81c\ub97c \ud480\uc5b4\ubcf4\ub294 \ub290\ub08c\uc774\ub77c \uc815\ub9d0 \uc7ac\ubc0c\uac8c \uc77d\uc5c8\uc2b5\ub2c8\ub2e4.\\n\\n\uc77c\ubc18\uc801\uc778 \uba38\uc2e0\ub7ec\ub2dd \ucc45\uc774\ub77c\uba74 \uc54c\uace0\ub9ac\uc998\uacfc \ubaa8\ub378\uc5d0 \ub300\ud574 \uc218\ud559\uc801\uc73c\ub85c \ud55c\ucc38 \uc124\uba85\ud558\ub294\ub370 \uc774 \ucc45\uc740 \ub2ec\ub790\uc2b5\ub2c8\ub2e4. \uc218\ud559\uc801\uc778 \ubd80\ubd84\uc740 \ucd5c\uc18c\ud654\ud558\uc5ec \uc124\uba85\ud569\ub2c8\ub2e4. \uadf8\ub7f0 \uc810\uc5d0\uc11c \uc77d\uae30 \ud3b8\ud588\uc2b5\ub2c8\ub2e4.\\n\\n### \ucd5c\uc2e0 \uba38\uc2e0\ub7ec\ub2dd \uc54c\uace0\ub9ac\uc998\\n\\n\uc774 \ucc45\uc740 \uba38\uc2e0\ub7ec\ub2dd\uc5d0\uc11c \uaf64 \uc790\uc8fc \uc0ac\uc6a9\ub418\ub294 10\uac00\uc9c0 \uc54c\uace0\ub9ac\uc998\uc744 \uac01\uac01 \ud55c Chapter\uc5d0 \uac78\uccd0 \uc124\uba85\ud569\ub2c8\ub2e4. \uc804 \ub2e4\ub978 8\uac1c\uc758 \uc54c\uace0\ub9ac\uc998\uc740 \uadf8\ub798\ub3c4 \uc720\uba85\ud558\uae30\uc5d0 \uc54c\uace0 \uc788\uc5c8\uc73c\ub098 \ucd5c\uadfc \uce90\uae00\uc5d0\uc11c \ud56b\ud558\ub2e4\ub294 XGBoost \uac19\uc740 \uc54c\uace0\ub9ac\uc998\uc740 \ub300\ub7b5 \ubcf4\uae30\ub9cc \ud588\uace0 \uacf5\ubd80\ud574\ubcf8 \uc801\uc740 \uc5c6\uae30\uc5d0 \uc774 \ucc45\uc774 \uad81\uae08\ud588\uc2b5\ub2c8\ub2e4. \ucc45\uc758 \uad6c\uc131\ub3c4 \uc2dc\uc911\uc5d0 \ub098\uc628 \ub2e4\ub978 \ucc45\uacfc \ub2ec\ub790\uc9c0\ub9cc, XGBoost\ub098 LightGBM \uac19\uc740 \uaf64 \ucd5c\uadfc \uc54c\uace0\ub9ac\uc998\uae4c\uc9c0 \ub2e4\ub8ec\ub2e4\ub294 \uc810\uc5d0\uc11c \uc774 \ucc45\uc744 \uc77d\uc5b4\ubcfc \uac00\uce58\uac00 \uc788\uc5c8\uc2b5\ub2c8\ub2e4.\\n\\n### \uc544\uc26c\uc6b4 \uc624\ud0c8\uc790\\n\\n\uc77d\uc73c\uba74\uc11c \uc740\uadfc\ud788 \uc624\ud0c8\uc790\uac00 \ub208\uc5d0 \ubcf4\uc600\uc2b5\ub2c8\ub2e4. \ucc45\uc744 \ub9cc\ub4dc\ub294 \uac83\uc774 \ud56d\uc0c1 \uc644\ubcbd\ud560 \uc218\ub294 \uc5c6\uc9c0\ub9cc, \ub3c5\uc790\ub85c\uc11c\ub294 \uc544\uc26c\uc6c0\uc774 \ud070 \ubd80\ubd84\uc774\uc5c8\uc2b5\ub2c8\ub2e4. \uc5bc\ub978 \ub354 \ub9ce\uc774 \ud314\ub824\uc11c 2\uc1c4\uc5d0\ub294 \uc624\ud0c8\uc790\uac00 \uc218\uc815\ub418\uae30\ub97c \ubc14\ub78d\ub2c8\ub2e4.\\n\\n## \ub300\uc0c1 \ub3c5\uc790\\n\\n\uba38\uc2e0\ub7ec\ub2dd\uc5d0 \uc785\ubb38\ud558\ub294 \ucd08\ubcf4\uc790\ub97c \ub300\uc0c1\uc73c\ub85c \uc218\ud559 \ub610\ub294 \ud1b5\uacc4\uc801\uc778 \uc9c0\uc2dd\ubcf4\ub2e4\ub294 \uadf8\ub9bc\uc744 \uc0ac\uc6a9\ud558\ub294 \uc800\uc790\uc758 \ub178\ub825\uc774 \ucc45\uc5d0\uc11c \ubcf4\uc600\uc73c\ub098 \uac1c\ub150\uc744 \ucc98\uc74c\ubd80\ud130 \uc790\uc138\ud788 \uc54c\ub824\uc8fc\ub294 \ubc29\uc2dd\uc774 \uc544\ub2c8\uace0, \uc9c4\ud589\ud558\uba74\uc11c \uc54c\uace0\ub9ac\uc998\uc744 \uc124\uba85\ud558\ub294 \ubc29\uc2dd\uc740 \uba38\uc2e0\ub7ec\ub2dd\uc744 \ucc98\uc74c \uacf5\ubd80\ud558\ub294 \uc0ac\ub78c\uc5d0\uac8c\ub294 \uc5b4\ub824\uc6b8 \uc218 \uc788\uc744 \uac70\ub77c \uc0dd\uac01\ud569\ub2c8\ub2e4. \uadf8\ub7ec\ub098 \uba38\uc2e0\ub7ec\ub2dd\uc5d0 \uacbd\ud5d8\uc774 \uc788\uc73c\uc2e0 \ubd84\uc774\ub77c\uba74 \uc7ac\ubc0c\uac8c \uc77d\uc73c\uc2e4 \uc218 \uc788\uc744 \uac83 \uac19\uc2b5\ub2c8\ub2e4.\\n\\n\uba38\uc2e0\ub7ec\ub2dd \uc54c\uace0\ub9ac\uc998\ub9cc \ub2e4\ub8e8\ub294 \uac83\uc774 \uc544\ub2cc \uba38\uc2e0\ub7ec\ub2dd\uc758 \uc804\uccb4\uc801\uc778 \uacfc\uc815\uc774 \uad81\uae08\ud558\uc2e0 \ubd84\uaed8 \ucd94\ucc9c\ud569\ub2c8\ub2e4. \uc800\ub294 \uba38\uc2e0\ub7ec\ub2dd\uc5d0\uc11c \uc54c\uace0\ub9ac\uc998\ub3c4 \uc911\uc694\ud558\ub2e4\uace0 \uc0dd\uac01\ud558\uc9c0\ub9cc, \uac00\uc7a5 \uc911\uc694\ud55c \uac74 \ud559\uc2b5\ud558\uae30 \uc804 \ub370\uc774\ud130 \uc804\ucc98\ub9ac \uacfc\uc815\uc774\ub77c \uc0dd\uac01\ud569\ub2c8\ub2e4. \uc774 \ucc45\uc740 \uc54c\uace0\ub9ac\uc998\ub9cc \uc124\uba85\ud558\ub294 \uac83\uc774 \uc544\ub2c8\ub77c \ub370\uc774\ud130 \ud074\ub9ac\ub2dd, \ud53c\ucc98 \uc5d4\uc9c0\ub2c8\uc5b4\ub9c1, \uc2dc\uac01\ud654 \ub4f1\ub3c4 \ub2e4\ub8f9\ub2c8\ub2e4. \uc774\ub7f0 \uacfc\uc815\uc5d0\uc11c \uc815\ub9d0 \uc88b\uc740 \uafc0\ud301\uc744 \uc5bb\uc744 \uc218 \uc788\uc5b4 \uc88b\uc558\uc2b5\ub2c8\ub2e4."},{"id":"/computer-structure-and-OS-to-study-alone","metadata":{"permalink":"/en/blog/computer-structure-and-OS-to-study-alone","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2022-09-11-computer-structure-and-OS-to-study-alone/computer-structure-and-OS-to-study-alone.md","source":"@site/blog/2022-09-11-computer-structure-and-OS-to-study-alone/computer-structure-and-OS-to-study-alone.md","title":"\ud83d\udcd6 \ud63c\uc790 \uacf5\ubd80\ud558\ub294 \ucef4\ud4e8\ud130 \uad6c\uc870+\uc6b4\uc601\uccb4\uc81c","description":"\ud63c\uacf5 \uc2dc\ub9ac\uc988","date":"2022-09-11T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \ud63c\uc790 \uacf5\ubd80\ud558\ub294 \ucef4\ud4e8\ud130 \uad6c\uc870+\uc6b4\uc601\uccb4\uc81c","date":"2022-09-11","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/computer-structure-and-OS-to-study-alone","description":"\ud63c\uacf5 \uc2dc\ub9ac\uc988","keywords":["\ucef4\ud4e8\ud130\uad6c\uc870","\uc6b4\uc601\uccb4\uc81c","\ud63c\uacf5","\ud63c\uc790 \uacf5\ubd80\ud558\ub294"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \ub370\uc2f8\ub178\ud2b8\uc758 \uc2e4\uc804\uc5d0\uc11c \ud1b5\ud558\ub294 \uba38\uc2e0\ub7ec\ub2dd","permalink":"/en/blog/ML-in-practice"},"nextItem":{"title":"\ud83d\udcd6 \uba74\uc811\uc744 \uc704\ud55c CS \uc804\uacf5\uc9c0\uc2dd \ub178\ud2b8","permalink":"/en/blog/CS-note-for-interview"}},"content":":::info\\n\ud55c\ube5b\ubbf8\ub514\uc5b4 \\\\<\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4\\\\> \ud65c\ub3d9\uc744 \uc704\ud574\uc11c \ucc45\uc744 \uc81c\uacf5\ubc1b\uc544 \uc791\uc131\ub41c \uc11c\ud3c9\uc785\ub2c8\ub2e4.\\n:::\\n\\n![\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4 2022](../assets/I-am-reviewer-2022.jpg)\\n\\n## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/computer-structure-and-OS-to-study-alone.jpg)](https://www.kyobobook.co.kr/product/detailViewKor.laf?ejkGb=KOR&mallGb=KOR&barcode=9791162243091&orderClick=LEa&Kc=)\\n\\n- \uc81c\ubaa9: \ud63c\uc790 \uacf5\ubd80\ud558\ub294 \ucef4\ud4e8\ud130 \uad6c\uc870+\uc6b4\uc601\uccb4\uc81c\\n- \uc800\uc790: \uac15\ubbfc\ucca0\\n- \ucd9c\ud310\uc0ac: \ud55c\ube5b\ubbf8\ub514\uc5b4\\n- \ucd9c\uac04: 2022-08-16\\n\\n{/* truncate */}\\n\\n## \ud83c\udfacIntro\\n\\nC\uc5b8\uc5b4, \ud30c\uc774\uc36c, \uc790\ubc14\ub97c \uac70\uccd0 \ubc8c\uc368 \ub124 \ubc88\uc9f8\ub85c \uc77d\uc5b4\ubcf4\ub294 \ud63c\uacf5 \uc2dc\ub9ac\uc988\uc774\ub2e4. \uc804\uacf5\uc0dd\uc73c\ub85c\uc11c \ucef4\ud4e8\ud130 \uad6c\uc870, \uc6b4\uc601\uccb4\uc81c \uc218\uc5c5\uc744 \ub4e4\uc5c8\uc74c\uc5d0\ub3c4 \ubd80\uc871\ud568\uc744 \ub290\uaef4 \uc774 \ucc45\uc744 \uc77d\uac8c \ub410\ub2e4. \uc774\ubc88\uc5d0\ub294 \ub9ac\ubdf0\uc5b4\uc5d0 \uc9c0\uc6d0\ud558\uc5ec \uc77d\uac8c \ub410\uc9c0\ub9cc, \ub9ac\ubdf0\uc5b4\uac00 \ub418\uc9c0 \uc54a\uc558\ub354\ub77c\ub3c4 \uad6c\ub9e4\ud574\uc11c \uc77d\uc5b4\ubcfc \uc608\uc815\uc774\uc5c8\ub358 \ucc45\uc774\uc5c8\ub2e4.\\n\\n\uc0ac\uc2e4 \ub098\ub294 \ud63c\uacf5 \uc2dc\ub9ac\uc988\uac00 \ub108\ubb34 \uc27d\uace0 \uac04\ub2e8\ud558\ub2e4\ub294 \uc774\uc720\ub85c \ubcc4\ub85c \uc88b\uc544\ud558\uc9c0 \uc54a\uc558\uc5c8\ub2e4. \uadf8\ub7ec\ub098 \uba87\uba87 \ucc45\ub4e4\uc744 \uc77d\uc5b4\ubcf4\ub2c8 \uacb0\uad6d \ud63c\uacf5 \uc2dc\ub9ac\uc988\ub9cc\ud07c \uc785\ubb38\ud558\uae30 \ud3b8\ud55c \ucc45\uc774 \uc5c6\uc5c8\ub2e4. \uac00\ub054 \ube44\uc804\uacf5\uc790 \uce5c\uad6c\ub4e4\uc774 \ucf54\ub529\uc744 \uc774\uc81c \ub9c9 \uc2dc\uc791\ud574\ubcf8\ub2e4\uace0 \ub098\uc5d0\uac8c \ucd94\ucc9c\uc744 \ud574\ub2ec\ub77c\uace0 \ud558\ub294\ub370 \uadf8\ub7f4 \ub54c\ub9c8\ub2e4 \ub098\ub294 \ud63c\uacf5 \uc2dc\ub9ac\uc988\ub97c \ucd94\ucc9c\ud55c\ub2e4. \\n\\n## Book Review\\n\\n### \uc77d\uae30 \uc26c\uc6b4 \ucc45\\n\\n\ucef4\ud4e8\ud130 \uad6c\uc870\ub97c \ucc98\uc74c \uacf5\ubd80\ud558\uac8c \ub418\uba74 architecture\ub97c ARM \ub610\ub294 RISC-V\ub85c \ubc30\uc6b0\uac8c \ub418\ub294\ub370 \uc774 \ucc45\uc740 \uc785\ubb38\uc790\ub97c \ub300\uc0c1\uc73c\ub85c \ud558\ub2c8 \uadf8\ub7f0 \uc5b4\ub824\uc6b4 \uc9c0\uc2dd\uc740 \uac70\uc758 \ubc30\uc81c\ub418\uc5b4 \uc788\uc2b5\ub2c8\ub2e4. \ub108\ubb34 \ub514\ud14c\uc77c\ud558\uac8c CS \uc9c0\uc2dd\uc744 \uc54c\ub824\uc8fc\ub294 \ucc45\uc740 \uc544\ub2c8\ub77c\ub294 \uac81\ub2c8\ub2e4. \ub9cc\uc57d \uc774 \ucc45\uc774 \ub514\ud14c\uc77c\ud558\uace0 \uc27d\uac8c \uc124\uba85\ub418\uc5c8\ub354\ub77c\uba74 \ucc45 \ub450\uaed8\uac00 \uc5b4\ub9c8\uc5b4\ub9c8\ud588\uc744 \uac83\uc785\ub2c8\ub2e4.\\n\\n\uc815\ub9d0 \uaf2d \ud544\uc694\ud55c \ud544\uc218 \uc694\uc18c \uc989, \ud575\uc2ec\ub9cc \ub4e4\uc5b4\uac00 \uc788\uc73c\uba70 \uacf5\ubd80\ud558\uba74\uc11c \uc4f8\ub370\uc5c6\ub294 \uace0\ubbfc\uc744 \ud558\uc9c0 \uc54a\uac8c \ud574\uc90d\ub2c8\ub2e4. \uadf8\ub7f0 \uc810\uc5d0\uc11c \uc800\ub294 \uc774 \ucc45\uc774 \ub9e4\uc6b0 \uc77d\uae30 \uc26c\uc6e0\ub2e4\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4.\\n\\n### \uc774\ud574\ud558\uae30 \uc26c\uc6b4 \uadf8\ub9bc\\n\\n\ucd08\ubcf4\uc790\uac00 \uc774\ud574\ud558\uae30 \uc27d\ub3c4\ub85d \uadf8\ub9bc\uc73c\ub85c \ub9e4\uc6b0 \uc124\uba85\uc774 \uc798 \ub418\uc5b4 \uc788\uc2b5\ub2c8\ub2e4. \ucef4\ud4e8\ud130 \uad6c\uc870 \uac19\uc740 \uacbd\uc6b0\uc5d0\ub294 \ucc98\uc74c \uacf5\ubd80\ud558\uac8c \ub418\uba74 \uc5b4\ub5a4 \uad6c\uc870\ub85c \ub3cc\uc544\uac00\ub294\uc9c0 \uc21c\uc11c\uac00 \uc5b4\ub824\uc6b8 \uc218 \uc788\uc73c\ub098 \uc774 \ucc45\uc740 \uccab \uc7a5\ubd80\ud130 \uc0bd\ud654\ub85c \uc798 \ud45c\ud604\uc774 \ub418\uc5b4 \uc788\uae30\uc5d0 \uc5b4\ub835\uc9c0 \uc54a\uc558\uc2b5\ub2c8\ub2e4.\\n\\n### \ud63c\uc790 \uacf5\ubd80\ud558\ub294 \uc0ac\ub78c\uc744 \uc704\ud55c \ucc45\\n\\n\ud63c\uc790 \uacf5\ubd80\ud558\uac8c \ub418\uba74 \uc790\uc2e0\uc774 \uacf5\ubd80\ud55c \uc9c0\uc2dd\uc774 \uc81c\ub300\ub85c \uba38\ub9ac\uc5d0 \ub0a8\uc558\ub294\uc9c0 \uc54c\uae30 \uc27d\uc9c0 \uc54a\uc2b5\ub2c8\ub2e4. \uadf8\ub807\uae30\uc5d0 \uacf5\ubd80\ud55c \ud6c4\uc5d0 \uc9c0\uc2dd\uc744 \uc810\uac80\ud558\ub294 \uac83\uc740 \uaf2d \ud544\uc218\ub77c \uc0dd\uac01\ud569\ub2c8\ub2e4. \uc774 \ucc45\uc740 \uac01 \uc7a5\uc758 \ub9c8\uc9c0\ub9c9\uc5d0 \ub9c8\ubb34\ub9ac\ub85c \uc815\ub9ac\ub97c \ub2e4\uc2dc \ud574\uc8fc\uace0, \ud655\uc778 \ubb38\uc81c\ub97c \ud1b5\ud574 \uc81c\ub300\ub85c \uacf5\ubd80\ud588\ub294\uc9c0 \uc810\uac80\ud560 \uc218 \uc788\uac8c \ud574\uc90d\ub2c8\ub2e4. \uc774\uac8c \ud63c\uacf5 \uc2dc\ub9ac\uc988\uc758 \uc7a5\uc810\uc774\ub77c \uc0dd\uac01\ud569\ub2c8\ub2e4.\\n\\n\ucc45\uc758 \ub9e8 \ub05d\uc5d0 \ubd80\ub85d\uc73c\ub85c \ud63c\uc790 \uacf5\ubd80\ud558\ub294 \uc0ac\ub78c\uc744 \uc704\ud55c \ud63c\uacf5 \uc6a9\uc5b4 \ub178\ud2b8\uac00 \uc788\uc2b5\ub2c8\ub2e4. \uc815\ub9d0 \ubcc4\uac70 \uc544\ub2c8\uc9c0\ub9cc, \uc815\ub9d0 \ucd9c\ud310\uc0ac\uac00 \ud63c\uc790 \uacf5\ubd80\ud558\ub294 \uc0ac\ub78c\uc744 \uc704\ud574 \ub178\ub825\ud558\uace0 \uc788\ub2e4\ub294 \ubaa8\uc2b5\uc774 \ubcf4\uc5ec\uc11c \uc7ac\ubc0c\uae30\ub3c4 \ud558\uace0, \uc774 \ucc45\uc744 \uc77d\ub294 \uc0ac\ub78c\uc5d0\uac8c\ub294 \ub3c4\uc6c0\uc774 \ub9ce\uc774 \ub418\uaca0\ub2e4\uace0 \uc0dd\uac01\ud588\uc2b5\ub2c8\ub2e4.\\n\\n## \ub300\uc0c1 \ub3c5\uc790\\n\\n\ucef4\ud4e8\ud130 \uad6c\uc870\uc640 \uc6b4\uc601\uccb4\uc81c \uc218\uc5c5\uc744 \uc120\ud589 \ud559\uc2b5\ud574\ubcf4\uace0 \uc2f6\uc740 2\ud559\ub144 \uc804\uacf5\uc0dd\uc5d0\uac8c \ucd94\ucc9c\ud569\ub2c8\ub2e4. \uc81c\uac00 \ucef4\ud4e8\ud130\uad6c\uc870, \uc6b4\uc601\uccb4\uc81c \uc218\uc5c5\uc744 \ub4e4\uc744 \ub54c \uc774 \ucc45\uc774 \uc65c \uc5c6\uc5c8\ub294\uc9c0 \uc544\uc27d\uae30\ub9cc \ud569\ub2c8\ub2e4. \uadf8\ub54c\ub85c \ub3cc\uc544\uac04\ub2e4\uba74 \uc218\uc5c5\ubcf4\ub2e4 \uc774 \ucc45\uc73c\ub85c \uba3c\uc800 \uacf5\ubd80\ub97c \uc2dc\uc791\ud588\uc744 \uac83\uc785\ub2c8\ub2e4.\\n\\n\ub610\ud55c, \uc694\uc998 \ub9ce\uc740 \ube44\uc804\uacf5\uc790\ub4e4\uc774 \uad6d\ube44 \ud559\uc6d0 \ub610\ub294 \ub2e4\uc591\ud55c \uad50\uc721 \ud504\ub85c\uadf8\ub7a8\uc744 \ud1b5\ud574 \uac1c\ubc1c\uc744 \uacf5\ubd80\ud558\uace0 \uc788\ub294 \uac78\ub85c \uc54c\uace0 \uc788\uc2b5\ub2c8\ub2e4. \uadf8\ub7ec\ub098 \uadf8\ub7f0 \ud504\ub85c\uadf8\ub7a8\uc5d0\uc11c\ub294 CS \uacf5\ubd80\ub97c \uc815\ub9d0 \uac04\ub2e8\ud558\uac8c \ud558\ub294 \uacbd\uc6b0\uac00 \ub9ce\uc740 \uac83 \uac19\uc2b5\ub2c8\ub2e4. \uc194\uc9c1\ud788 \uc800\ub294 \uadf8 \uc815\ub3c4\uc758 \uacf5\ubd80\ub85c\ub294 \ubd80\uc871\ud558\ub2e4\uace0 \uc0dd\uac01\ud558\ub294 \uc0ac\ub78c\uc73c\ub85c\uc11c \ud504\ub85c\uadf8\ub7a8\uc758 \uc218\uc5c5\uc744 \ub4e3\ub418, \uaf2d \ucc45\ub3c4 \uc77d\uc5b4\ubcf4\ub77c\uace0 \uad8c\ud569\ub2c8\ub2e4. \ub098\uc911\uc5d0 \ub2e4\uc2dc \ucc45 \ucd94\ucc9c\ud560 \uc77c\uc774 \uc788\ub354\ub77c\uba74 \uc774 \ucc45\uc73c\ub85c \ucef4\ud4e8\ud130\uad6c\uc870\uc640 \uc6b4\uc601\uccb4\uc81c\ub97c \uacf5\ubd80\ud574\ubcf4\ub77c\uace0 \ud560 \uac83 \uac19\uc2b5\ub2c8\ub2e4."},{"id":"/CS-note-for-interview","metadata":{"permalink":"/en/blog/CS-note-for-interview","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2022-08-05-CS-note-for-interview/CS-note-for-interview.md","source":"@site/blog/2022-08-05-CS-note-for-interview/CS-note-for-interview.md","title":"\ud83d\udcd6 \uba74\uc811\uc744 \uc704\ud55c CS \uc804\uacf5\uc9c0\uc2dd \ub178\ud2b8","description":"\ub514\uc790\uc778 \ud328\ud134, \uc6b4\uc601\uccb4\uc81c, \ub370\uc774\ud130\ubca0\uc774\uc2a4, \uc790\ub8cc \uad6c\uc870, \ub124\ud2b8\uc6cc\ud06c, \uac1c\ubc1c\uc790 \uba74\uc811","date":"2022-08-05T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":false,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \uba74\uc811\uc744 \uc704\ud55c CS \uc804\uacf5\uc9c0\uc2dd \ub178\ud2b8","date":"2022-08-05","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/CS-note-for-interview","description":"\ub514\uc790\uc778 \ud328\ud134, \uc6b4\uc601\uccb4\uc81c, \ub370\uc774\ud130\ubca0\uc774\uc2a4, \uc790\ub8cc \uad6c\uc870, \ub124\ud2b8\uc6cc\ud06c, \uac1c\ubc1c\uc790 \uba74\uc811","keywords":["\uba74\uc811","Computer Science","\ucde8\uc5c5 \uc900\ube44"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \ud63c\uc790 \uacf5\ubd80\ud558\ub294 \ucef4\ud4e8\ud130 \uad6c\uc870+\uc6b4\uc601\uccb4\uc81c","permalink":"/en/blog/computer-structure-and-OS-to-study-alone"},"nextItem":{"title":"\ud83c\udfcd\ufe0f 5\uc6d4 \uc6b0\uc544\ud55c\ud14c\ud06c\uc138\ubbf8\ub098","permalink":"/en/blog/may-woowahan-tech-seminar"}},"content":":::info\\n\uae38\ubc97 \u201818\ucc28 \uac1c\ubc1c\uc790 \ub9ac\ubdf0\uc5b4\u2019 \ud65c\ub3d9\uc744 \uc704\ud574\uc11c \ucc45\uc744 \uc81c\uacf5\ubc1b\uc544 \uc791\uc131\ub41c \uc11c\ud3c9\uc785\ub2c8\ub2e4.\\n:::\\n\\n## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/book-review-CS-note-for-interview.jpg)](https://www.kyobobook.co.kr/product/detailViewKor.laf?ejkGb=KOR&mallGb=KOR&barcode=9791165219529&orderClick=LEa&Kc=)\\n\\n- \uc81c\ubaa9: \uba74\uc811\uc744 \uc704\ud55c CS \uc804\uacf5\uc9c0\uc2dd \ub178\ud2b8\\n- \uc800\uc790: \uc8fc\ud64d\ucca0\\n- \ucd9c\ud310\uc0ac: \uae38\ubc97\\n- \ucd9c\uac04: 2022-04-28\\n\\n## Intro\\n\\n\uc2e0\uc785\uc73c\ub85c \ucde8\uc5c5\uc744 \uc900\ube44\ud558\ub294 \ud559\uc0dd\uc5d0\uac8c \uac00\uc7a5 \uc911\uc694\ud55c \uac74 \ucf54\ub529 \ud14c\uc2a4\ud2b8\uc640 \uba74\uc811 \uc900\ube44\ub77c\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4. \uc804 \ucde8\uc900\uc0dd\uc73c\ub85c\uc11c \ucf54\ub529 \ud14c\uc2a4\ud2b8\ub294 \uc9c0\uae08\ub3c4 \uc5f4\uc2ec\ud788 \uacf5\ubd80\ud558\uace0 \uc788\uc9c0\ub9cc, \ubb38\uc81c\ub294 \uba74\uc811 \uc900\ube44\uc600\uc2b5\ub2c8\ub2e4. \uc5b4\ub290\uc815\ub3c4 \ub300\ub2f5\uc740 \ud560 \uc218 \uc788\uaca0\uc9c0\ub9cc, \uc544\uc9c1 \ubd80\uc871\ud558\ub2e4\uace0 \uc0dd\uac01\ud588\uc2b5\ub2c8\ub2e4.\\n\\n\ubcf4\ud1b5 \uba74\uc811\uc5d0\uc11c\ub294 \ud504\ub85c\uc81d\ud2b8 \uad00\ub828\ud574\uc11c\ub3c4 \uc9c0\uc6d0\uc790\uc5d0\uac8c \ubb3b\uc9c0\ub9cc Computer Science \uc804\uacf5 \uc9c0\uc2dd\uc744 \ubb3b\ub294 \uacbd\uc6b0\ub3c4 \uc788\uc2b5\ub2c8\ub2e4. \uadf8\ub807\uae30\uc5d0 CS \uc804\uacf5\uc744 \ub2e4\uc2dc \ubcf5\uc2b5\ud560 \ud544\uc694\uac00 \uc788\uc5c8\uace0, \uc804 \\"\uc5b4\ub5bb\uac8c CS \uc804\uacf5\uc744 \ub2e4\uc2dc \ubcf5\uc2b5\ud558\uc9c0?\\"\ub77c\ub294 \uace0\ubbfc\uc744 \ud558\uac8c \ub410\uc2b5\ub2c8\ub2e4. \uc6b4\uc601\uccb4\uc81c, \ub124\ud2b8\uc6cc\ud06c, \uc790\ub8cc\uad6c\uc870, \ub370\uc774\ud130\ubca0\uc774\uc2a4, \ucef4\ud4e8\ud130 \uad6c\uc870 \ub4f1\uc758 \uc804\uacf5\uc740 \ubaa8\ub450 \ucc45 \ud558\ub098\ud558\ub098\uac00 \uc5c4\uccad \ub450\uaecd\uae30\uc5d0 \ub2e4\uc2dc \ubcfc \uc0dd\uac01\uc5d0 \ub9c9\ub9c9\ud588\uc2b5\ub2c8\ub2e4. \uc774\ub7f0 \uace0\ubbfc\uc744 \ud558\uace0 \uc788\uc744 \ub54c \uc774 \ucc45\uc744 \ub9cc\ub098\uac8c \ub410\uace0, \uc774\ubc88 \uac1c\ubc1c\uc790 \ub9ac\ubdf0\uc5b4\uc5d0 \uc2e0\uccad\uc744 \ud558\uc5ec \uc77d\uc5b4\ubcfc \uc218 \uc788\uac8c \ub410\uc2b5\ub2c8\ub2e4.\\n\\n### \ub514\uc790\uc778 \ud328\ud134\\n\\n\uc800\ub294 \uc774 \ubd80\ubd84\uc774 \uac00\uc7a5 \uc5b4\ub824\uc6b0\uba74\uc11c \uac00\uc7a5 \uc720\uc6a9\ud588\ub358 \ubd80\ubd84\uc774\uc5c8\uc2b5\ub2c8\ub2e4. \uc194\uc9c1\ud788 \uc804 \ubc31\uc5d4\ub4dc\ub97c \uc5c4\uccad \uae4a\uac8c \uacf5\ubd80\ud55c \uac83\uc774 \uc544\ub2c8\ub77c \ub514\uc790\uc778 \ud328\ud134\uc5d0 \ub300\ud574 \uac04\ub7b5\ud558\uac8c\ub294 \uc54c\uace0 \uc788\uc5c8\uc73c\ub098, \uc608\ub97c \ub4e4\uc5b4 \\"\uc2f1\uae00\ud1a4 \ud328\ud134\\"\uc5d0 \ub300\ud574 \uc790\uc138\ud788 \uc124\uba85\ud574\ubcf4\ub77c\uace0 \ud558\uba74 \ubabb\ud560 \uc218\uc900\uc774\uc5c8\uc2b5\ub2c8\ub2e4. \uc774 \ucc45\uc744 \uc77d\uc73c\uba74\uc11c \ub2e4\uc591\ud55c \ub514\uc790\uc778 \ud328\ud134\uc744 \uacf5\ubd80\ud574\ubcfc \uc218 \uc788\uc5b4 \ub3c4\uc6c0\uc774 \ub410\uc2b5\ub2c8\ub2e4.\\n\\n### \uc815\ub9d0 \ud544\uc218\uc801\uc778 \uc9c0\uc2dd\\n\\n\uc815\ub9d0 \uac01 \uc804\uacf5 \uacfc\ubaa9\uc5d0\uc11c \ud544\uc218\uc801\uc778 \uc9c0\uc2dd\ub9cc \uac04\ub2e8\ud558\uac8c \uc5c4\uccad \uc798 \uc694\uc57d\ub418\uc5b4 \uc788\uc2b5\ub2c8\ub2e4. \uc911\uac04\uc911\uac04 \uc774\ubbf8\uc9c0 \uc0bd\ud654\ub85c \uc124\uba85\ud558\ub294 \ubd80\ubd84\ub3c4 \uc88b\uc558\uace0, \uc9c4\uc9dc \ubcf5\uc2b5\uc6a9\uc73c\ub85c \uacf5\ubd80\ud558\uae30 \uc88b\uc740 \ucc45\uc774\ub77c\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4.\\n\\n\uc774 \ucc45\uc744 \ubcf4\uba74\uc11c \uac01\uc885 \ubb38\uc11c\ub4e4\uc744 \ucc38\uace0\ud558\uba74\uc11c \uacf5\ubd80\ud588\uc9c0\ub9cc, \uc774 \ucc45\uc5d0\uc11c \uc124\uba85\ud558\ub294 \uac83\uc774 \uac00\uc7a5 \uae54\ub054\ud588\uc2b5\ub2c8\ub2e4. \ub610\ud55c, \ucc45\uc5d0 \uae30\uc220\ub418\uc5b4 \uc788\ub294 \uac83\ub4e4\ub9cc \ub0b4\uac00 \uc81c\ub300\ub85c \uc54c\uace0, \uc124\uba85\ud560 \uc218 \uc788\uc73c\uba74 \uc801\uc5b4\ub3c4 \uba74\uc811\uc5d0\uc11c \ub9c8\uc774\ub108\uc2a4\ub294 \ub418\uc9c0 \uc54a\uaca0\ub2e4\uace0 \uc0dd\uac01\ud588\uae30\uc5d0 \uc774 \ucc45\uc744 \ubbff\uace0 \uacf5\ubd80\ud588\uc2b5\ub2c8\ub2e4. \ub525\ud558\uac8c\ub294 \uc544\ub2c8\uc9c0\ub9cc \uac04\ub2e8\ud558\uac8c\ub77c\ub3c4 \uc774 \ucc45\uc744 \ud1b5\ud574\uc11c \uc804\ubc18\uc801\uc778 \ub514\uc790\uc778 \ud328\ud134\uc744 \uacf5\ubd80\ud560 \uc218 \uc788\uac8c \ub418\uc5b4 \ub108\ubb34 \uc720\uc775\ud588\uc2b5\ub2c8\ub2e4.\\n\\n### \ub9c8\uc9c0\ub9c9 \uba74\uc811 \ud301\\n\\n\uc800\uc790\uc758 \uac1c\uc778\uc801\uc778 \ud3ec\ud2b8\ud3f4\ub9ac\uc624 \uc791\uc131 \ubc29\ubc95, \uba74\uc811 \ub178\ud558\uc6b0 \ub4f1\uc744 \uc124\uba85\ud569\ub2c8\ub2e4. \ub610\ud55c, \uba74\uc811\uc5d0\uc11c \ub098\uc62c \uc218 \uc788\ub294 \uc608\uc0c1 \uc9c8\ubb38\uc5d0 \ub300\ud574 \uc124\uba85\ud569\ub2c8\ub2e4. \uc800\uc790\uaed8\uc11c \uc9c1\uc811 \uacbd\ud5d8\ud558\uc2dc\uace0 \ub300\ub2f5\ud558\uc2e0 \ub0b4\uc6a9\ub4e4\uc774 \ub2f4\uaca8\uc838 \uc788\uc5b4 \uba74\uc811\uc5d0\uc11c \uc5b4\ub5bb\uac8c \ub300\ub2f5\uc744 \ud574\uc57c \ud560\uc9c0 \ucc38\uace0\ud560 \uc218 \uc788\uc5c8\uc2b5\ub2c8\ub2e4.\\n\\n## \ub300\uc0c1 \ub3c5\uc790\\n\\n\uba74\uc811 \uc804\uc5d0 \uae09\ud558\uac8c \uacf5\ubd80\ud574\uc57c \ud560 \ub54c \ubcf4\uae30 \uc88b\uc744 \uac83 \uac19\uc2b5\ub2c8\ub2e4. \uadf8\ub7ec\ub098 \uaf2c\ub9ac \ubb3c\uae30 \uc9c8\ubb38\uc744 \uc5b4\ub514\uae4c\uc9c0 \ud560\uc9c0 \uc800\ud76c\ub294 \uc608\uce21\ud560 \uc218 \uc5c6\uc73c\ub2c8 \ucc45\uc5d0 \uc801\ud78c \ub0b4\uc6a9\uc73c\ub85c\ub3c4 \ubd80\uc871\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \ubd80\uc81c\uc5d0 \uae30\uc220\ub418\uc5b4 \uc788\ub294 \uc804\uacf5 \uacfc\ubaa9\uc5d0 \ub300\ud574 \uacf5\ubd80\ud574\ubcf4\uc2dc\uc9c0 \uc54a\uc73c\uc168\ub2e4\uba74 \uc0c1\ub2f9\ud788 \uc5b4\ub824\uc6b8 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \uadf8\ub7f4 \ub54c\ub294 \ub2e4\ub978 \ubb38\uc11c\ub4e4\uacfc \ud568\uaed8 \uc77d\uc73c\uc2dc\uba74\uc11c \uacf5\ubd80\ud558\uc2dc\ub294 \uac78 \ucd94\ucc9c\ud569\ub2c8\ub2e4."},{"id":"/may-woowahan-tech-seminar","metadata":{"permalink":"/en/blog/may-woowahan-tech-seminar","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2022-07-24-may-woowahan-tech-seminar/may-woowahan-tech-seminar.md","source":"@site/blog/2022-07-24-may-woowahan-tech-seminar/may-woowahan-tech-seminar.md","title":"\ud83c\udfcd\ufe0f 5\uc6d4 \uc6b0\uc544\ud55c\ud14c\ud06c\uc138\ubbf8\ub098","description":"\uc6b0\uc544\ud55c\ud615\uc81c\ub4e4 \uac1c\ubc1c\uc790\ub294 \uc5b4\ub5bb\uac8c \uacf5\ubd80\ud558\uace0 \uc131\uc7a5\ud560\uae4c?","date":"2022-07-24T00:00:00.000Z","tags":[{"inline":true,"label":"Insight","permalink":"/en/blog/tags/insight"}],"hasTruncateMarker":false,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83c\udfcd\ufe0f 5\uc6d4 \uc6b0\uc544\ud55c\ud14c\ud06c\uc138\ubbf8\ub098","date":"2022-07-24","authors":"teddygood","tags":["Insight"],"draft":false,"slug":"/may-woowahan-tech-seminar","description":"\uc6b0\uc544\ud55c\ud615\uc81c\ub4e4 \uac1c\ubc1c\uc790\ub294 \uc5b4\ub5bb\uac8c \uacf5\ubd80\ud558\uace0 \uc131\uc7a5\ud560\uae4c?","keywords":["\uc6b0\uc544\ud55c\ud14c\ud06c\uc138\ubbf8\ub098","\uc131\uc7a5","\uacf5\ubd80"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \uba74\uc811\uc744 \uc704\ud55c CS \uc804\uacf5\uc9c0\uc2dd \ub178\ud2b8","permalink":"/en/blog/CS-note-for-interview"},"nextItem":{"title":"\ud83d\udcd6 AWS\ub85c \uc2dc\uc791\ud558\ub294 \uc778\ud504\ub77c \uad6c\ucd95\uc758 \uc815\uc11d","permalink":"/en/blog/building-infrastructure-starting-with-AWS"}},"content":"## Intro\\n\\n### \uc6b0\uc544\ud55cTech\\n\\n\ub098\ub294 \uc6b0\uc544\ud55cTech \ucc44\ub110 \ubc0f \ub2e4\uc591\ud55c \uac1c\ubc1c \ucc44\ub110\ub4e4\uc744 \uad6c\ub3c5\ud558\uace0 \uc788\ub294 \ucef4\ud4e8\ud130\uacf5\ud559 \ud559\ubd80\uc0dd\uc774\ub2e4. \uc6b0\uc544\ud55cTech \ucc44\ub110\uc740 \ub2e4\uc591\ud55c \uac1c\ubc1c \uad00\ub828\ub41c \uc9c0\uc2dd\uc744 \uacf5\ubd80\ud560 \uc218 \uc788\uc73c\uba70, \ud604\uc5c5\uc5d0 \uc77c\ud558\uc2dc\uace0 \uc788\uc73c\uc2e0 \ubd84\ub4e4\uc758 \uacbd\ud5d8\uc744 \uac04\uc811\uc801\uc73c\ub85c \uccb4\ud5d8\ud560 \uc218 \uc788\ub2e4\ub294 \uc810\uc5d0\uc11c \uad6c\ub3c5\ud558\uac8c \ub410\ub2e4. \\n\\n### 5\uc6d4 \ub9ac\ubdf0\\n\\n\uc6b0\uc544\ud55cTech \ucc44\ub110\uc5d0\uc11c\ub294 \ub9e4\ub2ec \uc6b0\uc544\ud55c\ud14c\ud06c\uc138\ubbf8\ub098\uac00 \uc9c4\ud589\ub41c\ub2e4. \uba87 \ub2ec \uc804\uc5d0\ub3c4 [\uc9c0\uc18d\uac00\ub2a5\ud55c SW \uac1c\ubc1c\uc744 \uc704\ud55c \ucf54\ub4dc\ub9ac\ubdf0 :: 4\uc6d4 \uc6b0\uc544\ud55c\ud14c\ud06c\uc138\ubbf8\ub098](https://www.youtube.com/watch?v=ssDMIcPBqUE)\ub97c \uaf64 \uc7ac\ubc0c\uac8c \ubd24\ub2e4. \uc0c1\ub2f9\ud788 \ub3c4\uc6c0\uc774 \ub418\ub294 \ub0b4\uc6a9\uc774 \ub9ce\uc558\ub2e4\uace0 \uc0dd\uac01\ud558\uc9c0\ub9cc, \ub300\ud559\uc0dd\ubcf4\ub2e4\ub294 \uc624\ud788\ub824 \uc8fc\ub2c8\uc5b4 \uac1c\ubc1c\uc790\uc5d0\uac8c \ub354 \uc5b4\uc6b8\ub9ac\ub294 \ub0b4\uc6a9\uc774\uc5c8\uc5b4\uc11c \ub9ac\ubdf0\ub294 \uc0dd\ub7b5\ud588\ub2e4.\\n\\n5\uc6d4 \uc6b0\uc544\ud55c\ud14c\ud06c\uc138\ubbf8\ub098\ub294 **\\"\uacf5\ubd80\ud558\uace0 \uc131\uc7a5\ud558\ub294 \ub178\ud558\uc6b0\\"** \uc5d0 \ub300\ud574 \uc774\uc57c\uae30\ub97c \ud55c\ub2e4. \ub098\ub294 \ud56d\uc0c1 **\u201c\ub0b4\uac00 \ud558\uace0 \uc788\ub294 \uacf5\ubd80\uac00 \ub9de\ub294 \uac74\uac00?\u201d** \ub97c \uc0dd\uac01\ud558\uba70 \uc790\uae30 \uc790\uc2e0\uc744 \uc810\uac80\ud558\ub294 \uc2b5\uad00\uc744 \uac16\uace0 \uc788\uc5b4 \ub9e4\uc6b0 \ud765\ubbf8\uac00 \uac00\ub294 \uc8fc\uc81c\uc600\ub2e4. \ub610\ud55c, \uac1c\ubc1c\uc790\ub97c \ubaa9\ud45c\ub85c \ud558\ub294 \ub300\ud559\uc0dd\uc758 \uc785\uc7a5\uc73c\ub85c\uc11c **\u201c\uc6b0\uc544\ud55c\ud615\uc81c\ub4e4 \uac1c\ubc1c\uc790\ub294 \uc5b4\ub5bb\uac8c \uacf5\ubd80\ud558\uace0 \uc131\uc7a5\ud588\uc744\uae4c?\u201d** \ub77c\ub294 \uad81\uae08\uc99d\uc774 \uc0dd\uaca8 \ub098\uc640 \ube44\uad50\ud560 \uacb8 \uc774 \uc601\uc0c1\uc744 \ub9ac\ubdf0\ud558\uac8c \ub410\ub2e4. \\n\\n## \uc21c\uc11c\\n\\n\uc774 \uc601\uc0c1\uc740 1\ubd80\uc640 2\ubd80\ub85c \ub098\ub204\uc5b4\uc9c4\ub2e4. 1\ubd80\uc5d0\uc11c\ub294 \uc6b0\uc544\ud55c\ud615\uc81c\ub4e4\uc758 DevRel\ud300\uc774 \uc6b0\ud615 \uac1c\ubc1c\uc790\ub4e4\uc758 \uc131\uc7a5\uacfc \uacf5\ubd80\ub97c \uc704\ud574 \uc5b4\ub5a4 \ub178\ub825\uc744 \ud588\uc5c8\ub294\uc9c0 \uc18c\uac1c\ub97c \ud558\uace0, 2\ubd80\uc5d0\uc11c\ub294 \uc6b0\ud615 \uac1c\ubc1c\uc790 \uc138 \ubd84\uacfc \ud568\uaed8 \ud328\ub110 \ud1a0\ud06c\ub97c \uc9c4\ud589\ud55c\ub2e4.\\n\\n## Review\\n\\n### DevRel?\\n\\nDeveloper Relations \uc989, DevRel\ud300\uc740 \uac04\ub2e8\ud788 \ub9d0\ud558\uba74 \uc0ac\ub0b4 \ub0b4\ubd80 \uac1c\ubc1c\uc790\uc758 \uc131\uc7a5\uc744 \uc704\ud55c \uc870\uc9c1\uc774\ub2e4. \ub610\ud55c, \uc774\ub97c \uae30\ubc18\uc73c\ub85c \uae30\uc220 \uc870\uc9c1\uc758 \ube0c\ub79c\ub529 \uac15\ud654\uc5d0\ub3c4 \ubaa9\uc801\uc774 \uc788\ub2e4\uace0 \ud55c\ub2e4. \\n\\n\ucc98\uc74c \uc800 \uc815\uc758\ub97c \uc77d\uc5c8\uc744 \ub54c \u201c\uc5b4\ub5bb\uac8c \ube0c\ub79c\ub529 \uac15\ud654\uc5d0 \ub3c4\uc6c0\uc744 \uc8fc\uc9c0?\u201d\ub77c\ub294 \uc758\ubb38\uc774 \uc788\uc5c8\ub2e4. \uadf8\ub7ec\ub098 \ub2e4\uc2dc \uc0dd\uac01\ud574\ubcf4\ub2c8 \ub9de\ub294 \ub9d0\uc774\uc5c8\ub2e4. \uac1c\ubc1c\uc790\ub97c \uc704\ud574 \ub178\ub825\uc744 \ud558\uace0 \uc788\ub2e4\ub294 \uac83\uc744 \ube14\ub85c\uadf8 \ub610\ub294 \uc720\ud29c\ube0c \uc601\uc0c1\uc73c\ub85c \ubcfc \uc218 \uc788\uc73c\ub2c8 \uc131\uc7a5\ud558\uae30 \uc88b\uc544\ud558\ub294 \uac1c\ubc1c\uc790\ub77c\uba74 \uadf8 \ud68c\uc0ac\uc5d0 \ub300\ud574 \ub9e4\ub825\uc744 \ub290\ub084 \uac83\uc774\ub77c \uc0dd\uac01\uc774 \ub4e4\uc5c8\ub2e4. \\n\\n\uc6b0\uc544\ud55c \ud615\uc81c\ub4e4\uc758 DevRel\ud300\uc774 \uc5b4\ub5a4 \ub178\ub825\uc744 \ud558\uace0 \uc788\ub294\uc9c0 \ub354 \uc790\uc138\ud558\uac8c \uc54c\uace0 \uc2f6\ub2e4\uba74 \uc6b0\uc544\ud55c\ud615\uc81c\ub4e4 \uae30\uc220 \ube14\ub85c\uadf8\uc5d0 [\uc6b0\uc544\ud55c\ud615\uc81c\ub4e4\uc758 Developer Relations](https://techblog.woowahan.com/3799/), [\uc6b0\uc544\ud55c\ud615\uc81c\ub4e4 Developer Relations-Updated](https://techblog.woowahan.com/4091/) \uae00\uc744 \ucc38\uace0\ud558\uc790.\\n\\n\uc0ac\uc2e4 DevRel\ud300\uc744 \ub2e4\uc591\ud55c \uae30\uc5c5\ub4e4\uc758 \ucc44\uc6a9\uacf5\uace0, \uae30\uc220 \ube14\ub85c\uadf8 \ub4f1\uc744 \ubcf4\uba74\uc11c \ucd5c\uadfc\uc5d0 \uc54c\uac8c \ub410\uc73c\ub098, \uc0c1\ub2f9\ud788 \ub9e4\ub825\uc801\uc778 \uc870\uc9c1\uc774\ub77c\uace0 \ub290\uaef4\uc84c\ub2e4. \uc544\ubb34\ub798\ub3c4 \ub0b4\uac00 \ud68c\uc0ac \uc870\uc9c1 \ubb38\ud654, \uc77c \ubb38\ud654 \ub4f1\uc5d0 \uad00\uc2ec\uc774 \ub9ce\uc544 \uac1c\ubc1c\uc790 \uc131\uc7a5\uc744 \uc704\ud574 \ub2e4\uc591\ud55c \ubaa8\uc784\ub4e4\uc744 \uae30\ud68d\ud558\ub294 \ub4f1\uc758 \uc77c\ub3c4 \uc7ac\ubc0c\uc744 \uac83 \uac19\ub2e4\uace0 \uc0dd\uac01\ud588\ub098 \ubcf4\ub2e4. \ub610\ud55c, \uac1c\ubc1c\uc790\ub97c \uc9c0\ub9dd\ud558\uace0 \uc788\uc9c0\ub9cc, \ub0b4 \ub178\ub825\uc744 \ud1b5\ud574 \ub204\uad70\uac00 \uc131\uc7a5\ud558\ub294 \uac78 \ubcf4\ub294 \uac83\ub3c4 \uc0c1\ub2f9\ud788 \uc7ac\ubc0c\ub294 \uc77c\uc774 \ub418\uaca0\ub2e4\uace0 \uc0dd\uac01\ud55c\ub2e4.\\n\\n### \uc6b0\uc544\ud55c\uc2a4\ud130\ub514\\n\\n\uc6b0\uc544\ud55c\ud615\uc81c\ub4e4 \uac1c\ubc1c\uc790\ub4e4\uc774 \ub0b4\ubd80 \uac1c\ubc1c\uc790\ub4e4\ubfd0\ub9cc \uc544\ub2c8\ub77c \ub2e4\ub978 \uc18c\uc18d \ub610\ub294 \ubb34\uc18c\uc18d\uc758 \uac1c\ubc1c\uc790\ub4e4\uacfc\ub3c4 \ud568\uaed8 \uc2a4\ud130\ub514\ub97c \uc9c4\ud589\ud560 \uc218 \uc788\ub3c4\ub85d \ud558\ub294 \ud504\ub85c\uc81d\ud2b8\uc774\ub2e4. \uc791\ub144 12\uc6d4, \ub2e4\uc591\ud55c \uc8fc\uc81c\uc758 \uc2a4\ud130\ub514\ub85c \uc2dc\ubc94 \uc2dc\ud589\ud588\uace0, \uc774\ubc88 5\uc6d4\ubd80\ud130 \ub2e4\uc2dc \uc815\uc2dd \uc6b4\uc601\uc774 \ub410\ub2e4\uace0 \ud55c\ub2e4. \\n\\n\uc778\uc0c1 \uae4a\uc5c8\ub358 \uc810\uc740 \uc6b0\uc544\ud55c\ud615\uc81c\ub4e4 DevRel \ud300\uc5d0\uc11c \uc6b0\uc544\ud55c\ud615\uc81c\ub4e4 \uac1c\ubc1c\uc790\uc758 \uc131\uc7a5\uc744 \uc704\ud574 \ub2e4\ub978 \uc18c\uc18d \ub610\ub294 \ub300\ud559\uc0dd, \ucde8\uc900\uc0dd \uac19\uc740 \ubb34\uc18c\uc18d\uc778 \uc0ac\ub78c\ub4e4\uacfc \uc5f0\uacb0\ud574\uc8fc\uc2e0\ub2e4\ub294 \uac83\uc774\uc5c8\ub2e4. \uc774\uac74 \uc6b0\ud615 \uac1c\ubc1c\uc790\ub4e4\ub3c4 \uc774 \ud504\ub85c\uadf8\ub7a8\uc5d0 \ucc38\uc5ec\ud558\uc2dc\ub294 \ubd84\ub4e4\ub3c4 \ubaa8\ub450 \ub3c4\uc6c0\uc774 \ub420 \uac83 \uac19\ub2e4. \\n\\n\uc778\ub9e5\uc774 \uc5c6\uc73c\uc2e0 \ubd84\ub4e4\uaed8\ub294 \uc5f4\uc815\uc801\uc778 \uc2a4\ud130\ub514\ub97c \uacbd\ud5d8\ud574\ubcfc \uc218 \uc788\ub294 \uc0c1\ub2f9\ud788 \uc88b\uc740 \uae30\ud68c\uac00 \ub410\uc744 \uac83\uc774\ub2e4. \ub610\ud55c, \ub0b4\ubd80 \uac1c\ubc1c\uc790\ub4e4\uc5d0\uac8c\ub294 \ub2e4\uc591\ud55c \uacac\ud574, \uc9c0\uc2dd\uc744 \uac00\uc9c4 \uc0ac\ub78c\ub4e4\uc774 \uacf5\ubd80\ub97c \ubaa9\uc801\uc73c\ub85c \uc2a4\ud130\ub514\ub97c \ud558\ub294 \uac83\uc774\ub2c8\uae4c \uc6b4\uc601\ub9cc \uc798\ub41c\ub2e4\uba74 \uc0ac\ub0b4 \uac1c\ubc1c\uc790\uc758 \uc785\uc7a5\uc5d0\uc11c\ub294 \uc0c8\ub85c\uc6b4 \uc778\uc0ac\uc774\ud2b8\ub97c \uc5bb\uc744 \uc218\ub3c4 \uc788\uae30\uc5d0 \uc0c1\ub2f9\ud788 \ub3c4\uc6c0\uc774 \ub420 \uac83\uc774\ub77c \uc0dd\uac01\ud55c\ub2e4.\\n\\n\uc5b8\uc81c\ub098 \ub290\ub07c\ub294 \uac83\uc774\uc9c0\ub9cc \uc2a4\ud130\ub514\ub294 \ub0b4\uac00 \uc6d0\ud558\ub294 \uc8fc\uc81c, \uc2a4\ud130\ub514 \ucc38\uc5ec\uc790\uc758 \uacf5\ubd80 \uc218\uc900, \uc5f4\uc815\uc801\uc778 \uc2a4\ud130\ub514 \ucc38\uc5ec\uc790 \ub4f1 \uace0\ub824\ud574\uc57c \ud560 \uc870\uac74\uc774 \ub9ce\uc544 \ucc98\uc74c \uad6c\uc131\ud560 \ub54c\uac00 \uac00\uc7a5 \ud798\ub4e0 \uac83 \uac19\ub2e4. \uadf8\ub807\uae30\uc5d0 \uacf5\ubd80\ud560 \ubaa9\uc801\ub9cc \uc788\ub2e4\uba74 \uc774\ub807\uac8c \uae30\uc5c5\uc5d0\uc11c \uc9c1\uc811 \uc5f0\uacb0\ud574\uc8fc\ub294 \uc2a4\ud130\ub514\ub97c \ucc3e\uc544 \ub4e4\uc5b4\uac00\ub294 \uac83\ub3c4 \uc88b\uc740 \ubc29\ubc95\uc778 \uac83 \uac19\ub2e4. \ub610\ud55c, \ub300\ud559\uc0dd\uc73c\ub85c\uc11c\ub294 \ud604\uc5c5\uc5d0 \uacc4\uc2e0 \ubd84\ub4e4\uacfc \uc2a4\ud130\ub514\ud574\ubcf4\ub294 \uacbd\ud5d8\uc740 \ud754\uce58 \uc54a\uc73c\ub2c8 \ub3c4\uc804\ud574\ubcf4\ub294 \uac83\ub3c4 \uc88b\ub2e4\uace0 \uc0dd\uac01\ud55c\ub2e4.\\n\\n\ub098\ub3c4 \uc6b0\uc544\ud55c\ud615\uc81c\ub4e4\uc758 DevRel\ud300\uc5d0\uc11c \ubcf4\ub0b4\uc8fc\uc2e0 \uba54\uc77c\uc744 \ubc1b\uace0 \u201c\uc6b0\uc544\ud55c\uc2a4\ud130\ub514\uc5d0 \uc9c0\uc6d0\ud574\ubcfc\uae4c?\u201d \uace0\ubbfc\ud558\uae30\ub3c4 \ud588\uc9c0\ub9cc, \ud604\uc7ac \ud558\uace0 \uc788\ub294 \uac83\ub9cc \ud574\ub3c4 \ub108\ubb34 \ub9ce\uc544 \uc2e0\uccad\ud558\uc9c0 \uc54a\uc558\ub2e4. \ub2e4\uc74c\uc5d0 \uc5ec\uc720\ub85c\uc6b8 \ub54c \uc774\ub7f0 \uae30\ud68c\uac00 \ub610 \uc788\ub2e4\uba74 \ud55c \ubc88 \uc2e0\uccad\ud574\ubd10\uc57c\uc9c0.\\n\\n### \uc6b0\uc544\ud55c\uba58\ud1a0\ub9c1\uacfc \ud14c\ud06c\uc0b4\ub871\\n\\n\uac1c\ubc1c\uc790\ub4e4\uc774 \ub124\ud2b8\uc6cc\ud0b9\ud558\uae30 \ud3b8\ud558\ub3c4\ub85d \uc6b0\uc544\ud55c\ud615\uc81c\ub4e4\uc5d0\uc11c \ub9cc\ub4e0 \uacf5\uac04\uc774\ub2e4. \uc544\ubb34\ub798\ub3c4 \uc6b0\uc544\ud55c\ud615\uc81c\ub4e4\uc740 \ub0b4\ubd80\uc801\uc73c\ub85c \ud558\ub294 \ud504\ub85c\uadf8\ub7a8\uc774 \ub9ce\uc544 \ucc38\uc5ec\uc790\ubd84\ub4e4\uacfc \ub124\ud2b8\uc6cc\ud0b9\uc744 \ub354 \uc27d\uac8c \ud560 \uc218 \uc788\ub294 \uacf5\uac04\uc744 \ub530\ub85c \ub9cc\ub4e0 \uac83 \uac19\ub2e4. \ud604\uc7ac\ub294 \uc6b0\ud615\uc5d0\uc11c \uc9c4\ud589\uc911\uc778 \ud504\ub85c\uadf8\ub7a8\uc5d0 \ucc38\uc5ec\ud558\uc2dc\ub294 \ubd84\ub4e4\ub9cc \ucd9c\uc785\uc774 \uac00\ub2a5\ud558\ub2e4\uace0 \ud55c\ub2e4. \ub098\uc911\uc5d0\ub294 \uc6b0\ud615 \uc18c\uc18d\uc774 \uc544\ub2c8\ub354\ub77c\ub3c4 \ubaa8\uc5ec\uc11c \ub124\ud2b8\uc6cc\ud0b9\uc744 \ud560 \uc218 \uc788\ub294 \uc790\ub9ac\ub85c \ubc1c\uc804\ud560 \uc218 \uc788\uc9c0 \uc54a\uc744\uae4c.\\n\\n\uc601\uc0c1\uc5d0\uc11c \uc138\ubbf8\ub098\ub97c \uc9c4\ud589\ud558\uace0 \uc788\ub294 \uc7a5\uc18c\uac00 \ubc14\ub85c \ud14c\ud06c\uc0b4\ub871\uc774\ub77c\uace0 \ud55c\ub2e4. \uc5c4\uccad \uc88b\uc740 \ucef4\ud4e8\ud130\uac00 \uc9c0\uae09\ub418\ub294 \uac83\ub3c4 \uc88b\uc740 \ubcf5\uc9c0 \uc911 \ud558\ub098\uc9c0\ub9cc, \uc774\ub7f0 \uacf5\uac04\uacfc \uac74\ubb3c\uc744 \uc0ac\uc6a9\ud560 \uc218 \uc788\ub2e4\ub294 \uac83\ub3c4 \uc88b\uc740 \ubcf5\uc9c0\uac00 \uc544\ub2d0\uae4c \uc2f6\ub2e4. \ud14c\ud06c\uc0b4\ub871\uc5d0 \ub300\ud55c \uc790\uc138\ud55c \uc18c\uac1c\ub294 [<open>\ud14c\ud06c\uc0b4\ub871</open>](https://story.baemin.com/3168/)\ub97c \ucc38\uace0\ud558\uc790.\\n\\n\uc6b0\uc544\ud55c\uba58\ud1a0\ub9c1\uc5d0 \ub300\ud574\uc11c\ub3c4 \uc774\uc804\uc5d0 \uba54\uc77c\uc744 \ubc1b\uc740 \uc801\uc774 \uc788\uc5c8\ub2e4. \uba54\uc77c\uc5d0\ub294 \uc7ac\uc9c1 \uc911\uc778 \uac1c\ubc1c\uc790\ub97c \ub300\uc0c1\uc73c\ub85c \uac1c\ubc1c\uc790\ub85c\uc11c \uacaa\ub294 \uace0\ubbfc\uc744 \ud568\uaed8 \uacf5\uc720\ud558\ub294 \ud504\ub85c\uadf8\ub7a8\uc774\ub77c\uace0 \uc18c\uac1c\ub418\uc5b4 \uc788\uc5c8\ub294\ub370 \uc774\ubc88 \uc601\uc0c1\uc744 \ubcf4\uace0 \u201c\uac1c\ubc1c\uc790\uac00 \uc544\ub2c8\uc5c8\uc5b4\ub3c4 \ub410\ub358 \uac74\uac00?\u201d\ub77c\ub294 \uc758\ubb38\uc774 \ub4e4\uc5c8\ub2e4. \uac1c\ubc1c \ubfd0\ub9cc \uc544\ub2c8\ub77c \uc2a4\ud0c0\ud2b8\uc5c5 CEO \ub4f1 \uc5ec\ub7ec \ub3c4\uba54\uc778\uc5d0 \uacbd\ud5d8\uc774 \uc788\uc73c\uc2e0 \ubd84\ub4e4 \ub610\ub294 \ub2e4\uc591\ud55c \uc9c1\uad70\uc5d0\uc11c \uc77c\ud558\uc2dc\ub294 \ubd84\ub4e4\uc744 \ucd08\uccad\ud574\uc11c \uc57d 8\uc8fc \ub3d9\uc548 \uc6b0\uc544\ud55c\ud615\uc81c\ub4e4\uc5d0 \uad81\uae08\ud55c \uc0ac\ud56d\ub4e4\uc744 \uba58\ud1a0\ub9c1 \ud558\uc168\ub2e4\uace0 \ud55c\ub2e4. \uc544\ub9c8 \ub0b4\uac00 \ubc1b\uc740 \uba54\uc77c\ubcf4\ub2e4 \ub354 \ub9ce\uc740 \ud504\ub85c\uadf8\ub7a8\uc774 \uc9c4\ud589\ub41c\ub4ef \uc2f6\ub2e4.\\n\\n\uc774 \ud504\ub85c\uadf8\ub7a8\ub3c4 \u201c\uc2e0\uccad\ud574\ubcfc\uae4c?\u201d\ub77c\ub294 \uc0dd\uac01\uc744 \ud588\uc5c8\uc73c\ub098, \uc7ac\uc9c1 \uc911\uc778 \uac1c\ubc1c\uc790 \ub300\uc0c1\uc774\uc5c8\uace0 \uc624\ud504\ub77c\uc778 \ubaa8\uc784\uc774\uc5c8\uae30\uc5d0 \uc2e0\uccad\ud558\uc9c0\ub3c4 \ubabb\ud588\uc5c8\ub2e4. \uc544\uc27d\uc9c0\ub9cc \ub098\uc911\uc5d0 \uac1c\ubc1c\uc790\ub85c \ucde8\uc9c1 \uc131\uacf5\ud558\uc5ec \ub2e4\uc2dc \uc11c\uc6b8\ub85c \uc62c\ub77c\uac04\ub2e4\uba74 \uc774\ub7f0 \ud504\ub85c\uadf8\ub7a8\uc5d0 \ud55c \ubc88\ucbe4 \ucc38\uc5ec\ud574\ubcf4\uace0 \uc2f6\ub2e4. \uba58\ud1a0\ub9c1 \uac19\uc740 \uacbd\uc6b0\uc5d0\ub3c4 \ub2e4\uc74c\uc5d0\ub294 \ucde8\uc900\uc0dd \ub610\ub294 \ub300\ud559\uc0dd\ub4e4\uc744 \ubd88\ub7ec \uba58\ud1a0\ub9c1\uc744 \ud558\ub294 \uae30\ub300\ub3c4 \ud574\ubcfc \uc218 \uc788\uc744 \uac83 \uac19\ub2e4.\\n\\n### \uc2a4\ud130\ub514 \ud328\ub110 \ud1a0\ud06c\\n\\n\uc2a4\ud130\ub514\uc640 \uad00\ub828\ub41c \ud1a0\ud06c\uc5d0\uc11c\ub294 \uc6b0\uc544\ud55c\uc2a4\ud130\ub514\uc5d0\uc11c Rust\ub77c\ub294 \uc5b8\uc5b4\ub97c \ud559\uc2b5\ud558\uace0 \ud504\ub85c\uc81d\ud2b8\ub97c \ud574\ubcf4\ub294 \uc2a4\ud130\ub514\ub97c \uc9c4\ud589\ud558\uc2e0 \ub9ac\ub354\uc168\ub358 \ud0dc\uc6d0\ub2d8\uacfc \uc9c4\ud589\ub418\uc5c8\ub2e4. \ub2e4\uc591\ud55c \ubd84\uc57c, \uc774\ud574\ub3c4\ub97c \uac00\uc9c4 \ubd84\ub4e4\uc774 \uc624\uc154\uc11c \uba3c\uc800 \uacf5\ubd80\ud558\uace0 \uc774\ud6c4 \ud1a0\ub860\uc744 \uc9c4\ud589\ud558\ub294 \uac83\uc774 \uc774\ubc88 \uc2a4\ud130\ub514 \ubaa9\ud45c\uc600\ub2e4\uace0 \ud55c\ub2e4.\\n\\n\uc9c0\uae08\uae4c\uc9c0 \uc5ec\ub7ec \uc2a4\ud130\ub514\uc5d0 \ucc38\uc5ec\ud574\ubcf4\uae30\ub3c4 \ud558\uace0 \uc6b4\uc601\ud574\ubcf4\uae30\ub3c4 \ud588\uc9c0\ub9cc, \uc815\ub9d0 \uc774\uc0c1\uc801\uc778 \uc2a4\ud130\ub514\ub77c \uc0dd\uac01\ud55c\ub2e4. \uc800\ub7f0 \uc2a4\ud130\ub514\ub97c \uc6b4\uc601\ud558\ub824\uba74 \uc778\uc6d0\uc744 \uc815\ub9d0 \uc798 \ubf51\uc544\uc57c \ud560 \uac83 \uac19\uc558\ub2e4.\\n\\n\uc774\ub7f0 \uc2a4\ud130\ub514\ub97c \uc9c4\ud589\ud560 \ub54c\ub294 \ub354 \ub9ce\uc774 \uc774\ud574\ud558\uace0, \uc54c\uace0 \uc788\uc73c\uc2e0 \ubd84\ub4e4\uc740 \uc798 \uc774\ub04c\uc5b4\uc57c \ud558\uace0, \ubaa8\ub974\uc2dc\ub294 \ubd84\ub4e4\uc740 \uc9c8\ubb38\ud558\ub294\ub370 \uac81\uba39\uc9c0 \uc54a\ub294 \uac83\uc774 \uac00\uc7a5 \uc911\uc694\ud55c \uac83 \uac19\ub2e4.\\n\\n\ub098\ub3c4 \ub300\ub7b5 8\uac1c\uc6d4 \uc815\ub3c4 \uc2a4\ud130\ub514\ub97c \ud588\ub358 \uacbd\ud5d8\uc774 \uc788\ub2e4. \uadf8\ub807\uae30\uc5d0 \uc2a4\ud130\ub514\ub97c \uc624\ub798 \ud558\uae30 \uc704\ud574\uc11c\ub294 \uae30\uc220\uc801\uc778 \uacf5\ubd80\uc5d0\ub9cc \ubaa9\uc801\uc744 \ub450\ub294 \uac83\uc774 \uc544\ub2c8\ub77c, \uc778\uac04\uc801\uc778 \uba74\ub3c4 \uace0\ub824\ud574\uc57c \ud55c\ub2e4\ub294 \uac83\uc5d0 \ub9e4\uc6b0 \uacf5\uac10\uc774 \ub410\ub2e4. \uc628\ub77c\uc778\uc73c\ub85c \uc9c4\ud589\ub41c \uc2a4\ud130\ub514\uc5ec\uc11c \uac19\uc774 \ubc25\uc744 \uba39\uac70\ub098 \uac10\uc815\uc801\uc73c\ub85c \uc7ac\ubc0c\ub294 \ud65c\ub3d9\uc744 \ub9ce\uc774 \ud558\uc9c0\ub294 \uc54a\uc558\uc9c0\ub9cc, \uc2a4\ud130\ub514\ub97c \uc624\ub798 \ud558\uae30 \uc704\ud574\uc11c\ub294 \uadf8\ub7f0 \ubd80\ubd84\ub3c4 \uace0\ub824\ud558\ub294 \uac83\uc774 \uc88b\uc740 \uac83 \uac19\ub2e4.\\n\\n\uc784\uc2dc\uc801\uc778 \uc870\uc9c1\uc774\ub354\ub77c\ub3c4 \ubb34\uac10\uc815\uc73c\ub85c \uc784\ud558\uae30 \ubcf4\ub2e4\ub294 \uc88b\uc740 \uac10\uc815\uc744 \uac16\uace0 \uc2a4\ud130\ub514\uc5d0 \ucc38\uc5ec\ud558\ub294 \uac83\uc774 \ub354 \ub3c4\uc6c0\uc774 \ub41c\ub2e4. \ub610\ud55c, \uc5b4\ub5a0\ud55c \uc0ac\uc815\uc73c\ub85c \uc778\ud574 \uc2a4\ud130\ub514\uc5d0 \ucc38\uc5ec\ud558\uc9c0 \ubabb\ud560 \uc218\ub3c4 \uc788\uae30\uc5d0 \uc11c\ub85c \uc2dc\uac04 \uc870\uc815\uc744 \uc798\ud574\uc57c \ud55c\ub2e4\ub294 \uac83\uc774 \uc911\uc694\ud558\ub2e4. \\n\\n### \ub098 \ud63c\uc790 \uc131\uc7a5\ud558\ub294 \uac83\uacfc \uac19\uc774 \uc131\uc7a5\ud558\ub294 \uac83 \uc911\uc5d0 \uc5b4\ub5a4 \uac83\uc774 \ubcf8\uc778\uc774 \uc131\uc7a5\ud558\ub294\ub370 \ub354 \ub3c4\uc6c0\uc774 \ub420\uae4c?\\n\\n> \uac19\uc774 \uc131\uc7a5\ud558\ub294 \uac83\uc774 \ub354 \ub3c4\uc6c0\uc774 \ub41c\ub2e4. \ub098\ub220\uc8fc\ub294 \uac83\uc744 \uc544\uae4c\uc6cc \ud560 \ud544\uc694\uac00 \uc5c6\ub2e4. \uc6b0\ub9ac\uac00 \uacf5\ubd80\ud558\uace0 \uc788\ub294 \uac83\uc774 \uc778\ub958\uc5d0 \uc601\ud5a5\uc744 \ub07c\uce60\uc815\ub3c4\ub85c \uc5c4\uccad\ub09c \uac83\uc774 \uc544\ub2c8\ub2e4. \uacf5\uc720\ud558\uace0 \uc54c\ub824\uc8fc\ub824\uace0 \ud560 \ub54c \ub0b4 \uc548\uc758 \uc9c0\uc2dd\ub4e4\uc774 \ub354 \ub2e8\ub2e8\ud574\uc9c4\ub2e4.\\n\\n\uc800 \ub300\ub2f5\uc5d0 \ub3d9\uc758\ub97c \ubabb \ud558\ub294 \uc0ac\ub78c\ub3c4 \uc788\uc744 \uc218\ub294 \uc788\ub2e4. \ub098\ub3c4 \uc608\uc804\uc5d0 \ub3d9\uc758\ub97c \ubabb \ud588\ub358 \uc0ac\ub78c\uc774\uc5c8\ub2e4. **\\"\uad73\uc774 \ub098\ub20c \ud544\uc694 \uc788\ub098. \ub098\ub3c4 \ubc1b\uc740 \uac83\uc774 \uc5c6\ub294\ub370.\\"** \ub77c\uace0 \uc0dd\uac01\ud588\uc5c8\ub2e4. \uadf8\ub7ec\ub098 \ub2e4\uc2dc \uc798 \uc0dd\uac01\ud574\ubcf4\uc790. \uc9c0\uae08 \uac1c\ubc1c\ud558\uba70 \ubaa8\ub974\ub294 \uc815\ubcf4\uac00 \uc788\uc744 \ub54c, \uad6c\uae00\ub9c1\uc744 \ud574\uc11c \ub0b4\uac00 \uc6d0\ud558\ub294 \uc9c0\uc2dd\uc744 \ucc3e\uc744 \uc218 \uc788\ub294 \uac83\ub3c4 \ub204\uad70\uac00 \ubb38\uc11c\ub85c \uc791\uc131\ud574\uc11c \uacf5\uc720\ud588\uae30 \ub54c\ubb38\uc774\ub2e4. \uc5b8\uc5b4\ub098 \ud504\ub808\uc784\uc6cc\ud06c \ub4f1\uc744 \uc0ac\uc6a9\ud560 \uc218 \uc788\ub294 \uac83\ub3c4 \ub204\uad70\uac00 \uc0ac\uc6a9\ud558\ub77c\uace0 \uacf5\uac1c\ud588\uae30 \ub54c\ubb38\uc774\ub2e4. \uc774\uac8c \ub2e4 \ubc1b\uc740 \uac83\uc774 \uc544\ub2c8\uba74 \ubb34\uc5c7\uc77c\uae4c.\\n\\n\ub610\ud55c, \ub204\uad70\uac00\ub97c \uac00\ub974\uccd0 \ubcf8 \uc0ac\ub78c\uc740 \uc54c\uaca0\uc9c0\ub9cc, \ub0b4 \uc9c0\uc2dd\uc744 \uacf5\uc720\ud558\uace0 \uc54c\ub824\uc8fc\ub824\uace0 \ud560 \ub54c \ub0b4\uac00 \uc54c\uace0 \uc788\ub294 \uc9c0\uc2dd\uc744 \uc810\uac80\ud558\uac8c \ub418\uace0 \ub2e4\uc2dc \ubcf5\uc2b5\ud560 \uc218 \uc788\uac8c \ub41c\ub2e4. \ubc1c\ud45c\ub098 \ub9d0\ub85c \uc54c\ub824\uc8fc\uac8c \ub418\uba74 \uc124\uba85\ud558\uace0 \ub9d0\ud558\ub294 \ub2a5\ub825\ub3c4 \ud0a4\uc6b8 \uc218 \uc788\uc73c\uba70 \ubb38\uc11c\ud654\ub97c \ud558\uac8c \ub418\uba74 \uae00\uc4f0\uae30 \ub2a5\ub825\uc744 \uae30\ub97c \uc218\uac00 \uc788\ub2e4. \uc5bc\ub9c8\ub098 \uc7a5\uc810\uc774 \ub9ce\uc740\uac00. \uadf8\ub807\uae30\uc5d0 \ub098\ub3c4 \uc694\ucc3d\ub2d8\uc758 \ud558\uc2e0 \uc800 \ub9d0\uc5d0 \ub3d9\uc758\ud55c\ub2e4.\\n\\n### \uacc4\uc18d\ud574\uc11c \uc131\uc7a5\ud558\uace0 \uacf5\ubd80\ub97c \ud558\ub294 \uc774\uc720\uac00 \ubb34\uc5c7\uc77c\uae4c?\\n\\n> \uacc4\uc18d\ud574\uc11c \ub098\uc758 \ucee4\ub9ac\uc5b4\ub97c \ubc1c\uc804\uc2dc\ud0a4\uc9c0 \uc54a\uc73c\uba74 \ub3c4\ud0dc\ub418\uae30 \uc26c\uc6b0\ub2c8\uae4c \uacc4\uc18d \uc5f4\uc2ec\ud788 \uacf5\ubd80\ud558\uace0 \uc131\uc7a5\ud574\uc57c \ud55c\ub2e4. \uae30\uc220\uc740 \uad49\uc7a5\ud788 \ube60\ub974\uac8c \ubc1c\uc804\ud558\uae30\uc5d0 2~3\ub144\uc5d0 \ud55c\ubc88\uc529 \uc2dc\uc7a5\uc5d0\uc11c \ub098\ub97c \ucc3e\ub294\uac00 \ud655\uc778\ubc1b\ub294 \uac83\uc774 \uad49\uc7a5\ud788 \uc911\uc694\ud558\ub2e4. \uc2dc\uc7a5\uc758 \ud750\ub984\uc5d0 \ub530\ub77c\uc11c \ub098\ub3c4 \uadf8\ub9cc\ud07c \uacf5\ubd80\ud558\uace0 \uacc4\uc18d \uc900\ube44\ud558\uace0 \uc788\uc5b4\uc57c \ud55c\ub2e4.\\n\\n`\uc774\uc9c1`\uc774\ub77c\ub294 \uac83\uc744 \uc2dc\uc7a5\uc5d0\uc11c 2~3\ub144\uc5d0 \ud55c \ubc88\uc529 \ub098\ub97c \ucc3e\ub294\uac00 \ud655\uc778\ubc1b\ub294 \uac83\uc774\ub77c\uace0 \ud45c\ud604\ud558\uc2e0 \ubd80\ubd84\uc774 \ub9e4\uc6b0 \uc778\uc0c1 \uae4a\uc5c8\ub2e4. \ub0b4\uac00 \uac00\uc9c4 \uae30\uc220\ub85c \ub354 \ub9ce\uc740 \uc218\uc785\uc744 \uc5bb\uc744 \uc218 \uc788\ub294\uac00, \ub0b4\uac00 \uc774 \uc2dc\uc7a5\uc5d0\uc11c \uc218\uc694\uac00 \uc788\ub294 \uac83\uc778\uac00. \ub09c \uc544\uc9c1 \ub300\ud559\uc0dd\uc774\uc9c0\ub9cc, \uc800 \uc0dd\uac01\ub3c4 \ub108\ubb34 \uc774\ud574\ub418\uace0 \ub098\ub3c4 \ub098\uc911\uc5d0 \uc5f0\ucc28\uac00 \ub298\uc5b4\ub0a0\uc218\ub85d \uc800\ub7f0 \uc0dd\uac01\uc744 \ud558\uac8c \ub418\ub294 \uac1c\ubc1c\uc790\ub85c \uc131\uc7a5\ud588\uc73c\uba74 \uc88b\uaca0\ub2e4. \\n\\n### \uac1c\ubc1c\uc790\uc5d0\uac8c\ub3c4 \uae00\uc4f0\uae30\uac00 \uc911\uc694\ud55c \uc774\uc720\ub294?\\n\\n> \ub0b4 \uc0dd\uac01\uc744 \uc815\ub9ac\ud560 \uc218 \uc788\ub294 \uc2dc\uac04\uc744 \uac00\uc9c8 \uc218 \uc788\ub2e4. \ub17c\ub9ac\uc801\uc73c\ub85c \ub0a8\uc744 \uc774\ud574\uc2dc\ud0ac \uc218 \uc788\ub294 \uae00\uc744 \uc4f8 \uc218 \uc788\ub2e4. \uac1c\ubc1c\uc790\ub9cc \uc77d\uc744 \uc218 \uc788\ub294 \uae00\uc774 \uc544\ub2c8\ub77c \uac1c\ubc1c \uc9c1\uad70\uc774 \uc544\ub2cc \uc0ac\ub78c\ub3c4 \uc774\ud574\ud560 \uc218 \uc788\ub294 \uae00\uc778\uac00 \uc0dd\uac01\ud574\ubcf4\uc790. \uc544\ubb34\uac83\ub3c4 \ubaa8\ub974\ub294 \uc0ac\ub78c\uc774 \ud558\ub098\ub77c\ub3c4 \uc774\ud574\ud560 \uc218 \uc788\ub294 \ubd80\ubd84\uc774 \uc788\uc5b4\uc57c \ub098\ub791 \uac19\uc774 \uc77c\ud558\ub294 \uc0ac\ub78c\ub4e4\ub3c4 \uc774\ud574\uc2dc\ud0ac \uc218 \uc788\ub2e4.\\n\\n> \uc7a5\ubb38\uc758 \uae00\uc744 \uc4f0\ub824\uace0 \ud558\uc9c0 \ub9d0\uace0 \uc624\ub298 \ubc30\uc6b4 \uac83\uc744 \ub0b4 \ubb38\uccb4\ub85c \ud55c \ubb38\uc7a5 \ub610\ub294 \ub450 \ubb38\uc7a5 \uc815\ub3c4\ub85c \uc9e7\uac8c \uc815\ub9ac\ud574\ubcf4\uc790. \uc774\uac8c \ubaa8\uc5ec\uc11c \ud558\ub098\ub97c \ub9cc\ub4dc\ub294 \uac83\uc774\ub2e4.\\n\\n\uc694\ucc3d\ub2d8\uc774 \uc704\uc758 \ub9d0\uc744 \ud558\uc2dc\uba74\uc11c \uc81c\ud154\uce74\uc2a4\ud150 \uc598\uae30\ub97c \uaebc\ub0b4\uc168\ub2e4. \ub098\ub3c4 \ucd5c\uadfc \ub4e4\uc5b4 \uc81c\ud154\uce74\uc2a4\ud150 \ucc45\uc744 \uc77d\uace0 \uc870\uae08\uc529 \ud574\ubcf4\ub824\uace0 \ub178\ub825\ud558\ub294 \uc911\uc774\ub77c \uc870\uae08 \uc2e0\uae30\ud588\ub358 \ubd80\ubd84\uc774\ub2e4. \\n\\n\ub610, \uc601\uc0c1\uc5d0\uc11c \uad11\ud718\ub2d8\uc774 TIL(Today I Learned) \uc598\uae30\ub97c \ud558\uc168\ub294\ub370 \ud655\uc2e4\ud788 TIL\uc744 \uc791\uc131\ud558\ub294 \uac83\ub3c4 \uac1c\ubc1c\uc744 \uacf5\ubd80\ud558\uace0 \uc815\ub9ac\ud558\ub294\ub370 \uaf64 \ub9ce\uc740 \ub3c4\uc6c0\uc774 \ub41c\ub2e4. \ubb38\uc81c\ub294 \uc774\uac78 \ub9e4\uc77c \ud558\ub294 \uac83\uc774 \ub108\ubb34 \ud798\ub4e4\ub2e4\ub294 \uac83. \uadf8\ub7ec\ub098 \uafb8\uc900\ud788 \ud558\uba74 \ud655\uc2e4\ud788 \ub3c4\uc6c0\uc774 \ub41c\ub2e4.\\n\\n\ube14\ub85c\uadf8\uc5d0 \uae00 \uc4f0\ub294 \uac83\ub3c4 \ub3c4\uc6c0\uc774 \ub41c\ub2e4. \uac1c\ubc1c \uc678\uc801\uc73c\ub85c \ub9ce\uc740 \ub3c4\uc6c0\uc774 \ub418\uace0 \uac1c\ubc1c\uc801\uc73c\ub85c\ub3c4 \uacf5\ubd80\ud558\uace0 \uae00\uc744 \uc4f0\ub358 \uc2b5\uad00\uc774 \uc788\uc73c\ub2c8 \uac1c\ubc1c \uacf5\ubd80\ub97c \ud574\uc57c \ud560 \ub54c \ube60\ub974\uac8c \uc815\ub9ac\ud558\uba74\uc11c \uc9c0\uc2dd\uc744 \uc2b5\ub4dd\ud560 \uc218 \uc788\ub2e4.\\n\\n\uc9c4\uc2ec\uc73c\ub85c \ub3d9\uc758\ud55c\ub2e4. \uc218\ub9ce\uc740 \uc9c0\uc2dd\ub4e4 \uc18d\uc5d0\uc11c \uc790\uc2e0\uc774 \uc911\uc694\ud55c \uc815\ubcf4\ub97c \ubf51\uc544\ub0b4\uc5b4 \ub098\ub9cc\uc758 \uae00\ub85c \ube60\ub974\uac8c \uc791\uc131\ud560 \uc218 \uc788\ub2e4\uba74 \uacf5\ubd80\ud558\ub294 \ub370 \ub9ce\uc740 \ub3c4\uc6c0\uc774 \ub41c\ub2e4.\\n\\n\uad11\ud718\ub2d8\ub3c4 \ub9e4\ubc88 \uae00\uc4f0\uae30\ub97c \uc9c4\ud589\ud558\uc168\uc73c\uba70 2\uc8fc\uc5d0 \ud55c \ubc88\uc529\uc740 \uae00\uc4f0\uae30\ub97c \ud558\uc790\uace0 \uc0dd\uac01\ud588\ub294\ub370 \ubabb \uc9c0\ud0a4\uc168\ub2e4\uace0 \ud55c\ub2e4. \uadf8\ub798\uc11c \uc120\ud0dd\ud558\uc2e0 \ubc29\ubc95\uc774 \\"\uae00\ub610\\"\ub77c\ub294 \ubaa8\uc784\uc5d0 \ub4e4\uc5b4\uac00\ub294 \uac83\uc774\uc5c8\ub2e4. \uc989, \uac15\uc81c\uc131\uc744 \ubd80\uc5ec\ud558\uc5ec \uacf5\ubd80\ud558\ub294 \ubc29\ubc95\uc774\ub2e4. \uadf8\ub7ec\ub098 \uac15\uc81c\uc131\uc744 \ubd80\uc5ec\ud558\ub354\ub77c\ub3c4 \ubc8c\uae08\uc774 \uc544\ub2c8\ub77c \ubc8c\uce59\uc73c\ub85c \uac15\uc81c\uc131\uc744 \ubd80\uc5ec\ud574\ubcf4\uc790. \ub3c8\uc740 \ub0b4\uba74 \uadf8\ub9cc\uc774\ub2e4.\\n\\n\uc774 \ubc29\ubc95\ub3c4 \uacf5\ubd80\uac00 \ub108\ubb34 \ud558\uae30 \uc2eb\uc744 \ub54c \ub3c4\uc6c0\uc774 \ub418\uba70, \ub098\ub3c4 \uac00\ub054 \uc0ac\uc6a9\ud558\ub294 \ubc29\ubc95\uc774\ub2e4. \ub09c \uacf5\ubd80\ub97c \ub108\ubb34 \ud558\uae30 \uc2eb\uc744 \ub54c \uc2a4\ud130\ub514\uc5d0 \ub4e4\uc5b4\uac00\uc11c \ubc1c\ud45c\ub97c \ud558\uae30 \uc704\ud55c \uacf5\ubd80\ub97c \ud55c\ub2e4. \ubc1c\ud45c\ub97c \ud558\uae30 \uc704\ud574\uc11c\ub294 \ub2e4\uc591\ud55c \uc790\ub8cc\ub97c \uc900\ube44\ud574\uc57c \ud558\uace0, \ub9d0\uc744 \uadf8\ub9cc\ud07c \uc775\uc219\ud558\uac8c \ud558\uae30 \uc704\ud574 \uc124\uba85\ud558\uace0\uc790 \ud558\ub294 \uac1c\ub150\uc744 \uac70\uc758 \uc644\ubcbd\ud558\uac8c \uc54c\uace0 \uc788\uc5b4\uc57c \ud55c\ub2e4. \ub9ce\uc740 \uc2dc\uac04\uc774 \uc18c\ubaa8\ub41c\ub2e4\ub294 \ub2e8\uc810\uc774 \uc788\uc9c0\ub9cc, \uadf8\ub798\ub3c4 \uacf5\ubd80 \ud558\ub098\ub9cc\ud07c\uc740 \ud655\uc2e4\ud558\uac8c \ub41c\ub2e4. \\n\\n\ud604\uc5c5\uc5d0\uc11c \uc77c\ud558\uc2dc\ub294 \ubd84\ub4e4 \uc774\uc57c\uae30\ub97c \ub4e4\uc5b4\ubcf4\uba74 \ucf54\ub4dc\ub97c \uc791\uc131\ud558\ub294 \uc77c\ub3c4 \uc788\uc9c0\ub9cc, \ubb38\uc11c\ud654\ub3c4 \uc5c4\uccad\ub098\uac8c \uc911\uc694\ud558\ub2e4\uace0 \ub9d0\ud55c\ub2e4. \uadf8\ub807\uae30\uc5d0 \uac1c\ubc1c\uc790\uc5d0\uac8c\ub3c4 \uc694\uc998 \uae00\uc4f0\uae30\uac00 \ub354 \uc911\uc694\ud558\ub2e4\uace0 \ub9d0\ud558\ub294 \uac83\uc774 \uc544\ub2d0\uae4c?\\n\\n### \uac1c\ubc1c\uc790\uc5d0\uac8c \uae00\uc4f0\uae30\ub780 \ubb34\uc5c7\uc778\uac00?\\n\\n> \uae00\uc4f0\uae30\ub294 \uc0dd\ud65c\ud654\ub418\uc5b4\uc57c \ud55c\ub2e4. \uacf5\ubd80\ub97c \ud588\ub358 \uae30\uc5b5\uc744 \uae38\uac8c \uc720\uc9c0\ub97c \ud560 \uc218 \uc788\uac8c \ud558\ub294 \uc7a5\uce58\uc774\uba70, \ucee4\ubba4\ub2c8\ucf00\uc774\uc158 \ube44\uc6a9\uc744 \uc904\uc774\ub294 \ub3c4\uad6c\uc774\ub2e4. \ubaa8\ub450\uac00 \uc5b8\uc81c\ub4e0\uc9c0 \uc624\ud504\ub77c\uc778\uc5d0\uc11c \ub9cc\ub0a0 \uc218 \uc788\ub294 \uac83\uc774 \uc544\ub2c8\uace0, \uc5b8\uc81c\ub4e0\uc9c0 \uc804\ud654\ub97c \ud560 \uc218 \uc788\ub294 \uac83\uc774 \uc544\ub2c8\ub2e4. \uadf8\ub807\uae30\uc5d0 \ubb38\uc11c\ud654\ub97c \uc798 \ud558\uace0 \uc758\uacac\uc744 \uc798 \ub2f4\uc544\uc11c \uc815\ub9ac\ud560 \uc218 \uc788\uc5b4\uc57c \ud55c\ub2e4. \\n\\n### \uac1c\ubc1c\ud558\uae30\ub3c4 \ubc14\uc05c\ub370 \ub2e4\uc591\ud55c \ud65c\ub3d9\uc744 \ud558\ub294 \uc5d0\ub108\uc9c0\uc758 \uadfc\uc6d0\uc740?\\n\\n- \ud6c4\ud68c\ub97c \ub35c \ub0a8\uae30\uae30 \uc704\ud574 \ud55c\ub2e4.\\n\\n- \uc608\uc804\uc5d0 \uc37c\ub358 \uae00\uc5d0 \ub313\uae00, \uc798\ubabb\ub41c \uc9c0\uc2dd\uc744 \uc804\ubb38\uac00\ub4e4\uc774 \uc7a1\uc544\uc904 \ub54c \uc131\uc7a5\uc774 \ub41c\ub2e4.\\n  - \uc0c8\ub86d\uac8c \uc2dc\uc791\ud558\ub294 \ubd84\ub4e4\uaed8 \uae30\uc220 \ube14\ub85c\uadf8\ub97c \uc791\uc131\ud558\ub294 \uac83\uc744 \ucd94\ucc9c\ud55c\ub2e4. \uacf5\ubd80\ud55c \uac83\uc744 \uc815\ub9ac\ud558\ub294 \ub370 \ub3c4\uc6c0\uc774 \ub418\uba70, \uc0ac\uc218\uac00 \uc5c6\ub294 \ud658\uacbd\uc5d0\uc11c \uc77c\ud558\uac8c \ub420 \uacbd\uc6b0 \uc870\uc5b8, \ud53c\ub4dc\ubc31\uc744 \ubc1b\uc744 \uc218 \uc788\ub2e4.\\n\\n### \uae30\ud0c0\\n\\n- \ub9d0\uc744 \uc798\ud558\uae30\ubcf4\ub2e4\ub294 \uc11c\ub85c\ub97c \uc798 \uacf5\uac10\ud574\uc8fc\uace0 \uc11c\ub85c \uc131\uc7a5\ud558\uace0\uc790 \ud558\ub294 \ub9c8\uc778\ub4dc\ub97c \uac16\uc790.\\n- \uadfc\uac70\ub97c \uc798 \ub300\uc790.\\n  - \uc5b8\uc81c \uc368\ubcf4\ub2c8 \uc88b\ub2e4\ub354\ub77c, \ub204\uac00 \uc774\uac70 \uc88b\ub2e4\ub354\ub77c \ub4f1\uc758 \uac1d\uad00\uc801\uc774\uc9c0 \uc54a\uc740 \uc774\uc720\ubcf4\ub2e4 \ud56d\uc0c1 \uc0c1\ud669\uc744 \uc798 \ud30c\uc545\ud558\uace0 \uc5ec\ub7ec \uc694\uc18c\ub4e4\uc744 \uac10\uc548\ud55c \uadfc\uac70\ub97c \ub300\uc790.\\n- \ubb38\uc81c\ub97c \uc815\uc758\ud558\uace0 \uac00\uc124\uc744 \uc138\uc6b0\uace0 \ud53c\ub4dc\ubc31 \ubc1b\ub294 \uac83\uc744 \uc798\ud558\ub294 \uc0ac\ub78c\uc774 \ub418\uc790.\\n- \ud558\ub098\uc758 \ubb38\uc81c\ub97c \ubd10\ub3c4 \ub2e4\uc591\ud55c \uad00\uc810\uc5d0\uc11c \ubcf4\ub824\uace0 \ub178\ub825\ud574\ubcf4\uc790.\\n  - \ud558\ub098\uc758 \uae30\uc220\uc744 \ubd10\ub3c4 \ub2e4\ubc29\uba74\uc73c\ub85c \uc0dd\uac01\ud574\ubcf4\uc790.\\n- \uc0c1\uc0ac \ub610\ub294 \ud300\uc7a5\uc5d0\uac8c \uc9c8\ubb38\ud558\ub294 \uac83\uc744 \uac81\ub0b4\uc9c0 \ub9d0\uae30. \\n\\n### \ubc88\uc544\uc6c3\uc774 \uc628\uc801 \uc788\uc73c\uc2e0\uac00\uc694? \ub9ce\uc57d \uc788\ub2e4\uba74 \uc5b4\ub5bb\uac8c \uadf9\ubcf5\ud588\ub294\uc9c0 \uc54c \uc218 \uc788\uc744\uae4c\uc694?\\n\\n> \uac1c\ubc1c\ub9cc \ud558\uba74 \ubc88\uc544\uc6c3\uc774 \uc628\ub2e4. \uadf8\ub7ec\ub098 \uac1c\ubc1c\uc5d0 \ud558\ub294\ub370 \uc788\uc5b4\uc11c \ub3c4\ub2ec\ud558\ub294 \uc601\uc5ed\uae4c\uc9c0 \uacf5\ubd80\ud558\ub824\uace0 \ub178\ub825\uc744 \ud588\ub2e4.\\n\\n\uad11\ud718\ub2d8\uc740 \ubc88\uc544\uc6c3\uc774 \uc628 \uc801\uc774 \uc5c6\uc73c\uc2dc\ub2e4\uace0 \ud55c\ub2e4. \uae30\ud68d\uc11c\ub97c \ubd84\uc11d\ud558\ub294 \ud504\ub85c\uc138\uc2a4, \ucee4\ubba4\ub2c8\ucf00\uc774\uc158\uc744 \uc798\ud558\uae30 \uc704\ud55c \ud750\ub984\uc744 \ub9cc\ub4dc\ub294 \ub4f1 \ub2e4\uc591\ud55c \ub178\ub825\uc744 \ud558\uc2e0 \uac83 \uac19\uc558\ub2e4. \uac1c\ubc1c\ub9cc \uacf5\ubd80\ud558\ub294 \uac83\uc774 \uc544\ub2c8\ub77c \uc774\ub7f0 \uac83\ub4e4\uc744 \uacc4\uc18d \ubc88\uac08\uc544 \uac00\uba74\uc11c \uacf5\ubd80\ub97c \ud558\ub2c8 \ubc88\uc544\uc6c3\uc774 \uc624\uc9c0 \uc54a\uc73c\uc168\ub2e4\uace0 \ud55c\ub2e4.\\n\\n\uc774 \ubd80\ubd84\uc774 \ub0b4\uac00 \ubc18\uc131\ud588\ub358 \ubd80\ubd84\uc774\uc5c8\ub2e4. \ub098\ub294 \uc5b4\uca4c\uba74 \ub108\ubb34 \uac1c\ubc1c\uc5d0\ub9cc \uc5f4\uc2ec\ud788 \ud558\ub824\uace0 \uc788\uc9c0 \uc54a\uc558\ub294\uc9c0 \ub2e4\uc2dc \uc0dd\uac01\ud574\ubcf4\ub294 \uacc4\uae30\uac00 \ub410\ub2e4. \uac1c\ubc1c\uc790\ub294 \uac1c\ubc1c\ub9cc \uc798\ud558\uba74 \ub418\ub294 \uac83\uc774 \uc544\ub2c8\ub2e4. \ucee4\ubba4\ub2c8\ucf00\uc774\uc158\ub3c4 \uae00\uc4f0\uae30 \ub4f1 \ub2e4\uc591\ud55c \ub2a5\ub825\ub4e4\uc744 \ud0a4\uc6b8 \uc218 \uc788\uc73c\uba74 \uc88b\uc740 \uac83 \uac19\ub2e4.\\n\\n\ub09c \ud56d\uc0c1 \ubc88\uc544\uc6c3\uc774 \uc624\uc9c0 \uc54a\uc73c\ub824\uace0 \ub178\ub825\uc744 \ud558\uc9c0\ub9cc, \uacb0\uad6d\uc740 \uac1c\ubc1c\uc744 \uacc4\uc18d\ud558\ub2e4\uac00 \ubcf4\uba74 \uc5b8\uc820\uac00 \uc9c0\uce60 \ub54c\uac00 \uc788\uc5c8\ub2e4. \uadf8\ub7f4 \ub54c \ub098\ub294 \uadf8\ub0e5 \uacbd\uc81c, \uc2ec\ub9ac \ub4f1\uc758 \ub2e4\ub978 \ubd84\uc57c\uc758 \ucc45\uc744 \uc77d\uc5c8\ub2e4. \ub2e4\ub978 \ubd84\uc57c\ub85c \ud658\uae30\ub97c \uc2dc\ud0a4\ub294 \uac83\uc774\ub2e4. \uc774\uac8c \uc0c1\ub2f9\ud788 \ub3c4\uc6c0\uc774 \ub41c\ub2e4. \ub610\ud55c \uac00\ub9cc\ud788 \uc88c\uc808\ud558\uace0 \uc788\ub294 \uac83\ubcf4\ub2e4 \ud6e8\uc52c \ub0ab\ub2e4.\\n\\n> \ub3c8 \ubc1b\uc73c\uba74 \ub204\uad6c\ub098 \ud504\ub85c\ub2e4. \ud504\ub85c\ub294 \uc2a4\uc2a4\ub85c \ucee8\ub514\uc158 \uad00\ub9ac\ub97c \ud574\uc57c \ud55c\ub2e4. \\n\\n\uc694\ucc3d\ub2d8\uc774 \uc800 \ub9d0\uc744 \ud558\uc168\uc744 \ub54c \uc5c4\uccad\ub098\uac8c \ucda9\uaca9\uc744 \ubc1b\uc558\ub2e4. \ub098\uc911\uc5d0 \ub0b4\uac00 \ub3c8\uc744 \ubc1b\uace0 \uc77c\ud558\ub294 \uac1c\ubc1c\uc790\uac00 \ub41c\ub2e4\uba74 \uc800 \ubb38\uc7a5\uc744 \ub2e4\uc2dc \uc0dd\uac01\ud558\uac8c \ub420 \uac83 \uac19\ub2e4.\\n\\n## \ud6c4\uae30\\n\\n\uc704\uc758 \ub0b4\uc6a9\uc740 \uc601\uc0c1\uc744 \uac04\ub2e8\ud558\uac8c \uc694\uc57d \ud574\uc11c \uc801\uc740 \uac83\uc774\uba70, \uac1c\uc778\uc801\uc778 \uc0dd\uac01\ub3c4 \uc801\ud600 \uc788\uae30\uc5d0 \uc6d0\ubcf8 \uc601\uc0c1\uc744 \uaf2d \ubcf4\ub294 \uac83\uc744 \ucd94\ucc9c\ud55c\ub2e4. \uc9c0\uae08\uae4c\uc9c0 \uc88b\uc740 \uac1c\ubc1c\uc790, \uacc4\uc18d \uc131\uc7a5\ud558\ub294 \uac1c\ubc1c\uc790\uac00 \ub418\uae30 \uc704\ud574 \ucc45\ub4e4\ub3c4 \ub9ce\uc774 \uc77d\uc5c8\uc5c8\uc9c0\ub9cc, \uc774\ubc88 \uc601\uc0c1\uc744 \ud1b5\ud574\uc11c \ub2e4\uc2dc \ubcf5\uc2b5\ud560 \uc218 \uc788\uc5b4 \uc88b\uc558\ub2e4. \ub2e4\uc2dc \ud55c \ubc88 \uc815\ub9d0 \ub9ce\uc740 \uc0dd\uac01\ub4e4\uc744 \ud558\uac8c \ub418\ub294 \uc601\uc0c1\uc774\uc5c8\uace0, \uac1c\ubc1c\uc790\ub97c \ubaa9\ud45c\ub85c \ud558\ub294 \ud559\uc0dd\uc774\ub098 \uc131\uc7a5\uc5d0 \ub300\ud55c \uace0\ubbfc\uc744 \uac16\uace0 \uc788\ub294 \uc8fc\ub2c8\uc5b4 \uac1c\ubc1c\uc790\ub4e4\uc774 \uae30\ud68c\uac00 \ub41c\ub2e4\uba74 \ubd24\uc73c\uba74 \uc88b\uaca0\ub2e4."},{"id":"/building-infrastructure-starting-with-AWS","metadata":{"permalink":"/en/blog/building-infrastructure-starting-with-AWS","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2022-07-21-building-infrastructure-starting-with-AWS/building-infrastructure-starting-with-AWS.md","source":"@site/blog/2022-07-21-building-infrastructure-starting-with-AWS/building-infrastructure-starting-with-AWS.md","title":"\ud83d\udcd6 AWS\ub85c \uc2dc\uc791\ud558\ub294 \uc778\ud504\ub77c \uad6c\ucd95\uc758 \uc815\uc11d","description":"\uacac\uace0\ud55c \uc5d4\ud130\ud504\ub77c\uc774\uc988 \ud504\ub85c\ub355\uc158 \ud658\uacbd\uc744 \ub9cc\ub4e4\uace0 \uc9c0\ud0a4\ub294 \ubc95 | I\u2665Cloud \uc81c\uc774\ud38d\uc758 \ud074\ub77c\uc6b0\ub4dc \uc2dc\ub9ac\uc988 23","date":"2022-07-21T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 AWS\ub85c \uc2dc\uc791\ud558\ub294 \uc778\ud504\ub77c \uad6c\ucd95\uc758 \uc815\uc11d","date":"2022-07-21","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/building-infrastructure-starting-with-AWS","description":"\uacac\uace0\ud55c \uc5d4\ud130\ud504\ub77c\uc774\uc988 \ud504\ub85c\ub355\uc158 \ud658\uacbd\uc744 \ub9cc\ub4e4\uace0 \uc9c0\ud0a4\ub294 \ubc95 | I\u2665Cloud \uc81c\uc774\ud38d\uc758 \ud074\ub77c\uc6b0\ub4dc \uc2dc\ub9ac\uc988 23","keywords":["Amazon Web Service","\uc778\ud504\ub77c","\ud074\ub77c\uc6b0\ub4dc"]},"unlisted":false,"prevItem":{"title":"\ud83c\udfcd\ufe0f 5\uc6d4 \uc6b0\uc544\ud55c\ud14c\ud06c\uc138\ubbf8\ub098","permalink":"/en/blog/may-woowahan-tech-seminar"},"nextItem":{"title":"\ud83d\udcd6 \uc5d1\uc140, R, \ud30c\uc774\uc36c\uc73c\ub85c \uc2dc\uc791\ud558\ub294 \ub370\uc774\ud130 \ubd84\uc11d","permalink":"/en/blog/advancing-into-analytics"}},"content":":::info\\n\uc81c\uc774\ud38d \\\\<\ubbff\uace0 \ubcf4\ub294 IT \uc804\ubb38\uc11c \ub9ac\ubdf0\uc5b4\\\\> \ud65c\ub3d9\uc744 \uc704\ud574\uc11c \ucc45\uc744 \uc81c\uacf5\ubc1b\uc544 \uc791\uc131\ub41c \uc11c\ud3c9\uc785\ub2c8\ub2e4.\\n:::\\n\\n## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/building-infrastructure-starting-with-AWS.jpg)](https://www.kyobobook.co.kr/product/detailViewKor.laf?ejkGb=KOR&mallGb=KOR&barcode=9791191600971&orderClick=LEa&Kc=)\\n\\n- \uc81c\ubaa9: AWS\ub85c \uc2dc\uc791\ud558\ub294 \uc778\ud504\ub77c \uad6c\ucd95\uc758 \uc815\uc11d\\n- \uc800\uc790: Kenji Nakagaki(\ub098\uce74\uac00\ud0a4 \uac90\uc9c0)\\n- \uc5ed\uc790: \uae40\ubaa8\uc138\\n- \ucd9c\ud310\uc0ac: \uc81c\uc774\ud38d\\n- \ucd9c\uac04: 2022-06-09\\n\\n{/* truncate */}\\n\\n## Intro\\n\\n\uc694\uc998\uc740 \uc5b4\uca4c\ub2e4 \uc544\ub9c8\uc874 \uc6f9 \uc11c\ube44\uc2a4\uc5d0\uc11c \uba54\uc77c\uc744 \ubc1b\uace0 \uc788\uc5b4 \ub2e4\uc591\ud55c \uad50\uc721\ub4e4\uc744 \ub4e4\uc5b4\ubcf4\ub77c\uace0 \ud64d\ubcf4 \uba54\uc77c\ub4e4\uc774 \uc800\uc5d0\uac8c \uc635\ub2c8\ub2e4. \uc804 \uc774\ub7f0 \uba54\uc77c\uc744 \ud1b5\ud574 aws\uc5d0\uc11c \ud074\ub77c\uc6b0\ub4dc \uae30\ucd08 \uad50\uc721\uc744 \uc9c4\ud589\ud55c\ub2e4\ub294 \uac83\uc744 \uc54c\uac8c \ub410\uc2b5\ub2c8\ub2e4. \uc544\ub9c8 \uc774\ub7f0 \uad50\uc721\ub4e4\uc774 \uc788\uc5c8\ub2e4\ub294 \uc0ac\uc2e4\uc744 \uc54c\uace0 \uc788\uc5c8\ub354\ub77c\uba74, \uad73\uc774 \ucc45\uc744 \uc77d\uc9c0 \uc54a\uc558\uc744\uc9c0\ub3c4 \ubaa8\ub974\uaca0\uc2b5\ub2c8\ub2e4. \\n\\n\uc804 \uc800\ub7f0 \uad50\uc721\ub4e4\uc774 \uc544\ub9c8\uc874 \uc6f9 \uc11c\ube44\uc2a4\uc5d0 \uad00\uc2ec\uc774 \uc788\uc9c0 \uc54a\ub2e4\uba74 \uc811\ud558\uae30 \ud798\ub4e4\ub2e4\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4. \uc800\ub294 aws\ub97c \uc0dd\ud65c\ucf54\ub529\uc5d0\uc11c \ucc98\uc74c \uacf5\ubd80\ud558\uac8c \ub410\uace0, \ub2e4\ub978 \ucc45\ub4e4\uc744 \ud1b5\ud574 \uc6f9 \uc11c\ube44\uc2a4 \ubc30\ud3ec\ub97c \uacf5\ubd80\ud558\uba70 \uc870\uae08\uc529 \uc811\ud588\uc5c8\uc2b5\ub2c8\ub2e4. \ubb3c\ub860 \uc0dd\ud65c\ucf54\ub529\uc5d0\uc11c\ub3c4 \uc798 \uc54c\ub824\uc8fc\uc9c0\ub9cc, \ub354 \uc790\uc138\ud788 \uacf5\ubd80\ud558\uae30 \uc704\ud574\uc11c \ucc45\uc744 \uc77d\uc5b4\ubcf4\uac8c \ub410\uc2b5\ub2c8\ub2e4.\\n\\n## Book Review\\n\\n### \ub2e8\uc21c\ud788 aws \uc11c\ube44\uc2a4\ub97c \uacf5\ubd80\ud558\ub294 \uac83\uc774 \uc544\ub2cc\\n\\n\ub2e8\uc21c\ud788 aws \uc11c\ube44\uc2a4\uc5d0 \uad00\ub828\ub41c \ub0b4\uc6a9\uc774 \uc544\ub2c8\ub77c \uc880 \ub354 CS \uc9c0\uc2dd \uad00\uc810\uc73c\ub85c \uc4f0\uc5ec\uc838 \uc788\uc5b4 CS \uc9c0\uc2dd\uc774 \uaf64 \ubd80\uc871\ud55c \uc0ac\ub78c\ub3c4 \ud3b8\ud558\uac8c \uc77d\uc744 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \uc81c\uac00 \uadf8\ub7ac\uc2b5\ub2c8\ub2e4. \uc804 \uc804\uacf5\uc790\ub85c\uc11c \ub124\ud2b8\uc6cc\ud06c \uc218\uc5c5\uc744 \ub4e4\uc5c8\uc9c0\ub9cc, \uc774 \ucc45\uc744 \uc77d\uc73c\uba70 \uc81c\uac00 \ub124\ud2b8\uc6cc\ud06c \uc9c0\uc2dd\uc774 \uc5c4\uccad \ubd80\uc871\ud588\ub2e4\ub294 \uc0ac\uc2e4\uc744 \uae68\ub2eb\uac8c \ub410\uc2b5\ub2c8\ub2e4. \ucef4\ud4e8\ud130 \uc804\uacf5\uc790\ub77c\uba74 \ud070 \uc5b4\ub824\uc6c0 \uc5c6\uc774 \uc774 \ucc45\uc744 \uc77d\uc73c\uc2e4 \uc218 \uc788\uc744 \uac83 \uac19\uc2b5\ub2c8\ub2e4.\\n\\n\ub610\ud55c, \ucc45 \uc81c\ubaa9\uacfc \uc54c\ub9de\uac8c \ub2e8\uc21c\ud788 aws \uc11c\ube44\uc2a4\ub97c \ud558\ub098\ud558\ub098 \ubc30\uc6b0\ub294 \uac83\uc774 \uc544\ub2cc, \uc810\ud504 \uc11c\ubc84, \uce90\uc2dc \uc11c\ubc84 \ub4f1\uc744 \ubc30\uc6b0\uba70 \ub9d0 \uadf8\ub300\ub85c \uc778\ud504\ub77c \uad6c\ucd95\uc5d0 \uad00\uc810\uc744 \ub454 \ucc45\uc785\ub2c8\ub2e4. aws \uc11c\ube44\uc2a4\ub97c \uc810\ucc28\uc801\uc73c\ub85c \ucd94\uac00\ud574\ubcf4\uba74\uc11c \uacf5\ubd80\ud574\ubcf4\uace0 \uc2f6\uc740 \ubd84\ub4e4\uaed8 \ub3c4\uc6c0\uc774 \ub9ce\uc774 \ub420 \uac83\uc774\ub77c \uc0dd\uac01\ud569\ub2c8\ub2e4.\\n\\n### \ub9ac\uc18c\uc2a4 \uc774\ubbf8\uc9c0\\n\\n\uc81c\uac00 \uc774 \ucc45\uc744 \uc77d\uc73c\uba74\uc11c \uac00\uc7a5 \uc88b\uac8c \ubcf8 \ubd80\ubd84\uc785\ub2c8\ub2e4. \ud55c chapter\ub9c8\ub2e4 \uc77d\uc744 \ub54c\ub9c8\ub2e4 \ub9ac\uc18c\uc2a4\uac00 \ucd94\uac00 \ub418\ub294\ub370 \uc2dc\uac01\uc801\uc73c\ub85c \ubb34\uc5c7\uc774 \ub2ec\ub77c\uc84c\ub294\uc9c0 \uc54c  \uc218 \uc788\uc5b4\uc11c \ucc45\uc744 \uc77d\ub294\ub370 \ub3c4\uc6c0\uc774 \ub410\uc2b5\ub2c8\ub2e4. \uc774 \ubc16\uc5d0\ub3c4 \uadf8\ub9bc\uc744 \ud1b5\ud574 \uc5b4\ub824\uc6b4 \ub0b4\uc6a9\uc744 \uc27d\uac8c \uc2dc\uac01\uc801\uc73c\ub85c \ubcf4\uc5ec\uc92c\ub2e4\ub294 \uc810\uc5d0\uc11c \ub108\ubb34 \uc77d\uae30 \ud3b8\ud588\uc2b5\ub2c8\ub2e4.\\n\\n### \ubd80\ub85d\\n\\n\ubd80\ub85d\uc5d0\uc11c\ub294 \uc774 \ucc45\uc5d0\uc11c \uc4f4 \ub9ac\uc18c\uc2a4 \uc815\ubcf4\ub4e4\uacfc \ub9ac\uc18c\uc2a4 \uc0ad\uc81c \ubc29\ubc95 \ub4f1\uc744 \uc18c\uac1c\ud569\ub2c8\ub2e4. \uc774 \ubd80\ubd84\ub3c4 \ub9c8\uc74c\uc5d0 \ub4e4\uc5c8\ub358 \uac8c \ub9ac\uc18c\uc2a4 \uc0ad\uc81c \ubc29\ubc95\uc744 \ubab0\ub77c\uc11c \uc0ad\uc81c\ub97c \ubabb\ud558\uc2dc\ub294 \ubd84\ub4e4\ub3c4 \ub9ce\uc740\ub370 \uc774\ub807\uac8c \ub530\ub85c \uc0ad\uc81c \ubc29\ubc95\uc744 \uac04\ub2e8\ud558\uac8c\ub098\ub9c8 \uc54c\ub824\uc900\ub2e4\ub294 \uac83\uc774 \ud070 \ub3c4\uc6c0\uc774 \ub420 \uac83 \uac19\uc558\uc2b5\ub2c8\ub2e4. \\n\\n## \ub300\uc0c1\ub3c5\uc790\\n\\n\uc778\ud504\ub77c \uad6c\ucd95\uc5d0 \uad00\uc2ec\uc774 \ub9ce\uc73c\uba70 \ub124\ud2b8\uc6cc\ud06c\ub97c \ud55c \ubc88\ucbe4 \uacf5\ubd80\ud574\ubcf4\uc2e0 \ubd84\ub4e4\uc774 \uc77d\uc73c\uc2dc\ub294 \uac78 \ucd94\ucc9c\ud569\ub2c8\ub2e4. \ub610\ud55c, \ub9ac\ub205\uc2a4 \uba85\ub839\uc5b4 \uc0ac\uc6a9 \uacbd\ud5d8\uc774 \uc788\uc5b4\uc57c \ud569\ub2c8\ub2e4. \ucc45\uc5d0\uc11c \uac04\ub2e8\ud558\uac8c \uc124\uba85\uc740 \ud574\uc8fc\uc9c0\ub9cc, \uadf8\ub798\ub3c4 \uc5b4\ub290\uc815\ub3c4 \uc0ac\uc6a9\ud560 \uc218 \uc788\uc5b4\uc57c \ucc45 \uc77d\ub294\ub370 \ubd88\ud3b8\ud568\uc774 \uc5c6\uc744 \uac83 \uac19\uc2b5\ub2c8\ub2e4. \uc774 \ucc45\uc758 \uae30\uc900 \ud658\uacbd\uc740 Windows10\uc774\uc9c0\ub9cc \ub2e4\ub978 OS\uc5d0 \uc775\uc219\ud558\uc2e0 \ubd84\ub4e4\uc774 \uc77d\uc5b4\ubcf4\uc154\ub3c4 \ud070 \uc5b4\ub824\uc6c0\uc740 \uc5c6\uc744 \uac83 \uac19\uc2b5\ub2c8\ub2e4."},{"id":"/advancing-into-analytics","metadata":{"permalink":"/en/blog/advancing-into-analytics","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2022-05-29-advancing-into-analytics/advancing-into-analytics.md","source":"@site/blog/2022-05-29-advancing-into-analytics/advancing-into-analytics.md","title":"\ud83d\udcd6 \uc5d1\uc140, R, \ud30c\uc774\uc36c\uc73c\ub85c \uc2dc\uc791\ud558\ub294 \ub370\uc774\ud130 \ubd84\uc11d","description":"\ub370\uc774\ud130 \ubd84\uc11d\uc758 \uae30\ubcf8 \uac1c\ub150\uacfc 3\uac00\uc9c0 \ud575\uc2ec \ub3c4\uad6c \uc0ac\uc6a9\ubc95","date":"2022-05-29T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \uc5d1\uc140, R, \ud30c\uc774\uc36c\uc73c\ub85c \uc2dc\uc791\ud558\ub294 \ub370\uc774\ud130 \ubd84\uc11d","date":"2022-05-29","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/advancing-into-analytics","description":"\ub370\uc774\ud130 \ubd84\uc11d\uc758 \uae30\ubcf8 \uac1c\ub150\uacfc 3\uac00\uc9c0 \ud575\uc2ec \ub3c4\uad6c \uc0ac\uc6a9\ubc95","keywords":["\uc5d1\uc140","\ub370\uc774\ud130 \ubd84\uc11d"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 AWS\ub85c \uc2dc\uc791\ud558\ub294 \uc778\ud504\ub77c \uad6c\ucd95\uc758 \uc815\uc11d","permalink":"/en/blog/building-infrastructure-starting-with-AWS"},"nextItem":{"title":"\ud83d\udcd6 \uc88b\uc740 \ucf54\ub4dc, \ub098\uc05c \ucf54\ub4dc","permalink":"/en/blog/good-code-bad-code"}},"content":":::info\\n\ud55c\ube5b\ubbf8\ub514\uc5b4 \\\\<\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4\\\\> \ud65c\ub3d9\uc744 \uc704\ud574\uc11c \ucc45\uc744 \uc81c\uacf5\ubc1b\uc544 \uc791\uc131\ub41c \uc11c\ud3c9\uc785\ub2c8\ub2e4.\\n:::\\n\\n![\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4 2022](../assets/I-am-reviewer-2022.jpg)\\n\\n## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/advancing-into-analytics.jpg)](https://www.kyobobook.co.kr/product/detailViewKor.laf?ejkGb=KOR&mallGb=KOR&barcode=9791162245576&orderClick=LEa&Kc=)\\n\\n- \uc81c\ubaa9: \uc5d1\uc140, R, \ud30c\uc774\uc36c\uc73c\ub85c \uc2dc\uc791\ud558\ub294 \ub370\uc774\ud130 \ubd84\uc11d\\n- \uc800\uc790: George Mount\\n- \uc5ed\uc790: \uc2dc\uc9c4\\n- \ucd9c\ud310\uc0ac: \ud55c\ube5b\ubbf8\ub514\uc5b4\\n- \ucd9c\uac04: 2022-04-29\\n\\n{/* truncate */}\\n\\n## Intro\\n\\n\ucc98\uc74c\uc5d0 \uc774 \ucc45\uc758 \ud45c\uc9c0\uc640 \ubaa9\ucc28\ub97c \uc77d\uc5c8\uc744 \ub54c \uc0c1\ub2f9\ud788 \ud765\ubbf8\ub85c\uc6b4 \uc8fc\uc81c\ub77c\uace0 \uc0dd\uac01\ud588\uc2b5\ub2c8\ub2e4. \uc804 \ub370\uc774\ud130 \ubd84\uc11d\uc774 \ucc98\uc74c\uc740 \uc544\ub2c8\uc9c0\ub9cc, \ub9e4\ubc88 \ud30c\uc774\uc36c\uc73c\ub85c\ub9cc \ud588\uc5c8\uc9c0, \uc5d1\uc140\ub85c\ub294 \uacbd\ud5d8\ud574\ubcf4\uc9c0 \ubabb\ud588\uac70\ub4e0\uc694. \uc5d1\uc140\ub85c\ub3c4 \ubb3c\ub860 \ud558\uba74 \ud560 \uc218\ub294 \uc788\uaca0\uc9c0\ub9cc, \ub9e4\ubc88 \ud504\ub85c\uadf8\ub798\ubc0d \uc5b8\uc5b4\ub9cc \uc0ac\uc6a9\ud558\ub358 \uc0ac\ub78c\uc73c\ub85c\uc11c \uadf8\ub7f4 \uc0dd\uac01\uc744 \ud574\ubcf4\uc9c0\ub294 \ubabb\ud588\uc5b4\uc694. \uadf8\ub9ac\uace0 \uc81c\uac00 \uc608\uc804\uc5d0 \uacf5\ubd80\ud558\ub2e4 \ud3ec\uae30\ud55c R\uc744 \ubc30\uc6cc\ubcfc \uc218 \uc788\ub2e4\ub294 \uac83\ub9cc\uc73c\ub85c\ub3c4 \uc7ac\ubc0c\uaca0\ub2e4\uace0 \uc0dd\uac01\ud588\uc2b5\ub2c8\ub2e4.\\n\\n## Book Review\\n\\n### \ub370\uc774\ud130 \ubd84\uc11d \uc785\ubb38\uc790\ub97c \uc704\ud55c \ucc45\\n\\n\uc81c\uac00 \uc0dd\uac01\ud558\uae30\uc5d0 \uc774 \ucc45\uc740 \ub370\uc774\ud130 \ubd84\uc11d \uc785\ubb38\uc790\uc5d0\uac8c \uc720\uc6a9\ud560 \uac83 \uac19\uc2b5\ub2c8\ub2e4. \ud2b9\ud788 \uc5d1\uc140\uc744 \uc790\uc8fc \uc0ac\uc6a9\ud588\ub358 \uc0ac\ub78c\ub4e4\uc5d0\uac8c\ub294 \ub354\ub354\uc6b1\uc774\uc694. \uc790\uc5f0\uc2a4\ub7fd\uac8c \ud30c\uc774\uc36c\uacfc R\uacfc \uac19\uc740 \ud504\ub85c\uadf8\ub798\ubc0d \uc5b8\uc5b4\ub97c \uacf5\ubd80\ud560 \uc218 \uc788\ub294 \uae30\ud68c\uac00 \ub420 \uac83\uc785\ub2c8\ub2e4. \uc774 \ucc45\uc740 R \ub610\ub294 \ud30c\uc774\uc36c\uc744 \uc804\ud600 \ubaa8\ub974\ub294 \ub3c5\uc790\ub97c \ub300\uc0c1\uc73c\ub85c \uc791\uc131\ub418\uc5c8\uae30 \ub54c\ubb38\uc5d0 \ubaa8\ub974\ub354\ub77c\ub3c4 \uc27d\uac8c \uacf5\ubd80\ud574\ubcfc \uc218 \uc788\uc744 \uac83\uc785\ub2c8\ub2e4. \ub610\ud55c, \ub370\uc774\ud130 \ubd84\uc11d \uacf5\ubd80\ub97c \uc5b4\ub514\uc11c\ubd80\ud130 \uc2dc\uc791\ud558\uba74 \uc88b\uc744\uc9c0 \uace0\ubbfc\ud558\ub358 \uac1c\ubc1c\uc790\uc5d0\uac8c\ub3c4 \uc720\uc6a9\ud560 \uac83 \uac19\uc2b5\ub2c8\ub2e4. \ub370\uc774\ud130 \ubd84\uc11d\uc758 \uac1c\ub150\ubd80\ud130 \uac04\ub2e8\ud55c \ud504\ub85c\uadf8\ub798\ubc0d \uc2e4\uc2b5\uae4c\uc9c0 \ub2e4\ub8e8\uace0 \uc788\uae30 \ub54c\ubb38\uc774\uc8e0. \uc5ec\uae30\uc11c \ub370\uc774\ud130 \ubd84\uc11d\uc758 \uac1c\ub150\uc774\ub780 \ud1b5\uacc4\uc801\uc778 \uac1c\ub150\uc744 \uc758\ubbf8\ud569\ub2c8\ub2e4. \uc608\uc2dc\ub97c \ub4e4\uc5b4 \uc124\uba85\ud558\uc5ec \ud1b5\uacc4 \uc6a9\uc5b4\uac00 \ub098\uc640\ub3c4 \ud06c\uac8c \uc5b4\ub835\uc9c0 \uc54a\uc558\uc2b5\ub2c8\ub2e4.\\n\\n### \ub3c5\uc790\ub97c \uc2e0\uacbd \uc4f4 \ucc45\\n\\n\uc5b4\ub5a4 \ubd84\uc57c\ub098 \uae30\uc220\uc5d0 \uad00\uc2ec \uc788\uc73c\uba74 \ucc45\uc744 \ucc38\uace0\ud574\ubcf4\ub77c\uace0 \ucc45 \uc81c\ubaa9\uc744 \uc801\uc5b4\ub480\uc2b5\ub2c8\ub2e4. \uc774 \ucc45\uc5d0 \ubaa8\ub4e0 \uc9c0\uc2dd\uc744 \uc801\uc5b4\ub458 \uc218 \uc5c6\uc73c\ub2c8 reference\ub77c\ub3c4 \uc81c\uacf5\ud558\ub294 \uac70\uc8e0. \ubb3c\ub860 \ubc88\uc5ed\ub418\uc9c0 \uc54a\uc740 \ucc45\ub4e4\ub3c4 \ub9ce\uc9c0\ub9cc \ucd94\ucc9c\ud574\uc8fc\ub294 \ubd80\ubd84\uc774 \uc778\uc0c1\uc801\uc774\uc5c8\uc2b5\ub2c8\ub2e4. \uc800\uc790\uac00 \uc5bc\ub9c8\ub098 \ub3c5\uc790\ub97c \uc2e0\uacbd \uc4f4 \ucc45\uc778\uc9c0 \uc54c \uc218 \uc788\ub294 \ubd80\ubd84\uc785\ub2c8\ub2e4.\\n\\n\ub610\ud55c, \uc5f0\uc2b5\ubb38\uc81c\ub97c \uc81c\uacf5\ud569\ub2c8\ub2e4. \uc5f0\uc2b5 \ubb38\uc81c \ud574\ub2f5\ub3c4 [GitHub \uc800\uc7a5\uc18c](https://github.com/stringfestdata/advancing-into-analytics-book)\uc5d0 \uc81c\uacf5\ud558\ubbc0\ub85c \ud480\uc5b4\ubcf4\uc2dc\uace0 \ub2f5\uacfc \ud574\ub2f5\uc744 \ube44\uad50\ud558\uc2dc\uba74 \ub420 \uac83 \uac19\uc2b5\ub2c8\ub2e4.\\n\\n## \ub300\uc0c1\ub3c5\uc790\\n\\n\ub370\uc774\ud130 \ubd84\uc11d\uc5d0 \uc785\ubb38\ud558\ub294 \uc0ac\ub78c\uc5d0\uac8c \ucd94\ucc9c\ud569\ub2c8\ub2e4. \uac1c\ubc1c\uc744 \uc9c0\uae08\uae4c\uc9c0 \ud55c \ubc88\ub3c4 \uacbd\ud5d8\ud574\ubcf4\uc9c0 \ubabb\ud55c \ubd84\ub4e4\uc774\uc5b4\ub3c4 \uad1c\ucc2e\uc2b5\ub2c8\ub2e4. \ub2e4\ub9cc \ud655\ub960\uacfc \ud1b5\uacc4\ub97c \ud55c \ubc88\ub3c4 \uacf5\ubd80\ud574\ubcf4\uc2e0 \uc801\uc774 \uc5c6\uc73c\uc2dc\ub2e4\uba74 \uc774 \ucc45\uc774 \uc7ac\ubbf8\uc5c6\uc744 \uc218\ub3c4 \uc788\uc2b5\ub2c8\ub2e4. \uadf8\ub9cc\ud07c \ud1b5\uacc4\uc801\uc778 \uac1c\ub150\ub4e4\uc774 \ub098\uc624\ub2c8 \uc774 \ubd80\ubd84\ub9cc \uac10\ub2f9\ud558\uace0 \uc77d\uc73c\uc2e4 \uc218\ub9cc \uc788\ub2e4\uba74 \ub3c4\uc804\ud574\ubcf4\uc138\uc694!"},{"id":"/good-code-bad-code","metadata":{"permalink":"/en/blog/good-code-bad-code","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2022-06-01-good-code-bad-code/good-code-bad-code.md","source":"@site/blog/2022-06-01-good-code-bad-code/good-code-bad-code.md","title":"\ud83d\udcd6 \uc88b\uc740 \ucf54\ub4dc, \ub098\uc05c \ucf54\ub4dc","description":"\ud504\ub85c\uadf8\ub798\uba38\uc758 \ucf54\ub4dc \uac1c\uc120\ubc95","date":"2022-05-29T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \uc88b\uc740 \ucf54\ub4dc, \ub098\uc05c \ucf54\ub4dc","date":"2022-05-29","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/good-code-bad-code","description":"\ud504\ub85c\uadf8\ub798\uba38\uc758 \ucf54\ub4dc \uac1c\uc120\ubc95","keywords":["\uc790\ubc14","\ud14c\uc2a4\ud2b8 \ucf54\ub4dc","\uac00\ub3c5\uc131"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \uc5d1\uc140, R, \ud30c\uc774\uc36c\uc73c\ub85c \uc2dc\uc791\ud558\ub294 \ub370\uc774\ud130 \ubd84\uc11d","permalink":"/en/blog/advancing-into-analytics"},"nextItem":{"title":"\ud83d\udcd6 \uc18c\ubb38\ub09c \uba85\uac15\uc758 \uc18c\ud50c\uc758 \ucc98\uc74c \ub9cc\ub09c \ub9ac\uc561\ud2b8","permalink":"/en/blog/first-met-react"}},"content":":::info\\n\uc81c\uc774\ud38d \\\\<\ubbff\uace0 \ubcf4\ub294 IT \uc804\ubb38\uc11c \ub9ac\ubdf0\uc5b4\\\\> \ud65c\ub3d9\uc744 \uc704\ud574\uc11c \ucc45\uc744 \uc81c\uacf5\ubc1b\uc544 \uc791\uc131\ub41c \uc11c\ud3c9\uc785\ub2c8\ub2e4.\\n:::\\n\\n## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/good-code-bad-code.jpg)](https://www.kyobobook.co.kr/product/detailViewKor.laf?ejkGb=KOR&mallGb=KOR&barcode=9791191600896&orderClick=LEa&Kc=)\\n\\n- \uc81c\ubaa9: \uc88b\uc740 \ucf54\ub4dc, \ub098\uc05c \ucf54\ub4dc\\n- \uc800\uc790: Tom Long\\n- \uc5ed\uc790: \ucc28\uac74\ud76c\\n- \ucd9c\ud310\uc0ac: \uc81c\uc774\ud38d\\n- \ucd9c\uac04: 2022-05-26\\n\\n{/* truncate */}\\n\\n## Intro\\n\\n\uc774 \ucc45\uc758 \uc81c\ubaa9\uc744 \ucc98\uc74c \uc811\ud588\uc744 \ub54c *\ub09c \uc88b\uc740 \ucf54\ub4dc\ub97c \uc4f0\uace0 \uc788\ub294\uac00?*\uc5d0 \ub300\ud574 \uace0\ubbfc\ud558\uba70 \ubc18\uc131\ud558\ub294 \uc2dc\uac04\uc744 \uac16\uac8c \ub410\uc2b5\ub2c8\ub2e4. \ub9e4\ubc88 \ud300 \ub2e8\uc704\ub85c \ucf54\ub4dc\ub97c \uc791\uc131\ud574\uc57c \ud588\uc744 \ub54c\ub294 \uadf8\uc800 \ud504\ub85c\uc81d\ud2b8\ub97c \ube68\ub9ac \ub05d\ub0b4\uace0 \uc2f6\ub2e4\ub294 \uc0dd\uac01 \ubc16\uc5d0 \ud558\uc9c0 \uc54a\uc558\uac70\ub4e0\uc694. \uadf8 \ucf54\ub4dc\ub4e4\uc740 \uc801\uc5b4\ub3c4 \ub2e4\uc2dc \ubcfc \ucf54\ub4dc\ub294 \uc544\ub2c8\ub77c\uace0 \uc0dd\uac01\ud588\uae30 \ub54c\ubb38\uc774\uc8e0. \uadf8\ub7ec\ub2e4 \uc81c\uac00 \uc4f4 \ucf54\ub4dc\ub97c \ub2e4\uc2dc \ucc38\uace0\ud560 \uc77c\uc774 \uc788\uc5b4 \uc77d\uc5b4\ubcf4\ub294\ub370 \uc8fc\uc11d\ub3c4 \ub300\ucda9 \uc801\ud600 \uc788\uace0 \uc77d\uae30 \ud798\ub4e4\uc5b4 \ucc98\uc74c\ubd80\ud130 \ub2e4\uc2dc \ucf54\ub4dc\ub97c \uc9f0\ub358 \uacbd\ud5d8\uc774 \uc788\uc2b5\ub2c8\ub2e4. \uc774\ub7f0 \uc0c1\ud669\uc774 \ub2e4\uc2dc \uc624\uc9c0 \uc54a\uac8c \ud558\uae30 \uc704\ud574 \uc774 \ucc45\uc744 \uc77d\uc5b4\ubcf4\uac8c \ub410\uc2b5\ub2c8\ub2e4.  \\n\\n## Book Review\\n\\n### \uc0ac\ub78c\ub4e4\uacfc \uc2a4\ud130\ub514 \ud574\ubcf4\uace0 \uc2f6\uc740 \ucc45\\n\\n\uc774 \ucc45\uc774 \uc7ac\ubc0c\ub294 \uc810\uc740 \uac70\uc758 \ub300\ubd80\ubd84\uc758 \uc7a5\uc744 \uc2dc\uc791\ud560 \ub54c \ubb38\uc81c\uac00 \ub420 \uc218 \uc788\ub294 \uc0ac\ub840 \ub610\ub294 \ucf54\ub4dc\ub97c \uba3c\uc800 \uc124\uba85\ud558\uace0 \uc774\ub97c \ud574\uacb0\ud558\uae30 \uc704\ud55c \ucf54\ub4dc\ub97c \ubcf4\uc5ec\uc8fc\ub294 \ubc29\uc2dd\uc785\ub2c8\ub2e4. \uadf8\ub7ec\ub2c8 \uc88b\uc740 \uc0ac\ub840\uc640 \ub098\uc05c \uc0ac\ub840\ub97c \uc54c\ub824\uc8fc\ub294 \ucc45\uc778 \uac83\uc774\uc8e0. \ubb3c\ub860 \uc774 \ubd80\ubd84\uc740 \uc8fc\uad00\uc801\uc778 \uac83\uc774\uba70 \uc800\uc790\ubd84\uaed8\uc11c\ub3c4 \uc0c1\ud669\uc5d0 \ub530\ub77c \ub2e4\ub97c \uc218 \uc788\ub2e4\ub294 \uc810\uc744 \uc8fc\uc758\ud558\ub77c\uace0 \ud569\ub2c8\ub2e4. \ub3d9\ub8cc\ub4e4\uacfc \ucc45\uc5d0 \ub098\uc624\ub294 \uc0ac\ub840 \uc678\uc5d0\ub3c4 \ub354 \uc0dd\uac01\ud574\ubcf4\ub294 \uc2a4\ud130\ub514\ub97c \uc9c4\ud589\ud574\ubcf4\ub294 \uac83\ub3c4 \uc7ac\ubc0c\uc744 \uac83\uc774\ub77c \uc0dd\uac01\ud569\ub2c8\ub2e4. \\n\\n### \uc800\uc790\uc640 \uc5ed\uc790 \ubaa8\ub450 \ub178\ub825\ud55c \ucc45\\n\\n\ucc45\uc744 \uc77d\uc73c\uba70 \uc800\uc790\ubd84\uaed8\uc11c \uc54c\ub824\uc8fc\uace0 \uc2f6\uc73c\uc2e0 \ub0b4\uc6a9\ub4e4\uc774 \uc5c4\uccad \ub9ce\uc558\ub2e4\ub294 \ub290\ub08c\uc744 \ubc1b\uc558\uc2b5\ub2c8\ub2e4. \uc774 \ucc45\uc740 \uc88b\uc740 \ucf54\ub4dc\ub97c \uc791\uc131\ud558\uae30 \uc704\ud574 \uc720\ub2db \ud14c\uc2a4\ud2b8\uae4c\uc9c0 \ub2e4\ub8e8\uba70 TDD \ub4f1\uc758 \ud14c\uc2a4\ud2b8 \ucca0\ud559\uae4c\uc9c0 \uc870\uae08\uc774\ub098\ub9c8 \ub2e4\ub904\ubd05\ub2c8\ub2e4. \uc194\uc9c1\ud788 \uc720\ub2db \ud14c\uc2a4\ud2b8 \ub0b4\uc6a9\uae4c\uc9c0 \uc5c6\uc5b4\ub3c4 \uc88b\uc740 \ucf54\ub4dc\uc5d0 \ub300\ud574 \ucda9\ubd84\ud788 \uc0dd\uac01\ud574\ubcf4\uace0 \ub0b4\uc6a9\uc5d0 \ub300\ud574 \ub9cc\uc871\ud560\ub9cc\ud55c \ucc45\uc785\ub2c8\ub2e4.\\n\\n\ub610\ud55c, *\ud504\ub85c\uadf8\ub798\uba38\uc758 \ub1cc*\ub77c\ub294 \ucc45\uc5d0\uc11c\ub3c4 \ub290\uaf08\uc9c0\ub9cc \uc5ed\uc790\ubd84\uaed8\uc11c \ub0a8\uae30\uc2e0 \uc8fc\uc11d\ub4e4, \uadf8\ub9ac\uace0 \ub9e4\ub044\ub7fd\uac8c \uc77d\ud788\ub294 \uc810\uc5d0\uc11c \ubc88\uc5ed\ud558\uc2dc\ub294 \ub370 \ub3c5\uc790\uac00 \uc5b4\ub5bb\uac8c \ub354 \uc798 \uc774\ud574\ud560 \uc218 \uc788\uc744\uae4c \uace0\ubbfc\ud558\uc2e0 \uc810\ub4e4\uc774 \ubcf4\uc600\uc2b5\ub2c8\ub2e4. \uac1c\uc778\uc801\uc73c\ub85c \uc804\uacf5 \uc11c\uc801\uc5d0\ub3c4 \uc774\ub7f0 \uc5ed\uc790\ubd84\ub4e4\uc774 \ub9ce\uc774 \uc0dd\uae30\uc168\uc73c\uba74 \uc88b\uaca0\ub2e4\ub294 \uc18c\ub9dd\uc774 \uc788\uc2b5\ub2c8\ub2e4.\\n\\n## \ub300\uc0c1\ub3c5\uc790\\n\\n\uc774\uc81c \ub9c9 \ud55c \uc5b8\uc5b4\uc758 \ubb38\ubc95\uc11c\ub97c \ub5c0 \ubd84\ub4e4\uaed8\ub294 \uadf8\ub2e5 \ucd94\ucc9c\ud558\uc9c0 \uc54a\uc2b5\ub2c8\ub2e4. \uc758\uc0ac\ucf54\ub4dc\ub85c \uae30\uc220\ub418\uc5b4 \uc788\uc73c\ub098 \uc790\ubc14 or C#\uacfc \ub2ee\uc740 \ucf54\ub4dc\ub97c \uc0ac\uc6a9\ud558\uae30\uc5d0 \uc790\ubc14 \ub4f1\uc758 \uac1d\uccb4 \uc9c0\ud5a5 \ud504\ub85c\uadf8\ub798\ubc0d \uc5b8\uc5b4\ub85c \ucf54\ub4dc\ub97c \ub9ce\uc774 \uc791\uc131\ud574\ubcf8 \ubd84\ub4e4\uaed8 \ucd94\ucc9c\ud569\ub2c8\ub2e4. \ud2b9\ud788, \ucde8\uc5c5\uc744 \uc704\ud574 \uacf5\ubd80\ud558\uc2dc\ub294 \ubd84\ub4e4\ubcf4\ub2e4\ub294 \ud604\uc5c5\uc5d0\uc11c \uc5d4\uc9c0\ub2c8\uc5b4\ub85c \uc77c\ud55c \uacbd\ud5d8\uc774 \uc788\uc73c\uc2e0 \ubd84\ub4e4\uaed8 \ub354 \ucd94\ucc9c\ud558\uace0 \uc2f6\uc2b5\ub2c8\ub2e4. \uc55e\uc73c\ub85c \uac1c\ubc1c\ud558\uba74\uc11c \ub3c4\uc6c0\uc774 \ub418\ub294 \uc88b\uc740 \ud301\ub4e4\uc774 \ub9e4\uc6b0 \ub9ce\uc9c0\ub9cc, \ucde8\uc5c5\uc744 \uc704\ud574 \uacf5\ubd80\ud558\uc2dc\ub294 \ubd84\ub4e4\uc740 \uc18c\ud654\ud558\uae30 \uc5b4\ub824\uc6b8 \uc218 \uc788\uaca0\ub2e4\ub294 \uc0dd\uac01\uc774 \ub4e4\uc5c8\uc2b5\ub2c8\ub2e4. \ud300\uc6d0\ub4e4\uacfc \ud611\uc5c5\ud558\ub294 \uc0c1\ud669\uc77c \ub54c \ub354\uc6b1 \ub3c4\uc6c0\uc774 \ub420 \ucc45\uc778 \uac83 \uac19\uc2b5\ub2c8\ub2e4."},{"id":"/first-met-react","metadata":{"permalink":"/en/blog/first-met-react","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2022-06-26-first-met-react/first-met-react.md","source":"@site/blog/2022-06-26-first-met-react/first-met-react.md","title":"\ud83d\udcd6 \uc18c\ubb38\ub09c \uba85\uac15\uc758 \uc18c\ud50c\uc758 \ucc98\uc74c \ub9cc\ub09c \ub9ac\uc561\ud2b8","description":"\ub9ac\uc561\ud2b8 \uae30\ucd08 \uac1c\ub150 \uc815\ub9ac\ubd80\ud130 \uc2e4\uc2b5\uae4c\uc9c0","date":"2022-05-29T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \uc18c\ubb38\ub09c \uba85\uac15\uc758 \uc18c\ud50c\uc758 \ucc98\uc74c \ub9cc\ub09c \ub9ac\uc561\ud2b8","date":"2022-05-29","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/first-met-react","description":"\ub9ac\uc561\ud2b8 \uae30\ucd08 \uac1c\ub150 \uc815\ub9ac\ubd80\ud130 \uc2e4\uc2b5\uae4c\uc9c0","keywords":["React","\ud504\ub860\ud2b8\uc5d4\ub4dc"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \uc88b\uc740 \ucf54\ub4dc, \ub098\uc05c \ucf54\ub4dc","permalink":"/en/blog/good-code-bad-code"},"nextItem":{"title":"\ud83d\udcd6 \uc3d9\uc3d9 \ub4e4\uc5b4\uc624\ub294 \ud568\uc218\ud615 \ucf54\ub529","permalink":"/en/blog/grokking-simplicity"}},"content":":::info\\n\ud55c\ube5b\ubbf8\ub514\uc5b4 \\\\<\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4\\\\> \ud65c\ub3d9\uc744 \uc704\ud574\uc11c \ucc45\uc744 \uc81c\uacf5\ubc1b\uc544 \uc791\uc131\ub41c \uc11c\ud3c9\uc785\ub2c8\ub2e4.\\n:::\\n\\n![\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4 2022](../assets/I-am-reviewer-2022.jpg)\\n\\n## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/first-met-react.jpg)](https://www.kyobobook.co.kr/product/detailViewKor.laf?ejkGb=KOR&mallGb=KOR&barcode=9791162245682&orderClick=LAG&Kc=)\\n\\n- \uc81c\ubaa9: \uc18c\ubb38\ub09c \uba85\uac15\uc758 \uc18c\ud50c\uc758 \ucc98\uc74c \ub9cc\ub09c \ub9ac\uc561\ud2b8\\n- \uc800\uc790: \uc774\uc778\uc81c\\n- \ucd9c\ud310\uc0ac: \ud55c\ube5b\ubbf8\ub514\uc5b4\\n- \ucd9c\uac04: 2022-05-31\\n\\n{/* truncate */}\\n\\n## Intro\\n\\n\ubc31\uc5d4\ub4dc\ub9cc \uacf5\ubd80\ud558\ub358 \ud559\uc0dd\uc774\uc9c0\ub9cc, \uc5b8\uc820\uac00\ub294 \ud504\ub860\ud2b8\ub3c4 \ud574\ubd10\uc57c \ud558\uc9c0 \uc54a\uaca0\ub098 \ub9e4\ubc88 \uc0dd\uac01\ud588\uc2b5\ub2c8\ub2e4. \uadf8\ub7ec\ub098 \uc790\ubc14\uc2a4\ud06c\ub9bd\ud2b8\uae4c\uc9c0 \uc790\uc138\ud788 \uacf5\ubd80\ud558\uae30\uc5d0\ub294 \uc2dc\uac04\uc774 \ud56d\uc0c1 \ubd80\uc871\ud588\uc2b5\ub2c8\ub2e4. \ubc31\uc5d4\ub4dc\ub97c \uc704\ud55c \uae30\uc220\uc2a4\ud0dd \uacf5\ubd80\ud558\uae30\uc5d0\ub3c4 \ubc14\uc05c\ub370 \ud504\ub860\ud2b8\uae4c\uc9c0 \uc5b8\uc81c \ud558\uace0 \uc788\uaca0\uc5b4\uc694. \ub610\ud55c, \uc11c\uc810\uc5d0 \uac00\uc11c \ucc45\ub4e4\uc744 \uc0b4\ud3b4\ubcf4\uba74 \uc790\ubc14\uc2a4\ud06c\ub9bd\ud2b8 \ucc45\uc774 \ub450\uaecd\ub354\ub77c\uace0\uc694. \uadf8\ub798\uc11c \uc790\uc5f0\uc2a4\ub7fd\uac8c \ud504\ub860\ud2b8\ub294 \uc54c\uc544\uc57c \ud560 \uac83\ub3c4 \ub9ce\uace0 \uc9c4\uc785\uc7a5\ubcbd\uc774 \ub192\ub2e4\ub294 \uc0dd\uac01\uc744 \uac16\uac8c \ub410\uc5c8\uc2b5\ub2c8\ub2e4. \uadf8\ub7ec\ub358 \uc911 \uc774 \ucc45\uc744 \ubc1c\uacac\ud558\uac8c \ub410\uace0, \uc774 \ucc45\uc758 \ub300\uc0c1 \ub3c5\uc790\uc5d0 `HTML\uacfc \uc790\ubc14\uc2a4\ud06c\ub9bd\ud2b8\ub97c \ub2e4\ub904\ubcf8 \uacbd\ud5d8\uc774 \uc788\ub2e4\uba74 \uc218\uc6d4\ud558\uac8c \uc774 \ucc45\uc758 \uacfc\uc815\uc744 \ub530\ub77c\uac08 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \uc774\uc640 \uac19\uc740 \uc6f9 \uac1c\ubc1c\uc744 \uc704\ud55c \uae30\ucd08 \ub0b4\uc6a9\uc744 \ubc30\uc6b4 \uc801\uc774 \uc5c6\uac70\ub098 \uc6f9 \uac1c\ubc1c \uacbd\ud5d8\uc774 \uc5c6\ub354\ub77c\ub3c4 \uc774 \ucc45\uc758 \ud559\uc2b5 \uacfc\uc815\uc744 \ub530\ub77c\uac08 \uc218 \uc788\ub3c4\ub85d HTML\uacfc \uc790\ubc14\uc2a4\ud06c\ub9bd\ud2b8, CSS\uc758 \uae30\ucd08\uc801\uc778 \ub0b4\uc6a9\uc744 \uc124\uba85\ud558\uace0 \uc788\uc2b5\ub2c8\ub2e4.`\ub77c\uace0 \uc801\ud600\uc838 \uc788\uc5b4\uc11c \uc120\ud0dd\ud558\uac8c \ub410\uc2b5\ub2c8\ub2e4.\\n\\n## Book Review\\n\\n### \ub9ac\uc561\ud2b8 \uc785\ubb38\uc790\ub97c \uc704\ud55c \ucc45\\n\\n\uc704\uc758 Intro\uc5d0\uc11c\ub3c4 \uc5b8\uae09\ud588\ub4ef\uc774 \ucd08\ubc18\ubd80\uc5d0 HTML, CSS, JavaScript\uc5d0 \ub300\ud574 \uba3c\uc800 \uac04\ub2e8\ud558\uac8c \uc124\uba85\ud569\ub2c8\ub2e4. \uc194\uc9c1\ud788 \uc774 \ucc45\uc744 \uc77d\ub294 \uc0ac\ub78c\uc774 \uc815\ub9d0 HTML, CSS, JavaScript\uc5d0 \ub300\ud574 \ubaa8\ub97c\ub9ac\ub294 \uc5c6\uc9c0\ub9cc \uadf8\ub798\ub3c4 \uac04\ub2e8\ud558\uac8c\ub77c\ub3c4 \uc815\ub9ac\ud574\uc8fc\uace0 \uc2dc\uc791\ud558\ub294 \uac83\uc774 \uc785\ubb38\uc790\uc5d0\uac8c\ub294 \ub9e4\uc6b0 \ub3c4\uc6c0\uc774 \ub420 \uac83\uc774\ub77c \uc0dd\uac01\ud588\uc2b5\ub2c8\ub2e4. \uadf8 \ud6c4 \ub9ac\uc561\ud2b8\uac00 \ubb34\uc5c7\uc774\uace0 \ub9ac\uc561\ud2b8\uac00 \uc5b4\ub5bb\uac8c \ub3d9\uc791\ud558\ub294\uc9c0, \uc65c \ub9ac\uc561\ud2b8\ub97c \uc0ac\uc6a9\ud558\ub294\uc9c0 \ub4f1\uc744 \uc798 \uc124\uba85\ud569\ub2c8\ub2e4. \uc804\uccb4\uc801\uc778 \uc124\uba85\uc774 \uc801\uc808\ud55c \ube44\uc720\uc640 \uc608\uc2dc\ub97c \ub4e4\uc5b4\uac00\uba70 \uc124\uba85\ud558\uc5ec \uc774\ud574\ud558\uae30 \ud3b8\ud588\uc2b5\ub2c8\ub2e4.\\n\\n\ub610\ud55c, \uc800\ub294 [\uc800\uc790\ubd84\uaed8\uc11c \uc81c\uacf5\ud558\ub294 \uac15\uc758](https://www.youtube.com/playlist?list=PLo3AHtncM26y0qX58gjc_QrkYlBCzQ2R_)\ub97c \ud65c\uc6a9\ud558\uba74\uc11c \uacf5\ubd80\ud588\uc2b5\ub2c8\ub2e4. \uadf8\ub807\uac8c \uae38\uc9c0 \uc54a\uace0 \uc9e7\uc740 \uac15\uc758\ub4e4\uc774\ub77c \ub4e3\ub294\ub370\ub3c4 \ubd80\ub2f4\uac10\uc774 \uc5c6\uc5c8\uace0 \uc800 \uac19\uc740 \uc785\ubb38\uc790\ub4e4\uc5d0\uac8c\ub294 \ub9ce\uc740 \ub3c4\uc6c0\uc774 \ub420 \uac83 \uac19\uc2b5\ub2c8\ub2e4.\\n\\nChapter\ub9c8\ub2e4 \ub9c8\uc9c0\ub9c9\uc5d0 \uac04\ub2e8\ud558\uac8c \uc815\ub9ac\uac00 \ub418\uc5b4 \uc788\uc5b4 \uc774\ubc88 Chapter\uc5d0\uc11c\ub294 \ubb34\uc5c7\uc744 \ubc30\uc6e0\ub098 \ub2e4\uc2dc \uc0dd\uac01\ud574\ubcfc \uc218\ub3c4 \uc788\uc5b4\uc11c \uc88b\uc558\uc2b5\ub2c8\ub2e4.\\n\\n\ub9c8\uc9c0\ub9c9\uc5d0\ub294 \uc9c0\uae08\uae4c\uc9c0 \ubc30\uc6b4 \ub0b4\uc6a9\uc744 \ud65c\uc6a9\ud558\uc5ec \uac04\ub2e8\ud558\uac8c \ube14\ub85c\uadf8\ub97c \ub9cc\ub4e4\uc5b4\ubcf4\ub294 \ubbf8\ub2c8 \ud504\ub85c\uc81d\ud2b8\ub97c \uc9c4\ud589\ud569\ub2c8\ub2e4. \uc810\uac80 \ucc28\uc6d0\uc5d0\uc11c \ub9e4\uc6b0 \ub3c4\uc6c0\uc774 \ub410\uc2b5\ub2c8\ub2e4. \uc870\uae08 \uc544\uc26c\uc6b4 \uc810\uc740 \ub610 \ub2e4\ub978 \uc8fc\uc81c\uc758 \ud504\ub85c\uc81d\ud2b8\ub3c4 \uc788\uc5c8\ub2e4\uba74 \ubcf5\uc2b5\ud558\ub294 \ub370 \ub3c4\uc6c0\uc774 \ub410\uc744 \uac83 \uac19\uc2b5\ub2c8\ub2e4.\\n\\n## \ub300\uc0c1 \ub3c5\uc790\\n\\n\ub9ac\uc561\ud2b8\uc5d0 \uc785\ubb38\ud558\uc2dc\ub294 \ubd84\uaed8 \ucd94\ucc9c\ud569\ub2c8\ub2e4. \ub610\ud55c, \ud504\ub860\ud2b8\uc5d4\ub4dc \uac1c\ubc1c\uc790\ub97c \ubaa9\ud45c\ub85c \ud558\uc2dc\ub294 \ubd84\ub4e4\uaed8 \ucd94\ucc9c\ud569\ub2c8\ub2e4. \uac1c\ubc1c\uc790\uc640 \ud568\uaed8 \uc77c\ud558\uc2dc\ub294 \ubd84\ub4e4\uaed8\ub3c4 \ucd94\ucc9c\ud569\ub2c8\ub2e4. \ud2b9\ud788 \ube44\uac1c\ubc1c\uc790\uc778 \uae30\ud68d\uc790, \ub514\uc790\uc774\ub108\ubd84\ub4e4\uaed8\ub3c4 \ud070 \ub3c4\uc6c0\uc774 \ub420 \uac83 \uac19\uc2b5\ub2c8\ub2e4."},{"id":"/grokking-simplicity","metadata":{"permalink":"/en/blog/grokking-simplicity","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2022-05-08-grokking-simplicity/grokking-simplicity.md","source":"@site/blog/2022-05-08-grokking-simplicity/grokking-simplicity.md","title":"\ud83d\udcd6 \uc3d9\uc3d9 \ub4e4\uc5b4\uc624\ub294 \ud568\uc218\ud615 \ucf54\ub529","description":"\uc2ec\ud50c\ud55c \ucf54\ub4dc\ub85c \ubcf5\uc7a1\ud55c \uc18c\ud504\ud2b8\uc6e8\uc5b4 \uae38\ub4e4\uc774\uae30","date":"2022-05-08T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \uc3d9\uc3d9 \ub4e4\uc5b4\uc624\ub294 \ud568\uc218\ud615 \ucf54\ub529","date":"2022-05-08","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/grokking-simplicity","description":"\uc2ec\ud50c\ud55c \ucf54\ub4dc\ub85c \ubcf5\uc7a1\ud55c \uc18c\ud504\ud2b8\uc6e8\uc5b4 \uae38\ub4e4\uc774\uae30","keywords":["\ud568\uc218\ud615 \ud504\ub85c\uadf8\ub798\ubc0d"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \uc18c\ubb38\ub09c \uba85\uac15\uc758 \uc18c\ud50c\uc758 \ucc98\uc74c \ub9cc\ub09c \ub9ac\uc561\ud2b8","permalink":"/en/blog/first-met-react"},"nextItem":{"title":"\ud83d\udcd6 \ub3c4\uba54\uc778 \uc8fc\ub3c4 \uac1c\ubc1c \uc2dc\uc791\ud558\uae30","permalink":"/en/blog/start-DDD"}},"content":":::info\\n\uc81c\uc774\ud38d \\\\<\ubbff\uace0 \ubcf4\ub294 IT \uc804\ubb38\uc11c \ub9ac\ubdf0\uc5b4\\\\> \ud65c\ub3d9\uc744 \uc704\ud574\uc11c \ucc45\uc744 \uc81c\uacf5\ubc1b\uc544 \uc791\uc131\ub41c \uc11c\ud3c9\uc785\ub2c8\ub2e4.\\n:::\\n\\n## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/grokking-simplicity.jpg)](https://www.kyobobook.co.kr/product/detailViewKor.laf?ejkGb=KOR&mallGb=KOR&barcode=9791191600759&orderClick=LEa&Kc=)\\n\\n- \uc81c\ubaa9: \uc3d9\uc3d9 \ub4e4\uc5b4\uc624\ub294 \ud568\uc218\ud615 \ucf54\ub529\\n- \uc800\uc790: Eric Normand\\n- \uc5ed\uc790: \uae40\uc740\ubbfc\\n- \ucd9c\ud310\uc0ac: \uc81c\uc774\ud38d\\n- \ucd9c\uac04: 2022-04-22\\n\\n{/* truncate */}\\n\\n## Intro\\n\\n\ucd5c\uadfc \ub4e4\uc5b4 \ud568\uc218\ud615 \ud504\ub85c\uadf8\ub798\ubc0d\uc740 \uc774\uc288\uc600\uc8e0. \uadf8\uc5d0 \ub530\ub77c \uc810\uc810 \uba85\ub839\ud615 \uc5b8\uc5b4\uc5d0\ub294 \ud568\uc218\ud615 \ud504\ub85c\uadf8\ub798\ubc0d\uc758 \uac1c\ub150\ub4e4\uc774 \ub4e4\uc5b4\uac00\uae30 \uc2dc\uc791\ud588\uc2b5\ub2c8\ub2e4. \uc608\ub97c \ub4e4\uc5b4 \ub300\ud45c\uc801\uc73c\ub85c \uc4f0\uc774\ub294 \ub78c\ub2e4 \ud568\uc218\ub098 \uc81c\ub108\ub808\uc774\ud130\uac00 \uc788\uaca0\ub124\uc694. \uc5b4\uca0c\ub4e0 \uc804 \uadf8\ub7f0 \uc774\uc288\ub3c4 \uc788\uc5c8\uc9c0\ub9cc \ud568\uc218\ud615 \ud504\ub85c\uadf8\ub798\ubc0d\uc5d0 \ub300\ud574 \uad00\uc2ec\uc774 \uc0dd\uae34 \uac74 \ub2e4\ub978 \uc774\uc720\uac00 \uc788\uc5c8\uc2b5\ub2c8\ub2e4. \ub370\uc774\ud130 \uc5d4\uc9c0\ub2c8\uc5b4\ub9c1 \uacf5\ubd80\ub97c \ud558\ub2e4 \ubcf4\ub2c8 *Spark*\ub77c\ub294 \ud504\ub808\uc784\uc6cc\ud06c\ub97c \uacf5\ubd80\ud574\uc57c \ud588\uace0, \uadf8\uc5d0 \ub9de\uac8c *Scala*\ub77c\ub294 \uc5b8\uc5b4\ub3c4 \uacf5\ubd80\ud574\uc57c \ud588\uc8e0. \uadf8 \uc2a4\uce7c\ub77c\ub77c\ub294 \uc5b8\uc5b4\uac00 \ud568\uc218\ud615 \ud504\ub85c\uadf8\ub798\ubc0d\uc744 \uc798 \uc9c0\uc6d0\ud558\ub294 \uc5b8\uc5b4\uc785\ub2c8\ub2e4. \uadf8\ub807\uac8c \ud568\uc218\ud615 \ud504\ub85c\uadf8\ub798\ubc0d\uc5d0 \ub300\ud574 \uad00\uc2ec\uc774 \uc0dd\uae30\uac8c \ub410\uace0 \uc774 \ucc45\uc744 \uc120\ud0dd\ud558\uac8c \ub410\uc2b5\ub2c8\ub2e4.\\n\\n## Book Review\\n\\n### \ub6f0\uc5b4\ub09c \uc608\uc81c, \uc0c1\uc138\ud55c \uc124\uba85\\n\\n\uc774 \ucc45\uc740 \ud568\uc218\ud615 \ud504\ub85c\uadf8\ub798\ubc0d\uc744 \uc9c1\uc811 \uc0ac\uc6a9\ud574\ubcf8 \ud504\ub85c\uadf8\ub798\uba38\uac00 \uacbd\ud5d8\ud55c \ub0b4\uc6a9\uc744 \ubc14\ud0d5\uc73c\ub85c \uc2e4\uc6a9\uc801\uc778 \ub0b4\uc6a9\uc744 \ub2e4\ub8f9\ub2c8\ub2e4. \uadf8\ub807\uae30\uc5d0 \uc2e4\uc81c\ub85c \uc77c\uc5b4\ub0a0 \ubc95\ud55c \uc608\uc81c\ub97c \uac00\uc838\uc640 \ud568\uc218\ud615 \ud504\ub85c\uadf8\ub798\ubc0d\uc744 \uc801\uc6a9\ud574\ubd05\ub2c8\ub2e4. \uc608\uc81c\ub97c \uc124\uba85\ud558\uace0 \ud6c4\uc5d0 \ud568\uc218\ud615 \ud504\ub85c\uadf8\ub798\ubc0d\uc744 \ud558\uae30 \uc704\ud55c \uc0ac\uace0\ub97c \uc124\uba85\ud558\ub294 \ud615\uc2dd\uc785\ub2c8\ub2e4. \ub610\ud55c, \uac04\ub2e8\ud558\uac8c \uc0dd\uac01\ud560 \uac70\ub9ac\ub3c4 \uc8fc\uc5b4\uc9c0\uba74\uc11c \uc5f0\uc2b5\ubb38\uc81c\ub3c4 \ud480\uc5b4\ubcfc \uc218 \uc788\ub3c4\ub85d \ub418\uc5b4 \uc788\uc2b5\ub2c8\ub2e4. \uc774 \uc810\uc774 \uc0c1\ub2f9\ud788 \uc778\uc0c1 \uae4a\uc5c8\uc2b5\ub2c8\ub2e4.\\n\\n\\n### \uc544\ud0a4\ud14d\ucc98, \uc88b\uc740 \ucf54\ub4dc \ub4f1\uc744 \uc0dd\uac01\ud558\uac8c \ub9cc\ub4dc\ub294 \ucc45\\n\\n\uc544\ud0a4\ud14d\ucc98\ub97c \uc124\uacc4\ud558\uace0 \uc88b\uc740 \ucf54\ub4dc\ub97c \uc791\uc131\ud558\ub294 \ub370\ub3c4 \ub3c4\uc6c0\uc774 \ub41c\ub2e4\uace0 \uc0dd\uac01\ud558\ub294 \ucc45\uc785\ub2c8\ub2e4. \ub354 \uc88b\uc740 \ucf54\ub4dc\ub97c \uc791\uc131\ud558\uae30 \uc704\ud55c \uc0dd\uac01, \uc18c\ud504\ud2b8\uc6e8\uc5b4\ub97c \uc124\uacc4\ud55c\ub2e4\ub294 \uad00\uc810 \ub4f1\uc744 \uc124\uba85\ud558\ub294 \ubd80\ubd84\uc5d0\uc11c *\ud074\ub9b0 \ucf54\ub4dc*, *\ud074\ub9b0 \uc544\ud0a4\ud14d\ucc98*\uc640 \uac19\uc740 \ucc45\ub4e4\uacfc \uacb0\uc774 \ube44\uc2b7\ud558\ub2e4\uace0 \ub290\uaf08\uc2b5\ub2c8\ub2e4. \uc774 \ucc45\uc740 \uc800\ub7f0 \ucc45\ub4e4\uc5d0\uc11c \ud568\uc218\ud615 \ud504\ub85c\uadf8\ub798\ubc0d\uc744 \uacf5\ubd80\ud588\ub358 \uac83\ubcf4\ub2e4 \ub354 \uc790\uc138\ud788 \uacf5\ubd80\ud574\ubcf4\uace0 \uc2f6\uc740 \uc0ac\ub78c\ub4e4\uc5d0\uac8c \ucd94\ucc9c\ud560 \uc218 \uc788\uaca0\uc2b5\ub2c8\ub2e4.\\n\\n### \ub3c5\uc790\ub97c \uc2e0\uacbd \uc4f4 \ucc45\\n\\n\ucc45\uc744 \uc77d\uc73c\uba70 \uc800\uc790\uac00 \ub3c5\uc790\ub97c \uc5bc\ub9c8\ub098 \uc2e0\uacbd\uc744 \ub9ce\uc774 \uc37c\ub294\uc9c0 \uc54c \uc218 \uc788\uc5c8\uc2b5\ub2c8\ub2e4. \ucc45\uc744 \uc77d\ub2e4\uac00 \uad81\uae08\ud560 \uc218 \uc788\ub294 \ubd80\ubd84\uc744 \uac70\uc758 \ubaa8\ub450 \uc9c8\ubb38\uacfc \ub2f5\uc758 \ud615\uc2dd\uc73c\ub85c *\uc26c\ub294 \uc2dc\uac04*\uc774\ub77c\ub294 \ubd80\ubd84\uc744 \ub530\ub85c \ub9cc\ub4e4\uc5b4 \uc124\uba85\ud569\ub2c8\ub2e4. \ub610\ud55c, \ucf54\ub4dc\uac00 \uc5b4\ub5bb\uac8c \ubcc0\ud654\ub418\uc5c8\ub294\uc9c0 \ube44\uad50\ub97c \ud558\uba70 \ubcf4\uc5ec\uc8fc\uae30\ub3c4 \ud558\uace0, \ub3c5\uc790\uac00 \uc5f4\uc2ec\ud788 \uacf5\ubd80\ud588\ub294\uc9c0 \ud655\uc778\ud558\ub294 \uc5f0\uc2b5 \ubb38\uc81c\ub3c4 \uc8fc\uc5b4\uc9d1\ub2c8\ub2e4. \uc5f0\uc2b5 \ubb38\uc81c \uc774\uc678\uc5d0\ub3c4 \uc0dd\uac01\ud560 \uac70\ub9ac\ub97c \ub9ce\uc774 \uc8fc\uc5b4\uc9c0\ub2c8 \uc77d\ub294\ub370 \uc18d\ub3c4\ub294 \ub290\ub9ac\ub354\ub77c\ub3c4 \ud655\uc2e4\ud558\uac8c \uac1c\ub150\uc744 \uacf5\ubd80\ud574\ubcfc \uc218 \uc788\uc5c8\uc2b5\ub2c8\ub2e4.\\n\\n## \ub300\uc0c1 \ub3c5\uc790\\n\\n\ud55c \ubc88\uc774\ub77c\ub3c4 \ud568\uc218\ud615 \ud504\ub85c\uadf8\ub798\ubc0d\uc5d0 \ub300\ud574 \uacf5\ubd80\ud574\ubcf8 \uacbd\ud5d8\uc774 \uc788\uc73c\uc2dc\uace0, \uc6f9\uc5d0 \ub300\ud55c \uc774\ud574\uac00 \uc5b4\ub290 \uc815\ub3c4 \uc788\uc73c\uc2e0 \ubd84\ub4e4\uaed8 \ucd94\ucc9c\ud569\ub2c8\ub2e4. \uc774 \ucc45\uc740 \uc790\ubc14\uc2a4\ud06c\ub9bd\ud2b8\ub97c \uae30\uc900\uc73c\ub85c \uc124\uba85\ud558\uae30 \ub54c\ubb38\uc5d0 \uc6f9 \uac1c\ubc1c\uc790\ub294 \uc77d\ub294\ub370 \ud070 \uc5b4\ub824\uc6c0\uc774 \uc5c6\uc744 \uac70\ub77c \uc0dd\uac01\ud569\ub2c8\ub2e4. \uc704\uc5d0\uc11c\ub3c4 \uc5b8\uae09\ud55c *\ud074\ub9b0 \ucf54\ub4dc*\ub77c\ub294 \ucc45\uc744 \uc77d\uc5b4\ubcf4\uace0\ub3c4 \ud568\uc218\ud615 \ud504\ub85c\uadf8\ub798\ubc0d\uc744 \ub354 \uc790\uc138\ud788 \uacf5\ubd80\ud574\ubcf4\uace0 \uc2f6\ub2e4\uace0 \ub290\ub080 \ubd84\ub4e4\uc5d0\uac8c\ub3c4 \ucd94\ucc9c\ud574 \ub4dc\ub9bd\ub2c8\ub2e4."},{"id":"/start-DDD","metadata":{"permalink":"/en/blog/start-DDD","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2022-04-24-start-DDD/start-DDD.md","source":"@site/blog/2022-04-24-start-DDD/start-DDD.md","title":"\ud83d\udcd6 \ub3c4\uba54\uc778 \uc8fc\ub3c4 \uac1c\ubc1c \uc2dc\uc791\ud558\uae30","description":"DDD \ud575\uc2ec \uac1c\ub150 \uc815\ub9ac\ubd80\ud130 \uad6c\ud604\uae4c\uc9c0","date":"2022-04-24T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \ub3c4\uba54\uc778 \uc8fc\ub3c4 \uac1c\ubc1c \uc2dc\uc791\ud558\uae30","date":"2022-04-24","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/start-DDD","description":"DDD \ud575\uc2ec \uac1c\ub150 \uc815\ub9ac\ubd80\ud130 \uad6c\ud604\uae4c\uc9c0","keywords":["\ub3c4\uba54\uc778","\ub3c4\uba54\uc778 \uc8fc\ub3c4 \uac1c\ubc1c","DDD"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \uc3d9\uc3d9 \ub4e4\uc5b4\uc624\ub294 \ud568\uc218\ud615 \ucf54\ub529","permalink":"/en/blog/grokking-simplicity"},"nextItem":{"title":"\ud83d\udcd6 \ub525\ub7ec\ub2dd \ud30c\uc774\ud1a0\uce58 \uad50\uacfc\uc11c","permalink":"/en/blog/deep-learning-pytorch-textbook"}},"content":":::info\\n\ud55c\ube5b\ubbf8\ub514\uc5b4 \\\\<\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4\\\\> \ud65c\ub3d9\uc744 \uc704\ud574\uc11c \ucc45\uc744 \uc81c\uacf5\ubc1b\uc544 \uc791\uc131\ub41c \uc11c\ud3c9\uc785\ub2c8\ub2e4.\\n:::\\n\\n![\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4 2022](../assets/I-am-reviewer-2022.jpg)\\n\\n## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/start-DDD.jpg)](https://www.kyobobook.co.kr/product/detailViewKor.laf?ejkGb=KOR&mallGb=KOR&barcode=9791162245385&orderClick=LEa&Kc=)\\n\\n- \uc81c\ubaa9: \ub3c4\uba54\uc778 \uc8fc\ub3c4 \uac1c\ubc1c \uc2dc\uc791\ud558\uae30\\n- \uc800\uc790: \ucd5c\ubc94\uade0\\n- \ucd9c\ud310\uc0ac: \ud55c\ube5b\ubbf8\ub514\uc5b4\\n- \ucd9c\uac04: 2022-03-21\\n\\n{/* truncate */}\\n\\n## Intro\\n\\n\ud559\uad50 \uc120\ubc30\uac00 \ubc31\uc5d4\ub4dc \uacf5\ubd80\ub97c \ud560 \uac70\ub77c\uba74 *\ucd5c\ubc94\uade0*\ub2d8\uc758 *DDD Start!*\ub77c\ub294 \ucc45\uc744 DDD \uc785\ubb38\uc11c\ub85c \ucd94\ucc9c\ud55c\ub2e4\uace0 \ub9d0\ud588\ub358 \uae30\uc5b5\uc774 \uc788\uc2b5\ub2c8\ub2e4. \uadfc\ub370 \uc544\uc27d\uac8c\ub3c4 \uc9c0\uae08\uc740 \uc808\ud310\ub410\uace0 \uc9c0\uae08\uc740 \ub9c9 \ub9ac\uc140\uac00\ub85c \ud314\ub9ac\uae30 \ub54c\ubb38\uc5d0 \ub3c4\uc11c\uad00\uc774\ub77c\ub3c4 \uac00\uc11c \uaf2d \uc77d\uc5b4\ubcf4\ub77c\uace0 \ub9d0\ud558\ub354\ub77c\uace0\uc694. \ubc31\uc5d4\ub4dc \uacf5\ubd80\ud558\ub2e4 \ubcf4\uba74 \uc5b8\uc820\uac00 \uc77d\uc5b4\ubcf4\uaca0\uc9c0 \uc2f6\uc5c8\ub294\ub370 \uc774\ubc88\uc5d0 \ud55c\ube5b\ubbf8\ub514\uc5b4\uc5d0\uc11c \uc0c8\ub86d\uac8c \ub098\uc624\uac8c \ub418\uc5b4 \uc77d\uc5b4\ubcf4\uac8c \ub410\uc2b5\ub2c8\ub2e4.\\n\\n## Book Review\\n\\n### \uae30\ubcf8 \uc9c0\uc2dd\\n\\n\uc774 \ucc45\uc758 \uc608\uc81c \ucf54\ub4dc\ub294 \uc790\ubc14\ub85c \uc4f0\uc5ec\uc838 \uc788\uc73c\uba70 \uc2a4\ud504\ub9c1 MVC\uc640 JPA\ub97c \uae30\ubc18\uc73c\ub85c \uad6c\ud604\ub418\uc5b4 \uc788\uc2b5\ub2c8\ub2e4. \ucd08\ubc18\uc5d0\ub294 \uac1c\ub150\uc5d0 \ub300\ud55c \ub0b4\uc6a9\uc774 \ub9ce\uc544\uc11c \ucf54\ub4dc\uc5d0 \ub300\ud574 \uc815\ud655\ud788 \ubaa8\ub974\uc2dc\ub294 \ubd84\uc774 \uc77d\uc5b4\ub3c4 \uad1c\ucc2e\uc9c0\ub9cc, \ud6c4\ubc18\uc73c\ub85c \uac08\uc218\ub85d \ucf54\ub4dc\ub85c \uad6c\ud604\ud558\uba74\uc11c \uc124\uba85\ud558\ub294 \ubd80\ubd84\uc774 \ub9ce\uace0 \ubd88\ud544\uc694\ud55c \uc124\uba85\uc740 \uc0dd\ub7b5\ub418\uc5b4 \uc788\uc73c\ub2c8 Spring\uacfc JPA\uc5d0 \ub300\ud55c \uacbd\ud5d8\uc774 \uc5b4\ub290\uc815\ub3c4 \uc788\uc73c\uc2e0 \ubd84\uaed8 \ucd94\ucc9c\ub4dc\ub9ac\ub294 \ucc45\uc785\ub2c8\ub2e4.\\n\\n### \uc2e0\uc785 \uac1c\ubc1c\uc790\\n\\n\uc774 \ucc45\uc740 DDD \uace0\uc218\uac00 \ub418\ub294 \ubc95\uc744 \uc54c\ub824\uc8fc\ub294 \ucc45\uc774 \uc544\ub2c8\uba70 DDD\ub97c \ucc98\uc74c \uc811\ud558\ub294 \uc785\ubb38\uc790\ub97c \ub300\uc0c1\uc73c\ub85c \ud558\ub294 \ucc45\uc774\ub77c\uace0 \ud569\ub2c8\ub2e4. DDD \uc785\ubb38\uc790\uac00 \uc77d\uae30\uc5d0\ub294 \uc88b\uc740 \ucc45\uc774\uc9c0\ub9cc \ubc31\uc5d4\ub4dc \uac1c\ubc1c\uc790\ub97c \ubaa9\ud45c\ub85c \ud558\ub294 \ub300\ud559\uc0dd \uc785\uc7a5\uc5d0\uc11c\ub294 \uc2e0\uc785 \uac1c\ubc1c\uc790\uac00 \uc77d\uae30\uc5d0\ub294 \uc880 \uc5b4\ub824\uc6b4 \ucc45\uc774\ub77c\uace0 \ub290\uaf08\uc2b5\ub2c8\ub2e4. \ud6c4\ubc18\uc73c\ub85c \uac00\uba74 \uac08\uc218\ub85d \uc81c\uac00 JPA\uc5d0 \ub300\ud55c \uc774\ud574\uac00 \ub0ae\uc544\uc11c \uadf8\ub7f0\uc9c0 \uc810\uc810 \uc77d\uae30 \ud798\ub4e4\uc5c8\uc2b5\ub2c8\ub2e4. \ub610\ud55c, DDD\uc5d0 \ub300\ud55c \uac1c\ub150\uc801\uc778 \ub0b4\uc6a9\uc740 \ub300\ub7b5\uc801\uc73c\ub85c \uc774\ud574\uac00 \ub410\uc73c\ub098 \uc774\uac78 \uc5b4\ub5bb\uac8c \uad6c\ud604\ud574\uc57c \ud560 \uc9c0\ub294 \uc544\uc9c1\ub3c4 *\ub9c9\ub9c9\ud558\ub2e4*\ub77c\uace0 \ub290\uaf08\uc2b5\ub2c8\ub2e4. \uadf8\ub7ec\ub098 \uc800\uc790\uc758 \uae54\ub054\ud558\uace0 \uc790\uc138\ud55c \uc124\uba85 \ub355\ubd84\uc5d0 \ucda9\ubd84\ud788 \uacf5\ubd80\ud574\uc11c \uc77d\uc5b4\ubcfc \ub9cc\ud55c \ucc45\uc774\ub77c \uc0dd\uac01\ud569\ub2c8\ub2e4.\\n\\n## \ub300\uc0c1\ub3c5\uc790\\n\\n\ub300\uc0c1\ub3c5\uc790\ub294 \uc544\ubb34\ub798\ub3c4 \uc774 \ucc45\uc758 \uc800\uc790\uaed8\uc11c \uc124\uba85\ud558\uc2e0 \uac83\ucc98\ub7fc \ub300\ub7b5 3~4\ub144\uc758 \uac1c\ubc1c \uacbd\ud5d8\uc774 \uc788\ub2e4\uba74 \ub0b4\uc6a9\uc744 \uc774\ud574\ud558\ub294 \ub370 \uc5b4\ub824\uc6c0\uc740 \uc5c6\uc744 \uac83 \uac19\uc2b5\ub2c8\ub2e4. \ub610\ud55c, Spring\uc5d0 \uad00\uc2ec \uc788\uace0 \uc790\ubc14 \ubc31\uc5d4\ub4dc \uac1c\ubc1c\uc790\uac00 \ub418\uace0 \uc2f6\uc740 \ub300\ud559\uc0dd\uc774\ub77c\uba74 \ud55c \ubc88\ucbe4 \uc77d\uc5b4\ubcfc \ub9cc\ud55c \ucc45\uc774\ub77c \uc0dd\uac01\ud569\ub2c8\ub2e4."},{"id":"/deep-learning-pytorch-textbook","metadata":{"permalink":"/en/blog/deep-learning-pytorch-textbook","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2022-04-07-deep-learning-pytorch-textbook/deep-learning-pytorch-textbook.md","source":"@site/blog/2022-04-07-deep-learning-pytorch-textbook/deep-learning-pytorch-textbook.md","title":"\ud83d\udcd6 \ub525\ub7ec\ub2dd \ud30c\uc774\ud1a0\uce58 \uad50\uacfc\uc11c","description":"\uae30\ubcf8\uae30\uc5d0 \ucda9\uc2e4\ud55c \ub525\ub7ec\ub2dd \uc785\ubb38\uc11c!","date":"2022-04-07T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \ub525\ub7ec\ub2dd \ud30c\uc774\ud1a0\uce58 \uad50\uacfc\uc11c","date":"2022-04-07","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/deep-learning-pytorch-textbook","description":"\uae30\ubcf8\uae30\uc5d0 \ucda9\uc2e4\ud55c \ub525\ub7ec\ub2dd \uc785\ubb38\uc11c!","keywords":["\ud30c\uc774\ud1a0\uce58","\ub525\ub7ec\ub2dd"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \ub3c4\uba54\uc778 \uc8fc\ub3c4 \uac1c\ubc1c \uc2dc\uc791\ud558\uae30","permalink":"/en/blog/start-DDD"},"nextItem":{"title":"\ud83d\udcd6 \ud504\ub85c\uadf8\ub798\uba38\uc758 \ub1cc","permalink":"/en/blog/the-programmers-brain"}},"content":":::info\\n\uae38\ubc97 \u201817\ucc28 \uac1c\ubc1c\uc790 \ub9ac\ubdf0\uc5b4\u2019 \ud65c\ub3d9\uc744 \uc704\ud574\uc11c \ucc45\uc744 \uc81c\uacf5\ubc1b\uc544 \uc791\uc131\ub41c \uc11c\ud3c9\uc785\ub2c8\ub2e4.\\n:::\\n\\n## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/deep-learning-pytorch-textbook.jpg)](https://www.kyobobook.co.kr/product/detailViewKor.laf?ejkGb=KOR&mallGb=KOR&barcode=9791165218942&orderClick=LAG&Kc=)\\n\\n- \uc81c\ubaa9: \ub525\ub7ec\ub2dd \ud30c\uc774\ud1a0\uce58 \uad50\uacfc\uc11c\\n- \uc800\uc790: \uc11c\uc9c0\uc601\\n- \ucd9c\ud310\uc0ac: \uae38\ubc97\\n- \ucd9c\uac04: 2022-03-04\\n\\n{/* truncate */}\\n\\n## Intro\\n\\n\ucd5c\uadfc \ud30c\uc774\ud1a0\uce58\ub97c \uc0ac\uc6a9\ud55c \ucc45\ub4e4\uc744 \uc77d\uc73c\uba70 \uac04\ub2e8\ud55c \uac1c\ub150\ub4e4\uc740 [\ud30c\uc774\ud1a0\uce58 \ud29c\ud1a0\ub9ac\uc5bc](https://tutorials.pytorch.kr/)\uc774\ub0987 [PyTorch\ub85c \uc2dc\uc791\ud558\ub294 \ub525\ub7ec\ub2dd \uc785\ubb38](https://wikidocs.net/book/2788)\uc744 \ucc38\uace0\ud558\uba74\uc11c \uacf5\ubd80\ud588\uc5c8\uc2b5\ub2c8\ub2e4. \ud544\uc694\ud560 \ub54c\ub9c8\ub2e4 \ucc3e\uc544\ubcf4\ub294 \ud615\uc2dd\uc73c\ub85c \uacf5\ubd80\ud588\uc5c8\uc8e0. \uadf8\ub7ec\ub098 \uc55e\uc73c\ub85c \ud30c\uc774\ud1a0\uce58\ub97c \uc0ac\uc6a9\ud560 \uc77c\uc774 \ub9ce\uc544\uc9c8 \uac83 \uac19\uc544 \uae30\ubcf8 \uac1c\ub150\ub4e4\uc744 \uae54\ub054\ud558\uac8c \uc815\ub9ac\ud574\ubcf4\uba74\uc11c \uc190\uc5d0 \uc775\uc219\ud574\uc9c0\uace0 \uc2f6\uc5c8\uc2b5\ub2c8\ub2e4. \ubb3c\ub860 \uc800 \ubb38\uc11c\ub4e4\ub3c4 \ubb38\uc11c\ud654\uac00 \uc798 \ub418\uc5b4 \uc788\ub294 \uc790\ub8cc\ub4e4\uc774\uc9c0\ub9cc, \uae30\ubcf8 \uac1c\ub150\ub4e4\uc744 \uae54\ub054\ud558\uac8c \uc815\ub9ac \ud574\ubcf4\uae30\ub294 \uc5b4\ub835\ub2e4\uace0 \uc0dd\uac01\ud558\uc5ec \uc774 \ucc45\uc744 \uc77d\uac8c \ub410\uc2b5\ub2c8\ub2e4.\\n\\n## Book Review\\n\\n### \uad50\uacfc\uc11c\\n\\n\uc815\ub9d0 \uad50\uacfc\uc11c\ub77c\ub294 \uc774\ub984\uc5d0 \uc5b4\uc6b8\ub9ac\ub294 \ucc45\uc785\ub2c8\ub2e4. \uc81c \uae30\uc5b5\uc5d0\uc11c \uad50\uacfc\uc11c\ub294 \uc5b8\uc81c\ub098 \ubaa8\ub4e0 \uac78 \uc54c\ub824\uc8fc\ub294 \ucc45\uc778 \uc801\uc740 \uc5c6\uc5c8\uc2b5\ub2c8\ub2e4. \uae30\ubcf8, \uae30\ucd08\uc801\uc778 \uac83\ub4e4\ub9cc \uc54c\ub824\uc8fc\uace0 \ub354 \uc2ec\ud654\ub41c \ub0b4\uc6a9\uc5d0 \ub300\ud574\uc11c\ub294 \uc9c1\uc811 \uc0dd\uac01\ud558\uac8c \ud574\uc8fc\ub294 \uadf8\ub7f0 \ucc45\uc774\uc5c8\uc2b5\ub2c8\ub2e4. \ub364\uc73c\ub85c \ub2e4\uc74c\uc5d0 \uacf5\ubd80\ud574\ubcf4\uba74 \ub3c4\uc6c0\uc774 \ub418\ub294 \ud0a4\uc6cc\ub4dc\ub97c \uc54c\ub824\uc8fc\ub294 \ucc45\uc774\uc5c8\uc8e0. \uc804 \uadf8\ub7f0 \ucc45\ub4e4\uc774 \uc5b8\uc81c\ub098 \ub2e4\uc74c \uacf5\ubd80\ub97c \uc704\ud55c \ud638\uae30\uc2ec\uc744 \uc790\uadf9\ud588\uc5c8\uc2b5\ub2c8\ub2e4. \uc774 \ucc45 \ub610\ud55c \uadf8\ub7f0 \ub290\ub08c\uc5d0 \uac00\uae5d\uc2b5\ub2c8\ub2e4. \\n\\n### \ub2e4\uc591\ud55c \uc8fc\uc81c\\n\\n750\ud398\uc774\uc9c0\uc758 \uc5c4\uccad\ub09c \ub450\uaed8\ub85c *\ud30c\uc774\ud1a0\uce58\ub97c \uc0ac\uc6a9\ud55c \ub525\ub7ec\ub2dd \uae30\ubcf8* \uadf8 \uc790\uccb4\ub97c \ub2f4\uace0 \uc788\ub294 \ucc45\uc774\ub77c\uace0 \ud574\ub3c4 \ubd80\uc871\ud568\uc774 \ud06c\uac8c \uc5c6\ub294 \ucc45\uc785\ub2c8\ub2e4. CNN, \uc2dc\uacc4\uc5f4 \ubd84\uc11d, \uc790\uc5f0\uc5b4 \ucc98\ub9ac \ub4f1 \ub525\ub7ec\ub2dd\uc5d0\uc11c \ud574\ubcfc\ub9cc\ud55c \uc8fc\uc81c\ub294 \uac70\uc758 \ub2e4 \ub2f4\uace0 \uc788\uc744 \ubfd0\ub9cc \uc544\ub2c8\ub77c, \ub525\ub7ec\ub2dd\uc744 \uc2dc\uc791\ud558\uae30 \uc804 \uba38\uc2e0\ub7ec\ub2dd\uc5d0 \ub300\ud55c \uae30\ucd08 \uc9c0\uc2dd\ub3c4 \uc54c\ub824\uc90d\ub2c8\ub2e4. \ub2e4\uc591\ud55c \uc8fc\uc81c\ub4e4\uc774 \ubaa8\ub450 \ud30c\uc774\ud1a0\uce58\ub85c \uad6c\ud604\ub418\uc5b4 \uc788\ub294 \uac83\uc740 \uc544\ub2c8\uc9c0\ub9cc \uc804\uccb4\uc801\uc73c\ub85c \ubd24\uc744 \ub54c *\uc774\ub7f0 \uae30\uc220\ub4e4\ub3c4 \uc788\ub2e4.*\ub77c\ub294 \uac83\uc744 \ubcf4\uc5ec\uc8fc\uae30 \ub54c\ubb38\uc5d0 \uae30\uc220 \ud30c\uc545\uc5d0 \ub3c4\uc6c0\uc774 \ub410\uc2b5\ub2c8\ub2e4.\\n\\n\uc804 \uc774 \ucc45\uc5d0\uc11c \ud2b9\ud788 \uc778\uc0c1 \uae4a\uc5c8\ub358 \uc810\uc740 CNN \ud30c\ud2b8\ub97c 2\uac1c\ub85c \ub098\ub204\uc5b4 \uc790\uc138\ud558\uac8c \uc124\uba85\ud558\ub294 \ubd80\ubd84\uc774\uc5c8\uc2b5\ub2c8\ub2e4. \uc800\uc790\uaed8\uc11c \uc774 \ubd80\ubd84\uc744 \uc138\ubd80\uc801\uc73c\ub85c \uc124\uba85\ud574\uc8fc\uace0 \uc2f6\ub2e4\ub294 \uac83\uc774 \ub3cb\ubcf4\uc600\uc2b5\ub2c8\ub2e4.  \uadf8\ub7ec\ub098 \uc774 \ubd80\ubd84\uc774 \ub108\ubb34 \uc790\uc138\ud558\uac8c \uc124\uba85\ub418\uc5b4 \uc788\uc5b4 RNN, \uc784\ubca0\ub529, \uc0dd\uc131 \ubaa8\ub378 \ub4f1\uc758 \ubd80\ubd84\uc774 \uad6c\ud604\ud558\ub294 \ub370 \uc788\uc5b4 \uc124\uba85\uc774 \uc870\uae08 \uc544\uc27d\ub2e4\uace0 \uc0dd\uac01\ud560 \uc218\ub3c4 \uc788\uc744 \uac83 \uac19\uc2b5\ub2c8\ub2e4.  \\n\\n### \uadf8\ub9bc\\n\\n\uc774 \ucc45\uc758 \uac00\uc7a5 \ud070 \uc7a5\uc810 \uc911 \ud558\ub098\ub294 \uadf8\ub9bc\uc774\ub77c\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4. \ub3c5\uc790\uac00 \uc774\ud574\ud558\uae30 \ud798\ub4e0 \ubd80\ubd84\ub4e4\ub3c4 \ubaa8\ub450 \uadf8\ub9bc\uc744 \ud1b5\ud574 \uc790\uc138\ud558\uac8c \uc124\uba85\ud569\ub2c8\ub2e4. \ub610\ud55c, \uadf8\ub9bc\uc774 \uceec\ub7ec\uc778 \uac83\ub3c4 \uaf64 \uc778\uc0c1 \uae4a\uc5c8\uc2b5\ub2c8\ub2e4. \uadf8\ub9bc\uc744 \ud1b5\ud574 \uae54\ub054\ud558\uac8c \uc2dc\uac01\ud654 \ub418\uc5b4 \uc788\uc5b4 \uc774\ud574\ud558\uae30 \uc26c\uc6e0\uc2b5\ub2c8\ub2e4.\\n\\n## \ub300\uc0c1 \ub3c5\uc790\\n\\n\ub525\ub7ec\ub2dd \uc785\ubb38\uc790 \ubcf4\ub2e4\ub294 \uadf8\ub798\ub3c4 \uba87 \ubc88 \uacbd\ud5d8\ud574\ubcf8 \ucd08\uae09\uc790\uc5d0\uac8c \uc801\uc808\ud55c \ucc45\uc774\ub77c\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4. \ubc11\ubc14\ub2e5\ubd80\ud130 \uc5b4\ub290\uc815\ub3c4 \uad6c\ud604\ud574\ubcf4\uace0 \uc774 \ucc45\uc744 \uc77d\uc73c\uc2dc\uba74 \ud30c\uc774\ud1a0\uce58\ub77c\ub294 \ud504\ub808\uc784\uc6cc\ud06c\ub97c \uc785\ubb38\ud558\ub294 \ub370 \ub9e4\uc6b0 \uc88b\ub2e4\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4.\\n\\n\ub610\ud55c, \ub525\ub7ec\ub2dd \uacf5\ubd80\ub97c \uc774\ub860\uc801\uc73c\ub85c\ub9cc \ud558\uc154\uc11c \uc2e4\uc81c \uad6c\ud604\uc5d0 \uc790\uc2e0\uc774 \uc5c6\uc73c\uc2e0 \ubd84\ub4e4\uaed8\ub3c4 \ucd94\ucc9c\ud569\ub2c8\ub2e4. \uc774 \ucc45\uc740 \uad6c\ud604\uc5d0 \ud070 \uc911\uc810\uc744 \ub454 \ucc45\uc774\ub77c \uc0c1\ub2f9\ud788 \ub3c4\uc6c0\uc774 \ub9ce\uc774 \ub420 \uac83\uc785\ub2c8\ub2e4."},{"id":"/the-programmers-brain","metadata":{"permalink":"/en/blog/the-programmers-brain","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2022-04-05-the-programmers-brain/the-programmers-brain.md","source":"@site/blog/2022-04-05-the-programmers-brain/the-programmers-brain.md","title":"\ud83d\udcd6 \ud504\ub85c\uadf8\ub798\uba38\uc758 \ub1cc","description":"\ud6cc\ub96d\ud55c \ud504\ub85c\uadf8\ub798\uba38\uac00 \uc54c\uc544\uc57c \ud560 \uc778\uc9c0\uacfc\ud559\uc758 \ubaa8\ub4e0 \uac83","date":"2022-04-05T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \ud504\ub85c\uadf8\ub798\uba38\uc758 \ub1cc","date":"2022-04-05","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/the-programmers-brain","description":"\ud6cc\ub96d\ud55c \ud504\ub85c\uadf8\ub798\uba38\uac00 \uc54c\uc544\uc57c \ud560 \uc778\uc9c0\uacfc\ud559\uc758 \ubaa8\ub4e0 \uac83","keywords":["\uc778\uc9c0\uacfc\ud559","\ud504\ub85c\uadf8\ub798\uba38","\ucf54\ub529"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \ub525\ub7ec\ub2dd \ud30c\uc774\ud1a0\uce58 \uad50\uacfc\uc11c","permalink":"/en/blog/deep-learning-pytorch-textbook"},"nextItem":{"title":"\ud83d\udcd6 \ud30c\uc774\uc36c \ub77c\uc774\ube0c\ub7ec\ub9ac\ub97c \ud65c\uc6a9\ud55c \uba38\uc2e0\ub7ec\ub2dd","permalink":"/en/blog/introduction-to-ml-with-python"}},"content":":::info\\n\uc81c\uc774\ud38d \\\\<\ubbff\uace0 \ubcf4\ub294 IT \uc804\ubb38\uc11c \ub9ac\ubdf0\uc5b4\\\\> \ud65c\ub3d9\uc744 \uc704\ud574\uc11c \ucc45\uc744 \uc81c\uacf5\ubc1b\uc544 \uc791\uc131\ub41c \uc11c\ud3c9\uc785\ub2c8\ub2e4.\\n:::\\n\\n## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/the-programmers-brain.jpg)](https://www.kyobobook.co.kr/product/detailViewKor.laf?ejkGb=KOR&mallGb=KOR&barcode=9791191600650&orderClick=LEa&Kc=)\\n\\n- \uc81c\ubaa9: \ud504\ub85c\uadf8\ub798\uba38\uc758 \ub1cc\\n- \uc800\uc790: Felienne Hermans\\n- \uc5ed\uc790: \ucc28\uac74\ud68c\\n- \ucd9c\ud310\uc0ac: \uc81c\uc774\ud38d\\n- \ucd9c\uac04: 2022-01-12\\n\\n{/* truncate */}\\n\\n## Intro\\n\\n\uc11c\ub860\ub9cc \ubd24\uc744 \ub54c\ub294 \uc880 \uc0dd\uc18c\ud55c \ub2e8\uc5b4\ub3c4 \ub9ce\uc774 \ubcf4\uc774\uace0, \uc778\uc9c0 \uacfc\ud559\uc5d0 \uc811\ubaa9\ud558\uc5ec \uc124\uba85\ud558\ub294 \ubaa8\uc2b5\uc774 \ubcf4\uc5ec \uc0c1\ub2f9\ud788 \uace0\ubbfc\ub410\uc9c0\ub9cc \ub3c4\uc6c0\uc774 \ub420 \uac83 \uac19\uc544 \uc77d\uac8c \ub410\uc2b5\ub2c8\ub2e4. \ub610\ud55c, \uc804\uccb4\uc801\uc778 \ud504\ub85c\uadf8\ub798\ubc0d\uc744 \uc9c4\ud589\ud558\uba74\uc11c \uc5b4\ub5bb\uac8c \ub1cc\uac00 \uc778\uc9c0\ud558\ub294\uc9c0 \uadf8 \uacfc\uc815\uc744 \uc774 \ucc45\uc5d0\uc11c\ub294 \uc5b4\ub5bb\uac8c \uc124\uba85\ud558\ub294\uac00 \uad81\uae08\ud558\uc5ec \uc77d\uae30\ub85c \uacb0\uc2ec\ud588\uc2b5\ub2c8\ub2e4.\\n\\n## Book Review\\n\\n### \uacf5\ubd80\\n\\n\ud504\ub85c\uadf8\ub798\ubc0d\uc774\ub4e0\uc9c0 \ucc45\uc774\ub4e0\uc9c0 \ubb50\ub4e0 \uac04\uc5d0 \uadf8\uc800 \ud55c \ubc88 \uc77d\uc5b4\ubcf4\uace0, \uac80\uc0c9\ud558\uba70 \uacf5\ubd80\ud55c\ub2e4\uace0 \ud574\uc11c \uc790\uc2e0\uc774 \uc544\ub294 \uac83\uc774 \uc544\ub2c8\ub77c\ub294 \uac78 \ub2e4\uc2dc \ud55c\ubc88 \uae68\ub2eb\uac8c \ud574\uc90d\ub2c8\ub2e4. \uc774 \ucc45\uc5d0\uc11c\ub294 \ucf54\ub4dc\ub97c \uc778\uc2dd\ud560 \ub54c\uc758 \ub1cc\uac00 \uc5b4\ub5bb\uac8c \uc791\ub3d9\ud558\ub294\uc9c0, \uadf8\ub9ac\uace0 \ucf54\ub4dc\ub97c \uc5b4\ub5bb\uac8c \ud558\uba74 \ube60\ub974\uac8c \ud30c\uc545\ud558\uace0 \uc77d\uc744 \uc218 \uc788\ub294\uc9c0\uc5d0 \ub300\ud574 \uc124\uba85\ud569\ub2c8\ub2e4. \uc774\ub7f0 \ubd80\ubd84\ub4e4\uc774 \uc0ac\uc2e4 \ucf54\ub529\ubfd0\ub9cc \uc544\ub2c8\ub77c \ub2e4\uc591\ud55c \uacf5\ubd80\uc5d0\ub3c4 \uc811\ubaa9\ud560 \uc218 \uc788\ub294 \ubd80\ubd84\uc774\ub77c \uc81c\uac00 \uc9c0\uae08\uae4c\uc9c0 \ud588\ub358 \uacf5\ubd80\ub4e4\uc744 \ubc18\uc131\ud558\uae30\ub3c4 \ud558\uba74\uc11c \uaf64 \uc7ac\ubc0c\uac8c \uc77d\uc5c8\uc2b5\ub2c8\ub2e4.\\n\\n### \uacfc\ud559\uc801\\n\\nIntro\uc5d0\uc11c\ub3c4 \uc124\uba85\ud588\ub4ef\uc774 \uc774 \ucc45\uc740 \uc804\uccb4\uc801\uc778 \ud504\ub85c\uadf8\ub798\ubc0d \uacfc\uc815\uc744 \uc778\uc9c0 \uacfc\ud559\uc5d0 \uc811\ubaa9\ud558\uc5ec \uc124\uba85\ud569\ub2c8\ub2e4. \uba87\uba87 \uc0dd\uc18c\ud55c \ub2e8\uc5b4\ub4e4\uc774 \ubcf4\uc5ec \uc77d\uae30 \uc5b4\ub824\uc6e0\uc73c\ub098, \ubaa8\ub450 \uacfc\ud559\uc801\uc73c\ub85c \uc785\uc99d\ub41c \uc5f0\uad6c \uacb0\uacfc\ub97c \uae30\ubc18\uc73c\ub85c \uc124\uba85\ud558\uae30\uc5d0 \ud765\ubbf8\ub85c\uc6e0\uc2b5\ub2c8\ub2e4. \uc8fc\uc11d\uc73c\ub85c \uc5b4\ub5a4 \uc5f0\uad6c\ub97c \uc9c4\ud589\ud588\ub294\uc9c0 \ubaa8\ub450 \ub098\uc640 \uc788\uae30\uc5d0 \uc2e0\ube59\uc131\uc774 \ub192\uc544 \ud070 \uac71\uc815 \uc5c6\uc774 \uc77d\uc5c8\uc2b5\ub2c8\ub2e4.\\n\\n### \ucf54\ub4dc\\n\\n\uc0ac\uc2e4 \uc608\uc804\uc5d0\ub3c4 \uc5b4\ub290 \ucc45\uc5d0\uc11c \uc88b\uc740 \ucf54\ub4dc\ub97c \uc791\uc131\ud558\ub294 \ubc29\ubc95\uc5d0 \ub300\ud574 \uc77d\uc740 \uc801\uc774 \uc788\uc2b5\ub2c8\ub2e4. \uadf8 \ucc45\uc740 \ub2e8\uc21c\ud788 \\"\uc774\ub7f0 \ucf54\ub4dc\ub294 \uc88b\uace0 \uc774\ub7f0 \ucf54\ub4dc\ub294 \uc88b\uc9c0 \uc54a\ub2e4\\"\ub97c \uc608\uc2dc\ub97c \ub4e4\uc5b4 \uc124\uba85\ud588\uc9c0\ub9cc, \uc774 \ucc45\uc740 \uc608\uc2dc\ub97c \ub4e4\uc5b4 \uc124\uba85\ud558\uae30\ub3c4 \ud558\uc9c0\ub9cc, \uc778\uc9c0\uacfc\ud559\uc801\uc73c\ub85c \uc124\uba85\ud558\ub294 \ubd80\ubd84\uc774 \uc778\uc0c1 \uae4a\uc5c8\uc2b5\ub2c8\ub2e4.\\n\\n## \ub300\uc0c1 \ub3c5\uc790\\n\\n\uc774 \ucc45\uc740 \uc804\uccb4\uc801\uc778 \ud504\ub85c\uadf8\ub798\ubc0d \uacfc\uc815\uc744 \uc778\uc9c0 \uacfc\ud559\uc5d0 \uc811\ubaa9\ud558\uc5ec \uc124\uba85\ud569\ub2c8\ub2e4. \uadf8\ub807\uae30\uc5d0 \uc790\uc2e0\uc774 \ud504\ub85c\uadf8\ub798\uba38\ub85c\uc11c \ubb34\uc758\uc2dd\uc801\uc73c\ub85c \ud558\uace0 \uc788\ub294 \uc77c\ub4e4\uc774 \ub1cc\uc5d0\uc11c \uc5b4\ub5bb\uac8c \uc77c\uc5b4\ub098\ub294\uc9c0 \uad81\uae08\ud558\uc2e0 \ubd84\ub4e4\uaed8 \ucd94\ucc9c\ud574 \ub4dc\ub9bd\ub2c8\ub2e4.\\n\\n\uc81c \uae30\uc900\uc73c\ub85c \ubbf8\ubb18\ud558\uac8c \uc774\ud574\uac00 \uc548 \ub418\ub294 \ubd80\ubd84\ub4e4\uc774 \uba87\uba87 \uc788\uae30\uc5d0 \uac1c\uc778\uc801\uc73c\ub85c \uc2e4\ubb34\ub97c \uacbd\ud5d8\ud574\ubcf8 \ubd84\ub4e4\uaed8 \ub354\uc6b1 \ucd94\ucc9c\ud569\ub2c8\ub2e4. \uaf2d \ud544\uc218\ub294 \uc544\ub2d9\ub2c8\ub2e4. \uc790\ubc14, \ud30c\uc774\uc36c \ucf54\ub4dc\ub97c \uc77d\uc744 \uc218 \uc788\uace0 \ucf54\ub529\uc744 \uaf64 \uacbd\ud5d8\ud574\ubcf8 \ub300\ud559\uc0dd \uc815\ub3c4\uba74 \ucda9\ubd84\ud788 \uc77d\uc5b4\ubcfc \ub9cc\ud55c \ucc45\uc778 \uac83 \uac19\uc2b5\ub2c8\ub2e4. \uc0dd\uc0b0\uc131 \uad00\uc810\uc5d0\uc11c\ub3c4 \ub3c4\uc6c0\uc774 \ub418\ub294 \ub0b4\uc6a9\uc774 \ub9ce\uae30\uc5d0 \uc0dd\uc0b0\uc131 \ud5a5\uc0c1\uc5d0 \uad00\uc2ec \uc788\uc73c\uc2e0 \ubd84\ub4e4\uaed8\ub3c4 \ucd94\ucc9c\ud574 \ub4dc\ub9bd\ub2c8\ub2e4."},{"id":"/introduction-to-ml-with-python","metadata":{"permalink":"/en/blog/introduction-to-ml-with-python","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2022-03-26-introduction-to-ml-with-python/introduction-to-ml-with-python.md","source":"@site/blog/2022-03-26-introduction-to-ml-with-python/introduction-to-ml-with-python.md","title":"\ud83d\udcd6 \ud30c\uc774\uc36c \ub77c\uc774\ube0c\ub7ec\ub9ac\ub97c \ud65c\uc6a9\ud55c \uba38\uc2e0\ub7ec\ub2dd","description":"\uc0ac\uc774\ud0b7\ub7f0 \ud575\uc2ec \uac1c\ubc1c\uc790\uac00 \uc4f4 \uba38\uc2e0\ub7ec\ub2dd\uacfc \ub370\uc774\ud130 \uacfc\ud559 \uc2e4\ubb34\uc11c","date":"2022-03-26T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \ud30c\uc774\uc36c \ub77c\uc774\ube0c\ub7ec\ub9ac\ub97c \ud65c\uc6a9\ud55c \uba38\uc2e0\ub7ec\ub2dd","date":"2022-03-26","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/introduction-to-ml-with-python","description":"\uc0ac\uc774\ud0b7\ub7f0 \ud575\uc2ec \uac1c\ubc1c\uc790\uac00 \uc4f4 \uba38\uc2e0\ub7ec\ub2dd\uacfc \ub370\uc774\ud130 \uacfc\ud559 \uc2e4\ubb34\uc11c","keywords":["\uc0ac\uc774\ud0b7\ub7f0","\uba38\uc2e0\ub7ec\ub2dd","\ud30c\uc774\uc36c"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \ud504\ub85c\uadf8\ub798\uba38\uc758 \ub1cc","permalink":"/en/blog/the-programmers-brain"},"nextItem":{"title":"\ud83d\udcd6 Apache Airflow \uae30\ubc18\uc758 \ub370\uc774\ud130 \ud30c\uc774\ud504\ub77c\uc778","permalink":"/en/blog/data-pipelines-with-apache-airflow"}},"content":":::info\\n\ud55c\ube5b\ubbf8\ub514\uc5b4 \\\\<\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4\\\\> \ud65c\ub3d9\uc744 \uc704\ud574\uc11c \ucc45\uc744 \uc81c\uacf5\ubc1b\uc544 \uc791\uc131\ub41c \uc11c\ud3c9\uc785\ub2c8\ub2e4.\\n:::\\n\\n![\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4 2022](../assets/I-am-reviewer-2022.jpg)\\n\\n## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/introduction-to-ml-with-python.jpg)](https://www.kyobobook.co.kr/product/detailViewKor.laf?ejkGb=KOR&mallGb=KOR&barcode=9791162245279&orderClick=LEa&Kc=)\\n\\n- \uc81c\ubaa9: \ud30c\uc774\uc36c \ub77c\uc774\ube0c\ub7ec\ub9ac\ub97c \ud65c\uc6a9\ud55c \uba38\uc2e0\ub7ec\ub2dd\\n- \uc800\uc790: Andreas C. M\xfcller, Sarah Guido\\n- \uc5ed\uc790: \ubc15\ud574\uc120\\n- \ucd9c\ud310\uc0ac: \ud55c\ube5b\ubbf8\ub514\uc5b4\\n- \ucd9c\uac04: 2022-02-25\\n\\n{/* truncate */}\\n\\n## Intro\\n\\n\uac1c\uc815\ub418\uae30 \uc804\ubd80\ud130 \uc774 \ucc45\uc740 \uba38\uc2e0\ub7ec\ub2dd\uc744 \uacf5\ubd80\ud558\uae30\uc5d0 \uc0c1\ub2f9\ud788 \uc720\uba85\ud55c \ucc45 \uc911 \ud558\ub098\uc600\uc2b5\ub2c8\ub2e4. \uc5b8\uc820\uac00 \ud55c \ubc88\ucbe4 \uaf2d \uc77d\uc5b4\ubcf4\uace0 \uc2f6\ub2e4\ub294 \uc0dd\uac01\uc744 \ud588\ub294\ub370 \uc774\ubc88 \uae30\ud68c\uc5d0 \uc77d\uac8c \ub410\uc2b5\ub2c8\ub2e4!\\n\\n## Book Review\\n\\n### \uac04\ub2e8\ud55c \uc18c\uac1c\\n\\n\uc27d\uac8c \uc124\uba85\ud558\uba74 \uba38\uc2e0\ub7ec\ub2dd\uc5d0 \uad00\ud55c \uc785\ubb38\uc11c\ub77c\uace0 \ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \uadf8\ub7ec\ub098 \ubc14\ub2e5\ubd80\ud130 \uc644\uc804\ud788 \uad6c\ud604\ud558\ub294 \uac83\uc774 \uc544\ub2cc *\uc0ac\uc774\ud0b7\ub7f0*\uc774\ub77c\ub294 \ub77c\uc774\ube0c\ub7ec\ub9ac\ub97c \uc774\uc6a9\ud574\uc11c \uc774\ubbf8 \uad6c\ud604\ub418\uc5b4\uc838 \uc788\ub294 \uac83\uc744 \ubc30\uc6b0\ub294 \ucc45\uc785\ub2c8\ub2e4. TensorFlow, PyTorch\uc744 \uc0ac\uc6a9\ud558\uc5ec \ub525\ub7ec\ub2dd\uc744 \ud558\ub294 \ucc45\uc774 \uc544\ub2c8\ub77c, sklearn \uae30\ubc18\uc758 \uba38\uc2e0\ub7ec\ub2dd \uae30\ucd08 \uc9c0\uc2dd\uc744 \ubc30\uc6b0\ub294 \uac78 \ubaa9\ud45c\ub85c \ud558\uace0 \uc788\uc2b5\ub2c8\ub2e4. \ub610\ud55c, \ub531\ud788 \ucf54\ub4dc\ub97c \uc2e4\ud589\ud558\uae30 \uc704\ud55c \ud658\uacbd \uc124\uc815\uc774 \ud544\uc694\uc5c6\uc73c\uba70 \uc774 \ucc45\uc758 \ubaa8\ub4e0 \ucf54\ub4dc\ub294 \uad6c\uae00 \ucf54\ub7a9(Colab)\uc5d0\uc11c \uc2e4\uc2b5\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\n\\n### \uc8fc\uc11d\\n\\n\uc774\ubc88 \ucc45\uc758 \uc5ed\uc790\ub2d8\uc740 *\ubc15\ud574\uc120*\ub2d8\uc785\ub2c8\ub2e4. \uc5ed\uc2dc\ub098 \ub2e4\ub97c\uae4c \uc5ed\uc790\ub2d8\uaed8\uc11c \ubc88\uc5ed\ud558\uc2e0 \ub2e4\ub978 \ucc45\ucc98\ub7fc \uc774\ubc88 \ucc45\ub3c4 \ubc88\uc5ed\uc774 \uae54\ub054\ud558\uace0, \uc5ed\uc8fc\ub97c \ub108\ubb34 \uc790\uc138\ud558\uac8c \uc798 \ud65c\uc6a9\ud574\uc8fc\uc154\uc11c \uc77d\uae30 \ud3b8\ud588\uc2b5\ub2c8\ub2e4. \uc77c\ubc18\uc801\uc778 \ucc45\ub4e4\uc758 \uc8fc\uc11d\uc774\ub77c\uba74 \ucd9c\ucc98 \ub610\ub294 \ucc38\uace0\ud560\ub9cc\ud55c \uac83\ub4e4\ub9cc \uac04\ub2e8\ud558\uac8c \uc815\ub9ac\ud558\uc9c0\ub9cc, \ubc15\ud574\uc120\ub2d8\uc774 \ubc88\uc5ed\ud558\uc2e0 \ucc45\uc5d0\ub294 \ub3c5\uc790\uac00 \uc774\ud574\ud558\uae30 \uc27d\ub3c4\ub85d \ubd80\uac00\uc801\uc778 \ub0b4\uc6a9\ub4e4\uae4c\uc9c0 \uc801\ud600 \uc788\uc2b5\ub2c8\ub2e4. \uadf8\ub807\uae30\uc5d0 \uc8fc\uc11d\ub3c4 \uaf3c\uaf3c\ud558\uac8c \uc77d\uc5b4\ubcf4\uc2dc\ub294 \uac83\uc744 \ucd94\ucc9c\ud569\ub2c8\ub2e4.\\n\\n### \ub9c8\ubb34\ub9ac\\n\\n\ub9c8\ubb34\ub9ac chapter 8\uc5d0\uc11c\ub294 \uc55e\uc73c\ub85c \uc5b4\ub5a4 \uac78 \ub354 \uacf5\ubd80\ud560\uc9c0 \uac00\uc774\ub4dc\ub97c \ud574\uc90d\ub2c8\ub2e4. \uc774 \ucc45\uc744 \ubc97\uc5b4\ub098 \ub354 \uae4a\uac8c \uba38\uc2e0\ub7ec\ub2dd\uc744 \uacf5\ubd80\ud574\ubcf4\uace0 \uc2f6\uc740 \uc0ac\ub78c\ub4e4\uc5d0\uac8c \ucd94\ucc9c\ud558\ub294 \uc804\ubb38\uc801\uc778 \uc790\ub8cc\ub4e4\uc785\ub2c8\ub2e4. \uc800\ub294 \uc774 \ucc45\ucc98\ub7fc \ud0a4\uc6cc\ub4dc\ub97c \ub358\uc838\uc8fc\ub294 \ucc45\uc744 \uc815\ub9d0 \uc120\ud638\ud558\ub294 \ud3b8\uc785\ub2c8\ub2e4. \uce5c\uc808\ud558\ub2e4\uace0 \ub290\uaef4\uc9c0\uba70 \ub3c5\uc790\uc5d0\uac8c \ub9e4\uc6b0 \ub3c4\uc6c0\ub418\ub294 \ub0b4\uc6a9\uc774\ub77c\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4.\\n\\n### \ud55c\uad6d\uc5b4\ud310 \ubd80\ub85d\\n\\n\ud55c\uad6d\uc5b4\ud310 \ubd80\ub85d\uc73c\ub85c [KoNLPy](https://konlpy.org/ko/latest/)\ub97c \uc0ac\uc6a9\ud558\uc5ec \ud55c\uad6d\uc5b4 \uc790\uc5f0\uc5b4\ucc98\ub9ac\ub97c \ud574\ubd05\ub2c8\ub2e4. \uc774\ubc16\uc5d0\ub3c4 \uc5ed\uc790\ub2d8\uaed8\uc11c \ub354 \ub123\uace0 \uc2f6\uc740 \ubd80\ubd84\ub4e4\uc740 \ubd80\ub85d\uc73c\ub85c \ucd94\uac00\ud574\uc11c \ub123\uc740 \ubaa8\uc2b5\uc744 \ud655\uc778\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \ub610\ud55c, \uc7ac\ubc0c\uc5c8\ub358 \uc810\uc740 \uc774 \ucc45\uc758 \uc800\uc790\uac00 \ud55c\uad6d \ub3c5\uc790\ub97c \uc704\ud574 \uc778\ud130\ubdf0\ub97c \ud588\uc5c8\ub2e4\ub294 \uac83\uc785\ub2c8\ub2e4. \uc0ac\uc774\ud0b7\ub7f0\uc758 \ubcc0\ud654\uc640 \uad00\ub828\ub41c \ub0b4\uc6a9\uc774 \uc788\uc73c\ub2c8 \uc0ac\uc774\ud0b7\ub7f0\uc5d0 \uad00\uc2ec\uc788\uc73c\uc2e0 \ubd84\ub4e4\uc740 \uc774 \ubd80\ubd84\ub3c4 \ucda9\ubd84\ud788 \uc77d\uc744 \uac00\uce58\uac00 \uc788\uc5b4 \ubcf4\uc785\ub2c8\ub2e4.\\n\\n## \ub300\uc0c1\ub3c5\uc790\\n\\n\uba38\uc2e0\ub7ec\ub2dd\uc5d0 \uad00\uc2ec\uc774 \uc0dd\uaca8 \uc785\ubb38\ud558\uc2dc\ub294 \ud559\ubd80\uc0dd\ub4e4\uaed8 \ucd94\ucc9c\ub4dc\ub9ac\uba70, \uc0ac\uc774\ud0b7\ub7f0\uc774 \uc5c5\ub370\uc774\ud2b8 \ub428\uc5d0 \ub530\ub77c \ubb50\uac00 \ubc14\ub00c\uc5c8\ub294\uc9c0 \uad81\uae08\ud558\uc2e0 \ubd84\uc774 \uc77d\uc73c\uc154\ub3c4 \uc88b\uc744 \uac83 \uac19\uc2b5\ub2c8\ub2e4."},{"id":"/data-pipelines-with-apache-airflow","metadata":{"permalink":"/en/blog/data-pipelines-with-apache-airflow","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2022-03-22-data-pipelines-with-apache-airflow/data-pipelines-with-apache-airflow.md","source":"@site/blog/2022-03-22-data-pipelines-with-apache-airflow/data-pipelines-with-apache-airflow.md","title":"\ud83d\udcd6 Apache Airflow \uae30\ubc18\uc758 \ub370\uc774\ud130 \ud30c\uc774\ud504\ub77c\uc778","description":"\uc5d0\uc5b4\ud50c\ub85c \uc911\uc2ec\uc758 \uc6cc\ud06c\ud50c\ub85c \uad6c\ucd95\uc5d0\uc11c \ucee4\uc2a4\ud140 \ucef4\ud3ec\ub10c\ud2b8 \uac1c\ubc1c \ubc0f \ubc30\ud3ec, \uad00\ub9ac\uae4c\uc9c0 | I\u2665Cloud \uc81c\uc774\ud38d\uc758 \ud074\ub77c\uc6b0\ub4dc \uc2dc\ub9ac\uc988 22","date":"2022-03-22T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 Apache Airflow \uae30\ubc18\uc758 \ub370\uc774\ud130 \ud30c\uc774\ud504\ub77c\uc778","date":"2022-03-22","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/data-pipelines-with-apache-airflow","description":"\uc5d0\uc5b4\ud50c\ub85c \uc911\uc2ec\uc758 \uc6cc\ud06c\ud50c\ub85c \uad6c\ucd95\uc5d0\uc11c \ucee4\uc2a4\ud140 \ucef4\ud3ec\ub10c\ud2b8 \uac1c\ubc1c \ubc0f \ubc30\ud3ec, \uad00\ub9ac\uae4c\uc9c0 | I\u2665Cloud \uc81c\uc774\ud38d\uc758 \ud074\ub77c\uc6b0\ub4dc \uc2dc\ub9ac\uc988 22","keywords":["\uc5d0\uc5b4\ud50c\ub85c","\uc544\ud30c\uce58","\ud30c\uc774\ud504\ub77c\uc778","\ub370\uc774\ud130 \uc5d4\uc9c0\ub2c8\uc5b4\ub9c1"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \ud30c\uc774\uc36c \ub77c\uc774\ube0c\ub7ec\ub9ac\ub97c \ud65c\uc6a9\ud55c \uba38\uc2e0\ub7ec\ub2dd","permalink":"/en/blog/introduction-to-ml-with-python"},"nextItem":{"title":"\ud83d\udcd6 \ucfe0\ube0c\ud50c\ub85c \uc6b4\uc601 \uac00\uc774\ub4dc","permalink":"/en/blog/kubeflow-operations-guide"}},"content":":::info\\n\uc81c\uc774\ud38d \\\\<\ubbff\uace0 \ubcf4\ub294 IT \uc804\ubb38\uc11c \ub9ac\ubdf0\uc5b4\\\\> \ud65c\ub3d9\uc744 \uc704\ud574\uc11c \ucc45\uc744 \uc81c\uacf5\ubc1b\uc544 \uc791\uc131\ub41c \uc11c\ud3c9\uc785\ub2c8\ub2e4.\\n:::\\n\\n## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/data-pipelines-with-apache-airflow.jpg)](https://www.kyobobook.co.kr/product/detailViewKor.laf?ejkGb=KOR&mallGb=KOR&barcode=9791191600681&orderClick=LEa&Kc=)\\n\\n- \uc81c\ubaa9: Apache Airflow \uae30\ubc18\uc758 \ub370\uc774\ud130 \ud30c\uc774\ud504\ub77c\uc778\\n- \uc800\uc790: Bas P. Harenslak, Julian Rutger de Ruiter\\n- \uc5ed\uc790: \uae40\uc815\ubbfc, \ubb38\uc120\ud64d\\n- \ucd9c\ud310\uc0ac: \uc81c\uc774\ud38d\\n- \ucd9c\uac04: 2022-03-16\\n\\n{/* truncate */}\\n\\n## Book Review\\n\\n### \uad6c\uc131\\n\\n\uc774 \ucc45\uc740 4\uac1c\uc758 \ud070 \ud30c\ud2b8\ub85c \uc774\ub8e8\uc5b4\uc9c0\uba70, *\uae30\ubcf8*, *\uc911\uae09*, *\uc2e4\uc2b5*, *\ud074\ub77c\uc6b0\ub4dc\uc5d0\uc11c airflow \uc0ac\uc6a9*\uc73c\ub85c \uad6c\uc131\ub418\uc5b4 \uc788\uc2b5\ub2c8\ub2e4.  \\n*\uae30\ubcf8*\uc5d0\uc11c\ub294 \ub9d0 \uadf8\ub300\ub85c \ub370\uc774\ud130 \ud30c\uc774\ud504\ub77c\uc778 \uac1c\ub150, Airflow DAG(Directed Acyclic Graph)\uc758 \uc815\uc758, \uc2a4\ucf00\uc904\ub9c1, \uc758\uc874\uc131 \uc815\uc758 \ub4f1\uc744 \uc124\uba85\ud569\ub2c8\ub2e4. \uc774 \ubd80\ubd84\uc5d0\uc11c airflow 2.x \ubc84\uc804\uc5d0 \ub3c4\uc785\ub41c Taskflow API\ub97c \uc0ac\uc6a9\ud574\ubd05\ub2c8\ub2e4.  \\n*\uc911\uae09*\uc5d0\uc11c\ub294 \uc678\ubd80 \uc2dc\uc2a4\ud15c\uc5d0\uc11c task\ub97c \uc2e4\ud589\ud560 \uc218 \uc788\ub294 \ubc29\ubc95, \ud14c\uc2a4\ud2b8, \ucee8\ud14c\uc774\ub108\ub97c \uc774\uc6a9\ud55c \ud30c\uc774\ud504\ub77c\uc778 \ub4f1\uc744 \ubc30\uc6b8 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \uc608\uc2dc\ub97c \ud65c\uc6a9\ud558\uc5ec \uc124\uba85\ud558\ub294 \uc810\uc774 \uc778\uc0c1 \uae4a\uc5c8\uc2b5\ub2c8\ub2e4.  \\n*\uc2e4\uc2b5*\uc5d0\uc11c\ub294 \ubcf8\uaca9\uc801\uc73c\ub85c \ud6a8\uc728\uc801\uc774\uace0 \uc720\uc9c0\ubcf4\uc218 \uac00\ub2a5\ud55c \ud30c\uc774\ud504\ub77c\uc778 \uad6c\ucd95, Airflow\ub97c \uc2e4\uc81c \uc11c\ube44\uc2a4\uc5d0 \ub3c4\uc785\ud560 \ub54c \uace0\ub824\ud574\uc57c \ud560 \uac83 \ub4f1\uc744 \uc124\uba85\ud569\ub2c8\ub2e4. \ub108\ubb34 \uac1c\ub150\ub9cc \uc124\uba85\ud558\ub294 \uac83\uc774 \uc544\ub2c8\uace0, \uc790\uc138\ud558\uac8c \uc124\uba85\uc774 \ub418\uc5b4 \uc788\uc5b4\uc11c \ud544\uc694\ud560 \ub54c \ub2e4\uc2dc \ubcf4\uba74 \uc88b\uc740 \ub0b4\uc6a9\ub4e4\uc774\uc5c8\uc2b5\ub2c8\ub2e4.  \\n*\ud074\ub77c\uc6b0\ub4dc\uc5d0\uc11c airflow \uc0ac\uc6a9*\uc740 \ub9d0 \uadf8\ub300\ub85c AWS, Azure, GCP \ud658\uacbd\uc5d0\uc11c Airflow \ubc30\ud3ec\uc5d0 \ub300\ud55c \uc544\ud0a4\ud14d\ucc98\ub97c \uc124\uacc4\ud574\ubd05\ub2c8\ub2e4. \uc774 \ubd80\ubd84\ub3c4 \ub2e8\uc21c\ud788 \ud558\ub294 \ubc29\ubc95\ub9cc \uc124\uba85\ud558\ub294 \uac83\uc774 \uc544\ub2c8\ub77c \uc608\uc81c\uac00 \uc788\uc5b4\uc11c \ub108\ubb34 \uc88b\uc558\uc2b5\ub2c8\ub2e4.\\n\\n### \ub3c4\ucee4 \ud658\uacbd\\n\\n\uc8fc\uc5b4\uc9c4 \ucf54\ub4dc\ub4e4\uc758 \uac01 \uc7a5\uc5d0\ub294 \uac70\uc758 \ubaa8\ub450 *docker-compose.yml* \ud30c\uc77c\uc774 \uc788\uc2b5\ub2c8\ub2e4. \uc989, \ucf54\ub4dc\ub97c \uc27d\uac8c \uc2e4\ud589\ud560 \uc218 \uc788\ub3c4\ub85d \ub3c4\ucee4\ub97c \ud658\uacbd\uc774 \uc8fc\uc5b4\uc9c4\ub2e4\ub294 \uac83\uc774\uc8e0. \uc774 \ubd80\ubd84\uc774 \uc774 \ucc45\uc758 \uac00\uc7a5 \ud070 \uc7a5\uc810\uc774\ub77c\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4. \uc804 \uadf8\ub798\ub3c4 \ud658\uacbd \uad6c\uc131\uc744 \uc9c1\uc811 \ud574\ubd10\uc57c \uaca0\ub2e4 \uc2f6\uc5b4\uc11c \ub85c\uceec\ub85c \ub3cc\ub824\ubcf4\uae30\ub3c4 \ud588\uace0 \uac04\ub2e8\ud558\uac8c \ub3c4\ucee4\ub85c \ub3cc\ub824\ubcf4\uae30\ub3c4 \ud588\uc2b5\ub2c8\ub2e4. \ud655\uc2e4\ud788 \ub3c4\ucee4\uac00 \ud3b8\ud569\ub2c8\ub2e4. \uc774 \ucc45\uc744 \uc77d\uc73c\uc2e0\ub2e4\uba74 \uc774 \ubd80\ubd84 \uaf2d \ud65c\uc6a9\ud558\uc2dc\uae38 \ubc14\ub77c\uaca0\uc2b5\ub2c8\ub2e4.\\n\\n### \ubd80\ub85d\\n\\n\uc774 \ucc45\uc758 \ubd80\ub85d\uc5d0\uc11c\ub3c4 \ub3c4\uc6c0\uc774 \ub418\ub294 \ub0b4\uc6a9\uc774 \ub9ce\uc558\uc2b5\ub2c8\ub2e4. \uc608\ub97c \ub4e4\uc5b4  *Appendix A \uc2e4\ud589 \ucf54\ub4dc \uc608\uc81c*\uc5d0\uc11c\ub294 \ucf54\ub4dc\uac00 \uc5b4\ub5bb\uac8c \uad6c\uc131\ub418\uc5b4 \uc788\ub294\uc9c0 \uac04\ub2e8\ud558\uac8c \uc124\uba85\ud558\uace0, \ub3c4\ucee4 \ud658\uacbd\uc744 \uc0ac\uc6a9\ud558\uc5ec \uc608\uc81c\ub97c \uc2e4\ud589\uc2dc\ud0a4\ub294 \uba85\ub839\uc5b4 \uba87\uac00\uc9c0\ub97c \uc54c\ub824\uc90d\ub2c8\ub2e4. \ub3c4\ucee4\ub97c \ud55c \ubc88\ub3c4 \uc0ac\uc6a9\ud574\ubcf4\uc9c0 \ubabb\ud574\ubcf4\uc168\ub354\ub77c\ub3c4 \uc774 \ubd80\ub85d \ud558\ub098\ub9cc \uc788\uc73c\uba74 \uc27d\uac8c \ud658\uacbd\uc744 \uad6c\uc131\ud560 \uc218 \uc788\uc744 \uac83\uc785\ub2c8\ub2e4.  \ub610\ud55c, *Appendix B Airflow 2 \ud328\ud0a4\uc9c0 \uad6c\uc131*\uc5d0\uc11c\ub294 Airflow 1\uacfc 2\uc758 \ud328\ud0a4\uc9c0 \ucc28\uc774\uc5d0 \ub300\ud574 \uac04\ub2e8\ud558\uac8c \uc124\uba85\ud569\ub2c8\ub2e4. \uc5b4\ub5a4 \ucc28\uc774\uac00 \uc788\uc5c8\ub294\uc9c0 \uc124\uba85\ud574\uc8fc\ub294 \uc774\ub7f0 \uc138\uc2ec\ud55c \ubd80\ub85d\uc774 \uc88b\uc558\uc2b5\ub2c8\ub2e4.\\n\\n## \ub300\uc0c1\ub3c5\uc790\\n\\n\uc774 \ucc45\uc740 \uc18c\uac1c\uc5d0\ub3c4 \uc801\ud600 \uc788\ub4ef\uc774 \uc911\uae09 \uc774\uc0c1\uc758 \ud30c\uc774\uc36c \uc2a4\ud0ac\uc744 \ubcf4\uc720\ud55c DevOps \uc5d4\uc9c0\ub2c8\uc5b4, \ub370\uc774\ud130 \uc5d4\uc9c0\ub2c8\uc5b4, \uba38\uc2e0\ub7ec\ub2dd \uc5d4\uc9c0\ub2c8\uc5b4 \ub4f1\uc744 \uc704\ud55c \ucc45\uc774\ub77c\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4. \uc774\uc81c \ub9c9 \ub370\uc774\ud130 \uc5d4\uc9c0\ub2c8\uc5b4\ub9c1\uc5d0 \uc785\ubb38\ud558\uac70\ub098 \uae30\ucd08 CS \uc9c0\uc2dd\uc774 \uc5c6\ub2e4\uba74 \uc77d\uae30 \uc5b4\ub824\uc6b8 \uc218\ub3c4 \uc788\uc744 \uac83 \uac19\uc2b5\ub2c8\ub2e4. \uadf8\ub7ec\ub098 \uad6d\ub0b4 IT \ub3c4\uc11c \uc911 Airflow\ub97c \uc8fc\uc694 \uc8fc\uc81c\ub85c \uc9c0\uc815\ud558\uc5ec \uc4f0\uc5ec\uc9c4 \ucc45\uc740 \uc544\uc9c1 \uc774 \ucc45 \ubc16\uc5d0 \uc5c6\ub294 \uc810\uc5d0\uc11c \ud30c\uc774\ud504\ub77c\uc778, Airflow\uc5d0 \uad00\uc2ec\uc774 \uc788\ub2e4\uba74 \ucda9\ubd84\ud788 \uc77d\uc5b4\ubcfc\ub9cc\ud55c \uc88b\uc740 \ucc45\uc785\ub2c8\ub2e4. \uc870\uae08\uc740 \uc5b4\ub835\uc9c0\ub9cc \uc0c1\ud669\ubcc4 \uc608\uc81c\uac00 \ub9ce\uc544 Airflow\ub97c \uc0ac\uc6a9\ud560 \uc608\uc815\uc774\ub77c\uba74 \uaf2d \uc77d\uc5b4\ubcf4\uae38 \ucd94\ucc9c\ud569\ub2c8\ub2e4."},{"id":"/kubeflow-operations-guide","metadata":{"permalink":"/en/blog/kubeflow-operations-guide","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2022-02-22-kubeflow-operations-guide/kubeflow-operations-guide.md","source":"@site/blog/2022-02-22-kubeflow-operations-guide/kubeflow-operations-guide.md","title":"\ud83d\udcd6 \ucfe0\ube0c\ud50c\ub85c \uc6b4\uc601 \uac00\uc774\ub4dc","description":"\uc628\ud504\ub808\ubbf8\uc2a4 \ubc30\ud3ec\uc640 \ud074\ub77c\uc6b0\ub4dc \uc6b4\uc601","date":"2022-02-22T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \ucfe0\ube0c\ud50c\ub85c \uc6b4\uc601 \uac00\uc774\ub4dc","date":"2022-02-22","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/kubeflow-operations-guide","description":"\uc628\ud504\ub808\ubbf8\uc2a4 \ubc30\ud3ec\uc640 \ud074\ub77c\uc6b0\ub4dc \uc6b4\uc601","keywords":["\ud30c\uc774\ud504\ub77c\uc778","Kubeflow","\ud074\ub77c\uc6b0\ub4dc"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 Apache Airflow \uae30\ubc18\uc758 \ub370\uc774\ud130 \ud30c\uc774\ud504\ub77c\uc778","permalink":"/en/blog/data-pipelines-with-apache-airflow"},"nextItem":{"title":"\ud83d\udcd6 \ub098\ub294 \uc544\ub9c8\uc874\uc5d0\uc11c \ubbf8\ub798\ub97c \ub2e4\ub154\ub2e4","permalink":"/en/blog/I-went-to-the-future-on-Amazon"}},"content":":::info\\n\ud55c\ube5b\ubbf8\ub514\uc5b4 \\\\<\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4\\\\> \ud65c\ub3d9\uc744 \uc704\ud574\uc11c \ucc45\uc744 \uc81c\uacf5\ubc1b\uc544 \uc791\uc131\ub41c \uc11c\ud3c9\uc785\ub2c8\ub2e4.\\n:::\\n\\n![\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4 2022](../assets/I-am-reviewer-2022.jpg)\\n\\n## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/kubeflow-operations-guide.jpg)](https://www.kyobobook.co.kr/product/detailViewKor.laf?ejkGb=KOR&mallGb=KOR&barcode=9791162245118&orderClick=LEa&Kc=)\\n\\n- \uc81c\ubaa9: \ucfe0\ube0c\ud50c\ub85c \uc6b4\uc601 \uac00\uc774\ub4dc\\n- \uc800\uc790: Josh Patterson, Michael Katzenellenbogen, Austin Harris\\n- \uc5ed\uc790: \uae40\uc18c\ud615\\n- \ucd9c\ud310\uc0ac: \ud55c\ube5b\ubbf8\ub514\uc5b4\\n- \ucd9c\uac04: 2022-01-31\\n\\n{/* truncate */}\\n\\n## Book Review\\n\\n### Introduction\\n\\n\ucd5c\uadfc \uba87 \ub144 \ub3d9\uc548 MLOps\uac00 \ub728\uae30 \uc2dc\uc791\ud558\uba74\uc11c \ucfe0\ubc84\ub124\ud2f0\uc2a4(Kubernetes)\ub294 \uac00\uc7a5 \uc778\uae30\uc788\ub294 \uae30\uc220\uc774 \ub418\uc5c8\uc2b5\ub2c8\ub2e4.  Kubeflow\ub294 \uadf8\ub7f0 \ucfe0\ubc84\ub124\ud2f0\uc2a4\uc5d0 ML workflow\ub97c \ubc30\ud3ec\ud558\ub294 \uac83\uc744 \ub2e8\uc21c\ud558\uace0 \ud655\uc7a5 \uac00\ub2a5\ud558\ub3c4\ub85d \ud558\ub294 \ub370 \ubaa9\ud45c\ub97c \ub454 \uae30\uc220\uc785\ub2c8\ub2e4. \ucd5c\uadfc Airflow\ub97c \uc0ac\uc6a9\ud574\ubcf4\uace0 \uacf5\ubd80\ud558\uba74\uc11c Kubeflow\uc5d0 \ub300\ud574\uc11c\ub3c4 \uad00\uc2ec\uc774 \uc0dd\uacbc\ub294\ub370 \ub9c8\uce68 \uc774\ub7f0 \uc88b\uc740 \ucc45\uc744 \uc77d\uc744 \uae30\ud68c\uac00 \uc0dd\uacbc\uc2b5\ub2c8\ub2e4. \\n\\n### \uac04\ub2e8\ud55c \uad6c\uc131 \uc18c\uac1c\\n\\n\ucc98\uc74c 3\uac1c\uc758 Chapter\ub294 Kubeflow\uc5d0 \ub300\ud55c \uac1c\ub150\uc801\uc778 \ubd80\ubd84\uc5d0 \ub300\ud574 \uc124\uba85\ud569\ub2c8\ub2e4. Kubeflow\uac00 \uc65c \ub4f1\uc7a5\ud558\uac8c \ub410\uace0, Kubeflow \uc544\ud0a4\ud14d\ucc98, \uc5b4\ub5a4 \ud658\uacbd\uc5d0\uc11c \uc0ac\uc6a9\ud574\uc57c \ud558\ub294\uc9c0 \ub4f1\uc5d0 \ub300\ud574 \uc54c\ub824\uc90d\ub2c8\ub2e4. \uac1c\uc778\uc801\uc73c\ub85c \uc774 \ubd80\ubd84\uacfc KFServing\uc744 \uc0ac\uc6a9\ud558\ub294 Chapter 8, \ubd80\ub85d\uc774 \uc77d\uae30 \uac00\uc7a5 \uc5b4\ub824\uc6e0\uc2b5\ub2c8\ub2e4.  \\n\\nChapter 4\ubd80\ud130 Chapter 7\uae4c\uc9c0\ub294 \ubcf8\uaca9\uc801\uc73c\ub85c Kubeflow \ud658\uacbd\uc744 \uad6c\ucd95\ud569\ub2c8\ub2e4. \uc628\ud504\ub808\ubbf8\uc2a4\uc640 3\uac00\uc9c0\uc758 \ud074\ub77c\uc6b0\ub4dc \ud658\uacbd\uc5d0\uc11c \uad6c\ucd95\ud558\ub294 \ubc29\ubc95\uc744 \uc124\uba85\ud569\ub2c8\ub2e4. \\n\\nChapter 8\uc740 Kubeflow\uc758 \ud575\uc2ec \ubaa8\ub378 \uad00\ub9ac \uad6c\uc131 \uc694\uc18c\uc778 KFServing\uc5d0 \ub300\ud574 \uc18c\uac1c\ud569\ub2c8\ub2e4. KFServing\uc5d0 \ub300\ud55c \uac1c\ub150, \uc0ac\uc6a9 \uc2dc \uc774\uc810, KFServing\uc5d0 \ubaa8\ub378 \ubc30\ud3ec \ubc29\ubc95 \ub4f1\uc744 \uc124\uba85\ud569\ub2c8\ub2e4. \\n\\n### \ud074\ub77c\uc6b0\ub4dc\uc5d0\uc11c Kubeflow \ud658\uacbd \uad6c\ucd95\\n\\nGCP, AWS, Azure\uc758 \uac01 \uc11c\ube44\uc2a4\uc5d0 \ub300\ud574 \uac04\ub2e8\ud558\uac8c \uc124\uba85\ud558\uace0 Kubeflow \ud658\uacbd\uc744 \uad6c\ucd95\ud558\ub294 \ubc29\ubc95\uc744 \uc124\uba85\ud569\ub2c8\ub2e4. \uad6c\ucd95\ud558\ub294 \ub370 \uc5b4\ub824\uc6c0\uc744 \uac16\uace0 \uc788\uc73c\uc2e0 \ubd84\ub4e4\uaed8\ub294 \ub3c4\uc6c0\uc774 \ub418\ub294 \ub0b4\uc6a9\uc774\uc9c0\ub9cc, \ub108\ubb34 \ub2e4\uc591\ud55c \ud658\uacbd\uc5d0 \uad6c\ucd95\ud558\ub294 \uac83\uc5d0 \ucd08\uc810\uc774 \ub9de\ucdb0\uc838 \uc788\uc5b4\uc11c \uadf8\ub7f0\uc9c0 \uc2e4\uc81c \uc0ac\uc6a9 \uc608\uc81c\uac00 \uc5c6\ub2e4\ub294 \uac74 \ub9e4\uc6b0 \uc544\uc26c\uc6e0\uc2b5\ub2c8\ub2e4. \ud074\ub77c\uc6b0\ub4dc\uc5d0\uc11c Kubeflow \ud658\uacbd\uc744 \uad6c\ucd95 \ud6c4 \uc9c1\uc811\uc801\uc73c\ub85c \uc0ac\uc6a9\ud574\ubcf4\uace0 \uc2f6\uc73c\uc2dc\ub2e4\uba74 Kubeflow Documentation\uc5d0\uc11c \uc81c\uacf5\ud558\ub294 [MNIST image classification](https://www.kubeflow.org/docs/started/kubeflow-examples/)\uc744 \ub530\ub77c \ud574\ubcf4\uc2dc\ub294 \uac78 \ucd94\ucc9c\ud574 \ub4dc\ub9bd\ub2c8\ub2e4. \ubb3c\ub860 \uc774 \uc608\uc2dc\ub3c4 AWS SDK\uc778 boto3, GCP SDK\uc778 Cloud SDK\uc5d0 \ub300\ud55c \uc0ac\uc804 \uc9c0\uc2dd\uc774 \ud544\uc694\ud558\uae30\uc5d0 \uc804\ubc18\uc801\uc778 \ud074\ub77c\uc6b0\ub4dc \uacf5\ubd80\uac00 \ud544\uc694\ud569\ub2c8\ub2e4.\\n\\n### \ucd94\uac00\uc801\uc778 \uc815\ubcf4\\n\\n\uc774 \ucc45\uc5d0 \ub2f4\uae34 \ub0b4\uc6a9\ub4e4 \uc774\uc678\uc5d0\ub3c4 Kubeflow\uc640 \uad00\ub828\ub41c \uc218\ub9ce\uc740 \uc815\ubcf4\ub4e4\uc774 \ub9ce\uc2b5\ub2c8\ub2e4. \ucc45 \ud55c \uad8c\uc5d0\uc11c \ubaa8\ub450 \uc790\uc138\ud558\uac8c \uc124\uba85\ud558\uc9c0 \ubabb\ud558\uae30\uc5d0 \uadf8\ub7f0 \uc815\ubcf4\ub4e4\uc740 \uac01\uc8fc\ub97c \uc0ac\uc6a9\ud558\uc5ec \uc18c\uac1c\ud569\ub2c8\ub2e4. \ub2e4\uc74c \uacf5\ubd80\ub97c \uc704\ud574 \uac00\uc774\ub4dc\ub97c \uc54c\ub824\uc8fc\ub294 \ub290\ub08c\uc774\ub77c \uac1c\uc778\uc801\uc73c\ub85c \ub3c4\uc6c0\uc774 \ub410\ub358 \ubd80\ubd84\uc785\ub2c8\ub2e4. \\n\\n## \ub300\uc0c1 \ub3c5\uc790\\n\\n\uac1c\uc778\uc801\uc73c\ub85c \uc774 \ucc45\uc740 \uc800\uc5d0\uac8c \uaf64 \uc5b4\ub824\uc6b4 \ucc45\uc774\uc5c8\uc2b5\ub2c8\ub2e4. \uc65c \uae30\uc5c5\uc5d0\uc11c MLOps \uc778\ud134, \uc2e0\uc785 \ud3ec\uc9c0\uc158\uc774 \ud754\ud558\uc9c0 \uc54a\uc740\uc9c0 \uc54c\ub824\uc8fc\ub294 \ucc45\uc774\uc5c8\uc2b5\ub2c8\ub2e4. \ub354 \uc5f4\uc2ec\ud788 \uacf5\ubd80\ud574\uc57c\uaca0\ub2e4\ub294 \uac78 \uc624\ub298\ub3c4 \ub290\ub07c\ub124\uc694.\\n\\n\uc774 \ucc45\uc740 MLOps\uc5d0 \uad00\uc2ec \uc788\uc73c\uba70 \ucfe0\ubc84\ub124\ud2f0\uc2a4, ML\uc5d0 \ub300\ud574 \uc774\ud574\ub3c4\uac00 \uc788\ub294 \uc5d4\uc9c0\ub2c8\uc5b4\uac00 \uc77d\uc73c\uc2dc\ub294 \uac78 \ucd94\ucc9c\ud574 \ub4dc\ub9bd\ub2c8\ub2e4. \ud2b9\ud788 DevOps \uc5d4\uc9c0\ub2c8\uc5b4, ML \uc5d4\uc9c0\ub2c8\uc5b4, \ub370\uc774\ud130 \uc5d4\uc9c0\ub2c8\uc5b4 \ub4f1\uc758 \ud3ec\uc9c0\uc158\uc5d0 \uc788\uc73c\uc2e0 \ubd84\ub4e4\uc774 \uc77d\uc73c\uc2dc\uba74 \ub3c4\uc6c0\uc774 \ub420 \uac83\uc774\ub77c \uc0dd\uac01\ud569\ub2c8\ub2e4."},{"id":"/I-went-to-the-future-on-Amazon","metadata":{"permalink":"/en/blog/I-went-to-the-future-on-Amazon","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2022-01-04-I-went-to-the-future-on-Amazon/I-went-to-the-future-on-Amazon.md","source":"@site/blog/2022-01-04-I-went-to-the-future-on-Amazon/I-went-to-the-future-on-Amazon.md","title":"\ud83d\udcd6 \ub098\ub294 \uc544\ub9c8\uc874\uc5d0\uc11c \ubbf8\ub798\ub97c \ub2e4\ub154\ub2e4","description":"\ud3c9\uade0 \uadfc\uc18d 1\ub144 \uc544\ub9c8\uc874\uc5d0\uc11c 12\ub144 \uc77c\ud55c \ud55c\uad6d\uc778\uc774 \uae68\ub2ec\uc740 \uc77c\uacfc \uc0b6\uc744 \uc124\uacc4\ud558\ub294 \ubc95","date":"2022-01-04T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":false,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \ub098\ub294 \uc544\ub9c8\uc874\uc5d0\uc11c \ubbf8\ub798\ub97c \ub2e4\ub154\ub2e4","date":"2022-01-04","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/I-went-to-the-future-on-Amazon","description":"\ud3c9\uade0 \uadfc\uc18d 1\ub144 \uc544\ub9c8\uc874\uc5d0\uc11c 12\ub144 \uc77c\ud55c \ud55c\uad6d\uc778\uc774 \uae68\ub2ec\uc740 \uc77c\uacfc \uc0b6\uc744 \uc124\uacc4\ud558\ub294 \ubc95","keywords":["Amazon","\uc131\uc7a5","\ud68c\uace0"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \ucfe0\ube0c\ud50c\ub85c \uc6b4\uc601 \uac00\uc774\ub4dc","permalink":"/en/blog/kubeflow-operations-guide"},"nextItem":{"title":"\ud83d\udcd6 \ud30c\uc774\ud1a0\uce58\ub85c \ubc30\uc6b0\ub294 \uc790\uc5f0\uc5b4 \ucc98\ub9ac","permalink":"/en/blog/NLP-with-PyTorch"}},"content":"## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/I-went-to-the-future-on-Amazon.jpg)](https://www.kyobobook.co.kr/product/detailViewKor.laf?ejkGb=KOR&mallGb=KOR&barcode=9791157843183&orderClick=LEa&Kc=)\\n\\n- \uc81c\ubaa9: \ub098\ub294 \uc544\ub9c8\uc874\uc5d0\uc11c \ubbf8\ub798\ub97c \ub2e4\ub154\ub2e4\\n- \uc800\uc790: \ubc15\uc815\uc900\\n- \ucd9c\ud310\uc0ac: \ud55c\ube5b\ube44\uc988\\n- \ucd9c\uac04: 2019-03-07\\n\\n## Book Review\\n\\n\uc774\uc9c1\ub960\uc774 \ub9e4\uc6b0 \ub192\ub2e4\ub294 Amazon\uc5d0\uc11c 12\ub144\uac04 \uc77c\uc744 \ud55c \uacbd\ud5d8\uc740 \ud754\uce58 \uc54a\ub2e4\uace0 \uc0dd\uac01\ub418\uae30\uc5d0 \ucd9c\uac04\ub418\uc5c8\uc744 \ub54c\ubd80\ud130 \uaf2d \uc77d\uc5b4\ubcf4\uace0 \uc2f6\ub358 \ucc45\uc774\uc5c8\uc2b5\ub2c8\ub2e4. \ubc00\ub9ac\uc758 \uc11c\uc7ac\uc5d0\uc11c \'\uc5b4\ub5a4 \ucc45\uc744 \uc77d\uc5b4\ubcfc\uae4c\' \uace0\ubbfc\ud558\ub358 \uc911\uc5d0 \uc62c\ub77c\uc640 \uc788\ub294 \uac78 \uc6b0\uc5f0\ud788 \ubc1c\uacac\ud558\uc5ec \uc77d\uac8c \ub410\uc2b5\ub2c8\ub2e4. \\n\\n\uc800\uc790\ub294 \uc544\ub9c8\uc874\uc5d0\uc11c \uac1c\ubc1c\uc790\ub85c \uc2dc\uc791\uc744 \ud558\uc600\uc9c0\ub9cc, \ubd80\uc11c\ub3c4 \uc9c1\uc885\ub3c4 \uc544\ub9c8\uc874 \uc548\uc5d0\uc11c \uba87 \ubc88\uc744 \ubcc0\uacbd\ud558\uba74\uc11c \uc544\ub9c8\uc874 \uae30\uc5c5 \uadf8 \uc790\uccb4\ub97c \uacbd\ud5d8\ud558\uc2e0 \ubd84\uc774\uc2ed\ub2c8\ub2e4. \ubd80\uc11c\ub97c \uc62e\uae30\ub294 \uac74 \uadf8\ub807\ub2e4 \ud558\ub354\ub77c\ub3c4 \uc9c1\uc885\uc744 \uc62e\uae34\ub2e4\ub294 \uc77c\uc740 \uc815\ub9d0 \uc5b4\ub824\uc6b4 \uc77c\uc774\ub77c \uc0dd\uac01\ud558\ub294\ub370 \ub300\ub2e8\ud558\ub2e4\uace0 \uc0dd\uac01\ud588\uc2b5\ub2c8\ub2e4.\\n\\n\uc800\uc790\uac00 \uc77c\uc744 \ud588\uc744 \ub2f9\uc2dc \uc791\uc558\ub358 \uc544\ub9c8\uc874\ubd80\ud130 \uc2dc\uc791\ud558\uc5ec \ubbf8\uad6d\ubfd0\ub9cc \uc544\ub2c8\ub77c \uc804\uc138\uacc4 \uc720\ud1b5 \uc2dc\uc7a5\uc744 \uba39\uace0 \uc788\ub294 \ud604\uc7ac \ub300\uae30\uc5c5\uc774 \ub41c \uc544\ub9c8\uc874\uae4c\uc9c0\uc758 \uc131\uc7a5\ud558\ub294 \ubaa8\uc2b5\uc744 \uc774 \ucc45\uc5d0\uc11c \ubcfc \uc218 \uc788\uc5c8\uc2b5\ub2c8\ub2e4. \uc544\ub9c8\uc874\uc774 \uc131\uc7a5\ud568\uc5d0 \ub530\ub77c \uc800\uc790\uac00 \uc5b4\ub5bb\uac8c \uc774\uacf3\uc5d0\uc11c \uacc4\uc18d \uc788\uc5c8\uace0, \uc0dd\ud65c\ud558\uace0, \uc800\uc790 \uc790\uc2e0 \ub610\ud55c \uc131\uc7a5\ud588\ub294\uc9c0 \uadf8 \uc131\uc7a5 \uc2a4\ud1a0\ub9ac\ub97c \ubcf4\uc5ec\uc8fc\ub294 \ucc45\uc785\ub2c8\ub2e4.\\n\\n\ud55c \uae30\uc5c5\uc5d0\uc11c 12\ub144 \ub3d9\uc548 \uc788\ub2e4\ub294 \uac83\ub3c4 \ub300\ub2e8\ud558\uc9c0\ub9cc \uc800\uc790 \ub610\ud55c \uc5b4\ub824\uc6c0\uacfc \uace0\ubbfc\uc774 \ub9ce\uc558\ub2e4\ub294 \uac83\uc744 \uc774 \ucc45\uc5d0\uc11c \ubcf4\uc5ec\uc90d\ub2c8\ub2e4. \uc9c0\uae08\ub3c4 \uc544\ub9c8\uc874\uc740 \uadfc\ubb34\ud558\uae30 \uc5b4\ub824\uc6b4 \uae30\uc5c5\uc774\ub77c\uace0 \uc18c\ubb38\uc774 \ub09c \uae30\uc5c5\uc785\ub2c8\ub2e4. \uadf8 \uae30\uc5c5\uc5d0\uc11c \uc5b4\ub824\uc6e0\uace0 \ud798\ub4e4\uc5c8\ub358 \uc810\ub4e4\uc744 \uc544\ub9c8\uc874 \uc815\uc2e0\uc5d0 \ube57\ub300\uc5b4 \uc5b4\ub5bb\uac8c \uadf9\ubcf5\ud588\ub294\uc9c0 \uc6b0\ub9ac\uc5d0\uac8c \ub9d0\ud574\uc90d\ub2c8\ub2e4. \ud55c \uc0ac\ub78c\uc758 \ub2e4\ud050\uba58\ud130\ub9ac\ub97c \ubcf4\ub294 \uae30\ubd84\uc774\uc5c8\uc2b5\ub2c8\ub2e4.\\n\\n\uba87\uba87 \uc544\ub9c8\uc874 \uc815\uc2e0\uc774 \uc800\uc5d0\uac8c \ub9de\uc9c0 \uc54a\ub294 \ubd80\ubd84\ub4e4\ub3c4 \ub9ce\uc558\uc9c0\ub9cc, \uc774 \ucc45\uc740 \ub2e4\uc2dc \uc790\uc138\ud558\uac8c \uc77d\uc5b4\ubcf4\uace0 \uc2f6\uc740 \ucc45\uc774\uc5c8\uc2b5\ub2c8\ub2e4. \\n\\n### 6-pager\\n\\n\uc544\ub9c8\uc874\uc5d0\uc11c\ub294 \ud30c\uc6cc\ud3ec\uc778\ud2b8\ub97c \uc0ac\uc6a9\ud558\uc9c0 \uc54a\uc2b5\ub2c8\ub2e4. \ub300\uc2e0 *6\ud398\uc774\uc800*\ub77c\ub294 \ubc29\ubc95\uc744 \ud65c\uc6a9\ud558\uc5ec \ub9ce\uc740 \uc815\ubcf4\ub97c \ube60\ub974\uac8c \uc804\ub2ec\ud560 \uc218 \uc788\ub3c4\ub85d \ud558\uc8e0. \uc0ac\uc2e4 \uc774 \ubd80\ubd84\uc5d0\uc11c \ud30c\uc6cc\ud3ec\uc778\ud2b8\uc758 \ub2e8\uc810\uc744 \ub2e4\uc2dc \uc0dd\uac01\ud574\ubcf4\uac8c \ub410\uc2b5\ub2c8\ub2e4. \ud56d\uc0c1 \ud504\ub808\uc820\ud14c\uc774\uc158\uc744 \uc2dc\uac01\uc801\uc778 \uc790\ub8cc\uc640 \ud568\uaed8 \ub9cc\ub4dc\ub294 \uac83\ub3c4 \ud798\ub4e0\ub370 \uadf8 \uc2ac\ub77c\uc774\ub4dc \ud558\ub098\ud558\ub098\uc5d0 \ubaa8\ub4e0 \ub0b4\uc6a9\uc744 \ub2f4\uae30 \ud798\ub4e4\ub2e4\ub294 \uc0dd\uac01\uc774 \ub9ce\uc558\uc2b5\ub2c8\ub2e4. \ub610\ud55c, \uadf8\ub807\uac8c \ud558\ub294 \ubc1c\ud45c\uac00 *\uc0ac\ub78c\ub4e4\uc5d0\uac8c \ub3c4\uc6c0\uc774 \ub420\uae4c?, \uc0ac\ub78c\ub4e4\uc774 \uc774\ud574\ub97c \ud560 \uc218 \uc788\uc744\uae4c?* \ub77c\ub294 \uc0dd\uac01\ub3c4 \ud588\uace0\uc694. \\n\\n\ubc18\uba74 6-pager \ubc29\uc2dd\uc740 A4 \uc6a9\uc9c0 6\uc7a5\uc758 \ubb38\uc11c\uc5d0 \uc0ac\uc804 \uc9c0\uc2dd\uc774 \uc5c6\ub294 \uc0ac\ub78c\ub3c4 \ubcc4\ub2e4\ub978 \ucd94\uac00 \uc124\uba85 \uc5c6\uc774 \ub05d\uae4c\uc9c0 \uc77d\uace0 \uc774\ud574\ud560 \uc218 \uc788\ub3c4\ub85d \ub9d0\ub85c \uc124\uba85\ud558\ub4ef\uc774 \uc4f0\ub294 \ubc29\ubc95\uc785\ub2c8\ub2e4. \ud68c\uc758 \ub54c \uadf8 \ubb38\uc11c\ub97c \ub098\ub220\uc8fc\uace0, \uadf8 \ubb38\uc11c\ub97c \ud68c\uc758\uc5d0 \ucc38\uc11d\ud55c \uc0ac\ub78c\ub4e4\uc774 \uc77d\uc740 \ud6c4 \uadf8\uc81c\uc11c\uc57c \ub17c\uc758\uac00 \uc2dc\uc791\ub418\ub294 \uac70\uc8e0. \ub2f9\uc5f0\ud788 \ubc1c\ud45c\uc790\uc5d0\uac8c\ub294 \uc0c1\ub2f9\ud788 \uc5b4\ub824\uc6b4 \ud68c\uc758 \ubc29\uc2dd\uc774\uae34 \ud569\ub2c8\ub2e4\ub9cc, PPT\ub85c \ud504\ub808\uc820\ud14c\uc774\uc158\uc774 \ub05d\ub09c \ud6c4 Q&A\ub97c \ubc1b\ub294 \uac83\ubcf4\ub2e4 \ub354\uc6b1 \ud6a8\uacfc\uc801\uc778 \ud68c\uc758 \ubc29\uc2dd\uc774\ub77c \uc0dd\uac01\ud588\uc2b5\ub2c8\ub2e4. \ud63c\uc790 \ubc1c\ud45c\ud558\ub294 \uac83\uc774 \uc544\ub2c8\ub77c \ub2e4 \uac19\uc774 \ud1a0\uc758\ud558\ub294 \ub290\ub08c\uc774\ub2c8\uae4c\uc694.\\n\\n## \uc774\uc9c1\\n\\n\uc544\ub9c8\uc874\uc740 \uc81c\uac00 \uc0dd\uac01\ud558\ub294 \uc2e4\ub9ac\ucf58\ubc38\ub9ac, \ubbf8\uad6d \ubb38\ud654 \uadf8 \uc790\uccb4\uc600\uc2b5\ub2c8\ub2e4. \uc9c8\ubb38\ud558\ub294 \uac83\uc5d0 \ub450\ub824\uc6cc \ud558\uc9c0 \uc54a\uace0, \ub458\ub7ec\ub300\ub294 \uac70\uc9d3\ub9d0 \ub530\uc704 \ud558\uc9c0 \uc54a\ub294 \ubaa8\uc2b5\uc744 \ubcf4\uba74\uc11c \ud55c\uad6d\uacfc \ub2e4\ub974\uac8c \ub9e4\ub825\uc801\uc774\ub77c \ub290\uaf08\uc2b5\ub2c8\ub2e4. \uc0ac\ub0b4 \uc774\uc9c1\uc744 \ub3c5\ub824\ud558\uae30\ub3c4 \ud558\uace0\uc694.\\n\\n\uadf8\ub7ec\ub098 \uc815\ub9d0 \ucd5c\uace0\uc758 \uc778\uc7ac\ub4e4\uc744 \ubf51\uc544 \uc544\ub9c8\uc874\uc774\ub77c\ub294 \uc6b8\ud0c0\ub9ac \uc548\uc5d0\uc11c \uacbd\uc7c1\uc2dc\ud0a4\uba70, \uadf8\ub4e4\uc758 \ub2a5\ub825\uacfc \ub178\ub825\uc5d0 \ub530\ub77c \uc2e4\uc9c8\uc801\uc778 \ubcf4\uc0c1\uc744 \ud574\uc900\ub2e4\ub294 \uc810\uc774 \ud2b9\ud788 \uc544\ub9c8\uc874\uc740 \uadfc\ubb34\ud558\uae30 \ub9e4\uc6b0 \uc5b4\ub835\uaca0\ub2e4\uace0 \ub290\uaf08\uc2b5\ub2c8\ub2e4. \uc5b4\ucc0c\ubcf4\uba74 \ub2f9\uc5f0\ud55c \uac70\uc9c0\ub9cc, \ucd5c\uace0\uc758 \uc778\uc7ac\ub4e4\uacfc \ud568\uaed8 \uacbd\uc7c1\ud558\ub294 \ubd80\ubd84\uc5d0\uc11c \uc990\uac70\uc6c0, \uc7ac\ubbf8\ub3c4 \uc788\uaca0\uc9c0\ub9cc, \uc555\ubc15\uac10\uc774 \ub9e4\uc6b0 \ud074 \uac83\uc774\ub77c \uc0dd\uac01\ub429\ub2c8\ub2e4."},{"id":"/NLP-with-PyTorch","metadata":{"permalink":"/en/blog/NLP-with-PyTorch","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2021-12-23-NLP-with-PyTorch/NLP-with-PyTorch.md","source":"@site/blog/2021-12-23-NLP-with-PyTorch/NLP-with-PyTorch.md","title":"\ud83d\udcd6 \ud30c\uc774\ud1a0\uce58\ub85c \ubc30\uc6b0\ub294 \uc790\uc5f0\uc5b4 \ucc98\ub9ac","description":"\ub525\ub7ec\ub2dd\uc744 \uc774\uc6a9\ud55c \uc790\uc5f0\uc5b4 \ucc98\ub9ac \uc560\ud50c\ub9ac\ucf00\uc774\uc158 \uad6c\ucd95","date":"2021-12-23T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \ud30c\uc774\ud1a0\uce58\ub85c \ubc30\uc6b0\ub294 \uc790\uc5f0\uc5b4 \ucc98\ub9ac","date":"2021-12-23","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/NLP-with-PyTorch","description":"\ub525\ub7ec\ub2dd\uc744 \uc774\uc6a9\ud55c \uc790\uc5f0\uc5b4 \ucc98\ub9ac \uc560\ud50c\ub9ac\ucf00\uc774\uc158 \uad6c\ucd95","keywords":["NLP","PyTorch"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \ub098\ub294 \uc544\ub9c8\uc874\uc5d0\uc11c \ubbf8\ub798\ub97c \ub2e4\ub154\ub2e4","permalink":"/en/blog/I-went-to-the-future-on-Amazon"},"nextItem":{"title":"\ud83d\udcd6 \ud55c \uad8c\uc73c\ub85c \uc77d\ub294 \ucef4\ud4e8\ud130 \uad6c\uc870\uc640 \ud504\ub85c\uadf8\ub798\ubc0d","permalink":"/en/blog/The-secret-life-of-programs"}},"content":":::info\\n\ud55c\ube5b\ubbf8\ub514\uc5b4 \\\\<\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4\\\\> \ud65c\ub3d9\uc744 \uc704\ud574\uc11c \ucc45\uc744 \uc81c\uacf5\ubc1b\uc544 \uc791\uc131\ub41c \uc11c\ud3c9\uc785\ub2c8\ub2e4.\\n:::\\n\\n![\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4 2021](../assets/I-am-reviewer-2021.jpg)\\n\\n## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/NLP-with-PyTorch.jpg)](https://www.kyobobook.co.kr/product/detailViewKor.laf?ejkGb=KOR&mallGb=KOR&barcode=9791162244333&orderClick=LEa&Kc=)\\n\\n- \uc81c\ubaa9: \ud30c\uc774\ud1a0\uce58\ub85c \ubc30\uc6b0\ub294 \uc790\uc5f0\uc5b4 \ucc98\ub9ac\\n- \uc800\uc790: Delip Rao, Brian McMahan\\n- \uc5ed\uc790: \ubc15\ud574\uc120\\n- \ucd9c\ud310\uc0ac: \ud55c\ube5b\ubbf8\ub514\uc5b4\\n- \ucd9c\uac04: 2021-06-01\\n\\n{/* truncate */}\\n\\n## \ucc45 \ub9ac\ubdf0\\n\\n### \ubc88\uc5ed\\n\\n\ubc8c\uc368 *\ubc15\ud574\uc120* \ub2d8\uc774 \ubc88\uc5ed\ud558\uc2e0 \ucc45\uc744 \ub450 \uad8c\uc9f8 \uc77d\uc5b4\ubcf4\ub124\uc694. \ubc88\uc5ed\ub3c4 \uc5b8\uc81c\ub098 \uae54\ub054\ud558\uace0, *\uc5ed\uc8fc*\ub97c \ud65c\uc6a9\ud558\uc154\uc11c \ube44\uc2b7\ud55c \uc758\ubbf8\ub85c \uc4f0\uc774\ub294 \ub2e8\uc5b4\ub294 \ud55c \ub2e8\uc5b4\ub85c \ud1b5\ud569\ud558\uc5ec \ubc88\uc5ed\ud588\ub2e4\uace0 \uc54c\ub824\uc8fc\uc2dc\uae30\ub3c4 \ud558\uae30\uc5d0 \uc5bc\ub9c8\ub098 \uc790\uc138\ud558\uac8c \ubc88\uc5ed\uc744 \ud558\uc2dc\ub294\uc9c0 \uc54c \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\n\uc804 *\ubc15\ud574\uc120* \ub2d8\uc774 \ubc88\uc5ed\ud558\uc2e0 \ucc45\uc744 \uc77d\uc744 \ub54c\ub9c8\ub2e4 \ud3b8\ud558\uac8c \uc77d\uc744 \uc218 \uc788\uc5b4\uc11c \uc88b\uc558\uc2b5\ub2c8\ub2e4. *\ubc15\ud574\uc120* \ub2d8\uc758 GitHub\uc5d0 \ubcf4\uba74 Jupyter Notebook\uc758 \uc608\uc81c\ucf54\ub4dc\uc758 \uc8fc\uc11d\uae4c\uc9c0 \ubc88\uc5ed\ub418\uc5b4 \uc788\uc73c\ub2c8 \ucc38\uace0\ud558\uc2dc\uba74 \uc88b\uc744 \uac83 \uac19\uc2b5\ub2c8\ub2e4.\\n\\n### \uc5f0\uc2b5\ubb38\uc81c\\n\\n\uc774 \ucc45\uc758 \uac00\uc7a5 \uc544\uc26c\uc6b4 \uc810 \uc911 \ud558\ub098\uc785\ub2c8\ub2e4. \uc65c 1\uc7a5\ub9cc \uc5f0\uc2b5\ubb38\uc81c\uac00 \uc788\uace0 \ub2e4\ub978 \uc7a5\uc5d0\ub294 \uc5f0\uc2b5\ubb38\uc81c\uac00 \uc5c6\uc744\uae4c\uc694. \uac1c\uc778\uc801\uc73c\ub85c 1\uc7a5\uc5d0 \uc5f0\uc2b5\ubb38\uc81c\ub97c \ud480\uba74\uc11c \'\ub2e4\uc74c \uc7a5\uc5d0\ub3c4 \uc5f0\uc2b5\ubb38\uc81c\uac00 \uc788\uaca0\uc9c0?\'\ub77c\uace0 \uc0dd\uac01\ud558\uba74\uc11c \ub3c5\uc790\uac00 \uc815\ub9d0 \uacf5\ubd80\ud558\uae30 \uc88b\uc740 \ucc45\uc774\ub77c \uc0dd\uac01\ud588\ub294\ub370 \uc774 \ubd80\ubd84\uc740 \uc544\uc26c\uc6e0\uc2b5\ub2c8\ub2e4.\\n\\n### Pororo\\n\\n\uc544\ubb34\ub798\ub3c4 \ubc88\uc5ed\uc11c\uc774\uae30 \ub54c\ubb38\uc5d0 \uc601\uc5b4\ub97c \uae30\uc900\uc73c\ub85c \ubaa8\ub4e0 \ub0b4\uc6a9\uc774 \uc9c4\ud589\ub429\ub2c8\ub2e4. \ud55c\uae00\uc744 \ucc98\ub9ac\ud558\uae30 \uc704\ud574\uc11c\ub294 \ub3c5\uc790\uc758 \uac1c\uc778\uc801\uc778 \uc5f0\uad6c\uac00 \ud544\uc694\ud574 \ubcf4\uc785\ub2c8\ub2e4. \uadf8\ub7ec\ub098 *\ubc15\ud574\uc120* \ub2d8\uc774 \uc774\ub7f0 \ubd80\ubd84\uc774 \uc544\uc26c\uc6b0\uc168\ub294\uc9c0 \ubd80\ub85d\uc5d0 \uce74\uce74\uc624 \ube0c\ub808\uc778\uc5d0\uc11c \ubc1c\ud45c\ud55c \uc790\uc5f0\uc5b4 \ucc98\ub9ac \ub77c\uc774\ube0c\ub7ec\ub9ac\uc778 *Pororo*(Platform Of neuRal mOdels for natuRal language prOcessing)\ub97c \uc18c\uac1c\ud574\uc8fc\uc2ed\ub2c8\ub2e4. \uc608\uc804\uc5d0 \ubc1c\ud45c \uc18c\uc2dd\uc744 \ub4e4\uc5b4\ubcf4\uae30\ub9cc \ud588\ub294\ub370 \uc774\ubc88 \uae30\ud68c\uc5d0 \uc9c1\uc811\uc801\uc73c\ub85c \uc0ac\uc6a9\ud574\ubcf4\uac8c \ub418\uc5b4\uc11c \uc720\uc775\ud588\uc2b5\ub2c8\ub2e4.\\n\\n### \ucc98\uc74c \uacf5\ubd80\\n\\n\uc804 NLP\ub97c \ub300\ub7b5 \uacf5\ubd80\ud574\ubcf8 \uc801\uc740 \uc788\uc73c\ub098 \uc81c\ub300\ub85c \ubc30\uc6cc\ubcf8 \uc801\uc740 \uc5c6\uc2b5\ub2c8\ub2e4. \uc774 \ucc45\uc744 \ud1b5\ud574\uc11c \uc81c\ub300\ub85c \uacf5\ubd80\ud574\ubcf8 \uac74\ub370 NLP\ub77c\ub294 \ubd84\uc57c\uac00 \uc0c1\ub2f9\ud788 \ub9e4\ub825\uc801\uc774\ub77c\ub294 \uac83\uc744 \ub290\uaf08\uc2b5\ub2c8\ub2e4. \uc804 \uc5b4\ub5a4 \ubd84\uc57c\uc5d0 \uc785\ubb38\ud560 \ub54c \uac00\uc7a5 \uc911\uc694\ud558\uac8c \uc0dd\uac01\ud558\ub294 \uc810\uc774 *\uc7ac\ubc0c\ub294\uac00?*\uc778\ub370 \uc774 \ucc45\uc740 \uadf8 \uc870\uac74\uc5d0 \ubd80\ud569\ud569\ub2c8\ub2e4. \uc774 \ucc45\uc758 \uc800\uc790\uac00 \uc774 \ucc45\uc5d0 \ub2e4\uc591\ud55c \uc9c0\uc2dd\uc744 \ub2f4\uace0 \uc2f6\uc5c8\ub294\uc9c0 \uae30\ucd08\ubd80\ud130 \ucc9c\ucc9c\ud788 \uaf3c\uaf3c\ud558\uac8c \uc54c\ub824\uc90d\ub2c8\ub2e4. \uac00\uc7a5 \uac04\ub2e8\ud55c \uc2e0\uacbd\ub9dd\uc778 *\ud37c\uc149\ud2b8\ub860*\ubd80\ud130 \ud30c\uc774\ud1a0\uce58\ub85c \uad6c\ud604\ud558\uba70 \ubc11\ubc14\ub2e5\ubd80\ud130 \uc704\ub85c \uc62c\ub77c\uac00\uba70 \uad6c\ud604\ud558\ub294 \ub290\ub08c\uc785\ub2c8\ub2e4. \ub2e4\uc591\ud55c \ub0b4\uc6a9\uc774 \ub9ce\uc544 \uc7ac\ubc0c\uac8c \uc785\ubb38\ud558\uac8c \ub410\uc2b5\ub2c8\ub2e4.\\n\\n## \ub300\uc0c1 \ub3c5\uc790\\n\\n\uc704\uc5d0\uc11c\ub294 \uc785\ubb38\uc790\ub3c4 \uc27d\uac8c \uc785\ubb38\ud560 \uc218 \uc788\ub4ef\uc774 \ub9d0\ud588\uc9c0\ub9cc, \uc0ac\uc2e4 \uadf8\uac74 \uc544\ub2d9\ub2c8\ub2e4. \uac1c\uc778\uc801\uc73c\ub85c \uc774 \ucc45\uc744 \uc77d\uae30 \uc804\uc5d0 \uba38\uc2e0\ub7ec\ub2dd \uc9c0\uc2dd\uc774 \uc788\uc73c\uc2e0 \ubd84\uc774 \uc77d\uc73c\uc2dc\uba74 \uc27d\uac8c \uc77d\uc73c\uc2dc\uc73c\ub9ac\ub77c \uc0dd\uac01\ud569\ub2c8\ub2e4. \ucc45\uc5d0\uc11c \ud30c\uc774\ud1a0\uce58 \uae30\ucd08\uc5d0 \ub300\ud574 \uc544\uc8fc \uac04\ub7b5\ud558\uac8c \uc124\uba85\ud558\uc9c0\ub9cc, \uadf8\uac78\ub85c\ub294 \uc870\uae08 \ubd80\uc871\ud569\ub2c8\ub2e4. \uc774\ub7f0 \ubd80\ubd84\uc740 \uacf5\uc2dd \ubb38\uc11c\ub97c \uc77d\uc73c\uba74\uc11c \uacf5\ubd80\ud558\uc2dc\ub294 \ub418\uba74 \ubd80\ubd84\uc774\ub77c \uac71\uc815\uc774 \ub418\uc9c0\ub294 \uc54a\uc9c0\ub9cc, \uadf8\ub798\ub3c4 \uae30\ubcf8\uc801\uc778 \uba38\uc2e0\ub7ec\ub2dd \uc9c0\uc2dd\uc740 \uc788\uc73c\uc2e0 \ubd84\uc774 \uc77d\uc73c\uc2dc\ub294 \uac78 \ucd94\ucc9c\ud574 \ub4dc\ub9bd\ub2c8\ub2e4. \uc608\ub97c \ub4e4\uc5b4 \uba38\uc2e0\ub7ec\ub2dd \uac15\uc758\ub97c \ub4e4\uc740 \uacbd\ud5d8 \ub610\ub294 NumPy\ub85c \uc2e0\uacbd\ub9dd\uc744 \ubc11\ubc14\ub2e5\ubd80\ud130 \uad6c\ud604\ud574\ubcf8 \uacbd\ud5d8, \uc0ac\uc774\ud0b7\ub7f0\uc744 \uc0ac\uc6a9\ud558\uc5ec \uacf5\ubd80\ud574\ubcf4\uc2e0 \ubd84\ub4e4\uc774 \uc788\uaca0\ub124\uc694. \uc774\ub7f0 \uacbd\ud5d8\uc774 \uc788\uc73c\uc2dc\ub2e4\uba74 \ud655\uc2e4\ud788 NLP\uc5d0 \uc785\ubb38\ud558\uae30\uc5d0\ub294 \uc88b\uc740 \ucc45\uc774\ub77c \uc0dd\uac01\ub429\ub2c8\ub2e4."},{"id":"/The-secret-life-of-programs","metadata":{"permalink":"/en/blog/The-secret-life-of-programs","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2021-12-04-The-secret-life-of-programs/The-secret-life-of-programs.md","source":"@site/blog/2021-12-04-The-secret-life-of-programs/The-secret-life-of-programs.md","title":"\ud83d\udcd6 \ud55c \uad8c\uc73c\ub85c \uc77d\ub294 \ucef4\ud4e8\ud130 \uad6c\uc870\uc640 \ud504\ub85c\uadf8\ub798\ubc0d","description":"\ub354 \ub098\uc740 \uc18c\ud504\ud2b8\uc6e8\uc5b4 \uac1c\ubc1c\uc744 \uc704\ud55c \ud558\ub4dc\uc6e8\uc5b4 | \uc790\ub8cc\uad6c\uc870, \ud544\uc218 \uc54c\uace0\ub9ac\uc998 \ub4f1 \ud504\ub85c\uadf8\ub798\uba38\uc758 \ube44\ubc00 \ub178\ud2b8","date":"2021-12-04T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":false,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \ud55c \uad8c\uc73c\ub85c \uc77d\ub294 \ucef4\ud4e8\ud130 \uad6c\uc870\uc640 \ud504\ub85c\uadf8\ub798\ubc0d","date":"2021-12-04","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/The-secret-life-of-programs","description":"\ub354 \ub098\uc740 \uc18c\ud504\ud2b8\uc6e8\uc5b4 \uac1c\ubc1c\uc744 \uc704\ud55c \ud558\ub4dc\uc6e8\uc5b4 | \uc790\ub8cc\uad6c\uc870, \ud544\uc218 \uc54c\uace0\ub9ac\uc998 \ub4f1 \ud504\ub85c\uadf8\ub798\uba38\uc758 \ube44\ubc00 \ub178\ud2b8","keywords":["\ucef4\ud4e8\ud130 \uad6c\uc870","\ucef4\ud4e8\ud130 \uacf5\ud559","\ud558\ub4dc\uc6e8\uc5b4"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \ud30c\uc774\ud1a0\uce58\ub85c \ubc30\uc6b0\ub294 \uc790\uc5f0\uc5b4 \ucc98\ub9ac","permalink":"/en/blog/NLP-with-PyTorch"},"nextItem":{"title":"\ud83d\udcd6 \uc0b4\uc544 \uc6c0\uc9c1\uc774\ub294 \uba38\uc2e0\ub7ec\ub2dd \ud30c\uc774\ud504\ub77c\uc778 \uc124\uacc4","permalink":"/en/blog/building-machine-learning-pipelines"}},"content":"## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/The-secret-life-of-programs.jpg)](https://www.kyobobook.co.kr/product/detailViewKor.laf?ejkGb=KOR&mallGb=KOR&barcode=9791189909284&orderClick=LEa&Kc=)\\n\\n- \uc81c\ubaa9: \ud55c \uad8c\uc73c\ub85c \uc77d\ub294 \ucef4\ud4e8\ud130 \uad6c\uc870\uc640 \ud504\ub85c\uadf8\ub798\ubc0d\\n- \uc800\uc790: Jonathan E. Steinhart\\n- \uc5ed\uc790: \uc624\ud604\uc11d\\n- \ucd9c\ud310\uc0ac: \ucc45\ub9cc\\n- \ucd9c\uac04: 2021-04-08\\n\\n## Book Review\\n\\n\uc81c\ubaa9\uc774 *\ud55c \uad8c\uc73c\ub85c \uc77d\ub294 \ucef4\ud4e8\ud130 \uad6c\uc870\uc640 \ud504\ub85c\uadf8\ub798\ubc0d*\uc774\ub4ef\uc774 \uc800\uc790\uac00 \uc774 \ucc45 \ud55c \uad8c\uc5d0 \uc9c0\uc2dd\uc744 \ucd5c\ub300\ud55c \ub9ce\uc774 \ub123\uc73c\ub824\uace0 \ud55c \ub178\ub825\uc774 \ubcf4\uc785\ub2c8\ub2e4. \uc5b4\ub5bb\uac8c \ubcf4\uba74 \uc7a5\uc810\uc774\uace0 \uc5b4\ub5bb\uac8c \ubcf4\uba74 \ub2e8\uc810\uc774\ub77c\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4\\n\\n\uba3c\uc800 \uc7a5\uc810\uc740 \ubc29\ub300\ud55c \uc9c0\uc2dd\ub4e4\uc744 \uc54c \uc218 \uc788\ub2e4\ub294 \uc810\uc785\ub2c8\ub2e4. \uc774 \ucc45\uc740 \ucd1d 636 \ud398\uc774\uc9c0\ub85c \uc774\ub8e8\uc5b4\uc838 \uc788\uc73c\uba70, \ucef4\ud4e8\ud130\uacf5\ud559(CE) \ubfd0\ub9cc \uc544\ub2c8\ub77c \uc804\uc790\uacf5\ud559(EE)\uae4c\uc9c0 \ub2e4\ub8ec\ub2e4\uace0 \uc0dd\uac01\uc774 \ub4dc\ub294 \ucc45\uc785\ub2c8\ub2e4. \ud604\uc7ac \ucef4\ud4e8\ud130\uacf5\ud559\uc744 \uc804\uacf5\ud558\uace0 \uc788\ub294 \uc800\ub3c4 \uc774 \ucc45\uc744 \uc77d\uc73c\uba74\uc11c \ubab0\ub790\ub358 \uc9c0\uc2dd\ub4e4\uc774 \ub9ce\ub2e4\uace0 \ub290\uaf08\uc73c\ub2c8\uae4c\uc694. \uc989, \uc694\uc998 \ucef4\ud4e8\ud130\uacf5\ud559\uc5d0\uc11c \uc798 \uac00\ub974\uce58\uc9c0 \uc54a\ub294 \ub0b4\uc6a9\ub4e4\uc744 \ud3ec\ud568\ud569\ub2c8\ub2e4. \ub610\ud55c, \ucef4\ud4e8\ud130\uacf5\ud559\uc744 \uc804\uacf5\ud558\uba70 \uad50\uc218\ub2d8\ub4e4\uc758 \uac15\uc758\ub97c \ub4e3\ub2e4 \ubcf4\uba74 \uc608\uc804\uc758 \uc720\ub798\ub77c\ub4e0\uc9c0, \uc2e0\uae30\ud55c \uc9c0\uc2dd\ub4e4\uc744 \uac00\ub054 \ub9d0\uc500\ud574 \uc8fc\uc2dc\ub294\ub370 \uadf8\ub7f0 \ub0b4\uc6a9\ub4e4\uc774 \uc774 \ucc45\uc5d0 \ud3ec\ud568\ub418\uc5b4 \uc788\uc2b5\ub2c8\ub2e4.\\n\\n\ub2e8\uc810\uc740 \ucc45\uc774 \ub450\uaebc\uc6cc\uc11c \uc5b4\ub514\uc5d0\ub3c4 \ub4e4\uace0 \ub2e4\ub2c8\uae30\ub294 \uc5b4\ub835\uc2b5\ub2c8\ub2e4. \uc9c4\uc9dc \ubb34\uac81\uc2b5\ub2c8\ub2e4. \uc804 \uc774 \ucc45\uc744 \ub2e4\uc2dc \uc77d\ub294\ub2e4\uba74 \uaf2d \ubd84\ucca0\ud574\uc11c \uc77d\uace0 \uc2f6\uc2b5\ub2c8\ub2e4. \ub610\ud55c, \ucc45\uc744 \uc77d\ub294\ub370 \uaf64 \uc9c0\ub8e8\ud558\ub2e4\ub294 \uc810\uc785\ub2c8\ub2e4. \uc800\uc790\uaed8\uc11c \ub0b4\uc6a9\uc744 \ucd5c\ub300\ud55c \uc27d\uac8c \uc124\uba85\ud574 \uc8fc\uc9c0\ub9cc, \ub9e4\uc77c \ud55c, \ub450 \uc7a5\uc529 \uc77d\uc5b4\ub3c4 \uc9c4\ub3c4\uac00 \uc798 \uc548 \ub098\uac14\uc2b5\ub2c8\ub2e4. \ud3c9\uc18c \ucc45\uc744 \ube68\ub9ac \uc77d\ub294\ub2e4\uace0 \uc0dd\uac01\uc774 \ub418\ub294 \uc81c\uac00 \ucc45\uc744 \uc77d\ub294\ub370 \uc608\uc0c1\uc678\ub85c \uc624\ub798 \uac78\ub838\uc2b5\ub2c8\ub2e4.\\n\\n\uc774 \ucc45\uc744 \uc77d\uae30 \uc804 \uc6f9\uc5d0 \ub300\ud574\uc11c\ub3c4 \uac04\ub2e8\ud558\uac8c \uc54c\uace0 \uc788\uc73c\uc2dc\uba74 \uc77d\uae30 \uc218\uc6d4\ud560 \uac83 \uac19\uc2b5\ub2c8\ub2e4. 9\uc7a5\uc5d0\uc11c \uac11\uc790\uae30 \uc6f9\uc5d0 \ub300\ud55c \uc774\uc57c\uae30\ub97c \ud558\uba74\uc11c \uc800\uc5d0\uac8c \uc775\uc219\ud558\uc9c0 \uc54a\uc740 \uc790\ubc14\uc2a4\ud06c\ub9bd\ud2b8 \ucf54\ub4dc\uac00 \ub098\uc640\uc11c \uc870\uae08 \ub180\ub7ac\uc2b5\ub2c8\ub2e4. \\n\\n## \ub300\uc0c1 \ub3c5\uc790\\n\\n\ucef4\ud4e8\ud130\uacf5\ud559\uc744 \uc774\uc81c \ub9c9 \uc804\uacf5\ud558\ub294 \uc2e0\uc785\uc0dd, \ube44\uc804\uacf5\uc790\uc5d0\uac8c\ub294 \ucd94\ucc9c\ud558\uc9c0 \uc54a\uc2b5\ub2c8\ub2e4. \uc774 \ucc45\uc740 \ub2e4\uc591\ud55c \uc11c\uc810\uc5d0\uc11c \ubca0\uc2a4\ud2b8\uc140\ub7ec\ub85c \uc62c\ub77c\uc640 \uc788\uc9c0\ub9cc \uadf8\ub807\ub2e4\uace0 \ud574\uc11c \uc785\ubb38\uc790\ub4e4\uc774 \uc27d\uac8c \uc77d\uc744 \uc218 \uc788\ub294 \ucc45\uc774 \uc804\ud600 \uc544\ub2c8\ub77c\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4.\\n\\n\uac1c\uc778\uc801\uc73c\ub85c \uc774 \ucc45\uc740 \uacf5\ud559\ub3c4\ub4e4\uc774 \uc77d\uc5b4\ubcf4\uba74 \uc88b\uc740 \ubcf5\uc2b5\uc6a9 \ucc45\uc774\ub77c\uace0 \ub290\uaef4\uc9d1\ub2c8\ub2e4. \ucef4\ud4e8\ud130\uacf5\ud559(CE)\ub97c \uc804\uacf5\ud558\ub294 \ud559\uc0dd\ubfd0\ub9cc \uc544\ub2c8\ub77c, \ucef4\ud4e8\ud130\uacfc\ud559(CS), \uc804\uc790\uacf5\ud559(EE) \ub4f1 \uae30\ucd08 CS \uacfc\ubaa9\uc5d0 \ub300\ud55c \uacf5\ubd80\ub97c \ud588\ub358 \uacbd\ud5d8\uc774 \uc788\uc73c\uc2dc\ub2e4\uba74 \uc77d\uc5b4\ubcf4\uba74 \ub9e4\uc6b0 \ub3c4\uc6c0\uc774 \ub420 \uac83\uc774\ub77c \uc0dd\uac01\ud569\ub2c8\ub2e4. \uc18c\uc704 \uc694\uc998 \uac00\ub974\uce58\uc9c0 \uc54a\ub294 \uc7ac\ubc0c\ub294 \uc9c0\uc2dd\ub4e4\ub3c4 \uac00\ub974\uccd0\uc90d\ub2c8\ub2e4. \uc608\ub97c\ub4e4\uc5b4 \uba87\uba87 \ud559\ubd80\ub294 \uc218\uce58\ud574\uc11d\uc744 \uc548 \uac00\ub974\uce58\ub358\ub370 CORDIC \uc54c\uace0\ub9ac\uc998\ucc98\ub7fc \uc218\uce58\ud574\uc11d\uc744 \uc0b4\uc9dd \ub9db\ubcf4\ub294 \ub0b4\uc6a9\uc774 \uc788\uc2b5\ub2c8\ub2e4. \uc804 \uc774\ub7f0 \ub0b4\uc6a9\ub4e4\uc744 \uc624\ub79c\ub9cc\uc5d0 \ub2e4\uc2dc \uacf5\ubd80\ud560 \uc218 \uc788\uc5b4 \ub9ce\uc740 \ub3c4\uc6c0\uc774 \ub410\ub2e4\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4."},{"id":"/building-machine-learning-pipelines","metadata":{"permalink":"/en/blog/building-machine-learning-pipelines","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2021-11-20-building-machine-learning-pipelines/building-machine-learning-pipelines.md","source":"@site/blog/2021-11-20-building-machine-learning-pipelines/building-machine-learning-pipelines.md","title":"\ud83d\udcd6 \uc0b4\uc544 \uc6c0\uc9c1\uc774\ub294 \uba38\uc2e0\ub7ec\ub2dd \ud30c\uc774\ud504\ub77c\uc778 \uc124\uacc4","description":"\ud150\uc11c\ud50c\ub85c\uc640 \ud568\uaed8\ud558\ub294 \uba38\uc2e0\ub7ec\ub2dd \ud504\ub85c\uc81d\ud2b8 \uc790\ub3d9\ud654","date":"2021-11-20T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \uc0b4\uc544 \uc6c0\uc9c1\uc774\ub294 \uba38\uc2e0\ub7ec\ub2dd \ud30c\uc774\ud504\ub77c\uc778 \uc124\uacc4","date":"2021-11-20","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/building-machine-learning-pipelines","description":"\ud150\uc11c\ud50c\ub85c\uc640 \ud568\uaed8\ud558\ub294 \uba38\uc2e0\ub7ec\ub2dd \ud504\ub85c\uc81d\ud2b8 \uc790\ub3d9\ud654","keywords":["\uba38\uc2e0\ub7ec\ub2dd","\ud30c\uc774\ud504\ub77c\uc778","\ub370\uc774\ud130 \uc5d4\uc9c0\ub2c8\uc5b4\ub9c1"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \ud55c \uad8c\uc73c\ub85c \uc77d\ub294 \ucef4\ud4e8\ud130 \uad6c\uc870\uc640 \ud504\ub85c\uadf8\ub798\ubc0d","permalink":"/en/blog/The-secret-life-of-programs"},"nextItem":{"title":"\ud83d\udcd6 \uac1c\ubc1c\uc790\uc758 \uae00\uc4f0\uae30","permalink":"/en/blog/developer-writing"}},"content":":::info\\n\ud55c\ube5b\ubbf8\ub514\uc5b4 \\\\<\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4\\\\> \ud65c\ub3d9\uc744 \uc704\ud574\uc11c \ucc45\uc744 \uc81c\uacf5\ubc1b\uc544 \uc791\uc131\ub41c \uc11c\ud3c9\uc785\ub2c8\ub2e4.\\n:::\\n\\n![\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4 2021](../assets/I-am-reviewer-2021.jpg)\\n\\n## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/building-machine-learning-pipelines.jpg)](https://www.kyobobook.co.kr/product/detailViewKor.laf?ejkGb=KOR&mallGb=KOR&barcode=9791162244814&orderClick=LEa&Kc=)\\n\\n- \uc81c\ubaa9: \uc0b4\uc544 \uc6c0\uc9c1\uc774\ub294 \uba38\uc2e0\ub7ec\ub2dd \ud30c\uc774\ud504\ub77c\uc778 \uc124\uacc4\\n- \uc800\uc790: Hannes Hapke, Catherine Nelson\\n- \uc5ed\uc790: \uc1a1\ud638\uc5f0\\n- \ucd9c\ud310\uc0ac: \ud55c\ube5b\ubbf8\ub514\uc5b4\\n- \ucd9c\uac04: 2021-10-11\\n\\n{/* truncate */}\\n\\n## \ucc45 \uc18c\uac1c\\n\\n### \uba38\uc2e0\ub7ec\ub2dd \uc9c0\uc2dd\uacfc \ud074\ub77c\uc6b0\ub4dc \uc9c0\uc2dd\\n\\n\uba38\uc2e0\ub7ec\ub2dd \ud30c\uc774\ud504\ub77c\uc778\uc744 \uc124\uacc4\ud574\ubcf4\ub824\uba74 \uc124\uacc4 \uc804\uc5d0 \uba38\uc2e0\ub7ec\ub2dd\uc5d0 \ub300\ud55c \uae30\ubcf8 \uc9c0\uc2dd\uc740 \uc54c\uc544\uc57c\uaca0\uc8e0? \uc774 \ucc45\uc744 \uc77d\uae30 \uc804\uc5d0 TensorFlow\uc640 Keras \ucf54\ub4dc \uc77d\uae30\uac00 \uc775\uc219\ud558\uc2e0\uc9c0 \uc0dd\uac01\ud574\ubcf4\uc2dc\uae38 \ubc14\ub78d\ub2c8\ub2e4. \ub610\ud55c, \ud30c\uc774\uc36c\uc5d0\ub3c4 \uc5c4\uccad \uc775\uc219\ud558\uc154\uc57c \ud569\ub2c8\ub2e4! \uc774 \ucc45\uc744 \uace0\ub824\ud558\uc2dc\ub294 \ubd84\uc774\ub77c\uba74 \ub2f9\uc5f0\ud558\uc2e4 \uac70\ub77c \ubbff\uc2b5\ub2c8\ub2e4.\\n\\n\ub610\ud55c, \uc774 \ucc45\uc744 \uc77d\uae30 \uc704\ud55c \ud544\uc218 \uc9c0\uc2dd\uc740 \uc544\ub2c8\uc9c0\ub9cc \ud074\ub77c\uc6b0\ub4dc\ub97c \uc0ac\uc6a9\ud574\ubcf4\uc2e0 \ubd84\uc774 \uc77d\uc73c\uc2dc\uba74 \ucc45\uc744 \uc77d\uc73c\uc2dc\ub294 \ub370 \ub3c4\uc6c0\uc774 \ub420 \uac83\uc785\ub2c8\ub2e4. \ud2b9\ud788 GCP(Google Cloud Platform) \ub610\ub294 AWS(Amazon Web Services) \ud658\uacbd\uc744 \ud55c \ubc88\uc774\ub77c\ub3c4 \uc0ac\uc6a9\ud574\ubcf4\uc168\uc73c\uba74 \ub429\ub2c8\ub2e4. \ud30c\uc774\ud504\ub77c\uc778\uc744 \uc124\uacc4\ud558\uba74\uc11c \ud074\ub77c\uc6b0\ub4dc \uc11c\ube44\uc2a4\ub97c \ud65c\uc6a9\ud558\uc5ec \ucd5c\uc801\ud654\ud560 \uc218 \uc788\ub294 \ubc29\ubc95\ub4e4\uc744 \uc124\uba85\ud558\uae30\uc5d0 \uc0ac\uc804\uc5d0 GCP \ub610\ub294 AWS\ub97c \uc0ac\uc6a9\ud574\ubcf4\uc168\ub2e4\uba74 \uc77d\uc73c\uc2dc\ub294 \ub370 \ud6e8\uc52c \ud3b8\ud560 \uac83\uc785\ub2c8\ub2e4. \\n\\n### \uc804\ubc18\uc801\uc778 \ub0b4\uc6a9 \uc815\ub9ac\\n\\nTensorFlow Extended(TFX)\ub97c \uc0ac\uc6a9\ud558\uc5ec \ub370\uc774\ud130 \uc218\uc9d1 -> \ub370\uc774\ud130 \uac80\uc99d -> \ub370\uc774\ud130 \uc804\ucc98\ub9ac -> \ubaa8\ub378 \ud559\uc2b5 -> \ubaa8\ub378 \ubd84\uc11d \ubc0f \uac80\uc99d -> \ubaa8\ub378 \ubc30\ud3ec \uc21c\uc73c\ub85c \ud30c\uc774\ud504\ub77c\uc778\uc744 \uc124\uacc4\ud574\ubd05\ub2c8\ub2e4. \uc804\ubc18\uc801\uc73c\ub85c \ud150\uc11c\ud50c\ub85c\uc6b0 \uc0dd\ud0dc\uacc4\ub97c \uc0ac\uc6a9\ud574\ubcf8\ub2e4\uace0 \uc0dd\uac01\ud558\uc2dc\uba74 \ub429\ub2c8\ub2e4. TFX \uacf5\uc2dd \ubb38\uc11c\uac00 \uc798 \ubc88\uc5ed\uc774 \ub418\uc5b4 \uc788\uc5b4\uc11c \uacf5\uc2dd \ubb38\uc11c\ub3c4 \ubcf4\uc2dc\uba74\uc11c \ucc45\uc744 \uc77d\uc73c\uc2dc\uba74 \ub354\uc6b1 \ub3c4\uc6c0\uc774 \ub420 \uac83 \uac19\uc2b5\ub2c8\ub2e4!\\n\\n\ub9c8\uc9c0\ub9c9\uc73c\ub85c\ub294 \ud30c\uc774\ud504\ub77c\uc778\uc744 \uc2e4\ud589\ud558\ub294 \ub370 \uc0ac\uc6a9\ud558\ub294 \uc624\ucf00\uc2a4\ud2b8\ub808\uc774\uc158 \ub3c4\uad6c\ub97c \uc0ac\uc6a9\ud574\ubd05\ub2c8\ub2e4. \uac1c\uc778\uc801\uc73c\ub85c \ud30c\uc774\ud504\ub77c\uc778\uc744 \uc124\uacc4\ud558\ub294 \ubd80\ubd84\ub3c4 \ud765\ubbf8\uac00 \uc788\uc5b4 \uc7ac\ubc0c\uac8c \uc77d\uc5c8\uc9c0\ub9cc, \ub9c8\uc9c0\ub9c9 \ubd80\ubd84\uc5d0 \uc138 \uac00\uc9c0 \uc624\ucf00\uc2a4\ud2b8\ub808\uc774\uc158 \ub3c4\uad6c(Apache Beam, Apache Airflow, Kubeflow)\ub97c \uc124\uba85\ud558\ub294 \ubd80\ubd84\uc744 \uaf64 \uc7ac\ubc0c\uac8c \uc77d\uc5c8\uc2b5\ub2c8\ub2e4. \ucee4\ubba4\ub2c8\ud2f0\uc5d0\uc11c \ub4e3\uae30\ub9cc \ud574\ubd24\ub358 \ub3c4\uad6c\ub4e4\uc774\ub77c \uc774 \ucc45\uc744 \ud1b5\ud574 \uc0ac\uc6a9\ud574\ubcfc \uae30\ud68c\uac00 \uc0dd\uaca8\uc11c \ub3c4\uc6c0\uc774 \ub410\uc2b5\ub2c8\ub2e4.\\n\\n### \ubd80\ub85d\\n\\n\ubd80\ub85d\uc5d0\uc11c\ub294 \uba38\uc2e0\ub7ec\ub2dd\uc5d0 \uc720\uc6a9\ud55c \uc778\ud504\ub77c \ub3c4\uad6c\uc774\uba70 \ucee8\ud14c\uc774\ub108\ub97c \uad00\ub9ac\ud558\ub294 \ub3c4\ucee4\ub098 \ucfe0\ubc84\ub124\ud2f0\uc2a4\ub97c \uac04\ub2e8\ud788 \uc18c\uac1c\ud569\ub2c8\ub2e4. \ub610\ud55c, \uad6c\uae00 \ud074\ub77c\uc6b0\ub4dc\uc5d0 \ucfe0\ubc84\ub124\ud2f0\uc2a4 \ud074\ub7ec\uc2a4\ud130\ub97c \uc124\uc815\ud574\ubd05\ub2c8\ub2e4. \ub3c4\ucee4\ub098 \ucfe0\ubc84\ub124\ud2f0\uc2a4\ub97c \uc0ac\uc6a9\ud574\ubcf4\uc2dc\uc9c0 \uc54a\uc73c\uc168\ub354\ub77c\uba74 \ubd80\ub85d\uc5d0\uc11c \uccb4\ud5d8\ud574\ubcf4\uc2e4 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \\n\\n## \ub300\uc0c1 \ub3c5\uc790\\n\\n\uc774 \ucc45\uc740 \uba38\uc2e0\ub7ec\ub2dd \ud504\ub808\uc784\uc6cc\ud06c\uc5d0 \uc775\uc219\ud558\uc2dc\uace0 \uba38\uc2e0\ub7ec\ub2dd \ud30c\uc774\ud504\ub77c\uc778\uc744 \uc9c1\uc811 \uc124\uacc4\ud574\ubcf4\uc2dc\uace0 \uc2f6\uc740 \ubd84\ub4e4\uaed8 \ucd94\ucc9c\ud569\ub2c8\ub2e4. \ube44\ub85d \uc774 \ucc45\uc758 \uba38\uc2e0\ub7ec\ub2dd \ucf54\ub4dc\uac00 \ud150\uc11c\ud50c\ub85c\uc640 \ucf00\ub77c\uc2a4 \uae30\ubc18\uc774\uc9c0\ub9cc, \ud575\uc2ec\uc801\uc778 \uac1c\ub150\uc740 \uc5b4\ub5a0\ud55c \ud504\ub808\uc784\uc6cc\ud06c\uc5d0\uc11c\ub3c4 \uc801\uc6a9\ud560 \uc218 \uc788\uc744 \uac83\uc73c\ub85c \uc608\uc0c1\ub429\ub2c8\ub2e4. \\n\\nTensorFlow \uc0dd\ud0dc\uacc4\uc5d0 \uad00\uc2ec\uc774 \ub9ce\uc73c\uc2e0 \uba38\uc2e0\ub7ec\ub2dd \uc5d4\uc9c0\ub2c8\uc5b4\ubd84\ub4e4\uc774 \uc77d\uc73c\uc154\ub3c4 \ub3c4\uc6c0\uc774 \ub420 \ucc45\uc774\ub77c\uace0 \uc608\uc0c1\ud569\ub2c8\ub2e4. \ub610\ud55c, MLOps\uc5d0 \uad00\uc2ec\uc774 \uc788\uc73c\uc2e0 DevOps \uc5d4\uc9c0\ub2c8\uc5b4 \ubd84\uc774 \uc77d\uc73c\uc154\ub3c4 \ub3c4\uc6c0\uc774 \ub420 \ucc45\uc774\ub77c\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4."},{"id":"/developer-writing","metadata":{"permalink":"/en/blog/developer-writing","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2021-11-05-developer-writing/developer-writing.md","source":"@site/blog/2021-11-05-developer-writing/developer-writing.md","title":"\ud83d\udcd6 \uac1c\ubc1c\uc790\uc758 \uae00\uc4f0\uae30","description":"\ubcc0\uc218 \ub124\uc774\ubc0d\ubd80\ud130 \ub9b4\ub9ac\uc2a4 \ub178\ud2b8, \uc7a5\uc560 \ubcf4\uace0\uc11c, \uae30\uc220 \ube14\ub85c\uadf8\uae4c\uc9c0! | \ud504\ub85c\uadf8\ub798\uba38\uc758 \uae00\uc4f0\uae30 \uace0\ubbfc \ub05d!","date":"2021-11-05T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":false,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \uac1c\ubc1c\uc790\uc758 \uae00\uc4f0\uae30","date":"2021-11-05","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/developer-writing","description":"\ubcc0\uc218 \ub124\uc774\ubc0d\ubd80\ud130 \ub9b4\ub9ac\uc2a4 \ub178\ud2b8, \uc7a5\uc560 \ubcf4\uace0\uc11c, \uae30\uc220 \ube14\ub85c\uadf8\uae4c\uc9c0! | \ud504\ub85c\uadf8\ub798\uba38\uc758 \uae00\uc4f0\uae30 \uace0\ubbfc \ub05d!","keywords":["\uac1c\ubc1c\uc790","\uae00\uc4f0\uae30","\uae30\uc220 \ubb38\uc11c","\ube14\ub85c\uadf8"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \uc0b4\uc544 \uc6c0\uc9c1\uc774\ub294 \uba38\uc2e0\ub7ec\ub2dd \ud30c\uc774\ud504\ub77c\uc778 \uc124\uacc4","permalink":"/en/blog/building-machine-learning-pipelines"},"nextItem":{"title":"\ud83d\udcd6 \ucee8\ud14c\uc774\ub108 \uc778\ud504\ub77c \ud658\uacbd \uad6c\ucd95\uc744 \uc704\ud55c \ucfe0\ubc84\ub124\ud2f0\uc2a4/\ub3c4\ucee4","permalink":"/en/blog/k8s-and-docker-for-container-infrastructure"}},"content":"## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/developer-writing.jpg)](http://www.kyobobook.co.kr/product/detailViewKor.laf?ejkGb=KOR&mallGb=KOR&barcode=9791158391744&orderClick=LEa&Kc=)\\n\\n- \uc81c\ubaa9: \uac1c\ubc1c\uc790\uc758 \uae00\uc4f0\uae30\\n- \uc800\uc790: \uae40\ucca0\uc218\\n- \ucd9c\ud310\uc0ac: \uc704\ud0a4\ubd81\uc2a4\\n- \ucd9c\uac04: 2019-10-04\\n\\n## Book Review\\n\\n\uc790\uc138\ud55c \ucf54\ub4dc\ub4e4\uc744 \ubcf4\uc5ec\uc8fc\uba74\uc11c \ub2e4\uc591\ud55c \uacbd\uc6b0\uc5d0 \ub300\ud574 \uc774\ud574\uac00 \uc798 \ub418\ub3c4\ub85d \uc124\uba85\ud574\uc90d\ub2c8\ub2e4. \ub610\ud55c, \ub300\ubd80\ubd84\uc758 \ub0b4\uc6a9\ub4e4\uc5d0 \uc608\uc2dc \ub610\ub294 \uc0ac\ub840\uac00 \uc788\uae30\uc5d0 \ub3c5\uc790 \uc785\uc7a5\uc5d0\uc11c \ucc45 \uc77d\uae30 \ub9e4\uc6b0 \uc218\uc6d4\ud588\uc2b5\ub2c8\ub2e4.\\n\\n\uc81c\ubaa9\uc774 *\uac1c\ubc1c\uc790\uc758 \uae00\uc4f0\uae30*\ub77c \uc774 \ucc45\uc5d0\uc11c \ub9d0\ud558\ub294 *\uae00\uc4f0\uae30*\uac00 \ubb54\uc9c0 \uad81\uae08\ud588\uc5c8\ub294\ub370 \uc77d\uace0 \ub098\uc11c \uc54c\uac8c \ub410\uc2b5\ub2c8\ub2e4. \uc774 \ucc45\uc5d0\uc11c *\uae00\uc4f0\uae30*\ub780 \uc77c\ubc18\uc801\uc778 \ube44\uc988\ub2c8\uc2a4 \uae00\uc4f0\uae30\uac00 \uc544\ub2cc \uc815\ub9d0 *\uac1c\ubc1c\uc790\ub97c \uc704\ud55c \uae00\uc4f0\uae30*\ub97c \ub9d0\ud569\ub2c8\ub2e4. \uac1c\ubc1c\ud558\uba74\uc11c \ud544\uc694\ud55c \uae00, \ubb38\uc7a5 \ub4f1\uc744 \uc5b4\ub5bb\uac8c \uc4f8 \uc9c0\uc5d0 \ub300\ud574 \uc124\uba85\ud569\ub2c8\ub2e4. \ud3c9\uc18c \uac1c\ubc1c\ud558\uba74\uc11c \uc5b4\ub835\uac8c \uc0dd\uac01\ud558\ub294 \ubcc0\uc218 \ub124\uc774\ubc0d, \uc8fc\uc11d, \uc5d0\ub7ec \uba54\uc2dc\uc9c0 \ub4f1 \uc0dd\uac01\ud558\uae30 \ud798\ub4e0 \uac83\ub4e4 \uc880 \ub354 \ud3b8\ud558\uac8c \uace0\ub824\ud574\ubcfc \uc218 \uc788\ub3c4\ub85d \ub3c4\uc640\uc8fc\ub294 \ucc45\uc785\ub2c8\ub2e4. \\n\\n### \uc0ac\uc6a9\uc790(\ub3c5\uc790) \uc785\uc7a5\uc5d0\uc11c \uae00\uc4f0\uae30\\n\\n\uc774 \ucc45\uc5d0\uc11c \uac1c\ubc1c\uc790\uc758 \uad00\uc810\uacfc \ub3c5\uc790\uc758 \uad00\uc810\uc740 \ub2e4\ub974\ub2e4\ub294 \uac83\uc744 \uac15\uc870\ud569\ub2c8\ub2e4. \uadf8\ub807\uae30\uc5d0 \ub3c5\uc790\ub97c \ub204\uad6c\ub85c \uc120\uc815\ud558\ub294 \uac00\uc5d0 \ub530\ub77c \uae00\uc4f0\uae30 \ubc29\uc2dd\uc740 \ub2ec\ub77c\uc9c0\uba70, \uae00\uc744 \ubcf4\ub294 \uc0ac\ub78c\uc774 \ubb34\uc5c7\uc5d0 \uad00\uc2ec\uc774 \uc788\ub294 \uac00\uc5d0 \ub300\ud574 \uc0dd\uac01\ud558\uace0 \uae00\uc744 \uc791\uc131\ud574\uc57c \ud55c\ub2e4\uace0 \ub9d0\ud569\ub2c8\ub2e4. \uc0ac\uc2e4 \uc5b4\ub5bb\uac8c \uc0dd\uac01\ud558\uba74 \ub2f9\uc5f0\ud55c \uc774\uc57c\uae30\uc9c0\ub9cc, \ud3c9\uc18c \uc81c\uac00 \uae00\uc744 \uc791\uc131\ud558\uba74\uc11c *\ub3c5\uc790\ub97c \uace0\ub824\ud558\uba70 \uae00\uc744 \uc37c\ub294\uac00* \uc758\ubb38\uc774 \uc0dd\uacbc\uc73c\uba70, \ub2e4\uc2dc \uc0dd\uac01\ud574\ubcf4\uac8c \ub410\uc2b5\ub2c8\ub2e4.\\n\\n## \ub3c4\uc6c0\uc774 \ub41c \ub0b4\uc6a9\\n\\n### \ud55c\uad6d\uc5b4 \ubb38\ubc95\\n\\n\ucd5c\uadfc SNS \ub610\ub294 \ube14\ub85c\uadf8\ub97c \ubcf4\uba74 \ub300\ubd80\ubd84\uc758 \uc0ac\ub78c\ub4e4\uc774 \ub9de\ucda4\ubc95\uc744 \uc2e0\uacbd\uc4f0\uc9c0 \uc54a\uace0 \uc4f0\ub294 \uacbd\uc6b0\uac00 \ub9ce\uc2b5\ub2c8\ub2e4. \uacf5\uc2dd\uc801\uc778 \uc0c1\ud669\uc774 \uc544\ub2c8\ub77c\uba74 \ud06c\uac8c \uc911\uc694\ud558\uc9c0\ub294 \uc54a\uace0, \ube44\uacf5\uc2dd\uc801\uc778 \uc0c1\ud669\uc5d0\uc11c \uc644\ubcbd\ud788 \uc9c0\ud0a4\uba74 \ub610 \ub531\ub531\ud574\ubcf4\uc774\uae30 \ub54c\ubb38\uc5d0 \uaf2d \uc9c0\ucf1c\uc11c \uc0ac\uc6a9\ud558\uc9c0\ub294 \uc54a\uc8e0. \uc800\ub3c4 \ube14\ub85c\uadf8\uc5d0 \uc4f8 \ub54c \ub9d0\uace0\ub294 \uc644\ubcbd\ud558\uac8c \uc9c0\ud0a4\uc9c0\ub294 \uc54a\uc2b5\ub2c8\ub2e4. \uadf8\ub7ec\ub2e4\ubcf4\ub2c8 \uac00\ub054 \ub9de\ucda4\ubc95\uc774 \ud5f7\uac08\ub9ac\ub294 \uacbd\uc6b0\ub4e4\ub3c4 \uc788\uc2b5\ub2c8\ub2e4. \uc774 \ucc45\uc5d0\uc11c\ub294 \uae00\uc744 \uc4f0\uba74\uc11c \ud070\ub530\uc634\ud45c\uc640 \uc791\uc740\ub530\uc634\ud45c \uc0ac\uc6a9\ubc95, \uc678\ub798\uc5b4 \ud45c\uae30\ubc95 \ub4f1 \uac19\uc774 \ud5f7\uac08\ub9b4\ub9cc\ud55c \ub9de\ucda4\ubc95\ub4e4\uc744 \uc54c\ub824\uc90d\ub2c8\ub2e4.\\n\\n\\"\ud070\ub530\uc634\ud45c\ub294 \uae00\uc5d0\uc11c \uc9c1\uc811 \ub300\ud654\ub97c \ud45c\uc2dc\ud558\uac70\ub098 \ub9d0\uc774\ub098 \uae00\uc744 \uc778\uc6a9\ud560 \ub54c \uc0ac\uc6a9\ud55c\ub2e4. \uc791\uc740\ub530\uc634\ud45c\ub294 \uc778\uc6a9\ud55c \ub9d0 \uc548\uc5d0 \uc788\ub294 \uc778\uc6a9\ud55c \ub9d0, \ub610\ub294 \ub9c8\uc74c\uc18d\uc73c\ub85c \ud55c \ub9d0\uc744 \uc4f8 \ub54c \uc0ac\uc6a9\ud55c\ub2e4.\\"\\n\\n### \ub2e4\uc591\ud55c \ub73b\uc744 \uac00\uc9c4 \uc601\uc5b4 \ub2e8\uc5b4\\n\\n\uc804 \ucee4\ubc0b \uba54\uc2dc\uc9c0\ub97c \uc791\uc131\ud560 \ub54c \uc8fc\ub85c change\uc640 modify\ub97c \uc790\uc8fc \uc0ac\uc6a9\ud558\ub294 \ud3b8\uc785\ub2c8\ub2e4. \uac00\ub054 *revise*\ub77c\ub294 \ub2e8\uc5b4\ub294 \uc5b4\ub5bb\uac8c \uc368\uc57c\ud560 \uc9c0 \uace0\ubbfc\uc774\uc5c8\ub294\ub370 \uc774 \ucc45\uc5d0\uc11c\ub294 \uadf8\ub7f0 \uace0\ubbfc\ub3c4 \ud574\uacb0\ud574\uc90d\ub2c8\ub2e4. \\n\\n\\"change\ub294 \ub0b4\uc6a9\uc744 \ub2e8\uc21c\ud788 \ubc14\uafb8\ub294 \uac83, modify\ub294 \uc798\ubabb\ub41c \uac83\uc744 \ubc14\ub85c\uc7a1\uc744 \ub54c \uc0ac\uc6a9, revise\ub294 \uae30\uc874\uc5d0 \uc5c6\ub358 \uc0c8\ub85c\uc6b4 \uc815\ubcf4\ub098 \uc544\uc774\ub514\uc5b4\ub97c \ub367\ubd99\uc5ec \uae30\uc874 \ub0b4\uc6a9\uacfc \ub2ec\ub77c\uc84c\uc74c\uc744 \ubd84\uba85\ud788 \ud560 \ub54c \uc0ac\uc6a9\ud55c\ub2e4.\\"\\n\\n### \ucf54\ub4dc \ub124\uc774\ubc0d \ucee8\ubca4\uc158\\n\\n\\"\ucf54\ub4dc \ub124\uc774\ubc0d \ucee8\ubca4\uc158\uc774 \uc874\uc7ac\ud558\ub294 \uc774\uc720\ub294 \ucf54\ub4dc\ub97c \uc77d\uae30 \uc27d\uac8c \ub9cc\ub4e4\uace0 \ub2e4\ub978 \uac1c\ubc1c\uc790\uc640 \uc18c\ud1b5\ud558\uae30 \uc704\ud574\uc11c\uc774\ub2e4. \uc57d\uc5b4\ub97c \ub9cc\ub4dc\ub294 \uc88b\uc740 \ubc29\ubc95\uc740 \ubcf4\ud3b8\uc131\uc744 \uae30\uc900\uc73c\ub85c \uc815\ud558\ub294 \uac83\uc774\ub2e4. \uc911\uc694\ud55c \ub2e8\uc5b4\ub97c \uc55e\uc5d0 \uc4f4\ub2e4.\\"\\n\\n### \uae30\uc220 \ube14\ub85c\uadf8 \\n\\n\\"\ubb34\ud131\ub300\uace0 \uae00\ubd80\ud130 \uc4f0\uba74 \uc548 \ub418\uace0, \ucda9\ubd84\ud788 \uc0dd\uac01\ud574\uc11c \uc8fc\uc81c\ub97c \uc815\ud558\uace0 \uc8fc\uc81c \uc758\uc2dd\uc744 \ud655\ub9bd\ud55c \ud6c4 \uae00\uc744 \uc4f8 \ub54c \ud544\uc694\ud55c \uc790\ub8cc\ub098 \uc544\uc774\ub514\uc5b4\ub97c \uad6c\ud574\uc57c \ud55c\ub2e4.\\"\\n\\n### \uae30\ud0c0\\n\\n\uc774\ubc16\uc5d0\ub3c4 \ub3c4\uc6c0\uc774 \ub418\ub294 \ub0b4\uc6a9\ub4e4\uc740 \ub9ce\uc558\uc73c\ub098, \ud604\uc7ac \uc800\uc5d0\uac8c\ub294 \ud544\uc694\ud558\uc9c0 \uc54a\uc740 \ub0b4\uc6a9\uc774\ub77c \uc0dd\ub7b5\ud569\ub2c8\ub2e4. \\n\\n## \ub300\uc0c1\ub3c5\uc790\\n\\n\uc804 \uac1c\ubc1c \ube14\ub85c\uadf8\ub97c \uc6b4\uc601\ud558\uace0 \uc788\uae30\uc5d0 \uac1c\ubc1c\uc790\uc758 \uc785\uc7a5\uc5d0\uc11c \uc5b4\ub5bb\uac8c \uae00\uc744 \uc791\uc131\ud558\uba74 \uc88b\uc744 \uc9c0\uc5d0 \ub300\ud574 \uad81\uae08\uc99d\uc774 \uc0dd\uaca8 \uc774 \ucc45\uc744 \uc77d\uc5c8\uc2b5\ub2c8\ub2e4. \ub3c4\uc6c0\uc774 \ub418\ub294 \ub0b4\uc6a9\ub4e4\uc774 \ub9ce\uc9c0\ub9cc, \uadf8\ub807\uc9c0 \uc54a\uc740 \ubd80\ubd84\ub4e4\ub3c4 \uc788\uc2b5\ub2c8\ub2e4. \uae30\uc220 \ube14\ub85c\uadf8\uc5d0 \uad00\ud55c \uae00\uc4f0\uae30\uac00 \uc911\uc810\uc740 \uc544\ub2c8\ub77c\ub294 \uac83\uc785\ub2c8\ub2e4. \uadf8\ub807\uae30\uc5d0 \uc800\ucc98\ub7fc \uae30\uc220 \ube14\ub85c\uadf8 \uc791\uc131\uc5d0 \uad00\uc2ec\uc774 \uc788\uc73c\uc2e0 \ubd84\uc774\ub77c\uba74, \ud544\uc694\ud55c \ubd80\ubd84\ub9cc \uc77d\uc5b4\ubcf4\uc2dc\uae30 \ubc14\ub78d\ub2c8\ub2e4. \uc544\ubb34\ub798\ub3c4 \uc5c5\ubb34\uc640 \uad00\ub828\ub41c \ub0b4\uc6a9\ub4e4\ub3c4 \uaf64 \uc788\uc5b4\uc11c \ud604\uc5c5\uc5d0 \uc788\uc73c\uc2e0 \ubd84\uc774 \uc544\ub2cc \ud559\uc0dd\ubd84\ub4e4\uc774\uc2dc\ub77c\uba74, \ub300\uc0c1\uc73c\ub85c \uc801\uc808\ud558\uc9c0 \uc54a\uc744 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\n\\n\ub2f9\uc7a5 \uc5c5\ubb34\uc5d0\uc11c \uac1c\ubc1c\uacfc \uad00\ub828\ub41c \uae00\uc4f0\uae30\uac00 \ud544\uc694\ud558\uc2e0 \ubd84\uc774\ub77c\uba74 \ucd94\ucc9c\ub4dc\ub9ac\uace0 \uc2f6\uc2b5\ub2c8\ub2e4. \uc7a5\uc560 \ubcf4\uace0\uc11c, \ub9b4\ub9ac\uc2a4 \ubb38\uc11c, \uc81c\uc548\uc11c \ub4f1\uc744 \uc791\uc131\ud558\ub294\ub370 \ub3c4\uc6c0\uc774 \ub420 \uac83\uc774\ub77c \uc0dd\uac01\ud569\ub2c8\ub2e4.\\n\\n\uc800\ub294 \uc544\uc9c1 \ud604\uc5c5\uc744 \uacbd\ud5d8\ud558\uc9c0 \ubabb\ud574 \uac1c\ubc1c\uc790\uac00 \ubb38\uc11c\ub97c \uc5bc\ub9c8\ub098 \ub9ce\uc774 \uc791\uc131\ud558\ub294 \uc9c0\ub294 \uc798 \ubaa8\ub985\ub2c8\ub2e4. \uadf8\ub807\uc9c0\ub9cc \uba87\uba87 \uc2e0\uc785 \uac1c\ubc1c\uc790\ubd84\ub4e4\uc758 \ud68c\uace0\ub97c \uc77d\uc5b4\ubcf4\uba74 \uac1c\ubc1c\ud558\ub294 \uac83\ubcf4\ub2e4 \uac1c\ubc1c \ubb38\uc11c\ub97c \uc791\uc131\ud558\ub294 \uc77c\uc774 \ub354 \ub9ce\ub2e4\uace0 \ud569\ub2c8\ub2e4. \ub9cc\uc57d \uc790\uc2e0\uc774 \uc785\uc0ac\ud55c \ud68c\uc0ac\uac00 \ubb38\uc11c\ud654\uc5d0 \uad00\ud55c \ubb38\ud654\uac00 \uc798 \uc774\ub8e8\uc5b4\uc838 \uc788\ub2e4\uace0 \uc0dd\uac01\ub41c\ub2e4\uba74, \uc5b4\ub5bb\uac8c \ubb38\uc11c\ub97c \uc791\uc131\ud574\uc57c \ud558\ub294 \uc9c0\ub3c4 \ub300\ub7b5\uc801\uc73c\ub85c\ub294 \ubb38\uc11c\ud654\uac00 \uc774\ub8e8\uc5b4\uc84c\uc744\ud14c\ub2c8 \uadf8 \ub54c\ub294 \uc774 \ucc45\uc774 \uaf2d \ud544\uc694\ud558\uc9c0\ub294 \uc54a\uaca0\uc8e0. \uadf8\ub7ec\ub098 \ub9cc\uc57d \uadf8\ub807\uc9c0 \uc54a\ub2e4\uba74 \uc774 \ucc45\uc744 \ucc38\uace0\ud558\uc2dc\ub294 \uac83\ub3c4 \ub098\uc058\uc9c0 \uc54a\uc73c\ub9ac\ub77c \uc0dd\uac01\ud569\ub2c8\ub2e4."},{"id":"/k8s-and-docker-for-container-infrastructure","metadata":{"permalink":"/en/blog/k8s-and-docker-for-container-infrastructure","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2021-10-30-k8s-and-docker-for-container-infrastructure/k8s-and-docker-for-container-infrastructure.md","source":"@site/blog/2021-10-30-k8s-and-docker-for-container-infrastructure/k8s-and-docker-for-container-infrastructure.md","title":"\ud83d\udcd6 \ucee8\ud14c\uc774\ub108 \uc778\ud504\ub77c \ud658\uacbd \uad6c\ucd95\uc744 \uc704\ud55c \ucfe0\ubc84\ub124\ud2f0\uc2a4/\ub3c4\ucee4","description":"\ucee8\ud14c\uc774\ub108 \uc778\ud504\ub77c \ud658\uacbd\uc744 \uc774\ud574\ud558\uace0 \uc9c1\uc811 \uad6c\ucd95\ud574 \ubcf4\uc790!","date":"2021-10-30T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \ucee8\ud14c\uc774\ub108 \uc778\ud504\ub77c \ud658\uacbd \uad6c\ucd95\uc744 \uc704\ud55c \ucfe0\ubc84\ub124\ud2f0\uc2a4/\ub3c4\ucee4","date":"2021-10-30","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/k8s-and-docker-for-container-infrastructure","description":"\ucee8\ud14c\uc774\ub108 \uc778\ud504\ub77c \ud658\uacbd\uc744 \uc774\ud574\ud558\uace0 \uc9c1\uc811 \uad6c\ucd95\ud574 \ubcf4\uc790!","keywords":["Docker","Kubernetes","k8s"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \uac1c\ubc1c\uc790\uc758 \uae00\uc4f0\uae30","permalink":"/en/blog/developer-writing"},"nextItem":{"title":"\ud83d\udcd6 \uc81c\ub300\ub85c \ubc30\uc6b0\ub294 \uc218\ud559\uc801 \ucd5c\uc801\ud654","permalink":"/en/blog/mathematical-optimization"}},"content":":::info\\n\uae38\ubc97 \u201816\ucc28 \uac1c\ubc1c\uc790 \ub9ac\ubdf0\uc5b4\u2019 \ud65c\ub3d9\uc744 \uc704\ud574\uc11c \ucc45\uc744 \uc81c\uacf5\ubc1b\uc544 \uc791\uc131\ub41c \uc11c\ud3c9\uc785\ub2c8\ub2e4.\\n:::\\n\\n## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/k8s-and-docker-for-container-infrastructure.jpg)](http://www.kyobobook.co.kr/product/detailViewKor.laf?ejkGb=KOR&mallGb=KOR&barcode=9791165215743&orderClick=LEa&Kc=)\\n\\n- \uc81c\ubaa9: \ucee8\ud14c\uc774\ub108 \uc778\ud504\ub77c \ud658\uacbd \uad6c\ucd95\uc744 \uc704\ud55c \ucfe0\ubc84\ub124\ud2f0\uc2a4/\ub3c4\ucee4\\n- \uc800\uc790: \uc870\ud6c8, \uc2ec\uadfc\uc6b0, \ubb38\uc131\uc8fc\\n- \ucd9c\ud310\uc0ac: \uae38\ubc97\\n- \ucd9c\uac04: 2021-06-07\\n\\n{/* truncate */}\\n\\n## Book Review\\n\\n### \uc774\ubbf8\uc9c0\ub97c \ud1b5\ud55c \uc790\uc138\ud55c \uc124\uba85\\n\\n\uc644\uc804\ud55c \uceec\ub7ec\ub294 \uc544\ub2c8\uc9c0\ub9cc, \uc774\ubbf8\uc9c0\ub97c \ud65c\uc6a9\ud55c \uc790\uc138\ud55c \uc124\uba85\uc744 \ubcf4\uc5ec\uc90d\ub2c8\ub2e4. \uc77c\ubc18\uc801\uc778 \ucc45\uc774\ub77c\uba74 \uc5b4\ucc28\ud53c \uac1c\ubc1c\uc790\uac00 \uc77d\uc744 \ucc45\uc774\ub77c\uace0 \uac00\uc815\ud558\uace0 \uc124\uce58 \uacfc\uc815 \ub530\uc704\ub294 \ub118\uc5b4\uac08 \ud150\ub370 \uadf8\ub7f0 \uacfc\uc815\ub4e4\uc870\ucc28 \uc774\ubbf8\uc9c0\ub85c \ucc45\uc5d0 \uc124\uba85\ud574\uc8fc\uc154\uc11c \ub108\ubb34 \ub3c4\uc6c0\uc774 \ub410\uc2b5\ub2c8\ub2e4. \ud504\ub85c\uadf8\ub7a8 \uc124\uce58 \uacfc\uc815 \uc774\uc678\uc5d0\ub3c4 \uc815\uc0c1\uc801\uc73c\ub85c \uc791\ub3d9\ud558\uc9c0 \uc54a\uc740 \uacbd\uc6b0\uc5d0 \ub300\ud574\uc11c\ub3c4 \uae00 \ub610\ub294 \uc774\ubbf8\uc9c0\ub85c \uc124\uba85\ud574\uc8fc\uc154\uc11c \ub108\ubb34 \ud3b8\ud558\uac8c \uc77d\uc744 \uc218 \uc788\uc5c8\uc2b5\ub2c8\ub2e4.\\n\\n*\uae38\ubc97* \ucd9c\ud310\uc0ac\uc758 \ucc45\uc740 \ud55c \uc7a5\uc744 \ud3bc\uce58\uba74 \uc800\uc790 \ubc0f \uc5ed\uc790\uc758 \uc18c\uac1c\uac00 \uc801\ud600 \uc788\uace0 *\ub3c5\uc790\uc758 1\ucd08\ub97c \uc544\uaef4\uc8fc\ub294 \uc815\uc131\uc744 \ub9cc\ub098\ubcf4\uc138\uc694!*\ub77c\ub294 \ubb38\uad6c\uac00 \uc801\ud600\uc788\uc2b5\ub2c8\ub2e4. \uc194\uc9c1\ud788 \uc774 \ubb38\uad6c\ub294 \ud06c\uac8c \uc640 \ub2ff\uc9c0 \uc54a\uc558\ub294\ub370 \uadf8\ub2e4\uc74c  *\uc138\uc0c1\uc774 \uc544\ubb34\ub9ac \ubc14\uc058\uac8c \ub3cc\uc544\uac00\ub354\ub77c\ub3c4 \ucc45\uae4c\uc9c0 \uc544\ubb34\ub807\uac8c\ub098 \ube68\ub9ac \ub9cc\ub4e4 \uc218\ub294 \uc5c6\uc2b5\ub2c8\ub2e4.*\ub77c\uace0 \uc801\ud600\uc838 \uc788\ub294 \ubb38\uad6c\uac00 \uc804 \ud06c\uac8c \uc640 \ub2ff\uc558\uc2b5\ub2c8\ub2e4. \ucc45\uc5d0\uc11c \uc800\uc790\ubd84\uaed8\uc11c \uc124\uba85\ud558\uc168\ub4ef\uc774 IT\ub9cc\ud07c \ube60\ub974\uac8c \ubcc0\ud654\ud558\ub294 \ubd84\uc57c\uac00 \uc5c6\uc2b5\ub2c8\ub2e4. \uadf8\ub807\uae30\uc5d0 \ucc45 \ub610\ud55c \uae30\uc220\uc774 \ube60\ub974\uac8c \ubcc0\ud654\ud558\ub354\ub77c\ub3c4 \ucc45\uae4c\uc9c0 \uc544\ubb34\ub807\uac8c\ub098 \ube60\ub974\uac8c \ub9cc\ub4e4\uc5b4\uc57c \ud55c\ub2e4\uace0 \uc0dd\uac01\ud558\uc9c0 \uc54a\uc2b5\ub2c8\ub2e4.\\n\\n### \uc2e4\uc2b5\ud558\uae30 \uc704\ud55c \ub2e4\uc591\ud55c \ub3c4\uad6c\ub4e4\\n\\n\uc774 \ucc45\uc5d0\uc11c\ub294 Kubernetes, Docker\ubfd0\ub9cc \uc544\ub2c8\ub77c \uc2e4\uc2b5\uc744 \uc704\ud574 \ub2e4\uc591\ud55c \ub3c4\uad6c\ub4e4\uc744 \uc0ac\uc6a9\ud569\ub2c8\ub2e4. \uac00\uc0c1 \ucef4\ud4e8\ud130\ub97c \ub9cc\ub4dc\ub294 Oracle VM VirtualBox, \uc0ac\uc6a9\uc790\uc758 \uc694\uad6c\uc5d0 \ub9de\uac8c \uc2dc\uc2a4\ud15c \uc790\uc6d0\uc744 \ud560\ub2f9, \ubc30\uce58, \ubc30\ud3ec\ud574 \ub450\uc5c8\ub2e4\uac00 \ud544\uc694\ud560 \ub54c \uc2dc\uc2a4\ud15c\uc744 \uc0ac\uc6a9\ud560 \uc218 \uc788\ub294 \uc0c1\ud0dc\ub85c \ub9cc\ub4dc\ub294 \ud504\ub85c\ube44\uc800\ub2dd \ub3c4\uad6c\uc778 *Vagrant*\ub97c \uc0ac\uc6a9\ud569\ub2c8\ub2e4. \uc774 \ubc16\uc5d0\ub3c4 CI/CD\ub97c \uc704\ud55c Jenkins, \ucee8\ud14c\uc774\ub108 \uc778\ud504\ub77c \ud658\uacbd\uc5d0\uc11c \ubaa8\ub2c8\ud130\ub9c1 \ub370\uc774\ud130\ub97c *Prometheus*\ub85c \uc218\uc9d1\ud558\uace0 \uc218\uc9d1\ud55c \uc815\ubcf4\ub97c \ud1b5\ud569\ud558\uc5ec *Grafana*\ub85c \uc2dc\uac01\ud654\ud569\ub2c8\ub2e4. \ucd5c\uadfc DevOps \ucabd\uc73c\ub85c \uacf5\ubd80\ub97c \ud574\ubcf4\uace0 \uc2f6\ub2e4\ub294 \uc0dd\uac01\uc744 \uac16\uace0 \uc788\uc5c8\ub294\ub370 \uc774\ubc88 \uae30\ud68c\uc5d0 \uc774 \ucc45\uc73c\ub85c DevOps\uc758 \uc804\uccb4\uc801\uc778 \uacfc\uc815\uc744 \uac04\uc811\uc801\uc73c\ub85c \uccb4\ud5d8\ud574\ubcfc \uc218 \uc788\uc5b4\uc11c \ub3c4\uc6c0\uc774 \ub410\uc2b5\ub2c8\ub2e4.\\n\\n### \ubc84\uc804\\n\\n\ucc45\uc5d0\uc11c \uc9c0\uc815\ud55c \ubc84\uc804\uc744 \uc0ac\uc6a9\ud558\ub294 \uac83\uc744 \ucd94\ucc9c\ud574 \ub4dc\ub9bd\ub2c8\ub2e4. \uac00\uc7a5 \ucd5c\uadfc \ubc84\uc804\uc744 \uc0ac\uc6a9\ud558\ub824\uace0 \ud558\ub2e4\uac00 \uc2dc\uac04\uc744 \ub9ce\uc774 \ubc84\ub838\uc2b5\ub2c8\ub2e4. \uc774\ub7f0 \uae30\uc220\ub4e4\uc740 \uac00\uc7a5 \ucd5c\uadfc \ubc84\uc804\uc73c\ub85c \uacf5\ubd80\ud558\ub294 \uac83\uc774 \uc88b\ub2e4\uace0 \ud56d\uc0c1 \uacf5\ubd80\ud560 \ub54c\ub9c8\ub2e4 \ub290\uaf08\ub294\ub370 \ub2e4\uc2dc \uc0dd\uac01\ud574\ubcf4\ub2c8 \uc9c0\uae08 \ub2f9\uc7a5 \ud604\uc5c5\uc5d0 \ub6f0\uc5b4\ub4e4 \uac83\ub3c4 \uc544\ub2c8\uace0, \ud604\uc5c5\uc5d0\uc11c\ub3c4 \uc774\ub807\uac8c \ucd5c\uc2e0 \ubc84\uc804\uc744 \uc4f0\ub294 \uacf3\uc740 \ub4dc\ubb3c\ub2e4\uace0 \uc0dd\uac01\ud558\uae30 \ub54c\ubb38\uc5d0 \ud06c\uac8c \uc758\ubbf8 \uc5c6\uc744 \uac83\uc73c\ub85c \uc608\uc0c1\uc774 \ub429\ub2c8\ub2e4. \uc790\uc2e0\uc774 OS\uc758 \uac70\uc758 \ubaa8\ub4e0 \ubd80\ubd84\uc744 \uc124\uc815\ud558\uace0 \uad00\ub9ac\ud560 \uc218 \uc788\uc73c\uba74 \ucc45\uc758 \uc608\uc81c\uc640 \uac19\uc774 \ub530\ub77c \ud558\uc9c0 \uc54a\uc544\ub3c4 \uad1c\ucc2e\uc9c0\ub9cc, \uadf8\ub7f0 \ubd80\ubd84\uc5d0\uc11c \ubd80\uc871\ud568\uc744 \ub290\ub080\ub2e4\uba74 \ucc45\uc758 \uc608\uc2dc\ub97c \uadf8\ub300\ub85c \ub530\ub77c \ud558\uc2dc\ub294 \uac83\uc744 \ucd94\ucc9c\ud569\ub2c8\ub2e4.\\n\\n### \uc544\uc26c\uc6e0\ub358 \uc810\\n\\n\uac00\uc0c1\uba38\uc2e0 \uc774\ubbf8\uc9c0\uc758 \uba54\ubaa8\ub9ac\ub97c \ub108\ubb34 \ud06c\uac8c \uc7a1\uace0 \ub3cc\ub824\uc11c \uadf8\ub7f0\uc9c0 \ub7a8 8\uae30\uac00 \ub178\ud2b8\ubd81\uc5d0\uc11c \ubc84\uac70\uc6e0\uc2b5\ub2c8\ub2e4. \uc774 \ucc45\uc758 \ub0b4\uc6a9\uc744 \uc2e4\uc2b5\ud574\ubcf4\uae30 \uc704\ud55c \ucef4\ud4e8\ud130\uc758 \ucd5c\uc800 \uc0ac\uc591\uc774 \ud45c\uae30\uac00 \ub418\uc5c8\ub2e4\uba74 \ub3c5\uc790\uc5d0\uac8c \ub354 \ub3c4\uc6c0\uc774 \ub410\uc744 \uac83 \uac19\uc2b5\ub2c8\ub2e4.\\n\\n\uc800\uc790\ubd84\uaed8\uc11c \uc0ac\uc804\uc5d0 \ud544\uc694\uc5d0 \ub9de\uac8c \uc6b4\uc601\uccb4\uc81c \uc774\ubbf8\uc9c0\ub97c \uc218\uc815\ud574\ub193\uc73c\uc154\uc11c \uc800\ud76c\ub294 Vagrant Cloud\uc5d0\uc11c \ub0b4\ub824\ubc1b\uae30\ub9cc \ud558\uba74 \ub429\ub2c8\ub2e4. \uae30\ubcf8\uc801\uc778 \ud658\uacbd\uc744 \uc81c\uacf5\ud574\uc8fc\ub294 \uac83\uc740 \ub108\ubb34 \ud3b8\ud588\uc9c0\ub9cc, \ud544\uc694\uc5d0 \ub9de\uac8c \uc774\ubbf8\uc9c0\ub97c \uc124\uc815\ud558\ub294 \ubd80\ubd84\uc774 \ucc45\uc5d0 \ub2f4\uae30\uc9c0 \uc54a\uc544\uc11c \uc870\uae08 \uc544\uc26c\uc6e0\uc2b5\ub2c8\ub2e4. \ubb3c\ub860 Vagrant Cloud\uc5d0 \uc218\uc815\ud55c \ubc84\uc804\ub9c8\ub2e4 \uc5b4\ub5a4 \ubd80\ubd84\uc744 \uc218\uc815\ud588\ub294\uc9c0 \uac04\ub2e8\ud55c \uc124\uba85\uc774 \ub098\uc640 \uc788\uc9c0\ub9cc, *\uc774\ub7f0 \ubd80\ubd84\ub3c4 \ucc45\uc5d0 \ub2f4\uacbc\uc73c\uba74 \uc5b4\ub560\uc744\uae4c*\ub77c\ub294 \uc0dd\uac01\uc774 \ub4e4\uc5c8\uc2b5\ub2c8\ub2e4. \uc544\ubb34\ub798\ub3c4 \uc774 \ucc45\uc758 \uc911\uc2ec\uc740 \ucfe0\ubc84\ub124\ud2f0\uc2a4\uc640 \ub3c4\ucee4\uc774\ub2e4 \ubcf4\ub2c8 \ub9ac\ub205\uc2a4 \ud658\uacbd\uc744 \uc138\ud305\ud558\ub294 \ubd80\ubd84\uc740 \ucc45\uc5d0 \ub123\uc9c0 \uc54a\uc73c\uc2e0 \uac83 \uac19\uc2b5\ub2c8\ub2e4.\\n\\n### \ub300\uc0c1 \ub3c5\uc790\\n\\n\uac1c\ubc1c \ucd08\uc2ec\uc790\uc5d0\uac8c \uc26c\uc6b4 \ucc45\uc774\ub77c\uace0\ub294 \uc0dd\uac01\uc774 \ub4e4\uc9c0 \uc54a\uc2b5\ub2c8\ub2e4. \ub9ac\ub205\uc2a4\ub97c \uac00\ub974\uccd0\uc8fc\ub294 \ucc45\uc774 \uc544\ub2c8\uae30 \ub54c\ubb38\uc5d0 \uae30\ubcf8\uc801\uc73c\ub85c UNIX \uba85\ub839\uc5b4\uc5d0 \ub300\ud55c \uc9c0\uc2dd, \uc6b4\uc601\uccb4\uc81c\uc5d0 \uad00\ud55c \uc9c0\uc2dd, \uc258 \uc2a4\ud06c\ub9bd\ud2b8\uc5d0 \ub300\ud55c \uc9c0\uc2dd\uc744 \uc5b4\ub290 \uc815\ub3c4 \uac16\uace0 \uc788\uc5b4\uc57c \ud569\ub2c8\ub2e4. \ub610\ud55c, \ucfe0\ubc84\ub124\ud2f0\uc2a4, \ub3c4\ucee4\uc5d0 \ub300\ud55c \uae30\ucd08 \uc9c0\uc2dd\ub3c4 \ud544\uc694\ud558\ub2e4\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4. \uc774\ub807\uac8c \ub9ce\uc740 \uc0ac\uc804 \uc9c0\uc2dd\uc774 \ud544\uc694\ud55c \ucc45\uc774\uc9c0\ub9cc, \ucfe0\ubc84\ub124\ud2f0\uc2a4, \ub3c4\ucee4 \uae30\uc220\uc5d0 \uad00\uc2ec \uc788\uc73c\uc2dc\uace0 \ucee8\ud14c\uc774\ub108 \uc778\ud504\ub77c \ud658\uacbd \uad6c\ucd95\uc744 \ud574\ubcf4\uc2dc\uace0 \uc2f6\uc740 \ubd84\uc740 \uaf2d \uc77d\uc5b4\ubcf4\uba74 \uc88b\uc744 \ucc45\uc774\ub77c\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4."},{"id":"/mathematical-optimization","metadata":{"permalink":"/en/blog/mathematical-optimization","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2021-10-21-mathematical-optimization/mathematical-optimization.md","source":"@site/blog/2021-10-21-mathematical-optimization/mathematical-optimization.md","title":"\ud83d\udcd6 \uc81c\ub300\ub85c \ubc30\uc6b0\ub294 \uc218\ud559\uc801 \ucd5c\uc801\ud654","description":"\ucd5c\uc801\ud654 \ubaa8\ub378\ub9c1\ubd80\ud130 \uc54c\uace0\ub9ac\uc998\uae4c\uc9c0","date":"2021-10-21T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \uc81c\ub300\ub85c \ubc30\uc6b0\ub294 \uc218\ud559\uc801 \ucd5c\uc801\ud654","date":"2021-10-21","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/mathematical-optimization","description":"\ucd5c\uc801\ud654 \ubaa8\ub378\ub9c1\ubd80\ud130 \uc54c\uace0\ub9ac\uc998\uae4c\uc9c0","keywords":["optimization"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \ucee8\ud14c\uc774\ub108 \uc778\ud504\ub77c \ud658\uacbd \uad6c\ucd95\uc744 \uc704\ud55c \ucfe0\ubc84\ub124\ud2f0\uc2a4/\ub3c4\ucee4","permalink":"/en/blog/k8s-and-docker-for-container-infrastructure"},"nextItem":{"title":"\ud83d\udcd6 \ubc15\ubbf8\uc815\uc758 \uae43&\uae43\ud5c8\ube0c \uc785\ubb38","permalink":"/en/blog/Git-GitHub-introduction"}},"content":":::info\\n\ud55c\ube5b\ubbf8\ub514\uc5b4 \\\\<\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4\\\\> \ud65c\ub3d9\uc744 \uc704\ud574\uc11c \ucc45\uc744 \uc81c\uacf5\ubc1b\uc544 \uc791\uc131\ub41c \uc11c\ud3c9\uc785\ub2c8\ub2e4.\\n:::\\n\\n![\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4 2021](../assets/I-am-reviewer-2021.jpg)\\n\\n## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/mathematical-optimization.jpg)](http://www.kyobobook.co.kr/product/detailViewKor.laf?ejkGb=KOR&mallGb=KOR&barcode=9791162244661&orderClick=LEa&Kc=)\\n\\n- \uc81c\ubaa9: \uc81c\ub300\ub85c \ubc30\uc6b0\ub294 \uc218\ud559\uc801 \ucd5c\uc801\ud654\\n- \uc800\uc790: Shunji Umetani(\uc6b0\uba54\ud0c0\ub2c8 \uc28c\uc9c0)\\n- \uc5ed\uc790: \uae40\ubaa8\uc138\\n- \ucd9c\ud310\uc0ac: \ud55c\ube5b\ubbf8\ub514\uc5b4\\n- \ucd9c\uac04: 2021-09-30\\n\\n{/* truncate */}\\n\\n## Book Review\\n\\n### \uc218\ud559\uc801 \uc9c0\uc2dd\uc774 \ud544\uc694\ud55c \ucc45\\n\\n\uc81c\ubaa9\uc5d0\uc11c\ubd80\ud130 `\uc218\ud559`\uc774\ub77c\ub294 \ub9d0\uc774 \ub4e4\uc5b4\uac00\ub294 \uac83\uc744 \ubcf4\uba74 \uc54c \uc218 \uc788\ub4ef\uc774 \ucd5c\uc801\ud654 \uc774\ub860\uc744 \uacf5\ubd80\ud558\uae30 \uc704\ud574\uc11c\ub294 \uc120\uc218 \uc9c0\uc2dd\ub4e4\uc774 \ud544\uc694\ud569\ub2c8\ub2e4. \ucd5c\uc801\ud654 \ubb38\uc81c\ub780 \uac04\ub2e8\ud558\uac8c \uc124\uba85\ud558\uba74 \uc5b4\ub5a4 \ubaa9\uc801 \ud568\uc218\uc758 \uacb0\uacfc \uac12\uc744 \ucd5c\uc801\ud654(\ucd5c\ub300\ud654 \ub610\ub294 \ucd5c\uc18c\ud654)\ud558\ub294 \uc194\ub8e8\uc158\uc744 \ucc3e\ub294 \ubb38\uc81c\uc785\ub2c8\ub2e4. \ub2e8\uc21c\ud558\uac8c \ubcf4\uba74 \uc218\ud559 \ubb38\uc81c\ub85c \ubc16\uc5d0 \uc548 \ubcf4\uc774\uc9c0\ub9cc \uac70\uc758 \ub300\ubd80\ubd84\uc758 \uba38\uc2e0\ub7ec\ub2dd, \ub525\ub7ec\ub2dd \ubd84\uc57c\uc5d0\uc11c \uc4f0\uc785\ub2c8\ub2e4. \uc608\ub97c \ub4e4\uc5b4\uc11c Gredient discent, Convex optimization \ub4f1\uc774 \uc4f0\uc774\uc8e0. \ub2e4\uc591\ud55c \uc218\ud559\uc801 \uc9c0\uc2dd\ub4e4\uacfc \uc54c\uace0\ub9ac\uc998\uc73c\ub85c \uc774\ub8e8\uc5b4\uc838 \uc788\uae30 \ub54c\ubb38\uc5d0 \uc120\uc218 \uc9c0\uc2dd\ub4e4\uc774 \uaf64 \ud544\uc694\ud55c \ud3b8\uc774\uba70 \ub300\ud45c\uc801\uc73c\ub85c \uc120\ud615\ub300\uc218, \ub300\ud45c\uc801\uc778 \uc54c\uace0\ub9ac\uc998, \uc790\ub8cc\uad6c\uc870, \ubbf8\uc801\ubd84 \ub4f1\uc744 \uc54c\uace0 \uc788\uc5b4\uc57c\ud569\ub2c8\ub2e4.  \\n\\n\uc804 \uc774 \ucc45\uc744 \uc77d\uae30 \uc704\ud55c \uc900\ube44\uac00 \ub410\ub2e4\uace0 \uc0dd\uac01\ud588\uc5c8\ub294\ub370 \uc804\ud600 \uadf8\ub807\uc9c0 \uc54a\uc558\uc2b5\ub2c8\ub2e4. \uc120\uc218\uc9c0\uc2dd\ub4e4\uc744 \ubaa8\ub450 \uacf5\ubd80\ud574\ubd24\uc5b4\ub3c4 \uc77d\ub294\ub370 \uc5b4\ub824\uc6c0\uc774 \uc788\uc5c8\uc2b5\ub2c8\ub2e4. \ucc98\uc74c \uc774 \ucc45\uc744 \ub300\ucda9 \ud6d1\uc5b4\ubd24\uc744 \ub54c\ub294 \uc218\uce58\ud574\uc11d\uacfc \ube44\uc2b7\ud558\ub2e4\uace0 \ub290\uaef4 `\ube44\uc2b7\ud55c \ub0b4\uc6a9\uc774\ub2c8 \ud06c\uac8c \uc5b4\ub835\uc9c0 \uc54a\uaca0\uc9c0?`\ub77c\ub294 \uc0dd\uac01\uc744 \ud588\uc5c8\uc2b5\ub2c8\ub2e4. \uc644\uc804 \uc5b4\ub9ac\uc11d\uc740 \uc0dd\uac01\uc774\uc5c8\uc2b5\ub2c8\ub2e4. \\n\\n### \ub2e4\uc591\ud55c \uc608\uc2dc \ubb38\uc81c \uc801\uc6a9\\n\\n\ub2e8\uc21c\ud788 \uc218\ud559\uc801\uc778 \uc9c0\uc2dd\ub9cc \uc124\uba85\ud558\ub294 \uac83\uc774 \uc544\ub2cc \uc218\ud559\uc801 \ucd5c\uc801\ud654\uc758 \uc0ac\uace0\ubc29\uc2dd\uc744 \uc54c\ub824\uc8fc\uae30 \uc704\ud574 \uc608\uc2dc\ub97c \ub4e4\uc5b4 `\uc5b4\ub5bb\uac8c \ud558\uba74 \ucd5c\uc801\ud654 \ud560 \uc218 \uc788\uc744\uae4c?`\uc5d0 \ub300\ud55c \ub2f5\uc744 \uc54c\ub824\uc90d\ub2c8\ub2e4. \uc804 \uc774\ubd80\ubd84\uc774 \uc774 \ucc45\uc758 \uac00\uc7a5 \ud070 \uc7a5\uc810\uc774\ub77c\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4. \uc5b4\uc9dc\ud53c \uc218\ub9ce\uc740 \uc774\ub860\ub4e4\uc744 \uc54c\uc544\ubd24\uc790 \uadf8\uac78 \uc774\ud574\ud558\uc9c0\ub3c4 \ubabb\ud558\uace0, \uc801\uc6a9\ud560 \uc218\ub3c4 \uc5c6\ub2e4\uba74 \ubb34\uc758\ubbf8\ud574\uc9d1\ub2c8\ub2e4. \uc774 \ucc45\uc5d0\uc11c\ub294 \uad6c\uccb4\uc801\uc73c\ub85c \uc608\uc2dc\ub97c \ub4e4\uba74\uc11c \uc124\uba85\ud574\uc8fc\ub2c8 \uc774\ud574\ud558\uae30 \uc26c\uc6e0\uc2b5\ub2c8\ub2e4.\\n\\n### \uc5f0\uc2b5\ubb38\uc81c\uc640 \ucc38\uace0\ubb38\ud5cc\\n\\n\uac1c\ub150\ub9cc \uacf5\ubd80\ud558\uba74 \uae30\uc5b5\uc5d0 \uc624\ub798 \ub0a8\uc9c0 \uc54a\ub294\ub2e4\uace0 \uc0dd\uac01\ud574\uc11c \uc218\ud559 \ucc45\uc5d0 \uc5f0\uc2b5 \ubb38\uc81c\uac00 \uc788\ub2e4\ub294 \uac83\uc774 \uc88b\ub2e4\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4. \uacf5\ubd80\ud558\ub294 \ud559\uc0dd \uc785\uc7a5\uc5d0\uc11c\ub3c4 \ub3c4\uc6c0\uc774 \uaf64 \ub418\uba70 \uc774 \ucc45\uc740 \ub2e4\ub978 \ucc45\ub4e4\uacfc \ub2e4\ub974\uac8c \uc5f0\uc2b5\ubb38\uc81c\uc5d0 \ub300\ud55c \ud574\uc124\ub3c4 \ubd80\ub85d\uc73c\ub85c \uc81c\uacf5\ud574\uc90d\ub2c8\ub2e4. \ubb38\uc81c\ub97c \uba3c\uc800 \ud480\uc5b4\ubcf4\uace0 \ud574\uc124\uacfc \ube44\uad50\ud574\ubcfc \uc218 \uc788\uaca0\uc8e0.\\n\\n\ucc38\uace0\ubb38\ud5cc\ub3c4 \ub9e4 \ub2e8\uc6d0\uc774 \ub05d\ub0a0 \ub54c\ub9c8\ub2e4 \uc5f4\uc2ec\ud788 \uacf5\ubd80\ud558\ub77c\uace0 \ub123\uc5b4\uc8fc\uc2ed\ub2c8\ub2e4. \uc774 \ucc45\uc758 \ubc94\uc704\uac00 \ub9e4\uc6b0 \ubc29\ub300\ud558\ub2e4\ub294 \uac83\uc744 \uc608\uc0c1\ud560 \uc218 \uc788\uc8e0. \ub9e4\ub2e8\uc6d0\ub9c8\ub2e4 \ub9ce\uc740 \uc591\uc758 \ucc45\ub4e4\uc744 \ucc38\uace0\ud558\ub77c\uace0 \ucd94\ucc9c\ud574\uc8fc\uc2dc\ub294\ub370 \ucc38\uace0\ubb38\ud5cc\uc774 \ub300\ubd80\ubd84 \uc77c\ubcf8\uc5b4 \ucc45\uc774\ub77c\uc11c \uc870\uae08 \ubd80\ub2f4\ub429\ub2c8\ub2e4. \uadf8\ub798\ub3c4 \uc601\uc5b4\ub85c \uc4f0\uc5ec\uc9c4 \uba87\uba87 \ucc45\ub4e4\uc744 \uc54c\ub824\uc8fc\uc154\uc11c \ub098\uc911\uc5d0 \ud544\uc694\ud55c \ub0b4\uc6a9\ub4e4\uc774 \uc788\ub2e4\uba74 \ucc38\uace0\ud558\uac70\ub098 \uc77d\uc5b4\ubcfc \uc608\uc815\uc785\ub2c8\ub2e4.\\n\\n## \ub300\uc0c1 \ub3c5\uc790\\n\\n\uc804 \uc77c\ub2e8 \uccab\ubc88\uc9f8\ub85c `\ucd5c\uc801\ud654 \uc774\ub860\uc5d0 \uad00\uc2ec \uc788\ub294 \uc0ac\ub78c`\uc774 \uc77d\uc73c\uc168\uc73c\uba74 \uc88b\uaca0\uc2b5\ub2c8\ub2e4. \uad00\uc2ec\ub3c4 \uc5c6\ub294\ub370 \uba38\uc2e0\ub7ec\ub2dd, \ub525\ub7ec\ub2dd\uc5d0 \uad00\ub828\ub418\uc5b4 \uc788\ub2e4\uace0 \ud574\uc11c \uacf5\ubd80\ud558\uc2dc\ub294 \uac74 \ucd94\ucc9c\ub4dc\ub9ac\uc9c0 \uc54a\uc2b5\ub2c8\ub2e4. \ucef4\ud4e8\ud130 \uacf5\ud559\uacfc \uae30\uc900\uc73c\ub85c \ud559\ubd80\uc0dd\uc774 \uc77d\ub294 \uac83\ub3c4 \ubcc4\ub85c \ucd94\ucc9c\ub4dc\ub9ac\uace0 \uc2f6\uc9c0\ub294 \uc54a\uc2b5\ub2c8\ub2e4. \uc77d\uc5b4\ub3c4 4\ud559\ub144 \ub54c \uba38\uc2e0\ub7ec\ub2dd \uc218\uc5c5\uc744 \ub4e4\uc73c\uba74\uc11c \uc77d\uc5b4\ubcf4\uc2dc\uace0 \ub2e4\ub978 \ucc45\uc73c\ub85c \uba3c\uc800 \ucd5c\uc801\ud654 \uc774\ub860\uc5d0 \ub300\ud574 \uacf5\ubd80\ud574\ubcf4\ub294 \uac83\uc744 \ucd94\ucc9c\ub4dc\ub9bd\ub2c8\ub2e4. \\n\\n\ucd5c\uc801\ud654 \uc9c0\uc2dd\uc5d0 \ub300\ud574 \uc804\ubc18\uc801\uc73c\ub85c \uc54c\uace0 \uc2f6\uc73c\uc2e0 \ubd84\uc774 \uc77d\uc73c\uc168\uc73c\uba74 \uc88b\uaca0\uc2b5\ub2c8\ub2e4. \uc774 \ucc45\uc740 \uc218\ud559\uc801 \ucd5c\uc801\ud654\uc5d0 \ub300\ud574 \uae30\ubcf8 \uc9c0\uc2dd\uc744 \ubc30\uc6b0\uae30 \uc704\ud55c \ucc45\uc785\ub2c8\ub2e4. \uacb0\uad6d \uc785\ubb38\uc11c\ub77c\ub294 \uac70\uc8e0. \ub354 \uc790\uc138\ud788 \uacf5\ubd80\ud574\ubcf4\uace0 \uc2f6\uc73c\uc2dc\ub2e4\uba74 \uc774 \ucc45\uc5d0 \ub098\uc640\uc788\ub294 \ucc38\uace0\ubb38\ud5cc\uc758 \ucc45\ub4e4\uacfc \uac19\uc774 \uc77d\uc5b4\ubcf4\uc2dc\uae38 \ubc14\ub78d\ub2c8\ub2e4."},{"id":"/Git-GitHub-introduction","metadata":{"permalink":"/en/blog/Git-GitHub-introduction","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2021-10-08-Git-GitHub-introduction/Git-GitHub-introduction.md","source":"@site/blog/2021-10-08-Git-GitHub-introduction/Git-GitHub-introduction.md","title":"\ud83d\udcd6 \ubc15\ubbf8\uc815\uc758 \uae43&\uae43\ud5c8\ube0c \uc785\ubb38","description":"Must Have | \ud504\ub85c\uc81d\ud2b8 \ud611\uc5c5 \uc2dc\ub098\ub9ac\uc624\ub85c \uc775\ud788\ub294 Git&GitHub \ubc18\ubcf5 \ud559\uc2b5\uc11c","date":"2021-10-08T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \ubc15\ubbf8\uc815\uc758 \uae43&\uae43\ud5c8\ube0c \uc785\ubb38","date":"2021-10-08","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/Git-GitHub-introduction","description":"Must Have | \ud504\ub85c\uc81d\ud2b8 \ud611\uc5c5 \uc2dc\ub098\ub9ac\uc624\ub85c \uc775\ud788\ub294 Git&GitHub \ubc18\ubcf5 \ud559\uc2b5\uc11c","keywords":["Git","GitHub","\ud611\uc5c5","Must Have"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \uc81c\ub300\ub85c \ubc30\uc6b0\ub294 \uc218\ud559\uc801 \ucd5c\uc801\ud654","permalink":"/en/blog/mathematical-optimization"},"nextItem":{"title":"\ud83d\udcd6 \ud30c\uc774\uc36c \uba38\uc2e0\ub7ec\ub2dd \ud310\ub2e4\uc2a4 \ub370\uc774\ud130 \ubd84\uc11d","permalink":"/en/blog/pandas-data-analysis"}},"content":":::info\\n\uace8\ub4e0\ub798\ube57 \ucd9c\ud310\uc0ac\ub85c\ubd80\ud130 \ucc45\uc744 \uc81c\uacf5\ubc1b\uc544 \uc791\uc131\ud588\uc2b5\ub2c8\ub2e4.\\n:::\\n\\n## \u2139\ufe0fBook Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/Git-GitHub-introduction.jpg)](http://www.kyobobook.co.kr/product/detailViewKor.laf?ejkGb=KOR&mallGb=KOR&barcode=9791191905014&orderClick=LEa&Kc=)\\n\\n- \uc81c\ubaa9: \ubc15\ubbf8\uc815\uc758 \uae43&\uae43\ud5c8\ube0c \uc785\ubb38\\n- \uc800\uc790: \ubc15\ubbf8\uc815\\n- \ucd9c\ud310\uc0ac: \uace8\ub4e0\ub798\ube57\\n- \ucd9c\uac04: 2021-09-13\\n\\n{/* truncate */}\\n\\n## \ud83c\udfacIntro\\n\\n\uc774 \ucc45\uc758 \uc800\uc790\ub294 \ud398\uc774\uc2a4\ubd81 [\uc5bc\ub610(Early \ub610\ub77c\uc774)](https://www.facebook.com/groups/earlyddorai/about) \uadf8\ub8f9\uc744 \uc6b4\uc601\ud558\uc2dc\ub294 \uc6b4\uc601\uc9c4 \uc911 \ud55c \ubd84\uc774\uba70, \ubc30\ubbfc \ubca0\ud2b8\ub0a8\uc744 \uac70\uccd0 \ud604\uc7ac \ubb34\uc2e0\uc0ac\uc5d0 \uacc4\uc2e0 \ubc15\ubbf8\uc815 \ub2d8\uc774\ub2e4. \uc815\ub9d0 \ubc30\uc6b8 \uc810\uc774 \ub9ce\ub2e4\uace0 \uc0dd\uac01\ud558\ub294 \uac1c\ubc1c\uc790 \ubd84 \uc911 \ud55c \ubd84\uc774\ub2e4. \uae30\ud68c\uac00 \ub41c\ub2e4\uba74 \ud0c8\uc789\uc5d0\uc11c **\uc131\uc7a5\ud558\ub294 \uac1c\ubc1c\uc790\uc758 \'\uacf5\uc720\uc758 \uae30\uc220\'**\uc774\ub77c\ub294 \uc8fc\uc81c\ub85c \ubc1c\ud45c\ud558\uc2e0 [\uc6d4\uac04 \ucf54\ub4dc\ub9ac\ubdf0 ver_0.1: \ucee4\ub9ac\uc5b4 \uc131\uc7a5 CODE](https://taling.me/vod/view/39222)\ub97c \ubcf4\ub294 \uac83\ub3c4 \uac1c\ubc1c\uc790\ub85c\uc11c \uc131\uc7a5\ud558\ub294\ub370 \ub3c4\uc6c0\uc774 \ub420 \uac83\uc774\ub2e4.\\n\\n\ucc45\uc774 \ucd9c\uac04\ub418\uae30 \uc804 \ubca0\ud0c0\ub9ac\ub354\ub97c \ubaa8\uc9d1\ud588\uc744 \ub54c, \uc800\uc790\ub97c \ud5a5\ud55c \uac1c\uc778\uc801\uc778 \ud32c\uc2ec\uc73c\ub85c \uc2e0\uccad\ud588\uc73c\ub098 \ub5a8\uc5b4\uc84c\uc5c8\ub2e4. \uadf8\ub7ec\ub098 \ucd9c\uac04\ub41c \uc774\ud6c4 \uace8\ub4e0\ub798\ube57 \ucd9c\ud310\uc0ac\uc5d0\uc11c **\ubd81\uc2a4\ud3ec\uc77c\ub7ec**\ub97c \ubaa8\uc9d1\ud588\uace0, \uc9c0\uc6d0\ud558\uc5ec \uc774 \ucc45\uc744 \uc77d\uc744 \uc218 \uc788\uac8c \ub410\ub2e4. \uc774\ubc88\uc5d0\ub294 \uac1c\uc778\uc801\uc778 \ud32c\uc2ec \ub54c\ubb38\uc774 \uc544\ub2c8\ub77c git\uc744 \ud65c\uc6a9\ud558\uc5ec \ud611\uc5c5\ud560 \uc904 \uc544\ub294 \uc0ac\ub78c\uc774 \ub418\uace0 \uc2f6\ub2e4\ub294 \ub9c8\uc74c\uc774 \ub354 \ucef8\ub2e4. \ub300\ud559\uc5d0\uc11c \uc624\ud508\uc18c\uc2a4 \uad00\ub828 \uc218\uc5c5\uc744 \ub4e4\uc5b4 git\uc758 \uae30\ub2a5\uc744 \uc5b4\ub290\uc815\ub3c4 \ud65c\uc6a9\ud560 \uc218 \uc788\uc5c8\uc9c0\ub9cc, \ub2e8\uc21c\ud788 commit, push\uc5d0 \uc775\uc219\ud55c \uc0ac\ub78c\uc774 \uc544\ub2c8\ub77c branch \ubcc0\uacbd, merge, pull request \ub4f1\uc758 \uae30\ub2a5\uc744 \uc4f8 \uc904 \uc544\ub294 \ud611\uc5c5\uc5d0 \uc775\uc219\ud55c \uc0ac\ub78c\uc774 \ub418\uace0 \uc2f6\uc5c8\ub2e4.\\n\\n\uace8\ub4e0\ub798\ube57 \ucd9c\ud310\uc0ac\uc5d0\uc11c \ubaa8\uc9d1\ud558\ub294 \ubd81\uc2a4\ud3ec\uc77c\ub7ec\ub294 \uc5b4\ub5a4 \ucc45\uc774 \uc88b\uc740 \ucc45\uc778\uc9c0 \uc9c4\uc194\ud558\uac8c \uc54c\ub824\uc918 \ub3c5\uc790\uac00 \uc88b\uc740 \ucc45\uc744 \uc120\ud0dd\ud560 \uc218 \uc788\uac8c \ub3d5\ub294 \uc2a4\ud3ec\uc77c\ub7ec\ub97c \uc758\ubbf8\ud55c\ub2e4. \uc8fc\ub85c \uace8\ub4e0\ub798\ube57 \ucd9c\ud310\uc0ac\uc758 \ud398\uc774\uc2a4\ubd81 \ud398\uc774\uc9c0 \ub610\ub294 \uc774\uba54\uc77c\uc744 \ud1b5\ud574 \ubaa8\uc9d1\ud558\ubbc0\ub85c \uc6d0\ud558\ub294 \uc8fc\uc81c\uc758 \ucc45\uc774\ub77c\uba74 \uc9c0\uc6d0\ud574\ubcf4\ub294 \uac83\uc744 \ucd94\ucc9c\ud55c\ub2e4. \\n\\n## \ud83d\udcd6Book Review\\n\\n### \uc2dc\ub098\ub9ac\uc624\\n\\n\uc774 \ucc45\uc740 **\uc2dc\ub098\ub9ac\uc624**\ub77c\ub294 \ub2e4\ub978 \ucc45\uacfc \uad6c\ubcc4\ub418\ub294 \uc7ac\ubc0c\ub294 \ubd80\ubd84\uc774 \uc788\ub2e4. \ub9d0 \uadf8\ub300\ub85c \uac01\ubcf8\uc774\ub2e4. \uc774\uc804\uc5d0 \ubc30\uc6b4 \ub0b4\uc6a9\uc744 \uae30\ubc18\uc73c\ub85c \ud604\uc5c5\uc5d0\uc11c \uc9c4\ud589\ub418\ub294 \ud504\ub85c\uc81d\ud2b8 \ud504\ub85c\uc138\uc2a4\ub97c \uadf8\ub300\ub85c \uacbd\ud5d8\ud574\ubcfc \uc218 \uc788\ub294 \ubd80\ubd84\uc774\ub2e4. \ub450 \uac1c\uc758 \uc2dc\ub098\ub9ac\uc624\uac00 \uc874\uc7ac\ud558\uba70, \ub2e8\uc21c\ud55c \uc2e4\uc2b5\uc774 \uc544\ub2c8\ub77c \uc9c4\uc9dc \ud604\uc5c5\uc5d0\uc11c \uc77c\uc5b4\ub0a0 \ubc95\ud55c \uc77c\ub4e4\uc774\uc5c8\ub2e4. \ud2b9\ud788 Node.js\uc640 Express\ub97c \uc0ac\uc6a9\ud558\ub294 \ubd80\ubd84\uc774 \uc778\uc0c1 \uae4a\uc5c8\ub2e4. \ub2e8\uc21c\ud788 \ub9c8\ud06c\ub2e4\uc6b4 \ud30c\uc77c\uc744 \uc218\uc815\ud558\ub294 \uc2e4\uc2b5\uc774 \uc544\ub2c8\ub77c\uc11c \\"\uc2dc\ub098\ub9ac\uc624\uc5d0 \uc9c4\uc2ec\uc774\uad6c\ub098.\\"\ub77c\uace0 \uc0dd\uac01\ud588\ub2e4. \ub2e8\uc21c\ud788 \uc11c\ubc84\ub97c \ucf1c\uace0 localhost\uc5d0\uc11c \uc218\uc815\uc0ac\ud56d\uc744 \ud655\uc778\ud558\ub294 \uc815\ub3c4\ub77c Node.js\uc640 Express\uc5d0 \uacbd\ud5d8\uc774 \uc5c6\uc5b4\ub3c4 \ub530\ub77c\ud560 \uc218 \uc788\ub294 \uc608\uc81c\ub85c \uad6c\uc131\ub418\uc5b4 \uc788\ub2e4.\\n\\n> \uae43\ud5c8\ube0c\uc758 \uc774\uc288\ub098 \ud504\ub85c\uc81d\ud2b8 \ubcf4\ub4dc\ub97c \uc0ac\uc6a9\ud574\uc11c \uc2e4\uc81c \uc694\uad6c\uc0ac\ud56d\uc744 \ucf54\ub4dc\uc5d0 \ubc18\uc601\ud558\ub294 \uacfc\uc815\uc744 \ubcf4\uc5ec\uc918\uc57c \ud558\ub294\ub370 .md \ud30c\uc77c \uc218\uc815\ub9cc\uc73c\ub85c\ub294 \ud55c\uacc4\uac00 \uc788\uc2b5\ub2c8\ub2e4.\\n\\n### \ubc30\ud3ec \ubc0f \uc790\ub3d9\ud654 \\n\\n\uc704\uc5d0\uc11c\ub3c4 \uc5b8\uae09\ud588\ub4ef\uc774 \ub2e4\ub978 \ucc45 \ub610\ub294 \uac15\uc758\uc5d0\uc11c\ub294 Git, GitHub \uc2e4\uc2b5\uc744 \uac04\ub2e8\ud55c \ud30c\uc77c \uc218\uc815\uc73c\ub85c \uc9c4\ud589\ud588\uc744 \ud150\ub370 \uc774 \ucc45\uc5d0\uc11c\ub294 Node.js \uae30\ubc18\uc73c\ub85c \uc9c4\ud589\ub41c\ub2e4. \uadf8 \uc774\uc720\ub294 \uc544\ub9c8 \'GitHub Actions\ub97c \uc774\uc6a9\ud574\ubcf4\uae30 \uc704\ud574\uc11c\'\ub77c\ub294 \uc774\uc720\ub3c4 \uc788\uc5c8\uc9c0 \uc54a\uc558\uc744\uae4c \ucd94\uce21\ud574\ubcf8\ub2e4. \ub300\ubd80\ubd84\uc758 \ucc45\uc5d0\uc11c GitHub Actions\uae4c\uc9c0 \uc124\uba85\ud558\ub294 \uacbd\uc6b0\ub294 \ub4dc\ubb3c\ub2e4\uace0 \uc0dd\uac01\ud558\uae30\uc5d0 \uc0c1\ub2f9\ud788 \uc778\uc0c1\uc801\uc774\uc5c8\ub2e4.\\n\\nGitHub Actions\uc758 \uac04\ub2e8\ud55c \uc0ac\uc6a9\ubc95\uc744 \ubc30\uc6cc\ubcf4\uace0 AWS ECS\uc640 Azure Web App Service\uc5d0 \ubc30\ud3ec \uc790\ub3d9\ud654\ub97c \uc124\uc815\ud574\ubcf8\ub2e4. \uc774 \ubd80\ubd84\uc740 \uc790\uc138\ud558\uac8c \uc124\uba85\ud558\uc9c0\ub294 \uc54a\uace0, \'\uc774\ub7f0 \ubc29\ubc95\ub3c4 \uc788\ub2e4.\'\ub77c\ub294 \ub290\ub08c\uc73c\ub85c \uc18c\uac1c\ud55c\ub2e4. AWS, Azure\uae4c\uc9c0 \uc790\uc138\ud788 \uc124\uba85\ud558\ub294 \uac83\uc740 \ucc45\uc758 \ucde8\uc9c0\uc640 \ub9de\uc9c0 \uc54a\uae30\uc5d0 \uac04\ub2e8\ud558\uac8c \uc18c\uac1c\ub418\uc5b4 \uc788\ub294 \uac83 \uac19\ub2e4. \\n\\n### VSCode \uae30\ub2a5 \uc0ac\uc6a9\ud574\ubcf4\uae30\\n\\n\uc608\uc804\uc5d0 \ub098\ub294 CLI(Command-Line Interface) \ud658\uacbd\uc5d0\uc11c Git \uba85\ub839\uc5b4\ub97c \uc9c1\uc811 \uc785\ub825\ud558\uace0 \uc2e4\ud589\ud558\ub294 \uac83\uc774 \ub108\ubb34 \ubc88\uac70\ub86d\ub2e4\uace0 \uc5ec\uacbc\ub2e4. \ucc98\uc74c \uacf5\ubd80\ud588\uc744 \ub54c\ub294 \\"\uc774\uac78 \uad73\uc774 command\ub97c \ub2e4 \uc678\uc6b0\uace0 \uc788\uc5b4\uc57c \ud560\uae4c?\\"\ub77c\ub294 \uc0dd\uac01\uc744 \ud558\uc5ec Sourcetree, GitHub Desktop, TortoiseGit \ub4f1\uacfc \uac19\uc740 GUI(Graphical User Interface) \ud234\uc744 \uc0ac\uc6a9\ud588\uc5c8\ub2e4. (\uadfc\ub370 \uacb0\uad6d \uc790\uc8fc \uc4f0\uace0 \uc26c\uc6b4 \uba85\ub839\uc5b4\ub4e4\uc740 \ub2e4 \uc678\uc6b0\uac8c \ub418\ub354\ub77c...\ud83e\udd26\u200d\u2642\ufe0f) \uc800\ub7f0 \ud234\ub4e4\ub3c4, command\ub3c4 \ub2e4 \uc88b\uc9c0\ub9cc \ucd5c\uadfc\uc758 \ub098\ub294 VSCode\ub97c \uc560\uc6a9\ud55c\ub2e4. \\n\\n\uc774 \ucc45\uc740 \uae43 \uba85\ub839\uc5b4\ub97c \uc9c1\uc811 \uc2e4\ud589\ud558\uace0 \ubc18\ubcf5\ud558\uba70 \uae43\uc5d0 \uc775\uc219\ud574\uc9c0\ub294 \uac83\uc5d0 \ucd08\uc810\uc744 \ub9de\ucd94\uace0 \uc788\uc73c\ub098 \ubd80\ub85d\uc5d0\ub294 VSCode\ub97c \ud1b5\ud574 \uc5b4\ub5bb\uac8c commit\uacfc push\ub97c \ud558\ub294\uc9c0 \uc18c\uac1c\ub418\uc5b4 \uc788\ub2e4. \ub098\ub294 \ub3cc\uace0 \ub3cc\uc544 VSCode\uc5d0 \uc815\ucc29\ud588\ub294\ub370 \uc774 \ucc45\uc744 \uc77d\ub294 \uc0ac\ub78c\uc740 \uc774\ub7f0 \uac04\ub2e8\ud55c \ubc29\ubc95\uc744 \ubc14\ub85c \uc0ac\uc6a9\ud560 \uc218 \uc788\ub2e4\ub294 \uac83\uc774 \uc21c\uac04\uc801\uc73c\ub85c \ub108\ubb34 \ubd80\ub7ec\uc6e0\ub2e4. \uc774\ub7f0 \uac8c \ucc45\uc774 \uc8fc\ub294 \ud798\uc774 \uc544\ub2cc\uac00 \uc2f6\ub2e4. \uadf8\ub807\ub2e4\uace0 \ud574\uc11c Git \uba85\ub839\uc5b4\ub97c \ubab0\ub77c\ub3c4 \ub41c\ub2e4\ub294 \uac83\uc740 \uc544\ub2c8\ub2e4. \uc5b4\ub5a0\ud55c \uac1c\ubc1c\uc790\uac00 \ub418\ub4e0 CLI \ud658\uacbd\uc774 \uc775\uc219\ud558\uba74 \ub354 \uc720\ub9ac\ud560 \uc218\ub3c4 \uc788\ub2e4\uace0 \uc0dd\uac01\ud55c\ub2e4. \uadf8\ub7ec\ub2c8 \uc774 \ucc45\uc744 \ud1b5\ud574 \uc5f0\uc2b5\ud574\ubcf4\uace0 \uacf5\ubd80\ud560 \uc218 \uc788\ub294 \uae30\ud68c\uac00 \ub418\uae38 \ubc14\ub780\ub2e4.\\n\\n### \uc27d\uac8c \uc124\uba85\ub418\uc5b4\uc9c4 \ucc45\\n\\n\ub09c \uc608\uc804\uc5d0 Git\uc744 [\uc0dd\ud65c\ucf54\ub529 \uac15\uc758](https://opentutorials.org/course/3838)\uc640 [Pro Git](https://git-scm.com/book/ko/v2)\uc73c\ub85c \uacf5\ubd80\ud588\uc5c8\ub2e4. GitHub\ub294 \ub530\ub85c \uc778\ud130\ub137\uc5d0\uc11c \ubb38\uc11c\ub4e4\uc744 \ub9c9 \ucc3e\uc544\uac00\uba74\uc11c \uacf5\ubd80\ud588\uc5c8\ub2e4. \uc544\ubb34 \uc0dd\uac01 \uc5c6\uc774 \ucc45\uc73c\ub85c Git\uc744 \uacf5\ubd80\ud558\ub2e4\uac00 \ub450 \ubc88 \uc815\ub3c4 \ud3ec\uae30\ud588\ub358 \uacbd\ud5d8\ub3c4 \uc788\ub2e4. tracked, staged \ub4f1 \uad00\uacc4\ub97c \uc774\ud574\ud558\ub294 \ub370 \uba38\ub9ac\uac00 \uc544\ud320\uc5c8\ub2e4. \uc774 \ucc45\uc740 \uc27d\uac8c \uc124\uba85\ub418\uc5b4 \uc788\uc5b4 \uadf8\ub7f4 \uc77c\uc774 \uc5c6\ub2e4.\\n\\n\ub610\ud55c, \ub3c5\uc790\ub97c \ubc30\ub824\ud558\ub294 \uc138\uc138\ud55c \ubd80\ubd84\ub4e4\uc774 \ub9ce\uc558\ub2e4. \ud3b8\uc9d1\uc790\uc774\uc2e0 [\uc0dd\uac01\uc774 \ucc45\uc73c\ub85c, \ubc15\ubbf8\uc815\uc758 \uae43&\uae43\ud5c8\ube0c\\n](https://brunch.co.kr/@hwchoi/32)\ub9cc \uc77d\uc5b4\ubd10\ub3c4 \uc54c \uc218 \uc788\ub2e4. \uc815\ub9d0 \ub3c5\uc790\ub97c \uc704\ud558\uace0 \uae54\ub054\ud558\uac8c \uc815\ub9ac\ub418\uc5b4 \uc788\ub294 \ucc45\uc744 \uc77d\uc5b4 \ubcf4\uace0 \uc2f6\ub2e4\uba74 \uc774 \ucc45\uc744 \ucd94\ucc9c\ud55c\ub2e4.\\n\\n## \ud83e\uddd1\u200d\ud83c\udfeb\ub300\uc0c1 \ub3c5\uc790\\n\\nGit\uacfc GitHub\ub294 \uac1c\ubc1c\uc790\ub77c\uba74 \ub204\uad6c\ub4e0\uc9c0 \uc54c\uc544\uc57c \ud558\ub294 \ud544\uc218 \uc9c0\uc2dd\uc774 \ub418\uc5b4\ubc84\ub838\ub2e4. \uae30\uc220\uc774 \ub610 \uc5b4\ub5bb\uac8c \ubc14\ub014\uc9c0\ub294 \uc798 \ubaa8\ub974\uaca0\uc9c0\ub9cc, GitHub\ub294 \uac1c\ubc1c\uc790\ub4e4\uc758 \uc18c\uc15c \ubbf8\ub514\uc5b4 \uac19\uc740 \ub290\ub08c\uc774\ub77c \uc720\uc6a9\ud55c \uc815\ubcf4\uc640 \ucf54\ub4dc\uac00 \ub9ce\uc774 \uc62c\ub77c\uc628\ub2e4. \uac1c\ubc1c\uc790\ub97c \ubaa9\ud45c\ub85c \ud55c\ub2e4\uba74 \uc77c\ucc0d \ubc30\uc6b0\uba74 \ubc30\uc6b8\uc218\ub85d \uc88b\ub2e4. \\n\\n\uc774 \ucc45\uc758 \uac00\uc7a5 \ud070 \uc7a5\uc810\uc740 \uc785\ubb38\uc11c\uc774\uba74\uc11c \uc2e4\uc81c\ub85c \ud611\uc5c5\ud558\ub294 \uac83 \uac19\uc740 \ud504\ub85c\uc138\uc2a4\ub97c \uacbd\ud5d8\ud574\ubcfc \uc218 \uc788\ub2e4\ub294 \uc810\uc774\ub2e4. Git\ub3c4 \uc0ac\uc2e4 \uc2ec\ud654\uc801\uc778 \ubd80\ubd84\uc73c\ub85c \ub4e4\uc5b4\uac00\uba74 [submodule](https://git-scm.com/book/ko/v2/Git-%EB%8F%84%EA%B5%AC-%EC%84%9C%EB%B8%8C%EB%AA%A8%EB%93%88) \ub4f1\uc758 \ub0b4\uc6a9\uc774 \uc788\uc9c0\ub9cc \uadf8\ub7f0 \ub0b4\uc6a9\uc740 \uc81c\uc678\ud558\uace0 \uc815\ub9d0 \uc2e4\uc81c\ub85c \uc790\uc8fc \uc4f0\uc774\ub294 \ub0b4\uc6a9\ub9cc \ub2f4\uaca8 \uc788\uc73c\ub2c8 Git\uacfc GitHub\uc5d0 \uc775\uc219\ud558\uc9c0 \uc54a\uc740 \uc785\ubb38\uc790\uc5d0\uac8c \ucd94\ucc9c\ud55c\ub2e4."},{"id":"/pandas-data-analysis","metadata":{"permalink":"/en/blog/pandas-data-analysis","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2021-09-27-pandas-data-analysis/pandas-data-analysis.md","source":"@site/blog/2021-09-27-pandas-data-analysis/pandas-data-analysis.md","title":"\ud83d\udcd6 \ud30c\uc774\uc36c \uba38\uc2e0\ub7ec\ub2dd \ud310\ub2e4\uc2a4 \ub370\uc774\ud130 \ubd84\uc11d","description":".","date":"2021-09-27T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":false,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \ud30c\uc774\uc36c \uba38\uc2e0\ub7ec\ub2dd \ud310\ub2e4\uc2a4 \ub370\uc774\ud130 \ubd84\uc11d","date":"2021-09-27","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/pandas-data-analysis","description":".","keywords":["pandas","Machine Learning","Data Analysis"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \ubc15\ubbf8\uc815\uc758 \uae43&\uae43\ud5c8\ube0c \uc785\ubb38","permalink":"/en/blog/Git-GitHub-introduction"},"nextItem":{"title":"\ud83d\udcd6 fastai\uc640 \ud30c\uc774\ud1a0\uce58\uac00 \ub9cc\ub098 \uaf43\ud53c\uc6b4 \ub525\ub7ec\ub2dd","permalink":"/en/blog/fastai-and-pytorch"}},"content":"## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/pandas-data-analysis.jpg)](http://www.kyobobook.co.kr/product/detailViewKor.laf?ejkGb=KOR&mallGb=KOR&barcode=9788956748337&orderClick=LAG&Kc=)\\n\\n- \uc81c\ubaa9: \ud30c\uc774\uc36c \uba38\uc2e0\ub7ec\ub2dd \ud310\ub2e4\uc2a4 \ub370\uc774\ud130 \ubd84\uc11d\\n- \uc800\uc790: \uc624\uc2b9\ud658\\n- \ucd9c\ud310\uc0ac: \uc815\ubcf4\ubb38\ud654\uc0ac\\n- \ucd9c\uac04: 2019-06-15\\n\\n## \ucc45 \ub9ac\ubdf0\\n\\n### \uc65c \ud310\ub9e4\ub7c9\uc774 \ub9ce\uc740\uac00?\\n\\n\uc804 \uc8fc\ub85c \uc5b4\ub5a4 \ucc45\uc744 \uc77d\uac70\ub098 \uc0b4 \ub54c \uadf8 \ucc45\uc758 \ud310\ub9e4\ub7c9\uc744 \ucc38\uace0\ud569\ub2c8\ub2e4. \uad50\ubcf4\ubb38\uace0\uc5d0\uc11c\uc758 \ud310\ub9e4\ub7c9\uc740 \ub2e4\ub978 \ucc45\uc774 \ub354 \ub9ce\uc740 \uac70\ub85c \uc815\ub82c\ub410\uc9c0\ub9cc, yes24\uc5d0\uc11c\ub294 \ud310\ub9e4\uc9c0\uc218\uac00 10,524\ub85c \ud310\ub2e4\uc2a4\ub77c\uace0 \uac80\uc0c9\ud588\uc744 \ub54c \uac00\uc7a5 \ub192\uc558\uc2b5\ub2c8\ub2e4. \uc815\ubcf4\ubb38\ud654\uc0ac\ub77c\ub294 \ucd9c\ud310\uc0ac\ub294 \uc81c\uac00 \uc774 \ucc45\uc744 \ud3ec\ud568\ud558\uc5ec \ub450 \ubc88\uc9f8\ub85c \uc77d\uc5b4\ubd05\ub2c8\ub2e4. \uc790\uc8fc \ubcf4\uc9c0 \ubabb\ud55c \ucd9c\ud310\uc0ac\uc9c0\ub9cc \ud310\ub9e4\ub7c9\uc774 \ub9ce\ub2e4\ub294 \uc810\uc5d0\uc11c \uc774 \ucc45\uc774 \ub108\ubb34 \uad81\uae08\ud588\uc2b5\ub2c8\ub2e4. \\n\\n\uc804 pandas\ub97c \ud544\uc694\ud560 \ub54c\ub9c8\ub2e4 \uacf5\uc2dd\ubb38\uc11c\ub97c \ubcf4\uac70\ub098 \uc720\ud29c\ube0c\uc5d0\uc11c \uac15\uc758\ub97c \ucc3e\uc544\ubcf4\uba74\uc11c \uacf5\ubd80\ud55c \ucf00\uc774\uc2a4\uc785\ub2c8\ub2e4. \ub9c9 \ub744\uc5c4\ub744\uc5c4 \uacf5\ubd80\ud558\ub2e4 \ubcf4\ub2c8\uae4c pandas\ub97c \ucc45\uc73c\ub85c \ud55c \ubc88 \uc81c\ub300\ub85c \uacf5\ubd80\ud574\ubcf4\uace0 \uc2f6\uc5c8\uc2b5\ub2c8\ub2e4. \ub9c8\uce68 \uc774 \ucc45\uc774 pandas, matplotlib, seaborn \ub4f1 \ub2e4\uc591\ud55c \ub77c\uc774\ube0c\ub7ec\ub9ac\ub97c \uc54c\ub824\uc8fc\ub294 \uac83 \uac19\uc544\uc11c \uc120\ud0dd\ud588\uc2b5\ub2c8\ub2e4. \uc774 \ucc45 \ub9d0\uace0\ub3c4 `\ub370\uc774\ud130 \uc0ac\uc774\uc5b8\uc2a4 \uc2a4\ucfe8`, `\ud30c\uc774\uc36c \ub77c\uc774\ube0c\ub7ec\ub9ac\ub97c \ud65c\uc6a9\ud55c \ub370\uc774\ud130 \ubd84\uc11d` \ub4f1\uc758 \uc88b\uc740 \ucc45\ub4e4\ub3c4 \uc788\uc2b5\ub2c8\ub2e4. \ub2e4\ub978 \ucc45\ub4e4\ub3c4 \ub098\uc911\uc5d0 \uc77d\uc5b4\ubcfc \uc608\uc815\uc785\ub2c8\ub2e4.\\n\\n### \uc608\uc81c\uac00 \ub9ce\uc740 \ucc45\\n\\n\uc608\uc81c\uac00 \uaf64 \ub9ce\ub2e4\uace0 \ub290\uaf08\uc73c\uba70 \uadf8\ub807\uae30\uc5d0 \ucd08\ubcf4\uc790\ub4e4\uc774 \uacf5\ubd80\ud558\ub294\ub370 \ud06c\uac8c \uc5b4\ub824\uc6c0\uc774 \uc5c6\uc744 \uac83\uc774\ub77c\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4. \uc608\uc81c\uc5d0\uc11c \uc880 \uc544\uc26c\uc6e0\ub358 \uc810\uc740 Jupyter Notebook\uc73c\ub85c \uc2e4\uc2b5\ud558\ub294 \ud658\uacbd\uc73c\ub85c \uc791\uc131\ub41c \uac83\uc774 \uc544\ub2c8\uace0 \uc77c\ubc18\uc801\uc778 Python \ud30c\uc77c\uc774\uc5c8\ub2e4\ub294 \uc810\uc785\ub2c8\ub2e4. \ubb3c\ub860 Python\uc73c\ub85c\ub3c4 \ub3cc\uc544\uac00\uaca0\uc9c0\ub9cc \'Jupyter Notebook \ud615\uc2dd\uc73c\ub85c \ud558\ub098\ud558\ub098 \ubcf4\uc5ec\uc92c\ub2e4\uba74 \uc5b4\ub560\uc744\uae4c\'\ub77c\ub294 \uc544\uc26c\uc6c0\uc774 \ub0a8\uc2b5\ub2c8\ub2e4. \\n\\n### \uc2dc\uac01\ud654 \ub77c\uc774\ube0c\ub7ec\ub9ac\\n\\nmatplotlib, seaborn\uc744 \uc624\ub79c\ub9cc\uc5d0 \ub2e4\uc2dc \ubcf5\uc2b5\ud560 \uc218 \uc788\ub2e4\ub294 \uc810\uc774 \uc88b\uc558\uc2b5\ub2c8\ub2e4. \ub610\ud55c, Folium \ub77c\uc774\ube0c\ub7ec\ub9ac\ub97c \uc0ac\uc6a9\ud558\uc5ec \uc9c0\ub3c4\ub97c \uc2dc\uac01\ud654\ud55c\ub2e4\ub294 \uac83\ub3c4 \uc7ac\ubc0c\uc5c8\uc2b5\ub2c8\ub2e4. \ub370\uc774\ud130\ub97c \ubd84\uc11d\ud558\ub294 \uacfc\uc815\uc774\ub098 \uba38\uc2e0\ub7ec\ub2dd \ubaa8\ub378\uc744 \ub9cc\ub4e4 \ub54c \uc5b4\ub5bb\uac8c \ud65c\uc6a9\ud560\uc9c0 \uc54c\ub824\uc90d\ub2c8\ub2e4. \\n\\n### \uba38\uc2e0\ub7ec\ub2dd \ub9db\ubcf4\uae30\\n\\n\uba38\uc2e0\ub7ec\ub2dd \ubaa8\ub378\uc744 \ub9cc\ub4e4\uc5b4\ubcf4\ub294 \uacfc\uc815\uc744 \ubcf4\uc5ec\uc90d\ub2c8\ub2e4. \ubaa8\ub378\uc744 \uc9c1\uc811 \ub9cc\ub4dc\ub294 \uacfc\uc815\uc5d0\uc11c pandas\uac00 \uc5b4\ub5bb\uac8c \uc4f0\uc774\uace0 \ub2e4\ub978 \uc2dc\uac01\ud654 \ub77c\uc774\ube0c\ub7ec\ub9ac\ub4e4\uc774 \uc5b4\ub5bb\uac8c \uc4f0\uc774\ub294 \uc9c0 \ubcf4\uc5ec\uc8fc\ub294 \uac83\uc740 \ub9e4\uc6b0 \uc88b\uc558\uc73c\ub098, \uc544\uc26c\uc6e0\ub358 \uc810\uc740 \uba38\uc2e0\ub7ec\ub2dd \ubd80\ubd84\uc73c\ub85c \ub4e4\uc5b4\uac00\uace0 \uc2ec\ud654\uc801\uc778 \ubd80\ubd84\uc73c\ub85c \ub4e4\uc5b4\uac00\uc11c \uadf8\ub7f0\uc9c0 \uc138\ubd80\uc801\uc778 \uc124\uba85\uc740 \uc0dd\ub7b5\ub418\uc5b4 \uc788\uc2b5\ub2c8\ub2e4. \ucc28\ub77c\ub9ac KNN, SVM \uac19\uc740 \uc5b4\ub824\uc6b4 \uc54c\uace0\ub9ac\uc998\uc740 \uc81c\uc678\ud558\uace0 \uc26c\uc6b4 \uc54c\uace0\ub9ac\uc998\uc73c\ub85c \ub9cc\ub4e4\uc5b4\uc9c4 ML \ubaa8\ub378\uc744 \ub9cc\ub4e4\uc5b4\ubcf4\ub294 \uacfc\uc815\uc744 \uc18c\uac1c\ud588\ub2e4\uba74 \ucc98\uc74c \uacf5\ubd80\ud558\uc2dc\ub294 \ubd84\ub4e4\uc5d0\uac8c \uc77d\ub294\ub370 \ub354 \ud3b8\ud588\uc744 \uac83\uc774\ub77c \uc0dd\uac01\ud569\ub2c8\ub2e4. \\n\\n## \ub300\uc0c1\ub3c5\uc790\\n\\n\ud30c\uc774\uc36c\uc744 \uacf5\ubd80\ud55c \ud6c4 \ub370\uc774\ud130 \ubd84\uc11d\uc744 \ud574\ubcf4\uace0 \uc2f6\uc740\ub370 \ubb58 \uacf5\ubd80\ud574\uc57c \ud560 \uc9c0 \uac10\uc774 \uc548 \uc7a1\ud788\ub294 \uc0ac\ub78c\uc5d0\uac8c \ucd94\ucc9c\ub4dc\ub9bd\ub2c8\ub2e4. \uc774 \ucc45\uc740 392 \ud398\uc774\uc9c0\ub85c \ub2e4\ub978 \ub370\uc774\ud130 \ubd84\uc11d \ucc45\ub4e4\uacfc \ube44\uad50\ud558\uba74 \ubd80\ub2f4\uc774 \ub35c \ud55c \ucc45\uc785\ub2c8\ub2e4. pandas\ub9cc \uacf5\ubd80\ud558\ub294 \ucc45\uc740 \uc544\ub2c8\ubbc0\ub85c \uc804\ubc18\uc801\uc778 \ub370\uc774\ud130 \ubd84\uc11d\uc744 \ud574\ubcf4\uace0 \uc2f6\uc740 \ubd84\ub4e4\uaed8 \ucd94\ucc9c\ub4dc\ub9bd\ub2c8\ub2e4. pandas\ub9cc \uacf5\ubd80\ud558\uc2dc\uace0 \uc2f6\uc73c\uc2dc\ub2e4\uba74 [pandas 10\ubd84 \uc644\uc131](https://dataitgirls2.github.io/10minutes2pandas/) \ubb38\uc11c\ub97c \uc77d\uc73c\uc2dc\ub294 \uac78 \ucd94\ucc9c\ub4dc\ub9bd\ub2c8\ub2e4."},{"id":"/fastai-and-pytorch","metadata":{"permalink":"/en/blog/fastai-and-pytorch","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2021-09-19-fastai-and-pytorch/fastai-and-pytorch.md","source":"@site/blog/2021-09-19-fastai-and-pytorch/fastai-and-pytorch.md","title":"\ud83d\udcd6 fastai\uc640 \ud30c\uc774\ud1a0\uce58\uac00 \ub9cc\ub098 \uaf43\ud53c\uc6b4 \ub525\ub7ec\ub2dd","description":"\ubc15\uc0ac \ud559\uc704 \uc5c6\uc774 AI\ub97c \ud3fc\ub098\uac8c \uad6c\ud604\ud558\ub294 \ubc95","date":"2021-09-19T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 fastai\uc640 \ud30c\uc774\ud1a0\uce58\uac00 \ub9cc\ub098 \uaf43\ud53c\uc6b4 \ub525\ub7ec\ub2dd","date":"2021-09-19","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/fastai-and-pytorch","description":"\ubc15\uc0ac \ud559\uc704 \uc5c6\uc774 AI\ub97c \ud3fc\ub098\uac8c \uad6c\ud604\ud558\ub294 \ubc95","keywords":["PyTorch","fastai","\ub525\ub7ec\ub2dd"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \ud30c\uc774\uc36c \uba38\uc2e0\ub7ec\ub2dd \ud310\ub2e4\uc2a4 \ub370\uc774\ud130 \ubd84\uc11d","permalink":"/en/blog/pandas-data-analysis"},"nextItem":{"title":"\ud83d\udcd6 \uae54\ub054\ud55c \ud30c\uc774\uc36c \ud0c4\ud0c4\ud55c \ubc31\uc5d4\ub4dc","permalink":"/en/blog/python-backend-flask"}},"content":":::info\\n\ud55c\ube5b\ubbf8\ub514\uc5b4 \\\\<\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4\\\\> \ud65c\ub3d9\uc744 \uc704\ud574\uc11c \ucc45\uc744 \uc81c\uacf5\ubc1b\uc544 \uc791\uc131\ub41c \uc11c\ud3c9\uc785\ub2c8\ub2e4.\\n:::\\n\\n![\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4 2021](../assets/I-am-reviewer-2021.jpg)\\n\\n## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/fastai-and-pytorch.jpg)](http://www.kyobobook.co.kr/product/detailViewKor.laf?ejkGb=KOR&mallGb=KOR&barcode=9791162244630&orderClick=LEa&Kc=)\\n\\n- \uc81c\ubaa9: fastai\uc640 \ud30c\uc774\ud1a0\uce58\uac00 \ub9cc\ub098 \uaf43\ud53c\uc6b4 \ub525\ub7ec\ub2dd\\n- \uc800\uc790: Jeremy Howard, Sylvain Gugger\\n- \uc5ed\uc790: \ubc15\ucc2c\uc131, \uae40\uc9c0\uc740\\n- \ucd9c\ud310\uc0ac: \ud55c\ube5b\ubbf8\ub514\uc5b4\\n- \ucd9c\uac04: 2021-08-10\\n\\n{/* truncate */}\\n\\n## Book Review\\n\\n### \ud480\uceec\ub7ec\\n\\n\ub2e4\ub978 \ubd84\ub4e4\uc758 \ub9ac\ubdf0\ub4e4\uc744 \ubcf4\uc2dc\uba74 \uc544\uc2dc\ub4ef\uc774 \uc774 \ucc45\uc740 \ud480\uceec\ub7ec\uc785\ub2c8\ub2e4. \uc815\ub9d0 \ubcc4 \uac70 \uc544\ub2c8\uc9c0\ub9cc \uc774\ub7f0 \ubc88\uc5ed\uc11c\ub4e4\uc744 \ud480\uceec\ub7ec\ub85c \ub9cc\ub098\uba74 \uae30\ubd84\uc774 \uadf8\ub807\uac8c \uc88b\ub354\ub77c\uace0\uc694. \ub610\ud55c, 720p\ub77c\ub294 \ub450\uaebc\uc6b4 \ucc45\uc778\ub370\ub3c4 \ubd88\uad6c\ud558\uace0 \ud480\uceec\ub7ec\ub85c \uc778\uc1c4\ud55c \ucd9c\ud310\uc0ac\uac00 \uc815\ub9d0 \ub300\ub2e8\ud558\ub2e4\uace0 \ub290\uaef4\uc9d1\ub2c8\ub2e4. \ub355\ubd84\uc5d0 \uc2dc\uac01\uc801\uc73c\ub85c\ub3c4 \ud3b8\ud558\uac8c \ubcfc \uc218 \uc788\uc5c8\uc2b5\ub2c8\ub2e4.\\n\\n### TIP\\n\\n\uc774 \ucc45\uc758 \uc800\uc790 2\uba85\uacfc \ud568\uaed8 Alexis Gallagher\ub77c\ub294 \uc0ac\ub78c\uc774 \uc218\ud559\uacfc \uacf5\ud559\uc801 \ubc30\uacbd\uc9c0\uc2dd\uc774 \uc801\uac70\ub098 \uc2e4\uc81c\ub85c \ucf54\ub529 \uacbd\ud5d8\uc774 \uc5c6\ub294 \uc0ac\ub78c\ub4e4\uc744 \uc704\ud55c TIP\uc744 \uc54c\ub824\uc90d\ub2c8\ub2e4. \uac01 \ubd84\uc57c\uc5d0 \ub300\ud55c \uba58\ud1a0\uac00 \ub098\uc640\uc11c \ud55c \ubc88\uc529 \uc870\uc5b8\ud574\uc8fc\uace0 \uc54c\ub824\uc8fc\ub294 \ub290\ub08c\uc785\ub2c8\ub2e4. \ub9e4\uc6b0 \ub3c4\uc6c0\uc774 \ub410\uc2b5\ub2c8\ub2e4.\\n\\n### \ud558\ud5a5\uc2dd\\n\\n\uc77c\ubc18\uc801\uc778 \uba38\uc2e0\ub7ec\ub2dd, \ub525\ub7ec\ub2dd \ucc45\uc774\uc5c8\ub2e4\uba74 \ubc11\ubc14\ub2e5\ubd80\ud130 \ubb34\uc5b8\uac00\ub97c \uad6c\ud604\ud558\uc5ec \ud558\ub098\ud558\ub098 \ub2e8\uacc4\ub97c \uc62c\ub77c\uac00\ub294 \ub290\ub08c\uc778\ub370 \uc774 \ucc45\uc740 \ub2e4\ub985\ub2c8\ub2e4. \uc774 \ucc45\uc740 top-down(\ud558\ud5a5\uc2dd)\uc73c\ub85c \uc4f0\uc5ec\uc9c4 \ucc45\uc785\ub2c8\ub2e4. \uc77c\ubc18\uc801\uc778 \uac15\uc758 \ub610\ub294 \ucc45\uacfc\ub294 \uc815\ubc18\ub300\uc758 \ubc29\uc2dd\uc774\uae30 \ub54c\ubb38\uc5d0 \ub525\ub7ec\ub2dd\uc5d0 \ub300\ud55c \uc0ac\uc804 \uc9c0\uc2dd\uc774 \uc788\uc73c\uba74 \uc880 \ub354 \uc77d\uae30 \uc26c\uc6b8 \uac83 \uac19\uc2b5\ub2c8\ub2e4.\\n\\n\ucc98\uc74c\ubd80\ud130 \ubaa8\ub378\uc744 \ub9cc\ub4e4\uc5b4\ubcf4\uace0 \uc0ac\uc6a9\ud574\ubcf4\ub294\ub370 \uc774\ub7f0 \uacfc\uc815 \uc18d\uc5d0\uc11c \ubaa8\ub4e0 \uac1c\ub150\ub4e4\uc744 \uc774\ud574\ud558\uae30\ub294 \uc5b4\ub835\uc2b5\ub2c8\ub2e4. \uc774\ub7ec\ud55c \ucd08\ubc18\ubd80\uc5d0\uc11c\ub294 \uadf8\uc800 \ub525\ub7ec\ub2dd\uc5d0 \ub300\ud55c \ud765\ubbf8\ub97c \ub290\ub07c\ub294\ub370 \ubaa9\ud45c\ub97c \uac16\uae30\ub97c \ubc14\ub78d\ub2c8\ub2e4. \ud6c4\uc5d0 \ub0b4\ubd80\uc801\uc73c\ub85c \uc5b4\ub5bb\uac8c \uc791\ub3d9\ub418\ub294\uc9c0 \uc124\uba85\ud569\ub2c8\ub2e4. \\n\\n### \uac1c\ubc1c \ud658\uacbd\\n\\n\uc774 \ucc45\uc740 Jupyter Notebook\uc744 \uc0ac\uc6a9\ud569\ub2c8\ub2e4. \uadf8\ub7ec\ub098 \uc774\ub7f0 \ud658\uacbd\uc744 \uad6c\uc131\ud558\ub294 \ubc29\ubc95\uae4c\uc9c0 \uc790\uc138\ud788 \uc54c\ub824\uc8fc\uc9c0\ub294 \uc54a\uc2b5\ub2c8\ub2e4. \uadf8\ub807\uae30\uc5d0 \uc790\uc2e0\uc774 \ud658\uacbd\uc744 \uad6c\uc131\ud560 \uc904 \uc54c\uc544\uc57c \ud569\ub2c8\ub2e4. \uc804 GPU\uac00 \ub9e4\uc6b0 \uc88b\uc9c0 \uc54a\uae30 \ub54c\ubb38\uc5d0 \uad6c\uae00\uc5d0\uc11c \uc81c\uacf5\ud558\ub294 [Colab](https://colab.research.google.com/)\uc744 \uc0ac\uc6a9\ud588\uc2b5\ub2c8\ub2e4. \uadfc\ub370 \uc804 Colab Pro\ub97c \uc0ac\uc6a9\ud588\ub294\ub370 \uc65c \uc800\uc790\uc758 \ud658\uacbd\uc5d0\uc11c \ucf54\ub4dc\ub97c \uc2e4\ud589\uc2dc\ud0a4\ub294 \uac83\ubcf4\ub2e4 \ub354 \uc624\ub798 \uac78\ub9ac\ub294\uc9c0 \uc544\uc9c1\ub3c4 \uc798 \ubaa8\ub974\uaca0\uc2b5\ub2c8\ub2e4.\\n\\n\uc800\ucc98\ub7fc GPU\uac00 \uc88b\uc9c0 \uc54a\ub2e4\uba74 Colab \uc0ac\uc6a9 \ubc29\ubc95\ub3c4 \uc54c\uc544\uc57c\uaca0\uc8e0. \ucc45\uc5d0\uc11c\ub294 \uc790\uc138\ud788 \uc124\uba85\ub418\uc5b4 \uc788\uc9c0 \uc54a\uc9c0\ub9cc [fast.ai course](https://course.fast.ai/start_colab)\uc5d0\uc11c Colab\uc744 \uc0ac\uc6a9\ud558\uc5ec \ucf54\ub4dc\ub97c \uc0ac\uc6a9\ud558\ub294 \ubc29\ubc95\uc744 \uc54c\ub824\uc90d\ub2c8\ub2e4. \\n\\n### \uc9c8\ubb38\uc9c0(Questionnaire)\\n\\n\uac01 \uc7a5\uc774 \ub05d\ub0a0 \ub54c\ub9c8\ub2e4 \uc9c8\ubb38\uc9c0\uac00 \uc8fc\uc5b4\uc9d1\ub2c8\ub2e4. \uac1c\uc778\uc801\uc73c\ub85c \uc804 \uc774 \uc9c8\ubb38\uc9c0\uac00 \ub3c5\ud2b9\ud558\ub2e4\uace0 \ub290\uaf08\uace0 \uc815\ub9d0 \ub3c4\uc6c0\uc774 \ub9ce\uc774 \ub410\uc2b5\ub2c8\ub2e4. \uc774 \uc9c8\ubb38\uc9c0\ub294 \uac01 \uc7a5\uc744 \uacf5\ubd80\ud558\uae30 \uc804\uc5d0 \uba3c\uc800 \uc77d\uc5b4\ub3c4 \uc88b\uace0 \ud55c \uc7a5\uc744 \ub2e4 \uc77d\uace0 \ud6c4\uc5d0 \ubcf5\uc2b5\uc6a9\uc73c\ub85c \uc77d\uc5b4\ubd10\ub3c4 \uc88b\uc2b5\ub2c8\ub2e4. \uc0ac\uc804\uc5d0 \uc77d\uc73c\uba74 \uc5b4\ub5a4 \ubd80\ubd84\uc5d0 \uc9d1\uc911\ud560 \uc9c0 \ubbf8\ub9ac \uc54c \uc218 \uc788\uace0 \uacf5\ubd80\ud55c \ud6c4\uc5d0 \uc77d\uc73c\uba74 \uc790\uc2e0\uc774 \uc81c\ub300\ub85c \uacf5\ubd80\ud588\uc5c8\ub294\uc9c0 \uc9c0\uc2dd\uc744 \uc810\uac80\ud560 \uc218 \uc788\uaca0\uc8e0. \ucc45\uc744 \uc798 \uc77d\uc5c8\ub2e4\uba74 \ub300\ubd80\ubd84\uc758 \uc9c8\ubb38\ub4e4\uc740 \ub300\ub2f5\ud560 \uc218 \uc788\uc744 \uac83\uc785\ub2c8\ub2e4. \uadf8\ub798\ub3c4 \uc9c8\ubb38\uc9c0\uc758 \uc815\ub2f5\uc744 \ubaa8\ub974\uba74 [Fastbook questionnaire solutions - fast.ai forum](https://forums.fast.ai/t/fastbook-questionnaire-solutions-megathread/76955)\uc5d0\uc11c solution\uc774 \uc8fc\uc5b4\uc9c0\ub2c8 \ucc38\uace0\ud558\uc2dc\uba74 \uc88b\uc744 \uac83 \uac19\uc2b5\ub2c8\ub2e4. \ub098\uc911\uc5d0 \uae30\ud68c\uac00 \ub41c\ub2e4\uba74 \ube14\ub85c\uadf8\uc5d0 \uc9c8\ubb38\uc9c0\uc5d0 \ub300\ud55c \uc194\ub8e8\uc158\uc744 \uc62c\ub824\ubcfc \uc608\uc815\uc785\ub2c8\ub2e4.\\n\\n## \ub300\uc0c1\ub3c5\uc790\\n\\n\ucc45\uc5d0\uc11c\ub294 1\ub144 \uc815\ub3c4\uc758 \ucf54\ub529 \uacbd\ud5d8, \uace0\ub4f1\ud559\uad50 \uc774\uc0c1\uc758 \uc218\ud559 \uacfc\uc815\uc744 \ub9c8\uce5c \uc0ac\ub78c\uc744 \ub300\uc0c1\uc73c\ub85c \ud569\ub2c8\ub2e4. \uadf8\ub7ec\ub098 \uc804 \uc774 \ucc45\uc744 \uc77d\uae30 \uc804\uc5d0 \ucd94\uac00\uc801\uc73c\ub85c \ud30c\uc774\uc36c, PyTorch, \uc870\uae08\uc758 \ub525\ub7ec\ub2dd \uc9c0\uc2dd\uc774 \uc788\uc73c\uba74 \ucc45\uc744 \uc77d\ub294 \ub370 \ub354 \ud3b8\ud560 \uac83\uc774\ub77c\uace0 \uc608\uc0c1\ub429\ub2c8\ub2e4. \\n\\n\uc804 \ub525\ub7ec\ub2dd\uc774 \uacf5\ubd80\ud558\uae30 \uc5b4\ub824\uc6b4 \uc774\uc720\uac00 \'\ub2e4\uc591\ud55c \uac1c\ub150\ub4e4\uacfc \uae30\ucd08 \uc9c0\uc2dd\ub4e4\'\uc774\ub77c\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4. \uac1c\ub150\uc744 \uc138\ubd80\uc801\uc73c\ub85c \ub2e4 \uc124\uba85\ud574\uc918\ub3c4 \uac11\uc790\uae30 \ubb34\uc218\ud788 \ub9ce\uc740 \uac1c\ub150\ub4e4\uc774 \ub4f1\uc7a5\ud558\uba74 \uba38\ub9ac\uac00 \ud63c\ub780\uc2a4\ub7ec\uc6cc\uc9d1\ub2c8\ub2e4. \ub610\ud55c, \ub525\ub7ec\ub2dd\uc744 \uacf5\ubd80\ud558\uae30 \uc704\ud574\uc11c \uae30\ucd08 \uc9c0\uc2dd\uc774 \ucda9\ubd84\ud558\uc9c0 \uc54a\ub2e4\uba74 \uc774\ud574\ud558\uae30 \uc5b4\ub824\uc6b8 \uc218\ub3c4 \uc788\ub2e4\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4. \uadf8\ub807\uae30\uc5d0 \uc774 \ucc45\uc744 \uc77d\uc744 \ub54c \ube60\ub974\uac8c \uc77d\uae30 \ubcf4\ub2e4\ub294 \ucc9c\ucc9c\ud788 \uc2dc\uac04\uc801 \uc5ec\uc720\ub97c \ub450\uace0 \uc774\ud574\ud558\uba74\uc11c \uc77d\uc73c\uc2dc\ub294 \uac78 \ucd94\ucc9c\ub4dc\ub9bd\ub2c8\ub2e4."},{"id":"/python-backend-flask","metadata":{"permalink":"/en/blog/python-backend-flask","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2021-08-23-python-backend-flask/python-backend-flask.md","source":"@site/blog/2021-08-23-python-backend-flask/python-backend-flask.md","title":"\ud83d\udcd6 \uae54\ub054\ud55c \ud30c\uc774\uc36c \ud0c4\ud0c4\ud55c \ubc31\uc5d4\ub4dc","description":"\uc9c0\uae08\uae4c\uc9c0 \uc5c6\uc5c8\ub358 \ubc31\uc5d4\ub4dc \uac1c\ubc1c\uc790\ub97c \uc704\ud55c \ud30c\uc774\uc36c","date":"2021-08-23T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \uae54\ub054\ud55c \ud30c\uc774\uc36c \ud0c4\ud0c4\ud55c \ubc31\uc5d4\ub4dc","date":"2021-08-23","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/python-backend-flask","description":"\uc9c0\uae08\uae4c\uc9c0 \uc5c6\uc5c8\ub358 \ubc31\uc5d4\ub4dc \uac1c\ubc1c\uc790\ub97c \uc704\ud55c \ud30c\uc774\uc36c","keywords":["Backend","Flask","Python","API"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 fastai\uc640 \ud30c\uc774\ud1a0\uce58\uac00 \ub9cc\ub098 \uaf43\ud53c\uc6b4 \ub525\ub7ec\ub2dd","permalink":"/en/blog/fastai-and-pytorch"},"nextItem":{"title":"\ud83d\udcd6 \uc774\uac83\uc774 \ucde8\uc5c5\uc744 \uc704\ud55c \ucf54\ub529 \ud14c\uc2a4\ud2b8\ub2e4 with \ud30c\uc774\uc36c","permalink":"/en/blog/coding-test-with-python"}},"content":"## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/python-backend.jpg)](http://www.kyobobook.co.kr/product/detailViewKor.laf?ejkGb=KOR&mallGb=KOR&barcode=9791186697757&orderClick=LEa&Kc=)\\n\\n- \uc81c\ubaa9: \uae54\ub054\ud55c \ud30c\uc774\uc36c \ud0c4\ud0c4\ud55c \ubc31\uc5d4\ub4dc\\n- \uc800\uc790: \uc1a1\uc740\uc6b0\\n- \ucd9c\ud310\uc0ac: \ube44\uc81c\uc774\ud37c\ube14\ub9ad\\n- \ucd9c\uac04: 2019-01-25\\n\\n{/* truncate */}\\n\\n## \ud83c\udfacIntro\\n\\n\uc774 \ucc45\uc740 flask\ub85c API\ub97c \uc81c\uc791\ud574\ubcfc \uc218 \uc788\ub3c4\ub85d \ub3c4\uc640\uc8fc\ub294 \ucc45\uc785\ub2c8\ub2e4. \uc0ac\uc2e4 flask \ucc45\uc740 \ucd5c\uadfc\uc5d0 [Do it! \uc810\ud504 \ud22c \ud50c\ub77c\uc2a4\ud06c](http://www.kyobobook.co.kr/product/detailViewKor.laf?ejkGb=KOR&mallGb=KOR&barcode=9791163031970&orderClick=LEa&Kc=)\ub77c\ub294 \uc88b\uc740 \ucc45\uc774 \ub098\uc654\uc8e0. \uadf8\ub7f0\ub370\ub3c4 \uc774 \ucc45\uc744 \uc120\ud0dd\ud55c \uc774\uc720\ub294 \ub2e8\uc21c\ud788 \ud50c\ub77c\uc2a4\ud06c\ub9cc \uacf5\ubd80\ud558\ub294 \uac83\uc774 \uc544\ub2c8\ub77c API \uac1c\ubc1c\uc744 \uacf5\ubd80\ud574\ubcf4\uace0 \uc2f6\uc5c8\uae30 \ub54c\ubb38\uc785\ub2c8\ub2e4. \ud2b9\ud788 \ubc31\uc5d4\ub4dc\uc5d0\uc11c \uac70\uc758 \ud544\uc218\uc801\uc73c\ub85c \uc0ac\uc6a9\ud558\ub294 REST \uc544\ud0a4\ud14d\ucc98 \ud328\ud134\uc744 \uc0ac\uc6a9\ud574\uc11c \ub9d0\uc774\uc8e0.\\n\\n\uc774 \ucc45\uc740 \ud50c\ub77c\uc2a4\ud06c, REST API\ub97c \uac19\uc774 \uacf5\ubd80\ud560 \uc218 \uc788\ub294 \ucc45 \uc911\uc5d0 \uadf8\ub098\ub9c8 \uac00\uc7a5 \ucd5c\uadfc\uc5d0 \ub098\uc654\uc2b5\ub2c8\ub2e4. \ub2e4\ub978 \ucc45\ub4e4\uc740 \ub354 \uc624\ub798\ub41c \ucc45\uc774\uac70\ub098, \ucc45 \ub9ac\ubdf0\uac00 \uc5c6\uc5b4\uc11c \ud070 \uace0\ubbfc \uc5c6\uc774 \uc774 \ucc45\uc744 \uad6c\ub9e4\ud588\uc5c8\uc2b5\ub2c8\ub2e4. \uc544\ubb34\ub798\ub3c4 \uc800\ud76c \ub098\ub77c\uc5d0\uc11c\ub294 \ud30c\uc774\uc36c\uc744 \ud65c\uc6a9\ud55c \ubc31\uc5d4\ub4dc\ubcf4\ub2e4 \uc790\ubc14 \uc2a4\ud504\ub9c1\uc744 \ub354 \ub9ce\uc774 \uc0ac\uc6a9\ud574\uc11c \uadf8\ub7f0\uc9c0 \ud30c\uc774\uc36c\uc744 \ud65c\uc6a9\ud55c \ubc31\uc5d4\ub4dc \ucc45\uc774 \uaf64 \uc801\uc740 \uac83 \uac19\uc2b5\ub2c8\ub2e4. \ud30c\uc774\uc36c\uc744 \uc990\uaca8 \uc4f0\ub294 \uc0ac\ub78c\uc73c\ub85c\uc11c \ud30c\uc774\uc36c\uc744 \ud65c\uc6a9\ud55c \ubc31\uc5d4\ub4dc \ucc45\ub3c4 \ub9ce\uc774 \ub098\uc654\uc73c\uba74 \uc88b\uaca0\uc2b5\ub2c8\ub2e4.\\n\\n## Book Review\\n\\n### API \uc81c\uc791\\n\\ntwitter\uc758 \uc77c\ubd80 \ud575\uc2ec \uae30\ub2a5\ub4e4\uc744 \uad6c\ud604\ud574\uc11c \ubbf8\ub2c8 \ubc84\uc804\uc758 \ud2b8\uc704\ud130\ub97c \ub9cc\ub4e4\uc5b4\ubd05\ub2c8\ub2e4. \uc774 API\ub97c \ub9cc\ub4e4\uc5b4\ubcf4\uba74\uc11c HTTP, \ub370\uc774\ud130\ubca0\uc774\uc2a4, unit test, AWS \ubc30\ud3ec \ub4f1 \ubc31\uc5d4\ub4dc \uac1c\ubc1c\uc744 \uacbd\ud5d8\ud558\uace0 \uae30\ubcf8\uc801\uc778 \uac1c\ub150\ub4e4\uc744 \uacf5\ubd80\ud558\ub294 \ubc29\uc2dd\uc774\uc8e0. \uadf8\ub807\uae30\uc5d0 \uc804\ubc18\uc801\uc778 \ubc31\uc5d4\ub4dc \uacf5\ubd80\uc5d0\ub294 \ub3c4\uc6c0\uc774 \ub418\uc9c0\ub9cc, \ud50c\ub77c\uc2a4\ud06c \uc790\uccb4\uc5d0 \ub300\ud55c \uacf5\ubd80\uc5d0\ub294 \ub3c4\uc6c0\uc774 \ub418\uc9c0 \uc54a\uc558\uc2b5\ub2c8\ub2e4. \\n\\nHTTP \uac1c\ub150\ub4e4\uc744 \ub2e4\uc2dc \ubcf5\uc2b5\ud574\ubcfc \uc218 \uc788\uc5c8\uace0 \uc778\uc99d \ubd80\ubd84\uc744 \ub2e4\ub8e8\ub294 \uac83\ub3c4 \uc7ac\ubc0c\uc5c8\uc2b5\ub2c8\ub2e4. \uc544\ud0a4\ud14d\ucc98\ub97c \ub2e4\ub8e8\ub294 \ubd80\ubd84\ub3c4 \uc788\uc5c8\ub294\ub370 \\"\uc774\ub7f0 \ubd80\ubd84\ub3c4 \ub2e4\ub904\uc900\ub2e4\uace0?\\" \uc0dd\uac01\uc774 \ub4e4 \uc815\ub3c4\ub85c \ub3c4\uc6c0\uc774 \ub410\ub358 \uac83 \uac19\uc2b5\ub2c8\ub2e4. \\n\\n### \ud0a4\uc6cc\ub4dc \uc81c\uc2dc\\n\\n\uc804 \ucc45\ub4e4\uc744 \uc77d\uc744 \ub54c \ub05d\uc5d0 \ubd80\ub85d\uc5d0\uc11c\ub77c\ub3c4 \ub2e4\uc74c\uc5d0 \uacf5\ubd80\ud560 \uc8fc\uc81c\ub97c \uc81c\uc2dc\ud574\uc8fc\ub294 \uac83\uc744 \uc120\ud638\ud558\ub294 \ud3b8\uc785\ub2c8\ub2e4. \uac04\ub2e8\ud558\uac8c\ub77c\ub3c4 \uc5b4\ub5a4 \ud0a4\uc6cc\ub4dc\ub97c \uc81c\uc2dc\ud574\uc8fc\uba74 \uadf8\uc5d0 \ub300\ud574 \uad6c\uae00\ub9c1\uc744 \ud574\ubcf4\uba74\uc11c \ub610\ub294 \ub2e4\uc74c \uc8fc\uc81c\uc5d0 \ub300\ud55c \ucc45, \uac15\uc758\ub97c \ubcf4\uba74\uc11c \uacf5\ubd80\ub97c \ud560 \uc218 \uc788\uc73c\ub2c8\uae4c\uc694. \uadf8\ub7f0 \ubd80\ubd84\uc5d0\uc120 \uc800\ub294 \uc774 \ucc45\uc774 \uad1c\ucc2e\uc558\ub2e4\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4. \\n\\n### \ub808\uac70\uc2dc \ucf54\ub4dc\\n\\n\uc774 \ucc45\uc774 2019\ub144 \ucd08\ubc18\uc5d0 \ucd9c\uac04\ub418\uc5c8\uae30 \ub54c\ubb38\uc5d0 2021\ub144 \uc911\ubc18\uc5d0 \uc77d\ub294 \uc9c0\uae08 \uc5b4\ub290 \uc815\ub3c4\ub294 \uc608\uc0c1\ud588\ub358 \ubd80\ubd84\uc785\ub2c8\ub2e4. \\"\uc124\ub9c8 \ub9ce\uc774 \ub098\uc624\uaca0\uc5b4?\\"\ub77c\ub294 \uc0dd\uac01\uc744 \ud558\uba74\uc11c \uc774 \ucc45\uc744 \uc77d\uc5c8\uc2b5\ub2c8\ub2e4. \ub2e4\ud589\ud788 [decode \uc5d0\ub7ec](https://teddygood.github.io/python/AttributeError-str-decode/) \ub9d0\uace0\ub294 \ub531\ud788 \ucf54\ub4dc\ub97c \ub3cc\ub9ac\ub294 \ub370 \uc5b4\ub824\uc6c0\uc740 \uc5c6\uc5c8\ub358 \uac83 \uac19\uc2b5\ub2c8\ub2e4. \\n\\n### \uc0dd\ub7b5\ub41c \ucf54\ub4dc\\n\\n\uc544\ubb34\ub798\ub3c4 \ucc45\uc774 \ub2e4\uc591\ud55c \uc8fc\uc81c\ub4e4\uc744 \ub2e4\ub8e8\ub2e4 \ubcf4\ub2c8 \uad6c\ud604\ud574\uc57c \ud558\ub294 \uc77c\ubd80 \ucf54\ub4dc\ub97c \uc0dd\ub7b5\ud569\ub2c8\ub2e4. \uc774 \ubd80\ubd84\uc740 \uc880 \uc544\uc26c\uc6e0\uc9c0\ub9cc \uc774\ub7f0 \ubd80\ubd84\uae4c\uc9c0 \uc0c1\uc138\ud558\uac8c \ub2e4\ub918\ub2e4\uba74 \uc544\ubb34\ub798\ub3c4 \ucc45\uc774 400\ucabd\uc774 \uc544\ub2c8\ub77c \ub300\ub7b5 500~600\ucabd\uc774 \ub410\uc744 \uac70 \uac19\uc73c\ub2c8 \uc774\ub7f0 \ubd80\ubd84\uc740 \uc800\uc790\uc758 \uc785\uc7a5\uc774 \uc774\ud574\ub429\ub2c8\ub2e4.\\n\\n## \ub300\uc0c1 \ub3c5\uc790 \\n\\n\ud30c\uc774\uc36c\uc73c\ub85c \uc804\ubc18\uc801\uc778 \ubc31\uc5d4\ub4dc\ub97c \uacbd\ud5d8\ud574\ubcf4\uace0 \uc2f6\uc740 \ubd84\ub4e4\uaed8 \ucd94\ucc9c\ud574 \ub4dc\ub9bd\ub2c8\ub2e4. \uc608\uc804\uc5d4 \ubaa9\ud45c\ud558\ub294 \uc9c1\uad70\uc5d0 \ub300\ud574 \uc9c1\uc811 \uacbd\ud5d8\ud574\ubcf4\uc9c0 \ubabb\ud55c \ubd80\ubd84\ub4e4\uc740 \uc5b4\ub5bb\uac8c \uacf5\ubd80\ud574\uc57c \ud558\ub098 \uace0\ubbfc\ud588\uc744 \ud150\ub370 \ucc45\uc744 \ud1b5\ud558\uc5ec \uc27d\uac8c \uacbd\ud5d8\ud560 \uc218 \uc788\uc5b4\uc11c \uc88b\uc740 \uac83 \uac19\uc2b5\ub2c8\ub2e4. \uac1c\uc778\uc801\uc73c\ub85c \uc774\ub807\uac8c \ud504\ub85c\uc81d\ud2b8 \ud558\ub098\ub97c \uc8fc\uc81c\ub85c \uc804\ubc18\uc801\uc778 \ubd80\ubd84\uc744 \ub2e4\ub8f0 \uc218 \uc788\ub294 \ucc45\ub4e4\uc774 \ub354 \ub9ce\uc774 \ub098\uc640\uc92c\uc73c\uba74 \ud558\ub294 \ubc14\ub78c\uc785\ub2c8\ub2e4."},{"id":"/coding-test-with-python","metadata":{"permalink":"/en/blog/coding-test-with-python","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2021-08-21-coding-test-with-python/coding-test-with-python.md","source":"@site/blog/2021-08-21-coding-test-with-python/coding-test-with-python.md","title":"\ud83d\udcd6 \uc774\uac83\uc774 \ucde8\uc5c5\uc744 \uc704\ud55c \ucf54\ub529 \ud14c\uc2a4\ud2b8\ub2e4 with \ud30c\uc774\uc36c","description":"\ucde8\uc5c5\uacfc \uc774\uc9c1\uc744 \uacb0\uc815\ud558\ub294 \uc54c\uace0\ub9ac\uc998 \uc778\ud130\ubdf0 \uc644\ubcbd \uac00\uc774\ub4dc","date":"2021-08-21T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \uc774\uac83\uc774 \ucde8\uc5c5\uc744 \uc704\ud55c \ucf54\ub529 \ud14c\uc2a4\ud2b8\ub2e4 with \ud30c\uc774\uc36c","date":"2021-08-21","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/coding-test-with-python","description":"\ucde8\uc5c5\uacfc \uc774\uc9c1\uc744 \uacb0\uc815\ud558\ub294 \uc54c\uace0\ub9ac\uc998 \uc778\ud130\ubdf0 \uc644\ubcbd \uac00\uc774\ub4dc","keywords":["\ucf54\ub529 \ud14c\uc2a4\ud2b8","\uc54c\uace0\ub9ac\uc998 \uc778\ud130\ubdf0","\ucde8\uc5c5"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \uae54\ub054\ud55c \ud30c\uc774\uc36c \ud0c4\ud0c4\ud55c \ubc31\uc5d4\ub4dc","permalink":"/en/blog/python-backend-flask"},"nextItem":{"title":"\ud83d\udcd6 \ucd5c\uace0\uc758 \ud300\uc740 \uc65c \uae30\ubcf8\uc5d0 \ucda9\uc2e4\ud55c\uac00","permalink":"/en/blog/the-ideal-team-player"}},"content":":::info\\n\ud55c\ube5b\ubbf8\ub514\uc5b4 \\\\<\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4\\\\> \ud65c\ub3d9\uc744 \uc704\ud574\uc11c \ucc45\uc744 \uc81c\uacf5\ubc1b\uc544 \uc791\uc131\ub41c \uc11c\ud3c9\uc785\ub2c8\ub2e4.\\n:::\\n\\n![\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4 2021](../assets/I-am-reviewer-2021.jpg)\\n\\n## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/coding-test-with-python.jpg)](http://www.kyobobook.co.kr/product/detailViewKor.laf?ejkGb=KOR&mallGb=KOR&barcode=9791162243077&orderClick=LAG&Kc=)\\n\\n- \uc81c\ubaa9: \uc774\uac83\uc774 \ucde8\uc5c5\uc744 \uc704\ud55c \ucf54\ub529 \ud14c\uc2a4\ud2b8\ub2e4 with \ud30c\uc774\uc36c\\n- \uc800\uc790: \ub098\ub3d9\ube48\\n- \ucd9c\ud310\uc0ac: \ud55c\ube5b\ubbf8\ub514\uc5b4\\n- \ucd9c\uac04: 2020-08-05\\n\\n{/* truncate */}\\n\\n## Intro\\n\\n\uc694\uc998 \ucde8\uc5c5 \uc900\ube44\uc0dd\ub4e4\uc5d0\uac8c\ub294 \uae30\uc5c5\uc5d0 \ub4e4\uc5b4\uac00\uae30 \uc704\ud574\uc11c\ub294 \ucf54\ub529 \ud14c\uc2a4\ud2b8\uac00 \ud544\uc218\uac00 \ub418\uc5b4 \ubc84\ub838\uc2b5\ub2c8\ub2e4. \ucf54\ub529 \ud14c\uc2a4\ud2b8\ub97c \uc548 \ubcf4\ub294 \uae30\uc5c5\ub4e4\ub3c4 \uc874\uc7ac\ud558\uc9c0\ub9cc \ub300\ubd80\ubd84\uc758 \uae30\uc5c5\ub4e4\uc774 \ucf54\ub529 \ud14c\uc2a4\ud2b8\ub97c \uccd0\uc11c \uba74\uc811 \uc804\uc5d0 \ucc44\uc6a9 \uacfc\uc815\uc5d0\uc11c \uc0ac\ub78c\ub4e4\uc744 \uac70\ub974\ub294 \uc2dc\ud5d8\uc774 \ub418\uc5b4 \ubc84\ub838\uc2b5\ub2c8\ub2e4. \uc608\uc804\uc774\uc5c8\ub2e4\uba74 \uc885\ub9cc\ubd81\uc744 \uc0ac\uc11c \ud63c\uc790 \uc5f4\uc2ec\ud788 \ub059\ub059 \ub418\uba74\uc11c \uacf5\ubd80\ud588\uc744\ud150\ub370 \uc694\uc998\uc5d0\ub294 \ucde8\uc5c5\uc744 \uc704\ud55c \ucf54\ub529 \ud14c\uc2a4\ud2b8\ub97c \uc900\ube44\ud558\ub294 \ucc45\uc774 \ub098\uc640 \uacf5\ubd80\ud560 \uc218 \uc788\ub294 \ub9ac\uc18c\uc2a4\uac00 \ub9ce\uc544 \ub2e4\ud589\uc774\ub77c \uc0dd\uac01\ud569\ub2c8\ub2e4.\\n\\n\ud2b9\ud788 \ucf54\ub529\uc744 \uacf5\ubd80\ud558\uc2dc\ub294 \ubd84\uc774\ub77c\uba74 \ud55c \ubc88\ucbe4\uc740 \uc720\ud29c\ube0c\uc5d0\uc11c \ubd24\uc744 \ubd84\uc778 \ub098\ub3d9\ube48\ub2d8\uc774 \uc774 \ucc45\uc758 \uc800\uc790\uc154\uc11c \ubc18\uac00\uc6e0\uc2b5\ub2c8\ub2e4. \uc800\ub294 \ucf54\ub529 \ud14c\uc2a4\ud2b8 \uc900\ube44\ub97c C++\ub85c \ud574\uc11c \'\uc774 \ucc45\uc744 \uc77d\uc744 \uae30\ud68c\ub294 \uc5c6\uaca0\uad6c\ub098\' \uc2f6\uc5c8\ub294\ub370 \uc774\ub807\uac8c \uc88b\uc740 \ud504\ub85c\uadf8\ub7a8\uc5d0 \ucc38\uc5ec\ud558\uace0 \uc788\uc5b4\uc11c \uc77d\uc744 \uc218 \uc788\ub294 \uae30\ud68c\uac00 \uc0dd\uacbc\uc2b5\ub2c8\ub2e4. \uc608\uc804\ubd80\ud130 [\uc720\ud29c\ube0c \uac15\uc758 \uc601\uc0c1](https://www.youtube.com/c/dongbinna/videos)\ub3c4 \ubcf4\uace0 [\uc548\uacbd\uc7a1\uc774\uac1c\ubc1c\uc790 \ube14\ub85c\uadf8](https://ndb796.tistory.com/)\ub3c4 \ub9ce\uc774 \ubcf4\uba74\uc11c \ucc38\uace0\ud588\uc5c8\uc2b5\ub2c8\ub2e4. \uc774 \ubd84\uc744 \ubaa8\ub974\uc2e0\ub2e4\uba74 \ub4e4\uc5b4\uac00\uc11c \uad6c\uacbd\ud574\ubcf4\uc138\uc694. \ucf54\ub529\uc744 \uacf5\ubd80\ud558\uc2dc\uac70\ub098 \uc785\ubb38\ud558\uc2dc\uace0 \uc2f6\uc740 \ubd84\ub4e4\uc5d0\uac8c \ucd94\ucc9c\ub4dc\ub9bd\ub2c8\ub2e4.\\n\\n\ub610\ud55c, \uc774 \ucc45\uc740 \ud55c\ube5b\ubbf8\ub514\uc5b4\uc758 **\uc774\uac83\uc774** \uc2dc\ub9ac\uc988\uc785\ub2c8\ub2e4. \uc608\uc804\uc5d0 \uc774\uac83\uc774 \uc6b0\ubd84\ud22c \ub9ac\ub205\uc2a4\ub2e4\ub97c \ubcf4\uace0 \uc6b0\ubd84\ud22c\ub97c \uacf5\ubd80\ud588\ub358 \uae30\uc5b5\uc774 \uc788\ub294\ub370 \uc774\ub807\uac8c \ud45c\uc9c0\ub3c4 \ubc14\ub00c\uace0 \uba4b\uc788\uac8c \uc7ac\ud0c4\uc0dd\ud55c \uc2dc\ub9ac\uc988 \uac19\uc544\uc11c \ub9e4\uc6b0 \ubc18\uac00\uc6e0\uc2b5\ub2c8\ub2e4.\\n\\n## Book Review\\n\\n### \ucd5c\uc2e0 \ucf54\ub529 \ud14c\uc2a4\ud2b8 \ubd84\uc11d\\n\\n\uc77c\ubc18\uc801\uc778 \uc54c\uace0\ub9ac\uc998 \ubb38\uc81c \ud480\uc774\ub97c \uc704\ud55c \ucc45\uc774 \uc544\ub2c8\ub77c \uc774 \ucc45\uc740 \ucde8\uc5c5\uc744 \uc704\ud55c \ucf54\ub529 \ud14c\uc2a4\ud2b8\ub97c \uc900\ube44\ud560 \uc218 \uc788\ub294 \ucc45\uc785\ub2c8\ub2e4. \ucde8\uc5c5 \uc900\ube44\ub97c \uc704\ud574\uc11c\ub294 \uace0\ub4f1\ud559\uc0dd \ub54c \uae30\ucd9c\uc744 \ud480\uace0, \uc5b4\ub5a4 \ubb38\uc81c\uac00 \ub098\uc62c\uc9c0 \ubd84\uc11d\ud588\ub4ef\uc774 \ucf54\ub529 \ud14c\uc2a4\ud2b8\uc5d0\uc11c\ub3c4 \uc5b4\ub5a4 \ubb38\uc81c \uc720\ud615\uc774 \ub098\uc62c\uc9c0 \uc0dd\uac01\ud574\ubcfc \ud544\uc694\uac00 \uc788\uc2b5\ub2c8\ub2e4.\\n\\n\uc774 \ucc45\uc5d0\uc11c\ub294 \uadf8\uac04 16~20\ub144 \ub3d9\uc548\uc758 \ub2e4\uc591\ud55c \uae30\uc5c5\ub4e4\uc758 \ucf54\ub529 \ud14c\uc2a4\ud2b8 \uae30\ucd9c\ubb38\uc81c \uc720\ud615\ub4e4\uc744 \ubd84\uc11d\ud558\uc5ec \ucf54\ub529 \ud14c\uc2a4\ud2b8\ub97c \uc5b4\ub5bb\uac8c \uc900\ube44\ud560 \uc9c0 \ubc29\ud5a5\uc744 \uc7a1\uc544\uc8fc\uba70 \ucd5c\uc2e0 \uacbd\ud5a5\uc744 \uc54c\ub824\uc8fc\uae30\ub3c4 \ud569\ub2c8\ub2e4. \ubd80\ub85d\uc5d0\ub294 \uce74\uce74\uc624, \uc0bc\uc131\uc804\uc790 \uae30\ucd9c\ubb38\uc81c\uac00 \uc8fc\uc5b4\uc9c0\uae30\ub3c4 \ud558\uc8e0. \ud2b9\uc815 \uae30\uc5c5\uc744 \ubaa9\ud45c\ub85c \ud558\ub294 \ubd84\ub4e4\uc5d0\uac8c\ub294 \uc0c1\ub2f9\ud788 \ub3c4\uc6c0\uc774 \ub420 \uac83\uc774\ub77c\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4.\\n\\n### \ucc44\uc6a9 \uac00\uc774\ub4dc\\n\\n\uc774 \ucc45\uc740 \uc77c\ubc18\uc801\uc778 \uc54c\uace0\ub9ac\uc998 \ucf54\ub529 \ud14c\uc2a4\ud2b8\ub97c \uc900\ube44\ud558\ub294 \ucc45\uc774\uc9c0\ub9cc, \uc804\ubc18\uc801\uc778 \ucde8\uc5c5\uc5d0 \ub300\ud55c \uac00\uc774\ub4dc\ub3c4 \ud574\uc8fc\ub294 \ucc45\uc785\ub2c8\ub2e4. \ube44\ub85d \ud2b9\uc815 \uac1c\ubc1c \uc9c1\uad70\uc5d0 \ub300\ud574 \uc138\ubd80\uc801\uc73c\ub85c \uba74\uc811 \uc900\ube44\ub97c \ud560 \uc218 \uc788\ub3c4\ub85d \ub3c4\uc640\uc8fc\ub294 \uac83\uc740 \uc544\ub2c8\uc9c0\ub9cc, \ucf54\ub529 \ud14c\uc2a4\ud2b8\ub97c \uc900\ube44\ud558\uace0 \ud639\uc2dc \ubaa8\ub97c \uadf8 \ub2e4\uc74c\uc758 \uba74\uc811\uae4c\uc9c0 \ub300\ube44\ud558\uae30 \uc704\ud55c \ub0b4\uc6a9\ub4e4\uc744 \uc124\uba85\ud574\uc90d\ub2c8\ub2e4. \uc608\ub97c \ub4e4\uc5b4 \ub300\ubd80\ubd84\uc758 \uae30\uc5c5\ub4e4\uc758 \uc77c\ubc18\uc801\uc778 \ucc44\uc6a9 \ud504\ub85c\uc138\uc2a4, \uae30\uc220 \uba74\uc811\uc758 \ub300\ud45c\uc801\uc778 \uc720\ud615, \uae30\uc220 \uba74\uc811 \uc900\ube44\ud558\ub294 \ubc29\ubc95 \ub4f1 \uae30\uc220 \uba74\uc811\uc744 \uc900\ube44\ud558\uae30 \uc704\ud574 \uc5b4\ub5a4 \ubd80\ubd84\ub4e4\uc744 \uacf5\ubd80\ud558\uace0 \ub300\ube44\ud574\uc57c \ud560 \uc9c0 \uc54c\ub824\uc90d\ub2c8\ub2e4. \\n\\n### \ub9e4\uc6b0 \uce5c\uc808\ud55c \ucc45\\n\\n\uc81c\ubaa9\uc744 \ubcf4\uba74 \uc54c \uc218 \uc788\ub4ef\uc774 \uc774 \ucc45\uc740 Python\uc73c\ub85c \uc124\uba85\ub418\uc5b4\uc9c4 \ucc45\uc785\ub2c8\ub2e4. \uadf8\ub7ec\ub098 \uc774 \ucc45\uc740 C++, Java\ub85c \uc791\uc131\ub41c \uc18c\uc2a4\ucf54\ub4dc\ub3c4 [\uae43\ud5c8\ube0c](https://github.com/ndb796/python-for-coding-test)\uc5d0 \uc81c\uacf5\uc774 \ub429\ub2c8\ub2e4. \uc5b8\uc5b4 \uc790\uccb4\ub294 \uc544\ubb34\ub798\ub3c4 \ucf54\ub529 \ud14c\uc2a4\ud2b8\uc5d0\uc11c \uc218\ub2e8\uc5d0 \ubd88\uacfc\ud558\ubbc0\ub85c \ud639\uc2dc \ub2e4\ub978 \uc5b8\uc5b4\ub85c \uacf5\ubd80\ud574\ubcf4\uace0 \uc2f6\uc740 \ubd84\ub4e4\uc744 \uc800\uc790\ubd84\uaed8\uc11c \ubc30\ub824\ud574\uc8fc\uc2e0 \uac83 \uac19\uc2b5\ub2c8\ub2e4. \uaf64 \ub9ce\uc740 \ucf54\ub4dc\ub4e4\uc774\uc5c8\ub294\ub370 \ub2e4\uc591\ud55c \uc5b8\uc5b4\ub85c \uc18c\uc2a4\ucf54\ub4dc\ub97c \uc81c\uacf5\ud574\uc8fc\uc2e0 \uac83 \uc815\ub9d0 \ub300\ub2e8\ud558\ub2e4\uace0 \uc0dd\uac01\ub429\ub2c8\ub2e4.\\n\\n\uc774 \ucc45\uc774 \uce5c\uc808\ud55c \uc774\uc720\ub294 \uc774\uac83\ubfd0\ub9cc\uc774 \uc544\ub2d9\ub2c8\ub2e4. \uc720\ud29c\ube0c\uc5d0\uc11c \uac15\uc758\ub3c4 \uc81c\uacf5\ub429\ub2c8\ub2e4. \uac15\uc758\uc5d0\uc11c\ub294 Python \ucf54\ub4dc\ub9cc \uc124\uba85\ud558\ub294 \uac83\uc774 \uc544\ub2c8\ub77c C++, Java \ucf54\ub4dc\uc5d0 \ub300\ud574\uc11c\ub3c4 \uac04\ub2e8\ud558\uac8c \uc124\uba85\ud558\ubbc0\ub85c \ub4e4\uc5b4\ubcf4\uc2dc\uba74 \ub3c4\uc6c0\uc774 \ub9ce\uc774 \ub420 \uac83\uc774\ub77c \uc0dd\uac01\ud569\ub2c8\ub2e4. \ud2b9\ud788 \uc804 \ucf54\ub529\ud14c\uc2a4\ud2b8 \ubc14\ub85c \uc804 \ub0a0\uc5d0 \ubcbc\ub77d\uce58\uae30\ub85c \ub3d9\ube48\ub2d8\uc758 \uc774\ucf54\ud14c \uc601\uc0c1 \ubab0\uc544\uc11c \ubd24\uc5c8\uc2b5\ub2c8\ub2e4. \\n\\n## \ubd80\ub85d\uacfc \uc88b\uc740 \ub0b4\uc6a9\ub4e4\\n\\n\ud559\uc2b5 \uc21c\uc11c, \uc774 \ucc45\uc73c\ub85c \ud559\uc2b5 \ud558\ub294 \ubc29\ubc95 \ub4f1 \uc800\uc790\uac00 \ucde8\uc5c5 \uc900\ube44\uc0dd\ubd84\ub4e4\uc5d0\uac8c \ub3c4\uc6c0\uc774 \ub420 \ub9cc\ud55c \uac83\ub4e4\uc740 \ubaa8\ub450 \ub123\uc73c\uc168\uc2b5\ub2c8\ub2e4. \uadf8\ub807\uae30\uc5d0 \ucc45\uc758 \uad6c\uc131\uc774 \ub9e4\uc6b0 \uc88b\ub2e4\uace0 \ub9d0\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \ud30c\uc774\uc36c\uc5d0 \uc775\uc219\ud558\uc9c0 \uc54a\uc740 \ubd84\ub4e4\uc744 \uc704\ud55c \uac04\ub2e8\ud55c \ud30c\uc774\uc36c \ubb38\ubc95\ub3c4 \ubd80\ub85d\uc73c\ub85c \uc8fc\uc5b4\uc838 \uc27d\uace0 \ube60\ub974\uac8c \ud30c\uc774\uc36c\uc744 \uacf5\ubd80\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \\n\\n\uc694\uc998 \ucf54\ub529 \ud14c\uc2a4\ud2b8\ub4e4\uc740 \uc77c\ubc18\uc801\uc778 \uc54c\uace0\ub9ac\uc998 \ubb38\uc81c\ub9cc \ud480 \uc904 \uc54c\uc544\uc11c\ub294 \uc548 \ub429\ub2c8\ub2e4. \ubb3c\ub860 \uae30\uc5c5\ub9c8\ub2e4 \ub2e4\ub97c \uc218\ub294 \uc788\uc2b5\ub2c8\ub2e4\ub9cc \ub300\ubd80\ubd84\uc758 \uc720\uba85\ud55c \uae30\uc5c5\ub4e4\uc740 \uac1c\ubc1c\ud615 \ucf54\ub529 \ud14c\uc2a4\ud2b8 \ubb38\uc81c\ub3c4 \uc788\uc2b5\ub2c8\ub2e4. \ub610\ub294, \ud654\uc0c1 \uba74\uc811\uc5d0\uc11c \uc774\ub7f0 \ubd80\ubd84\ub4e4\uc744 \uac11\uc791\uc2a4\ub7fd\uac8c \ud574\ubcf4\ub77c\uace0 \uc81c\uc2dc\ud560 \uc218\ub3c4 \uc788\uc8e0. \uc774 \ubd80\ubd84\uc5d0 \ub300\ud574\uc11c\ub3c4 \uc790\uc2e0\uc774 \uc9c0\uc6d0\ud558\ub294 \uc9c1\uad70\ub9c8\ub2e4 \uc5b4\ub5a4 \ubb38\uc81c\uac00 \ub098\uc62c\uc9c0 \ubaa8\ub974\uc9c0\ub9cc, \uc774 \ucc45\uc758 \ubd80\ub85d\uc5d0\uc11c\ub294 \uac1c\ubc1c\ud615 \ucf54\ub529 \ud14c\uc2a4\ud2b8\ub3c4 \uac04\ub2e8\ud558\uac8c \ub2e4\ub8f9\ub2c8\ub2e4. \uce74\uce74\uc624 \ucf54\ub529 \ud14c\uc2a4\ud2b8\uc5d0 \ub098\uc654\ub358 REST API \ud638\ucd9c, JSON format \ub370\uc774\ud130 \ud30c\uc2f1\uc744 \uc54c\ub824\uc90d\ub2c8\ub2e4. \uac1c\ub150\ubd80\ud130 \uc9c1\uc811 \ud30c\uc774\uc36c\uc73c\ub85c \ucf54\ub529\ud558\ub294 \ubd80\ubd84\uc774 \ub098\uc624\ub2c8 \ucc38\uace0\ud558\uc2dc\uba74 \uc88b\uc744 \uac83 \uac19\uc2b5\ub2c8\ub2e4. \\n\\n## \ub300\uc0c1 \ub3c5\uc790 \\n\\n\'\uc774 \ucc45 \ud55c \uad8c\uc73c\ub85c \ucf54\ub529 \ud14c\uc2a4\ud2b8\ub97c \ub9cc\uc810 \ubc1b\uace0 \uba74\uc811\uc73c\ub85c \ub118\uc5b4\uac08 \uc218 \uc788\ub2e4\' \uc774\uac74 \uc7a5\ub2f4 \ubabb \ud558\uaca0\uc2b5\ub2c8\ub2e4.  \ucf54\ub529 \ud14c\uc2a4\ud2b8\uc5d0\ub294 \uc5b4\ub5a4 \ubb38\uc81c\uac00 \ub098\uc62c\uc9c0 \ubaa8\ub974\uace0 \uc790\uc2e0\uc758 \ucee8\ub514\uc158\uc5d0 \ub530\ub77c \uc810\uc218\ub294 \uc5b8\uc81c\ub4e0\uc9c0 \ub2ec\ub77c\uc9c8 \uc218 \uc788\uae30 \ub54c\ubb38\uc774\uc8e0. \uadf8\ub7ec\ub098 \uc774 \ucc45\uc744 \ubcf4\uba74 \ucf54\ub529 \ud14c\uc2a4\ud2b8 \uc900\ube44\ub97c \ub300\ub7b5 \uc808\ubc18 \uc774\uc0c1\uc740 \ud560 \uc218 \uc788\ub2e4\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4.\\n\\n\uac1c\ubc1c \uc9c1\uad70\uc73c\ub85c \uc774\uc81c \ub9c9 \ucde8\uc5c5\uc744 \uc900\ube44\ud558\uc2dc\ub294 \ucde8\uc5c5 \uc900\ube44\uc0dd\ub4e4\uc774 \uc77d\uc73c\uc168\uc73c\uba74 \uc88b\uaca0\uc2b5\ub2c8\ub2e4. \uc774 \ucc45\uc5d0\ub294 \ucde8\uc5c5 \uc900\ube44\ub97c \uc704\ud574 \ub9ce\uc740 \uac83\ub4e4\uc774 \ub2f4\uaca8 \uc788\uc2b5\ub2c8\ub2e4. **\uc774\ub7f0 \uac78 \uc608\uc804\uc5d0 \ubbf8\ub9ac \uc54c\uc558\uc73c\uba74 \uc88b\uc558\uc744\ud150\ub370**\ub77c\uace0 \uc0dd\uac01\uc774 \ub4dc\ub294 \uc815\ubcf4\ub4e4\uc774 \ub9e4\uc6b0 \ub9ce\uc2b5\ub2c8\ub2e4. \uc0ac\ub78c\uc774 \ucc45\uc744 \uc77d\ub294 \uc774\uc720 \uc911 \ud558\ub098\ub294 \ub2e4\ub978 \uc0ac\ub78c\ub4e4\uc758 \uacbd\ud5d8, \uc790\uc2e0\uc5d0\uac8c \ub3c4\uc6c0\uc774 \ub418\ub294 \uc815\ubcf4\ub4e4\uc744 \ub354 \ube60\ub974\uac8c \uc2b5\ub4dd\ud558\uae30 \uc704\ud574\uc11c\ub77c\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4. `\uc815\ub9d0 \uc608\uc804\uc5d0 \uc774\ub7f0 \uc815\ubcf4\ub4e4\uc744 \uc54c\uc558\ub354\ub77c\uba74 \uc880 \ub354 \ube60\ub974\uac8c \ucde8\uc5c5 \uc900\ube44\ub97c \ud574\ubcfc \uc218 \uc788\uc5c8\uc9c0 \uc54a\uc558\uc744\uae4c?`\ub77c\ub294 \uc0dd\uac01\uc774 \ub4e4 \uc815\ub3c4\uc774\ub2c8 \ucde8\uc900\uc0dd\uc774 \uc544\ub2c8\ub354\ub77c\ub3c4 \ucef4\uacf5 \ud559\ubd80\uc0dd\uc774\ub77c\uba74 \ubbf8\ub9ac \uc77d\uc5b4\ubcf4\uc2dc\ub294 \uac83\uc744 \ucd94\ucc9c\ub4dc\ub9bd\ub2c8\ub2e4."},{"id":"/the-ideal-team-player","metadata":{"permalink":"/en/blog/the-ideal-team-player","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2021-08-20-the-ideal-team-player/the-ideal-team-player.md","source":"@site/blog/2021-08-20-the-ideal-team-player/the-ideal-team-player.md","title":"\ud83d\udcd6 \ucd5c\uace0\uc758 \ud300\uc740 \uc65c \uae30\ubcf8\uc5d0 \ucda9\uc2e4\ud55c\uac00","description":"\uc870\uc9c1\uc744 \uc131\uc7a5\uc2dc\ud0a4\ub294 \ud300 \ud50c\ub808\uc774\uc5b4\uc758 3\uac00\uc9c0 \ube44\ubc00","date":"2021-08-20T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":false,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \ucd5c\uace0\uc758 \ud300\uc740 \uc65c \uae30\ubcf8\uc5d0 \ucda9\uc2e4\ud55c\uac00","date":"2021-08-20","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/the-ideal-team-player","description":"\uc870\uc9c1\uc744 \uc131\uc7a5\uc2dc\ud0a4\ub294 \ud300 \ud50c\ub808\uc774\uc5b4\uc758 3\uac00\uc9c0 \ube44\ubc00","keywords":["\uae30\uc5c5 \ubb38\ud654","\uc870\uc9c1 \ubb38\ud654","\uc131\uc7a5"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \uc774\uac83\uc774 \ucde8\uc5c5\uc744 \uc704\ud55c \ucf54\ub529 \ud14c\uc2a4\ud2b8\ub2e4 with \ud30c\uc774\uc36c","permalink":"/en/blog/coding-test-with-python"},"nextItem":{"title":"\ud83d\udcd6 \uba38\uc2e0\ub7ec\ub2dd\uc744 \ud65c\uc6a9\ud55c \uc6f9 \ucd5c\uc801\ud654","permalink":"/en/blog/ml-with-web-optimization"}},"content":"## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/the-ideal-team-player.jpg)](http://www.kyobobook.co.kr/product/detailViewKor.laf?ejkGb=KOR&mallGb=KOR&barcode=9788965962762&orderClick=LEa&Kc=)\\n\\n- \uc81c\ubaa9: \ucd5c\uace0\uc758 \ud300\uc740 \uc65c \uae30\ubcf8\uc5d0 \ucda9\uc2e4\ud55c\uac00\\n- \uc800\uc790: Patrick M. Lencioni(\ud328\ud2b8\ub9ad \ub80c\uc2dc\uc624\ub2c8)\\n- \uc5ed\uc790: \uc720\uc815\uc2dd\\n- \ucd9c\ud310\uc0ac: \ud750\ub984\ucd9c\ud310\\n- \ucd9c\uac04: 2018-08-24\\n\\n## \uc774 \ucc45\uc744 \uc77d\uac8c \ub41c \uc774\uc720\\n\\n\ucd5c\uadfc \ud55c \uae30\uc5c5\uc758 \ucc44\uc6a9\uacf5\uace0\ub97c \ubcf4\uba74\uc11c \uadf8 \uae30\uc5c5\uc758 \ub178\uc158 \ud398\uc774\uc9c0\ub97c \ubcfc \uc218 \uc788\uc5c8\uc2b5\ub2c8\ub2e4. \uadf8 \ud398\uc774\uc9c0\uc5d0\ub294 \uadf8 \uae30\uc5c5\uc758 \ud544\ub3c5 \ub3c4\uc11c \ud398\uc774\uc9c0\uac00 \ub530\ub85c \uc788\ub354\ub77c\uace0\uc694. \ub9c8\uce68 \ucd5c\uadfc \uac1c\ubc1c \uad00\ub828 \ub3c5\uc11c\ub9cc \ud574\uc11c \ub2e4\ub978 \uc8fc\uc81c\uc758 \ucc45\ub4e4\ub3c4 \uc77d\uc5b4\ubcf4\uace0 \uc2f6\ub2e4\ub294 \uc0dd\uac01\uc744 \ud588\uc5c8\uc2b5\ub2c8\ub2e4. \uadf8 \ud398\uc774\uc9c0\uc5d0\ub294 \uc2a4\ud0c0\ud2b8\uc5c5, \uc778\uac04 \uad00\uacc4 \ub4f1 \uc2a4\ud0c0\ud2b8\uc5c5\uc5d0 \ub2e4\ub2c8\uace0 \uc788\ub294 \uc0ac\ub78c\uc774\uba74 \ub3c4\uc6c0\uc774 \ub9ce\uc774 \ub420 \uac83 \uac19\uc740 \ucc45\ub4e4\uc744 \uc18c\uac1c\ud574\uc92c\uc2b5\ub2c8\ub2e4. 10\uad8c\uc758 \ucc45\ub4e4 \uc911 \ubb58 \uc77d\uc744\uae4c \uace0\ubbfc\uc774 \ub418\ub354\ub77c\uace0\uc694.\\n\\n\uccab \ubc88\uc9f8\ub85c \uace0\ub824\ud55c \uc810\uc740 \uc77c\ub2e8 **\ub3c4\uc11c\uad00\uc5d0 \ucc45\uc774 \uc788\ub294\uac00**\uc600\uc2b5\ub2c8\ub2e4. \uc0ac\ub824\uba74 \ubc30\uc1a1\ub418\ub294 \uc2dc\uac04\ub3c4 \uaf64 \uac78\ub9ac\uace0 \ub3c8\uc774 \uc9c0\ucd9c\ub418\ub2c8\uae4c\uc694. \ub450 \ubc88\uc9f8\ub85c \uace0\ub824\ud55c \uc810\uc740 **\uc9c0\uae08 \uc774 \ucc45\uc774 \ub098\uc5d0\uac8c \ub3c4\uc6c0\uc774 \ub418\ub294\uac00**\uc600\uc2b5\ub2c8\ub2e4. \ub2e4\uc591\ud55c \uc2a4\ud0c0\ud2b8\uc5c5\uc758 \ucc44\uc6a9 \uacf5\uace0\ub97c \uc77d\uc73c\uba74\uc11c \uc81c\uac00 \ub290\ub080 \uc810\uc740 **\ub2e4\uc591\ud55c \uae30\uc5c5\ub4e4\uc758 \ubb38\ud654\ub4e4\uc5d0 \ub300\ud574 \uc880 \ub354 \uc54c\uc544\ubd10\uc57c \ud560 \ud544\uc694\uac00 \uc788\ub2e4\ub294 \uac83**\uc774\uc5c8\uc2b5\ub2c8\ub2e4. \ubd84\uba85\ud788 \uc2a4\ud0c0\ud2b8\uc5c5\ub4e4\uc740 \ubb3c\ub860 \uac1c\ubc1c\uc744 \uc798\ud558\ub294 \uc0ac\ub78c\uc744 \uc6d0\ud558\uae30\ub3c4 \ud558\uc9c0\ub9cc, \ubb38\ud654\ub97c \uc911\uc694\uc2dc\ud558\ub294 \uae30\uc5c5\ub4e4\ub3c4 \uc788\uc744 \ud14c\ub2c8\uae4c\uc694. \uc774 \ubc16\uc5d0\ub3c4 \ud55c \uad8c, \ud55c \uad8c \uace0\ub974\ub294 \uac83\uc5d0 \ub300\ud574 \ub2e4\uc591\ud55c \uc774\uc720\uac00 \uc788\uc5c8\uc9c0\ub9cc \uae00\uc774 \uae38\uc5b4\uc9c0\ubbc0\ub85c \uc801\uc9c0 \uc54a\uaca0\uc2b5\ub2c8\ub2e4.\\n\\n## \ucc45 \ub9ac\ubdf0\\n\\n### \ubaa8\ub378\\n\\n\uc774 \ucc45\uc5d0\uc11c\ub294 \uc81c\uc2dc\ud558\ub294 \ubaa8\ub378\uc774 \uc788\uc2b5\ub2c8\ub2e4. **\uacb8\uc190, \uac08\ub9dd, \uc601\ub9ac\ud568** \uc774 \uc138 \uac00\uc9c0 \uc790\uc9c8\uc744 \ubca4 \ub2e4\uc774\uc5b4\uadf8\ub7a8\uc73c\ub85c \ub418\uc5b4 \uc788\ub294 \ud615\ud0dc\uc785\ub2c8\ub2e4. \uc800 \ub2e8\uc5b4\ub4e4\uc744 \ud558\ub098\ud558\ub098 \ub2e8\uc21c\ud558\uac8c \ud574\uc11d\ud558\uba74 \uc548 \ub41c\ub2e4\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4. \uc138\ubd80\uc801\uc778 \uac1c\ub150\ub4e4\uc774 \uc228\uaca8\uc838 \uc788\uc73c\ubbc0\ub85c \uc774 \ucc45\uc758 \ub4b7\ubd80\ubd84\uc778 2\ubd80\uc5d0 \uc774\ub860\uc744 \uc77d\uc5b4\ubcf4\uc2dc\uace0 \uc790\uc2e0\uacfc \ube57\ub300\uc5b4 \uc131\ucc30\ud574\ubcf4\uc2dc\uba74 \ub3c4\uc6c0\uc774 \ub420 \uac83 \uac19\uc2b5\ub2c8\ub2e4. \uc774 \uc790\uc9c8\ub4e4\uc740 \ud300\uc6d0, \ub9ac\ub354 \ubaa8\ub450\uc5d0\uac8c \ub3c4\uc6c0\uc774 \ub420 \uac70\ub77c\uace0 \uc0dd\uac01\ub429\ub2c8\ub2e4.\\n\\n**\uc774 \uc138 \uac00\uc9c0\uc758 \uc790\uc9c8\uc774 \ubc18\ub4dc\uc2dc \ud575\uc2ec\uac00\uce58\uc5ec\uc57c \ud558\ub294\uac00?**\ub77c\ub294 \uc9c8\ubb38\uc744 \ud55c\ub2e4\uba74 \ud575\uc2ec\uac00\uce58\uc77c \ud544\uc694\ub294 \uc5c6\ub2e4\uace0 \ub9d0\ud574\uc8fc\uace0 \uc2f6\uc2b5\ub2c8\ub2e4. \uac01 \uae30\uc5c5\ub9c8\ub2e4 \uc0dd\uac01\uc774 \ub2e4\ub97c \uac83\uc774\uace0 \ud300\ub9c8\ub2e4 \uc0dd\uac01\uc774 \ub2e4\ub97c \uac83\uc774\ub2c8\uae4c\uc694. \uadf8\ub807\uc9c0\ub9cc \ud300\uc6cc\ud06c\uac00 \ud544\uc694\ud558\uace0, \ud300\uc73c\ub85c\uc368 \uc870\uc9c1\uc73c\ub85c\uc368 \uc131\uc7a5\uc744 \uc6d0\ud55c\ub2e4\uba74 \uc544\ub2c8, \uc880 \ub354 \ubc14\ub77c\ubd10\uc11c \uc131\uacf5\uae4c\uc9c0 \ubc14\ub780\ub2e4\uba74 \uc774\ub7f0 \ub355\ubaa9\uc774 \ud544\uc694\ud558\uc9c0 \uc54a\uc744\uae4c \uc2f6\uc2b5\ub2c8\ub2e4. \\n\\n2\ubd80 \uc774\ub860 \ubd80\ubd84\uc5d0\uc11c\ub294 \ub355\ubaa9\uc744 \uac00\uc9c4 \uc0ac\ub78c\ub4e4\uc5d0 \ub300\ud574 \uc124\uba85\ub3c4 \ud574\uc90d\ub2c8\ub2e4. \ub355\ubaa9\uc744 \uac00\uc9c4 \ubaa8\ub4e0 \uacbd\uc6b0\uc758 \uc218\uc5d0 \ub300\ud574 \uc124\uba85\ud558\ub2c8 \ub3c4\uc6c0\uc774 \ub9ce\uc774 \ub420 \uac83\uc774\ub77c\uace0 \uc0dd\uac01\ub429\ub2c8\ub2e4. \uc751\uc6a9\ub3c4 \uc788\uc2b5\ub2c8\ub2e4. \uc2e0\uaddc \uc9c1\uc6d0\uc744 \ucc44\uc6a9\ud560 \ub54c\uc758 \uc9c8\ubb38, \uae30\uc874 \uc9c1\uc6d0 \ud3c9\uac00\ud560 \ub54c\uc758 \uc9c8\ubb38, \ud558\ub098 \uc774\uc0c1\uc758 \ub355\ubaa9\uc774 \ubd80\uc871\ud55c \uc9c1\uc6d0\uc774 \uc788\uc744 \ub54c\ub294 \uc5b4\ub5bb\uac8c \uc0dd\uac01\ud558\uace0 \uac1c\ubc1c\ud574\uc57c\ub418\ub294\uac00 \ub4f1\uc744 \uc790\uc138\ud788 \uc124\uba85\ud574\uc90d\ub2c8\ub2e4. \uc774 \ubaa8\ub378\uc744 \ub3c4\uc785\ud558\uace0 \uc2f6\uc740 \ubd84\uc774\ub77c\uba74 \uc0c1\ub2f9\ud788 \ub3c4\uc6c0\uc774 \ub420 \uac83\uc785\ub2c8\ub2e4.\\n\\n### \uc2a4\ud1a0\ub9ac\ud154\ub9c1\\n\\n\uc774 \ucc45\uc758 \ud2b9\uc9d5 \uc911 \ud558\ub098\ub77c\uace0 \ub9d0\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \ube44\ubb38\ud559\uc73c\ub85c \uc4f0\uc778 \ucc45\uc774 \uc544\ub2c8\ub77c \uc2a4\ud1a0\ub9ac\uac00 \uc788\ub294 \ucc45\uc774\uba70 \uc2a4\ud1a0\ub9ac\ub97c \ud1b5\ud574 \uc774 \ucc45\uc5d0\uc11c \uc81c\uc2dc\ud558\ub294 \ubaa8\ub378\uc744 \uc124\uba85\ud558\uace0 \uc788\uc2b5\ub2c8\ub2e4. \ub2e8\uc21c\ud788 \ube44\ubb38\ud559\uc774 \uc544\ub2c8\uae30\uc5d0 \uc77d\uc73c\uba74\uc11c \uc0c1\ub2f9\ud788 \uc7ac\ubc0c\ub2e4\uace0 \ub290\uaf08\uc2b5\ub2c8\ub2e4. \uc2a4\ud1a0\ub9ac\uc640 \ucc45\uc5d0\uc11c \ub098\uc624\ub294 \uc8fc\uc778\uacf5\ub4e4\uc774 \uc788\uae30\uc5d0 \uadf8\ub4e4\uc758 \uad00\uacc4\ub97c \ubcf4\uba74\uc11c \ubaa8\ub378\uc744 \uc880 \ub354 \uc27d\uac8c \uc774\ud574\ud560 \uc218 \uc788\uc5c8\uc2b5\ub2c8\ub2e4.\\n\\n\uac04\ub2e8\ud558\uac8c \uc124\uba85\ud574\ubcf4\uba74 \'\uc81c\ud504\'\ub77c\ub294 \uc8fc\uc778\uacf5\uc774 \uc0bc\ucd0c\uc778 \'\ubc25\'\uc758 \uac74\uc124\uc5c5\uacc4\ub97c \uc5b4\uca54 \uc218 \uc5c6\uc774 \uacbd\uc601\ud558\uac8c \ub418\ub294 \uc2a4\ud1a0\ub9ac\uc785\ub2c8\ub2e4. \uae30\uc5c5\uc5d0 \ud604\uc7ac \uc0c1\ud669, \ubb38\uc81c\ub97c \ud30c\uc545\ud558\uace0 \ud544\uc694\ud55c \uc778\uc7ac\ub97c \ucc44\uc6a9\ud558\uac8c \ub418\uc8e0. \uc774 \uacfc\uc815 \uc18d\uc5d0\uc11c \uc5b4\ub5bb\uac8c \uc704\uc758 \ubaa8\ub378\ub85c \ud3c9\uac00\ud558\uace0 \ubaa8\ub378\uc774 \uc5b4\ub5a4 \uac1c\ub150\uc778\uc9c0 \uc54c \uc218 \uc788\uc2b5\ub2c8\ub2e4. \ub354 \uc790\uc138\ud55c \ub0b4\uc6a9\uc774 \uad81\uae08\ud558\uc2dc\uba74 \ucc45\uc744 \uc77d\uc5b4\ubcf4\uc2dc\uae30 \ubc14\ub78d\ub2c8\ub2e4. \\n\\n## \ub300\uc0c1\ub3c5\uc790\\n\\n\uc704\uc5d0\uc11c \ub300\ub7b5\uc801\uc73c\ub85c\ub294 \ub2e4 \uc124\uba85\ud55c \uac70 \uac19\uc740\ub370 \ub9c8\uc9c0\ub9c9\uc73c\ub85c \uc815\ub9ac\ud574\ubcf4\uaca0\uc2b5\ub2c8\ub2e4. \uac1c\uc778\uc801\uc778 \uc0dd\uac01\uc73c\ub85c \uc774 \ucc45\uc744 \uc77d\uc73c\uba74\uc11c \'\uc544 \ub098\ub294 \uc774\uac78 \ud575\uc2ec\uac00\uce58\ub85c \uc0bc\uace0 \uc870\uc9c1\uc5d0 \uaf2d \ub3c4\uc785\ud574\uc57c\uaca0\ub2e4!\' \uc774\ub7f0 \uc0dd\uac01\uc740 \uc548 \ud558\uc168\uc73c\uba74 \ud569\ub2c8\ub2e4. \uc88b\uc740 \uac00\uce58\ub4e4\uc774\uae34 \ud558\uc9c0\ub9cc \uc5b5\uc9c0\ub85c \ub3c4\uc785\ud560 \ud544\uc694\ub294 \uc5c6\uc744 \uac70\ub77c\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4. \ud300\uc6d0\ub4e4\uacfc \ub9ce\uc774 \uc0c1\uc758\ud558\uace0 \ud1a0\ub860\ud574\ubcf8 \ud6c4\uc5d0 \ubaa8\ub450\uac00 \ub3d9\uc758\ud55c\ub2e4\uba74 \uadf8 \ub54c \ub3c4\uc785\ud558\ub294 \uac83\uc740 \uad1c\ucc2e\uaca0\uc8e0. \ub2f9\uc5f0\ud788 \uc774\ub807\uac8c \uc0dd\uac01\ud558\uc2dc\uae38 \ubc14\ub77c\uaca0\uc2b5\ub2c8\ub2e4.\\n\\n\uc5b4\uca0c\ub4e0 \uc774\uac8c \uc911\uc694\ud55c \uac8c \uc544\ub2c8\uace0 \uc804 \uc774 \ucc45\uc744 **\uc870\uc9c1 \ubb38\ud654\ub4e4\uc744 \uac04\uc811\uc801\uc73c\ub85c \uacbd\ud5d8\ud574\ubcf4\uace0 \uc2f6\uc740 \uc0ac\ub78c, \uadf8\ub9ac\uace0 \uc774\ub4e4\uc740 \uc5b4\ub5a4 \uc0ac\ub78c\uc744 \ucc44\uc6a9\ud558\uae38 \uc6d0\ud558\ub294\uac00\uc5d0 \ub300\ud574 \uad81\uae08\ud55c \uc0ac\ub78c**\uc774 \uc77d\uc5b4\ubcf4\uc168\uc73c\uba74 \ud569\ub2c8\ub2e4. \uae30\uc5c5\ub4e4\uc758 \uc778\uc7ac\uc0c1\uc740 \ubaa8\ub450 \ub2e4\ub985\ub2c8\ub2e4. \uc5b4\ub5a4 \uae30\uc5c5\uc740 \uc778\uc7ac\uc0c1\uc744 \uc0ac\uc774\ud2b8\uc5d0 \uc62c\ub824\ub193\uae30\ub3c4 \ud569\ub2c8\ub2e4\ub9cc, \uc790\uc2e0\uc774 \uc9c1\uc811 \ucd94\ub9ac\ub97c \ud574\uc57c\ud558\ub294 \uacf3\ub4e4\uc774 \ub300\ubd80\ubd84\uc77c \uac83\uc774\ub77c\uace0 \uc608\uc0c1\ub429\ub2c8\ub2e4. \uc804 \ucd94\ub9ac\ub97c \ud574\ubcf4\uae30 \uc704\ud574\uc11c\ub294 \ub370\uc774\ud130\uac00 \ub9ce\uc774 \ud544\uc694\ud558\ub2e4\uace0 \uc0dd\uac01\ud558\uba70, \uc774 \ucc45\uc740 \uadf8\ub7f0 \ub370\uc774\ud130\ub97c \uc313\uae30 \uc704\ud574\uc11c \uc77d\uc5b4\ubcf4\uba74 \uc7ac\ubc0c\uc744 \ucc45\uc774\ub77c\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4. \\n\\n\ub610\ud55c, \uae30\uc5c5 \ubb38\ud654, \uc870\uc9c1 \ubb38\ud654\uc5d0 \uad00\uc2ec\uc774 \ub9ce\uc73c\uc2e0 \ubd84\ub4e4\uaed8\ub3c4 \ucd94\ucc9c\ub4dc\ub9bd\ub2c8\ub2e4. \uc77d\uc5b4\uc11c \uc2dc\uac04 \ub0ad\ube44\ud558\uace0 \ud6c4\ud68c\ub418\ub294 \ucc45\uc740 \uc808\ub300 \uc544\ub2c8\ub77c\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4."},{"id":"/ml-with-web-optimization","metadata":{"permalink":"/en/blog/ml-with-web-optimization","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2021-07-22-ml-with-web-optimization/ml-with-web-optimization.md","source":"@site/blog/2021-07-22-ml-with-web-optimization/ml-with-web-optimization.md","title":"\ud83d\udcd6 \uba38\uc2e0\ub7ec\ub2dd\uc744 \ud65c\uc6a9\ud55c \uc6f9 \ucd5c\uc801\ud654","description":"A/B \ud14c\uc2a4\ud2b8, \uba54\ud0c0\ud734\ub9ac\uc2a4\ud2f1, \uc2ac\ub86f\uba38\uc2e0 \uc54c\uace0\ub9ac\uc998\uc5d0\uc11c \ubca0\uc774\uc988 \ucd5c\uc801\ud654\uae4c\uc9c0","date":"2021-07-22T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \uba38\uc2e0\ub7ec\ub2dd\uc744 \ud65c\uc6a9\ud55c \uc6f9 \ucd5c\uc801\ud654","date":"2021-07-22","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/ml-with-web-optimization","description":"A/B \ud14c\uc2a4\ud2b8, \uba54\ud0c0\ud734\ub9ac\uc2a4\ud2f1, \uc2ac\ub86f\uba38\uc2e0 \uc54c\uace0\ub9ac\uc998\uc5d0\uc11c \ubca0\uc774\uc988 \ucd5c\uc801\ud654\uae4c\uc9c0","keywords":["\ud655\ub960","\ud1b5\uacc4","A/B \ud14c\uc2a4\ud2b8"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \ucd5c\uace0\uc758 \ud300\uc740 \uc65c \uae30\ubcf8\uc5d0 \ucda9\uc2e4\ud55c\uac00","permalink":"/en/blog/the-ideal-team-player"},"nextItem":{"title":"\ud83d\udcd6 \ubc11\ubc14\ub2e5\ubd80\ud130 \uc2dc\uc791\ud558\ub294 \ub525\ub7ec\ub2dd","permalink":"/en/blog/deep-learning-from-scratch"}},"content":":::info\\n\ud55c\ube5b\ubbf8\ub514\uc5b4 \\\\<\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4\\\\> \ud65c\ub3d9\uc744 \uc704\ud574\uc11c \ucc45\uc744 \uc81c\uacf5\ubc1b\uc544 \uc791\uc131\ub41c \uc11c\ud3c9\uc785\ub2c8\ub2e4.\\n:::\\n\\n![\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4 2021](../assets/I-am-reviewer-2021.jpg)\\n\\n## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/ml-with-web-optimization.jpg)](http://www.kyobobook.co.kr/product/detailViewKor.laf?ejkGb=KOR&mallGb=KOR&barcode=9791162244357&orderClick=LAG&Kc=)\\n\\n- \uc81c\ubaa9: \uba38\uc2e0\ub7ec\ub2dd\uc744 \ud65c\uc6a9\ud55c \uc6f9 \ucd5c\uc801\ud654\\n- \uc800\uc790: Shuhei Iitsuka(\uc774\uc4f0\uce74 \uc288\ud5e4\uc774)\\n- \uc5ed\uc790: \uae40\uc5f0\uc218\\n- \ucd9c\ud310\uc0ac: \ud55c\ube5b\ubbf8\ub514\uc5b4\\n- \ucd9c\uac04: 2021-06-07\\n\\n{/* truncate */}\\n\\n## Intro\\n\\n\uba38\uc2e0\ub7ec\ub2dd\uc5d0 `\ud655\ub960\uacfc \ud1b5\uacc4`\ub294 \uaf2d \ud544\uc694\ud55c \uc218\ud559 \uc911 \ud558\ub098\ub77c\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4. \uadf8\ub807\uae30\uc5d0 \uba38\uc2e0\ub7ec\ub2dd \ubd84\uc57c\ub97c \uc880 \ub354 \uc790\uc138\ud558\uac8c \uacf5\ubd80\ud558\uae30 \uc704\ud574\uc11c \ub300\ud559\uc5d0\uc11c\ub3c4 `\ud655\ub960\uacfc \ud1b5\uacc4` \uc218\uc5c5\uc744 \ub4e3\uae30\ub3c4 \ud588\uace0 \uac1c\uc778\uc801\uc73c\ub85c \ucc45\uc744 \ubcf4\uba70 \ub530\ub85c \uacf5\ubd80\ud558\uae30\ub3c4 \ud588\uc5c8\uc2b5\ub2c8\ub2e4. \uadf8\ub807\uae30\uc5d0 \uc774 \ucc45\uc744 \uc77d\uae30\uc5d0\ub294 \ucda9\ubd84\ud788 \uc77d\uc744 \uc218 \uc788\ub2e4\uace0 \uc0dd\uac01\ud588\uc2b5\ub2c8\ub2e4.\\n\\n\ub610\ud55c \uc774 \ucc45\uc758 \ubd80\uc81c\uc5d0\ub294 `A/B \ud14c\uc2a4\ud2b8`\ub77c\ub294 \ud0a4\uc6cc\ub4dc\uac00 \uc788\uc2b5\ub2c8\ub2e4. \ucd5c\uadfc\uc5d0 \uc800\ub294 \uc6f9 \ubc31\uc5d4\ub4dc\uc640 \ub370\uc774\ud130 \ubd84\uc11d \ucabd\uc744 \uacf5\ubd80\ud558\uace0 \uc788\uae30\uc5d0 A/B \ud14c\uc2a4\ud2b8\uc5d0 \ub300\ud574 \ub354 \uc138\ubd80\uc801\uc73c\ub85c \uacf5\ubd80\ud574\ubcf4\uace0 \uc2f6\uc5c8\uc2b5\ub2c8\ub2e4. \ub610\ud55c, \uc774 \ucc45\uc740 \uc81c\uac00 \uba38\uc2e0\ub7ec\ub2dd\uc744 \ubc30\uc6b0\uba74\uc11c \uacf5\ubd80\ud588\ub358 \ub0b4\uc6a9\ub4e4\uc744 \uc6f9 \ucd5c\uc801\ud654\uc5d0 \ub3c4\uc785\ud574\ubcfc \uc218 \uc788\ub2e4\ub294 \uc810\uc774 \uc800\uc5d0\uac8c \ub108\ubb34 \ub9e4\ub825\uc801\uc73c\ub85c \ub2e4\uac00\uc654\uae30\uc5d0 \uaf2d \uc77d\uc5b4\ubcf4\uace0 \uc2f6\uc5c8\uc2b5\ub2c8\ub2e4.\\n\\n## Book Review\\n\\n### \uc6f9 \ucd5c\uc801\ud654\\n\\n\uc800\ub294 \uba38\uc2e0\ub7ec\ub2dd\uc744 \uacf5\ubd80\ud558\uba74\uc11c \ub2e4\uc591\ud55c \uae30\ucd08 \uc9c0\uc2dd\ub4e4\uc744 \uacf5\ubd80\ud588\uc5c8\uc2b5\ub2c8\ub2e4. \uc774 \ucc45\uc740 \uae30\ucd08\uc801\uc778 \ud655\ub960\uacfc \ud1b5\uacc4 \uc9c0\uc2dd, \ucd5c\uc801\ud654 \uc774\ub860 \ub4f1\uc758 \ub0b4\uc6a9\ub4e4\uc744 \uc5b4\ub514\uc5d0 \uc0ac\uc6a9\ud560 \uc218 \uc788\ub294\uc9c0 \uc54c\ub824\uc90d\ub2c8\ub2e4. \uc9c0\uae08\uae4c\uc9c0 \uc628\uac16 \ucc45, \uac15\uc758 \ub4f1\uc5d0\uc11c \uacf5\ubd80\ud588\ub358 \ub0b4\uc6a9\ub4e4\uc744 \uc751\uc6a9\ud558\ub294 \ucc45\uc785\ub2c8\ub2e4. \uc774\ub860\uc801\uc778 \ub0b4\uc6a9\ubcf4\ub2e4\ub294 `\uc6f9 \ucd5c\uc801\ud654\ub77c\ub294 \uac83\uc744 \uc5b4\ub5bb\uac8c \uba38\uc2e0\ub7ec\ub2dd\uc73c\ub85c \uc801\uc6a9\ud588\uc744\uae4c`\uc5d0 \ub354 \uac00\uae5d\uc2b5\ub2c8\ub2e4. A/B \ud14c\uc2a4\ud2b8\ub97c \uc608\uc2dc\ub85c \ub4e4\uc5b4\uac00\uba74\uc11c \ubca0\uc774\uc988 \uc815\ub9ac\ub97c \ud65c\uc6a9\ud574 A\uc548\uacfc B\uc548 \uc911 \uc5b4\ub290 \uac83\uc774 \ub354 \uc88b\uc740\uac00\ub97c \uc54c\ub824\uc8fc\ub294 \ub4f1\uc758 \uc6f9 \ucd5c\uc801\ud654\uc5d0 \uad00\ud55c \uc608\uc2dc\ub4e4\uc744 \uc81c\uc2dc\ud569\ub2c8\ub2e4. \uc751\uc6a9\uc5d0\ub9cc \ub108\ubb34 \uac00\uae5d\ub2e4\uace0 \ud574\uc11c \uc774\ub860\uc801\uc778 \ub0b4\uc6a9\ub4e4\uc774 \uc5c6\ub294 \uac83\uc740 \uc804\ud600 \uc544\ub2d9\ub2c8\ub2e4. \ud544\uc218\uc801\uc778 \uc54c\uace0\ub9ac\uc998, \ud655\ub960\uacfc \ud1b5\uacc4 \uc9c0\uc2dd\ub4e4\uc740 \uc801\uc808\ud558\uac8c \uc798 \uc124\uba85\ub418\uc5b4 \uc788\uc2b5\ub2c8\ub2e4. \uac1c\uc778\uc801\uc73c\ub85c Chapter 7 \ubca0\uc774\uc988 \ucd5c\uc801\ud654\uac00 \uac00\uc7a5 \uc5b4\ub824\uc6b0\uba74\uc11c \ud765\ubbf8 \uc788\uac8c \ubd24\ub358 \ubd80\ubd84\uc774\uc5c8\uc2b5\ub2c8\ub2e4. \\n\\n### \ubd80\ub85d\\n\\n\uc774 \ucc45\uc758 \ub05d\uc744 \ubcf4\uba74 \ubd80\ub85d\uc774 \uc788\uc2b5\ub2c8\ub2e4. \ubd80\ub85d\uc5d0\uc11c\ub294 \ucc45\uc5d0\uc11c \uc124\uba85\ud588\ub358 \uacc4\uc0b0\ub4e4\uc744 \uc774\ud574\ud558\uae30 \uc704\ud574 \uaf2d \ud544\uc694\ud55c \uc5f0\uc0b0\ub4e4\uc774 \uc124\uba85\ub418\uc5b4 \uc788\uc2b5\ub2c8\ub2e4. \ud639\uc2dc\ub098 \ucc45\uc744 \uc77d\uc73c\uc2dc\uba74\uc11c \uc5b4\ub824\uc6b4 \uc810\uc774 \uc788\uc73c\uc2dc\ub2e4\uba74 \ubd80\ub85d\uc744 \ucc38\uace0\ud558\uc2dc\uba74 \ub420 \uac83 \uac19\uc2b5\ub2c8\ub2e4. \ucc45\uc744 \uc77d\uae30 \uc704\ud574 \ucd5c\uc18c\ud55c\uc758 \uc815\ubcf4\uac00 \uc788\uae30 \ub54c\ubb38\uc5d0 \ub9cc\uc57d \uc11c\uc810\uc5d0\uc11c \uc774 \ucc45\uc744 \uc0ac\uc2dc\ub294 \ubd84\ub4e4\uc774 \uc788\uc73c\uc2dc\ub2e4\uba74 \ubd80\ub85d\uc744 \uba3c\uc800 \ubcf4\uc2dc\uace0 \uc774 \uc815\ub3c4\ub294 \uc774\ud574\ud558\uc2e4 \uc218 \uc788\ub2e4\uace0 \ud310\ub2e8\uc774 \ub41c\ub2e4\uba74 \uad6c\ub9e4\ud558\uc2dc\ub294 \uac78 \ucd94\ucc9c\ub4dc\ub9bd\ub2c8\ub2e4.\\n\\n### \ucf54\ub4dc\\n\\n\ucf54\ub4dc\ub294 \uc8fc\ud53c\ud130 \ub178\ud2b8\ubd81\uc73c\ub85c \uc8fc\uc5b4\uc9d1\ub2c8\ub2e4. \uc774 \ucc45\uc5d0\uc11c\ub294 \ubaa8\ub4e0 \uc0d8\ud50c \ucf54\ub4dc\ub294 \uad6c\uae00 \ucf5c\ub7a9 \uc0c1\uc5d0\uc11c \uc2e4\ud589\ud558\ub294 \uac83\uc744 \uac00\uc815\ud558\uace0 \uc788\ub2e4\uace0 \ub418\uc5b4 \uc788\uc2b5\ub2c8\ub2e4. \uc774\ub7f0 \ubd80\ubd84\uc740 \uacf5\ubd80\ud558\ub294 \uc785\uc7a5\uc5d0\uc11c \ub108\ubb34 \ud3b8\ud558\uace0 \uc88b\uc558\uc9c0\ub9cc \uac00\uc7a5 \uc544\uc26c\uc6e0\ub358 \uc810\uc740 \uc608\uc81c \ucf54\ub4dc\uac00 \ub531 \ud55c \ud30c\uc77c\uc73c\ub85c \ub418\uc5b4 \uc788\uae30 \ub54c\ubb38\uc5d0 \ubcf4\uae30 \uc5b4\ub824\uc6e0\uc2b5\ub2c8\ub2e4. \uac01 Chapter \ubcc4\ub85c \ub098\ub258\uc5b4\uc838 \uc788\uc5c8\uc73c\uba74 \uc880 \ub354 \uc2e4\ud589\ud558\uae30 \ud3b8\ub9ac\ud588\uc744 \uac83 \uac19\uc558\ub294\ub370 \ud558\ub098\ud558\ub098 \ub2e4 \uc2e4\uc2b5\ud574\ubcf4\ub294 \ub3c5\uc790\ub85c\uc368 \ub108\ubb34 \uc544\uc26c\uc6e0\uc2b5\ub2c8\ub2e4.\\n\\n### \ub300\uc0c1 \ub3c5\uc790\\n\\n- \ud1b5\uacc4\ud559 \ub610\ub294 \uba38\uc2e0\ub7ec\ub2dd\uc5d0 \uc785\ubb38\ud558\uace0\uc790 \ud558\ub294 \uc6f9 \uc5d4\uc9c0\ub2c8\uc5b4\\n- \uc6f9 \ub9c8\ucf00\ud305 \uad00\ub828 \ub2f4\ub2f9\uc790, \uc6f9 \ub9c8\ucf00\ud130\\n- \uba38\uc2e0\ub7ec\ub2dd \uc751\uc6a9, \ud2b9\ud788 \uc0ac\ub78c\uacfc\uc758 \uc0c1\ud638 \uc791\uc6a9\uc5d0 \ub300\ud55c \uc751\uc6a9\uc5d0 \ud765\ubbf8\uac00 \uc788\ub294 \ubd84\\n\\n\uc81c\uac00 \ucd94\ucc9c\ud558\uc9c0 \uc54a\uc544\ub3c4 \uc774 \ucc45\uc5d0 \ub300\uc0c1 \ub3c5\uc790\uac00 \uc704\ucc98\ub7fc \uae30\uc220\ub418\uc5b4 \uc788\uc2b5\ub2c8\ub2e4. \uadfc\ub370 \uc194\uc9c1\ud788 \uc774 \ucc45\uc774 \uc26c\uc6b4 \ucc45\uc774 \uc544\ub2c8\ub77c \ucd5c\uc18c \ud1b5\uacc4\uc801\uc778 \uc9c0\uc2dd\uc774 \uc5b4\ub290\uc815\ub3c4 \uc788\uc73c\uc2e0 \ubd84\uc774 \uc77d\uc73c\uc168\uc73c\uba74 \uc88b\uaca0\uc2b5\ub2c8\ub2e4. \uc2ac\ub86f\uba38\uc2e0 \uc54c\uace0\ub9ac\uc998, \ubca0\uc774\uc988 \uc815\ub9ac, \ud68c\uadc0 \ub4f1 \uaf64 \uc5b4\ub824\uc6b4 \ub0b4\uc6a9\ub4e4\uc774 \ub9ce\uae30\uc5d0 \uba38\uc2e0\ub7ec\ub2dd\ub3c4 \uba3c\uc800 \uacf5\ubd80\ud574\ubcf8 \ubd84\ub4e4\uc5d0\uac8c \ucd94\ucc9c\ub4dc\ub9bd\ub2c8\ub2e4. \ucc45 \uc81c\ubaa9\ubd80\ud130 \uba38\uc2e0\ub7ec\ub2dd\uc744 `\ud65c\uc6a9\ud55c` \uc6f9 \ucd5c\uc801\ud654\uc785\ub2c8\ub2e4. \uaf2d \uc0ac\uc804 \uc9c0\uc2dd\uc774 \uc788\uc73c\uc2e0 \ubd84\ub4e4\uc774 \uc77d\uc73c\uc2dc\uae38 \ubc14\ub77c\uaca0\uc2b5\ub2c8\ub2e4."},{"id":"/deep-learning-from-scratch","metadata":{"permalink":"/en/blog/deep-learning-from-scratch","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2021-07-19-deep-learning-from-scratch/deep-learning-from-scratch.md","source":"@site/blog/2021-07-19-deep-learning-from-scratch/deep-learning-from-scratch.md","title":"\ud83d\udcd6 \ubc11\ubc14\ub2e5\ubd80\ud130 \uc2dc\uc791\ud558\ub294 \ub525\ub7ec\ub2dd","description":"\ud30c\uc774\uc36c\uc73c\ub85c \uc775\ud788\ub294 \ub525\ub7ec\ub2dd \uc774\ub860\uacfc \uad6c\ud604","date":"2021-07-19T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \ubc11\ubc14\ub2e5\ubd80\ud130 \uc2dc\uc791\ud558\ub294 \ub525\ub7ec\ub2dd","date":"2021-07-19","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/deep-learning-from-scratch","description":"\ud30c\uc774\uc36c\uc73c\ub85c \uc775\ud788\ub294 \ub525\ub7ec\ub2dd \uc774\ub860\uacfc \uad6c\ud604","keywords":["\ub525\ub7ec\ub2dd","\uc2e0\uacbd\ub9dd","CNN"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \uba38\uc2e0\ub7ec\ub2dd\uc744 \ud65c\uc6a9\ud55c \uc6f9 \ucd5c\uc801\ud654","permalink":"/en/blog/ml-with-web-optimization"},"nextItem":{"title":"\ud83d\udcd6 \uc18c\ubb38\ub09c \uba85\uac15\uc758 \uae40\uc0c1\ud615\uc758 SQL \uc815\ubcf5","permalink":"/en/blog/conquest-sql"}},"content":"## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/deep-learning-from-scratch.jpg)](http://www.kyobobook.co.kr/product/detailViewKor.laf?ejkGb=KOR&mallGb=KOR&barcode=9788968484636&orderClick=LEa&Kc=)\\n\\n- \uc81c\ubaa9: \ubc11\ubc14\ub2e5\ubd99\ud130 \uc2dc\uc791\ud558\ub294 \ub525\ub7ec\ub2dd\\n- \uc800\uc790: Koki Saitoh(\uc0ac\uc774\ud1a0 \uace0\ud0a4)\\n- \uc5ed\uc790: \uac1c\uc55e\ub9f5\uc2dc\\n- \ucd9c\ud310\uc0ac: \ud55c\ube5b\ubbf8\ub514\uc5b4\\n- \ucd9c\uac04: 2017-01-03\\n\\n{/* truncate */}\\n\\n## Intro\\n\\n\ubc8c\uc368 \uc774 \ucc45\uc774 \ucd9c\uac04\ud55c \uc9c0 4\ub144\uc774 \ub410\ub2e4. \ub300\ud559\uc5d0 \uc785\ud559\ud558\uace0 \ud30c\uc774\uc36c\uc744 \ucc98\uc74c \uacf5\ubd80\ud574\ubcf4\uae30 \uc2dc\uc791\ud560 \ub54c \uc774 \ucc45\uc744 \uc54c\uac8c \ub410\uc5c8\ub2e4. \ud55c\ucc3d \ub525\ub7ec\ub2dd\uc774 \ub728\uace0 \uc788\uc5c8\uace0, \ub0b4\uac00 \uc54c\ud30c\uace0 \uac19\uc740 \uc778\uacf5\uc9c0\ub2a5\uc744 \ub9cc\ub4e4 \uc218\ub3c4 \uc788\ub2e4\ub294 \uc0ac\uc2e4\uc5d0 \uc778\uacf5\uc9c0\ub2a5\uc744 \uacf5\ubd80\ud574\ubcf4\uace0 \uc2f6\uc5c8\ub2e4. \uadf8\ub7ec\ub098 \ud559\uad50 \uc218\uc5c5\uc744 \ub530\ub77c\uac00\uba74\uc11c \uacfc \uc0dd\ud65c\uc744 \ud558\uae30\uc5d0 \ubc14\ube74\uace0, \uc120\ud615\ub300\uc218\ud559\ub3c4 \uacf5\ubd80\ud574\ubcf8 \uc801\uc774 \uc5c6\uc5c8\uae30\uc5d0 \uc77d\ub294 \uac83\uc744 \ud3ec\uae30\ud588\uc5c8\ub2e4. \uc2dc\uac04\uc774 \uc9c0\ub098\uace0 3\ud559\ub144(\uc0ac\ub9dd\ub144)\uc774 \ub05d\ub09c \uc774\ud6c4 \ub4dc\ub514\uc5b4 \uc5ec\uc720\uac00 \uc0dd\uaca8 \uc77d\uac8c \ub410\ub2e4.\\n\\n## Book Review\\n\\n### \ub525\ub7ec\ub2dd \uc785\ubb38\ud558\uae30 \uc88b\uc740 \ucc45\\n\\n\ucc45\uc744 \ud310\ub9e4\ud558\ub294 \uac01\uc885 \uc628\ub77c\uc778 \uc11c\uc810 \uc0ac\uc774\ud2b8\ub97c \ub4e4\uc5b4\uac00\uc11c \ucef4\ud4e8\ud130 \uc11c\uc801 \ubca0\uc2a4\ud2b8\uc140\ub7ec \ub7ad\ud0b9\uc744 \ubcf4\uba74 \uc774 \ucc45\uc740 \uaf2d \ud55c \uc790\ub9ac\ub97c \ucc28\uc9c0\ud558\uace0 \uc788\ub2e4. 4\ub144\uc774\ub098 \ub41c \ucc45\uc774 \uc544\uc9c1\ub3c4 \uc788\ub2e4\ub294 \uac83\uc740 \uadf8\ub9cc\ud07c \uc124\uba85\uc774 \uc798 \ub418\uc5b4 \uc788\uace0, \uc88b\uc740 \ucc45\uc774\ub77c\uace0 \uc778\uc815\ubc1b\uc740 \uac83\uc774 \uc544\ub2d0\uae4c. \ub525\ub7ec\ub2dd\uc774 \ub728\uae30 \uc2dc\uc791\ud558\uace0 \uaf64 \uc2dc\uac04\uc774 \uc9c0\ub09c \uc9c0\uae08, \ub525\ub7ec\ub2dd\uc744 \uc8fc\uc81c\ub85c \ud558\ub294 \ucc45\uc740 \ub9e4\uc6b0 \ub9ce\uc774 \ucd9c\uac04\ub410\ub2e4. \uadf8\ub7ec\ub098 \ub098\ub294 \uc544\uc9c1\ub3c4 \uadf8 \ub9ce\uc740 \ucc45 \uc911 \uc774 \ucc45\ub9cc\ud07c \ub525\ub7ec\ub2dd \uc785\ubb38\uc5d0 \uc88b\uc740 \ucc45\uc740 \uc5c6\ub2e4\uace0 \ub290\ub080\ub2e4.\\n\\n\ub610\ud55c, \ub2e4\ub978 \ud3c9\ubc94\ud55c \ub525\ub7ec\ub2dd \ucc45\uacfc \ub2e4\ub974\uac8c \ub3cb\ubcf4\uc774\ub294 \uc810\ub3c4 \uc788\ub2e4. TensorFlow, PyTorch \uac19\uc740 \ub77c\uc774\ube0c\ub7ec\ub9ac\ub97c \uc0ac\uc6a9\ud558\uc9c0 \uc54a\uace0 NumPy\ub97c \uc0ac\uc6a9\ud558\uc5ec \ubc11\ubc14\ub2e5\ubd80\ud130 \ud30c\uc774\uc36c\uacfc \ub118\ud30c\uc774\ub85c \uad6c\ud604\uc744 \ud55c\ub2e4\ub294 \uac83. \uc81c\ubaa9 \uadf8\ub300\ub85c\ub2e4. \ud150\uc11c\ud50c\ub85c\ub098 \ud30c\uc774\ud1a0\uce58 \uac19\uc740 \ub77c\uc774\ube0c\ub7ec\ub9ac\ub97c \uc0ac\uc6a9\ud558\uba70 \ub525\ub7ec\ub2dd \uacf5\ubd80\ub97c \ud558\ub294 \uac83\uc774 \uc544\ub2cc, \ubc11\ubc14\ub2e5\ubd80\ud130 \uad6c\ud604\ud558\uc5ec \uae30\uc5b5\uc5d0 \ub354 \uc624\ub798 \ub0a8\uac8c \ud55c\ub2e4. \\n\\n\ub610 \ub2e4\ub978 \ucc45\ub4e4\uacfc \ub300\ube44\ub418\ub294 \uc810\uc740 \uc8fc\ud53c\ud130 \ub178\ud2b8\ubd81\uc744 \uc0ac\uc6a9\ud558\ub294 \uac83\uc774 \uc544\ub2cc \ud30c\uc774\uc36c \ud30c\uc77c\uc744 \ub9cc\ub4e0\ub2e4\ub294 \uc810\uc774\ub2e4. \ubb3c\ub860 \uc8fc\ud53c\ud130 \ub178\ud2b8\ubd81\uc744 \uc368\ub3c4 \ucf54\ub4dc\ub294 \ub3cc\uc544\uac04\ub2e4. \uadf8\ub7ec\ub098 \ud30c\uc774\uc36c \ud30c\uc77c \uc548\uc5d0\uc11c \uac1d\uccb4 \uc9c0\ud5a5\uc801\uc73c\ub85c \uad6c\ud604\ud574\ubcfc \uc218 \uc788\ub294 \uacbd\ud5d8\uc744 \ud560 \uc218 \uc788\uc5c8\ub2e4. \ub098\ub294 \uc774\ub7f0 \ubd80\ubd84\uc774 \ub108\ubb34 \ub9c8\uc74c\uc5d0 \ub4e4\uc5c8\ub2e4.\\n\\n### \ucc38\uace0 \ubb38\ud5cc\\n\\n\ub17c\ubb38 \uac19\uc774 \ucc38\uace0 \ubb38\ud5cc\uc774 \uae54\ub054\ud558\uac8c \ub2e4 \uc815\ub9ac\ub418\uc5b4 \uc788\ub2e4. \ub09c \uc774\uac8c \uc5c4\uccad\ub09c \uc7a5\uc810\uc774\ub77c \uc0dd\uac01\ud588\uc9c0\ub9cc \ud3b8\ud55c \uac83\ub9cc \ubc14\ub77c\ub294 \ub3c5\uc790\uc5d0\uac8c\ub294 \ubd88\ud3b8\ud560 \uc218\ub3c4 \uc788\uaca0\ub2e4\ub294 \uc0dd\uac01\uc774 \ub4e4\uc5c8\ub2e4. \ubcf4\ud1b5 \ucc45\uc5d0\uc11c \ud558\ub098\ud558\ub098 \uaf3c\uaf3c\ud788 \uc124\uba85\ud574\uc8fc\ub294 \uac83\uc744 \uc6d0\ud558\ub294 \ub3c5\uc790\ub4e4\uc774 \ub9ce\ub2e4. \uadf8\ub7ec\ub098 \uc774 \ucc45\uc740 \uadf8\ub4e4\uc5d0\uac8c\ub294 \ubd88\ud3b8\ud55c \ucc45\uc77c \uac83\uc774\ub2e4. \uc790\uc138\ud788 \uc124\uba85\ud574\uc8fc\uae34 \ud558\ub098, \uc880 \ub354 \uc2ec\ud654\uc801\uc778 \ubd80\ubd84\uc740 \uc9c1\uc811 \ucc38\uace0\ubb38\ud5cc\uc744 \ubcf4\uace0 \ucd94\uac00\ub85c \ub354 \uacf5\ubd80\ud574\uc57c \ud55c\ub2e4. \uc989, \ub17c\ubb38\uc5d0 \uc775\uc219\ud558\uc9c0 \uc54a\uc740 \uc0ac\ub78c\uc740 \uc870\uae08 \uc77d\ub294 \ub370 \uc624\ub798 \uac78\ub9b4 \uc218\ub3c4 \uc788\ub2e4. \uadf8\ub7ec\ub098 \ub098\uc5d0\uac8c\ub294 \uc774 \ucc45\uc744 \uc77d\uc740 \ud6c4\uc5d0 \ub354 \uacf5\ubd80\ud560 \uac83\uc744 \uc54c\ub824\uc8fc\ub294 \ub290\ub08c\uc774\ub77c \uc88b\uc558\ub2e4.\\n\\n### \uc55e\uc73c\ub85c\uc758 \uacf5\ubd80\\n\\nCHAPTER 8\uc5d0\uc11c\ub294 \uad6c\ud604\uacfc \uad00\ub828\ub41c \ub0b4\uc6a9\uc740 \uc5c6\uc73c\uba70 \ub525\ub7ec\ub2dd\uc5d0 \ub300\ud55c \uc804\ubc18\uc801\uc778 \uc124\uba85\uc774 \uae30\uc220\ub418\uc5b4 \uc788\ub2e4. \uc774 \ubd80\ubd84\uc5d0\ub294 \ub2e4\uc74c \uacf5\ubd80\ub97c \uc704\ud55c \ud0a4\uc6cc\ub4dc\ub97c \uc81c\uc2dc\ud558\uace0 \uc788\ub2e4. \uc774\ub7f0 \ud30c\ud2b8\uac00 \uc5c6\uc5c8\ub354\ub77c\uba74 \uc55e\uc73c\ub85c \ub525\ub7ec\ub2dd\uc744 \uc5b4\ub5bb\uac8c \ud65c\uc6a9\ud560\uc9c0, \uc5b4\ub5a4 \ubd84\uc57c\ub97c \ub354 \uc790\uc138\ud788 \uacf5\ubd80\ud560\uc9c0 \uace0\ubbfc\uc778 \ub3c5\uc790\ub4e4\uc774 \ub9ce\uc744 \uac83\uc774\ub77c \uc0dd\uac01\ub41c\ub2e4. \ub108\ubb34 \ub3c4\uc6c0\uc774 \ub418\ub294 \ubd80\ubd84\uc774\ub2e4. \ub525\ub7ec\ub2dd\uc5d0\uc11c \ud55c \ubc88\ucbe4 \ubcfc \ub4ef\ud55c \ud0a4\uc6cc\ub4dc\ub294 \ub300\ubd80\ubd84 \uc788\uc73c\ub2c8 \uaf2d \uad6c\uae00\ub9c1\uc744 \ud574\ubcf4\ub294 \uac83\uc744 \ucd94\ucc9c\ud55c\ub2e4.\\n\\n## \ub300\uc0c1 \ub3c5\uc790\\n\\n\ub525\ub7ec\ub2dd\uc5d0 \uad00\uc2ec\uc774 \uc788\ub2e4\uba74 \uaf2d \uc77d\uc5b4\ubcf4\uae38 \ucd94\ucc9c\ud55c\ub2e4. \ubc11\ubc14\ub2e5\ubd80\ud130 \uad6c\ud604\ub3c4 \uacbd\ud5d8\ud560 \uc218 \uc788\uace0, \uc804\ubc18\uc801\uc778 \ucef4\ud4e8\ud130\ube44\uc804\uc758 \uae30\ucd08 \uacf5\ubd80\uac00 \ub420 \uc218 \uc788\uc744 \uac83\uc774\ub2e4. \uc785\ubb38\uc6a9\uc73c\ub85c\ub3c4 \uaf64 \ucd94\ucc9c\ud55c\ub2e4. \uc5b8\uc81c\uae4c\uc9c0\ub098 \uc26c\uc6b4 \ucc45\ub9cc\uc744 \ubcfc \uc218 \uc5c6\uc73c\ub2c8 \uaf2d \ub3c4\uc804\ud574\ubcf4\uae38 \ubc14\ub780\ub2e4. \ubcf4\ud1b5 \uc624\ub798 \ub41c \ucc45\uc740 \uc77d\uc9c0 \uc54a\ub294 \uac83\uc744 \ucd94\ucc9c\ud558\ub098 \uc774 \ucc45\uc740 \ucd9c\uac04\ub41c \uc9c0 \uaf64 \ub410\uc5b4\ub3c4 \ub108\ubb34 \uc88b\uc740 \ucc45\uc774\uc5c8\ub2e4."},{"id":"/conquest-sql","metadata":{"permalink":"/en/blog/conquest-sql","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2021-06-20-conquest-sql/conquest-sql.md","source":"@site/blog/2021-06-20-conquest-sql/conquest-sql.md","title":"\ud83d\udcd6 \uc18c\ubb38\ub09c \uba85\uac15\uc758 \uae40\uc0c1\ud615\uc758 SQL \uc815\ubcf5","description":"\uba38\uc2e0 \ub7ec\ub2dd, \ub525\ub7ec\ub2dd \ud575\uc2ec \uc54c\uace0\ub9ac\uc998\ubd80\ud130 GAN, \uac15\ud654 \ud559\uc2b5\uae4c\uc9c0!","date":"2021-06-20T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \uc18c\ubb38\ub09c \uba85\uac15\uc758 \uae40\uc0c1\ud615\uc758 SQL \uc815\ubcf5","date":"2021-06-20","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/conquest-sql","description":"\uba38\uc2e0 \ub7ec\ub2dd, \ub525\ub7ec\ub2dd \ud575\uc2ec \uc54c\uace0\ub9ac\uc998\ubd80\ud130 GAN, \uac15\ud654 \ud559\uc2b5\uae4c\uc9c0!","keywords":["SQL","Oracle","MSSQL","MariaDB","\uc18c\ubb38\ub09c \uba85\uac15\uc758"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \ubc11\ubc14\ub2e5\ubd80\ud130 \uc2dc\uc791\ud558\ub294 \ub525\ub7ec\ub2dd","permalink":"/en/blog/deep-learning-from-scratch"},"nextItem":{"title":"\ud83d\udcd6 \uba38\uc2e0 \ub7ec\ub2dd \uad50\uacfc\uc11c with \ud30c\uc774\uc36c, \uc0ac\uc774\ud0b7\ub7f0, \ud150\uc11c\ud50c\ub85c","permalink":"/en/blog/python-machine-learning"}},"content":":::info\\n\ud55c\ube5b\ubbf8\ub514\uc5b4 \\\\<\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4\\\\> \ud65c\ub3d9\uc744 \uc704\ud574\uc11c \ucc45\uc744 \uc81c\uacf5\ubc1b\uc544 \uc791\uc131\ub41c \uc11c\ud3c9\uc785\ub2c8\ub2e4.\\n:::\\n\\n![\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4 2021](../assets/I-am-reviewer-2021.jpg)\\n\\n## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/conquest-sql.jpg)](http://www.kyobobook.co.kr/product/detailViewKor.laf?ejkGb=KOR&mallGb=KOR&barcode=9791162244241&orderClick=LAG&Kc=)\\n\\n- \uc81c\ubaa9: \uc18c\ubb38\ub09c \uba85\uac15\uc758 \uae40\uc0c1\ud615\uc758 SQL \uc815\ubcf5\\n- \uc800\uc790: \uae40\uc0c1\ud615\\n- \ucd9c\ud310\uc0ac: \ud55c\ube5b\ubbf8\ub514\uc5b4\\n- \ucd9c\uac04: 2021-05-01\\n\\n{/* truncate */}\\n\\n## Intro\\n\\n\uc774\ubc88 \ucc45\uc740 4\uc6d4 \ub2ec\uacfc \ub9c8\ucc2c\uac00\uc9c0\ub85c SQL\uc744 \uacf5\ubd80\ud558\uae30 \uc704\ud574\uc11c \uc120\ud0dd\ud55c \ucc45\uc785\ub2c8\ub2e4. \uc800\ubc88 `Learning SQL` \ucc45\uc740 MySQL\uc744 \uae30\uc900\uc73c\ub85c \uc4f0\uc5ec\uc9c4 \ucc45\uc774\uc5c8\ub2e4\uba74 \uc774\ubc88 \ucc45\uc740 Oracle \uae30\uc900\uc73c\ub85c \uc791\uc131\ub41c \ucc45\uc785\ub2c8\ub2e4. Oracle DB\ub294 \ud2b9\ud788 \ub300\uae30\uc5c5\uc5d0\uc11c \uc790\uc8fc \uc0ac\uc6a9\ub418\ub294 SQL\uc774\uc8e0. \uadf8\ub798\uc11c \ub354 \uad81\uae08\ud588\uc2b5\ub2c8\ub2e4. \ub9e4\ubc88 MySQL, PostgreSQL\ub9cc \ub2e4\ub904\ubcf4\ub358 \uc800\uc5d0\uac8c\ub294 \uc0dd\uc18c\ud55c SQL\uc774\uc5c8\uac70\ub4e0\uc694. \ub610\ud55c, MSSQL, MariaDB\uc640\uc758 \ucc28\uc774\uc810\ub3c4 \uac19\uc774 \uc124\uba85\ud574\uc8fc\ub2c8 \ub2e4\ub978 SQL\ub3c4 \uc785\ubb38\ud558\uae30\uc5d0 \uc88b\ub2e4\uace0 \uc0dd\uac01\ud588\uc2b5\ub2c8\ub2e4. \\n\\n## Book Review\\n\\n### \uc800\uc790\\n\\n\uc774 \ucc45\uc758 \uc800\uc790\ub294 [SoEn](http://www.soen.kr/)\uc774\ub77c\ub294 \uc0ac\uc774\ud2b8\ub97c \uc6b4\uc601\ud558\uc2dc\ub294 \ubd84\uc785\ub2c8\ub2e4.(\uc0ac\uc774\ud2b8\uac00 \uc5f0\uacb0\uc774 \uc548 \ub418\ub124\uc694. `soen.kr`\uc785\ub2c8\ub2e4.) C/C++ \ub610\ub294 Windows API\ub97c \uacf5\ubd80\ud574\ubcf4\uc2e0 \uc801\uc774 \uc788\uc73c\uc2dc\ub2e4\uba74 \uc544\uc2e4 \uac70\ub77c\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4. \uc608\uc804\uc5d0 [Windows API \uc815\ubcf5](http://www.kyobobook.co.kr/product/detailViewKor.laf?ejkGb=KOR&mallGb=KOR&barcode=9788979144215&orderClick=LAG&Kc=)\uc774\ub77c\ub294 \ucc45\ub3c4 \uc4f0\uc168\ub358 \ubd84\uc774\uc8e0. \uadf8\ub807\uae30\uc5d0 \uc774\ubc88 \ucc45\uc744 \uc77d\uc73c\uba74\uc11c \uc124\uba85\ub4e4\uc774 \uce5c\uadfc\ud558\ub2e4\uace0 \ub290\uaef4\uc84c\uc2b5\ub2c8\ub2e4. \\n\\n### \ub2e4\uc591\ud55c \uc885\ub958\uc758 SQL\\n\\nOracle DB\ub294 \ub300\ubd80\ubd84 \ub300\uae30\uc5c5\uc5d0\uc11c \uc0ac\uc6a9\ud558\ub294 \ub370\uc774\ud130\ubca0\uc774\uc2a4\uc785\ub2c8\ub2e4. \uadf8\ub798\uc11c \uadf8\ub7f0\uc9c0\ub294 \uc798 \ubaa8\ub974\uaca0\uc9c0\ub9cc, \uc774 \ucc45\uc740 \ucffc\ub9ac\ub9cc\uc744 \uc124\uba85\ud558\ub294 \uac83\uc774 \uc544\ub2c8\ub77c UI\uac00 \uc774\ub8e8\uc5b4\uc9c4 \uc2e4\uc2b5\ud658\uacbd\uc744 \uc0ac\uc6a9\ud558\uc5ec SQL\uc744 \uc2e4\uc2b5\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \ub610\ud55c, Oracle, MSSQL, MariaDB\uc758 3\uac00\uc9c0 \uc2e4\uc2b5\ud658\uacbd\uc744 \uad6c\ucd95\ud574\ubcf4\uba74\uc11c \ub2e4\uc591\ud55c \uc885\ub958\uc758 SQL\uc744 \uacbd\ud5d8\ud574\ubcfc \uc218 \uc788\uc5c8\uc2b5\ub2c8\ub2e4. \uc6d0\ub798\ub77c\uba74 \uac01\uac01\uc758 SQL\ub9c8\ub2e4 \ucc45 \ud55c \uad8c\uc529 \ubcf4\uba74\uc11c \uacf5\ubd80\ub97c \ud574\uc57c\ud558\ub294\ub370 \uc774\ub807\uac8c \ucc28\uc774\uc810\ub9cc \uc54c\ub824\uc8fc\ub2c8 \uacf5\ubd80\ud558\ub294 \uc785\uc7a5\uc5d0\uc11c\ub294 \ub108\ubb34 \ud3b8\ub9ac\ud588\uc5c8\uc2b5\ub2c8\ub2e4. \\n\\n### \uac1c\uc778\uc801\uc73c\ub85c \uc544\uc26c\uc6b4 \uc810\\n\\n\uc870\uae08 \uc544\uc26c\uc6e0\ub358 \uc810\uc740 PostgreSQL\uc5d0 \ub300\ud574\uc11c\ub294 \uac70\uc758 \uc5b8\uae09 \uc218\uc900\uc73c\ub85c\ub9cc \ub098\uc654\ub2e4\ub294 \uac83\uc785\ub2c8\ub2e4. \ucd5c\uadfc \uc2a4\ud0c0\ud2b8\uc5c5\uc5d0\uc11c\ub294 PostgreSQL\uc758 \uc0ac\uc6a9\uc774 \uc810\ucc28 \ub298\uc5b4\ub098\uace0 \uc788\ub2e4\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4. \uadf8\ub807\uae30\uc5d0 `MariaDB \ub300\uc2e0 PostgreSQL\uc774 \uc124\uba85\ub418\uc5c8\ub2e4\uba74 \ub354 \uc88b\uc9c0 \uc54a\uc558\uc744\uae4c?`\ub77c\ub294 \uc544\uc26c\uc6c0\uc744 \ub0a8\uc2b5\ub2c8\ub2e4.\\n\\n## \ub300\uc0c1 \ub3c5\uc790\\n\\nSQL\uc744 \uc815\uc11d\uc73c\ub85c \uacf5\ubd80\ud574\ubcf4\uc2dc\uace0 \uc2f6\uc740 \ubd84 \ub610\ub294 \ub2e4\uc591\ud55c \uc885\ub958\uc758 SQL\uc744 \uacbd\ud5d8\ud574\ubcf4\uc2dc\uace0 \uc2f6\uc73c\uc2e0 \ubd84\uaed8 \ucd94\ucc9c\ub4dc\ub9bd\ub2c8\ub2e4. \ub610\ud55c, \ub300\uae30\uc5c5\uc5d0\uc11c\ub9cc \uc8fc\ub85c \uc0ac\uc6a9\ub418\ub294 Oracle DB\uc5d0 \ub300\ud574 \uad81\uae08\ud558\uc168\ub358 \ubd84 \uadf8\ub9ac\uace0 \uc0ac\uc6a9\ud574\ubcf4\uc2dc\uace0 \uc2f6\uc740 \ubd84\ub4e4\uaed8 \ucd94\ucc9c\ub4dc\ub9bd\ub2c8\ub2e4."},{"id":"/python-machine-learning","metadata":{"permalink":"/en/blog/python-machine-learning","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2021-06-10-python-machine-learning/python-machine-learning.md","source":"@site/blog/2021-06-10-python-machine-learning/python-machine-learning.md","title":"\ud83d\udcd6 \uba38\uc2e0 \ub7ec\ub2dd \uad50\uacfc\uc11c with \ud30c\uc774\uc36c, \uc0ac\uc774\ud0b7\ub7f0, \ud150\uc11c\ud50c\ub85c","description":"\uba38\uc2e0 \ub7ec\ub2dd, \ub525\ub7ec\ub2dd \ud575\uc2ec \uc54c\uace0\ub9ac\uc998\ubd80\ud130 GAN, \uac15\ud654 \ud559\uc2b5\uae4c\uc9c0!","date":"2021-06-10T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \uba38\uc2e0 \ub7ec\ub2dd \uad50\uacfc\uc11c with \ud30c\uc774\uc36c, \uc0ac\uc774\ud0b7\ub7f0, \ud150\uc11c\ud50c\ub85c","date":"2021-06-10","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/python-machine-learning","description":"\uba38\uc2e0 \ub7ec\ub2dd, \ub525\ub7ec\ub2dd \ud575\uc2ec \uc54c\uace0\ub9ac\uc998\ubd80\ud130 GAN, \uac15\ud654 \ud559\uc2b5\uae4c\uc9c0!","keywords":["Machine Learning","TensorFlow","Scikit-learn","Jupyter Notebook"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \uc18c\ubb38\ub09c \uba85\uac15\uc758 \uae40\uc0c1\ud615\uc758 SQL \uc815\ubcf5","permalink":"/en/blog/conquest-sql"},"nextItem":{"title":"\ud83d\udcd6 Do it! \uc7a5\uace0+\ubd80\ud2b8\uc2a4\ud2b8\ub7a9 \ud30c\uc774\uc36c \uc6f9 \uac1c\ubc1c\uc758 \uc815\uc11d","permalink":"/en/blog/Do-it-django-bootstrap"}},"content":":::info\\n\uae38\ubc97 \u201815\ucc28 \uac1c\ubc1c\uc790 \ub9ac\ubdf0\uc5b4\u2019 \ud65c\ub3d9\uc744 \uc704\ud574\uc11c \ucc45\uc744 \uc81c\uacf5\ubc1b\uc544 \uc791\uc131\ub41c \uc11c\ud3c9\uc785\ub2c8\ub2e4.\\n:::\\n\\n## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/python-machine-learning.jpg)](http://www.kyobobook.co.kr/product/detailViewKor.laf?ejkGb=KOR&mallGb=KOR&barcode=9791165215187&orderClick=LAG&Kc=)\\n\\n- \uc81c\ubaa9: \uba38\uc2e0 \ub7ec\ub2dd \uad50\uacfc\uc11c with \ud30c\uc774\uc36c, \uc0ac\uc774\ud0b7\ub7f0, \ud150\uc11c\ud50c\ub85c\\n- \uc800\uc790: Sebastian Raschka, Vahid Mirjalili\\n- \uc5ed\uc790: \ubc15\ud574\uc120\\n- \ucd9c\ud310\uc0ac: \uae38\ubc97\\n- \ucd9c\uac04: 2021-03-31\\n\\n{/* truncate */}\\n\\n## Intro\\n\\n\uc774 \ucc45\uc740 \uae38\ubc97\uc5d0\uc11c \uc9c4\ud589\ud558\ub294 `15\ucc28 \uac1c\ubc1c\uc790 \ub9ac\ubdf0\uc5b4` \ud65c\ub3d9\uc5d0 \ucc38\uc5ec\ud558\uac8c \ub418\uc5b4 \uc77d\uac8c \ub410\uc2b5\ub2c8\ub2e4. [\ud398\uc774\uc2a4\ubd81 \ud398\uc774\uc9c0](https://www.facebook.com/gbitbook/)\ub97c \ud314\ub85c\uc6b0 \ud558\uace0 \uc788\ub2e4\ubcf4\ub2c8 \uc774\ub7f0 \ud65c\ub3d9\uc744 \uc54c \uc218 \uc788\uc5c8\uc2b5\ub2c8\ub2e4. \uc800\ub294 \uace7 4\ud559\ub144\uc774 \ub418\ub294 \ub300\ud559\uc0dd\uc73c\ub85c\uc368 \uc878\uc5c5 \uc791\ud488\uc73c\ub85c \uba38\uc2e0\ub7ec\ub2dd \ud504\ub85c\uc81d\ud2b8\ub97c \ud574\uc57c\ud560 \uc9c0\ub3c4 \ubaa8\ub974\ub294 \uc555\ubc15\uac10\uc5d0 \uc870\uae08\uc774\ub77c\ub3c4 \ubbf8\ub9ac \uba38\uc2e0\ub7ec\ub2dd\uc5d0 \ub300\ud574 \ub354 \uc790\uc138\ud788 \uacf5\ubd80\ud558\uace0 \uc2f6\uc5c8\uc2b5\ub2c8\ub2e4.\\n\\n\uc774\uc804\uc5d0 \uc120\ud615\ub300\uc218\ud559, \uc218\uce58\ud574\uc11d, \ud655\ub960 \ubc0f \ud1b5\uacc4 \ub4f1\uc758 \uacfc\ubaa9\uc5d0\uc11c \uc800\ub3c4 \ubaa8\ub974\ub294 \uc0ac\uc774\uc5d0 \uad50\uc218\ub2d8\uc774 \uba38\uc2e0\ub7ec\ub2dd \uacfc\uc81c\ub97c \ub0b4\uc8fc\uc154\uc11c \ud55c\ucc38\uc744 \ud5e4\ub9f8\uc5c8\uc2b5\ub2c8\ub2e4. \ud558\uace0 \uc788\ub294 \uacfc\uc81c\uac00 \uba38\uc2e0\ub7ec\ub2dd\uacfc \uad00\ub828\ub418\uc5c8\ub2e4\ub294 \uac83\ub3c4 \ubaa8\ub978\ucc44\ub85c \uc5b5\uc9c0\ub85c \uacfc\uc81c\ub97c \ud588\ub358 \uae30\uc5b5\uc774 \ub098\ub124\uc694. \uadf8 \ub54c\ub294 \uadf8\uc800 \uacfc\uc81c\ud558\ub294 \uac83\uc5d0\ub9cc \uae09\ud574\uc11c \uc81c\ub300\ub85c \uacf5\ubd80\ub97c \ud558\uc9c0 \ubabb \ud588\uc5c8\ub294\ub370 \uc774 \ucc45\uc744 \uc77d\uc73c\uba74\uc11c \uacf5\ubd80\ub97c \ud574\ubcf4\uace0 \uc2f6\uc5c8\uae30\uc5d0 \uc774 \ud65c\ub3d9\uc5d0 \uc2e0\uccad\ud558\uac8c \ub410\uc2b5\ub2c8\ub2e4.\\n\\n\ub2e4\ub978 \ucc45\ub4e4\ub3c4 \uc194\uc9c1\ud788 \ub2e4 \uc77d\uc5b4\ubcf4\uace0 \ub9ac\ubdf0\ub3c4 \ud574\ubcf4\uace0 \uc2f6\uc5c8\uc2b5\ub2c8\ub2e4. \ub108\ubb34 \ud0d0\ub098\ub294 \ucc45\ub4e4\uc774 \ub9ce\uc558\uc9c0\ub9cc \ud604\uc7ac \uac00\uc7a5 \uacf5\ubd80\ud574\ubcf4\uace0 \uc2f6\uc5c8\ub358 \uac74 \uba38\uc2e0\ub7ec\ub2dd\uc774\uc5c8\uae30 \ub54c\ubb38\uc5d0 \uc774 \ucc45\uc744 \uc120\ud0dd\ud558\uac8c \ub410\uc2b5\ub2c8\ub2e4. \ub2e4\ub978 \ucc45\ub4e4\uc740 \uc5b8\uc820\uac00 \uae30\ud68c\uac00 \ub418\uba74 \ub9ac\ubdf0\ub97c \ud558\uaca0\uc8e0? \uadf8\ub9ac\uace0 \uc194\uc9c1\ud788 \uc120\uc815\ub420 \uc9c0\ub294 \ubab0\ub790\ub294\ub370 \ub9c9\uc0c1 \ub418\ub2c8\uae4c \uae30\uc058\ub124\uc694.\\n\\n## Book Review\\n\\n### \ubc88\uc5ed\\n\\n\uc774 \ucc45\uc740 [Python Machine Learning 3rd Edition](https://www.amazon.com/Python-Machine-Learning-scikit-learn-TensorFlow/dp/1789955750)\ub97c \ubc88\uc5ed\ud55c \ucc45\uc785\ub2c8\ub2e4. \uc5ed\uc790\ub294 \uc720\uba85\ud55c \uba38\uc2e0\ub7ec\ub2dd \uad00\ub828 \ucc45\ub4e4\uc744 \ubc88\uc5ed\ud558\uc2dc\uace0 \uc4f0\uc2dc\ub294 `\ubc15\ud574\uc120`\ub2d8\uc785\ub2c8\ub2e4. \ubc15\ud574\uc120\ub2d8 \ubc88\uc5ed\uc11c\ub4e4\uc744 \ubcf4\uba74 \uae54\ub054\ud55c \ubc88\uc5ed\ub3c4 \uc7a5\uc810\uc774 \ub418\uaca0\uc9c0\ub9cc, `\uc5ed\uc8fc`\ub77c\ub294 \ubd80\ubd84\uc774 \ub3c4\uc6c0\uc774 \uaf64 \ub9ce\uc774 \ub429\ub2c8\ub2e4. \uc774 \ubd84\uc774 \uc5bc\ub9c8\ub098 \uc138\ubd80\uc801\uc73c\ub85c \ubc88\uc5ed\uc744 \ud558\uc2dc\ub294 \uc9c0 \uc54c \uc218 \uc788\ub294 \ubd80\ubd84\uc785\ub2c8\ub2e4. \uc774\ub7f0 \uac01\uc8fc\uc5d0 \uad00\ub828\ub41c \ubd80\ubd84\uc740 \uc0ac\ub78c\ub9c8\ub2e4 \ub2e4\ub974\uaca0\uc9c0\ub9cc, \uc800\ub294 \uc774\ub807\uac8c \uc138\ubd80\uc801\uc73c\ub85c \uc54c\ub824\uc8fc\ub294 \uac83\uc774 \ub108\ubb34 \uc88b\uc558\uc2b5\ub2c8\ub2e4.\\n\\n\ubc88\uc5ed\ub41c \ucc45\uc774\ub77c \uba38\uc2e0\ub7ec\ub2dd\uacfc \uad00\ub828\ub41c \uc6a9\uc5b4\ub4e4\uc774 \ud639\uc2dc \uc804\ubd80 \ud55c\uad6d\uc5b4\ub85c \ubc88\uc5ed\ub418\uc5b4 \uc788\uc9c0\ub294 \uc54a\uc744\uae4c \uc0b4\uc9dd \uac71\uc815\uc740 \ud588\uc9c0\ub9cc, \uadf8\ub7f0 \uac71\uc815\uc740 \uc804\ud600 \ud544\uc694\uc5c6\uc5c8\uc2b5\ub2c8\ub2e4. \uc804\ubb38 \uc6a9\uc5b4 \ubd80\ubd84\uc5d0\ub294 \uc804\ubd80 \uad75\uac8c \ud45c\uc2dc\ub418\uc5b4 \uc788\uc73c\uba70 \uad04\ud638\ub97c \uc774\uc6a9\ud574\uc11c \uc601\uc5b4\ub85c \ub2e4 \uc801\ud600 \uc788\uc2b5\ub2c8\ub2e4. \uc774\ub7f0 \uc138\uc138\ud55c \ubd80\ubd84\ub3c4 \ub108\ubb34 \uc88b\uc558\uc2b5\ub2c8\ub2e4. \uc544\ubb34\ub798\ub3c4 \uba38\uc2e0\ub7ec\ub2dd\uacfc \uad00\ub828\ub41c \ucc45\ub4e4\uc740 \uc601\ubb38 \ubb38\uc11c\ub4e4\uc744 \ubcfc \uc77c\uc774 \ub9ce\uc744\ud150\ub370 \ubc88\uc5ed\ub41c \uc6a9\uc5b4\ub85c \uc801\ud600 \uc788\uc5c8\ub354\ub77c\uba74 \uadf8 \uc6a9\uc5b4\uac00 \ubb54 \uc9c0 \uc798 \ubab0\ub790\uc744 \uac83\uc785\ub2c8\ub2e4. \\n\\n\uc694\uc998 \ud2b8\ub80c\ub4dc\ub294 Jupyter Notebook \ub610\ub294 Colab\uc5d0\uc11c \uc124\uba85\uacfc \ud568\uaed8 \uc2e4\uc2b5\ud560 \uc218 \uc788\ub294 \ucf54\ub4dc\uac00 \uc81c\uacf5\ub41c\ub2e4\ub294 \uac83\uc778\ub370 \uadf8\ub7f0 \uc2e4\uc2b5 \ucf54\ub4dc\ub3c4 \ud55c\uae00\ud654\ub41c \ucf54\ub4dc\uac00 \uc81c\uacf5\ub429\ub2c8\ub2e4. \uc774\ub7f0 \ubd80\ubd84\ub3c4 \ubc88\uc5ed\uc744 \ud558\uc2e0 \uac83\uc774 \uc815\ub9d0 \ub300\ub2e8\ud558\ub2e4\uace0 \ub290\uaef4\uc9d1\ub2c8\ub2e4. \\n\\n### \uac1c\ub150\uacfc \uc2e4\uc2b5\\n\\n\uac1c\ub150\uacfc \uc2e4\uc2b5 \ub450 \ub9c8\ub9ac \ud1a0\ub07c\ub97c \ub2e4 \uc7a1\ub294 \ucc45\uc774\ub77c\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4. 868 \ud398\uc774\uc9c0\uc758 \ub450\uaebc\uc6b4 \ucc45\uc5d0\uc11c \uac1c\ub150\uc744 \uc124\uba85\ud558\uba70 \ucf54\ub4dc\uae4c\uc9c0 \uc790\uc138\ud558\uac8c \ub098\uc640\uc788\uae30\uc5d0 \ucc45 \uc81c\ubaa9\ucc98\ub7fc `\uad50\uacfc\uc11c`\ub77c\ub294 \uc774\ub984\uc774 \uc5b4\uc6b8\ub9ac\ub294 \ucc45\uc785\ub2c8\ub2e4. NumPy\ub9cc\uc744 \uc774\uc6a9\ud558\uc5ec \ubc14\ub2e5\ubd80\ud130 \uc54c\uace0\ub9ac\uc998\uc744 \uc791\uc131\ud574\ubcf4\ub294 \uac83\uc774 \ub9e4\uc6b0 \uc778\uc0c1 \uae4a\uc5c8\uc2b5\ub2c8\ub2e4. \\n\\n\uc774 \ucc45\uc740 \uac01 \uc7a5\uc774 \uc2dc\uc791\ub420 \ub54c\ub9c8\ub2e4 \uc55e\uc73c\ub85c \ubb34\uc5c7\uc744 \ud560 \uac83\uc774\ub77c\ub294 \uac83\uc744 \uc124\uba85\ud574 \uc8fc\ub294 \ubd80\ubd84\ub4e4\uc774 \uc788\ub294\ub370 \uadf8\ub7f0 \ubd80\ubd84\uc5d0\uc11c \uc800\uc790\uac00 \uc2e0\uacbd\uc744 \uc4f0\uba74\uc11c \ucc45\uc744 \uc37c\ub2e4\ub294 \uac83\uc774 \ub290\uaef4\uc84c\uc2b5\ub2c8\ub2e4. \ub610\ud55c, \uc54c\uace0\ub9ac\uc998\uc774 \uc0dd\uae34 \ubc30\uacbd, \uc54c\uace0\ub9ac\uc998\uc774 \uc5b4\ub514\uc5d0 \uc4f0\uc774\ub294 \uc9c0 \ub4f1\uc5d0 \ub300\ud55c \ub0b4\uc6a9\ub3c4 \uac19\uc774 \uc54c\ub824\uc8fc\ub294 \uac83\uc774 \ucc45\uc744 \uc218\uc6d4\ud558\uac8c \uc77d\ub294 \ub370 \ub3c4\uc6c0\uc774 \ub410\uc2b5\ub2c8\ub2e4. \ub9c8\ub0e5 \uc5b4\ub824\uc6b4 \uac1c\ub150\ub4e4\ub9cc \uc124\uba85\ud558\ub294 \ucc45\uc774 \uc544\ub2c8\ub77c\ub294 \uac83\uc774\uc8e0.\\n\\n\ub354 \uc138\ubd80\uc801\uc778 \uac1c\ub150\uc744 \uc6d0\ud558\uba74 \ub17c\ubb38 \uc81c\ubaa9\uc744 \uc54c\ub824\uc8fc\uac70\ub098 \ucc38\uace0 \uc790\ub8cc\uc758 \ub9c1\ud06c \ub610\ub294 \uc81c\ubaa9\uc744 \uc54c\ub824\uc8fc\ub294 \ubd80\ubd84\uc5d0\uc11c\ub3c4 \uc800\uc790\uc758 \uc138\uc138\ud568\uc774 \ub290\uaef4\uc84c\uc2b5\ub2c8\ub2e4. \ub610\ud55c, \ucd5c\uadfc \uc774\uc288\uac00 \ub418\ub294 \uae30\uc220\ub4e4\uc778 GAN, transformer\ub97c \ub2e4\ub8e8\ub294 \uac83\ub3c4 \uc778\uc0c1 \uae4a\uc5c8\uc2b5\ub2c8\ub2e4. \\n\\n\uc774 \ucc45\uc73c\ub85c\ub3c4 \ubd80\uc871\ud558\uac70\ub098 \uc880 \ub354 \ubcf4\ucda9\ud558\uace0 \uc2f6\uc740 \ub0b4\uc6a9\uc774 \uc788\uc73c\uba74 \uc5ed\uc790\uc774\uc2e0 `\ubc15\ud574\uc120`\ub2d8\uc774 [\uc720\ud29c\ube0c\uc5d0 \uc62c\ub9ac\uc2dc\ub294 \uac15\uc88c](https://www.youtube.com/playlist?list=PLJN246lAkhQiEc-QvvGzUneCWuRnCNKgU)\ub97c \ub4e4\uc5b4\ubcf4\uc2dc\uae30 \ubc14\ub78d\ub2c8\ub2e4. \ud604\uc7ac 5\uc7a5\uc744 \uc62c\ub9ac\uc2dc\uace0 \uc788\uc73c\uc2dc\uba70 \ud070 \ub3c4\uc6c0\uc774 \ub420 \uac83\uc774\ub77c\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4. \\n\\n## \ub300\uc0c1 \ub3c5\uc790\\n\\n\uc194\uc9c1\ud788 \ub9d0\ud558\uba74 \uc774 \ubd84\uc57c\ub97c \ucc98\uc74c \uc2dc\uc791\ud558\ub294 \ubd84\uc5d0\uac8c\ub294 \uc77d\uae30 \uc5b4\ub824\uc6b8 \uc218\ub3c4 \uc788\uaca0\ub2e4\ub294 \uc0dd\uac01\uc774 \ub4ed\ub2c8\ub2e4. \ucc98\uc74c\ubd80\ud130 \uc5b4\ub824\uc6b4 \uac1c\ub150\uc744 \ud761\uc218\ud558\ub294 \uac83\uc744 \uc88b\uc544\ud558\uc2dc\ub294 \ubd84\uc774\uba74 \ucd94\ucc9c\ub4dc\ub9bd\ub2c8\ub2e4. \uac1c\ub150\ub4e4\uc774 \uc5b4\ub835\uc9c0\ub9cc \uc790\uc138\ud558\uc9c0 \uc54a\uc740 \ucc45\uc740 \uc544\ub2d9\ub2c8\ub2e4. \uc880 \ub354 \uc2dc\uac04\uc774 \uac78\ub9b4 \ubfd0\uc774\uc8e0. \\n\\n\uba38\uc2e0\ub7ec\ub2dd\uc5d0 \ub300\ud574 \uc798 \ubaa8\ub974\uc154\ub3c4 \uad1c\ucc2e\uc2b5\ub2c8\ub2e4\ub9cc, \ud30c\uc774\uc36c \ub77c\uc774\ube0c\ub7ec\ub9ac\uc5d0 \ub300\ud55c \uc774\ud574\ub3c4\ub294 \uc5b4\ub290\uc815\ub3c4 \ud544\uc694\ud558\ub2e4\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4. NumPy, matplotlib, pandas, TensorFlow, Scikit-learn, Scipy \ub4f1\uc758 \ub77c\uc774\ube0c\ub7ec\ub9ac\uac00 \uc788\uc2b5\ub2c8\ub2e4. \uadfc\ub370 \uc0ac\uc2e4 \uc800\ub3c4 pandas, Numpy, matplotlib\ub9cc \uc870\uae08 \uc4f8 \uc904 \uc54c\uc9c0 \ub098\uba38\uc9c0 \ub77c\uc774\ube0c\ub7ec\ub9ac\ub4e4\uc740 \ucc98\uc74c\uc785\ub2c8\ub2e4. \uadf8\ub807\uc9c0\ub9cc \uc800\ucc98\ub7fc \uc9c1\uc811 reference\ub97c \ucc3e\uc544\uac00\uba74\uc11c \uacf5\ubd80\ud560 \uc218 \uc788\ub294 \uc815\ub3c4\ub77c\uba74 \ubaa8\ub974\uc154\ub3c4 \ubb34\ub09c\ud560 \uac70 \uac19\uc2b5\ub2c8\ub2e4.  \\n\\n\ub610\ud55c, \uc120\ud615\ub300\uc218\ud559, \ud655\ub960 \ubc0f \ud1b5\uacc4, \uc218\uce58\ud574\uc11d \ub4f1\uc758 \uacfc\ubaa9\uc744 \ub4e4\uc5b4\ubcf4\uc2dc\uac70\ub098 \uba38\uc2e0\ub7ec\ub2dd \uc54c\uace0\ub9ac\uc998\uc5d0 \uad00\uc2ec\uc774 \uc788\uc73c\uc2e0 \ubd84\ub4e4\uc774\ub77c\uba74 \uc7ac\ubc0c\uac8c \uacf5\ubd80\ud558\uc2e4 \uc218 \uc788\uc744 \uac83\uc785\ub2c8\ub2e4. \uc774 \ucc45\uc758 \uc55e\ubd80\ubd84\uc778 `\uc2e4\uc2b5 \ud6c4\uae30`\ub97c \ubcf4\uba74 \uc54c \uc218 \uc788\ub4ef\uc774 \uc798 \uc791\ub3d9\ud558\uae30 \ub54c\ubb38\uc5d0 [\uba38\uc2e0\ub7ec\ub2dd \uad50\uacfc\uc11c 3\ud310 \uae43\ud5c8\ube0c](https://github.com/rickiepark/python-machine-learning-book-3rd-edition)\ub97c \ucc38\uace0\ud558\uc154\uc11c \uc2e4\uc2b5\ud574\ubcf4\uc2dc\uba74 \ub9ce\uc740 \ub3c4\uc6c0\uc774 \ub420 \uac83\uc785\ub2c8\ub2e4. \uc774 \uc2e4\uc2b5 \ucf54\ub4dc\ub97c \uc0ac\uc6a9\ud574\ubcf4\ub824\uba74 Colab \ub610\ub294 Jupyter Notebook\uc758 \uac04\ub2e8\ud55c \uc0ac\uc6a9\ubc95 \uc815\ub3c4\ub294 \uc54c\uace0 \uc788\uc5b4\uc57c \ud558\uaca0\uc8e0."},{"id":"/Do-it-django-bootstrap","metadata":{"permalink":"/en/blog/Do-it-django-bootstrap","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2021-06-09-do-it-django-bootstrap/Do-it-django-bootstrap.md","source":"@site/blog/2021-06-09-do-it-django-bootstrap/Do-it-django-bootstrap.md","title":"\ud83d\udcd6 Do it! \uc7a5\uace0+\ubd80\ud2b8\uc2a4\ud2b8\ub7a9 \ud30c\uc774\uc36c \uc6f9 \uac1c\ubc1c\uc758 \uc815\uc11d","description":"\ubaa8\ub358 \uc6f9\uc744 \uc704\ud55c \uc11c\ubc84 \uc0ac\uc774\ub4dc \uc790\ubc14\uc2a4\ud06c\ub9bd\ud2b8\uc758 \ubaa8\ub4e0 \uac83","date":"2021-06-09T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 Do it! \uc7a5\uace0+\ubd80\ud2b8\uc2a4\ud2b8\ub7a9 \ud30c\uc774\uc36c \uc6f9 \uac1c\ubc1c\uc758 \uc815\uc11d","date":"2021-06-09","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/Do-it-django-bootstrap","description":"\ubaa8\ub358 \uc6f9\uc744 \uc704\ud55c \uc11c\ubc84 \uc0ac\uc774\ub4dc \uc790\ubc14\uc2a4\ud06c\ub9bd\ud2b8\uc758 \ubaa8\ub4e0 \uac83","keywords":["Django","Python","Bootstrap","Docker"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \uba38\uc2e0 \ub7ec\ub2dd \uad50\uacfc\uc11c with \ud30c\uc774\uc36c, \uc0ac\uc774\ud0b7\ub7f0, \ud150\uc11c\ud50c\ub85c","permalink":"/en/blog/python-machine-learning"},"nextItem":{"title":"\ud83d\udcd6 \ud55c \uad8c\uc73c\ub85c \ub05d\ub0b4\ub294 Node & Express","permalink":"/en/blog/web-development-with-node-and-express"}},"content":"## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/Do-it-Django-Bootstrap.jpg)](http://www.kyobobook.co.kr/product/detailViewKor.laf?ejkGb=KOR&mallGb=KOR&barcode=9791163032069&orderClick=LET&Kc=)\\n\\n- \uc81c\ubaa9: Do it! \uc7a5\uace0+\ubd80\ud2b8\uc2a4\ud2b8\ub7a9 \ud30c\uc774\uc36c \uc6f9 \uac1c\ubc1c\uc758 \uc815\uc11d\\n- \uc800\uc790: \uc774\uc131\uc6a9, \uae40\ud0dc\uace4\\n- \ucd9c\ud310\uc0ac: \uc774\uc9c0\uc2a4\ud37c\ube14\ub9ac\uc2f1\\n- \ucd9c\uac04: 2021-01-18\\n\\n{/* truncate */}\\n\\n\\n## Intro\\n\\n\uc608\uc804\uc5d0 \uba4b\uc7c1\uc774\uc0ac\uc790\ucc98\ub7fc\uc5d0 \ucc38\uc5ec\ud558\ub294 \ubd84\ub4e4\uc774 Django\ub97c \uc0ac\uc6a9\ud558\ub294 \ubaa8\uc2b5\uc744 \ubcf4\uace0 \uc6f9\uc5d0 \ub300\ud55c \ucd08\ubcf4\uc790\ub3c4 \uacf5\ubd80\ud558\uae30 \uc26c\uc6b4 \uc2a4\ud0dd\uc774\uad6c\ub098 \uc2f6\uc5b4\uc11c \uacf5\ubd80\ub97c \uc2dc\ub3c4\ud588\ub358 \uc801\uc774 \uc788\uc5c8\uc2b5\ub2c8\ub2e4. \uadf8 \ub2f9\uc2dc\uc5d0\ub294 \ud63c\uc790 \uac1c\ubc1c \uacf5\ubd80\ud558\ub294 \ubc29\ubc95\uc744 \ubab0\ub77c Django\ub97c \uacf5\ubd80\ud558\ub294\ub370 \uc2e4\ud328\ud588\uc5c8\uc2b5\ub2c8\ub2e4. \ubb50\ub784\uae4c \ub204\uad70\uac00 \uac00\ub974\uccd0\uc8fc\ub294 \uc0ac\ub78c\uc774 \uc788\uc73c\uba74 \uc88b\uaca0\ub2e4\ub294 \uc0dd\uac01 \uac19\uc774 \uadf8\uc800 \ub5a0\uba39\uc5ec \uc8fc\uae38 \ubc14\ub7ac\ub358 \uac70 \uac19\uc544\uc694. \ubb50 \uc5b4\uca0c\ub4e0 \uadf8 \uc774\ud6c4\ub85c Django\ub294 \ud3ec\uae30\ud588\uace0 \ub2e4\ub978 \uc2a4\ud0dd\ub4e4\uc744 \ub9c9 \uacf5\ubd80\ud588\uc5c8\uc2b5\ub2c8\ub2e4.\\n\\n\ucd5c\uadfc\uc5d0 \ub2e4\uc2dc Django\ub97c \uacf5\ubd80\ud558\uae30 \uc704\ud574\uc11c \'\ubb58 \ubcf4\uace0 \uacf5\ubd80\ub97c \ud574\ubcfc\uae4c\' \uace0\ubbfc\ud558\ub358 \uc911 \uc774\ubc88 \ub144\uc5d0 Django \ucc45\uc774 2\uad8c \ucd9c\uc2dc\uac00 \ub410\ub294 \uac78 \uc54c\uac8c \ub410\uc2b5\ub2c8\ub2e4. \uadf8\uac8c \uc774\uc81c `Do it! \uc810\ud504 \ud22c \uc7a5\uace0`\uc640 `Do it! \uc7a5\uace0+\ubd80\ud2b8\uc2a4\ud2b8\ub7a9 \ud30c\uc774\uc36c \uc6f9 \uac1c\ubc1c\uc758 \uc815\uc11d`\uc774\uc5c8\uc2b5\ub2c8\ub2e4.\\n\\n\uc7a5\uace0 \uacf5\uc2dd \ubb38\uc11c, Two Scoops of Django \ub4f1 \uc704\uc758 \ucc45\ub4e4 \uc774\uc678\uc5d0\ub3c4 \uc120\ud0dd\uc9c0\ub294 \uc788\uc5c8\uc2b5\ub2c8\ub2e4. Two Scoops of Django\ub294 \uc601\uc5b4\ub77c \uc77d\ub294\ub370 \uc624\ub798 \uac78\ub9b4 \uac70 \uac19\uc544\uc11c \uc77d\uc9c0 \uc54a\uc558\uc2b5\ub2c8\ub2e4. \ubc88\uc5ed\uc11c\ub3c4 \uc788\uc9c0\ub9cc 2016\ub144\uc5d0 \ucd9c\uac04\ub41c \ucc45\uc774\ub77c \uc608\uc804 \ubc84\uc804\uc744 \uc0ac\uc6a9\ud560 \uac70 \uac19\uc544\uc11c \uc120\ud0dd\ud558\uc9c0 \uc54a\uc558\uc2b5\ub2c8\ub2e4. \\n\\n\uacb0\uad6d \uc704\uc758 Do it! \ucc45\ub4e4 \uc911 \ud55c \uad8c\uc744 \uc77d\uace0\uc790 \uacb0\uc2ec\ud588\uace0 \ub3c4\ucee4\ub97c \uc0ac\uc6a9\ud558\ub294 `Do it! \uc7a5\uace0+\ubd80\ud2b8\uc2a4\ud2b8\ub7a9 \ud30c\uc774\uc36c \uc6f9 \uac1c\ubc1c\uc758 \uc815\uc11d`\uc744 \uc120\ud0dd\ud588\uc2b5\ub2c8\ub2e4. [\uc810\ud504 \ud22c \uc7a5\uace0](https://wikidocs.net/book/4223)\ub294 \uc704\ud0a4\ubd81\uc2a4\uc5d0\ub3c4 \uc788\uc5b4\uc11c \ucc38\uace0\ud558\uba74\uc11c \uc77d\uc5c8\uc2b5\ub2c8\ub2e4.\\n\\n## Book Review\\n\\n### \uc6f9 \uac1c\ubc1c, \ubc30\ud3ec, \uc6b4\uc601\\n\\n\uc774 \ucc45\uc740 640 \ud398\uc774\uc9c0\uc758 \uc740\uadfc \ub450\uaebc\uc6b4 \ucc45\uc785\ub2c8\ub2e4. \'Django \ucc45\uc774 \ubb50 \uc774\ub807\uac8c \ub450\uaecd\ub098?\'\ub77c\uace0 \uc0dd\uac01\ud558\uc2e4 \uc218\ub3c4 \uc788\uaca0\uc9c0\ub9cc, \ucf54\ub4dc\ub3c4 \uc804\ubd80 \ud45c\uae30\ub418\uc5b4 \uc788\uc73c\uba74\uc11c, \uc7a5\uace0\ub3c4 \ub2e4\ub8e8\uace0, \ubd80\ud2b8\uc2a4\ud2b8\ub7a9, \ubc30\ud3ec \ubd80\ubd84\uae4c\uc9c0 \ub2e4 \ub2e4\ub8e8\ub294 \uac78 \uc0dd\uac01\ud558\uba74 640 \ud398\uc774\uc9c0\uac00 \ubd80\uc871\ud560 \uc815\ub3c4\uc785\ub2c8\ub2e4. \uadfc\ub370 \uadf8 \ubaa8\ub4e0 \uac78 \ucc45 \ud55c \uad8c\uc5d0 \uc815\ub9ac\ud574\ub1a8\ub2e4\ub294 \uac83\uc740 \uc815\ub9d0 \ub300\ub2e8\ud55c \uc77c\uc774\ub77c\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4. \uadf8\ub9ac\uace0 \uc800\ub294 \uc774 \ubd80\ubd84\uc774 \uc774 \ucc45\uc758 \uac00\uc7a5 \ud070 \uc7a5\uc810\uc774\ub77c\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4.\\n\\n### \uae30\uc220 \uc0ac\uc6a9\\n\\n\ucc45\uc5d0\uc11c\ub294 \ube14\ub85c\uadf8\ub97c \ub9cc\ub4e0\ub2e4\uace0 \ub418\uc5b4 \uc788\uc9c0\ub9cc \uc0ac\uc2e4\uc0c1 \ucee4\ubba4\ub2c8\ud2f0\ub97c \ub9cc\ub4ed\ub2c8\ub2e4. \ub85c\uadf8\uc778 \ud558\uc5ec\uc11c \uae00\uc744 \uc4f8 \uc218 \uc788\uc73c\uba70 \ub313\uae00 \uae30\ub2a5\ub3c4 \uad6c\ud604\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. Django\ub97c \uc0ac\uc6a9\ud558\uc5ec \ud754\ud788 \uc54c\ub824\uc838 \uc788\ub294 CRUD(\uc785\ub825/\uc870\ud68c/\uc218\uc815/\uc0ad\uc81c) \uae30\ub2a5\uc744 \uad6c\ud604\ud569\ub2c8\ub2e4.\\n\\n#### \ud504\ub860\ud2b8\\n\\n\ud504\ub860\ud2b8 \ubd80\ubd84\uc740 HTML, CSS, JavaScript\ub97c \ud544\uc694\ud55c \ubd80\ubd84\ub4e4\ub9cc \uc54c\ub824\uc8fc\uace0, Bootstrap\uc744 \ud65c\uc6a9\ud558\uc5ec \uac04\ub2e8\ud558\uac8c \uafb8\ubbf8\ub294 \ubc29\ubc95\uc744 \uc54c\ub824\uc90d\ub2c8\ub2e4. \uac1c\uc778\uc801\uc73c\ub85c\ub294 Bootstrap\ubcf4\ub2e4 \ucd5c\uadfc \ud504\ub860\ud2b8 \uae30\uc220\uc778 React\ub97c \ub2e4\ub918\uc73c\uba74 \ub354 \uc88b\uc558\uc744 \uac70\ub77c \uc0dd\uac01\ud558\uc9c0\ub9cc \uadf8\ub807\uac8c \ub418\uba74 \ucc45 \ucabd\uc218\uac00 1000 \ud398\uc774\uc9c0\uac00 \ub118\uc5b4\uac14\uc744 \uac70 \uac19\uc73c\ub2c8 \uc800\uc790\ubd84\uc774 \uc120\ud0dd\uc744 \uc798 \ud558\uc2e0 \uac83 \uac19\uc2b5\ub2c8\ub2e4. \ub610\ud55c, React \uac19\uc740 \uacbd\uc6b0\uc5d0\ub294 \ucd08\ubcf4\uc790\uac00 HTML, CSS, JavaScript\ub97c \uc790\uc138\ud788 \ubc30\uc6b0\uc9c0\ub3c4 \uc54a\uace0 \uac04\ub2e8\ud558\uac8c \uacf5\ubd80\ud558\uae30\uc5d0\ub294 \uc5b4\ub824\uc6b0\ub2c8 \ud544\uc694\ud55c \ubd80\ubd84\ub9cc \ubc14\ub85c\ubc14\ub85c \uc0ac\uc6a9\ud560 \uc218 \uc788\ub294 Bootstrap\uc744 \uc120\ud0dd\ud55c \uac70\ub77c \uc0dd\uac01\ub429\ub2c8\ub2e4.  \\n\\n\ubd80\ud2b8\uc2a4\ud2b8\ub7a9\uc774 \ud604\uc7ac \ubc84\uc804\uacfc \ub2e4\ub974\ubbc0\ub85c \ucd5c\uc2e0 \ubc84\uc804\uc744 \uc0ac\uc6a9\ud558\ub2e4\uac00\ub294 \uacf5\uc2dd \ubb38\uc11c\ub97c \ubcf4\uba70 \uacf5\ubd80\ub97c \ud574\uc57c\ud560 \uc218 \uc788\uc73c\ub2c8 \uc8fc\uc758\ud558\uc2dc\uae38 \ubc14\ub78d\ub2c8\ub2e4.\\n\\n#### Git\\n\\n\ub9e4 \ucf54\ub4dc\uc5d0\uc11c \ubb34\uc5b8\uac00 \uc9c4\ud589 \ud588\uc744 \ub54c GitHub\uc5d0 commit\ud558\uace0 push\ud569\ub2c8\ub2e4. \uac1c\uc778\uc801\uc73c\ub85c \ud504\ub85c\uc81d\ud2b8\ub97c \ud558\uba74\uc11c \uc5b8\uc81c \uc5b8\uc81c \uc62c\ub824\uc57c \ud558\ub294\uc9c0 \uc54c \uc218 \uc788\uc5c8\uae30\uc5d0 \uaf64 \ub3c4\uc6c0\uc774 \ub410\ub2e4\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4.\\n\\n#### Docker, AWS\\n\\n\uc800\ub294 \uc774\ubc88\uc5d0 \ub3c4\ucee4\ub97c \ucc98\uc74c \uc368\ubcf4\ub294 \uac83\uc774\ub77c \uc870\uae08 \uc5b4\ub824\uc6b4 \uac10\uc774 \uc788\uc5c8\uc2b5\ub2c8\ub2e4. \uc774 \ucc45\uc5d0\uc11c Docker\ub97c \uc0ac\uc6a9\ud55c \ubc30\ud3ec\ub97c \ub9db\ubcf4\uae30\ub85c \uc990\uae30\uae30 \uc88b\uc2b5\ub2c8\ub2e4. \ub610\ud55c, AWS \uac19\uc740 \uacbd\uc6b0\uc5d0\ub3c4 \uc0ac\uc6a9\ud558\uae30 \ud3b8\ud55c Lightsail\uc744 \uc0ac\uc6a9\ud569\ub2c8\ub2e4. Lightsail\uc740 \ucc98\uc74c \uc0ac\uc6a9\ud574\ubd24\ub294\ub370 AWS \ubb34\uacbd\ud5d8\uc790\ub3c4 \ud3b8\ud558\uac8c \uc0ac\uc6a9\ud560 \uc218 \uc788\uc744 \uac70\ub77c\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4. \uadf8\ub9ac\uace0 \ud55c \ub2ec\uc740 \ubb34\ub8cc\ub2c8 \uac71\uc815\ud560 \ud544\uc694\uac00 \uc5c6\ub2e4\uace0 \ud569\ub2c8\ub2e4.(\uc800\ubc88 AWS \ucc45\ub3c4 \uc774\ub7ec\ub2e4\uac00 \uacb0\uc81c \ub410\ub294\ub370 \uc5b4\ub5bb\uac8c \ub420 \uc9c0\ub294 \uc800\ub3c4 \ubaa8\ub974\uaca0\uc2b5\ub2c8\ub2e4\ub9cc \ubb50 \uc5bc\ub9c8 \uc548 \ub098\uc62c\ud14c\ub2c8\uae4c \ud574\ubcf4\uc2dc\uba74 \uc7ac\ubc0c\uc744 \uac81\ub2c8\ub2e4.)\\n\\n#### TDD\\n\\n\ud14c\uc2a4\ud2b8 \uc8fc\ub3c4 \uac1c\ubc1c\uc774\ub77c\uace0 \ubd88\ub9ac\ub294 TDD\uc785\ub2c8\ub2e4. \ub9e4\ubc88 Java\ud558\uc2dc\ub294 \ubd84\ub4e4\uc774 \ud558\uc2dc\ub294 \uc598\uae30\ub9cc \ub4e4\uc5c8\uc9c0 \uc2e4\uc81c\ub85c \uacbd\ud5d8\uc740 \ucc98\uc74c\uc774\uc5c8\uc2b5\ub2c8\ub2e4. Django\uc5d0\ub3c4 TDD\uac00 \uc788\ub2e4\ub294 \uc0ac\uc2e4\uc744 \uc54c\uac8c \ud574\uc92c\uc73c\uba70 \uc774 \ucc45\uc5d0\uc11c \ub9ce\uc740 \ubd80\ubd84\uc744 \ucc28\uc9c0\ud569\ub2c8\ub2e4. \ub9e4\ubc88 \uae30\ub2a5\uc744 \ub9cc\ub4e4\uae30 \uc804\uc5d0 \ud14c\uc2a4\ud2b8 \ucf54\ub4dc\ub97c \uc9dc\uc11c \ubbf8\ub9ac \uacbd\ud5d8\ud574\ubcfc \uc218 \uc788\uc2b5\ub2c8\ub2e4. \uc800\ub294 \uac1c\uc778\uc801\uc73c\ub85c \uc774 \ubd80\ubd84\uc774 \uac00\uc7a5 \uc7ac\ubc0c\uc5c8\uc2b5\ub2c8\ub2e4.\\n\\n#### Django\\n\\n\uac00\uc7a5 \uc911\uc694\ud55c \ubd80\ubd84\uc774\uc8e0. \ud504\ub85c\uc81d\ud2b8\ub97c \ub9cc\ub4e4\uace0 MTV \ud328\ud134, FBV, CBV, \ub2e4\ub300\uc77c \uad00\uacc4, \ub2e4\ub300\ub2e4 \uad00\uacc4 \ub4f1\uc744 \uad6c\ud604\ud558\uace0 \uc0ac\uc6a9\ud574\ubd05\ub2c8\ub2e4. \uc815\ub9d0 \uaf3c\uaf3c\ud558\uac8c \ucf54\ub4dc\ub4e4\uc774 \uae30\uc220\ub418\uc5b4 \uc788\uace0 \ucf54\ub4dc \ud558\ub098\ud558\ub098 \uc124\uba85\uc774 \uc798 \ub418\uc5b4 \uc788\ub294 \ubaa8\uc2b5\uc774 \uc778\uc0c1 \uae4a\uc5c8\uc2b5\ub2c8\ub2e4. \ub610\ud55c, Django \uac1c\ubc1c\uc744 \ud558\uba70 \uc720\uc6a9\ud558\uac8c \uc4f0\uc774\ub294 \ub2e4\ub978 \ud328\ud0a4\uc9c0\ub4e4\uc744 \uc54c\ub824\uc90d\ub2c8\ub2e4. \uc544\uc26c\uc6e0\ub358 \uc810\uc740 Django REST framework\uc5d0 \ub300\ud55c \ub0b4\uc6a9\uc774 \uc5c6\ub2e4\ub294 \uac83\uc785\ub2c8\ub2e4. \\n\\n### \uac1c\ub150\\n\\n\uc138\ubd80\uc801\uc774\uc9c0\ub294 \uc54a\uc9c0\ub9cc Django\uc5d0 \ub300\ud574 \uae30\ubcf8 \uc774\uc0c1\uc740 \uc54c\ub824\uc8fc\ub294 \ucc45\uc774\ub77c\uace0 \ubcf4\uc2dc\uba74 \ub420 \uac83 \uac19\uc2b5\ub2c8\ub2e4. \\"\ub9cc\ub4e4\uba74\uc11c \ubc30\uc6b0\ub294 \uc6f9 \uac1c\ubc1c\\"\uc774\ub77c\uace0 \ubd80\uc81c\uc5d0\uc11c\ub3c4 \ub098\uc640\uc788\ub4ef\uc774 \ub9cc\ub4e4\uba74\uc11c \uac1c\ub150\ub4e4 \ud558\ub098\ud558\ub098\ub97c \uac04\ub2e8\ud558\uac8c \uc124\uba85\ud569\ub2c8\ub2e4. \uadf8\ub807\uae30\uc5d0 \uc138\ubd80\uc801\uc778 \uac1c\ub150 \uc124\uba85\uc5d0 \ub300\ud574\uc11c\ub294 \uc870\uae08 \ubd80\uc871\ud558\ub2e4\uace0 \ub290\ub084 \uc218\ub3c4 \uc788\uc2b5\ub2c8\ub2e4. \\n\\n\uc704\uc5d0\uc11c\ub3c4 \ub9d0\ud588\ub4ef\uc774 \ud504\ub85c\uc81d\ud2b8\ub97c \uc9c4\ud589\ud558\ub294 \ub290\ub08c\uc5d0\uc11c \uc124\uba85\ud558\ub294 \uac83\uc774\uae30 \ub54c\ubb38\uc5d0 \uac00\ub054 \uc800\uc790\ubd84\ub4e4\uc774 \uc77c\ubd80\ub7ec \uc2e4\uc218\ud558\ub294 \ubd80\ubd84\uc774 \ub4e4\uc5b4\uac00\uc788\uae30\ub3c4 \ud569\ub2c8\ub2e4. \uc774\ub7f0 \uc0bd\uc9c8\uc744 \ud560 \uc218\ub3c4 \uc788\ub2e4\ub294 \ub0b4\uc6a9\uc744 \uc54c\ub824\uc8fc\uc2dc\uace0 \uc2f6\uc5c8\ub358 \uac83 \uac19\uc2b5\ub2c8\ub2e4. \uadfc\ub370 \uc774\ub7f0 \ubd80\ubd84 \uc774\uc678\uc5d0\ub3c4 \uc0bd\uc9c8\ud560 \ubd80\ubd84\uc774 \uc5c4\uccad \ub9ce\uc2b5\ub2c8\ub2e4.\\n\\n\uac00\ub054 \\"\uc54c\uc544\ub450\uba74 \uc88b\uc544\uc694\\"\ub77c\ub294 \ubd80\ubd84\uc73c\ub85c \uc911\uc694 \ubd80\ubd84 \uc774\uc678\uc758 \uac1c\ub150\ub4e4\ub3c4 \uc124\uba85\ud574\uc90d\ub2c8\ub2e4. \uc774\ub7f0 \ubd80\ubd84\ub3c4 \uc77d\uc5b4\ubcf4\uba74 \uc7ac\ubc0c\uc5c8\uc2b5\ub2c8\ub2e4. \\n\\n### \uc0bd\uc9c8\\n\\n\uc774 \ucc45\uc744 \uc77d\uc73c\uba74\uc11c \ud588\ub358 \uac71\uc815 \uc911 \uac00\uc7a5 \ud070 \uac83\uc740 `\ubc84\uc804\uc774 \ub2ec\ub77c\uc11c \uc0bd\uc9c8\ud558\uc9c0 \uc54a\uc744\uae4c?`\uc600\uc2b5\ub2c8\ub2e4. \uc81c\uac00 Django\ub97c \ucc98\uc74c \uacf5\ubd80\ud560 \ub54c\ub3c4 \uc774 \uc0dd\uac01\uc744 \uc5c4\uccad \ud588\uc5c8\uac70\ub4e0\uc694. \uc5b4\uca0c\ub4e0 \uacb0\ub860\uc740 \uc800\ub294 \uc774 \ucc45\uc744 \uc77d\uc73c\uba74\uc11c \ubc30\ud3ec \ubd80\ubd84\uc5d0\uc11c \uc0bd\uc9c8\uc744 \uaf64 \ud588\uc2b5\ub2c8\ub2e4. \ud328\ud0a4\uc9c0\uc758 \ubc84\uc804\ub3c4 \ub354 \uc5c5\ub370\uc774\ud2b8 \ub410\uae30\ub3c4 \ud558\uace0, \ub3c4\ucee4\ub97c \ucc98\uc74c \uc0ac\uc6a9\ud574\ubcf4\ub294 \uc785\uc7a5\uc774\uc5c8\uae30 \ub54c\ubb38\uc774\uc8e0. [Do It Django](https://doitdjango.com/) \uc800\uc790\ubd84\uc758 \ube14\ub85c\uadf8(?)\uc5d0\uc11c \ub2e4\ud589\ud788 Q&A\ub97c \uc798 \ubc1b\uc544\uc8fc\uc154\uc11c \uc798 \ud574\uacb0\ud560 \uc218 \uc788\uc5c8\uc2b5\ub2c8\ub2e4. \\n\\n\uc774\ubc88\uc774 \ucc45 \uc77d\uc73c\uba74\uc11c \ub2e4\ub978 \ubb38\uc11c\ub4e4\uc744 \uac00\uc7a5 \ub9ce\uc774 \ubd24\ub358 \ub54c\uc600\ub358 \uac70 \uac19\uc2b5\ub2c8\ub2e4. \uc815\ub9d0 \uadf8 \ud328\ud0a4\uc9c0 Repository \ub4e4\uc5b4\uac00\uc11c issue\ub3c4 \uc77d\uc5b4\ubcf4\uace0, \ud328\ud0a4\uc9c0 \uacf5\uc2dd \ubb38\uc11c, stackoverflow \ub4f1\ub4f1 \uac00\ub9ac\uc9c0 \uc54a\uc558\ub358 \uac70 \uac19\uc2b5\ub2c8\ub2e4. \uadf8\ub798\uc11c \uadf8\ub7f0\uc9c0 \uc774\ubc88 \ucc45\uc740 \uc77d\ub294\ub370 \ub354 \uc624\ub798 \uac78\ub838\uc2b5\ub2c8\ub2e4.\\n\\n\ubd80\ud2b8\uc2a4\ud2b8\ub7a9\ub3c4 \uc774 \ucc45\uc740 \ubc84\uc804\uc744 4.5\uc778\uac00\ub97c \uc0ac\uc6a9\ud558\ub294\ub370 `\ub09c \ucd5c\uc2e0 \uae30\uc220\uc744 \uc0ac\uc6a9\ud574\ubcf4\uace0 \uc2f6\uc5b4!`\ub77c\ub294 \uc0dd\uac01\uc5d0 5.0.1 \ubc84\uc804\uc744 \uc0ac\uc6a9\ud574\ubd24\ub2e4\uac00 \ubd80\ud2b8\uc2a4\ud2b8\ub7a9 \uacf5\uc2dd \ubb38\uc11c\ub97c \ub9ce\uc774 \ucc38\uace0\ud558\uac8c \ub410\uc2b5\ub2c8\ub2e4.\\n\\n\uac00\uc7a5 \uac71\uc815\ud588\ub358 Django\ub294 \ubc84\uc804\uc774 \ub2ec\ub77c\uc84c\ub2e4\uace0 \ud574\ub3c4 \uc0ac\uc6a9\ud558\ub294 \ubd80\ubd84\uc5d0\uc11c \ud06c\uac8c \ub2ec\ub77c\uc9c4 \uac83\uc740 \uc5c6\uc5c8\ub358 \uac83 \uac19\uc2b5\ub2c8\ub2e4. \\n\\n\uc81c\uac00 \uad6c\ub9e4\ud55c \ucc45\uc740 \ucd08\ud310 \ubc1c\ud589\uc774\ub77c \uadf8\ub7f0\uc9c0 \ub4e4\uc5ec\uc4f0\uae30, \uc624\ud0c0\uac00 \uaf64 \uc788\uc5b4\uc11c \uc774\ub7f0 \ubd80\ubd84\uc5d0\uc11c\ub3c4 \uc0bd\uc9c8\ud55c \uac83\ub4e4\ub3c4 \uc788\uc5c8\uc2b5\ub2c8\ub2e4. \ucc45\uc744 \uc790\uc138\ud788 \ubcf4\ub294 \uc785\uc7a5\uc5d0\uc11c \uc774\ub7f0 \ubd80\ubd84\uc740 \uc870\uae08 \uc544\uc26c\uc6e0\uc2b5\ub2c8\ub2e4. \\n\\n## \ub300\uc0c1 \ub3c5\uc790\\n\\n\uc800 \uac19\uc774 \ucd5c\uc2e0 \ubc84\uc804\uc744 \uc0ac\uc6a9\ud574\ubcf4\uc2dc\uace0 \uc2f6\uc740 \ubd84\ub4e4\uc774 \uc788\uc73c\uc2e4 \uc218\ub3c4 \uc788\uc744\ud14c\ub2c8 \uc0dd\ud65c\ucf54\ub529 HTML, CSS, JavaScript\ub97c \ub2e4 \ubcf4\uace0 \uc624\uc168\uc73c\uba74 \uc88b\uaca0\uc2b5\ub2c8\ub2e4. \uc7a5\uace0\ub97c \uacf5\ubd80\ud558\uae30 \uc704\ud55c \ud30c\uc774\uc36c\uc740 \uc6f9 \ud06c\ub864\ub9c1\uae4c\uc9c0 \uc54c\uace0 \uc624\uc2dc\uba74 \ub354\uc6b1 \uc88b\uaca0\uc9c0\ub9cc, \ud544\uc218\ub294 \uc544\ub2d9\ub2c8\ub2e4. \ud544\uc218\ub294 \ud568\uc218, \ud074\ub798\uc2a4, \uc0c1\uc18d \ub4f1\uc758 \ud30c\uc774\uc36c\uc758 \uae30\ubcf8 \uac1c\ub150\ub4e4\uc740 \ubb34\uc870\uac74 \uc54c\uace0 \uc788\uc73c\uc154\uc57c \ud569\ub2c8\ub2e4. \\n\\n\ud30c\uc774\uc36c\uc73c\ub85c \uc6f9 \uac1c\ubc1c\uc744 \uacbd\ud5d8\ud574\ubcf4\uace0 \uc2f6\uc740 \ubd84\uc740 \uc774 \ucc45\uc744 \ucc98\uc74c \uacf5\ubd80\ud558\ub294 \ucc45\uc73c\ub85c \ucd94\ucc9c\ud569\ub2c8\ub2e4. \ube60\ub974\uac8c \uc6f9 \ud504\ub85c\uc81d\ud2b8\ub97c \uac1c\ubc1c, \ubc30\ud3ec, \uc6b4\uc601\ud574\ubcfc \uc218 \uc788\uc2b5\ub2c8\ub2e4."},{"id":"/web-development-with-node-and-express","metadata":{"permalink":"/en/blog/web-development-with-node-and-express","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2021-05-20-web-development-with-node-and-express/web-development-with-node-and-express.md","source":"@site/blog/2021-05-20-web-development-with-node-and-express/web-development-with-node-and-express.md","title":"\ud83d\udcd6 \ud55c \uad8c\uc73c\ub85c \ub05d\ub0b4\ub294 Node & Express","description":"\ubaa8\ub358 \uc6f9\uc744 \uc704\ud55c \uc11c\ubc84 \uc0ac\uc774\ub4dc \uc790\ubc14\uc2a4\ud06c\ub9bd\ud2b8\uc758 \ubaa8\ub4e0 \uac83","date":"2021-05-20T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \ud55c \uad8c\uc73c\ub85c \ub05d\ub0b4\ub294 Node & Express","date":"2021-05-20","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/web-development-with-node-and-express","description":"\ubaa8\ub358 \uc6f9\uc744 \uc704\ud55c \uc11c\ubc84 \uc0ac\uc774\ub4dc \uc790\ubc14\uc2a4\ud06c\ub9bd\ud2b8\uc758 \ubaa8\ub4e0 \uac83","keywords":["Node.js","Express","\ubc31\uc5d4\ub4dc","JavaScirpt"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 Do it! \uc7a5\uace0+\ubd80\ud2b8\uc2a4\ud2b8\ub7a9 \ud30c\uc774\uc36c \uc6f9 \uac1c\ubc1c\uc758 \uc815\uc11d","permalink":"/en/blog/Do-it-django-bootstrap"},"nextItem":{"title":"\ud83d\udcd6 \uc544\ub9c8\uc874 \uc6f9 \uc11c\ube44\uc2a4 AWS Discovery Book","permalink":"/en/blog/AWS-Discovery-Book"}},"content":":::info\\n\ud55c\ube5b\ubbf8\ub514\uc5b4 \\\\<\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4\\\\> \ud65c\ub3d9\uc744 \uc704\ud574\uc11c \ucc45\uc744 \uc81c\uacf5\ubc1b\uc544 \uc791\uc131\ub41c \uc11c\ud3c9\uc785\ub2c8\ub2e4.\\n:::\\n\\n![\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4 2021](../assets/I-am-reviewer-2021.jpg)\\n\\n## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/web-development-with-node-and-express.jpg)](http://www.kyobobook.co.kr/product/detailViewKor.laf?ejkGb=KOR&mallGb=KOR&barcode=9791162244227&orderClick=LAG&Kc=)\\n\\n- \uc81c\ubaa9: \ud55c \uad8c\uc73c\ub85c \ub05d\ub0b4\ub294 Node & Express\\n- \uc800\uc790: Ethan Brown\\n- \uc5ed\uc790: \ud55c\uc120\uc6a9\\n- \ucd9c\ud310\uc0ac: \ud55c\ube5b\ubbf8\ub514\uc5b4\\n- \ucd9c\uac04: 2021-05-01\\n\\n{/* truncate */}\\n\\n## Intro\\n\\n\uc774 \ucc45\uc740 \ud55c\ube5b\ubbf8\ub514\uc5b4\uc5d0\uc11c \uc9c4\ud589\ud558\ub294 `\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4 2021`\uc5d0 \ucc38\uc5ec\ud558\uac8c \ub418\uc5b4 \uc77d\uac8c \ub410\uc2b5\ub2c8\ub2e4. \ub9e4 \ub2ec\ub9c8\ub2e4 \ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4 \ud65c\ub3d9\uc5d0 \uc81c\uacf5\ub418\ub294 \ucc45\uc740 \uc5b8\uc81c\ub098 \uc88b\uc740 \ucc45\ub4e4\uc774\uc5c8\uc2b5\ub2c8\ub2e4. \uadfc\ub370 \ud2b9\ud788 \uc774\ubc88 5\uc6d4 \ub2ec \ub9ac\ubdf0 \ud76c\ub9dd \ub3c4\uc11c\ub4e4\uc740 \uc81c\uac00 \uac1c\uc778\uc801\uc73c\ub85c \uacf5\ubd80\ud558\uace0 \uc2f6\uc740 \ubd84\uc57c\ub4e4\uc774\uc5c8\uae30\uc5d0 \uc120\ud0dd\ud560 \ub54c \ub108\ubb34 \uace0\ubbfc\ub410\uc2b5\ub2c8\ub2e4. \uacb0\uad6d `\ub370\uc774\ud130 \uacfc\ud559\uc744 \uc704\ud55c \ud1b5\uacc4(2\ud310)`, `\uace0\uc131\ub2a5 \ud30c\uc774\uc36c(2\ud310)`, `\ud55c \uad8c\uc73c\ub85c \ub05d\ub0b4\ub294 Node & Express(2\ud310)`\uc744 \uc120\ud0dd\ud588\uc9c0\ub9cc \ub9ac\uc2a4\ud2b8\uc5d0 \uc788\ub358 \ub2e4\ub978 \ucc45\ub4e4\ub3c4 \uae30\ud68c\uac00 \ub41c\ub2e4\uba74 \uc77d\uace0 \uc774 \ube14\ub85c\uadf8\uc5d0 \ub9ac\ubdf0\ub97c \uc4f8 \uc608\uc815\uc785\ub2c8\ub2e4.\\n\\n## Book Review\\n\\n\ucc98\uc74c \uc774 \ucc45\uc744 \ubc1b\uc558\uc744 \ub54c\ub294 Node.js\uc640 Express\ub97c \ud55c \uad8c\uc5d0 \ub05d\ub0b4\uae30\uc5d0\ub294 \uc5b4\ub835\uc9c0 \uc54a\uc744\uae4c \uc0dd\uac01\ud588\uc2b5\ub2c8\ub2e4. \uadf8\ub7ec\ub098 \uc77d\uc5b4\ubcf4\ub2c8 \uc120\uc218 \uc9c0\uc2dd\uc774 \uc788\uc73c\uba70, \uad6c\uae00\ub9c1\uc744 \ud560 \uc218 \uc788\ub2e4\ub294 \uac00\uc815\ud558\uc5d0 \uacf5\ubd80\ud558\uae30\uc5d0\ub294 \uc88b\uc740 \ucc45\uc774\ub77c\uace0 \ub290\uaf08\uc2b5\ub2c8\ub2e4. \\n\\n\ub2e8\uc21c\ud788 \uc6f9 \uc0ac\uc774\ud2b8\ub97c \ub9cc\ub4dc\ub294 \uac83\uc744 \ubaa9\uc801\uc73c\ub85c \ud558\ub294 \uac83\uc774 \uc544\ub2cc \ubc30\ud3ec \uacfc\uc815\uae4c\uc9c0 \uacf5\ubd80\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \ub610\ud55c, \uc2e4\ubb34 \ud658\uacbd\uacfc \uc720\uc0ac\ud55c \ud658\uacbd\uc5d0\uc11c \ud14c\uc2a4\ud2b8\ub97c \ud558\uba70, DB \uc5f0\uacb0\ub3c4 \ud574\ubcf4\uace0 \uc720\uc9c0 \ubcf4\uc218 \uacc4\ud68d\ub3c4 \uc870\uae08 \ub2e4\ub8f9\ub2c8\ub2e4. \uc774\ubc16\uc5d0\ub3c4 REST API, HTTP, QA, \ucfe0\ud0a4 \ub4f1\uc744 \uac04\ub2e8\ud558\uac8c \ub2e4\ub904\ubd05\ub2c8\ub2e4. \uc810\uc810 \ub05d\uc73c\ub85c \uac00\uba74 \ub370\uc774\ud130\ubca0\uc774\uc2a4\ub3c4 \ub2e4\ub8e8\uace0, React\ub3c4 \uc870\uae08 \uc0ac\uc6a9\ud574\ubcf4\ub294 \ucc45\uc774\ub2c8 \uc774\ub7f0 \ubd80\ubd84\uc5d0\uc11c\ub3c4 \uc120\uc218 \uc9c0\uc2dd\uc774 \uc788\uc73c\uba74 \uc88b\uc744 \uac83\uc774\ub77c\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4.\\n\\n\ucda9\ubd84\ud55c \ud575\uc2ec \ub0b4\uc6a9\ub9cc \uc804\ub2ec\ud558\uba70, \ubc29\ub300\ud55c \ub0b4\uc6a9\ub4e4\uc744 \uae4a\uac8c\uae4c\uc9c0 \ub2e4\ub8e8\uc9c0 \uc54a\uc2b5\ub2c8\ub2e4. \uc774\ub7f0 \ubd80\ubd84\uc740 node.js\uc5d0 \ub300\ud574 \uc219\ub828\uc790\uc774\uc2dc\uba74 \uc88b\uc544\ud558\uc2e4 \uc218 \uc788\uaca0\uc9c0\ub9cc, \uac1c\uc778\uc801\uc73c\ub85c \uc800 \uac19\uc740 \ucd08\ubcf4\uc790\uc5d0\uac8c\ub294 \ub9ce\uc774 \uc5b4\ub824\uc6e0\uc2b5\ub2c8\ub2e4.\\n\\n\uc544\ubb34\ub798\ub3c4 \ucc45\uc758 \ud398\uc774\uc9c0 \uc218\ub97c \uc904\uc774\uae30 \uc704\ud574\uc11c \ucc45\uc5d0\uc11c\ub294 \uc608\uc81c\uc758 \uaf2d \ud544\uc694\ud55c \ubd80\ubd84\ub9cc \uc18c\uac1c\ub418\uc5b4 \uc788\uc2b5\ub2c8\ub2e4. \uc804\uccb4 \uad6c\uc870\ub97c \ud655\uc778\ud558\ub294 \uac83\uc740 \uc800\uc790\uc758 [GitHub](https://github.com/EthanRBrown/web-development-with-node-and-express-2e)\uc744 \ucc38\uace0\ud558\uc2dc\uba74 \ub3c4\uc6c0\uc774 \ub420 \uac83\uc785\ub2c8\ub2e4. \\n\\n## \ub300\uc0c1 \ub3c5\uc790\\n\\n\uc800\ub294 Node.js\uc5d0 \uad00\ud55c \uc9c0\uc2dd\uc774 \uc5c6\uc73c\uba70, \uc790\ubc14\uc2a4\ud06c\ub9bd\ud2b8\uc5d0 \ub300\ud574\uc11c\ub294 \uc544\uc8fc \uc870\uae08\uc758 \uc9c0\uc2dd\ub9cc \uc788\uc5c8\uc2b5\ub2c8\ub2e4. \uc800\ucc98\ub7fc \uc790\ubc14\uc2a4\ud06c\ub9bd\ud2b8\uc5d0 \ub300\ud55c \uae4a\uc740 \uc774\ud574\uac00 \uc5c6\uc73c\uc2e0 \ubd84\ub4e4\uc740 \uc77d\uae30 \ud798\ub4dc\uc2e0 \ucc45\uc774\ubbc0\ub85c \uc774 \ucc45\uc744 \uc0ac\uae30 \uc804\uc5d0 \uc870\uae08 \uace0\ub824\ud558\uc2dc\uae38 \ubc14\ub77c\uaca0\uc2b5\ub2c8\ub2e4. \\n\\n\uc774 \ucc45\uc758 \ucd9c\ud310\uc0ac \ub9ac\ubdf0\uc5d0\ub3c4 \uc0ac\uc804 \uc9c0\uc2dd\uc5d0 `\uc790\ubc14\uc2a4\ud06c\ub9bd\ud2b8 \ucd08\uc911\uae09`, `\ub178\ub4dc & \uc775\uc2a4\ud504\ub808\uc2a4 \ucd08\uae09`\uc758 \uc9c0\uc2dd\uc774 \ud544\uc694\ud558\ub2e4\uace0 \uae30\uc220\ub418\uc5b4 \uc788\uc2b5\ub2c8\ub2e4.\\n\\n\ubb3c\ub860 \uc800\ucc98\ub7fc \uc0ac\uc804 \uc9c0\uc2dd\uc774 \uc5c6\uc73c\uba74 \uad6c\uae00\ub9c1 \ud558\uba74\uc11c \ub2e4\ub978 \uac15\uc758, \ucc45\ub3c4 \ucc38\uace0\ud558\uba74\uc11c \ube61\uc138\uac8c \uacf5\ubd80\ud558\uba74 \ub429\ub2c8\ub2e4. \\n\\nnode.js\uc758 \uac1c\ub150\uc740 \uc5b4\ub290\uc815\ub3c4 \uac16\uace0 \uc788\uc9c0\ub9cc, \ud504\ub85c\uc81d\ud2b8 \uacbd\ud5d8\uc774 \ubd80\uc871\ud558\uc2e0 \ubd84\ub4e4\uaed8 \ucd94\ucc9c\ub4dc\ub9bd\ub2c8\ub2e4. \ud2b9\ud788 \uc6f9 \uc0ac\uc774\ud2b8\ub97c \ubc30\ud3ec\uae4c\uc9c0 \ud558\ub294 \ud504\ub85c\uc81d\ud2b8\ub97c \ud574\ubcf4\uace0 \uc2f6\uc740 \ubd84\ub4e4\uaed8 \ub3c4\uc6c0\uc774 \ub420 \uac83\uc774\ub77c\uace0 \uc608\uc0c1\ub429\ub2c8\ub2e4."},{"id":"/AWS-Discovery-Book","metadata":{"permalink":"/en/blog/AWS-Discovery-Book","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2021-05-14-AWS-Discovery-Book/AWS-Discovery-Book.md","source":"@site/blog/2021-05-14-AWS-Discovery-Book/AWS-Discovery-Book.md","title":"\ud83d\udcd6 \uc544\ub9c8\uc874 \uc6f9 \uc11c\ube44\uc2a4 AWS Discovery Book","description":"\ud074\ub77c\uc6b0\ub4dc \uc11c\ube44\uc2a4 \uac1c\ub150\uc744 \uc774\ud574\ud558\uace0 \uc9c1\uc811 \uad6c\uc131\ud574\ubcf4\uae30","date":"2021-05-14T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \uc544\ub9c8\uc874 \uc6f9 \uc11c\ube44\uc2a4 AWS Discovery Book","date":"2021-05-14","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/AWS-Discovery-Book","description":"\ud074\ub77c\uc6b0\ub4dc \uc11c\ube44\uc2a4 \uac1c\ub150\uc744 \uc774\ud574\ud558\uace0 \uc9c1\uc811 \uad6c\uc131\ud574\ubcf4\uae30","keywords":["AWS","\ud074\ub77c\uc6b0\ub4dc","Amazon Web Service"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \ud55c \uad8c\uc73c\ub85c \ub05d\ub0b4\ub294 Node & Express","permalink":"/en/blog/web-development-with-node-and-express"},"nextItem":{"title":"\ud83d\udcd6 \ub7ec\ub2dd SQL","permalink":"/en/blog/Learning-SQL"}},"content":"## \u2139\ufe0f Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/aws-discovery-book.jpg)](https://product.kyobobook.co.kr/detail/S000000833222)\\n\\n- \uc81c\ubaa9: \uc544\ub9c8\uc874 \uc6f9 \uc11c\ube44\uc2a4 AWS Discovery Book\\n- \uc800\uc790: \uad8c\uc601\ud658\\n- \ucd9c\ud310\uc0ac: \uc815\ubcf4\ubb38\ud654\uc0ac\\n- \ucd9c\uac04: 2019-02-10\\n\\n{/* truncate */}\\n\\n## \ud83c\udfac Intro\\n\\n\ucd5c\uadfc \ubc31\uc5d4\ub4dc \uac1c\ubc1c\uc790 \ucc44\uc6a9 \uacf5\uace0\ub97c \uc77d\uc5b4\ubcf4\uba74 \ud074\ub77c\uc6b0\ub4dc \ud658\uacbd\uc5d0\uc11c\uc758 \uacbd\ud5d8\uc744 \uc694\uad6c\ud558\ub294 \uae30\uc5c5\ub4e4\uc774 \ub9ce\uc544\uc84c\ub2e4. \ud2b9\ud788 AWS\ub294 \ub300\ubd80\ubd84\uc758 \uae30\uc5c5\uc5d0\uc11c \uc4f0\uc774\uace0 \uc788\uae30\uc5d0 AWS \uc0ac\uc6a9 \uacbd\ud5d8\uc774 \uc788\ub294 \uc0ac\ub78c\uc744 \ub354\uc6b1 \uc120\ud638\ud55c\ub2e4. \ub098\ub3c4 \ubc31\uc5d4\ub4dc \uac1c\ubc1c\uc790\ub97c \ubaa9\ud45c\ub85c \ud558\uace0 \uc788\uae30\uc5d0 AWS\ub97c \uacbd\ud5d8\ud574\ubd10\uc57c\uaca0\ub2e4\ub294 \uc0dd\uac01\uc5d0 \uc774 \ucc45\uc744 \uc77d\uac8c \ub410\ub2e4.\\n\\n**\\"AWS \uacf5\uc2dd \ubb38\uc11c\uac00 \ub108\ubb34 \uc798 \ub418\uc5b4 \uc788\ub294\ub370 \uad73\uc774 \ucc45\uc744 \ubd10\uc57c \ud560\uae4c?\\"**\ub77c\ub294 \uc0dd\uac01\ub3c4 \ud588\uc9c0\ub9cc \uadf8 \ubb38\uc11c\ub97c \ub2e4 \uc77d\uc5b4\ubcfc \uc790\uc2e0\uc774 \uc5c6\uc5c8\uae30\uc5d0 \ud575\uc2ec\ub9cc \ube60\ub974\uac8c \uc2e4\uc2b5\ud560 \uc218 \uc788\ub294 \ucc45\uc744 \uc6d0\ud588\ub2e4. [\ub530\ub77c\ud558\uba70 \ubc30\uc6b0\ub294 AWS \ub124\ud2b8\uc6cc\ud06c \uc785\ubb38](https://product.kyobobook.co.kr/detail/S000060612654), [\uc11c\ube44\uc2a4 \uc6b4\uc601\uc774 \uc26c\uc6cc\uc9c0\ub294 AWS \uc778\ud504\ub77c \uad6c\ucd95 \uac00\uc774\ub4dc](https://product.kyobobook.co.kr/detail/S000001766358) \ub4f1\uc758 \ucc45\ub4e4\ub3c4 \uace0\ub824\ud588\uc5c8\uc2b5\ub2c8\ub2e4\ub9cc, \uc774 \ucc45\uc774 \uc804\uc790\ub3c4\uc11c\uad00\uc5d0 \uc788\uae38\ub798 \ube4c\ub824 \uc77d\uc5c8\ub2e4. [\ub530\ub77c\ud558\uba70 \ubc30\uc6b0\ub294 AWS \ub124\ud2b8\uc6cc\ud06c \uc785\ubb38](https://product.kyobobook.co.kr/detail/S000060612654) \ucc45\uc740 2020\ub144 \ud6c4\ubc18\uc5d0 \ub098\uc628 \ucc45\uc774\ub77c \ub354 \uace0\ubbfc\ub410\uc5c8\ub2e4. \ub098\uba38\uc9c0 \ucc45\ub4e4\uc740 \ub098\uc911\uc5d0 \uae30\ud68c\uac00 \ub41c\ub2e4\uba74 \uc77d\uc5b4\ubcfc \uc608\uc815\uc774\ub2e4.\\n\\n## \ud83d\udcd6 Book Review\\n\\n### \uad6c\uc131\\n\\n\ud55c \uc7a5\uc5d0 \uac1c\ub150->\uac1c\ub150\uc5d0 \ub9de\ub294 AWS \uc11c\ube44\uc2a4->\uc2e4\uc2b5->\uc5d0\ud544\ub85c\uadf8 \uc21c\uc11c\ub85c \uad6c\uc131\ub418\uc5b4 \uc788\ub2e4. \ud55c \uc7a5\uc774 \ub05d\ub0a0 \ub54c\ub9c8\ub2e4 \uc5d0\ud544\ub85c\uadf8\uc5d0\ub294 \ub3c4\uc6c0\uc774 \ub418\ub294 \ub0b4\uc6a9 \ub610\ub294 \uc5ed\uc0ac \ub4f1\uc774 \ub098\uc640\uc11c \uc800\uc5d0\uac8c\ub294 \uaf64 \uc7ac\ubc0c\ub294 \ubd80\ubd84\uc774\uc5c8\ub2e4. \ud2b9\ud788 8\uc7a5\uc5d0\uc11c Auto Scaling\uc744 \uacf5\ubd80\ud560 \ub54c\ub294 `auto scaling \uae30\ub2a5\uc774 \uc2e4\uc81c\ub85c \uc0ac\uc6a9\ud560 \uc218 \uc788\uc744\uae4c?, \uacfc\uc5f0 \uc0ac\uc6a9\ud558\ub294 \uc0ac\ub78c\uc774 \uc788\uc744\uae4c?`\ub77c\ub294 \uc758\ubb38\uc774 \uc788\uc5c8\ub294\ub370 \uc5d0\ud544\ub85c\uadf8\uc5d0 \ub370\ube0c\uc2dc\uc2a4\ud130\uc988\uc5d0\uc11c \ucfe0\ud0a4\ub7f0 \uc11c\ube44\uc2a4\ub97c \uc6b4\uc601\ud560 \ub54c \uc0ac\uc6a9\ud588\ub2e4\ub294 \uc0ac\ub840\uac00 \uc788\uc5c8\ub2e4. \ud604\uc5c5\uc5d0\uc11c\uc758 \uc0ac\ub840\uac00 \uc801\ud600 \uc788\uc5b4\uc11c \uadf8\ub7f0\uc9c0 \ub354\uc6b1 \uadf8 \uae30\uc220\uc5d0 \ud765\ubbf8\uac00 \uac14\uc5c8\ub2e4. \\n\\n\uc774 \ucc45\uc740 AWS\uc758 \uac1c\ub150\uc744 \uac04\ub2e8\ud558\uac8c \uc775\ud788\uae30 \uc88b\uc740 \uc785\ubb38 \ucc45\uc774\ub77c\uace0 \uc0dd\uac01\ud55c\ub2e4. \ub9e4\uc6b0 \uae4a\uac8c \uc54c\ub824\uc8fc\uc9c0\ub294 \uc54a\uc9c0\ub9cc \ud575\uc2ec\uc774\ub77c\uace0 \uc0dd\uac01\uc774 \ub4dc\ub294 \uc8fc\uc694 \uc11c\ube44\uc2a4\ub4e4\uc740 \ub2e4 \uc54c\ub824\uc900\ub2e4. \ub610\ud55c, \uc2e4\uc2b5\uc774 \uc5c4\uccad \ub9ce\uc73c\uba70 \ubaa8\ub450 \uc774\ubbf8\uc9c0\ub85c \uc790\uc138\ud558\uac8c \uc124\uba85\ub418\uc5b4 \uc788\ub2e4. AWS\ub97c \ucc98\uc74c \uacbd\ud5d8\ud558\ub294 \ucd08\ubcf4\uc790\ub4e4\ub3c4 AWS \ud68c\uc6d0\uac00\uc785\ubd80\ud130 EC2, S3, VPC \ub4f1\uc758 \uac83\ub4e4\uc744 \uce5c\uc808\ud558\uac8c \uac00\ub974\uccd0\uc900\ub2e4. \ubb3c\ub860 2019\ub144\uc5d0 \ub098\uc628 \ucc45\uc774\ub77c \uba54\ub274 \ub610\ub294 \uba85\uce6d\uc774 \uc870\uae08 \ub2e4\ub974\ub2e4. \uc774\ub7f0 \ubd80\ubd84\uc740 \uad6c\uae00\ub9c1 \ud558\uba74\uc11c \uacf5\ubd80\ud558\uba74 \ub41c\ub2e4.\\n\\n\ub9c8\uc9c0\ub9c9 12\uc7a5, 13\uc7a5\uc5d0\ub294 AWS certification\uc744 \uc5b4\ub5bb\uac8c \uc900\ube44\ud558\uace0 \uc5b4\ub5bb\uac8c \uacf5\ubd80\ud574\uc57c \ud558\ub294 \uc9c0\uc5d0 \uad00\ud574 \uc124\uba85\ud55c\ub2e4. \uc2dc\ud5d8 \uc751\uc2dc\ub098 \uc5b4\ub5a4 \uc0ac\uc774\ud2b8\uc5d0\uc11c \uacf5\ubd80\ub97c \ud574\uc57c \ud558\ub294\uc9c0 \ub4f1\uc744 \uc54c\ub824\uc8fc\ub294\ub370 \uc774\ubbf8\uc9c0\ub85c \uc798 \uc124\uba85\uc774 \ub418\uc5b4 \uc788\ub2e4. 2\ub144\uc774 \uc9c0\ub09c \uc9c0\uae08\ub3c4 \uc720\ud6a8\ud55c \ubc29\ubc95\uc778\uc9c0\ub294 \uc798 \ubaa8\ub974\uaca0\uc73c\ub098 AWS \uc790\uaca9\uc99d\uc5d0 \uad00\uc2ec\uc774 \uc788\uac70\ub098 \ud544\uc694\ud55c \uc0ac\ub78c\uc740 \ucc38\uace0\ud558\uba74 \ub41c\ub2e4.\\n\\n### \ud504\ub9ac\ud2f0\uc5b4 \ud65c\uc6a9\\n\\n\ub098\ub294 \uc608\uc804\uc5d0 AWS\ub97c \ucc98\uc74c \uacf5\ubd80\ud560 \ub54c \\"AWS\ub294 \uc720\ub8cc \uc11c\ube44\uc2a4\uc778\ub370 \uc5f0\uc2b5\uc744 \uc704\ud574\uc11c \uacb0\uc81c\uae4c\uc9c0 \ud574\uc57c \ud558\ub294\uac00?\\"\ub77c\ub294 \uc0dd\uac01\uc744 \ud588\uc5c8\ub2e4. \uc54c\uace0\ubcf4\ub2c8 \uac04\ub2e8\ud55c \uc2e4\uc2b5\ub4e4\uc740 \uacb0\uc81c\uac00 \ud544\uc694 \uc5c6\uc5c8\uace0, \uc774 \ucc45 \ub610\ud55c `AWS \ud504\ub9ac \ud2f0\uc5b4`\ub97c \ud65c\uc6a9\ud558\uae30\uc5d0 \uadf8\ub7f0 \uac71\uc815\uc740 \ud560 \ud544\uc694\uac00 \uc5c6\ub2e4. \ucc45\uc5d0\uc11c \ub098\uc628\ub300\ub85c \uc798 \ub530\ub77c\ud55c\ub2e4\uba74 \uacb0\uc81c\uac00 \ub420 \uc77c\uc774 \uc5c6\ub2e4. \uc2e4\uc2b5\uc774 \ub05d\ub0a0 \ub54c\ub9c8\ub2e4 \ubc14\ub85c\ubc14\ub85c \uc0ac\uc6a9\ud55c \uc11c\ube44\uc2a4\ub4e4\uc744 \uc885\ub8cc\ud558\uac70\ub098, \uc911\uc9c0\uc2dc\ud0b4\uc73c\ub85c\uc368 \uacfc\uae08\uc774 \ub420 \uc77c\uc774 \uc5c6\ub294 \uac83\uc774\ub2e4.\\n\\n## \ud83e\uddd1\u200d\ud83c\udfeb \ub300\uc0c1 \ub3c5\uc790\\n\\nAWS \uacbd\ud5d8\uc744 \ud574\ubcf4\uace0 \uc2f6\uc740 \uc785\ubb38\uc790\uc5d0\uac8c \uc774 \ucc45\uc744 \ucd94\ucc9c\ud55c\ub2e4. \ud63c\uc790 \ud558\uba74 \uc5b4\ub824\uc6b8 \uc218 \uc788\uc5c8\ub358 \uc2e4\uc2b5\ub4e4\uc744 \uc2dc\ud589\ucc29\uc624 \uc5c6\uc774 \ub3c4\uc640\uc900\ub2e4. \ub610\ud55c, \uc774\ubbf8\uc9c0\ub85c \uc124\uba85\uc774 \uc798 \ub418\uc5b4 \uc788\uc5b4\uc11c \uc804\ubc18\uc801\uc73c\ub85c \uce5c\uc808\ud558\ub2e4 \ub290\uaf08\ub2e4. \uadf8\ub7ec\ub098 \uc774 \ucc45\uc740 \uacb0\uad6d \ud074\ub77c\uc6b0\ub4dc\ub97c \uacf5\ubd80\ud558\ub294 \ucc45\uc774\uae30\uc5d0 \uae30\ubcf8\uc801\uc778 \ud130\ubbf8\ub110 \uc0ac\uc6a9\ubc95, \ub124\ud2b8\uc6cc\ud06c \ub4f1\uc758 \uae30\ucd08\uc801\uc778 CS \uc9c0\uc2dd\uc740 \ud544\uc694\ud558\ub2e4. \uac04\ud639 \uadf8\ub7f0 \uac1c\ub150\ub4e4\uc744 \uc124\uba85\ud574\uc8fc\uae30\ub294 \ud558\uc9c0\ub9cc \uc704\uc5d0\uc11c\ub3c4 \uc5b8\uae09\ud588\ub4ef\uc774 \uc790\uc138\ud788 \uc124\uba85\ud558\uc9c0\ub294 \uc54a\uace0, \uac04\ub2e8\ud558\uac8c \uc124\uba85\ud55c\ub2e4. \ubaa8\ub974\ub294 \ubd80\ubd84\uc774 \ub098\uc62c \ub54c\ub9c8\ub2e4 \uacc4\uc18d \uad6c\uae00\ub9c1\uc744 \ud558\uba74\uc11c \ucc45\uc744 \uc77d\ub294 \uac83\uc744 \ucd94\ucc9c\ud55c\ub2e4. \ub098\ub3c4 \uadf8\ub807\uac8c \ucc45\uc744 \uc77d\uc5c8\uace0, CS \uc9c0\uc2dd\uc744 \ub354 \uc5f4\uc2ec\ud788 \uacf5\ubd80 \ud574\uc57c\uaca0\ub2e4\ub294 \uc0dd\uac01\uc774 \ub4e4\uc5c8\ub2e4."},{"id":"/Learning-SQL","metadata":{"permalink":"/en/blog/Learning-SQL","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2021-04-17-learning-sql/Learning-SQL.md","source":"@site/blog/2021-04-17-learning-sql/Learning-SQL.md","title":"\ud83d\udcd6 \ub7ec\ub2dd SQL","description":"\ub370\uc774\ud130 \uc0dd\uc131, \uac80\uc0c9, \uc870\uc791\uae4c\uc9c0 \ub370\uc774\ud130 \uc81c\ub300\ub85c \uc8fc\ubb34\ub974\uae30","date":"2021-04-17T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \ub7ec\ub2dd SQL","date":"2021-04-17","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/Learning-SQL","description":"\ub370\uc774\ud130 \uc0dd\uc131, \uac80\uc0c9, \uc870\uc791\uae4c\uc9c0 \ub370\uc774\ud130 \uc81c\ub300\ub85c \uc8fc\ubb34\ub974\uae30","keywords":["SQL","RDBMS","MySQL"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \uc544\ub9c8\uc874 \uc6f9 \uc11c\ube44\uc2a4 AWS Discovery Book","permalink":"/en/blog/AWS-Discovery-Book"},"nextItem":{"title":"\ud83d\udcd6 GAN \uccab\uac78\uc74c","permalink":"/en/blog/make-your-first-gan"}},"content":":::info\\n\ud55c\ube5b\ubbf8\ub514\uc5b4 \\\\<\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4\\\\> \ud65c\ub3d9\uc744 \uc704\ud574\uc11c \ucc45\uc744 \uc81c\uacf5\ubc1b\uc544 \uc791\uc131\ub41c \uc11c\ud3c9\uc785\ub2c8\ub2e4.\\n:::\\n\\n![\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4 2021](../assets/I-am-reviewer-2021.jpg)\\n\\n## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/Learning-SQL.jpg)](https://product.kyobobook.co.kr/detail/S000001810371)\\n\\n- \uc81c\ubaa9: \ub7ec\ub2dd SQL\\n- \uc800\uc790: Alan Beaulieu\\n- \uc5ed\uc790: \ub958\uc218\ubbf8, \uc1a1\ud76c\uc815\\n- \ucd9c\ud310\uc0ac: \ud55c\ube5b\ubbf8\ub514\uc5b4\\n- \ucd9c\uac04: 2021-03-30\\n\\n{/* truncate */}\\n\\n## \ud83c\udfac Intro\\n\\n\ub098\ub294 \ucef4\ud4e8\ud130 \uacf5\ud559 \uc804\uacf5\uc0dd\uc774\uc9c0\ub9cc \uc544\uc9c1\uae4c\uc9c0 \ub370\uc774\ud130\ubca0\uc774\uc2a4 \uacf5\ubd80\ub97c \uc81c\ub300\ub85c \ud574\ubcf8 \uc801\uc774 \uc5c6\ub2e4. \uc6b0\ub9ac \ud559\uacfc\ub294 Computer Science\ubcf4\ub2e4 Computer Engineering\uc5d0 \ub354 \uac00\uae4c\uc6b4 \ud559\uacfc\uc774\uae30\uc5d0 4\ud559\ub144 2\ud559\uae30\uc5d0 \ub370\uc774\ud130\ubca0\uc774\uc2a4 \uacfc\ubaa9\uc774 \ubc30\uc815\ub418\uc5b4 \uc788\ub2e4. \uadf8\ub7ec\ub098 \ub098\ub294 \uc6f9 \ubc31\uc5d4\ub4dc\ub97c \uacf5\ubd80\ud558\ub2e4 \ubcf4\ub2c8 \uc790\uc5f0\uc2a4\ub7fd\uac8c SQL \uacf5\ubd80\uc758 \ud544\uc694\uc131\uc744 \ub290\ub07c\uac8c \ub410\uace0 \\"\uc5b8\uc820\uac00 \uacf5\ubd80\ub97c \ud574\uc57c\uaca0\ub2e4.\\"\ub77c\uace0 \uc0dd\uac01\ud588\ub2e4. \ub9c8\uce68 \ud55c\ube5b\ubbf8\ub514\uc5b4 \ucd9c\ud310\uc0ac\uc758 `\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4`\uc5d0 \ucc38\uc5ec\ud558\uac8c \ub418\uc5b4 \uc774 \ucc45\uc744 \uc77d\uc744 \uc218 \uc788\uac8c \ub410\ub2e4.\\n\\n## \ud83d\udcd6 Book Review\\n\\n### SQL\\n\\n\uc774 \ucc45\uc740 \uc81c\ubaa9\uc5d0\uc11c\ub3c4 \uc54c \uc218 \uc788\ub4ef\uc774 SQL\uc744 \uacf5\ubd80\ud558\ub294 \ucc45\uc774\ub2e4. SQL\uc740 MySQL, ORACLE DATABASE, IBM DB2, Microsoft SQL Server, PostgreSQL \ub465\uacfc \uac19\uc774 \uc88b\ub958\uac00 \ub2e4\uc591\ud558\ub2e4. \uadf8\uc911 \uc774 \ucc45\uc740 \uac00\uc7a5 \ub300\uc911\uc801\uc774\ub77c\uace0 \ud560 \uc218 \uc788\ub294 MySQL\uc744 \uc911\uc810\uc73c\ub85c \uc124\uba85\ud55c\ub2e4. \uac00\ub054 MySQL \uc774\uc678\uc758 SQL \uc608\uc81c\ub4e4\uc774 \ub098\uc624\uae30\ub3c4 \ud558\uc9c0\ub9cc SQL \ubb38\ubc95 \uc790\uccb4\uac00 \uac70\uc758 \ube44\uc2b7\ud558\uc5ec \ud070 \uc5b4\ub824\uc6c0\uc740 \uc5c6\ub2e4.\\n\\n### \uc2e4\uc2b5 \ud658\uacbd\\n\\n![katacoda](mysql-environment-katacoda.jpg)\\n\\n\ub300\ubd80\ubd84\uc758 SQL \ucc45\uc758 \uc608\uc81c\ub4e4\uc740 \ud14c\uc774\ube14\uc744 \uc9c1\uc811 \uc0dd\uc131\ud558\uc5ec \ub370\uc774\ud130\ub97c \ucd94\uac00\ud574\ubcf4\uae30\ub3c4 \ud558\uba70, \uc0ac\uc804\uc5d0 \ubbf8\ub9ac \ub9cc\ub4e4\uc5b4 \ub454 \ub370\uc774\ud130\ubca0\uc774\uc2a4\ub97c \uc9c1\uc811 \ub2e4\uc6b4 \ubc1b\uc544 \uc2e4\uc2b5\ud560 \uc218 \uc788\ub3c4\ub85d \ub418\uc5b4 \uc788\ub2e4. \uc774 \ucc45\uc5d0\uc11c\ub294 Sakila\ub77c\ub294 \ub370\uc774\ud130\ubca0\uc774\uc2a4\ub97c \uc0ac\uc6a9\ud55c\ub2e4. Sakila \ub370\uc774\ud130\ubca0\uc774\uc2a4\ub294 MySQL\uc5d0\uc11c \uac1c\ubc1c\ub41c \ub370\uc774\ud130\ubca0\uc774\uc2a4\ub85c \ucc45 \ub610\ub294 \ubb38\uc11c\uc758 \ud29c\ud1a0\ub9ac\uc5bc \ub4f1\uc758 \uc608\uc81c\uc5d0 \uc0ac\uc6a9\ud560 \uc218 \uc788\ub294 \ub370\uc774\ud130\ubca0\uc774\uc2a4\uc774\ub2e4.\\n\\n\uc774 \ucc45\uc744 \uacf5\ubd80\ud558\uae30 \uc704\ud55c \ud658\uacbd\uc744 \uc138\ud305\ud558\uae30 \uc704\ud574\uc11c\ub294 MySQL\uc744 \uc124\uce58\ud558\uace0 Sakila \ub370\uc774\ud130\ubca0\uc774\uc2a4\uae4c\uc9c0 \ubc1b\uc544\uc57c \ud558\uc9c0\ub9cc \ud658\uacbd\uc744 \uad6c\uc131\ud558\ub294 \uac83\uc774 \uc5b4\ub824\uc6b4 \uc0ac\ub78c\ub4e4\uc744 \uc704\ud574 \uc774 \ucc45\uc758 \uc6d0\uc11c \ucd9c\ud310\uc0ac\uc778 O\'REILLY\uc5d0\uc11c [Katacoda](https://www.katacoda.com/)\ub77c\ub294 \uc2e4\uc2b5 \ud658\uacbd\uc744 \uc81c\uacf5\ud55c\ub2e4. \uc774\uc81c \ub9c9 \uacf5\ubd80\ub97c \uc2dc\uc791\ud55c \uc0ac\ub78c\ub4e4 \uc785\uc7a5\uc5d0\uc11c\ub294 \ub9e4\uc6b0 \ud3b8\ud558\uac8c \uacf5\ubd80\ud560 \uc218 \uc788\ub2e4. \uc774\ub807\uac8c \uacf5\ubd80\ud560 \uc218 \uc788\ub294 \ud658\uacbd\uc744 \uc81c\uacf5\ud558\ub294 \uac83\uc774 \uc26c\uc6b4 \uc77c\uc740 \uc544\ub2c8\uae30\uc5d0 \uac00\uc7a5 \uc778\uc0c1\uc801\uc778 \ubd80\ubd84\uc774\uc5c8\ub2e4. \\n\\n![wsl2](mysql-environment-wsl2.jpg)\\n\\n\uc774\uc678\uc5d0\ub3c4 \ud658\uacbd\uc744 \uad6c\ucd95\ud560 \uc218 \uc788\ub294 \uc0ac\ub78c\uc740 \uc2a4\uc2a4\ub85c \ud658\uacbd\uc744 \uad6c\ucd95\ud558\uc5ec \ucc45\uc758 \uc608\uc81c\ub97c \ub530\ub77c \ud558\uba70 \uc2e4\uc2b5\uc744 \uc9c4\ud589\ud558\uba74 \ub41c\ub2e4. \ub098\ub294 WSL2(Windows Subsystem for Linux 2)\ub97c \uc0ac\uc6a9\ud558\uc5ec MySQL \ud658\uacbd\uc744 \uad6c\ucd95\ud588\ub2e4. \ucc98\uc74c\uc5d0\ub294 Windows\uc5d0\uc11c MySQL\uc744 \uc0ac\uc6a9\ud558\ub824 \ud588\uc73c\ub098 \uc774 \ucc45\uc740 \ud130\ubbf8\ub110\uc5d0\uc11c \ucffc\ub9ac\ub97c \uc791\uc131\ud558\uae30\uc5d0 \ub098\ub3c4 MySQL Workbench\ub97c \uc0ac\uc6a9\ud558\uc9c0 \uc54a\uace0 \ud130\ubbf8\ub110\uc5d0\uc11c \uc2e4\uc2b5\uc744 \uc9c4\ud589\ud588\ub2e4.\\n\\n### \uc785\ubb38\uc5d0 \uadf8\uce58\uc9c0 \uc54a\ub294 \ucc45\\n\\n\ub2e8\uc21c SQL \ucffc\ub9ac\ubb38\ub9cc\uc744 \uc0ac\uc6a9\ud558\ub294 \uac83\uc774 \uc544\ub2c8\ub77c SQL\uc758 \ubc30\uacbd\ubd80\ud130 \uae30\ucd08 \ubb38\ubc95\uc744 \uc124\uba85\ud558\uace0 \ub05d\uc5d0\ub294 \ubd84\ud560, \uad70\uc9d1\ud654, \uc0e4\ub529, \ube45\ub370\uc774\ud130 \uac19\uc774 SQL\uc744 \uc774\uc6a9\ud55c \uace0\uae09 \uae30\uc220\uc744 \uac04\ub2e8\ud558\uac8c \uc18c\uac1c\ud55c\ub2e4. \ud2b9\ud788 2015\ub144\uc5d0 \ucc98\uc74c \ub4f1\uc7a5\ud55c \uc624\ud508\uc18c\uc2a4\uc778 Apache Drill\uc744 \uc0ac\uc6a9\ud574\ubcf8\ub2e4. \\n\\n### \uacf5\ubd80\ud558\uae30 \ud3b8\ud55c \ucc45\\n\\n\uc774 \ucc45\uc740 455 \ud398\uc774\uc9c0\ub85c \uaf64 \ub450\uaebc\uc6b4 \ucc45\uc774\uc9c0\ub9cc Chapter\uac00 18\uae4c\uc9c0 \uc788\uc5b4 \uc870\uae08\uc529 \ubcf4\uae30 \uc88b\ub2e4. \ud558\ub8e8\uc5d0 \ud55c Chapter\uc529 \ubcf4\uba74 18\uc77c\uc774\uba74 \ub2e4 \uc77d\uc744 \uc218 \uc788\ub2e4. \uc774\ub807\uac8c \uc9e7\uac8c Chapter\uac00 \uad6c\uc131\ub418\uc5b4 \uc788\uc5b4 \ucc45\uc744 \uc77d\ub294\ub370 \ubd80\ub2f4\uc744 \ub35c\ud588\ub2e4.\\n\\n\ub610\ud55c, \ud55c Chapter\uac00 \ub05d\ub0a0 \ub54c\ub9c8\ub2e4 `\ud559\uc2b5 \uc810\uac80 \uc2e4\uc2b5 \ubb38\uc81c`\uac00 \uc788\uc5b4 Chapter\uc758 \ub0b4\uc6a9\ub4e4\uc744 \uc810\uac80\ud560 \uc218 \uc788\uc5c8\ub2e4. \uaf64 \ub9c8\uc74c\uc5d0 \ub4e4\uc5c8\ub358 \ubd80\ubd84\uc774\ub2e4. \ubcf4\ud1b5 \uc774\ub7f0 \ucc45\ub4e4\uc740 \uc2e4\uc2b5 \uc608\uc81c\ub9cc \uc788\uace0 \ubcf5\uc2b5\ud560 \uc218 \uc788\ub294 \ubb38\uc81c\ub4e4\uc774 \uc5c6\uae30 \ub9c8\ub828\uc774\ub2e4. \uadf8\ub7ec\ub098 \uc774 \ucc45\uc740 \ubb38\uc81c\ub97c \uc81c\uacf5\ud558\uace0 \ubb38\uc81c\uc758 \uc815\ub2f5\uae4c\uc9c0 \ubd80\ub85d\uc5d0 \uae30\uc220\ub418\uc5b4 \uc788\ub2e4. \uc989, \uacf5\ubd80\ud55c \ub0b4\uc6a9\ub4e4\uc744 \uc810\uac80\ud558\uae30\uc5d0 \uc88b\uc740 \ucc45\uc774\uc5c8\ub2e4.\\n\\n\ub9c8\uc9c0\ub9c9\uc73c\ub85c \uc2e4\uc2b5\ud560 \uc218 \uc788\ub294 \ucf54\ub4dc\uac00 \ub9ce\uc73c\uba70, \uc885\uc885 \uc608\uc81c \ucf54\ub4dc\ub9c8\ub2e4 \uc5b4\ub5a0\ud55c \uc0c1\ud669\uc5d0\uc11c \uc0ac\uc6a9\ud574\uc57c \ud558\ub294\uc9c0\uc5d0 \ub300\ud55c \uc124\uba85\uc774 \uc801\ud600 \uc788\uc5c8\ub2e4. SQL\uc744 \uc791\uc131\ud558\uba74\uc11c \uc5b4\ub824\uc6b4 \ubd80\ubd84\uc774 \uc788\uc744 \ub54c \ub808\ud37c\ub7f0\uc2a4\ub85c \ucc38\uace0\ud558\uba74\uc11c \uc77d\uae30\uc5d0\ub3c4 \uc88b\uc740 \ucc45\uc774\ub77c \uc0dd\uac01\ud55c\ub2e4.\\n\\n## \ud83d\udd16 \ub300\uc0c1 \ub3c5\uc790\\n\\n\uac00\ub054 \ud55c Chapter\uc5d0\uc11c \uc124\uba85\ud558\uace0\uc790 \ud558\ub294 \uc8fc\uc81c\uac00 \uc788\uc9c0\ub9cc, \uadf8 \uc8fc\uc81c\uc640 \uc870\uae08 \ubc97\uc5b4\ub09c \ub2e4\ub978 Chapter\uc758 \ucf54\ub4dc\ub97c \ubbf8\ub9ac \uc124\uba85\ud558\ub294 \uacbd\uc6b0\uac00 \uc788\ub2e4. \uadf8\ub807\uae30\uc5d0 SQL\uc744 \uc801\uc5b4\ub3c4 \ud55c \ubc88 \uacf5\ubd80\ud574\ubcf8 \uc0ac\ub78c\uc774 \uc77d\uc73c\uba74 \uc5bb\ub294 \uac83\uc774 \ub354 \ub9ce\uc744 \uac83\uc774\ub77c \uc0dd\uac01\ud55c\ub2e4.\\n\\nSQL\uc744 \uc644\uc804\ud788 \ucc98\uc74c \uacf5\ubd80\ud558\uc2dc\ub294 \ub3c5\uc790\ub77c\uba74 \ub2e4\ub978 \ucc45\ub3c4 \uac19\uc774 \uc77d\uc73c\uba74\uc11c \uacf5\ubd80\ud558\ub294 \uac78 \ucd94\ucc9c\ud558\uace0, SQL\uc744 \uacf5\ubd80\ud574\ubcf8 \ub3c5\uc790\ub77c\uba74 \uc774 \ucc45\uc758 \ucf54\ub4dc\ub4e4\uc744 \uac04\ub2e8\ud558\uac8c \uc2e4\uc2b5\ud574\ubcf4\uba74\uc11c \ubcf5\uc2b5\ud558\ub294 \ubc29\ud5a5\uc73c\ub85c \uc77d\uc73c\uc2dc\uba74 \ub3c4\uc6c0 \ub420 \uac83\uc774\ub77c\uace0 \uc0dd\uac01\ud55c\ub2e4."},{"id":"/make-your-first-gan","metadata":{"permalink":"/en/blog/make-your-first-gan","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2021-03-20-make-your-first-gan/make-your-first-gan.md","source":"@site/blog/2021-03-20-make-your-first-gan/make-your-first-gan.md","title":"\ud83d\udcd6 GAN \uccab\uac78\uc74c","description":"\ud30c\uc774\ud1a0\uce58 \uc2e0\uacbd\ub9dd \uc785\ubb38\ubd80\ud130 \uc5f0\uc608\uc778 \uc5bc\uad74 \uc0dd\uc131\uae4c\uc9c0","date":"2021-03-20T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 GAN \uccab\uac78\uc74c","date":"2021-03-20","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/make-your-first-gan","description":"\ud30c\uc774\ud1a0\uce58 \uc2e0\uacbd\ub9dd \uc785\ubb38\ubd80\ud130 \uc5f0\uc608\uc778 \uc5bc\uad74 \uc0dd\uc131\uae4c\uc9c0","keywords":["\uc2e0\uacbd\ub9dd","\ub525\ub7ec\ub2dd","GAN","\ud30c\uc774\ud1a0\uce58"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \ub7ec\ub2dd SQL","permalink":"/en/blog/Learning-SQL"},"nextItem":{"title":"\ud83d\udcd6 \uc2e0\uacbd\ub9dd \uccab\uac78\uc74c","permalink":"/en/blog/make-your-own-neural-network"}},"content":":::info\\n\ud55c\ube5b\ubbf8\ub514\uc5b4 \\\\<\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4\\\\> \ud65c\ub3d9\uc744 \uc704\ud574\uc11c \ucc45\uc744 \uc81c\uacf5\ubc1b\uc544 \uc791\uc131\ub41c \uc11c\ud3c9\uc785\ub2c8\ub2e4.\\n:::\\n\\n![\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4 2021](../assets/I-am-reviewer-2021.jpg)\\n\\n## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/Make-your-first-gan.jpg)](http://www.kyobobook.co.kr/product/detailViewKor.laf?ejkGb=KOR&mallGb=KOR&barcode=9791162243954&orderClick=LEa&Kc=)\\n\\n- \uc81c\ubaa9: GAN \uccab\uac78\uc74c\\n- \uc800\uc790: Tariq Rashid\\n- \uc5ed\uc790: \uace0\ub77d\uc724\\n- \ucd9c\ud310\uc0ac: \ud55c\ube5b\ubbf8\ub514\uc5b4\\n- \ucd9c\uac04: 2021-03-10\\n\\n{/* truncate */}\\n\\n## Intro\\n\\n\uc774 \ucc45\uc740 \ud55c\ube5b\ubbf8\ub514\uc5b4\uc5d0\uc11c \uc9c4\ud589\ud558\ub294 `\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4 2021`\uc5d0 \ucc38\uc5ec\ud558\uac8c \ub418\uc5b4 \uc77d\uac8c \ub410\uc2b5\ub2c8\ub2e4. \uc774\ubc88 3\uc6d4 \ub9ac\ubdf0\uc5d0\ub3c4 \uc88b\uc740 \ucc45\ub4e4\uc774 \ub9ce\uc558\uace0, \uc800\ub294 `\uc0ac\ud1a0\uc2dc\uc758 \uc11c`, `GAN \uccab\uac78\uc74c`, `\ub9ac\ud329\ud130\ub9c1 2\ud310`\uc744 \uc120\ud0dd\ud588\uc5c8\uc2b5\ub2c8\ub2e4. \uc774\ubc88\uc5d0\ub3c4 \ub2e4\ub978 \ucc45\ub4e4\uc744 \uc120\ud0dd\ud55c \uc774\uc720\ub294 \uc81c\uc678\ud558\uaca0\uc2b5\ub2c8\ub2e4.\\n\\n\ub525\ub7ec\ub2dd \uacf5\ubd80\ub97c \uc2dc\uc791\ud558\uba74\uc11c \ub9c9 \uc54c\uc544\ubcf4\ub358 \ub3c4\uc911 GAN\uc774\ub77c\ub294 \ubd84\uc57c\ub97c \uc54c\uac8c \ub410\uace0, \uacf5\ubd80\ub97c \ud558\ub824 \ud588\uc5c8\uc2b5\ub2c8\ub2e4. \uadf8\ub7ec\ub098 \uc218\ud559\uc801\uc778 \ubd80\ubd84\uacfc \uc5b4\ub824\uc6b4 \uac1c\ub150\ub4e4\uc5d0 \ub9c9\ud600 \uc81c\ub300\ub85c \uacf5\ubd80\ub3c4 \ud558\uc9c0 \ubabb\ud55c \uc0c1\ud669\uc774\uc5c8\uc8e0. \uc774\ub7f0 \uc0c1\ud669\uc5d0\uc11c \uc774 \ucc45\uc744 \uc54c\uac8c \ub410\uace0 \uc120\ud0dd\ud558\uac8c \ub410\uc2b5\ub2c8\ub2e4.\\n\\n## \ucc45 \uc18c\uac1c\\n\\n\uc774 \ucc45\uc740 GAN\uc758 \ud575\uc2ec\uc744 \uc54c\ub824\uc8fc\ub294 \ucc45\uc785\ub2c8\ub2e4. GAN\uc5d0 \ub300\ud574 \ubc18\ub4dc\uc2dc \ud544\uc694\ud55c \ubd80\ubd84\ub4e4\ub9cc \uc54c\ub824\uc8fc\uba70 GAN\uc758 \ubaa8\ub4e0 \ubd80\ubd84\uc744 \ub2e4\ub8e8\uc9c0\ub294 \uc54a\uc2b5\ub2c8\ub2e4. \uadf8\ub807\uae30\uc5d0 \uc804 \ub354\ub354\uc6b1 \ub3c4\uc6c0\uc774 \ub410\uc2b5\ub2c8\ub2e4. \\n\\n\uac1c\ub150\ub9cc \uc54c\ub824\uc8fc\ub294 \uac83\uc774 \uc544\ub2c8\ub77c \uac1c\ub150\uc744 \uc774\ud574\ud558\uae30 \uc704\ud574 \uc774\ub860\ub4e4\uc744 PyTorch\ub97c \uc0ac\uc6a9\ud558\uc5ec \ud558\ub098\ud558\ub098 \ub530\ub77c\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. [\uc800\uc790 Github \uc8fc\uc18c](https://github.com/makeyourownneuralnetwork)\uc5d0 \uc608\uc81c \ucf54\ub4dc\ub4e4\uc774 \uc788\uc73c\ub2c8 \ucc38\uace0\ud558\uc2dc\uba74\uc11c GAN \uc2e0\uacbd\ub9dd\uc744 \ub9cc\ub4e4 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \\n\\n\uc774 \ucc45\uc744 \uc77d\uae30 \uc804 Colab \ud658\uacbd\uacfc PyTorch\uc758 \uc0ac\uc6a9\ubc95\uc744 \uc5b4\ub290\uc815\ub3c4 \uc54c\uace0 \uc788\uc73c\uba74, \uc774 \ucc45\uc744 \uc77d\ub294\ub370 \ub3c4\uc6c0\uc774 \ub429\ub2c8\ub2e4. \uc774 \ucc45\uc758 \ud575\uc2ec\uc740 GAN\uc774\uae30 \ub54c\ubb38\uc5d0 Colab\uacfc PyTorch\ub97c \ud544\uc694\ud55c \ubd80\ubd84\uae4c\uc9c0\ub9cc \uc54c\ub824\uc8fc\uc9c0 \uc4f0\uc774\uc9c0 \uc54a\ub294 \uac83\uae4c\uc9c0\ub294 \uc54c\ub824\uc8fc\uc9c0 \uc54a\uc2b5\ub2c8\ub2e4.\\n\\n\ub610\ud55c, \uc774 \ucc45\uc744 \uc77d\uae30 \uc804 \uc800\uc790 Tariq Rashid\uc758 \uc804\uc791\uc778 `\uc2e0\uacbd\ub9dd \uccab\uac78\uc74c`\uc774\ub77c\ub294 \ucc45\uc744 \uc77d\uc73c\uc2dc\uba74 \uc774 \ucc45\uc744 \uc77d\ub294\ub370 \ub354\uc6b1 \ub3c4\uc6c0\uc774 \ub41c\ub2e4\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4. \uc774 \ucc45\uc5d0\uc11c\ub3c4 \uc2e0\uacbd\ub9dd\uc5d0 \ub300\ud574 \uc9da\uace0 \ub118\uc5b4\uac00\uc9c0\ub9cc, `\uc2e0\uacbd\ub9dd \uccab\uac78\uc74c` \ucc45\uc5d0\uc11c\ub294 \ub354\uc6b1 \uc790\uc138\ud558\uace0 \uce5c\uc808\ud558\uac8c \uc124\uba85\ud569\ub2c8\ub2e4. \uc804 \ub9ac\ubdf0 \ub3c4\uc11c\uac00 `GAN \uccab\uac78\uc74c`\uc774\ub77c\ub294 \uc0ac\uc2e4\uc744 \uc54c\uc558\uc744 \ub54c, \ubc14\ub85c `\uc2e0\uacbd\ub9dd \uccab\uac78\uc74c` \ucc45\uc744 \ub3c4\uc11c\uad00\uc5d0\uc11c \ube4c\ub824\uc11c \uc77d\uc740 \ud6c4\uc5d0 \uc774 \ucc45\uc744 \uc77d\uc5c8\uc2b5\ub2c8\ub2e4. \\n\\n\ubaa9\ucc28\ub97c \ubcf4\uba74 \ud070 Part\uac00 3\uac1c\uac00 \uc788\uace0 Part\uc548\uc5d0 \uc138\ubd80\uc801\uc73c\ub85c Chapter\uac00 \uc788\uc2b5\ub2c8\ub2e4. \uac04\ub2e8\ud558\uac8c \uc124\uba85\ud558\uba74 Part 1\uc740 `GAN \uc744 \uad6c\ud604\ud558\uae30 \uc704\ud55c \uac04\ub2e8\ud55c \uac1c\ub150\ub4e4\uacfc \uae30\ucd08`, Part 2\ub294 `GAN \uac1c\ub150\uacfc \ub9cc\ub4e4\uae30`, Part 3\ub294 `convolutional GAN, conditional GAN \uac19\uc740 GAN\uc758 \ud65c\uc6a9`\uc73c\ub85c \uc774\ub8e8\uc5b4\uc838 \uc788\uc2b5\ub2c8\ub2e4. \uc774\ub807\uac8c \uc790\uc5f0\uc2a4\ub7ec\uc6b4 \uc21c\uc11c\ub85c \uc774\ub8e8\uc5b4\uc838 \uc788\uae30\uc5d0 \ucd08\ubcf4\uc790\ub4e4\ub3c4 \uc27d\uac8c \ub530\ub77c\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\n\\n## \ub300\uc0c1 \ub3c5\uc790\\n\\n\ub525\ub7ec\ub2dd, \uba38\uc2e0\ub7ec\ub2dd\uc5d0 \uad00\uc2ec\uc774 \uc788\uc9c0\ub9cc \uc77c\ubd80 \uac1c\ub150\ub4e4\uc774 \ub108\ubb34 \uc5b4\ub835\uace0 \uc774\ud574\ud558\uae30 \ud798\ub4e4\uc5b4\uc11c \uacf5\ubd80\ud558\ub2e4\uac00 \ub9c9\ud78c \uc0ac\ub78c\ub4e4\uc774 \ubcf4\uba74 \uc88b\uc2b5\ub2c8\ub2e4. \uc81c\uac00 \uc774 \ucf00\uc774\uc2a4\uc600\uae30\uc5d0 \ub204\uad6c\ubcf4\ub2e4 \ud655\uc2e0\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \uc774 \ucc45\uc740 \uc5c4\uccad \uac04\ub2e8\ud558\uace0 \uce5c\uadfc\ud558\uac8c \uc124\uba85\ud558\uba70, \uc2dc\uac01\uc801\uc778 \uc774\ubbf8\uc9c0\ub4e4\uc774 \ub9ce\uc544\uc11c AI\ub97c \uacf5\ubd80\ud558\uae30 \uc704\ud55c \ucd08\ubcf4\uc790\ub4e4\uc774 \uc77d\uae30 \uc27d\uc2b5\ub2c8\ub2e4. \\n\\nAI \uacf5\ubd80\ub97c \ud558\uae30 \uc704\ud55c \uc785\ubb38\uc11c\ub77c\uace0 \uc0dd\uac01\ud558\uace0 \uc77d\uc5b4\ubcf4\uc2dc\uba74 \uc88b\uc2b5\ub2c8\ub2e4. \uc704\uc5d0\uc11c\ub3c4 \uc5b8\uae09\ud588\ub4ef\uc774 GAN\uc758 \ubaa8\ub4e0 \ubd80\ubd84\uc744 \ub2e4\ub8e8\ub824\uace0 \ud558\uc9c0\ub294 \uc54a\uc2b5\ub2c8\ub2e4. \ud544\uc218\uc801\uc778 \ub0b4\uc6a9\ub4e4\ub9cc \ub2f4\uaca8 \uc788\uae30\uc5d0 GAN\uc744 \uc815\ud655\ud788 \uc774\ud574\ud558\uace0 \uc774 \ucc45\uc744 \uc77d\uc740 \ud6c4\uc5d0\ub294 \ub354\uc6b1 \uc804\ubb38\uc801\uc778 \uacf5\ubd80\ub97c \ud560 \uc218 \uc788\ub3c4\ub85d \ub3c4\uc640\uc90d\ub2c8\ub2e4. \\n\\n## \uc694\uc57d\\n\\n`\ub525\ub7ec\ub2dd\uc5d0 \uc785\ubb38\ud558\uac70\ub098 GAN\uc774\ub77c\ub294 \ubd84\uc57c\uc5d0 \uad00\uc2ec \uc788\uc5b4\uc11c \uacf5\ubd80\ud574\ubcf4\uace0 \uc2f6\uc740 \ucd08\ubcf4\uc790\ub4e4 \ub610\ub294 \uc774 \ubd84\uc57c\uc5d0 \uacf5\ubd80\ud558\ub2e4\uac00 \ub9c9\ud78c \ubd84\ub4e4\uc740 \uc77d\uc5b4\ubcf4\ub294 \uac83\uc744 \ucd94\ucc9c\ub4dc\ub9bd\ub2c8\ub2e4.`\\n\\n\uc5b4\ub514\uae4c\uc9c0\ub098 \uc81c \uc0dd\uac01\uc77c \ubfd0\uc774\ub2c8 \ucc38\uace0\ub9cc \ud574\uc8fc\uc2dc\uae38 \ubc14\ub78d\ub2c8\ub2e4."},{"id":"/make-your-own-neural-network","metadata":{"permalink":"/en/blog/make-your-own-neural-network","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2021-03-10-make-your-own-neural-network/make-your-own-neural-network.md","source":"@site/blog/2021-03-10-make-your-own-neural-network/make-your-own-neural-network.md","title":"\ud83d\udcd6 \uc2e0\uacbd\ub9dd \uccab\uac78\uc74c","description":"\uc218\ud3ec\uc790\ub3c4 \uc774\ud574\ud558\ub294 \uc2e0\uacbd\ub9dd \ub3d9\uc791 \uc6d0\ub9ac\uc640 \ub525\ub7ec\ub2dd \uae30\ucd08","date":"2021-03-10T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":false,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \uc2e0\uacbd\ub9dd \uccab\uac78\uc74c","date":"2021-03-10","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/make-your-own-neural-network","description":"\uc218\ud3ec\uc790\ub3c4 \uc774\ud574\ud558\ub294 \uc2e0\uacbd\ub9dd \ub3d9\uc791 \uc6d0\ub9ac\uc640 \ub525\ub7ec\ub2dd \uae30\ucd08","keywords":["\uc2e0\uacbd\ub9dd","\ub525\ub7ec\ub2dd","\ub274\ub7f4 \ub124\ud2b8\uc6cc\ud06c"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 GAN \uccab\uac78\uc74c","permalink":"/en/blog/make-your-first-gan"},"nextItem":{"title":"\ud83d\udcd6 \uc5c5\ubb34\uc640 \uc77c\uc0c1\uc744 \uc815\ub9ac\ud558\ub294 \uc0c8\ub85c\uc6b4 \ubc29\ubc95 \ub178\uc158 Notion","permalink":"/en/blog/clean-up-with-notion"}},"content":"## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/Make-your-own-neural-network.jpg)](http://www.kyobobook.co.kr/product/detailViewKor.laf?ejkGb=KOR&mallGb=KOR&barcode=9788968483509&orderClick=LEa&Kc=)\\n\\n- \uc81c\ubaa9: \uc2e0\uacbd\ub9dd \uccab\uac78\uc74c\\n- \uc800\uc790: Tariq Rashid\\n- \uc5ed\uc790: \uc1a1\uad50\uc11d\\n- \ucd9c\ud310\uc0ac: \ud55c\ube5b\ubbf8\ub514\uc5b4\\n- \ucd9c\uac04: 2017-04-03\\n\\n## \uc774 \ucc45\uc744 \uc77d\uac8c \ub41c \uc774\uc720\\n\\n`\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4 2021` \ud65c\ub3d9\uc744 \ud558\uc5ec 3\uc6d4 \ub9ac\ubdf0\uc5d0\ub294 `GAN \uccab\uac78\uc74c`\uc774\ub77c\ub294 \ucc45\uc744 \uc81c\uacf5\ubc1b\uac8c \ub410\uc2b5\ub2c8\ub2e4. \uadf8 \ucc45\uc744 \uba3c\uc800 \uc77d\ub2e4\uac00 \ucc45 \uc11c\ub860\uc5d0\uc11c \uc800\uc790\uc758 \uc804\uc791\uc778 `\uc2e0\uacbd\ub9dd \uccab\uac78\uc74c`\uc774\ub77c\ub294 \ucc45\uc744 \uba3c\uc800 \uc77d\uace0 GAN \uccab\uac78\uc74c\uc744 \uc77d\uc73c\uba74 \ub354\uc6b1 \ub3c4\uc6c0\uc774 \ub420 \uac70\ub77c\uace0 \uc801\ud600 \uc788\uc5b4\uc11c \uad81\uae08\uc99d\uc774 \uc0dd\uacbc\uc2b5\ub2c8\ub2e4. \ucc98\uc74c\uc5d0\ub294 \ub2e4\ub978 \uc9c0\uc5ed \ub3c4\uc11c\uad00\uc744 \uac00\uc57c\ud558\ub098 \uc544\ub2c8\uba74 \ucc45\uc744 \uad6c\ub9e4\ub97c \ud560\uae4c \uace0\ubbfc\uc744 \ud588\uc5c8\ub294\ub370 \uc6b0\uc5f0\ud788 \ub300\ud559 \uc804\uc790 \ub3c4\uc11c\uad00\uc5d0 \uc774 \ucc45\uc774 \uc788\uc5b4\uc11c \ube4c\ub824 \uc77d\uac8c \ub410\uc2b5\ub2c8\ub2e4. \\n\\n## \ucc45 \uc18c\uac1c\\n\\n\ub525\ub7ec\ub2dd\uc5d0 \ub300\ud574 \uad00\uc2ec\uc774 \uc788\ub294 \uc0ac\ub78c\ub4e4\uc740 \ud55c \ubc88\ucbe4 \ub4e4\uc5b4\ubd24\uc744 \uc2e0\uacbd\ub9dd\uc5d0 \ub300\ud574 \uce5c\uc808\ud558\uace0 \uc27d\uac8c \uc54c\ub824\uc8fc\ub294 \ucc45\uc785\ub2c8\ub2e4. \uc774 \ucc45\uc758 \ubd80\uc81c\ub294 `\uc218\ud3ec\uc790\ub3c4 \uc774\ud574\ud558\ub294 \uc2e0\uacbd\ub9dd \ub3d9\uc791 \uc6d0\ub9ac\uc640 \ub525\ub7ec\ub2dd \uae30\ucd08`\uc785\ub2c8\ub2e4. `\ub525\ub7ec\ub2dd \uacf5\ubd80\ub97c \ud558\ub294\ub370 \uc5b4\ub5bb\uac8c \uc218\ud3ec\uc790\ub3c4 \uc774\ud574\ub97c \ud560 \uc218 \uc788\uc5b4?`\ub77c\ub294 \uc9c8\ubb38\uc744 \ud558\uc2e4 \uc218 \uc788\uc2b5\ub2c8\ub2e4\ub9cc, \uc9c4\uc9dc \uc774 \ucc45\uc744 \uc9c1\uc811 \ubcf4\uba74 \uc5b4\ub824\uc6b4 \uc218\uc2dd\ub4e4\uc774 \uc5c6\uc2b5\ub2c8\ub2e4. \uac04\ub2e8\ud55c \uc0b0\uc218 \uc815\ub3c4\ub85c\ub9cc \ud65c\uc6a9\ud558\uc5ec \ud574\uacb0\uc744 \ud569\ub2c8\ub2e4.\\n\\n\ub2e8\uc810\uc740 \uc218\ud559\uc801\uc778 \uac1c\ub150\ub4e4\uc774 \uc801\ub2e4 \ubcf4\ub2c8 `\uc65c \uc774\uac8c \uc4f0\uc774\uc9c0?`\ub77c\ub294 \uc9c8\ubb38\uc774 \uc0dd\uae41\ub2c8\ub2e4. \uce5c\uc808\ud55c \uac83\uc740 \uc88b\uc558\uc9c0\ub9cc, \uadf8\ub9cc\ud07c \uc218\ud559\uc801\uc778 \ubd80\ubd84\ub4e4\uc5d0\uc11c \uc790\uc138\ud558\uc9c0 \uc54a\uace0 \uc124\uba85\ud558\ub294 \uacfc\uc815 \uc5c6\uc774 \ub118\uc5b4\uac14\ub2e4\ub294 \uc810\uc774 \uc870\uae08\uc740 \uc544\uc26c\uc6e0\uc2b5\ub2c8\ub2e4. \uadf8\ub7ec\ub098 \uc774\ub7f0 \uc124\uba85\ub4e4\uc740 \uac80\uc0c9\ud558\uba74\uc11c\ub3c4 \uacf5\ubd80\ud560 \uc218 \uc788\uc5c8\uae30\uc5d0 \ub525\ub7ec\ub2dd \uc785\ubb38\uc11c\ub85c\ub294 \uc5c4\uccad \uad1c\ucc2e\ub2e4\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4. \\n\\n\uc815\ub9d0 \ube60\ub974\uac8c \ubcf4\uba74 \ud558\ub8e8 \ub610\ub294 \uc774\ud2c0\ub9cc\uc5d0 \ubcfc \uc218 \uc788\uc744 \uc815\ub3c4\ub85c \uc27d\uac8c \uc124\uba85\ud558\uba70, \uc2dc\uac01\ud654 \ub41c \uc774\ubbf8\uc9c0\ub4e4\uc774 \ub9ce\uc544\uc11c \ub354 \ube68\ub9ac \ubcf4\ub294\ub370 \ub3c4\uc6c0\uc774 \ub410\uc2b5\ub2c8\ub2e4. \\n\\n\uc774 \ucc45\uc744 \ucc98\uc74c \uc77d\uc5c8\uc744 \ub54c 2017\ub144\uc5d0 \uc774\ub807\uac8c \uce5c\uc808\ud55c \ucc45\uc774 \ucd9c\uac04\ud588\ub2e4\ub294 \uc810\uc774 \ub180\ub784 \uc815\ub3c4\uc600\uc2b5\ub2c8\ub2e4. \uadf8 \ub2f9\uc2dc\uc5d0 \uc774\ub7f0 \ucc45\uc774 \uc788\ub2e4\ub294 \uac83\uc744 \uc54c\uc558\ub354\ub77c\uba74 \uc880 \ub354 \ube60\ub974\uace0 \uc27d\uac8c \ub525\ub7ec\ub2dd\uc5d0 \uc785\ubb38 \ud588\uc744\ud150\ub370 \uc548\ud0c0\uae5d\ub2e4\ub294 \uc0dd\uac01\uc774 \ub4e4\uc5c8\uc2b5\ub2c8\ub2e4.\\n\\n\uc774 \ucc45\uc758 \ucc98\uc74c\uc5d0\ub294 \uac1c\ub150\ub4e4\uc774 \ub098\uc624\uba70 \uc810\uc810 \uac08\uc218\ub85d `Jupyter Notebook`\ud658\uacbd\uc5d0\uc11c \uc608\uc81c\ub97c \uacf5\ubd80\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \ucc45\uc5d0\uc11c\ub294 IPython\uc744 \uc0ac\uc6a9\ud558\uace0 \uc788\uc73c\ub098 Jupyter Notebook\uc744 \uc0ac\uc6a9\ud574\ub3c4 \ubb34\ubc29\ud569\ub2c8\ub2e4. \uc774\ub7f0 \ud658\uacbd \uad6c\uc131\uc5d0 \ub300\ud574\uc11c\ub3c4 \uc790\uc138\ud558\uac8c \uc54c\ub824\uc8fc\uc9c0\ub294 \uc54a\uc2b5\ub2c8\ub2e4\ub9cc, \uc800\ud770 \uad6c\uae00\ub9c1\uc744 \ud560 \uc218 \uc788\uc73c\ub2c8 \uc774 \uc815\ub3c4\ub294 \ubd10\uc8fc\ub3c4\ub85d \ud569\uc2dc\ub2e4. [\uc800\uc790 Github \uc8fc\uc18c](https://github.com/makeyourownneuralnetwork)\uc5d0 \ucf54\ub4dc\uac00 \uc788\uc73c\ub2c8 \ucc38\uace0\ud558\uba74\uc11c \uacf5\ubd80\ud558\uba74 \ub429\ub2c8\ub2e4.\\n\\n## \ub300\uc0c1 \ub3c5\uc790\\n\\n\uc804\uacf5\uc790\ub4e0 \ube44\uc804\uacf5\uc790\ub4e0 \uadf8\ub0e5 \ub525\ub7ec\ub2dd \uc785\ubb38\ud558\ub294 \uc0ac\ub78c\uc774 \uc77d\uc73c\uba74 \uc88b\uc2b5\ub2c8\ub2e4. coursera\uc5d0 Andrew Ng \uad50\uc218\ub2d8\uc758 Machine Learning, \uae40\uc131\ud6c8 \uad50\uc218\ub2d8\uc758 \ubaa8\ub450\ub97c \uc704\ud55c \ub525\ub7ec\ub2dd \uac19\uc740 \uac15\uc758\ub97c \ub4e3\uae30 \uc804\uc5d0 \uc785\ubb38\uc6a9\uc73c\ub85c \uc77d\uc5b4\ubcf4\uba74 \ub3c4\uc6c0\uc774 \ub420 \uac83\uc774\ub77c\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4.\\n\\n## \uc694\uc57d\\n\\n`\uc804\uacf5\uc790\ub4e0 \ube44\uc804\uacf5\uc790\ub4e0 \ub525\ub7ec\ub2dd\uc5d0 \uc785\ubb38\ud558\uc2dc\ub294 \ubd84\ub4e4\uc740 \uc77d\uc5b4\ubcf4\uba74 \ub9ce\uc740 \ub3c4\uc6c0\uc774\ub420\uac83\uc774\ub77c\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4.`\\n\\n\uc5b4\ub514\uae4c\uc9c0\ub098 \uc81c \uc0dd\uac01\uc77c \ubfd0\uc774\ub2c8 \ucc38\uace0\ub9cc \ud574\uc8fc\uc2dc\uae38 \ubc14\ub78d\ub2c8\ub2e4."},{"id":"/clean-up-with-notion","metadata":{"permalink":"/en/blog/clean-up-with-notion","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2021-02-25-clean-up-with-notion/clean-up-with-notion.md","source":"@site/blog/2021-02-25-clean-up-with-notion/clean-up-with-notion.md","title":"\ud83d\udcd6 \uc5c5\ubb34\uc640 \uc77c\uc0c1\uc744 \uc815\ub9ac\ud558\ub294 \uc0c8\ub85c\uc6b4 \ubc29\ubc95 \ub178\uc158 Notion","description":"\uc0dd\uac01 \uc815\ub9ac\ubd80\ud130 \uc5c5\ubb34 \uc0dd\uc0b0\uc131, \ud611\uc5c5 \uad00\ub9ac \ub3c4\uad6c\ub97c \ub178\uc158 \ud558\ub098\ub85c!","date":"2021-02-25T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \uc5c5\ubb34\uc640 \uc77c\uc0c1\uc744 \uc815\ub9ac\ud558\ub294 \uc0c8\ub85c\uc6b4 \ubc29\ubc95 \ub178\uc158 Notion","date":"2021-02-25","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/clean-up-with-notion","description":"\uc0dd\uac01 \uc815\ub9ac\ubd80\ud130 \uc5c5\ubb34 \uc0dd\uc0b0\uc131, \ud611\uc5c5 \uad00\ub9ac \ub3c4\uad6c\ub97c \ub178\uc158 \ud558\ub098\ub85c!","keywords":["\ub178\uc158","\ud611\uc5c5","\uc815\ub9ac","\uae30\ub85d"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \uc2e0\uacbd\ub9dd \uccab\uac78\uc74c","permalink":"/en/blog/make-your-own-neural-network"},"nextItem":{"title":"\ud83d\udcd6 \ub9db\uc788\ub294 \ub514\uc790\uc778 \ud3ec\ud1a0\uc0f5&\uc77c\ub7ec\uc2a4\ud2b8\ub808\uc774\ud130 CC 2021","permalink":"/en/blog/delicious-design-Ps-Ai"}},"content":"## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/How-to-clean-up-with-notion.jpg)](http://www.kyobobook.co.kr/product/detailViewKor.laf?ejkGb=KOR&mallGb=KOR&barcode=9791190665476&orderClick=LEa&Kc=)\\n\\n- \uc81c\ubaa9: \uc5c5\ubb34\uc640 \uc77c\uc0c1\uc744 \uc815\ub9ac\ud558\ub294 \uc0c8\ub85c\uc6b4 \ubc29\ubc95 \ub178\uc158 Notion\\n- \uc800\uc790: \uc804\uc2dc\uc9c4, \uc774\ud574\ubd04\\n- \ucd9c\ud310\uc0ac: \uc81c\uc774\ud38d\\n- \ucd9c\uac04: 2020-10-16\\n\\n{/* truncate */}\\n\\n## \uc65c \uc77d\uc5c8\uc5b4\uc694?\\n\\n\ub178\uc158\uc5d0 \ub300\ud574 \uad00\uc2ec\uc774 \uc788\uc5b4 \uc798 \uc0ac\uc6a9\ud558\uace0 \uc788\uc5c8\uc73c\ub098, \ub2e4\ub978 \uc0ac\ub78c\uc774 \ub9cc\ub4e4\uc5b4 \ub193\uc740 \ud15c\ud50c\ub9bf\ub9cc \uc4f0\uace0 \uc788\uc73c\ub2c8 \ubcc4\ub85c \uc800 \uc790\uc2e0\uc5d0 \ub9cc\uc871\uc774 \ub418\uc9c0 \uc54a\ub354\ub77c\uace0\uc694. \uc9c1\uc811 \ud15c\ud50c\ub9bf\uc744 \ub9cc\ub4e4\uc5b4\ubcf4\uc790\ub294 \uc0dd\uac01\uc744 \ud558\ub358 \ucc38\uc5d0 \uc774 \ucc45\uc744 \ub3c4\uc11c\uad00\uc5d0\uc11c \ubc1c\uacac\ud558\uc5ec \uc77d\uac8c \ub410\uc2b5\ub2c8\ub2e4.\\n\\n\ub610\ud55c, \uc804 \ube14\ub85c\uadf8\ub97c \ud558\ub2e4 \ubcf4\ub2c8 \ub9c8\ud06c\ub2e4\uc6b4 \ubb38\ubc95\uc5d0 \uc5b4\ub290 \uc815\ub3c4 \uc775\uc219\ud574\uc84c\ub2e4\uace0 \uc0dd\uac01\ud574\uc11c \ub178\uc158\uc744 \uc798 \uc0ac\uc6a9\ud558\uace0 \uc788\ub2e4\uace0 \uc0dd\uac01\ud588\uc9c0\ub9cc, \ub2e8\ucd95\ud0a4, \ub370\uc774\ud130\ubca0\uc774\uc2a4 \uac19\uc740 \ub178\uc158\uc758 \uace0\uc720\uc801\uc778 \uae30\ub2a5\ub4e4\uc744 \ud65c\uc6a9\ud558\uc5ec \uc0ac\uc6a9\ud558\uace0 \uc788\uc9c0\ub294 \uc54a\uc558\uc2b5\ub2c8\ub2e4. \uadf8\ub7f0 \ubd80\ubd84\ub4e4\uc744 \uacf5\ubd80\ud574\ubcf4\uace0 \uc2f6\uc5b4\uc11c \uc774 \ucc45\uc744 \uc77d\uc5c8\uc2b5\ub2c8\ub2e4.\\n\\n## \ub178\uc158\\n\\n\uc5d0\ubc84\ub178\ud2b8\ub97c \uc0ac\uc6a9\ud558\uc2dc\ub294 \ubd84\ub4e4\ub3c4 \ub118\uc5b4\uc62c \uc815\ub3c4\ub85c \uc0dd\uc0b0\uc131\uc774 \uc88b\ub2e4\ub294 \uc571\uc774\uc8e0. \ub300\ubd80\ubd84\uc758 \ud68c\uc0ac\uc5d0\uc11c\ub3c4 \ub178\uc158\uc744 \ud65c\uc6a9\ud558\uc5ec \ubb38\uc11c\ud654\ub97c \uc9c4\ud589\ud558\uace0 \uc788\uace0, \uac01 \uae30\uc5c5\uc758 \ucc44\uc6a9 \uacf5\uace0\ub97c \ubcf4\uba74 \ub300\ubd80\ubd84 \ub178\uc158 \ubb38\uc11c\ub85c \uc774\ub8e8\uc5b4\uc838 \uc788\ub294 \ubaa8\uc2b5\ub4e4\uc744 \ud655\uc778\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\n\\n\uc5b4\uca4c\ub2e4 \ubcf4\ub2c8 IT\uc5d0 \uc27d\uac8c \uc811\ud558\ub294 \uc0ac\ub78c\ub4e4\uc5d0\uac8c\ub294 \ud544\uc218 \uc544\ub2cc \ud544\uc218\uac00 \ub41c \uc571\uc785\ub2c8\ub2e4. \uadf8\ub9ac\uace0 \uc0ac\uc6a9 \uc774\uc720 \uc911 \uac00\uc7a5 \ud070 \uc774\uc720\ub294 \uc608\uc058\ub2e4\ub294 \uac83\uc774\uc8e0. \uae54\ub054\ud55c \ub514\uc790\uc778\uc774 \ud55c\ubaab\ud588\ub2e4\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4.\\n\\n\ub610\ud55c, \ub2e4\uc591\ud55c \uae30\ub2a5\ub4e4\uc744 \uc81c\uacf5\ud558\uace0 \uc704\uc82f\uc744 \ub9cc\ub4e4\uc5b4\uc11c \uc801\uc6a9\ub3c4 \uac00\ub2a5\ud558\uba70 \uc0ac\ub78c\ub4e4\uacfc \uc27d\uac8c \uacf5\uc720\ub3c4 \ub418\uace0, \ubb38\uc11c\ud654\ub97c \ud558\uae30 \uc27d\uc2b5\ub2c8\ub2e4. \ucd5c\uadfc\uc5d0\ub294 \ud55c\uad6d\uc5b4 \ubc84\uc804\uc774 \ub7f0\uce6d\ub418\uae30\ub3c4 \ud588\uc73c\uba70, \uae30\ub2a5\ub4e4\uc744 \ud65c\uc6a9\ud558\uae30 \uc5b4\ub824\uc6b4 \ubd84\ub4e4\uc744 \uc704\ud574 \ub2e4\uc591\ud55c \ud15c\ud50c\ub9bf\ub3c4 \uc81c\uacf5\ub429\ub2c8\ub2e4.\\n\\nPro \uc694\uae08\uc81c\uac00 \uc788\uae34 \ud558\uc9c0\ub9cc, \uae30\ubcf8\uc801\uc778 \uae30\ub2a5\ub4e4\uc740 \ubaa8\ub450 \ubb34\ub8cc\uc785\ub2c8\ub2e4. \uc774 \uc810\uc774 \ub108\ubb34 \ub9e4\ub825\uc801\uc774\uae30\ub3c4 \ud558\uc8e0. \ub300\ud559\uc0dd\uc740 Education \uacc4\uc815\uc73c\ub85c \uc804\ud658\ud558\uc5ec \uc0ac\uc6a9\ud558\uba74 Pro Plan\uc73c\ub85c \uc0ac\uc6a9\ud560 \uc218 \uc788\ub2e4\ub294 \uc7a5\uc810\ub3c4 \uc788\ub2f5\ub2c8\ub2e4. \uc774\ub7f0 \uc7a5\uc810\ub4e4\uc774 \uc788\uae30\uc5d0 \uc0ac\uc6a9\ud558\uc9c0 \uc54a\uc744 \uc774\uc720\ub294 \uc5c6\ub2e4\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4.\\n\\n\ubb3c\ub860 \ucc98\uc74c \ub178\uc158\uc744 \uc0ac\uc6a9\ud558\uac8c \ub418\uba74 \ubb58 \uc368\uc57c \ud560\uc9c0 \ud63c\ub780\uc2a4\ub7fd\uae30\ub294 \ud558\uc9c0\ub9cc, \uc0ac\uc6a9 \uc6a9\ub3c4\ub9cc \uba85\ud655\ud558\ub2e4\uba74 \uc88b\uc740 \ub3c4\uad6c\uac00 \ub420 \uc218 \uc788\ub2e4\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4.\\n\\n## \ub2e8\uc810\\n\\n\ub370\uc774\ud130\uac00 \ub9ce\uc544\uc9c8\uc218\ub85d \ub85c\ub529 \uc2dc\uac04\uc774 \uae38\uc5b4\uc9c4\ub2e4\ub294 \ub2e8\uc810\uc774 \uc788\uae34 \ud569\ub2c8\ub2e4. \uc774 \ubd80\ubd84\uc5d0 \ub300\ud574 \uc5b4\ub824\uc6c0\uc774 \uc870\uae08 \uc788\uc5c8\uc9c0\ub9cc, \uc774 \ucc45\uc5d0\uc11c \ud574\uacb0 \ubc29\ubc95\uc744 \uc54c\ub824\uc90d\ub2c8\ub2e4.\\n\\nPPT \ub610\ub294 Excel \uac19\uc740 \ud504\ub85c\uadf8\ub7a8 \ub3c4\uad6c\ub4e4\uc744 \uc0ac\uc6a9\ud558\ub294 \ub370 \uc775\uc219\ud558\uc9c0 \uc54a\uc740 \uc0ac\ub78c\ub4e4\uc5d0\uac8c\ub294 \uc9c4\uc785\uc7a5\ubcbd\uc774 \uc788\uc744 \uac83 \uac19\uc2b5\ub2c8\ub2e4. \uc774\ub7f0 \ubd84\ub4e4\uc740 \uae30\ub2a5\ub4e4\uc744 \uc5b4\ub5bb\uac8c \uc0ac\uc6a9\ud574\uc57c \ud560\uc9c0, \ub2e4\ub978 \uc0ac\ub78c\ub4e4\uc740 \uc5b4\ub5bb\uac8c \uc0ac\uc6a9\ud558\uace0 \uc788\ub294\uc9c0 \uba3c\uc800 \uc54c\uc544\uc57c \ud55c\ub2e4\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4.\\n\\n## \ucc45\\n\\n\ub178\uc158\uc774\ub77c\ub294 \ub3c4\uad6c\ub97c \uc5b4\ub5bb\uac8c \ud65c\uc6a9\ud574\uc57c \ud560\uc9c0\uc5d0 \ub300\ud574 \ubc29\ud5a5\uc131\uc774 \uc5c6\uc73c\uc2e0 \ubd84\ub4e4\uc740 \uc774 \ucc45\uc744 \ubcf4\uba74\uc11c \uacf5\ubd80\ud558\ub294 \uac83\uc774 \uc88b\ub2e4\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4. \ub2e4\uc591\ud55c \ud65c\uc6a9 \ubc29\ubc95\uc744 \uc54c\ub824\uc8fc\uae30\ub3c4 \ud558\uba70, \ub178\uc158\uc758 \uae30\ub2a5\ub4e4\uc744 \uaf64 \uc790\uc138\ud558\uac8c \uc54c\ub824\uc90d\ub2c8\ub2e4. \uc27d\uac8c \ub9d0\ud558\uba74 \ub3c4\uad6c\ub97c \uc0ac\uc6a9\ud558\uae30 \uc5b4\ub824\uc6b4 \ubd84\ub4e4\uc744 \uc704\ud55c \uba54\ub274\uc5bc\uc774\ub77c\uace0 \uc0dd\uac01\ud558\uba74 \ub420 \uac83 \uac19\uc2b5\ub2c8\ub2e4. \ucd08\uc2ec\uc790\ub3c4 \uc27d\uac8c \uc0ac\uc6a9\ud560 \uc218 \uc788\ub3c4\ub85d \ub3c4\uc640\uc90d\ub2c8\ub2e4.\\n\\n\uc804 Chapter 03, Chapter 04 \ubd80\ubd84\uc5d0\uc11c \ub9ce\uc740 \ub3c4\uc6c0\uc744 \uc5bb\uc5c8\uc2b5\ub2c8\ub2e4. \ub370\uc774\ud130\ubca0\uc774\uc2a4\uc5d0 \uad00\ud55c \ub0b4\uc6a9\ub4e4\uc774\uc5c8\uc73c\uba70, \uc774 \ubd80\ubd84\uc5d0 \ub300\ud574\uc11c\ub294 \ucd08\uc2ec\uc790\uc5d0\uac8c\ub294 \uc5b4\ub824\uc6b8 \uc218 \uc788\uc73c\ub098 \ud55c \ubc88 \uacf5\ubd80\ud574\ub450\uba74 \ub178\uc158\uc744 \uc0ac\uc6a9\ud558\ub294 \ub370 \uc5b4\ub824\uc6c0\uc774 \uc5c6\uc744 \uac83\uc774\ub77c\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4.\\n\\n## \ud65c\uc6a9\\n\\n\uc800\ub294 \ub2e4\uc774\uc5b4\ub9ac, \ucc45 \uae30\ub85d, \uacf5\ubd80\ud55c \uac83\ub4e4 \uc815\ub9ac \ub4f1\uc73c\ub85c \uc0ac\uc6a9\ud558\uace0 \uc788\uc2b5\ub2c8\ub2e4. \uc720\ud29c\ube0c\ub098 \ub2e4\ub978 \ubd84\ub4e4\uc758 \ud15c\ud50c\ub9bf\uc744 \uad6c\uacbd\ud558\uba74 \uc800\ubcf4\ub2e4 \ub354 \ud65c\uc6a9\uc801\uc73c\ub85c \uc0ac\uc6a9\ud558\uace0, \uac10\uc131\uc801\uc73c\ub85c \uc0ac\uc6a9\ud558\ub294 \ubd84\ub4e4\uc774 \ub9ce\uc2b5\ub2c8\ub2e4. \uc7ac\ubc0c\ub294 \ud15c\ud50c\ub9bf\ub4e4\uc774 \ub9ce\uc73c\ub2c8 \ucc38\uace0\ud558\uc2dc\uba74 \ub420 \uac83 \uac19\uc2b5\ub2c8\ub2e4.\\n\\n[Notion.so \ub178\uc158 \ud55c\uad6d \uc0ac\uc6a9\uc790 \ubaa8\uc784](https://www.facebook.com/groups/notion.so)\uc5d0\uc11c \uc0ac\ub78c\ub4e4\uc774 \uc5b4\ub5bb\uac8c \ub178\uc158\uc744 \uc0ac\uc6a9\ud558\ub294\uc9c0 \uc54c \uc218 \uc788\uc2b5\ub2c8\ub2e4. \uacf5\uc9c0\ub97c \ud655\uc778\ud558\uc2dc\uba74 \ucee4\ubba4\ub2c8\ud2f0 \ud398\uc774\uc9c0\ub3c4 \uc788\uace0, \ud15c\ud50c\ub9bf\uc744 \uc81c\uacf5\ud574\uc8fc\ub294 \uc0ac\uc774\ud2b8\ub3c4 \uc788\uc73c\ub2c8 \ub4e4\uc5b4\uac00\uc154\uc11c \ud655\uc778\ud574\ubcf4\uc2dc\uba74 \ub429\ub2c8\ub2e4.\\n\\n## \uacb0\ub860\ud3c9\\n\\n`\uc0dd\uc0b0\uc801\uc778 \uc0b6\uc744 \uc0b4\uace0 \uc2f6\uc73c\uc2e0 \ubd84\ub4e4\uc740 \uc0ac\uc6a9\ud574\ubcf4\uba74 \uc7ac\ubc0c\ub294 \ub3c4\uad6c\uac00 \ub420 \uc218 \uc788\ub2e4\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4.`\\n\\n\uc5b4\ub514\uae4c\uc9c0\ub098 \uc81c \uc0dd\uac01\uc77c \ubfd0\uc774\ub2c8 \ucc38\uace0\ub9cc \ud574\uc8fc\uc2dc\uae38 \ubc14\ub78d\ub2c8\ub2e4."},{"id":"/delicious-design-Ps-Ai","metadata":{"permalink":"/en/blog/delicious-design-Ps-Ai","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2021-02-18-delicious-design-Ps-Ai/delicious-design-Ps-Ai.md","source":"@site/blog/2021-02-18-delicious-design-Ps-Ai/delicious-design-Ps-Ai.md","title":"\ud83d\udcd6 \ub9db\uc788\ub294 \ub514\uc790\uc778 \ud3ec\ud1a0\uc0f5&\uc77c\ub7ec\uc2a4\ud2b8\ub808\uc774\ud130 CC 2021","description":"\ub204\uad6c\ub098 \uc27d\uac8c \ubc30\uc6cc \uc81c\ub300\ub85c \uc368\uba39\ub294 \uadf8\ub798\ud53d \uc785\ubb38\uc11c","date":"2021-02-18T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \ub9db\uc788\ub294 \ub514\uc790\uc778 \ud3ec\ud1a0\uc0f5&\uc77c\ub7ec\uc2a4\ud2b8\ub808\uc774\ud130 CC 2021","date":"2021-02-18","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/delicious-design-Ps-Ai","description":"\ub204\uad6c\ub098 \uc27d\uac8c \ubc30\uc6cc \uc81c\ub300\ub85c \uc368\uba39\ub294 \uadf8\ub798\ud53d \uc785\ubb38\uc11c","keywords":["\ud3ec\ud1a0\uc0f5","\uc77c\ub7ec\uc2a4\ud2b8\ub808\uc774\ud130","\ub514\uc790\uc774\ub108","\ub9db\uc788\ub294 \ub514\uc790\uc778"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \uc5c5\ubb34\uc640 \uc77c\uc0c1\uc744 \uc815\ub9ac\ud558\ub294 \uc0c8\ub85c\uc6b4 \ubc29\ubc95 \ub178\uc158 Notion","permalink":"/en/blog/clean-up-with-notion"},"nextItem":{"title":"\ud83d\udcd6 \ub098\ub294 LINE \uac1c\ubc1c\uc790\uc785\ub2c8\ub2e4","permalink":"/en/blog/LINE-developer"}},"content":":::info\\n\ud55c\ube5b\ubbf8\ub514\uc5b4 \\\\<\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4\\\\> \ud65c\ub3d9\uc744 \uc704\ud574\uc11c \ucc45\uc744 \uc81c\uacf5\ubc1b\uc544 \uc791\uc131\ub41c \uc11c\ud3c9\uc785\ub2c8\ub2e4.\\n:::\\n\\n![\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4 2021](../assets/I-am-reviewer-2021.jpg)\\n\\n## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/delicious-design-Ps-Ai.jpg)](http://www.kyobobook.co.kr/product/detailViewKor.laf?ejkGb=KOR&mallGb=KOR&barcode=9791162243848&orderClick=LEa&Kc=)\\n\\n- \uc81c\ubaa9: \ub9db\uc788\ub294 \ub514\uc790\uc778 \ud3ec\ud1a0\uc0f5&\uc77c\ub7ec\uc2a4\ud2b8\ub808\uc774\ud130 CC 2021\\n- \uc800\uc790: \ube68\uac04\uace0\ub798, \uc724\uc774\uc0ac\ub77c\\n- \ucd9c\ud310\uc0ac: \ud55c\ube5b\ubbf8\ub514\uc5b4\\n- \ucd9c\uac04: 2021-01-18\\n\\n{/* truncate */}\\n\\n## \ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4 2021\\n\\n\uc774 \ucc45\uc740 \ud55c\ube5b\ubbf8\ub514\uc5b4 \ub3c4\uc11c \uc11c\ud3c9\ub2e8 `<\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4 2021>`\uc5d0 \ucc38\uac00\ud558\uac8c \ub418\uc5b4 \ubc1b\uac8c \ub41c \ucc45\uc785\ub2c8\ub2e4. \ud55c\ube5b\ubbf8\ub514\uc5b4\uc5d0\uc11c \uc9c4\ud589\ud558\ub294 `<\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4>`\ub294 \ud3c9\uc18c IT \ubd84\uc57c\uc5d0 \uad00\uc2ec\uc774 \ub9ce\uc740 \ubd84\uc774\ub77c\uba74 \uc815\ub9d0 \ub3c4\uc6c0\uc774 \ub418\ub294 \ud504\ub85c\uadf8\ub7a8 \uc911 \ud558\ub098\uc778 \uac83 \uac19\uc2b5\ub2c8\ub2e4. \uc800\ub294 \uc791\ub144\uc5d0 \uc2e0\uccad\ud558\uace0 \uc2f6\uc5c8\uc9c0\ub9cc, \uc2e0\uccad \uc2dc\uae30\ub97c \ub193\uccd0\uc11c \uc544\uc26c\uc6e0\ub294\ub370 \uc774\ubc88\uc5d0 \uc774 \ud65c\ub3d9\uc5d0 \ucc38\uc5ec\ud560 \uc218 \uc788\uc5b4\uc11c \uc815\ub9d0 \uae30\uc069\ub2c8\ub2e4.\\n\\n\uc194\uc9c1\ud558\uac8c \ub9d0\ud558\uc790\uba74 `<\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4>`\uc5d0 \ucc38\uc5ec\ud560 \uc218 \uc788\uc744 \uc904\uc740 \uc0dd\uac01\ub3c4 \ubabb \ud588\uc2b5\ub2c8\ub2e4. GitHub Jekyll \ube14\ub85c\uadf8 \uc790\uccb4\uac00 \ub124\uc774\ubc84 \ube14\ub85c\uadf8, \ud2f0\uc2a4\ud1a0\ub9ac, \ube0c\ub7f0\uce58 \uac19\uc740 \ube14\ub85c\uadf8\ucc98\ub7fc \uc720\uc785\uc774 \uc798 \ub418\ub294 \ube14\ub85c\uadf8\ub3c4 \uc544\ub2d0\ubfd0\ub354\ub7ec, \ud63c\uc790 \uacf5\ubd80\ud558\uac70\ub098 \uc77d\uc740 \ucc45\ub4e4\uc744 \uc7a5\uc810\ub9cc \uc774\uc57c\uae30\ud558\ub294 \uac83\uc774 \uc544\ub2cc \uc0ac\uc2e4\uc801\uc73c\ub85c \ub9ac\ubdf0\ud558\ub294 \uc0ac\ub78c\uc774\uae30\uc5d0 \uadf8\ub807\uac8c \uc0dd\uac01\ud588\uc2b5\ub2c8\ub2e4.\\n\\n\ub300\ubd80\ubd84 \uc774\ub7f0 \ud65c\ub3d9\ub4e4\uc740 \uc5b4\ub290 \uc815\ub3c4 \uc601\ud5a5\ub825\uc774 \uc788\ub294 \uc0ac\ub78c \ub610\ub294 \uc720\uc785\uc774 \ub9ce\uc740 \uc0ac\uc774\ud2b8\uc5d0 \uae30\ud68c\ub97c \uc8fc\uae30\uc5d0 \uc800\ub294 \ud65c\ub3d9\uc5d0 \uc9c0\uc6d0\ud558\ub354\ub77c\ub3c4 \ub3c4\uc804\ud574\ubcf4\uc790\ub294 \ub9c8\uc74c\uc73c\ub85c \uc9c0\uc6d0\ud588\uc5c8\uc2b5\ub2c8\ub2e4. \uadf8\ub7ec\ub098 \uc81c\uac00 \ubf51\ud798\uc73c\ub85c\uc368 \uc815\ub9d0 `<\ub098\ub294 \ub9ac\ubdf0\uc5b4\ub2e4>` \ud65c\ub3d9\uc740 \uaf2d \uc601\ud5a5\ub825\uc774 \uc788\ub294 \uc778\ud50c\ub8e8\uc5b8\uc11c\ub9cc \ubf51\ub294 \ud65c\ub3d9\uc774 \uc544\ub2c8\ub77c\ub294 \uac83\uc744 \uc54c\uac8c \ub410\uc2b5\ub2c8\ub2e4. \uc800\uc5d0\uac8c \uc790\uae30\uacc4\ubc1c\uc758 \uae30\ud68c\ub97c \uc900 `\ud55c\ube5b\ubbf8\ub514\uc5b4` \ucd9c\ud310\uc0ac\uc5d0 \uac10\uc0ac\ub4dc\ub9bd\ub2c8\ub2e4.\\n\\n\uc774 \ud65c\ub3d9\uc740 \uc8fc\uc5b4\uc9c4 \ub9ac\uc2a4\ud2b8\uc5d0\uc11c \ud76c\ub9dd \ub3c4\uc11c 3\uad8c\uc744 \uc120\uc815\ud558\uba74 \uadf8\uc911 1\uad8c\uc744 \ub79c\ub364\uc73c\ub85c \ubc1b\uc744 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \uc800\ub294 `\ucc98\uc74c \ubc30\uc6b0\ub294 \uc560\uc800`, `\uc9c4\uc9c0\ud55c \ud30c\uc774\uc36c`, `\ub9db\uc788\ub294 \ub514\uc790\uc778 \ud3ec\ud1a0\uc0f5&\uc77c\ub7ec\uc2a4\ud2b8\ub808\uc774\ud130`\ub97c \uc120\ud0dd\ud588\uc5c8\uc2b5\ub2c8\ub2e4.\\n\\n## \uc65c \uc120\ud0dd\ud588\ub098\uc694?\\n\\n\uc800\ub294 \uac1c\ubc1c\uc790\ub97c \ubaa9\ud45c\ub85c \ud558\ub294 \uc0ac\ub78c\uc774\uc9c0\ub9cc \uc608\uc804\ubd80\ud130 \uc720\uba85\ud55c \ucef4\ud4e8\ud130 \ud504\ub85c\uadf8\ub7a8\uc5d0 \uad00\uc2ec\uc774 \uc788\uc5c8\uae30\uc5d0 \ub2e4\uc591\ud55c \ud504\ub85c\uadf8\ub7a8\ub4e4\uc744 \uacf5\ubd80\ud588\uc5c8\uc73c\uba70 \ud3ec\ud1a0\uc0f5 CS4\ub97c \uacf5\ubd80\ud588\ub358 \uacbd\ud5d8\uc774 \uc788\uc2b5\ub2c8\ub2e4.\\n\\n\ubb3c\ub860 \uac1c\ubc1c\uc790\ub97c \ubaa9\ud45c\ub85c \ud55c\ub2e4\uba74 \ud504\ub85c\uadf8\ub798\ubc0d\uc744 \uac00\uc7a5 \uc798\ud574\uc57c \ud558\ub294 \uac83\uc774 \ub9de\uc9c0\ub9cc, \uc608\uc804\ubd80\ud130 \uac1c\ubc1c\ub9cc \ud558\ub294 \uac1c\ubc1c\uc790\uac00 \ub418\ub294 \uac83\uc774 \uc544\ub2c8\ub77c \ub2e4\ub978 \ubd84\uc57c\ub4e4\uc758 \uc9c0\uc2dd\ub3c4 \uc5b4\ub290 \uc815\ub3c4\ub294 \uac00\uc9c4 \uac1c\ubc1c\uc790\uac00 \ub418\uace0 \uc2f6\uc5c8\uc2b5\ub2c8\ub2e4. \uc774\ubc88 \uae30\ud68c\ub97c \ud1b5\ud574 \ud3ec\ud1a0\uc0f5\uc744 \ub2e4\uc2dc \ubc30\uc6b8 \uc218 \uc788\uc5c8\uc73c\uba70, \uc77c\ub7ec\uc2a4\ud2b8\ub808\uc774\ud130\uae4c\uc9c0 \uacf5\ubd80\ud560 \uc218 \uc788\uc5c8\uc2b5\ub2c8\ub2e4.\\n\\n\ub2e4\ub978 2\uad8c\uc744 \uc120\ud0dd\ud55c \uc774\uc720\ub294 \uc9c0\uae08 \ub9ac\ubdf0\uc5d0\uc11c \ubd88\ud544\uc694\ud558\ub2c8 \uad73\uc774 \uc124\uba85\ud558\uc9c0\ub294 \uc54a\uaca0\uc2b5\ub2c8\ub2e4.\\n\\n## \ubb34\ub8cc \uccb4\ud5d8\\n\\n\uc800\ub294 Adobe\uc5d0\uc11c \uc81c\uacf5\ud558\ub294 \ubb34\ub8cc \uccb4\ud5d8\uc744 \uc774\uc6a9\ud558\uc5ec 7\uc77c \ub3d9\uc548 \uc774 \ucc45\uc744 \ub05d\ub0b4\uaca0\ub2e4\ub294 \uc0dd\uac01\uc73c\ub85c \ubd24\uc2b5\ub2c8\ub2e4. \ud559\uc0dd \ud560\uc778\uc744 \ubc1b\uc73c\uba74 \uc5b4\ub290 \uc815\ub3c4 \uc800\ub834\ud55c \uac00\uaca9\uc73c\ub85c \uc0ac\uc6a9\ud560 \uc218\ub3c4 \uc788\uc9c0\ub9cc, \uadf8\ub7ec\ub2e4\uac00\ub294 \ub108\ubb34 \uc5ec\uc720\ub86d\uac8c \ucc45\uc744 \ubcf4\uace0 \ub2e4 \uc548 \uc77d\uc740 \uc0c1\ud0dc\ub85c \ub9ac\ubdf0\ub97c \uc801\uc744 \uac83 \uac19\uc544\uc11c \\"\ucc28\ub77c\ub9ac \ube60\ub974\uac8c \uacf5\ubd80\ud558\uace0, \ub2e4 \uc77d\uc5b4\ubcf4\uace0 \ub9ac\ubdf0\ub97c \uc801\uc790\\"\ub77c\uace0 \uc0dd\uac01\ud588\uc2b5\ub2c8\ub2e4.\\n\\n7\uc77c \ub3d9\uc548 \uac1c\ubc1c \uacf5\ubd80, \ucc45 \uc77d\uae30 \ub4f1 \ubaa8\ub4e0 \uac83\uc744 \ub0b4\ub824\ub193\uace0 \uc774 \ucc45\ub9cc \ubd10\uc11c \uc870\uae08 \uc9c0\uce58\uace0 \uc5b4\ub824\uc6c0\uc774 \uc788\uc5c8\uc9c0\ub9cc, \uc911\uac04\uc5d0 \ud3ec\uae30\ud558\uace0 \uc2f6\uc9c0\ub294 \uc54a\uc558\uc2b5\ub2c8\ub2e4. \ud55c \ubc88 \ubcf4\uba74 \ub05d\uc744 \ub0b4\uace0 \uc2f6\ub2e4\uace0 \uc0dd\uac01\ud558\uae30 \ub54c\ubb38\uc774\uc8e0. \ubb34\ub8cc\ub85c \uc81c\uacf5 \ubc1b\uc740 \ucc45\uc774\ub77c\uace0 \ub300\ucda9 \ubcf4\uc9c0 \uc54a\uc558\uc2b5\ub2c8\ub2e4. \uc624\ud788\ub824 \ub193\uce5c \uac74 \uc5c6\ub294\uc9c0 \ub2e4\uc2dc \uc2e4\uc2b5\ud558\uba74\uc11c \ucc45\uc744 \uc77d\uc5c8\uc2b5\ub2c8\ub2e4.\\n\\n\\n## \ucd08\ubcf4\ubd84\ub4e4\uc774 \uc77d\uae30 \uc801\uc808\ud55c\uac00\uc694?\\n\\n\uc800\ub294 \uc801\uc808\ud558\ub2e4\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4. \uc608\uc804\uc5d0 \uc81c\uac00 \uacf5\ubd80\ud588\uc744 \ub54c 600p\uac00 \uae30\ubcf8\uc73c\ub85c \ub118\uc5b4\uac00\ub294 \ud3ec\ud1a0\uc0f5 \ucc45\ub4e4\ubcf4\ub2e4 \ud6e8\uc52c \uc77d\uae30\ub294 \ud3b8\ud588\uc2b5\ub2c8\ub2e4. \uc774 \ucc45\ub3c4 532p\ub77c\uc11c \ucd08\ubcf4\uc790\ubd84\ub4e4\uaed8 \uc9c4\uc785\uc7a5\ubcbd\uc774 \uc788\uc744 \uc218 \uc788\uaca0\uc9c0\ub9cc \uc0ac\uc2e4 \uc0ac\uc9c4 \ub54c\ubb38\uc5d0 \ud398\uc774\uc9c0\uac00 \ub9ce\uc740 \uac83\ubfd0\uc785\ub2c8\ub2e4.\\n\\n\ub610\ud55c, \uc124\uba85\ud558\ub294 \uae00\ub4e4\uc740 \uc904\uc774\uace0 \uc0ac\uc9c4 \ud558\ub098\ud558\ub098\uc5d0 \uc9e4\ub9c9\ud558\uac8c \uc124\uba85\uc774 \ub418\uc5b4 \uc788\uae30 \ub54c\ubb38\uc5d0 \uc624\ud788\ub824 \ubcf4\uae30 \ud3b8\ud588\uc2b5\ub2c8\ub2e4.\\n\\n\ud55c\ube5b\ubbf8\ub514\uc5b4\uc5d0\uc11c `\ub9db\uc788\ub294 \ub514\uc790\uc778 \uc2a4\ud130\ub514 \uadf8\ub8f9`\uc744 \uc6b4\uc601\ud558\uace0 \uc788\uc5b4 \ud63c\uc790 \ud559\uc2b5\ud558\uae30 \uc5b4\ub824\uc6b4 \ubd84\uc740 \uacf5\uc2dd \uce74\ud398\uc5d0\uc11c \uc9c4\ud589\ud558\ub294 \uc2a4\ud130\ub514\uc5d0 \ucc38\uc5ec\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \ub9c9\ud788\ub294 \ubd80\ubd84\uc774 \uc788\ub2e4\uba74 \uce74\ud398\uc758 Q&A \uac8c\uc2dc\ud310\uc5d0 \uc9c8\ubb38\ud560 \uc218\ub3c4 \uc788\ub2e4\uace0 \ud569\ub2c8\ub2e4.\\n\\n## \uc88b\uc558\ub358 \uc810\\n\\n1. \uc5c5\ub370\uc774\ud2b8\ub41c \uae30\ub2a5\ub4e4\uc744 \uc54c\ub824\uc90d\ub2c8\ub2e4.\\n\\n Adobe\ub294 CS6 \ubc84\uc804 \uc774\ud6c4\ubd80\ud130\ub294 \uc18c\ud504\ud2b8\uc6e8\uc5b4\ub97c \ub2e8\ud488\uc73c\ub85c \ud310\ub9e4\ud558\ub294 \ubc29\ubc95\uc744 \ubc84\ub9ac\uace0 CC\ub85c \ubcc0\uacbd\ub418\uba74\uc11c \uc628\ub77c\uc778 \uc5f0\uacb0\uc774 \ud544\uc218\ub85c \uc694\uad6c\ub418\ub294 \ubc84\uc804\uc73c\ub85c \ud310\ub9e4\ub418\uace0 \uc788\uc2b5\ub2c8\ub2e4. \uc77c\uc885\uc758 \uad6c\ub3c5\ub8cc\ub97c \ub0b4\uace0 \uc0ac\uc6a9\ud558\ub294 \uac83\uacfc \uac19\uc2b5\ub2c8\ub2e4.\\n\\n \ub610\ud55c, \ub9e4\ub144 \uc5c5\ub370\uc774\ud2b8\ub418\ub294 \uac83\uc73c\ub85c \uc54c\uace0 \uc788\uc2b5\ub2c8\ub2e4. \uc774 \ucc45\uc5d0\uc11c\ub294 \ud55c \ud574\uac00 \uc9c0\ub098\uace0 Adobe CC 2020 \ubc84\uc804\uc5d0\uc11c Adobe CC 2021\ub85c \ubcc0\ud654\ud558\uba74\uc11c \uc5b4\ub5a4 \uc2e0\uae30\ub2a5\ub4e4\uc774 \uc0dd\uacbc\ub294\uc9c0 \uc54c\ub824\uc90d\ub2c8\ub2e4.\\n\\n \ubb3c\ub860 \uc5c5\ub370\uc774\ud2b8\uac00 \ub418\uba74 \ubb34\uc5c7\uc774 \uc5c5\ub370\uc774\ud2b8\ub410\ub294\uc9c0 Adobe \uacf5\uc2dd \uc0ac\uc774\ud2b8 \ubb38\uc11c\ub97c \ubcf4\uba74 \ub354 \ube60\ub974\uac8c \ud655\uc778\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4\ub9cc, \uadf8\uac78 \ud655\uc778\ud558\uae30 \uadc0\ucc2e\uac70\ub098 \uc5b4\ub824\uc6b4 \ubd84\ub4e4\uc740 \uac04\ub2e8\ud558\uac8c \ucc45\uc744 \ubcf4\uba74 \ub429\ub2c8\ub2e4. \uc5b4\ub5a4 \uae30\ub2a5\ub4e4\uc774 \uc5c5\ub370\uc774\ud2b8\ub410\ub294\uc9c0 \uac00\uc7a5 \ube68\ub9ac \ud655\uc778\ud558\ub294 \ubc29\ubc95\uc740 \uacf5\uc2dd \ubb38\uc11c\ub97c \ubcf4\ub294 \uac83\uc774 \uac00\uc7a5 \uc88b\uc2b5\ub2c8\ub2e4.\\n\\n2. \uc608\uc81c \ud30c\uc77c\ub4e4\uc744 \uc81c\uacf5\ud574\uc90d\ub2c8\ub2e4.\\n\\n \ucd08\ubcf4\ubd84\ub4e4\uc5d0\uac8c\ub294 \uc774\ubbf8\uc9c0 \ub370\uc774\ud130\uac00 \uc5c6\uc744 \uc218 \uc788\uae30\uc5d0 \uc774 \ucc45\uc740 \uc608\uc81c \ud30c\uc77c\uc744 \uc81c\uacf5\ud569\ub2c8\ub2e4. \ub610\ud55c, \uc2e4\uc2b5 \ud6c4 \uc644\uc131 \ud30c\uc77c\ub3c4 \uc81c\uacf5\ud558\ub2c8 \uba3c\uc800 \uc2e4\uc2b5\ud574 \ubcf4\uace0 \ube44\uad50\ud560 \uc218\ub3c4 \uc788\uc2b5\ub2c8\ub2e4.\\n\\n3. \ud3ec\ud1a0\uc0f5\uacfc \uc77c\ub7ec\uc2a4\ud2b8\ub808\uc774\ud130\ub97c \ud55c \uad8c\uc5d0 \uacf5\ubd80\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\n\\n \uc774 \ubd80\ubd84\uc740 \ub204\uad6c\uc5d0\uac8c\ub294 \uc7a5\uc810\uc774 \ub420 \uc218\ub3c4 \uc788\uace0 \ub2e8\uc810\uc774 \ub420 \uc218\ub3c4 \uc788\uc744 \uac83 \uac19\uc2b5\ub2c8\ub2e4. \ub530\ub85c \ucd9c\ud310\ub418\uba74 \ub354 \uc790\uc138\ud788 \uacf5\ubd80\ud560 \uc218 \uc788\ub2e4\ub294 \uc7a5\uc810\uc774 \uc788\uaca0\uc9c0\ub9cc, \uc800 \uac19\uc740 \uacbd\uc6b0\uc5d0\ub294 \uac04\ub2e8\ud558\uac8c \uacf5\ubd80\ud558\uace0 \uc2f6\ub2e4\ub294 \uc0dd\uac01\uc774 \ub354 \ub9ce\uc558\uae30\uc5d0 \uc774\ub807\uac8c \ud55c \uad8c\uc73c\ub85c \ub418\uc5b4 \uc788\uc5b4\uc11c \uc88b\uc558\uc2b5\ub2c8\ub2e4.\\n\\n \uc194\uc9c1\ud788 \uc774\ub807\uac8c \ud55c \uad8c\uc73c\ub85c \uc77d\uc5c8\uc5b4\ub3c4 \uc800\ub294 \uc774 \ucc45\uc774 \uac04\ub2e8\ud558\ub2e4\uace0\ub294 \uc0dd\uac01\uc774 \ub4e4\uc9c0 \uc54a\uc558\uc2b5\ub2c8\ub2e4. \uc624\ud788\ub824 \ub2e4\uc591\ud55c \uae30\ub2a5\ub4e4\uc744 \uc54c\ub824\uc8fc\uae30\uc5d0 \uc790\uc138\ud558\ub2e4\uace0 \ub290\uaf08\uc2b5\ub2c8\ub2e4.\\n\\n \ud3ec\ud1a0\uc0f5\uacfc \uc77c\ub7ec\uc2a4\ud2b8\ub808\uc774\ud130\ub97c \ucc45 \ud55c \uad8c\uc73c\ub85c \ub9cc\ub4e4\uc5b4\uc11c \uadf8\ub7f0\uc9c0 \uac00\ubccd\uac8c \uc785\ubb38\uc6a9\uc73c\ub85c \uacf5\ubd80\ud558\uae30\uc5d0\ub294 \uc88b\uc740 \uac83 \uac19\uc2b5\ub2c8\ub2e4. \uc774\uc804 \ub9db\uc788\ub294 \ub514\uc790\uc778 CC 2020\uc740 \ud3ec\ud1a0\uc0f5, \uc77c\ub7ec\uc2a4\ud2b8\ub808\uc774\ud130 \ub530\ub85c \ucd9c\ud310\ud558\uae30\ub3c4 \ud588\uace0 \uac19\uc774 \ucd9c\ud310\ud558\uae30\ub3c4 \ud558\uc168\ub294\ub370 \uc774\ubc88\uc5d0\ub294 \uc5b4\ub5bb\uac8c \ub610 \ub530\ub85c \ucd9c\ud310\ud558\uc2e4\uc9c0\ub294 \uc798 \ubaa8\ub974\uaca0\uc2b5\ub2c8\ub2e4.\\n\\n4. \uae30\ub2a5\ub4e4\uc744 \uadf8\uc800 \uc0ac\uc6a9\ud558\ub294 \uac83\uc774 \uc544\ub2cc \ud65c\uc6a9\ud558\ub294 \ubc29\ubc95\uc744 \uc54c\ub824\uc90d\ub2c8\ub2e4.\\n\\n \ud3ec\ud1a0\uc0f5\uc5d0\uc11c\ub294 \uac1c\uc778\uc801\uc73c\ub85c \uc778\ubb3c \uc0ac\uc9c4 \ubcf4\uc815\ubc95\uc774 \uac00\uc7a5 \uc720\uc6a9\ud588\ub358 \uac83 \uac19\uc2b5\ub2c8\ub2e4. SNS\uc5d0 \uc0ac\uc9c4\uc744 \uc62c\ub9b4 \ub54c \ubc14\ub85c \ud65c\uc6a9\ud560 \uc218 \uc788\ub294 \uc88b\uc740 \ubc29\ubc95\ub4e4\uc778 \uac70 \uac19\uc2b5\ub2c8\ub2e4.\\n\\n \uc77c\ub7ec\uc2a4\ud2b8\ub808\uc774\ud130\uc5d0\uc11c\ub294 \uae00\uc790 \ubb38\uad6c \ub610\ub294 \ub85c\uace0\ub098 SNS\uc5d0\uc11c \uad11\uace0\ud560 \ub54c \uc790\uc8fc \uc0ac\uc6a9\ub418\ub294 \ud398\uc774\uc9c0 \ub4f1\uc744 \ub514\uc790\uc778\ud558\ub294 \uc2e4\uc2b5\uc744 \ud574\ubcfc \uc218 \uc788\uae30 \ub54c\ubb38\uc5d0 \uc774 \uc810\uc5d0\uc11c \ud65c\uc6a9\ub3c4\uac00 \ub192\uc740 \uac83 \uac19\uc2b5\ub2c8\ub2e4.\\n\\n## \uc544\uc26c\uc6b4 \uc810\\n\\n1. \ucc45\uc5d0\uc11c \ud504\ub85c\uadf8\ub7a8\uc744 \ud55c\uae00 \ubc84\uc804\uc73c\ub85c \uc0ac\uc6a9\ud558\uc9c0 \uc54a\uace0 \uc601\uc5b4 \ubc84\uc804\uc744 \uc0ac\uc6a9\ud569\ub2c8\ub2e4.\\n\\n \uc800 \uac19\uc740 \uacbd\uc6b0\uc5d0\ub294 \ub2e4\uc591\ud55c \ub3c4\uad6c\ub4e4\uc744 \uc601\uc5b4\ub85c \uc368\uc11c \uc5b4\ub290 \uc815\ub3c4 \uc775\uc219\ud558\uc9c0\ub9cc, \ub300\ubd80\ubd84\uc758 \uc0ac\ub78c\uc740 \ud55c\uae00\uc744 \uc4f8 \uac83\uc73c\ub85c \uc608\uc0c1\ub418\uae30\uc5d0 \uc870\uae08 \uc544\uc26c\uc6e0\uc2b5\ub2c8\ub2e4.\\n\\n `\uae30\ub2a5 \uaf3c\uaf3c \uc775\ud788\uae30`\uac00 \uc788\uc5b4\uc11c \ub2e4\ud589\ud788 \ub300\ubd80\ubd84 \uc124\uba85\uc744 \uc790\uc138\ud558\uac8c \ub2e4 \ud574\uc8fc\uc9c0\ub9cc, \uadf8\ub0e5 \uc601\uc5b4\ub97c \uaebc\ub9ac\uc2dc\ub294 \ubd84\ub4e4\uc774 \uc788\uc744 \uac83 \uac19\uc2b5\ub2c8\ub2e4. \uc5c5\ubb34\ub098 \ucde8\ubbf8 \ub4f1\uc73c\ub85c \ud65c\uc6a9\ud558\uace0\uc790 \ud558\ub294 \uc77c\ubc18\uc778\uc5d0\uac8c\ub294 \uc601\uc5b4\uc5d0 \ub300\ud55c \uc9c4\uc785\uc7a5\ubcbd\uc774 \uc788\uc9c0 \uc54a\uc744\uae4c \uc0dd\uac01\uc774 \ub429\ub2c8\ub2e4.\\n\\n2. \uac15\uc758\uac00 \uc5c6\uc2b5\ub2c8\ub2e4.\\n\\n \uac15\uc758\ub294 \ud544\uc218\uac00 \uc544\ub2c8\uae34 \ud569\ub2c8\ub2e4. \uadf8\ub807\uc9c0\ub9cc \uc774\ub807\uac8c \uc2e4\uc2b5\uc774 \uc694\uad6c\ub418\ub294 \ucc45\ub4e4\uc740 \uac15\uc758\uac00 \uc788\uc5c8\ub354\ub77c\uba74 \uc2e4\uc2b5\ud558\uae30 \ub354 \ud3b8\ud560 \uac83\uc774\ub77c\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4.\\n\\n \ucc45\uc5d0\uc11c \uc124\uba85\ud558\ub294 \ub300\ub85c \uc2e4\uc2b5\uc744 \uc9c4\ud589\ud588\uc9c0\ub9cc, \ucc45\uc5d0\uc11c \ub098\uc628 \uacb0\uacfc \uc774\ubbf8\uc9c0\uc640 \uc81c\uac00 \uc2e4\uc2b5\ud55c \ud30c\uc77c\ub4e4\uc744 \ube44\uad50\ud588\uc744 \ub54c \uacb0\uacfc\uac00 \ube44\uc2b7\ud558\uae34 \ud558\uc9c0\ub9cc \uc870\uae08 \ub2e4\ub974\uae30\ub3c4 \ud588\uc2b5\ub2c8\ub2e4.\\n\\n \uc5bc\ub9c8\ub098 \uc870\uc808\ud558\ub290\ub0d0, \uc5b4\ub290 \uc815\ub3c4 \ub4dc\ub798\uadf8\ub97c \ud558\ub290\ub0d0 \ub4f1\uc758 \uc774\uc720\uc5d0 \ub530\ub77c \ub2ec\ub77c\uc9c0\ub294 \uac83\uc774 \ub514\uc790\uc778\uc774\uae30 \ub54c\ubb38\uc5d0 \ucc45\uc758 \ub0b4\uc6a9\uc744 \uac10\uc73c\ub85c \ud30c\uc545\ud558\uace0 \uc0ac\uc6a9\ud574\ubcf4\ub294 \uac83\uc774 \uc5b4\ub824\uc6e0\uc2b5\ub2c8\ub2e4.\\n\\n \uc800\ub294 \uc5b4\ub290 \uc815\ub3c4 \uc790\uc138\ud55c \uc124\uba85\uc744 \uc88b\uc544\ud558\uae30 \ub54c\ubb38\uc5d0 \uac15\uc758\uac00 \uc788\uc5c8\uc73c\uba74 \ub354 \uc88b\uc558\uaca0\ub2e4\ub294 \uc0dd\uac01\uc774 \ub4e4\uc5c8\uc2b5\ub2c8\ub2e4.\\n\\n## \uac1c\uc778\uc801\uc73c\ub85c \uacf5\ubd80\ud558\uba70 \ub290\ub080 \uc810\\n\\n\uacf5\ubd80\ud558\uba74\uc11c \uac00\uc7a5 \ub180\ub790\ub358 \uc810\uc740 \uc778\uacf5\uc9c0\ub2a5 \uae30\ubc18\uc758 Neural \ud544\ud130\uc600\uc2b5\ub2c8\ub2e4. \uc774 \ud544\ud130\ub97c \ud65c\uc6a9\ud558\uba74 \uc791\uc5c5 \uacfc\uc815\uc744 \uac70\uce58\uc9c0 \uc54a\uc544\ub3c4 \uba87 \ubc88\uc758 \ud074\ub9ad\ub9cc\uc73c\ub85c \uc2e0\uae30\ud55c \uacb0\uacfc\ub4e4\uc744 \ub9cc\ub4e4\uc5b4\uc90d\ub2c8\ub2e4. \uc608\ub97c \ub4e4\uc5b4 \uc778\ubb3c\uc0ac\uc9c4\uc5d0\uc11c \uc0ac\ub78c\uc758 \ub098\uc774\uc640 \ud45c\uc815, \uc2dc\uc120 \ub4f1\uc744 \ubcc0\uacbd\ud558\uac70\ub098 \ud751\ubc31\uc5d0\uc11c \uceec\ub7ec\ub85c \ubc14\uafb8\ub294 \ub4f1\uc758 \uae30\ub2a5\ub4e4\uc774 \uc788\uc2b5\ub2c8\ub2e4.\\n\\n\uc774\ub7f0 \uae30\ub2a5\ub4e4\uc744 \uc0ac\uc6a9\ud558\uba74\uc11c \uc601\uc0c1, \uc774\ubbf8\uc9c0 \ucc98\ub9ac \uacfc\uc81c\ub4e4\uc744 \ud558\uba70 \ud544\ud130\ub4e4\uc744 \uc9c1\uc811 \uad6c\ud604\ud558\ub358 \ub54c\uac00 \uc790\uc5f0\uc2a4\ub7fd\uac8c \ud68c\uc0c1\ub410\uc2b5\ub2c8\ub2e4. \ub610\ud55c, \uc81c\uac00 \uacf5\ubd80\ud558\ub294 \uac83\ub4e4\ubcf4\ub2e4 \uae30\uc220\uc758 \ubc1c\uc804\uc740 \ub354 \ube60\ub974\ub2e4\ub294 \uac83\uc744 \ubab8\uc18c \ub290\uaf08\uc5c8\uc2b5\ub2c8\ub2e4. \uc5bc\ub978 \ub2e4\uc591\ud55c \uae30\ub2a5\ub4e4\uae4c\uc9c0\ub3c4 \uc9c1\uc811 \uad6c\ud604\ud560 \uc218 \uc788\ub294 \uc0ac\ub78c\uc774 \ub418\uace0 \uc2f6\ub2e4\uace0 \uc0dd\uac01\ud588\uc2b5\ub2c8\ub2e4.\\n\\n\ub3c4\uad6c\ub97c \uc798 \uc0ac\uc6a9\ud558\ub294 \uc0ac\ub78c\uc774 \ub418\ub294 \uac83\ub3c4 \uc88b\uc9c0\ub9cc, \uc800\ub294 \uc9c1\uc811 \ub9cc\ub4dc\ub294 \uc0ac\ub78c\uc774 \ub418\uace0 \uc2f6\uc2b5\ub2c8\ub2e4. \ucc3d\uc870\ub294 \uc5b4\uca4c\uba74 \uc8fc\uc5b4\uc9c4 \uae30\ub2a5\uc744 \ud65c\uc6a9\ud558\ub294 \uac83\uc744 \ub118\uc5b4\uc11c \uc0dd\uac01\ud558\uc9c0 \ubabb\ud55c \uac83\ub4e4\uc744 \ub9cc\ub4dc\ub294 \uac83\uc774\ub2c8\uae4c \uac1c\ubc1c\uc790\ub77c\ub294 \uc9c1\uc5c5\uc5d0, \ud504\ub85c\uadf8\ub798\ubc0d\uc5d0 \ub354\uc6b1 \ub9e4\ub825\uc744 \ub290\ub07c\ub294 \uac83 \uac19\uc2b5\ub2c8\ub2e4. \uc5f4\uc2ec\ud788 \uacf5\ubd80\ud574\uc57c\uaca0\ub2e4\uace0 \ub2e4\uc9d0\ud558\ub294 \uacc4\uae30\uac00 \ub410\uc2b5\ub2c8\ub2e4.\\n\\n## \uacb0\ub860 \ud3c9\\n\\n`\uc800\ucc98\ub7fc \ud3ec\ud1a0\uc0f5\uc744 \ud55c \ubc88\ucbe4 \uacf5\ubd80\ub97c \ud574\ubd24\uc9c0\ub9cc, \uae30\uc5b5\uc774 \uc798 \uc548 \ub098\uc11c \ub2e4\uc2dc \uacf5\ubd80\ud574\ubcf4\uace0 \uc2f6\uc740 \ubd84 \ub610\ub294 \ud3ec\ud1a0\uc0f5, \uc77c\ub7ec\uc2a4\ud2b8\ub808\uc774\ud130\ub97c \ucc98\uc74c \uacf5\ubd80\ud574\ubcf4\uc2dc\ub294 \ubd84\ub4e4\uaed8 \ucd94\ucc9c\ud569\ub2c8\ub2e4.`\\n\\n`\uc774 \ucc45\uc73c\ub85c\ub9cc \uc774 \ud504\ub85c\uadf8\ub7a8\ub4e4\uc744 \ub9c8\uc2a4\ud130\ud558\uaca0\ub2e4\uace0 \uc0dd\uac01\ud558\uc2dc\ub294 \ubd84\ub4e4\uaed8\ub294 \ucd94\ucc9c\ud558\uc9c0 \uc54a\uc2b5\ub2c8\ub2e4. \uc774 \ucc45\uc740 \uc785\ubb38\uc6a9\uc774\uba70 \uacb0\ucf54 \uc774 \ucc45\uc744 \ubd24\ub2e4\uace0 \ud574\uc11c \uc804\ubb38\uac00\uac00 \ub418\uc9c0\ub294 \uc54a\uc2b5\ub2c8\ub2e4.`\\n\\n\uc5b4\ub514\uae4c\uc9c0\ub098 \uc81c \uc0dd\uac01\uc77c \ubfd0\uc774\ub2c8 \ucc38\uace0\ub9cc \ud574\uc8fc\uc2dc\uae38 \ubc14\ub78d\ub2c8\ub2e4."},{"id":"/LINE-developer","metadata":{"permalink":"/en/blog/LINE-developer","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2021-02-07-LINE-developer/LINE-developer.md","source":"@site/blog/2021-02-07-LINE-developer/LINE-developer.md","title":"\ud83d\udcd6 \ub098\ub294 LINE \uac1c\ubc1c\uc790\uc785\ub2c8\ub2e4","description":"\ub77c\uc778\uc758 \uac1c\ubc1c\uace0\uc218 12\uc778\uc758 \ub3c4\uc804\uacfc \uae30\ud68c, \uc131\uc7a5\uc758 \uac1c\ubc1c \ub77c\uc774\ud504","date":"2021-02-07T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \ub098\ub294 LINE \uac1c\ubc1c\uc790\uc785\ub2c8\ub2e4","date":"2021-02-07","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/LINE-developer","description":"\ub77c\uc778\uc758 \uac1c\ubc1c\uace0\uc218 12\uc778\uc758 \ub3c4\uc804\uacfc \uae30\ud68c, \uc131\uc7a5\uc758 \uac1c\ubc1c \ub77c\uc774\ud504","keywords":["LINE","\uac1c\ubc1c\uc790","\uc131\uc7a5","\uacf5\ubd80"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \ub9db\uc788\ub294 \ub514\uc790\uc778 \ud3ec\ud1a0\uc0f5&\uc77c\ub7ec\uc2a4\ud2b8\ub808\uc774\ud130 CC 2021","permalink":"/en/blog/delicious-design-Ps-Ai"},"nextItem":{"title":"\ud83d\udcd6 \uc720\ub2c9\uc2a4\uc758 \ud0c4\uc0dd","permalink":"/en/blog/UNIX-history-memoir"}},"content":"## Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/LINE-developer.jpg)](http://www.kyobobook.co.kr/product/detailViewKor.laf?ejkGb=KOR&mallGb=KOR&barcode=9791162242162&orderClick=LEa&Kc=)\\n\\n- \uc81c\ubaa9: \ub098\ub294 LINE \uac1c\ubc1c\uc790\uc785\ub2c8\ub2e4\\n- \uc800\uc790: \uac15\uc724\uc2e0, \uae40\uc601\ud658, \uae40\uc7ac\uc11d, \uae40\uc815\uc5fd, \uae40\ud0dd\uc8fc, \ub178\uc2b9\ud5cc, \ubc15\ubbfc\uc6b0, \ubc30\uad8c\ud55c, \uc774\uc11c\uc5f0, \uc774\uc2b9\uc9c4, \uc774\ud64d\uaddc, \ud558\ud0dc\ud638\\n- \ucd9c\ud310\uc0ac: \ud55c\ube5b\ubbf8\ub514\uc5b4\\n- \ucd9c\uac04: 2019-08-23\\n\\n{/* truncate */}\\n\\n\\n## \uc65c \uc77d\uc5c8\uc5b4\uc694?\\n\\n\uc774\uc81c \uc81c \ube14\ub85c\uadf8\uc5d0\uc11c \ucc45 \ub9ac\ubdf0\ub97c \ubcf4\uc2dc\ub294 \ubd84\uc774\uba74 \uc544\uc2e4 \uac81\ub2c8\ub2e4. \uc65c \uc77d\uc5c8\ub294\uc9c0 \ucc45\ub9c8\ub2e4 \uacc4\uc18d \uc801\uc744 \uac70\uc608\uc694. \ub531\ud788 \uc7ac\ubc0c\ub294 \uc774\uc720\uac00 \uc788\ub294 \uac74 \uc544\ub2c8\uc9c0\ub9cc, \uadf8\ub0e5 \uc801\uc744 \uac70\uc608\uc694.\\n\\n\uc774 \ucc45\ub3c4 \ucc98\uc74c \ub098\uc654\uc744 \ub54c, \uad00\uc2ec \uc788\uac8c \uc0dd\uac01\ud558\ub358 \ucc45\uc774\uc5c8\ub294\ub370 \uc77d\uc744 \uae30\ud68c\uac00 \uc5c6\uc5b4\uc11c \ubabb \uc77d\uc5c8\uc5c8\uc8e0. \ub9e4\uc77c \uac00\ub358 \ub3c4\uc11c\uad00\uc774 \uc544\ub2cc \ub2e4\ub978 \ub3c4\uc11c\uad00\uc744 \uac00\ub2c8\uae4c \uc774 \ucc45\uc774 \uc788\ub354\ub77c\uace0\uc694. \uac1c\ubc1c\uc790 \ubb38\ud654, \ub77c\uc774\ud504, \ubcf8\ubc1b\uc544\uc57c \ud560 \uc810 \uac19\uc740 \ubd80\ubd84\uc5d0 \uad00\uc2ec \ub9ce\uc544\uc11c \uc77d\uac8c \ub410\uc2b5\ub2c8\ub2e4. \\n\\n\uc194\uc9c1\ud788 \uc790\uc2e0\uc774 \uac1c\ubc1c\uc790, \uc989 \ud504\ub85c\uadf8\ub798\uba38\ub97c \ubaa9\ud45c\ub85c \ud55c\ub2e4\uba74 LINE \uac1c\ubc1c\uc790\uc758 \ub77c\uc774\ud504\ub97c \uc54c\ub824\uc900\ub2e4\ub294\ub370 \uc548 \uad81\uae08\ud558\uaca0\uc5b4\uc694? LINE \uc774\uc678\uc758 \uc9c1\uc7a5\uc744 \uac16\uace0 \uc788\uc73c\uc2e0 \ubd84\ub4e4\uc740 \uc9c0\uc778\ub4e4\uaed8 \ub4e4\uc744 \uc218 \uc788\uace0 \ub610\ub294 \ube14\ub77c\uc778\ub4dc \uac19\uc740 \ucee4\ubba4\ub2c8\ud2f0\uc5d0\uc11c \uc5b4\ub5bb\uac8c \uac1c\ubc1c\uc790\ub4e4\uc774 \uc77c\ud558\ub294\uc9c0 \uc54c \uc218 \uc788\uc9c0\ub9cc \uc804 \ud3c9\ubc94\ud55c \ub300\ud559\uc0dd\uc774\ub77c \ub108\ubb34 \uad81\uae08\ud588\uc2b5\ub2c8\ub2e4.\\n\\n\uc774\ub7f0 \uc885\ub958\uc758 \ucc45\uc744 \uc77d\uc5b4\ubcf4\uc2e0 \ubd84\ub4e4\uc740 \uc544\uc2dc\uaca0\uc9c0\ub9cc \ubcf4\ud1b5 \uc11c\ub860\uc774 \uc788\uc2b5\ub2c8\ub2e4. \uc774 \ucc45\uc758 \uc11c\ub860\uc744 \uc77d\uc5b4\ubcf4\uba74 \uac1c\ubc1c\uc790\ub97c \uafc8\uafb8\ub294 \ud559\uc0dd\uc5d0\uac8c\ub294 \ub108\ubb34 \ub9e4\ub825\uc801\uc778 \ub9d0\ub4e4\uc774 \uc788\uc5b4\uc11c \uac00\uc838\uc654\uc2b5\ub2c8\ub2e4.\\n\\n> \\"\uc774 \ucc45\uc774 \uac1c\ubc1c\uc790\ub85c\uc11c\uc758 \ubbf8\ub798\uc640 \uc0b6\uc5d0 \ub300\ud574 \uace0\ubbfc\ud558\uace0 \uc8fc\uc800\ud558\ub294 \uccad\ub144\ub4e4\uc5d0\uac8c \'\uafc8\uc744 \uafbc\ub2e4\uba74 \ud560 \uc218 \uc788\ub2e4\'\ub77c\ub294 \uc6a9\uae30\ub97c \uc2ec\uc5b4\uc8fc\ub294 \ub370 \uc870\uae08\uc774\ub77c\ub3c4 \ub3c4\uc6c0\uc774 \ub420 \uc218 \uc788\ub2e4\uba74 \ub354\ud560 \ub098\uc704 \uc5c6\ub294 \ud070 \uae30\uc068\uc77c \uac83\uc774\ub2e4.\\"\\n\\n## LINE\\n\\n\ub77c\uc778\uc774\ub77c\uace0 \ud558\uba74 \ubb50\uac00 \ub5a0\uc624\ub974\uc138\uc694?\\n\\n\uc804 \uc9c0\uae08\uae4c\uc9c0\uc758 \uc0dd\uac01\uc740 \uc194\uc9c1\ud788 \ub9d0\ud558\uba74 `\ud55c\uad6d\ubcf4\ub2e4 \uc77c\ubcf8\uc5d0\uc11c \ub9ce\uc774 \uc4f0\ub294 \uba54\uc2e0\uc800`\uc774\uc5c8\uc2b5\ub2c8\ub2e4. \uc27d\uac8c `\uc77c\ubcf8\uc758 \uce74\uce74\uc624\ud1a1`\uc774\ub77c\uace0 \uc0dd\uac01\ud588\uc8e0. \uce74\uce74\uc624\uac00 \ud55c\uad6d\uc5d0\uc11c \ud558\ub294 \uac83\ucc98\ub7fc `\\"\ub77c\uc778\uc740 \uc77c\ubcf8\uc5d0\uc11c \ub9ce\uc740 \uc218\uc775\uc744 \ub0b4\uaca0\uad6c\ub098.\\"`\ub77c\uace0 \uc27d\uac8c \uc0dd\uac01\ud588\uc5b4\uc694.\\n\\n\uadf8\ub7ec\ub098 \uc774 \ucc45\uc744 \uc77d\uc73c\uba74\uc11c LINE\uc774\ub77c\ub294 \ud68c\uc0ac\ub294 \uc77c\uc744 \uc77c\ubcf8\uc5d0 \uad6d\ud55c\ud558\uc5ec\uc11c \ud558\ub294 \uac83\uc774 \uc544\ub2c8\ub77c \ub2e4\uc591\ud55c \ub098\ub77c\uc5d0\uc11c \uae00\ub85c\ubc8c\uc801\uc73c\ub85c \ud558\uace0 \uc788\ub2e4\ub294 \uac83\uc744 \uc54c\uac8c \ub410\uc2b5\ub2c8\ub2e4.\\n\\n\uac1c\uc778\uc801\uc73c\ub85c \ub77c\uc778\uc740 \ub610 \ubb34\uc2a8 \uc77c\ub4e4\uc744 \ud558\ub294\uc9c0 \uc54c\uc544\ubd24\ub294\ub370 \uba54\uc2e0\uc800 \uc774\uc678\uc5d0\ub3c4 \ub2e4\uc591\ud55c \uc77c\ub4e4\uc744 \ud558\ub354\ub77c\uace0\uc694. \ud5ec\uc2a4\ucf00\uc5b4, \uac00\uc0c1\ud654\ud3d0\ub97c \ub2e4\ub8e8\ub294 \ud30c\uc774\ub0b8\uc15c, \ub77c\uc778\ud504\ub80c\uc988 \ub4f1 \ud55c\uad6d\uc5d0\uc11c\ub9cc \uc720\uba85\ud558\uc9c0 \uc54a\uc744 \ubfd0\uc774\uc9c0, \ub2e4\ub978 \ub098\ub77c\uc5d0\uc11c\ub294 \uaf64 \ub2e4\uc591\ud55c \uc77c\ub4e4\uc744 \ud558\uace0 \uc788\uc2b5\ub2c8\ub2e4.\\n\\n## \ub9e4\ub825\uc801\uc778 \ubb38\ud654\\n\\n\uc774 \ucc45\uc744 \uc77d\uc73c\uba74\uc11c \ub77c\uc778\uc774\ub77c\ub294 \ud68c\uc0ac\ub294 \uac1c\ubc1c\uc790\ub4e4\uc774 \uc77c\ud558\uae30 \uc88b\uc740 \ud68c\uc0ac\ub77c\uace0 \uc0dd\uac01\uc774 \ub4e4\uba74\uc11c \uc774\uacf3\uc5d0\uc11c \uc77c\ud558\uc2dc\ub294 \ubd84\ub4e4\uaed8 \ubd80\ub7ec\uc6c0\uc744 \ub290\uaf08\uc2b5\ub2c8\ub2e4.\\n\\n\uac1c\uc778\uc801\uc73c\ub85c \uc2e4\ud328\ub97c \ud0d3\ud558\uc9c0 \uc54a\ub294 \ubb38\ud654\uac00 \ub108\ubb34 \ub9e4\ub825\uc801\uc73c\ub85c \ub2e4\uac00\uc654\uc2b5\ub2c8\ub2e4. \uc0ac\ub78c\uc740 \ub204\uad6c\ub098 \uc2e4\uc218\ub97c \ud560 \uc218 \uc788\uae30\uc5d0 \uc5b8\uc81c\ub4e0\uc9c0 \ucf54\ub4dc\uc5d0 \ubb38\uc81c\uac00 \uc0dd\uaca8\uc11c \uc11c\ube44\uc2a4\uc5d0 \uc9c0\uc7a5\uc744 \uc904 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\n\\n`\\"\uac04\ub2e8\ud55c \ucf54\ub529 \uc624\ub958\uc778\ub370\ub3c4 \ucf54\ub4dc \ub9ac\ubdf0\uc5d0\uc11c \ubc1c\uacac\ud574\ub0b4\uc9c0 \ubabb\ud55c \uc81c \uc798\ubabb\uc774\ub124\uc694.\\"`\\n\\n\uc2e4\uc218\uc5d0 \ub300\ud574 \ub9ce\uc774 \uc18d\uc0c1\ud560 \ub54c \uc774\ub7f0 \ub9d0\uc744 \ud574\uc904 \uc218 \uc788\ub294 \ud300\uc6d0\uc774 \uc788\ub2e4\uba74 \uc5bc\ub9c8\ub098 \uc88b\uc744\uae4c\uc694? \uc544\ub2c8, \uc88b\ub2e4\uae30\ubcf4\ub2e4\ub294 \ub108\ubb34 \uac10\uc0ac\ud558\uaca0\uc8e0. \uc11c\ub85c\ub97c \ud0d3\ud558\ub294 \ub370\uc5d0 \uc2dc\uac04\uc744 \ub0ad\ube44\ud558\uc9c0 \uc54a\uace0, \ud568\uaed8 \ubb38\uc81c\ub97c \ud574\uacb0\ud558\ub294 \ubb38\ud654. \ubcf4\ud1b5 \ud68c\uc0ac\uc5d0\uc11c \uc774\ub7f0 \uc77c\ub4e4\uc774 \uac00\ub2a5\ud560\uae4c\uc694? \\n\\n\ub610\ud55c, \uc7a5\uc560\ub97c \ud574\uacb0\ud558\uace0 \ub098\uba74 \uc989\uc2dc \uc7a5\uc560 \ubcf4\uace0\uc11c\ub97c \uc791\uc131\ud558\uace0 \ub9ac\ubdf0\ub97c \ud558\ub294 \uac83\ub3c4 \ub108\ubb34 \ub300\ub2e8\ud558\ub2e4\uace0 \uc0dd\uac01\ud588\uc2b5\ub2c8\ub2e4. \uc7a5\uc560 \ub9ac\ubdf0\ub294 \ub2f9\uc5f0\ud558\ub2e4\uace0 \uc0dd\uac01\ud588\uc9c0\ub9cc, \ubcf4\uace0\uc11c\uae4c\uc9c0 \uc368\uc11c \ubb38\uc11c\ud654\ub85c \uae30\ub85d\ud55c\ub2e4\ub294 \uac83\uc740 \uc0dd\uac01\ub3c4 \ubabb \ud588\uc2b5\ub2c8\ub2e4. \\n\\n\ubcf4\ud1b5 \ud68c\uc0ac\ub77c\uba74 \ubc14\uc058\ub2e4\uace0 \uc77c\uc5d0 \ub300\ud558\uc5ec \ubb38\uc11c\ud654\ub97c \uc798 \uc9c4\ud589\ud558\uc9c0 \uc54a\uc744\ud150\ub370 \ub77c\uc778\uc740 \ubb38\uc11c\ud654\ub97c \ud558\uc5ec \ubd88\ud544\uc694\ud55c \uc624\ud574\ub3c4 \uc5c6\uc560\uace0, \uac80\uc0c9\uc73c\ub85c \ubaa8\ub4e0 \uc815\ubcf4\ub97c \ucc3e\uc544 \uc5c5\ubb34\uc5d0 \ub3c4\uc6c0\uc774 \ub418\ub3c4\ub85d \ud55c\ub2e4\uace0 \ud569\ub2c8\ub2e4.\\n\\n\uc774 \ubc16\uc5d0\ub3c4 \uc57d 200\ub9cc\uc6d0 \uc9dc\ub9ac \ud5c8\uba3c\ubc00\ub7ec \uc758\uc790\ub97c \uc4f4\ub2e4\uac70\ub098, \uc790\uc2e0\ub4e4\uc758 \uac1c\ubc1c \uc870\uc9c1\uc744 \uc54c\ub824\uc11c \uc6b0\uc218\ud55c \uac1c\ubc1c\uc790\ub4e4\uc744 \ub04c\uc5b4\uc624\ub294 Developer Relations \ud300\uc774 \ub530\ub85c \uc788\ub294 \ub4f1\uc758 \uac1c\ubc1c\uc5d0 \uc9d1\uc911\ud560 \uc218 \uc788\ub3c4\ub85d \ub9cc\ub4e4\uc5b4\uc8fc\ub294 \ud68c\uc0ac\uc778 \uac83\uc744 \uc54c\uac8c \ub410\uc2b5\ub2c8\ub2e4.\\n\\n\\"\uc815\ub9d0 \ub77c\uc778\uc740 \uc131\uc7a5\ud558\uace0\ud508 \uac1c\ubc1c\uc790\ub4e4\uc774 \ub9c8\uc74c \ud3b8\ud558\uac8c \uac1c\ubc1c\ud560 \uc218 \uc788\ub294 \uacf3\uc774 \uc544\ub2d0\uae4c?\\" \uc0dd\uac01\uc774 \ub4e4 \uc815\ub3c4\uc785\ub2c8\ub2e4.\\n\\n\ub610\ud55c, \ud574\ud0b9\uc5d0 \uad00\uc2ec\uc774 \uc788\uc73c\uc168\ub2e4\uba74 \ud55c \ubc88\ucbe4\uc740 \ub4e4\uc5b4\ubcf4\uc168\uc744 \ud68c\uc0ac\uc778 \uadf8\ub808\uc774\ud574\uc26c, \uc9c0\uae08\uc740 \ub77c\uc778\uc758 \uadf8\ub808\uc774\ub7a9\uc5d0 \uc788\uc73c\uc2e0 \uc774\uc2b9\uc9c4 \ub2d8 \uac19\uc740 \uace0\uc218\ub4e4\uc774 \ub77c\uc778\uc5d0\ub294 \ub9ce\uc774 \uc788\ub2e4\ub294 \uac83\ubd80\ud130 \uc8fc\ub2c8\uc5b4 \uac1c\ubc1c\uc790\ub4e4\uc5d0\uac8c\ub294 \ubc30\uc6b8 \uc218 \uc788\ub294 \uc0ac\ub78c\ub4e4\uc774 \ub9ce\ub2e4\ub294 \uac83\uc774\ub2c8 \ub9e4\ub825\uc801\uc774\ub77c\uace0 \uc0dd\uac01\ud588\uc2b5\ub2c8\ub2e4.\\n\\n\uc774 \ubc16\uc5d0\ub3c4 \uc88b\uc740 \ubb38\ud654\uc640 \ubd80\ub7ec\uc6b4 \uc810\ub4e4\uc774 \ub9ce\uc9c0\ub9cc, \uae30\uc220\ud558\ub294 \uac74 \uc5ec\uae30\uae4c\uc9c0 \ud558\uaca0\uc2b5\ub2c8\ub2e4. \uacb0\ub860\uc740 \ub77c\uc778\uc740 \uc815\ub9d0 \uac1c\ubc1c\uc790\ub4e4\uc774 \ud558\uace0 \uc2f6\uc740 \uac1c\ubc1c\uc744 \ud558\uba70 \uc131\uc7a5\ud560 \uc218 \uc788\ub294 \uacf3\uc774\ub77c\ub294 \uc810\uc774 \ub108\ubb34 \ubd80\ub7ec\uc6e0\uc2b5\ub2c8\ub2e4.\\n\\n## \ucc45\\n\\n\uc774 \ucc45\uc740 \ub77c\uc778\uc5d0\uc11c \uc77c\ud558\ub294 12\uba85\uc758 \uac1c\ubc1c \uace0\uc218\ub4e4\uc758 \ub77c\uc774\ud504\ub97c \ubcf4\uc5ec\uc8fc\ub294 \ucc45\uc785\ub2c8\ub2e4. \ub610\ud55c, \ub77c\uc778\uc774 \uc5b4\ub5a4 \ud68c\uc0ac\uc778\uc9c0 \uc18c\uac1c\ud558\uba74\uc11c \uc5b4\ub5a4 \uac1c\ubc1c \ubb38\ud654\uac00 \uc774\ub8e8\uc5b4\uc838 \uc788\ub294\uc9c0 \ubcf4\uc5ec\uc90d\ub2c8\ub2e4.\\n\\n\ube44\uc804\uacf5\uc790 \ucd9c\uc2e0 \uac1c\ubc1c\uc790, \uc5d0\ubc18\uc824\ub9ac\uc2a4\ud2b8, \uc624\ud508\uc18c\uc2a4 \ub9e4\ub2c8\uc800 \ub4f1 \ub2e4\uc591\ud55c \uc0ac\ub78c\ub4e4\uc758 \uc774\uc57c\uae30\ub4e4\uc774 \uc801\ud600 \uc788\uae30\uc5d0 \uc7ac\ubc0c\uac8c \uc77d\uc5c8\uc2b5\ub2c8\ub2e4. \ube44\uc804\uacf5\uc790 \ucd9c\uc2e0 \uac1c\ubc1c\uc790\ub4e4\uc5d0 \ub300\ud55c \uc774\uc57c\uae30\ub294 \uac00\ub054 \uc811\ud588\uc9c0\ub9cc \uc5d0\ubc18\uc824\ub9ac\uc2a4\ud2b8, \uc624\ud508\uc18c\uc2a4 \ub9e4\ub2c8\uc800 \uac19\uc740 \ubd84\ub4e4\uc758 \ub77c\uc774\ud504\ub294 \ucc98\uc74c \uc54c\uac8c \ub418\uc5b4\uc11c \uc774\ub7f0 \ubd84\uc57c\ub3c4 \ub9e4\ub825\uc801\uc774\ub77c\uace0 \uc0dd\uac01\ud588\uc2b5\ub2c8\ub2e4.\\n\\n## \uacb0\ub860\ud3c9\\n\\n`\ub77c\uc778\uc740 \uc815\ub9d0 \uac1c\ubc1c\uc790\ub4e4\uc774 \ud3b8\ud558\uac8c \uac1c\ubc1c\ud560 \uc218 \uc788\ub294 \ud68c\uc0ac\uc785\ub2c8\ub2e4.`\\n\\n`\ucde8\uc900\uc0dd \ub610\ub294 \uac1c\ubc1c\uc790\ub97c \ubaa9\ud45c\ub85c \ud558\ub294 \ud559\uc0dd\ub4e4\uc774 \uc77d\uc5b4\ubcf4\uba74 \ud765\ubbf8\ub86d\ub2e4\uace0 \uc0dd\uac01\ud560 \ucc45\uc785\ub2c8\ub2e4.`\\n\\n\uc5b4\ub514\uae4c\uc9c0\ub098 \uc81c \uc0dd\uac01\uc77c \ubfd0\uc774\ub2c8 \ucc38\uace0\ub9cc \ud574\uc8fc\uc2dc\uae38 \ubc14\ub78d\ub2c8\ub2e4."},{"id":"/UNIX-history-memoir","metadata":{"permalink":"/en/blog/UNIX-history-memoir","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2021-01-28-UNIX-history-memoir/UNIX-history-momoir.md","source":"@site/blog/2021-01-28-UNIX-history-memoir/UNIX-history-momoir.md","title":"\ud83d\udcd6 \uc720\ub2c9\uc2a4\uc758 \ud0c4\uc0dd","description":"\uc138\uc0c1\uc744 \ubc14\uafbc \uc6b4\uc601\uccb4\uc81c\ub97c \ub9cc\ub4e0 \ucc9c\uc7ac\ub4e4\uc758 \uc228\uc740 \uc774\uc57c\uae30","date":"2021-01-28T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \uc720\ub2c9\uc2a4\uc758 \ud0c4\uc0dd","date":"2021-01-28","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/UNIX-history-memoir","description":"\uc138\uc0c1\uc744 \ubc14\uafbc \uc6b4\uc601\uccb4\uc81c\ub97c \ub9cc\ub4e0 \ucc9c\uc7ac\ub4e4\uc758 \uc228\uc740 \uc774\uc57c\uae30","keywords":["UNIX","\ube0c\ub77c\uc774\uc5b8 \ucee4\ub2c8\ud578","\ubca8 \uc5f0\uad6c\uc18c"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \ub098\ub294 LINE \uac1c\ubc1c\uc790\uc785\ub2c8\ub2e4","permalink":"/en/blog/LINE-developer"},"nextItem":{"title":"\ud83d\udcd6 \uc131\uacf5\ud558\ub294 \ud504\ub85c\uadf8\ub798\ubc0d \uacf5\ubd80\ubc95","permalink":"/en/blog/successful-programming"}},"content":"## \u2139\ufe0f Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/UNIX-history-and-memoir.jpg)](http://www.kyobobook.co.kr/product/detailViewKor.laf?ejkGb=KOR&mallGb=KOR&barcode=9791162243282&orderClick=LEa&Kc=)\\n\\n- \uc81c\ubaa9: \uc720\ub2c9\uc2a4\uc758 \ud0c4\uc0dd\\n- \uc800\uc790: Brian W. Kernighan\\n- \uc5ed\uc790: \ud558\uc131\ucc3d\\n- \ucd9c\ud310\uc0ac: \ud55c\ube5b\ubbf8\ub514\uc5b4\\n- \ucd9c\uac04: 2020-08-03\\n\\n{/* truncate */}\\n\\n## \ud83c\udfac Intro\\n\\n\uc774 \ucc45\ub3c4 \uc9d1 \uadfc\ucc98 \ub3c4\uc11c\uad00\uc5d0\uc11c \ube4c\ub824 \uc77d\uc740 \ucc45\uc774\ub2e4. \uc774 \ucc45\uc744 \ucc98\uc74c \uc811\ud55c \uacf3\uc740 \ud55c\ube5b\ubbf8\ub514\uc5b4 \ud398\uc774\uc2a4\ubd81 \ud398\uc774\uc9c0\uc600\ub2e4. \uadf8\ub54c \ub9c8\uce68 \ub300\ud559\uc5d0\uc11c \uc6b4\uc601\uccb4\uc81c \uac15\uc758\ub97c \ub4e3\uace0 \uc788\uc5c8\uae30 \ub54c\ubb38\uc5d0 \uc77d\uc5b4\ubcf4\uace0 \uc2f6\ub2e4\ub294 \uc0dd\uac01\uc744 \ud588\uc73c\ub098, \ud559\uae30 \uc911\uc5d0\ub294 \ub108\ubb34 \ubc14\ube60 \uc77d\uc744 \uc218\uac00 \uc5c6\uc5c8\ub2e4. \uc774 \ucc45\uc5d0 \ub300\ud574 \uc544\ubb34 \uc0dd\uac01 \uc5c6\uc774 \uc9c0\ub0b4\ub2e4\uac00 \ub3c4\uc11c\uad00\uc5d0\uc11c \ubc1c\uacac\ud558\uac8c \ub410\uace0, \\"\uacf5\uacf5 \ub3c4\uc11c\uad00\uc5d0 \uc65c \uc788\uc9c0?\\"\ub77c\ub294 \uc758\ubb38\uc774 \uc788\uc5c8\uc73c\ub098 \uc5b4\uca0c\ub4e0 \ube4c\ub824 \uc77d\uac8c \ub410\ub2e4.\\n\\n## \ud83d\udcd6 Book Review\\n\\n### \uc5b4\ub5a4 \ucc45\uc778\uac00?\\n\\n\uc800\uc790\uc778 \ube0c\ub77c\uc774\uc5b8 \ucee4\ub2c8\ud578\uc758 \ud68c\uace0\ub85d \uac19\uc740 \ucc45\uc774\ub2e4. \ube0c\ub77c\uc774\uc5b8 \ucee4\ub2c8\ud578\uc740 C\uc5b8\uc5b4\ub97c \uc6d0\uc11c\ub85c \uacf5\ubd80\ud574\ubcf8 \uc0ac\ub78c\uc774\uba74 \uc544\ub294 [The C Programming Language](http://www.kyobobook.co.kr/product/detailViewEng.laf?mallGb=ENG&ejkGb=ENG&barcode=9780131103627)\uc758 \uc800\uc790 \uc911 \ud55c \uba85\uc774\ub2e4. \uc774 \ucc45\uc740 C\uc5b8\uc5b4 \ucc3d\uc2dc\uc790\uc778 \ub370\ub2c8\uc2a4 \ub9ac\uce58\uc640 \ud568\uaed8 \uc37c\ub2e4.\\n\\n\uc800\uc790\uac00 \ubca8 \uc5f0\uad6c\uc18c\uc5d0\uc11c \uacbd\ud5d8\ud588\ub358 \uc77c\ub4e4, \ub3d9\ub8cc \uac1c\ubc1c\uc790\ub4e4\uacfc \uad00\ub828\ub41c \uc5ec\ub2f4 \ub4f1 \uc7ac\ubc0c\ub294 \uc77c\ud654\ub4e4\uc744 \uc54c\ub824\uc900\ub2e4. C\uc5b8\uc5b4\ub97c \ub9cc\ub4e0 \ub370\ub2c8\uc2a4 \ub9ac\uce58, \ub370\ub2c8\uc2a4 \ub9ac\uce58\uc640 \ud568\uaed8 \uc720\ub2c9\uc2a4\ub97c \ub9cc\ub4e4\uace0 \ud604\uc7ac\ub294 \uad6c\uae00\uc5d0\uc11c Go\uc5b8\uc5b4\ub97c \uacf5\ub3d9 \uac1c\ubc1c\ud55c \ucf04 \ud1b0\ud504\uc2a8 \ub4f1 \uc5c4\uccad\ub09c \uac1c\ubc1c\uc790\ub4e4\uc758 \uc774\ub984\uc774 \uc5b8\uae09\ub418\uba70, \uadf8 \ub2f9\uc2dc \ubca8 \uc5f0\uad6c\uc18c\uac00 \uc5bc\ub9c8\ub098 \uc601\ub9ac\ud55c \uc0ac\ub78c\ub4e4\uc774 \ub9ce\uc558\uc73c\uba70 \uc704\ub300\ud588\ub294\uc9c0 \uc54c\ub824\uc900\ub2e4. \ub610\ud55c, \uc720\ub2c9\uc2a4\uac00 \uc5b4\ub5bb\uac8c \ub9cc\ub4e4\uc5b4\uc84c\uace0 \uc5b4\ub5bb\uac8c \ubc1c\uc804\ud588\ub294\uc9c0 \ub4f1 \uc720\ub2c9\uc2a4\uc758 \uc5ed\uc0ac\ub97c \ubcf4\uc5ec\uc900\ub2e4.\\n\\n\uc815\ub9d0 \'\uacbd\uc774\ub86d\ub2e4\'\ub77c\ub294 \ub9d0\uc774 \uc5b4\uc6b8\ub9ac\ub294 \ucc45\uc774\ub2e4. \uc911\uac04\uc911\uac04 \uc720\ub2c9\uc2a4 \uac1c\ubc1c \uacfc\uc815\uc744 \uc124\uba85\ud560 \ub54c \uc18c\uc124 \uac19\uc740 \ub290\ub08c\ub3c4 \uc900\ub2e4. \ub610\ud55c, \ud568\uaed8 \uc788\ub358 \ub3d9\ub8cc\ub4e4\uc758 \uba54\uc77c\uc774\ub77c\ub358\uac00 \uadf8\ub4e4\uc774 \uc9c1\uc811 \ud55c \uc778\ud130\ubdf0\uac00 \ub098\uc624\ub294\ub370 \uadf8\ub7f0 \ubd80\ubd84\uc744 \uc77d\ub294 \uac83\ub3c4 \uc7ac\ubc0c\ub294 \uad6c\uc131\uc774\ub77c\uace0 \uc0dd\uac01\ud588\ub2e4. \uc774\ub7ec\ud55c \uc5ed\uc0ac\ub97c \uc5b4\ub5bb\uac8c \ub2e4 \uae30\uc5b5\ud558\ub294\uc9c0 \ub180\ub790\uc5c8\ub2e4. \uae30\ub85d\uc744 \ud588\uc5c8\ub2e4\uace0 \ud574\ub3c4 \'\uc774 \ub9ce\uc740 \ub0b4\uc6a9\ub4e4\uc744 \uc5b4\ub5bb\uac8c \ub2e4 \uae30\ub85d\uc744 \ud588\uc5c8\uc9c0?\'\ub77c\uace0 \uc0dd\uac01\uc774 \ub4e4 \uc815\ub3c4\uc600\ub2e4.\\n\\n### \uae00\uc4f0\uae30\uc5d0 \uc9c4\uc2ec\\n\\n\ubca8 \uc5f0\uad6c\uc18c\uc5d0 \uacc4\uc168\ub358 \uac1c\ubc1c\uc790\ub4e4\uc740 \uae00\uc4f0\uae30\uc5d0 \ub300\ud55c \uc9c4\uc9c0\ud55c \ud0dc\ub3c4\ub97c \uac00\uc84c\uc73c\uba70, \uc11c\ub85c \ube44\ud310\ud574\uc8fc\uace0, \uacbd\uc601\uc9c4\uc758 \uc9c0\uc9c0\uae4c\uc9c0 \ubc1b\uc558\uc5c8\ub2e4\uace0 \ud55c\ub2e4. \uadf8\ub807\uae30\uc5d0 \uc138\uc0c1\uc5d0 \ubaa8\ub4e0 \uac1c\ubc1c\uc790\uc5d0\uac8c \uc601\ud5a5\uc744 \ub07c\uce58\ub294 \ucc45\ub4e4\ub3c4 \ucd9c\uac04\ud560 \uc218 \uc788\uc5c8\uc9c0 \uc54a\uc558\uc744\uae4c. \ub098\ub3c4 \uc774\ub7f0 \ub300\ub2e8\ud55c \uac1c\ubc1c\uc790\ub4e4\uc774 \uac1c\ubc1c\ud55c \uc5b8\uc5b4\ub97c \uacf5\ubd80\ud558\uace0, \ucd9c\uac04\ud55c \ucc45\ub4e4\uc744 \uc77d\uc73c\uba70 \uacf5\ubd80\ud55c \ud559\uc0dd\uc774\uae30\uc5d0 \uc774\ub7f0 \ubd80\ubd84\uc5d0\uc11c \ub9e4\uc6b0 \uac10\uba85 \uae4a\uc5c8\ub2e4.\\n\\n\ub09c \uac1c\ubc1c\uc790\ub97c \ubaa9\ud45c\ub85c \uacf5\ubd80\ud558\ub294 \ub300\ud559\uc0dd\uc774\uc9c0\ub9cc \uae00\uc4f0\uae30\ub97c \uc88b\uc544\ud558\ub2c8 \uc774\ub807\uac8c \ucc45\uc744 \ub9ac\ubdf0\ud558\uac70\ub098 \uacf5\ubd80\ud55c \ub0b4\uc6a9\ub4e4\uc744 \uc815\ub9ac\ud558\ub294 \ube14\ub85c\uadf8\ub97c \uc6b4\uc601\ud558\uace0 \uc788\ub2e4. \ub204\uad70\uac00\uc5d0\uac8c \ub3c4\uc6c0\uc774 \ub410\uc73c\uba74 \ud558\ub294 \ub9c8\uc74c\uc73c\ub85c \uc801\uace0 \uc788\ub2e4. \uc55e\uc73c\ub85c\ub3c4 \ub098\ub294 \uae00\uc4f0\uae30\ub97c \uacc4\uc18d \uc5f0\uc2b5\ud558\uace0, \ube14\ub85c\uadf8\ub97c \ud1b5\ud558\uc5ec \uc138\uc0c1\uc5d0 \uc9c0\uc2dd\uc744 \ub0a8\uae30\ub294 \uac1c\ubc1c\uc790\uac00 \ub418\uace0 \uc2f6\ub2e4.\\n\\n### \uc7ac\ubc0c\ub294 \uc77c\ud654\\n\\n\uc774 \ucc45\uc5d0\ub294 \uc7ac\ubc0c\ub294 \uc77c\ud654\ub4e4\uc774 \ub9ce\uc774 \ub098\uc628\ub2e4. \uc720\ub2c9\uc2a4\ub77c\ub294 \uc774\ub984\ub3c4 \uc0ac\uc2e4 [\uba40\ud2f1\uc2a4(Multics, Multiplexed Information and Computing Service)](https://ko.wikipedia.org/wiki/%EB%A9%80%ED%8B%B1%EC%8A%A4)\uc5d0\uc11c \ub530\uc628 \ub9d0\uc7a5\ub09c\uc774\uc5c8\uc73c\uba70, \uc6d0\ub798 \uc774\ub984\uc740 UNICS\uc600\ub294\ub370 AT&T \ubcc0\ud638\uc0ac\ub4e4\uc774 eunuchs(\ub0b4\uc2dc)\uc640 \ubc1c\uc74c\uc774 \uc720\uc0ac\ud558\ub2e4\uace0 \ud558\uc5ec UNICS\ub97c \uc88b\uc544\ud558\uc9c0 \uc54a\uc558\ub2e4\uace0 \ud55c\ub2e4.\\n\\nC\uc5b8\uc5b4\ub97c \uacf5\ubd80\ud560 \ub54c \uc790\uc8fc \uc4f0\ub294 malloc \ub77c\uc774\ube0c\ub7ec\ub9ac\ub97c \ub9cc\ub4e0 \ub354\uae00\ub7ec\uc2a4 \ub9e4\ud074\ub85c\uc774\uc5d0 \ub300\ud55c \uc77c\ud654\uc5d0 \uad00\ud55c \uc774\uc57c\uae30\ub3c4 \ub098\uc628\ub2e4. C\uc5b8\uc5b4\ub97c \uacf5\ubd80\ud574\ubcf8 \uc0ac\ub78c\uc774\ub77c\uba74 \ub354\uc6b1 \uc7ac\ubc0c\uac8c \uc77d\uc744 \uc218 \uc788\ub294 \ud3ec\uc778\ud2b8\ub2e4. \uc774\ubc16\uc5d0\ub3c4 UNIX\uc5d0\uc11c \ud30c\uc77c\uc744 \ub9cc\ub4dc\ub294 system call \uc774\ub984\uc774 creat\uc778 \uc774\uc720\ub3c4 \uc0ac\uc2e4 \ucf04 \ud1b0\ud504\uc2a8\uc758 \uc624\ud0c0\uc600\ub2e4\ub294 \uc7ac\ubc0c\ub294 \uc77c\ud654\ub3c4 \uc54c \uc218 \uc788\ub2e4.\\n\\n\uc774\ubc16\uc5d0\ub3c4 GitHub\uc5d0\uc11c MIT \ub77c\uc774\uc120\uc2a4\uc640 \uac19\uc774 \uc790\uc8fc \uc4f0\uc774\ub294 BSD \ub77c\uc774\uc120\uc2a4\ub3c4 \uc0ac\uc2e4 \uc720\ub2c9\uc2a4 \ub54c\ubb38\uc5d0 \ub098\uc624\uac8c \ub41c \ub77c\uc774\uc120\uc2a4\ub77c\ub294 \uac83\ub3c4 \uc54c\uac8c \ub410\uc73c\uba70 \uaf64 \uc720\uc6a9\ud55c \uc815\ubcf4\ub4e4\uc774 \ub9ce\uc558\ub2e4. \uc9c0\uae08 \uc0dd\uac01\ub098\ub294 \uac83\ub4e4\uc740 \uc774\uac8c \uc804\ubd80\uc774\ub2e4. \uc774 \ubc16\uc5d0\ub3c4 \uc720\uc6a9\ud558\uae30\ub3c4 \ud558\uace0, \uc7ac\ubc0c\ub294 \uc77c\ud654\ub4e4\uc774 \ub9ce\uc774 \ub098\uc628\ub2e4. \uac1c\ubc1c\uc790\ub97c \ubaa9\ud45c\ub85c \ud55c\ub2e4\uba74 \uc2ec\uc2ec\ud560 \ub54c \ud55c \ubc88 \uc77d\uc5b4\ubd10\ub3c4 \uc7ac\ubc0c\uc744 \ucc45\uc774\ub2e4.\\n\\n## \ud83d\udd16 \ub300\uc0c1 \ub3c5\uc790\\n\\n\uac1c\ubc1c\uc744 \uc774\uc81c \ub9c9 \uacf5\ubd80\ud558\ub294 \uc0ac\ub78c\ub4e4\uc5d0\uac8c\ub294 \ubcc4\ub85c \ucd94\ucc9c\ud558\uc9c0 \uc54a\ub294\ub2e4. \uc801\uc5b4\ub3c4 \uc6b4\uc601\uccb4\uc81c, \uc2dc\uc2a4\ud15c \ud504\ub85c\uadf8\ub798\ubc0d\uc744 \uacf5\ubd80\ud588\ub358 \uacbd\ud5d8\uc774 \uc788\uc73c\uc2e0 \uc0ac\ub78c\uc5d0\uac8c \ucd94\ucc9c\ud558\uace0 \uc2f6\ub2e4. \ub09c \uc6b4\uc601\uccb4\uc81c \uacfc\ubaa9\uc744 \ub108\ubb34 \ub300\ucda9 \ub4e4\uc5c8\ub294\uc9c0 \uc774 \ucc45\uc774 \uc774 \ucc45\uc740 \uaf64 \uc5b4\ub824\uc6e0\ub2e4. \uc6b4\uc601\uccb4\uc81c\ub97c \uacf5\ubd80\ud574\ubcf4\uc9c0 \uc54a\uc740 \ubd84\ub4e4\uc774 \ubcf4\uae30\uc5d0\ub294 \ub354 \uc5b4\ub824\uc6b8 \uc218\ub3c4 \uc788\ub2e4.\\n\\n\uc6b0\ub9ac \ud559\uad50\ub294 \uc2dc\uc2a4\ud15c \ud504\ub85c\uadf8\ub798\ubc0d\uc774\ub77c\ub294 \uacfc\ubaa9\uc744 \ubc30\uc6cc\uc11c \ucc45\uc744 \uc77d\uc744 \ub54c \uc911\uac04\uc911\uac04\uc5d0 \ub098\uc624\ub294 \ucf54\ub4dc\ub4e4\uc774\ub77c\ub358\uac00 \uc6a9\uc5b4\ub4e4\uc744 \uc5b4\ub290 \uc815\ub3c4\ub294 \uc27d\uac8c \uc774\ud574\ud588\ub294\ub370 \ub2e4\ub978 \ub3c5\uc790\ub4e4\uc740 \uc5b4\ub5a4\uc9c0 \uc798 \ubaa8\ub974\uaca0\ub2e4. \ubaa8\ub974\ub294 \ucf54\ub4dc\ub294 \uad6c\uae00\ub9c1\uc744 \uc5f4\uc2ec\ud788 \ud574\ubcf4\uc790. \uc774\uc81c \ucc9c\ucc9c\ud788 \uc990\uae30\uba74\uc11c \ucc45\uc744 \uc77d\uc5b4\ubcf4\uc790!"},{"id":"/successful-programming","metadata":{"permalink":"/en/blog/successful-programming","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2021-01-07-successful-programming/successful-programming.md","source":"@site/blog/2021-01-07-successful-programming/successful-programming.md","title":"\ud83d\udcd6 \uc131\uacf5\ud558\ub294 \ud504\ub85c\uadf8\ub798\ubc0d \uacf5\ubd80\ubc95","description":"\uc0ac\ub78c\uacfc \ud504\ub85c\uadf8\ub798\uba38 \\\\#7 \ud504\ub85c\uadf8\ub798\uba38\uc758 \uae38\uc744 \uc0dd\uac01\ud55c\ub2e4","date":"2021-01-07T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 \uc131\uacf5\ud558\ub294 \ud504\ub85c\uadf8\ub798\ubc0d \uacf5\ubd80\ubc95","date":"2021-01-07T00:00:00.000Z","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/successful-programming","description":"\uc0ac\ub78c\uacfc \ud504\ub85c\uadf8\ub798\uba38 \\\\#7 \ud504\ub85c\uadf8\ub798\uba38\uc758 \uae38\uc744 \uc0dd\uac01\ud55c\ub2e4","keywords":["\uacf5\ubd80\ubc95","\ud504\ub85c\uadf8\ub798\ubc0d","\uc131\uc7a5"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \uc720\ub2c9\uc2a4\uc758 \ud0c4\uc0dd","permalink":"/en/blog/UNIX-history-memoir"},"nextItem":{"title":"\u2328\ufe0f \ub9c8\ud06c\ub2e4\uc6b4(Markdown) \uc815\ub9ac","permalink":"/en/blog/markdown"}},"content":"## \u2139\ufe0f Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/How-to-sutdy-programming.jpg)](https://product.kyobobook.co.kr/detail/S000001624697)\\n\\n- \uc81c\ubaa9: \uc131\uacf5\ud558\ub294 \ud504\ub85c\uadf8\ub798\ubc0d \uacf5\ubd80\ubc95\\n- \uc800\uc790: \ubc15\uc7ac\uc131, \uace0\uc885\ubc94, \ub0a8\uc7ac\ucc3d, \ubc15\uc9c0\ud604\\n- \ucd9c\ud310\uc0ac: \ub85c\ub4dc\ubd81\\n- \ucd9c\uac04: 2018-09-10\\n\\n{/* truncate */}\\n\\n## \ud83c\udfac Intro\\n\\n\uc6b0\uc5f0\ud788 \ub3c4\uc11c\uad00\uc744 \uac14\ub2e4\uac00 \uc774 \ucc45\uc744 \ubc1c\uacac\ud558\uac8c \ub418\uace0 \ubaa9\ucc28\uc5d0\uc11c \ub108\ubb34 \uad00\uc2ec\uc774 \uac00\uc11c \uc77d\uac8c \ub410\ub2e4. \ub2e4\uc2dc \ubcf4\ub2c8 \ubc30\ub2ec\uc758 \ubbfc\uc871\uc744 \ub9cc\ub4e0 \uc6b0\uc544\ud55c\ud615\uc81c\ub4e4\uc758 \uad50\uc721 \uacfc\uc815 \uc911 \ud558\ub098\uc778 \uc6b0\uc544\ud55c\ud14c\ud06c\ucf54\uc2a4\ub97c \ub9e1\uace0 \uacc4\uc2e0 \ubc15\uc7ac\uc131\ub2d8\uc774 \uc800\uc790\ub85c \ucc38\uc5ec\ud55c \ucc45\uc774\ub77c \uc77d\uc5b4\ubcf4\uace0 \uc2f6\uc5c8\ub2e4. \\n\\n\uc0c8\ub85c\uc6b4 \uac83\uc744 \ub9cc\ub4e4\uc5b4\ub0b4\uace0, \uc5b8\uc5b4\ub97c \ubc30\uc6b0\ub294 \uac83\uc774 \uc7ac\ubc0c\uc5c8\uc9c0\ub9cc \uac11\uc790\uae30 \ub108\ubb34 \ubc29\ub300\ud55c \uc9c0\uc2dd\uc774 \ubab0\ub824\uc640 \ud504\ub85c\uadf8\ub798\ubc0d\uc5d0 \ud765\ubbf8\ub97c \uc783\uc5c8\ub358 \uc2dc\uae30\uac00 \uc788\uc5c8\ub2e4. \uc9c0\uae08\uc740 \uadf8\ub7f0 \uc2dc\uae30\uac00 \uc9c0\ub098\uace0 \uc7ac\ubc0c\uc5b4\uc11c \uafb8\uc900\ud788 \uacf5\ubd80\ud558\uace0 \uc788\uc9c0\ub9cc **\'\uc5b4\ub5bb\uac8c \ud504\ub85c\uadf8\ub798\ubc0d\uc744 \uacf5\ubd80\ud574\uc57c \ud558\ub294\uac00\'**, **\'\uc5b4\ub5bb\uac8c \ucef4\ud4e8\ud130 \uacf5\ud559\uc744 \uacf5\ubd80\ud574\uc57c \ud558\ub294\uac00\'** \ub4f1\uc758 \uc0dd\uac01\uc740 \uc544\uc9c1\ub3c4 \uc5ec\uc804\ud788 \ud558\uace0 \uc788\uc73c\uba70 \uae38\uc744 \ubabb \uc7a1\uace0 \uc788\uc5c8\ub2e4. \uc774 \ucc45\uc744 \uc77d\uc5b4\ubcf4\uba74 \uadf8\ub798\ub3c4 \uc5b4\ub5bb\uac8c \uacf5\ubd80\ud574\uc57c \ud558\ub294\uac00\uc5d0 \ub300\ud55c \uc815\ub2f5\uc744 \ucc3e\uc744 \uc218 \uc788\uc9c0 \uc54a\uc744\uae4c \uc2f6\uc5b4 \uc77d\uac8c \ub410\ub2e4.\\n\\n\\n## \ud83d\udcd6 Book Review\\n\\n### \ud604\uc9c1 \ud504\ub85c\uadf8\ub798\uba38\ub4e4\uc758 \uacbd\ud5d8\\n\\n\uc774 \ucc45\uc5d0\ub294 \ud604\uc9c1 \ud504\ub85c\uadf8\ub798\uba38\ub4e4\uc758 \ub2e4\uc591\ud55c \uacbd\ud5d8\uc774 \ub2f4\uaca8 \uc788\ub2e4. \ud504\ub85c\uadf8\ub798\ubc0d\uc744 \uc5b4\ub5bb\uac8c \uacf5\ubd80\ud574\uc57c \ud558\ub294\uc9c0 \uace0\ubbfc\ud558\uace0 \uc788\ub294 \ubd84\ub4e4\uc774 \uc77d\uc73c\uba74 \ub3c4\uc6c0\uc774 \ub420 \uac83\uc774\ub2e4. \uadf8\ub4e4\uc758 \uacbd\ud5d8\uc744 \uac04\uc811\uc801\uc73c\ub85c\ub098\ub9c8 \uc774\ub807\uac8c \uccb4\ud5d8\ud560 \uc218 \uc788\uc5b4\uc11c \uc815\ub9d0 \uc88b\uc558\ub358 \ucc45\uc774\ub2e4. \\n\\n\uc774 \ucc45\uc758 \uc800\uc790\ub294 4\ubd84\uc73c\ub85c \uae30\ub85d\ub418\uc5b4 \uc788\uc9c0\ub9cc, \uc0ac\uc2e4 11\ubd84\uc758 \uc778\ud130\ubdf0\uac00 \uc801\ud600 \uc788\uc73c\uba70, \uac01\uc790 \ud504\ub85c\uadf8\ub798\ubc0d\uc744 \uc5b4\ub5bb\uac8c \uacf5\ubd80\ud588\ub294\uc9c0, \uc54c\uace0\ub9ac\uc998\uacfc \uc218\ud559\uc758 \uc911\uc694\uc131, \uacf5\ubd80\ud560 \ub54c\uc758 \ub3d9\uae30, \uacf5\ubd80\ud558\uba74\uc11c \ud63c\ub780\uc2a4\ub7ec\uc6e0\ub358 \uc2dc\uae30 \ub4f1\uc758 \uac01\uc790\uc758 \uacbd\ud5d8\ub4e4\uc744 \uc54c\ub824\uc900\ub2e4. \ub098\ub294 \uc774 \ubd80\ubd84\uc774 \ub108\ubb34 \ub3c4\uc6c0\uc774 \ub410\ub2e4. \\n\\n### \uc0dd\uac01\uc744 \ub9ce\uc774 \ud558\uac8c \ub418\ub294 \ucc45\\n\\n\uc774 \ucc45\uc740 \ud504\ub85c\uadf8\ub798\ubc0d \uacf5\ubd80\ubc95\uc5d0 \ub300\ud55c \uc804\ubc18\uc801\uc778 \ub0b4\uc6a9\uc744 \ub2e4\ub8e8\uace0 \uc788\uc73c\uba70 \\"\ud504\ub85c\uadf8\ub798\ubc0d \uacf5\ubd80\ub294 \uc65c \uc2e4\ud328\ud558\ub294\uac00\\"\uc5d0 \ub300\ud574 \ub2e4\uc2dc \uc0dd\uac01\ud558\ub3c4\ub85d \ub9cc\ub4e4\uc5b4\uc900\ub2e4. \uc88c\uc808\ud558\ub294 \uc2dc\uae30\uac00 \uc5c6\uc5c8\uc73c\uba74 \uc88b\uaca0\uc9c0\ub9cc, \uc5b4\ub5a4 \ubd84\uc57c\ub4e0\uc9c0 \uacf5\ubd80\ub97c \ud558\ub2e4 \ubcf4\uba74 \uc5b8\uc820\uac00\ub294 \uadf8\ub7f0 \uc21c\uac04\uc774 \ud55c \ubc88\ucbe4\uc740 \uc628\ub2e4. \uadf8\ub7f4 \ub54c \uc77d\uc73c\uba74 \uc88b\uc740 \ucc45\uc774\ub2e4. \\n\\n## \ub2e4\uc591\ud55c Story\\n\\n\ubaa9\ucc28\uc5d0\ub294 **n\uc7a5**\uc774\ub77c\uace0 \ud45c\ud604\ub418\uc5b4 \uc788\uc9c0\ub9cc, \ucc45 \ud6c4\uba74\uc5d0 **Story #n**\uc73c\ub85c \ud45c\uae30\ub418\uc5b4 \uc788\ub2e4.\\n\\nStory #1\uc740 \uc6b0\uc544\ud55c \ud615\uc81c\ub4e4\uc5d0\uc11c \uc9c4\ud589\ud558\ub294 \uac1c\ubc1c\uc790 \uad50\uc721 \ud504\ub85c\uadf8\ub7a8\uc778 \uc6b0\uc544\ud55c\ud14c\ud06c\ucf54\uc2a4, \uc6b0\uc544\ud55c\ud14c\ud06c\ucea0\ud504\uc758 \uad50\uc721\uc790\ub85c \uc720\uba85\ud558\uc2e0 \uc790\ubc14\uc9c0\uae30 \ubc15\uc7ac\uc131 \ub2d8\uc774 \uc4f0\uc2e0 \ubd80\ubd84\uc774\ub2e4. \uc774 \ud30c\ud2b8\uc5d0\uc11c\ub294 \uc55e\uc73c\ub85c \ud504\ub85c\uadf8\ub798\ubc0d\uc744 \uc5b4\ub5bb\uac8c \uacf5\ubd80\ud560\uc9c0\uc5d0 \ub300\ud55c \uace0\ubbfc\uc744 \ud560 \uc218 \uc788\uc5c8\uace0, \uc5b4\ub290\uc815\ub3c4 \uacb0\ub860\uc744 \ub0bc \uc218 \uc788\uc5c8\ub358 \ubd80\ubd84\uc774\ub2e4. \ub3c4\uc6c0\uc774 \ub9ce\uc774 \ub410\ub2e4.\\n\\nStory #2 \ubd80\ubd84\uc5d0\uc11c\ub294 \uc560\uc790\uc77c\uc5d0 \ub300\ud574 \uc124\uba85\ud55c\ub2e4. \ub09c \uc560\uc790\uc77c\uc5d0 \ub300\ud574 \ub4e4\uc5b4\ubcf4\uae30\ub9cc \ud588\uc9c0 \uc0ac\uc2e4 \uc798 \ubaa8\ub978\ub2e4. \uadf8\ub7ec\ub098 \uc608\uc2dc\ub97c \ud1b5\ud574 \uc774\uc57c\uae30\ub97c \uc798 \ud480\uc5b4\uc8fc\uc154\uc11c \uc7ac\ubc0c\uac8c \uc77d\uc740 \ubd80\ubd84\uc774\ub2e4. \uc560\uc790\uc77c\uc5d0 \ub300\ud574 \ub354 \uc54c\uc544\ubcf4\uace0 \uc2f6\uac8c \ub9cc\ub4e4\uc5b4\uc900\ub2e4.\\n\\nStory #3\uc740 \ub098\ucc98\ub7fc \uae00\uc744 \uc4f0\uc2dc\ub294 \ucde8\ubbf8\uac00 \uc788\uac70\ub098 \ucc45\uc744 \uc77d\ub294 \uac83\uc744 \uc88b\uc544\ud558\uc2dc\ub294 \uc0ac\ub78c\uc774\ub77c\uba74 \uc7ac\ubc0c\uc744 \uac83\uc774\ub77c \uc0dd\uac01\ud55c\ub2e4. \ucf54\ub529\uc744 \uc18c\ud1b5\uc73c\ub85c \ube44\uc720\ud574\uc11c \uc124\uba85\ud558\ub294 \ubd80\ubd84\uc774\ub2e4.\\n\\nStory #4\ub294 \ube44\uc804\uacf5\uc790\ub4e4\uc774 \uc77d\uc73c\uba74 \uc7ac\ubc0c\uace0 \ub3c4\uc6c0\uc774 \ub420 \uac83\uc774\ub77c \uc0dd\uac01\ub418\ub294 \ubd80\ubd84\uc774\ub2e4. \ub300\ud559\uc5d0 \uc785\ud559\ud558\uba74 \uc694\uc998\uc740 1\ud559\ub144 \ub54c \ub300\ubd80\ubd84 \ud544\uc218 \uad50\uc591\uc73c\ub85c \ud504\ub85c\uadf8\ub798\ubc0d\uc744 \uc811\ud560 \uac83\uc774\ub2e4. \uc8fc\ub85c **\ucef4\ud4e8\ud305 \uc0ac\uace0**\ub97c \uc124\uba85\ud558\uace0 \ud30c\uc774\uc36c\uc73c\ub85c \uc2e4\uc2b5\ud574\ubcf4\ub294 \uc218\uc5c5\uc778 \uac83\uc73c\ub85c \uc54c\uace0 \uc788\ub2e4. \uc774 \ud30c\ud2b8\ub294 \uadf8 \uacfc\ubaa9\uc758 \uc2ec\ud654 \ubc84\uc804\uc774\ub77c\uace0 \uc0dd\uac01\ud558\uba74 \ub420 \uac83 \uac19\ub2e4. \ub300\ub7b5 \uc694\uc57d\ud558\uc790\uba74 \uad50\uc591\uc73c\ub85c \ubc30\uc6b4 \ud504\ub85c\uadf8\ub798\ubc0d\uc744 \uc790\uc2e0\uc758 \uc804\uacf5\uacfc \uc2dc\ub108\uc9c0\ub97c \ub192\uc774\ub294 \ubc29\ubc95\uc5d0 \uad00\ud574 \uc54c\ub824\uc8fc\uae30\uc5d0 \ube44\uc804\uacf5\uc790\uc5ec\ub3c4 \uc774 \ucc45\uc744 \uc77d\uc73c\uba74 \uaf64 \ub3c4\uc6c0\uc774 \ub420 \uac83\uc774\ub77c \uc0dd\uac01\ud55c\ub2e4.\\n\\n## \ud83d\udd16 \ub300\uc0c1 \ub3c5\uc790\\n\\n\\"\ud504\ub85c\uadf8\ub798\ubc0d\uc744 \ucc98\uc74c \uacf5\ubd80\ud558\ub294 \uc0ac\ub78c\uc5d0\uac8c \uc801\uc808\ud55c \ucc45\uc778\uac00?\\"\ub77c\ub294 \uc9c8\ubb38\uc5d0\ub294 \\"\uc544\ub2c8\uc694\\"\ub77c\uace0 \ub300\ub2f5\ud560 \uac83\uc774\ub2e4. \ubb3c\ub860 \uc77d\uc5b4\ubcf4\ub294 \uac83\ub3c4 \ub3c4\uc804\uc774\uae30\uc5d0 \uc88b\ub2e4\uace0 \uc0dd\uac01\ud558\uc9c0\ub9cc \ubcc4\ub85c \ucd94\ucc9c\ud558\uace0 \uc2f6\uc9c0\ub294 \uc54a\ub2e4. \uc5b4\ub290\uc815\ub3c4 \uc790\uc2e0\uc758 \uc131\uc7a5\uacfc \uace0\ubbfc\uc5d0 \ub300\ud574 \uace0\ubbfc\uc744 \ud574\ubcf8 \uc0ac\ub78c\uc774 \uc77d\uc5b4\ubd24\uc73c\uba74 \ud55c\ub2e4.\\n\\n\uc704\uc5d0\uc11c\ub3c4 \uc5b8\uae09\ud588\ub4ef\uc774 \ube44\uc804\uacf5\uc790\uc5ec\ub3c4 \uc804\uacf5\uc790\uc5ec\ub3c4 \uac1c\ubc1c\uc790\ub97c \ubaa9\ud45c\ub85c \ud55c\ub2e4\uba74 \ud55c\ubc88 \uc77d\uc5b4\ubd10\ub3c4 \uc2dc\uac04\uc774 \uc544\uae5d\uc9c0 \uc54a\uc740 \ub0b4\uc6a9\ub4e4\uc774\ub2e4. \ud559\uc0dd\ub4e4\uc740 \ud68c\uc0ac\uc5d0 \ub4e4\uc5b4\uac00\uc9c0 \uc54a\ub294 \uc774\uc0c1 \uc120\ubc30 \uac1c\ubc1c\uc790\ub4e4\uc758 \uc774\uc57c\uae30\ub97c \uc811\ud558\uae30 \uc5b4\ub835\uae30\uc5d0 \ub354\uc6b1 \ucd94\ucc9c\ud55c\ub2e4."},{"id":"/markdown","metadata":{"permalink":"/en/blog/markdown","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2021-01-01-markdown/markdown.md","source":"@site/blog/2021-01-01-markdown/markdown.md","title":"\u2328\ufe0f \ub9c8\ud06c\ub2e4\uc6b4(Markdown) \uc815\ub9ac","description":"\ubb38\uc11c\ud654\uc5d0 \uc775\uc219\ud574\uc9c0\uae30 \uc704\ud55c Markdown \uc815\ub9ac","date":"2021-01-01T00:00:00.000Z","tags":[{"inline":true,"label":"Blog","permalink":"/en/blog/tags/blog"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\u2328\ufe0f \ub9c8\ud06c\ub2e4\uc6b4(Markdown) \uc815\ub9ac","date":"2021-01-01","authors":"teddygood","tags":["Blog"],"draft":false,"slug":"/markdown","description":"\ubb38\uc11c\ud654\uc5d0 \uc775\uc219\ud574\uc9c0\uae30 \uc704\ud55c Markdown \uc815\ub9ac","keywords":["Markdown","Blog"]},"unlisted":false,"prevItem":{"title":"\ud83d\udcd6 \uc131\uacf5\ud558\ub294 \ud504\ub85c\uadf8\ub798\ubc0d \uacf5\ubd80\ubc95","permalink":"/en/blog/successful-programming"},"nextItem":{"title":"\ud83d\udcd6 Do it! HTML5+CSS3 \uc6f9 \ud45c\uc900\uc758 \uc815\uc11d","permalink":"/en/blog/Do-it-html5-css3"}},"content":"\uc624\ub79c\ub9cc\uc5d0 \ube14\ub85c\uadf8\ub97c \ub2e4\uc2dc \uc2dc\uc791\ud558\ub2e4 \ubcf4\ub2c8 \ub9c8\ud06c\ub2e4\uc6b4\uc5d0 \uc775\uc219\ud558\uc9c0 \uc54a\uc740 \ubaa8\uc2b5\uc774 \ubcf4\uc5ec \ubb38\uc11c\ud654 \ud558\uba74\uc11c \uc5f0\uc2b5\ud55c\ub2e4.\\n\\n\x3c!--truncate--\x3e\\n\\n## Markdown\uc774\ub780?\\n\\n\ub9c8\ud06c\ub2e4\uc6b4(markdown)\uc740 \uc77c\ubc18 \ud14d\uc2a4\ud2b8 \ubb38\uc11c\uc758 \uc591\uc2dd\uc744 \ud3b8\uc9d1\ud558\ub294 \ubb38\ubc95\uc774\ub2e4. GitHub\uc758 README \ud30c\uc77c\uc774\ub098 \uc628\ub77c\uc778 \ubb38\uc11c, \ud639\uc740 \uc77c\ubc18 \ud14d\uc2a4\ud2b8 \ud3b8\uc9d1\uae30\ub85c \ubb38\uc11c\ub4e4\uc744 \ud3b8\uc9d1\ud560 \ub54c \uc4f0\uc778\ub2e4. \ub9c8\ud06c\ub2e4\uc6b4\uc744 \uc774\uc6a9\ud574 \uc791\uc131\ub41c \ubb38\uc11c\ub294 \uc27d\uac8c HTML \ub4f1 \ub2e4\ub978 \ubb38\uc11c\ud615\ud0dc\ub85c \ubcc0\ud658\uc774 \uac00\ub2a5\ud558\ub2e4. \uc989, \uac1c\ubc1c\uc790\ub4e4\uc774 \uc27d\uac8c \ubb38\uc11c\ud654\ub97c \ud560 \uc218 \uc788\ub3c4\ub85d \ub3c4\uc640\uc8fc\ub294 \uc5b8\uc5b4\uc774\ub2e4. \\n\\n## \ubb38\ubc95\uc744 \uadf8\ub300\ub85c \ubcf4\uc5ec\uc8fc\uace0 \uc2f6\uc744 \ub54c\\n\\n\ub9c8\ud06c\ub2e4\uc6b4 \ubb38\ubc95 \uc55e\uc5d0 `\\\\`\ub97c \ubd99\uc5ec\uc8fc\uba74 \ubb38\ubc95 \uadf8\ub300\ub85c\ub97c \ubcf4\uc5ec\uc900\ub2e4.\\n\\n```\\n\\\\### \uc548\ub155\\n```\\n\\n\\\\### \uc548\ub155\\n\\n## \ucf54\ub4dc \ube14\ub85d\\n\\n\\\\```\uc5b8\uc5b4 \uc774\ub984  \\n\ucf54\ub4dc  \\n\\\\```\\n\\n```c\\n#include <stdio.h>\\n\\nint main(void)\\n{\\n    printf(\\"Hello World!\\");\\n}\\n```\\n\\n## \ud14d\uc2a4\ud2b8\\n\\n```\\n*\uc774\ud0e4\ub9ad\uccb4(\uae30\uc6b8\uac8c)*\\n_\uc774\ud0e4\ub9ad\uccb4(\uae30\uc6b8\uac8c)_\\n**\uad75\uac8c**\\n__\uad75\uac8c__\\n~~\ucde8\uc18c\uc120~~\\n```\\n\\n*\uc774\ud0e4\ub9ad\uccb4*\\n_\uc774\ud0e4\ub9ad\uccb4_\\n**\uad75\uac8c**\\n__\uad75\uac8c__\\n~~\ucde8\uc18c\uc120~~\\n\\n\ubc11\uc904\uc774\ub098 \uae00\uc528 \uc0c9\uc740 HTML, CSS\ub97c \uc0ac\uc6a9\ud558\uc790.\\n\\n## \ud5e4\ub354\\n\\n`# ~ ######`\ub85c h1 ~ h6\uae4c\uc9c0\uc758 \uc81c\ubaa9 \ud06c\uae30\ub97c \ud45c\uae30\ud560 \uc218 \uc788\ub2e4. \\n\\n```\\n# This is a h1\\n## This is a h2\\n### This is a h3\\n#### This is a h4\\n##### This is a h5\\n###### This is a h6\\n```\\n\\n# This is a h1\\n## This is a h2  \\n### This is a h3\\n#### This is a h4\\n##### This is a h5\\n###### This is a h6  \\n\\n######## This is a h7 -> \uc9c0\uc6d0\ud558\uc9c0 \uc54a\ub294\ub2e4.  \\n\\nh2\ub97c \ubcf4\uba74 \uc544\ub798\uc5d0 \uad6c\ubd84\uc120\uc774 \uadf8\uc5b4\uc9c0\ub294 \ubaa8\uc2b5\uc744 \ud655\uc778\ud560 \uc218 \uc788\ub294\ub370 minimal-mistakes \ud14c\ub9c8\uc758 scss\uc5d0\uc11c \uc815\uc758\ub418\uace0 \uc788\uae30 \ub54c\ubb38\uc774\ub2e4. \uc774 \ubd80\ubd84\uc740 \uc774\ub300\ub85c \ub194\ub458\uc9c0 \uc218\uc815\ud560\uc9c0 \uace0\ubbfc \uc911\uc774\ub2e4.\\n\\n## \ub9ac\uc2a4\ud2b8\\n\\n### \uc21c\uc11c \uc788\ub294 \ub9ac\uc2a4\ud2b8\\n\\n1. 1\ubc88\\n2. 2\ubc88\\n3. 3\ubc88\\n\\n```\\n1. 1\ubc88\\n1. 2\ubc88\\n1. 3\ubc88\\n```\\n`1.`\ucc98\ub7fc \ud3c9\uc18c \uc4f0\ub358\ub300\ub85c \uc4f0\uba74 \ub41c\ub2e4. \uadf8\ub807\uc9c0\ub9cc \uc704\uc758 \ucf54\ub4dc \ube14\ub85d\uc758 \uc608\uc2dc\ucc98\ub7fc \uac19\uc740 \uc22b\uc790\ub97c \uc5f0\uc18d\ud574\uc11c \uc4f0\ub354\ub77c\ub3c4 \ub0b4\ub9bc\ucc28\uc21c\uc73c\ub85c \ud45c\uae30\ub41c\ub2e4.\\n\\n### \uc21c\uc11c \uc5c6\ub294 \ubaa9\ub85d(\uae00\uba38\ub9ac \uae30\ud638: `*`, `+`, `-`)\\n\\n```\\n- \ub098\ub294\\n  + \ud68c\ub97c\\n  * \uce58\ud0a8\uc744\\n    + \uba39\uace0\uc2f6\ub2e4.\\n```\\n\\n- \ub098\ub294\\n  + \ud68c\ub97c\\n  * \uce58\ud0a8\uc744\\n    + \uba39\uace0\uc2f6\ub2e4.\\n\\n\uae00\uba38\ub9ac \uae30\ud638\ub97c \ud55c \ubb38\uc790\ub85c \ud574\ub3c4 \ub418\uba70 \uc704\uc758 \uc608\uc2dc\ucc98\ub7fc \ud63c\ud569\ud574\uc11c \uc0ac\uc6a9\ud558\ub294 \uac83\ub3c4 \uac00\ub2a5\ud558\ub2e4.\\n\\n## \uac1c\ud589 \ub610\ub294 \uc904\ubc14\uafc8\\n\\nHTML\uc5d0\uc11c\ub294 `<br>`\ub97c \uc0ac\uc6a9\ud558\uc9c0\ub9cc Markdown\uc5d0\uc11c\ub294 Space bar\ub97c 2\ubc88 \ub204\ub978 \ud6c4 Enter\ub97c \ub204\ub974\uba74 \uac1c\ud589\uc774 \ub41c\ub2e4. \\n\\n```\\n\ubc30\uace0\ud504\ub2e4  \\n\uba39\uace0\uc2f6\ub2e4\\n```\\n\\n\ubc30\uace0\ud504\ub2e4  \\n\uba39\uace0\uc2f6\ub2e4  \\n\\n## \uc218\ud3c9\uc120\\n\\nHTML\uc5d0\uc11c\uc758 `<hr>`\uc774\ub2e4. \uae00\uc744 \uc4f0\ub2e4\uac00 \uad6c\ubd84\ud558\ub294 \uc120\uc73c\ub85c \ub9ce\uc774 \uc4f0\uc778\ub2e4. \uc989, \ud398\uc774\uc9c0 \ub098\ub204\uae30 \uc6a9\ub3c4\uc774\ub2e4. `---`, `***`\ub85c \uc4f0\uc774\uc9c0\ub9cc \uc544\ub798\uc640 \uac19\uc774 \uc870\uae08 \uc774\uc0c1\ud55c \ubb38\ubc95\ub3c4 \ud5c8\uc6a9\ud574\uc8fc\ub294 \uac83 \uac19\ub2e4.\\n\\n```\\n* * * \\n***\\n******\\n- - -\\n---\\n___\\n```\\n\\n---\\n***\\n\\n## \uc778\uc6a9\ubb38\\n\\n`>`\ub97c \uc0ac\uc6a9\ud558\uc5ec \ud45c\uae30\ud560 \uc218 \uc788\ub2e4. \uc608\uc2dc\uc640 \uac19\uc774 \uc911\ucca9\ub41c \uc778\uc6a9\ubb38\uc5d0\uc11c \uac1c\ud589\uc744 \ud558\uace0 \uc2f6\ub2e4\uba74 \uc704\uc5d0\uc11c \uc0ac\uc6a9\ud55c \uac83\ucc98\ub7fc \uac1c\ud589\uc744 \uc0ac\uc6a9\ud558\uba74 \ub41c\ub2e4. \\n\\n```\\n> \uc778\uc6a9\ubb38\\n>> \uc778\uc6a9\ubb38 \uc548\uc758 \uc778\uc6a9\ubb38   \\n2\ubc88\uc9f8 \uc778\uc6a9\ubb38\uc73c\ub85c \ub4e4\uc5b4\uac08\ub798\\n>>> \uc778\uc6a9\ubb38 \uc548\uc758 \uc778\uc6a9\ubb38 \uc548\uc758 \uc778\uc6a9\ubb38\\n```\\n\\n> \uc778\uc6a9\ubb38\\n>> \uc778\uc6a9\ubb38 \uc548\uc758 \uc778\uc6a9\ubb38  \\n2\ubc88\uc9f8 \uc778\uc6a9\ubb38\uc73c\ub85c \ub4e4\uc5b4\uac08\ub798\\n>>> \uc778\uc6a9\ubb38 \uc548\uc758 \uc778\uc6a9\ubb38 \uc548\uc758 \uc778\uc6a9\ubb38\\n\\n## \ub9c1\ud06c\\n\\n\ubb38\uc11c\uc5d0 \ub9c1\ud06c\ub97c \ucd94\uac00\ud558\ub294 \ubc29\ubc95\uc774\ub2e4. \uc774 \ubc29\ubc95\ub4e4 \uc774\uc678\uc5d0\ub3c4 \ub2e4\ub978 \ubc29\ubc95\ub4e4\uc774 \uc874\uc7ac\ud560 \uc218 \uc788\ub2e4. \\n\\n```\\nURL\uc744 \ubcf4\uc5ec\uc8fc\ub294 \ub9c1\ud06c: <https://www.google.com/>  \\n\\n\uc124\uba85\uc548\uc5d0 URL\uc774 \ud3ec\ud568\ub418\uc5b4 \uc788\ub294 \ub9c1\ud06c: [Google](https://www.google.com/)  \\n\\n\ub3d9\uc77c \ud30c\uc77c \ub0b4\uc5d0\uc11c \ud5e4\ub354 \uc774\ub3d9 \ub9c1\ud06c: [\uc778\uc6a9\ubb38](#\uc778\uc6a9\ubb38)  \\n```\\n\\n\\\\<https://www.google.com/\\\\>  \\n[Google](https://www.google.com/)  \\n[\uc778\uc6a9\ubb38](#\uc778\uc6a9\ubb38)  \\n\\n\ud5e4\ub354\ub85c \uc774\ub3d9\ud558\ub294 \ub9c1\ud06c \ub9cc\ub4dc\ub294 \ubc29\ubc95\\n\\n1. \ud5e4\ub354\ub97c \uc81c\uc678\ud558\uace0 \uc81c\ubaa9 \ubb38\uc790\uc5f4\uc744 \ubcf5\uc0ac\ud55c\ub2e4.\\n   \\n2. \ud2b9\uc218 \ubb38\uc790\ub97c \uc81c\uac70\ud55c\ub2e4.\\n\\n3. \uacf5\ubc31\uc744 `-`\ub85c \ubcc0\uacbd\ud55c\ub2e4.\\n\\n4. \ub300\ubb38\uc790\ub294 \uc18c\ubb38\uc790\ub85c \ubcc0\uacbd\ud55c\ub2e4.\\n\\n5. \ubb38\uc790\uc5f4\uc758 \uac00\uc7a5 \uc55e\uc5d0 #\uc744 \ubd99\uc5ec\uc900\ub2e4.\\n\\ne.g. \\"## \ubb38\ubc95\uc774 \uae30\uc5b5\ub098\uc9c0 \uc54a\uac70\ub098 \uc751\uc6a9\ud558\uace0 \uc2f6\uc744 \ub54c\\" -> \\"#\ubb38\ubc95\uc774-\uae30\uc5b5\ub098\uc9c0-\uc54a\uac70\ub098-\uc751\uc6a9\ud558\uace0-\uc2f6\uc744-\ub54c\\" \\n\\n## \uc774\ubbf8\uc9c0\\n\\n```\\n![\ud504\ub85c\ud544](profile.jpg)\\n\\n<img src=\\"profile.jpg\\" width=\\"300\\" height=\\"200\\">\\n\\n[![\ud504\ub85c\ud544](profile.jpg)](https://teddygood.github.io/)\\n```\\n\\n![\uc5f0\ud544](profile.jpg)\\n\\n[![\uc5f0\ud544](profile.jpg)](https://teddygood.github.io/)\\n\\n\uc774\ubbf8\uc9c0\ub294 \ub0b4\uac00 \uc9c1\uc811 \ub300\ud559\uc5d0\uc11c \ucc0d\uc740 \ub2ec.\\n\\n## \ud14c\uc774\ube14\\n\\n`|`\uc640 `-`\uc758 \uc870\ud569\uc73c\ub85c \ud14c\uc774\ube14\uc744 \ub9cc\ub4e4 \uc218 \uc788\ub2e4.\\n\\n- \uc815\ub82c\\n  * \uc67c\ucabd \uc815\ub82c |:\u2014|\\n  * \uc624\ub978\ucabd \uc815\ub82c |\u2014:|\\n  * \uac00\uc6b4\ub370 \uc815\ub82c |:\u2014:|\\n\\n|\uc74c\uc2dd|\uac00\uaca9|\ud3c9\uac00|  \\n|:---:|---:|---|  \\n|\uce58\ud0a8+\uce58\uc988\ubcfc|25000|\ub9db\uc788\uc5b4|  \\n|\uc300\uad6d\uc218|7000|\ub9db\uc5c6\uc5b4|\\n\\n## \ubb38\ubc95\uc774 \uae30\uc5b5\ub098\uc9c0 \uc54a\uac70\ub098 \uc751\uc6a9\ud558\uace0 \uc2f6\uc744 \ub54c \\n\\n\uc774\ub7f4 \ub54c\ub294 Markdown\uacfc HTML/CSS\ub97c \uc11e\uc5b4 \uc4f0\uba74 \ub41c\ub2e4. \uc608\ub97c \ub4e4\uc5b4 \uc904\ubc14\uafc8\uc774 \uc801\uc6a9\uc774 \uc548 \ub41c\ub2e4\uba74 `<br>` \ud0dc\uadf8\ub97c \uc0ac\uc6a9\ud558\uba74 \ub41c\ub2e4. \uc774\uc678\uc5d0\ub3c4 \uae00\uc528\uc758 \uc0c9\uc744 \ubc14\uafb8\uac70\ub098 \ubc11\uc904 \ub4f1\uc744 \uc0ac\uc6a9\ud558\uace0 \uc2f6\uc73c\uba74 HTML\uacfc CSS\ub97c \uc0ac\uc6a9\ud558\uba74 \ub41c\ub2e4. \\n\\n\\n## References\\n\\n- [Markdown](https://daringfireball.net/projects/markdown/)\\n\\n- [\uc704\ud0a4\ud53c\ub514\uc544 \ub9c8\ud06c\ub2e4\uc6b4](https://ko.wikipedia.org/wiki/%EB%A7%88%ED%81%AC%EB%8B%A4%EC%9A%B4)\\n\\n- [\ub9c8\ud06c\ub2e4\uc6b4 \ubb38\ubc95 \ucd1d \uc815\ub9ac](https://ansohxxn.github.io/blog/markdown/#%EA%B8%80%EC%94%A8-%EC%83%89)\\n\\n- [\ub9c8\ud06c\ub2e4\uc6b4 \uc0ac\uc6a9\ubc95 \ubc0f \uc608\uc81c](https://theorydb.github.io/envops/2019/05/22/envops-blog-how-to-use-md/#markdown%EC%9D%B4%EB%9E%80)\\n\\n- [\ub9c8\ud06c\ub2e4\uc6b4 \uc791\uc131\ubc95](https://gist.github.com/ihoneymon/652be052a0727ad59601)"},{"id":"/Do-it-html5-css3","metadata":{"permalink":"/en/blog/Do-it-html5-css3","editUrl":"https://github.com/teddygood/teddygood.github.io/tree/main/blog/2020-12-30-do-it-html5-css3/Do-it-html5-css3.md","source":"@site/blog/2020-12-30-do-it-html5-css3/Do-it-html5-css3.md","title":"\ud83d\udcd6 Do it! HTML5+CSS3 \uc6f9 \ud45c\uc900\uc758 \uc815\uc11d","description":"\ub370\uc774\ud130 \uc0dd\uc131, \uac80\uc0c9, \uc870\uc791\uae4c\uc9c0 \ub370\uc774\ud130 \uc81c\ub300\ub85c \uc8fc\ubb34\ub974\uae30","date":"2020-12-30T00:00:00.000Z","tags":[{"inline":true,"label":"Book Review","permalink":"/en/blog/tags/book-review"}],"hasTruncateMarker":true,"authors":[{"name":"Chanho Lee","title":"\ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uacf5\ubd80\ud558\uace0 \uc788\ub294 \ud559\uc0dd","url":"https://github.com/teddygood","imageURL":"https://github.com/teddygood.png","key":"teddygood"}],"frontMatter":{"title":"\ud83d\udcd6 Do it! HTML5+CSS3 \uc6f9 \ud45c\uc900\uc758 \uc815\uc11d","date":"2020-12-30","authors":"teddygood","tags":["Book Review"],"draft":false,"slug":"/Do-it-html5-css3","description":"\ub370\uc774\ud130 \uc0dd\uc131, \uac80\uc0c9, \uc870\uc791\uae4c\uc9c0 \ub370\uc774\ud130 \uc81c\ub300\ub85c \uc8fc\ubb34\ub974\uae30","keywords":["HTML","CSS","\uc6f9"]},"unlisted":false,"prevItem":{"title":"\u2328\ufe0f \ub9c8\ud06c\ub2e4\uc6b4(Markdown) \uc815\ub9ac","permalink":"/en/blog/markdown"}},"content":"## \u2139\ufe0f Book Info\\n\\n:::tip\\n\ucc45 \uc774\ubbf8\uc9c0\ub97c \ud074\ub9ad\ud558\uba74 \uad50\ubcf4\ubb38\uace0 \uc0ac\uc774\ud2b8\ub85c \uc774\ub3d9\ud569\ub2c8\ub2e4!\\n:::\\n\\n[![\ucc45](../assets/review/Do-it-HTML5-CSS3.jpg)](https://product.kyobobook.co.kr/detail/S000001817955)\\n\\n- \uc81c\ubaa9: Do it! HTML5+CSS3 \uc6f9 \ud45c\uc900\uc758 \uc815\uc11d\\n- \uc800\uc790: \uace0\uacbd\ud76c\\n- \ucd9c\ud310\uc0ac: \uc774\uc9c0\uc2a4\ud37c\ube14\ub9ac\uc2f1\\n- \ucd9c\uac04: 2019-12-27\\n\\n{/* truncate */}\\n\\n## \ud83c\udfac Intro\\n\\n\ub098\ub294 \uac04\ub2e8\ud55c HTML, CSS\ub294 \uc0dd\ud65c\ucf54\ub529\uc73c\ub85c \uacf5\ubd80\ud588\uc5c8\ub2e4. C\uc5b8\uc5b4\ub97c \uba3c\uc800 \uc811\ud588\uace0, \uc6f9 \ud504\ub85c\uadf8\ub798\ubc0d\uc774 \ucc98\uc74c\uc774\uc5c8\uc5b4\uc11c \uc18d\uc73c\ub85c\ub294 \'\uc774\ub807\uac8c \uac04\ub2e8\ud574\ub3c4 \ub418\ub294\uac74\uac00?\' \uc0dd\uac01\ud588\uc5c8\ub2e4. \uacb0\uad6d \uc880 \ub354 \uc790\uc138\ud558\uac8c \uacf5\ubd80\ub97c \ud574\ubcf4\uace0\uc790 \uc774 \ucc45\uc744 \uad6c\uc785\ud558\uac8c \ub410\ub2e4. \ub2e4\ub978 \ucc45\ub3c4 \ud6c4\ubcf4\uc5d0 \uc788\uc5c8\uc9c0\ub9cc \ucc45\uc744 \uad6c\ub9e4\ud588\uc744 \ub2f9\uc2dc \uc774 \ucc45\uc774 \uac00\uc7a5 \uc0c1\uc704\uad8c\uc5d0 \uc788\uc5c8\ub2e4.\\n\\n## \ud83d\udcd6 Book Review\\n\\n### \uc6f9 \ud504\ub85c\uadf8\ub798\ubc0d \uc785\ubb38\uc790\uc5d0\uac8c \uc801\uc808\ud55c\uac00 \\n\\n\uc194\uc9c1\ud788 \ud504\ub85c\uadf8\ub798\ubc0d\uc744 \ucc98\uc74c \uacf5\ubd80\ud558\uc2dc\ub294 \uc0ac\ub78c\uc740 \ucc45\uc774 \uaf64 \ub450\uaebc\uc6cc\uc11c \ub2e4 \ubcfc \uc218 \uc788\uc744\uc9c0 \ubaa8\ub974\uaca0\ub2e4. \ub098\ub3c4 \ubcf4\ub2e4\uac00 \ub108\ubb34 \ub450\uaebc\uc6cc \uba48\ucdc4\uc5c8\ub2e4. \uc774 \ucc45\uc744 \ub450 \ubc88 \uc815\ub3c4 \ub2e4\uc2dc \uc77d\uc73c\ub824\uace0 \ub3c4\uc804\ud588\uc5c8\uc744 \uc815\ub3c4\uc774\ub2e4. \uc5c4\uccad \uce5c\uc808\ud558\uac8c \uc124\uba85\uc744 \ud574\uc8fc\uc9c0\ub9cc \'\uc774\uac78 \ub2e4 \uc54c\uc544\uc57c \ud558\ub098?\'\ub77c\ub294 \uc0dd\uac01\uc774 \ub4e4\uc5c8\uc5c8\ub2e4.\\n\\n\uc5c4\uccad\ub09c \ud504\ub860\ud2b8\uc5d4\ub4dc \uac1c\ubc1c\uc790\uac00 \ub420 \uac83\uc774 \uc544\ub2c8\ub77c\uba74 \ub2e4 \uc77d\uc5b4\ubcf4\uace0 \uc2e4\uc2b5\ud574\ubcfc \ud544\uc694\ub294 \uc5c6\ub2e4\uace0 \uc0dd\uac01\ud55c\ub2e4. \ubb3c\ub860 \uc2dc\uac04\uc774 \ub9ce\ub2e4\uba74 \ucc9c\ucc9c\ud788 \uaf3c\uaf3c\ud558\uac8c \uc77d\uc5b4\ubcf4\ub294 \uac83\ub3c4 \uc88b\uaca0\uc9c0\ub9cc, \uc778\ud130\ub137\uc5d0 \ub2e4\uc591\ud55c \uac15\uc758\uac00 \uc874\uc7ac\ud558\ub2c8 \uac15\uc758\ub97c \ub4e4\uc73c\uba70 \uc815\ub9ac\ud574\ubcf4\uace0 \uc2f6\uc740 \uac1c\ub150\uc774 \uc788\ub2e4\uba74 \ucc45\uc744 \ubcf4\uace0 \uacf5\ubd80\ud558\ub294 \uac83\uc774 \uc88b\uc740 \ubc29\ubc95\uc774\ub2e4. \uc989, reference\ub85c \uc0ac\uc6a9\ud558\uae38 \ucd94\ucc9c\ud55c\ub2e4.\\n\\n\ucc98\uc74c\uc5d0\ub294 \uc774 \ucc45\uc73c\ub85c HTML\uacfc CSS\ub97c \uc644\ubcbd\ud558\uac8c \uacf5\ubd80\ud558\uace0 \uc678\uc6b4\ub2e4\ub294 \uc0dd\uac01\uc73c\ub85c \uc0c0\uc9c0\ub9cc, \uadf8\uac74 \uc801\uc808\ud558\uc9c0 \uc54a\uc740 \uacf5\ubd80 \ubc29\ubc95\uc774\ub77c\ub294 \uac78 \uc54c\uac8c \ub410\uace0 \uc0dd\ud65c\ucf54\ub529 \uac15\uc758\ub97c \ub2e4\uc2dc \ubcf4\uba74\uc11c \uc774 \ucc45\uc744 \uac04\ub2e8\ud558\uac8c \uc77d\uc5c8\ub2e4. \\n\\n### \uc774 \ucc45 \uc774\uc678\uc5d0 \ucd94\ucc9c \ubb38\uc11c\\n\\nHTML\uacfc CSS\ub294 \uc601\uc5b4\ub9cc \uc77d\uc744 \uc904 \uc54c\uba74 [MDN](https://developer.mozilla.org/ko/)\uacfc [w3schools](https://www.w3schools.com/)\ub97c \ucd94\ucc9c\ud55c\ub2e4. \uc774 \ubc16\uc5d0\ub3c4 \uac04\ub2e8\ud558\uac8c \uacf5\ubd80\ud558\uace0 \uc2f6\ub2e4\uba74 [\uc0dd\ud65c\ucf54\ub529](https://opentutorials.org/course/1), [\uc720\ud29c\ubc84 \ub4dc\ub9bc\ucf54\ub529](https://www.youtube.com/@dream-coding/featured) \ub4f1 \ub2e4\uc591\ud55c \uac15\uc758\ub4e4\uc774 \ub9ce\uc73c\ub2c8 \ucc38\uace0\ud558\uba74 \ub41c\ub2e4. \ub610\ud55c, [\ubd80\uc2a4\ud2b8\ucf54\uc2a4](https://www.boostcourse.org/web344)\uc5d0\ub3c4 \uac15\uc758\uac00 \uc788\uc73c\ub2c8 \ub3c4\uc6c0\uc774 \ub420 \uac83\uc774\ub2e4.\\n\\n## \ud83d\udd16 \ub300\uc0c1 \ub3c5\uc790\\n\\n\uc6f9 \ud37c\ube14\ub9ac\uc154\ub97c \ubaa9\ud45c\ub85c \ud558\ub294 \uc0ac\ub78c\ub4e4\uc5d0\uac8c \ucd94\ucc9c\ud55c\ub2e4. \ub610\ud55c, \ud504\ub860\ud2b8\uc5d4\ub4dc \uac1c\ubc1c\uc790\ub97c \ubaa9\ud45c\ub85c \ud558\ub294 \uc0ac\ub78c\uc5d0\uac8c \ucd94\ucc9c\ud55c\ub2e4."}]}}')}}]);